INSERT INTO [t30609] VALUES (1,'صلت خلف رسول الله صلى الله عليه وسلم، فسمعته يقرأ: {مالك يوم الدين فقرأ حتى بلغ: غير','2','0','0');
INSERT INTO [t30609] VALUES (2,'يقرأ على الجنازة بفاتحة الكتاب.','3','0','0');
INSERT INTO [t30609] VALUES (3,'المضمضة والاستنشاق في الوضوء الذي لا بد منه. هذا حديث غريب من حديث الزهري، عن عروة، عن','4','0','0');
INSERT INTO [t30609] VALUES (4,'يقرؤها: يأتون.','5','0','0');
INSERT INTO [t30609] VALUES (5,'بعث خيلا، فلم يأته خبرها شهرا، فنزلت: {والعاديات ضبحا} [العاديات: ١] ضبحت بمناخرها','6','0','0');
INSERT INTO [t30609] VALUES (6,'تصدقوا، فإن الصدقة فكاككم من النار.','7','0','0');
INSERT INTO [t30609] VALUES (7,'من رابط ليلة حارسا من وراء المسلمين، كان له مثل أجر من خلفه ممن صلى','8','0','0');
INSERT INTO [t30609] VALUES (8,'كن في الدنيا كأنك غريب، وكأنك عابر سبيل، واعدد نفسك من أهل القبور. قال مجاهد: ثم أقبل علي','9','0','0');
INSERT INTO [t30609] VALUES (9,'لا يجزي ولد والده إلا أن يكون عبدا فيعتقه.','10','0','0');
INSERT INTO [t30609] VALUES (10,'نهى عن قيل وقال، وكثرة السؤال، وإضاعة المال.','11','0','0');
INSERT INTO [t30609] VALUES (11,'ما أمرتم به فاتبعوه، وما نهيتم عنه فاجتنبوه.','12','0','0');
INSERT INTO [t30609] VALUES (12,'من كان يؤمن بالله واليوم الآخر فلا يقعد على مائدة يشرب عليها خمر.','13','0','0');
INSERT INTO [t30609] VALUES (13,'عن النوم قبلها والحديث بعدها. يعني: العشاء الآخرة.','14','0','0');
INSERT INTO [t30609] VALUES (14,'تبعث نار على أهل المشرق، فتحشرهم إلى المغرب، تبيت معهم حيث باتوا، وتقيل معهم حيث قالوا،','15','0','0');
INSERT INTO [t30609] VALUES (15,'اللهم إني أعوذ بك من غلبة الدين، وغلبة العدو، ومن بوار الأيم، ومن فتنة المسيح','16','0','0');
INSERT INTO [t30609] VALUES (16,'يخرج قبل أن يثوب بالمغرب، فيراهم يصلون، فلا يأمرهم ولا ينهاهم.','17','0','0');
INSERT INTO [t30609] VALUES (17,'نسيت آية كذا، أو نسيتها، قال:.','18','0','0');
INSERT INTO [t30609] VALUES (18,'قد ذبح كل نون في البحر لبني آدم.','19','0','0');
INSERT INTO [t30609] VALUES (19,'اللهم صل على محمد، وعلى آل محمد، كما صليت على إبراهيم، وعلى آل إبراهيم، إنك حميد مجيد،','20','0','0');
INSERT INTO [t30609] VALUES (20,'إذا كان الحر فأبردوا بالظهر، فإن شدة الحر من فيح جهنم.','21','0','0');
INSERT INTO [t30609] VALUES (21,'عدنا رجلا من الأنصار، فإذا هو مريض شديد المرض، فمات، وبسطنا عليه ثوبه، وله أم عجوز عند','22','0','0');
INSERT INTO [t30609] VALUES (22,'لا يدخل الجنة قتات، والقتات: النمام.','23','0','0');
INSERT INTO [t30609] VALUES (23,'تحملت حمالة، فأتيت النبي صلى الله عليه وسلم أستعينه فيها، وذكر الحديث','24','0','0');
INSERT INTO [t30609] VALUES (24,'أنا لك كأبي زرع لأم زرع. قلت: يا رسول الله، ومن أبو زرع؟ فقال: اجتمع نسوة من قريش بمكة،','25','0','0');
INSERT INTO [t30609] VALUES (25,'من الكبائر أن يسب الرجل والديه، قيل: وكيف يسب الرجل والديه؟ قال: يتعرض للناس فيسب','26','0','0');
INSERT INTO [t30609] VALUES (26,'المهدي من ولد العباس عمي.','27','0','0');
INSERT INTO [t30609] VALUES (27,'آخر زادك من الدنيا ضياح لبن. وقال لي رسول الله صلى الله عليه وسلم: تقتلك الفئة','28','0','0');
INSERT INTO [t30609] VALUES (28,'ليكونن في ولده، يعني ولد العباس، ملوك يلون أمر أمتي يعز الله عز وجل بهم','29','0','0');
INSERT INTO [t30609] VALUES (29,'من لم يحب العباس بن عبد المطلب وأهل بيته فقد برئ الله ورسوله منه.','30','0','0');
INSERT INTO [t30609] VALUES (30,'الصائم المتطوع أمير نفسه، أو أمين نفسه، إن شاء صام، وإن شاء أفطر. قال شعبة: فقلت لجعدة:','31','0','0');
INSERT INTO [t30609] VALUES (31,'المتطوع أمين، أو أمير، نفسه، فإن شاء صام، وإن شاء أفطر. قال شعبة: فقلت: سمعته من أم هانئ؟','32','0','0');
INSERT INTO [t30609] VALUES (32,'من الشعر حكمة.','33','0','0');
INSERT INTO [t30609] VALUES (33,'الصلوات كانت على عهد رسول الله صلى الله عليه وسلم ركعتين ركعتين، فزيد في صلاة المقيم،','34','0','0');
INSERT INTO [t30609] VALUES (34,'مع جنازة، فرأى من معها قياما ينتظرون أن توضع، فجعل علي يشير إليهم بدرة معه، ويقول: اجلسوا','35','0','0');
INSERT INTO [t30609] VALUES (35,'انتهى الإيمان إلى الورع، من قنع بما رزقه الله عز وجل دخل الجنة، ومن أراد الجنة لا شك فلا','36','0','0');
INSERT INTO [t30609] VALUES (36,'لو تدومون على ما تكونون عندي لصافحتكم الملائكة كفاحا، ساعة وساعة.','37','0','0');
INSERT INTO [t30609] VALUES (37,'لست من دد، ولا دد مني. أو قال: سمعت رسول الله صلى الله عليه وسلم يقول ذلك. قال وهب: قال','38','0','0');
INSERT INTO [t30609] VALUES (38,'معنا ليلة نام رسول الله صلى الله عليه وسلم عن صلاة الصبح، في صبيحتها حتى طلعت الشمس','39','0','0');
INSERT INTO [t30609] VALUES (39,'من الصدقة أن تفك الرقبة، وتعتق النسمة. فقال رجل: يا رسول الله، أليستا واحدة؟ قال: لا،','40','0','0');
INSERT INTO [t30609] VALUES (40,'صلى خلف رسول الله صلى الله عليه خلف الصف، أو رأى رجلا صلى خلف الصف، فأمره رسول الله صلى','41','0','0');
INSERT INTO [t30609] VALUES (41,'أشكر الناس لله عز وجل أشكرهم للناس.','42','0','0');
INSERT INTO [t30609] VALUES (42,'أمرها أن تحل، فحلت، ونضحت البيت بنضوح. قال جعفر: كان أحمد بن حنبل قال لي: إيش عند صاحبك','43','0','0');
INSERT INTO [t30609] VALUES (43,'أفرد الحج.','44','0','0');
INSERT INTO [t30609] VALUES (44,'أهل الجاهلية يفيضون ويقولون: أشرق ثبير كيما نغير، فخالفهم رسول الله صلى الله عليه','45','0','0');
INSERT INTO [t30609] VALUES (45,'موت الغريب شهادة.','46','0','0');
INSERT INTO [t30609] VALUES (46,'الله عز وجل من على قوم، فألهمهم، فأدخلهم في رحمته، وابتلى قوما ذكر كلمة فلم يستطيعوا أن','47','0','0');
INSERT INTO [t30609] VALUES (47,'من جلب طعاما إلى المدينة فلا يبيعه موسرا، يحبسه عن معسر، ومن جلب فإن شاء باع، وإن شاء','48','0','0');
INSERT INTO [t30609] VALUES (48,'إذا رأيتم الجنازة فقوموا حتى تجاوزكم، أو توضع.','49','0','0');
INSERT INTO [t30609] VALUES (49,'لا تطئوا السبايا حتى يحضن، ولا الحوامل حتى يضعن، ولا تولهوا والدا عن','50','0','0');
INSERT INTO [t30609] VALUES (50,'أشفع الأذان وأوتر الإقامة.','51','0','0');
INSERT INTO [t30609] VALUES (51,'نهى في غزوة أوطاس أن يقع الرجل على حامل حتى تضع. غريب من حديث داود ابن أبي هند، عن','52','0','0');
