INSERT INTO [t30617] VALUES (1,'أقيموا صفوفكم وليؤمكم أحدكم فإذا كبر فكبروا وإذا قال: {ولا الضالين} فقولوا آمين يجبكم','2','0','0');
INSERT INTO [t30617] VALUES (2,'صلى على جنازة , فقال: اللهم اغفر له وارحمه واعف عنه وعافه وأكرم نزله ووسع مدخله واغسله','3','0','0');
INSERT INTO [t30617] VALUES (3,'يصلي على ميت فسمعت , يقول في دعائه: اللهم اغفر له وارحمه واعف عنه وعافه وأكرم نزله ووسع','4','0','0');
INSERT INTO [t30617] VALUES (4,'بينا أنا في الحجر وقريش تسألني، عن إسرائي إلى بيت المقدس، قال: فسألوني عن أشياء في بيت','5','0','0');
INSERT INTO [t30617] VALUES (5,'يتنزل الله تبارك وتعالى كل ليلة، حين يمضي ثلث الليل، يقول: أنا الملك أنا الملك من ذا الذي','6','0','0');
INSERT INTO [t30617] VALUES (6,'لأدفعن الراية غدا إلى رجل يحب الله ورسوله، يفتح الله عليه. قال عمر: فما أحببت الإمارة إلا','7','0','0');
INSERT INTO [t30617] VALUES (7,'اتركوني ما تركتم فإنما هلك من كان قبلكم بكثرة مسائلهم واختلافهم على أنبيائهم، فما نهيتكم','8','0','0');
INSERT INTO [t30617] VALUES (8,'اللهم بارك لنا في ثمرنا، وبارك لنا في مدينتنا، وبارك لنا في صاعنا، وبارك لنا في مدنا','9','0','0');
INSERT INTO [t30617] VALUES (9,'الدال على الخير كفاعله. رواه عن بشر بن خالد، عن غندر، عن شعبة، قال أبو عبيدة: يقال للرجل','10','0','0');
INSERT INTO [t30617] VALUES (10,'الله عز وجل يعذب يوم القيامة من يعذب الناس في الدنيا. فأرسلهم , قال: فكان عمر إذا رفع','11','0','0');
INSERT INTO [t30617] VALUES (11,'لا وفاء لنذر في معصية الله ولا فيما لا يملك ابن آدم.','12','0','0');
INSERT INTO [t30617] VALUES (12,'نهى عن بيع الذهب بالذهب والفضة بالفضة والبر بالبر والشعير بالشعير والتمر بالتمر والملح','13','0','0');
INSERT INTO [t30617] VALUES (13,'أعتق ستة أعبد له عند موته لم يكن له مال غيرهم فبلغ ذلك رسول الله صلى الله عليه وسلم، فقال','14','0','0');
INSERT INTO [t30617] VALUES (14,'لا حسد إلا في اثنتين رجل علمه الله القرآن فهو يتلوه آناء الليل والنهار فسمعه جار له ,','15','0','0');
INSERT INTO [t30617] VALUES (15,'لا تدخلوا الجنة حتى تؤمنوا، ولا تؤمنوا حتى تحابوا أولا أدلكم على شيء إذا فعلتموها تحاببتم','16','0','0');
INSERT INTO [t30617] VALUES (16,'في الجنة لسوقا يأتونها كل جمعة فتهب ريح الشمال فتحثو في وجوههم وثيابهم فيزدادون جمالا','17','0','0');
INSERT INTO [t30617] VALUES (17,'إذا خرجوا إلى المقابر , قال: قائلهم يقول: السلام عليكم أهل الديار من المؤمنين، أو','18','0','0');
INSERT INTO [t30617] VALUES (18,'أتى على المقابر فقال: السلام عليكم أهل الديار من المؤمنين والمسلمين وإنا إن شاء الله بكم','19','0','0');
INSERT INTO [t30617] VALUES (19,'الله ليرضى عن العبد أن يأكل الأكلة أو يشرب الشربة فيحمده عليها.','20','0','0');
INSERT INTO [t30617] VALUES (20,'الله لا ينظر إلى صوركم وأموالكم وإنما ينظر إلى قلوبكم فأعمالكم.','21','0','0');
INSERT INTO [t30617] VALUES (21,'من أحب لقاء الله أحب الله لقاءه. رواه عن سعيد بن عمرو , بالمعنى، عن عبثر، وقد رواه عن','22','0','0');
INSERT INTO [t30617] VALUES (22,'سبحان الذي سخر لنا هذا وما كنا له مقرنين وإنا إلى ربنا منقلبون , اللهم إنا نسألك في سفرنا','23','0','0');
INSERT INTO [t30617] VALUES (23,'إذا نزل أحدكم منزلا فليقل: أعوذ بكلمات التامات من شر ما خلق فإنه لا يضره شيء حتى يرتحل','24','0','0');
INSERT INTO [t30617] VALUES (24,'إذا توضأ العبد المسلم أو المؤمن فغسل وجهه خرجت من وجهه كل خطيئة نظر إليها بعينه مع الماء','25','0','0');
INSERT INTO [t30617] VALUES (25,'تفتح أبواب الجنة يوم الأثنين والخميس فيغفر لكل عبد لا يشرك بالله شيئا إلا رجل كانت بينه','26','0','0');
INSERT INTO [t30617] VALUES (26,'إذا سمعت الرجل يقول هلك الناس فهو أهلكهم.','27','0','0');
INSERT INTO [t30617] VALUES (27,'بينما امرأتان معهما ابنا لهما إذ جاء الذئب فذهب بأحدهما فقالت هذه لصاحبتها: إنما ذهب','28','0','0');
INSERT INTO [t30617] VALUES (28,'كل مال أنحلته عبدي فهو له حلال، وإني خلقت عبادي حنفاء كلهم، فأتتهم الشياطين فاجتالتهم عن','29','0','0');
INSERT INTO [t30617] VALUES (29,'بعثتك لأبتليك وأبتلي بك، وأنزل عليك كتابا لا يغسله الماء، تقرؤه نائما','30','0','0');
INSERT INTO [t30617] VALUES (30,'أحرق قريشا، قال: فقلت: رب إذا يثلغوا رأسي حتى يذروه كأنه خبزة، قال: فقال:، استغزهم','31','0','0');
INSERT INTO [t30617] VALUES (31,'أهل الجنة ثلاثة: ذو سلطان مقتصد موفق، ورجل رحيم رقيق القلب بكل ذي قربى ومسلم، ورجل عفيف','32','0','0');
INSERT INTO [t30617] VALUES (32,'كل مال نحلته عبادي فهو لهم حلال وأني خلقت عبادي حنفاء كلهم فأتتهم الشياطين فاجتالتهم عن','33','0','0');
INSERT INTO [t30617] VALUES (33,'الله نظر إلى أهل الأرض فمقتهم عربهم وعجمهم إلا بقايا من أهل الكتاب','34','0','0');
INSERT INTO [t30617] VALUES (34,'بعثتك لأبتليك وأبتلي بك وقد أنزلت عليك كتابا لا يغسله الماء تقرؤه في المنام واليقظة','35','0','0');
INSERT INTO [t30617] VALUES (35,'أهل الجنة ثلاثة إمام مسقط ورجل رحيم رقيق القلب لكل ذي قربى ومسلم، ورجل غني عفيف متصدق،','36','0','0');
INSERT INTO [t30617] VALUES (36,'سألت الله لآجال مضروبة وآثار مبلوغة وأرزاق مقسومة لا يتعجل منها شيء قبل حله ولا يؤخر منها','37','0','0');
INSERT INTO [t30617] VALUES (37,'الله لم يهلك قوما فجعل لهم نسلا ولا عاقبة إن القردة والخنازير كانت قبل','38','0','0');
INSERT INTO [t30617] VALUES (38,'لما دفن النبي صلى الله عليه سلم جعلوا في لحده قطيفة حمراء.','39','0','0');
INSERT INTO [t30617] VALUES (39,'الله كتب الإحسان علي كله فإذا قتلتم فأحسنوا القتلة، وإذا ذبحتم فأحسنوا الذبح وليحد أحدكم','40','0','0');
INSERT INTO [t30617] VALUES (40,'لله عز وجل أشد فرحا بتوبة عبده حين يتوب إليه من أحدكم كان على راحلة بأرض فلاة فانفلتت','41','0','0');
INSERT INTO [t30617] VALUES (41,'إذا جلس بين شعبها الأربع، ومس الختان الختان وجب الغسل. رواه عن محمد بن المثنى، عن محمد بن','42','0','0');
INSERT INTO [t30617] VALUES (42,'من نام عن حزبه أو عزا شيء منه وقرأه فيما بين صلاة الفجر إلى صلاة الظهر كتب له كأنما قرأه','43','0','0');
INSERT INTO [t30617] VALUES (43,'ما من صاحب إبل ولا بقر ولا غنم لا يؤدي حقها إلا وقف لها يوم القيامة، بقاع تطأه ذات ظلف','44','0','0');
INSERT INTO [t30617] VALUES (44,'يقبر إنسان ليلا إلا أن يضطر إلى ذلك.','45','0','0');
INSERT INTO [t30617] VALUES (45,'إذا سافر: اللهم إني أعوذ بك من وعثاء السفر وكآبة المنقلب ومن الحور بعد الكون ودعوة','46','0','0');
INSERT INTO [t30617] VALUES (46,'لا تزال طائفة من أمتى يقاتلون على الحق ظاهرين إلى يوم القيامة، قال: فينزل عيسى بن مريم،','47','0','0');
INSERT INTO [t30617] VALUES (47,'يا أصحاب السمرة يا أصحاب سورة البقرة. وكنت رجلا صيتا، فقلت: يا أصحاب السمرة يا أصحاب سورة','48','0','0');
INSERT INTO [t30617] VALUES (48,'الكافر يشرب في سبعة أمعاء وإن المؤمن يشرب في معاء واحد.','49','0','0');
INSERT INTO [t30617] VALUES (49,'تصدق بناقة مخطومة، في سبيل الله، فقال رسول الله صلى الله عليه وسلم: لتأتين يوم القيامة','50','0','0');
INSERT INTO [t30617] VALUES (50,'انطلق إلى أم أيمن نزورها كما كان يفعل رسول الله صلى الله عليه وسلم , يزورها فلما انتهينا','51','0','0');
INSERT INTO [t30617] VALUES (51,'نبيا من الأنبياء كان أعجب بأمته، فقال: من يقوم لهؤلاء؟ فأوحى الله عز وجل إليه أن خيرهم','52','0','0');
INSERT INTO [t30617] VALUES (52,'كان ملك من الملوك، وكان لذلك الملك كاهن يتكهن له، فقال ذلك الكاهن: انظروا لي غلاما فطنا،','53','0','0');
INSERT INTO [t30617] VALUES (53,'إذا أتى أحدكم أهله فإذا أراد أن يعود فليتوضأ وضوءه للصلاة.','54','0','0');
INSERT INTO [t30617] VALUES (54,'عدو الله إبليس جاء بشهاب من نار ليجعله في وجهي , فقلت: أعوذ بالله منك , ثلاث مرات، ثم','55','0','0');
INSERT INTO [t30617] VALUES (55,'الله لا ينام ولا ينبغي له أن ينام يخفض القسط ويرفعه يرفع إليه عمل الليل قبل النهار، وعمل','56','0','0');
INSERT INTO [t30617] VALUES (56,'الله تعالى يبسط يده بالنهار ليتوب مسيء الليل، ويبسط يده بالليل ليتوب مسيء النهار، حتى','57','0','0');
INSERT INTO [t30617] VALUES (57,'أنا أحمد، ومحمد، وأنا الحاشر، وأنا العاقب، وأنا المقفي , ونبي الرحمة , ونبي','58','0','0');
INSERT INTO [t30617] VALUES (58,'إذا شرب تنفس في الإناء ثلاثا، ويقول: إنه أهنأ، وأمرأ، وأبرأ.','59','0','0');
INSERT INTO [t30617] VALUES (59,'آتي يوم القيامة باب الجنة فاستفتح فيقول لي الخازن: من أنت؟ فأقول: محمد. فيقول: أمرت أن','60','0','0');
INSERT INTO [t30617] VALUES (60,'لما نزلت على النبي صلى الله عليه وسلم {إنا فتحنا لك فتحا مبينا} إلى آخر الآية مرجعه من','61','0','0');
INSERT INTO [t30617] VALUES (61,'أريت ما يعمل، يعني الناس ويكدحون فيه أشيء قد قضى عليهم أو مما يستقبلونه أم مما آتاهم','62','0','0');
INSERT INTO [t30617] VALUES (62,'قلت بعدك أربع كلمات ثلاث مرات لو وزن بجميع ما قلت لوزنتهن، سبحان الله وبحمده عدد خلقه','63','0','0');
INSERT INTO [t30617] VALUES (63,'ليغان على قلبي، وإني استغفر الله تبارك وتعالى في اليوم مائة مرة.','64','0','0');
INSERT INTO [t30617] VALUES (64,'توبوا إلى ربكم فإني أتوب في كل يوم مائة مرة. رواه عن عبد الله بن معاذ، عن أبيه، وعن أبي','65','0','0');
INSERT INTO [t30617] VALUES (65,'يمسح مناكبنا في الصلاة، ويقول: لا تختلفوا فتختلف قلوبكم، وليليني منكم ألو الأحلام والنهى،','66','0','0');
INSERT INTO [t30617] VALUES (66,'رأيت موسى قائما يصلي في قبره.','67','0','0');
INSERT INTO [t30617] VALUES (67,'أنزلت علي آنفا { [سورة فقرأ بسم الله الرحمن الرحيم] إنا أعطيناك الكوثر فصل لربك وانحر إن','68','0','0');
INSERT INTO [t30617] VALUES (68,'نزلت علي آنفا { [سورة بسم الله الرحمن الرحيم] إنا أعطيناك الكوثر فصل لربك وانحر إن شانئك','69','0','0');
