INSERT INTO [t30657] VALUES (1,'اللهم أطعم من أطعمني واسق من سقاني. فأخذت الشفرة , وأخذت الشملة , وانطلقت إلى الأعنز','2','0','0');
INSERT INTO [t30657] VALUES (2,'أتريدون أن تقولوا كما قال أهل الكتابين من قبلكم: سمعنا وعصينا , بل قولوا: سمعنا وأطعنا','3','0','0');
INSERT INTO [t30657] VALUES (3,'أصبت سيفا يوم بدر فأتيت النبي صلى الله عليه وسلم، فقلت: يا رسول الله نفلنيه، فقال: ضعه من','4','0','0');
INSERT INTO [t30657] VALUES (4,'أليس قد أمر الله بطاعة الوالدين فلا آكل طعاما ولا أشرب شرابا حتى تكفر بالله، وامتنعت عن','5','0','0');
INSERT INTO [t30657] VALUES (5,'وصنع رجل من الأنصار طعاما فدعا ناسا من المهاجرين وناسا من الأنصار فأكلنا وشربنا حتى سكرنا','6','0','0');
INSERT INTO [t30657] VALUES (6,'وأراد أن يوصي بماله كله فجعل يناقصه حتى بلغ الثلث: فالناس يوصون','7','0','0');
INSERT INTO [t30657] VALUES (7,'أتاني داعي الجن فانطلقت أقرئهم القرآن فانطلق بنا فأرنا آثارهم ونيرانهم وسألوه الزاد فقال:','8','0','0');
INSERT INTO [t30657] VALUES (8,'رجل يصوم الدهر كله؟ فقال رسول الله صلى الله عليه وسلم: لا صام ولا أفطر. أو قال: ما صام','9','0','0');
INSERT INTO [t30657] VALUES (9,'الطلاق على عهد رسول الله صلى الله عليه وسلم، وأبي بكر، وسنين من خلافة عمر، رضي الله عنهم','10','0','0');
INSERT INTO [t30657] VALUES (10,'الثلاث، تجعل واحد على عهد رسول الله صلى الله عليه وسلم، وأبي بكر، وثلاثا من أمارة عمر؟','11','0','0');
INSERT INTO [t30657] VALUES (11,'كانت بنو إسرائيل يغتسلون عراة وينظر بعضهم إلى سرة بعض، وكان موسى عليه السلام يغتسل وحده،','12','0','0');
INSERT INTO [t30657] VALUES (12,'من تطهر في بيته ثم عمد إلى بيت من بيوت الله يؤدي فريضة من فرائض الله فخطوتاه إحداهما تحط','13','0','0');
INSERT INTO [t30657] VALUES (13,'ما منكم من أحد ينجيه عمله. قالوا: ولا أنت يا رسول الله؟ قال: ولا أنا إلا أن يتغمدني الله','14','0','0');
INSERT INTO [t30657] VALUES (14,'أتاكم أهل اليمن هم أرق أفئدة الإيمان يمان، والحكمة يمانية، والفقه','15','0','0');
INSERT INTO [t30657] VALUES (15,'أول زمرة تدخل الجنة على صورة القمر ليلة البدر، والتي تليها على صورة كوكب دري في السماء،','16','0','0');
INSERT INTO [t30657] VALUES (16,'كان لي فيكم أخوة وأصدقاء فإني أبرأ إلى الله أن أكون اتخذت منكم خليلا، وإن كنت متخذا خليلا','17','0','0');
INSERT INTO [t30657] VALUES (17,'تأمر من كان معه فضل زاد يأتي به إليك تجمعه على شيء ثم تدعو فيه بالبركة ثم تقسمه بينهم','18','0','0');
INSERT INTO [t30657] VALUES (18,'ونحن حرم فأهدي له طير وطلحة راقد فمنا من أكله ومنا من تورع ولم يأكل، فقال: أكلنا مع رسول','19','0','0');
INSERT INTO [t30657] VALUES (19,'يصلون نحو بيت المقدس فلما نزلت هذه الآية قوله عز وجل: {فول وجهك شطر المسجد الحرام} فمر','20','0','0');
INSERT INTO [t30657] VALUES (20,'ما من صاحب إبل ولا بقر ولا غنم لا يؤدي حقها إلا أقعد يوم القيامة بقاع قرقر تطأه ذات الظلف','21','0','0');
INSERT INTO [t30657] VALUES (21,'إذا جلس في الصلاة وضع كفه اليمنى على فخذه اليمنى، وقبض أصابعه كلها، وأشار بأصبعه التي تلي','22','0','0');
INSERT INTO [t30657] VALUES (22,'أكل كل ذي ناب من السباع حرام.','23','0','0');
INSERT INTO [t30657] VALUES (23,'{ألهاكم التكاثر حتى زرتم المقابر} قال: يقول ابن آدم: مالي مالي , ما لك من مالك إلا ما','24','0','0');
INSERT INTO [t30657] VALUES (24,'الطهور شطر الإيمان , والحمد لله تملأ الميزان , وسبحان الله والله أكبر تملآن ما بين السماء','25','0','0');
INSERT INTO [t30657] VALUES (25,'لا يقتل قرشي صبرا بعد هذا اليوم إلى يوم القيامة. قال: لم يدرك الإسلام من عصاة قريش إلا','26','0','0');
INSERT INTO [t30657] VALUES (26,'إذا قام من الليل؟ قالت: كان يكبر، ويفتتح صلاته، يقول: اللهم رب جبريل، وميكائيل، وإسرافيل','27','0','0');
INSERT INTO [t30657] VALUES (27,'فجاء رسول الله صلى الله عليه وسلم وترك خطبته فأتى بكرسي خلت قوائمه حديدا فقعد رسول الله','28','0','0');
INSERT INTO [t30657] VALUES (28,'المزارعة، فقال: أخبرني ثابت بن الضحاك، أن النبي صلى الله عليه وسلم , نهى','29','0','0');
INSERT INTO [t30657] VALUES (29,'من صلى العشاء في جماعة فكأنما قام شطر الليل. يعني العتمة ومن صلى الفجر في جماعة فكأنما','30','0','0');
INSERT INTO [t30657] VALUES (30,'من لعب بالنرد فكأنما غمس يده في لحم خنزير ودمه.','31','0','0');
INSERT INTO [t30657] VALUES (31,'إذا اجتهد الحاكم فأصاب فله أجران، وإذا اجتهد فأخطأ فله أجر.','32','0','0');
INSERT INTO [t30657] VALUES (32,'قضى بيمين وشاهد.','33','0','0');
INSERT INTO [t30657] VALUES (33,'ومنهن فتن كريح الصيف ومنها صغار ومنها كبار. قال حذيفة: فذهب أولئك الرهط كلهم','34','0','0');
INSERT INTO [t30657] VALUES (34,'فعطس رجل , فقال له النبي صلى الله عليه وسلم: يرحمك الله، ثم عطس أخرى , فقال الرجل:','35','0','0');
INSERT INTO [t30657] VALUES (35,'حوضي أبعد من أيلة وعدن والذي نفسي بيده لآنيته أكثر من عدد النجوم وهو أشد بياضا من اللبن','36','0','0');
INSERT INTO [t30657] VALUES (36,'أنا فرطكم بين أيديكم، فإن لم تجدوني فأنا على الحوض وحوضي قدر ما بين أيلة إلى مكة وسيأتي','37','0','0');
INSERT INTO [t30657] VALUES (37,'هل سمعت بمقام المحمود الذي يخرج الله به من يخرج؟ قال: ثم نعت وضع الصراط وممر الناس عليه،','38','0','0');
INSERT INTO [t30657] VALUES (38,'حق المسلم على المسلم ست. قالوا: وما هن يا رسول الله؟ قال: إذا لقيه سلم عليه، وإذا دعاه','39','0','0');
INSERT INTO [t30657] VALUES (39,'قولا لا أسأل عنه أحدا بعدك، قال: قل آمنت بالله ثم استقم.','40','0','0');
INSERT INTO [t30657] VALUES (40,'البنت بالبنت جلد مائة، ورجم بالحجارة، والبكر بالبكر جلد مائة، ونفي','41','0','0');
INSERT INTO [t30657] VALUES (41,'المستبان ما قالا فعلى البادئ منهما ما لم يعتدي المظلوم.','42','0','0');
INSERT INTO [t30657] VALUES (42,'المفلس من أمتي من يأتي يوم القيامة بصلاته وزكاته، وصيامه، وقد شتم هذا، وقذف هذا، وأكل مال','43','0','0');
INSERT INTO [t30657] VALUES (43,'من كان رسول الله صلى الله عليه وسلم , مستخلفا لو استخلف؟ قالت: أبو بكر. ثم قيل لها: من','44','0','0');
INSERT INTO [t30657] VALUES (44,'لا يدخل الجنة إلا مؤمن، وأيام منى أيام أكل وشرب.','45','0','0');
INSERT INTO [t30657] VALUES (45,'إذا خرجت إلى العشاء الآخرة فلا تمسي طيبا.','46','0','0');
INSERT INTO [t30657] VALUES (46,'ستكون بعدي هنات وهنات ممدودة فمن رأيتموه يفرق أمة محمد وهي جميع فاقتلوه كائنا من كان من','47','0','0');
INSERT INTO [t30657] VALUES (47,'الناس تبع لقريش في هذا الشأن فمسلمهم تبع لمسلمهم وكافرهم تبع لكافرهم.','48','0','0');
INSERT INTO [t30657] VALUES (48,'جعل تحت رسول الله صلى الله عليه وسلم , حين دفن قطيفة حمراء.','49','0','0');
INSERT INTO [t30657] VALUES (49,'إذا سلم، يقول: لا إله إلا الله , وحده لا شريك له , له الملك , وله الحمد , وهو على كل شيء','50','0','0');
INSERT INTO [t30657] VALUES (50,'أنتم أعلم بشئون دنياكم، وأما أمر آخرتكم فإلي.','51','0','0');
INSERT INTO [t30657] VALUES (51,'سترة المصلي في غزوة تبوك، فقال: مثل مؤخرة الرحل.','52','0','0');
INSERT INTO [t30657] VALUES (52,'سيحان وجيحان، والنيل، والفرات، كل من أنهار الجنة.','53','0','0');
INSERT INTO [t30657] VALUES (53,'من تاب قبل أن تطلع الشمس من مغربها تاب الله عليه.','54','0','0');
INSERT INTO [t30657] VALUES (54,'صلى الظهر، ثم أشعر بدنته من الجانب الأيمن، وقلدها، ثم ركب راحلته، فلما استوت به على','55','0','0');
INSERT INTO [t30657] VALUES (55,'لا يزال الناس يتساءلون حتى يقولوا: هذا الله خلق الخلق , فمن خلق الله؟ . قال: قد سئلت عنها','56','0','0');
INSERT INTO [t30657] VALUES (56,'منهم من تأخذه النار إلى كعبيه، ومنهم من تأخذه النار إلى ترقوته.','57','0','0');
INSERT INTO [t30657] VALUES (57,'من كان خلقه الله وكتبه في إحدى المنزلتين فيستعمل لها وتصديق ذلك في كتاب الله {ونفس وما','58','0','0');
INSERT INTO [t30657] VALUES (58,'الشمس والقمر آيتان من آيات الله، وإنهما لا ينكسفان لموت أحد ولا حياته، وإذا رأيتم شيئا من','59','0','0');
INSERT INTO [t30657] VALUES (59,'احتجت الجنة والنار , فقالت النار: في الجبارون والمتكبرون، قالت الجنة: في ضعفاء الناس','60','0','0');
INSERT INTO [t30657] VALUES (60,'لا يبغض الأنصار رجل يؤمن بالله واليوم الآخر ٠ رواه عن عثمان كذلك','61','0','0');
INSERT INTO [t30657] VALUES (61,'مثلي ومثل الأنبياء كمثل رجل بنى دارا فأتمها إلا لبنة واحدة فجئت أنا فأتممت','62','0','0');
INSERT INTO [t30657] VALUES (62,'إذا انقطع شعث أحدكم فلا يمش في نعل واحدة، وإذا ولغ الكلب في إناء أحدكم فليغسله سبع مرات.','63','0','0');
INSERT INTO [t30657] VALUES (63,'اللهم صل على محمد وعلى آل محمد كما صليت على إبراهيم وعلى آل إبراهيم، وبارك على محمد وعلى','64','0','0');
INSERT INTO [t30657] VALUES (64,'معقبات لا يخيب قائلهن يسبح الله في دبر كل صلاة ثلاثا وثلاثين، ويحمده ثلاثا وثلاثين،','65','0','0');
INSERT INTO [t30657] VALUES (65,'من استعملناه منكم على عمل فليأت بقليله وكثيره، فما أوتي منه أخذ مالا فليدع ومن كتم منه','66','0','0');
INSERT INTO [t30657] VALUES (66,'ثمانين رجلا من أهل مكة هبطوا على رسول الله صلى الله عليه وسلم، وأصحابه من قبل التنعيم،','67','0','0');
INSERT INTO [t30657] VALUES (67,'أهل الجنة لا يتغوطون، ولا يبولون طعامهم.... ورشح كالمسك يلهمون التسبيح والحمد كما يلهمون','68','0','0');
INSERT INTO [t30657] VALUES (68,'لا يقولن أحدكم: يا خيبة الدهر، فإن الله هو الدهر.','69','0','0');
INSERT INTO [t30657] VALUES (69,'من شر الناس ذو الوجهين يأتي هؤلاء بوجه، وهؤلاء بوجه.','70','0','0');
INSERT INTO [t30657] VALUES (70,'دينار تنفقه على نفسك في سبيل الله ودينار تنفقه على فرسك في سبيل الله ودينار تنفقه على','71','0','0');
INSERT INTO [t30657] VALUES (71,'لعن رسول الله صلى الله عليه وسلم من لعن والده، ومن ذبح لغير الله، ومن آوى محدثا، ومن غير','72','0','0');
INSERT INTO [t30657] VALUES (72,'لعن الله من ذبح لغير الله، ولعن الله من آوى محدثا، ولعن الله من لعن، يعني والديه، ولعن','73','0','0');
INSERT INTO [t30657] VALUES (73,'إذا أخبرتكم بشيء عن الله فخذوه، فإني لن أكذب على الله شيئا.','74','0','0');
INSERT INTO [t30657] VALUES (74,'أحبه في الله، قال: فأني رسول الله إليك إن الله أحبك كما أحببته.','75','0','0');
INSERT INTO [t30657] VALUES (75,'الله كتب عليكم الحج في مقامي هذا، فقال رجل: يا رسول الله أفي كل عام، أو مرة واحدة؟ فقال:','76','0','0');
INSERT INTO [t30657] VALUES (76,'هلك من كان قبلكم بكثرة سؤالهم واختلافهم على أنبيائهم فإذا أمرتكم بشيء فخذوا منه ما','77','0','0');
INSERT INTO [t30657] VALUES (77,'اطردهم لا يجترئون علينا، قال: فكنت أنا وابن مسعود، ورجل من هذيل ورجلان نسيت اسمهما فوقع','78','0','0');
INSERT INTO [t30657] VALUES (78,'الله أقدر عليك منك على هذا الغلام. قال: قلت: لا أضرب مملوكا بعد هذا','79','0','0');
