INSERT INTO [t30731] VALUES (1,'الراحمون يرحمهم الرحمن ارحموا أهل الأرض يرحمكم أهل السماء','2','0','0');
INSERT INTO [t30731] VALUES (2,'المؤذن يغفر الله مد صوته، ويصدقه من سمعه من رطب ويابس، وله مثل أجر من صلى','3','0','0');
INSERT INTO [t30731] VALUES (3,'من أراد أن يزحزح عن النار، ويدخل الجنة، وتدركه منيته وهو يؤمن بالله واليوم الآخر فليأت','4','0','0');
INSERT INTO [t30731] VALUES (4,'سبحان الله والحمد لله ولا إله إلا الله والله أكبر تساقطن الذنوب كما تساقط ورق هذه','5','0','0');
INSERT INTO [t30731] VALUES (5,'الوقت الأول من الصلاة رضوان الله، والوقت الآخر عفو الله','6','0','0');
INSERT INTO [t30731] VALUES (6,'عدت شابا من الأنصار فما كان بأسرع أن مات فأغمضناه ومددنا عليه الثوب، فقال: بعضنا لأمه','7','0','0');
INSERT INTO [t30731] VALUES (7,'مات ابن خارجة تنافست الأنصار في غسله حتى كاد يكون بينهم شيء، ثم استقام رأيهم على أن يغسله','8','0','0');
INSERT INTO [t30731] VALUES (8,'من أمر بمعروف فليكن أمره بمعروف','9','0','0');
INSERT INTO [t30731] VALUES (9,'عودوا المرضى وأمروهم فليدعوا لكم فإن دعوة المريض مستجابة، وذنبه مغفور','10','0','0');
INSERT INTO [t30731] VALUES (10,'من اشتاق إلى الجنة سارع إلى الخيرات، ومن أشفق من النار لهى عن الشهوات ومن يرقب الموت لهى','11','0','0');
INSERT INTO [t30731] VALUES (11,'نضر الله امرأ سمع منا حديثا فبلغه كما سمعه فإنه رب مبلغ أوعى من سامع','12','0','0');
INSERT INTO [t30731] VALUES (12,'كيف بكم إذا ألبستكم فتنة يربو فيها الصغير، ويهرم فيها الكبير ويتخذها الناس سنة فإذا ترك','13','0','0');
INSERT INTO [t30731] VALUES (13,'المغبون لا محمود ولا مأجور','14','0','0');
INSERT INTO [t30731] VALUES (14,'الكيس من دان نفسه وعمل لما بعد الموت والعاجز من أتبع نفسه هواها وتمنى على الله','15','0','0');
INSERT INTO [t30731] VALUES (15,'الإيمان بضع وستون شعبة أفضلها لا إله إلا الله، وأدناها إماطة الأذى عن الطريق، والحياء','16','0','0');
INSERT INTO [t30731] VALUES (16,'سبحان الله وبحمده سبحان الله العظيم، استغفر الله مائة مرة ما بين طلوع الفجر إلى أن تصلي','17','0','0');
INSERT INTO [t30731] VALUES (17,'اليد العليا خير من اليد السفلى، وليبدأ أحدكم بمن يعول، خير الصدقة ما كان عن ظهر غنى ومن','18','0','0');
INSERT INTO [t30731] VALUES (18,'ساعتان يفتح الله فيها أبواب السماء قل ما ترد على داع دعوته عند حضور النداء، والصف في سبيل','19','0','0');
INSERT INTO [t30731] VALUES (19,'أكل ولدك أعطيتهم مثل ما أعطيت ذا؟ .','20','0','0');
INSERT INTO [t30731] VALUES (20,'فأراهم انشقاق القمر مرتين','21','0','0');
INSERT INTO [t30731] VALUES (21,'إذا حلفت على يمين فرأيت غيرها خيرا منها، فأت الذي هو خير، وكفر عن','22','0','0');
INSERT INTO [t30731] VALUES (22,'المسلم إذا عاد أخاه مؤازرة في الله يقول الله تبارك وتعالى: طبت وطاب ممشاك وتبوأت في الجنة','23','0','0');
INSERT INTO [t30731] VALUES (23,'رأس العقل بعد الإيمان التودد إلى الناس','24','0','0');
INSERT INTO [t30731] VALUES (24,'زر غبا تزدد حبا وحدثنا ابن أبي الدنيا، حدثني محمد بن صالح الخياط، نا أبو عبيدة الحداد، عن','25','0','0');
INSERT INTO [t30731] VALUES (25,'لا تسبوا أبا بكر وعمر فإنهما سيدا كهول أهل الجنة من الأولين والآخرين، عدا النبيين','26','0','0');
INSERT INTO [t30731] VALUES (26,'من تطهر في بيته، ثم مشى إلى بيت من بيوت الله عز وجل ليؤدي فيه فريضة الله عز وجل عليه','27','0','0');
INSERT INTO [t30731] VALUES (27,'البر حسن الخلق، والإثم ما حاك في صدرك، وكرهت أن يطلع عليه الناس','28','0','0');
INSERT INTO [t30731] VALUES (28,'لو أن الله عز وجل عذب أهل السموات، وأهل الأرض لعذبهم وهو غير ظالم لهم، ولو رحمهم كانت','29','0','0');
INSERT INTO [t30731] VALUES (29,'لا يموتن أحدكم إلا وهو حسن الظن بالله عز وجل','30','0','0');
INSERT INTO [t30731] VALUES (30,'من كانت له إلى الله حاجة فليصم الأربعاء والخميس، فإذا كان يوم الجمعة تطهر وراح إلى الجمعة','31','0','0');
INSERT INTO [t30731] VALUES (31,'ما من عبد مسلم صلى علي صلاة إلا صلت عليه الملائكة ما صلى علي فليقل عبد من ذلك أو','32','0','0');
INSERT INTO [t30731] VALUES (32,'من نسي الصلاة علي خطئ طريق الجنة','33','0','0');
INSERT INTO [t30731] VALUES (33,'كفى شحا أن أذكر عند قوم ولا يصلون علي فأكثروا الصلاة علي يوم الجمعة فإن صلاتكم تعرض','34','0','0');
INSERT INTO [t30731] VALUES (34,'الجنة مائة درجة ولو أن العالمين اجتمعوا في إحداهن لوسعتهم','35','0','0');
INSERT INTO [t30731] VALUES (35,'سرادق النار أربع جدر غلظ كل جدار مسيرة أربع مائة سنة','36','0','0');
INSERT INTO [t30731] VALUES (36,'هذه القلوب تصدأ كما يصدأ الحديد.','37','0','0');
INSERT INTO [t30731] VALUES (37,'ألا أخبركم على من تحرم النار غدا؟ على كل هين لين قريب سهل','38','0','0');
INSERT INTO [t30731] VALUES (38,'من قرأ قل هو الله أحد مائتي مرة غفر الله له ذنوبه مائتي عام','39','0','0');
INSERT INTO [t30731] VALUES (39,'من كان له إمام فقراءة الإمام له قراءة.','40','0','0');
INSERT INTO [t30731] VALUES (40,'حلال بين وحرام بين وشبهات بين ذلك، فمن ترك ما اشتبه عليه كان لما استبان له أترك، ومن','41','0','0');
INSERT INTO [t30731] VALUES (41,'ليس عام إلا الذي بعده شر منه سمعت ذلك من نبيكم صلى الله عليه وسلم','42','0','0');
INSERT INTO [t30731] VALUES (42,'ما من مسلمين إلا بينهما ستر من الله فإذا ألقى أحدهما كلمة هجر خرق ستر الله عز','43','0','0');
INSERT INTO [t30731] VALUES (43,'كأن الموت فيها على غيرنا كتب وكأن الحق فيها على غيرنا وجب، وكأن الذين نعاين من الموتى سفر','44','0','0');
INSERT INTO [t30731] VALUES (44,'ما من مسلم يأخذ مضطجعه فيقرأ سورة من كتاب الله عز وجل إلا وكل الله به عز وجل ملكا يذب عنه','45','0','0');
INSERT INTO [t30731] VALUES (45,'من سأل وعنده ما يغنيه فإنما يستكثر من جمر جهنم. قالوا: يا رسول الله وما يغنيه؟ فقال: ما','46','0','0');
INSERT INTO [t30731] VALUES (46,'صدقة المرء المسلم تزيد في العمر وتمنع ميتة السوء، ويذهب الله عز وجل بها الفخر','47','0','0');
INSERT INTO [t30731] VALUES (47,'إذا جاء أحدكم إلى الصلاة فلا يسع ولكن يمشي وعليه السكينة والوقار، فليصل ما أدرك، وليقض ما','48','0','0');
INSERT INTO [t30731] VALUES (48,'إذا كان أحدكم صائما فلا يرفث ولا يجهل وإن امرؤ شاتمه أو قاتله فليقل: إني','49','0','0');
INSERT INTO [t30731] VALUES (49,'يهرم ابن آدم ويشيب منه اثنتان الحرص على المال، والحرص على العمر','50','0','0');
INSERT INTO [t30731] VALUES (50,'إن كنتم تعقلون فعدوا أنفسكم من الموتى، والذي نفسي بيده إن ما توعدون لآت، وما أنتم بمعجزين','51','0','0');
INSERT INTO [t30731] VALUES (51,'عن صلاة بعد العصر فلا أدري أتعذب عليهما أم تؤجر؟ لأن الله عز وجل قال: {وما كان لمؤمن ولا','52','0','0');
INSERT INTO [t30731] VALUES (52,'لا تسأل الإمارة من قبل نفسك فإنك إن أعطيتها عن مسألة تكل إليها، وإن أعطيتها عن غير مسألة','53','0','0');
INSERT INTO [t30731] VALUES (53,'لا يرث أهل ملة ملة، ولا تجوز شهادة أهل ملة على ملة، إلا أمتي فإنهم يجوزون على سائر','54','0','0');
INSERT INTO [t30731] VALUES (54,'غفر الله لك ولأمك، أما رأيت العارض الذي عرض لي؟ قلت: بلى , قال: ذلك ملك لم يهبط الأرض قبل','55','0','0');
INSERT INTO [t30731] VALUES (55,'الرحم شجنة من الرحمن عز وجل فمن قطعها حرم الله عليه الجنة حدثنا النقيب أبو طالب , أنبا','56','0','0');
