INSERT INTO [t3125] VALUES (150,'[١٠٧-] قال إسحاق: والسنة أن يتيمم لكل صلاة لقول الله عز وجل {فلم٢ جدوا ماء فتيمموا صعيدا طيبا ٣}','346','43','0');
INSERT INTO [t3125] VALUES (151,'[١٠٨-] قال إسحاق: أما٦ من خلع خفيه بعد المسح فإنه يتوضأ الوضوء كله لما صار وضوؤه متفرقا، بعضه','346','43','0');
INSERT INTO [t3125] VALUES (152,'[١٠٩-] قال إسحاق: وأما مس الذكر فإنا نرى منه الوضوء٦ لما صح','347','43','0');
INSERT INTO [t3125] VALUES (153,'[١١٠-] قال إسحاق: وأما لحم الجزور٢ فإنه يتوضأ منه الوضوء كاملا٣ على كل حال لما (استثنى) ٤ من','349','43','0');
INSERT INTO [t3125] VALUES (154,'[١١١-] قال إسحاق: وأما الحجامة فإنه يتوضأ منها، وكذلك من الرعاف وكل دم سائل٢ وليس عليه من','350','43','0');
INSERT INTO [t3125] VALUES (155,'[١١٢-] قال إسحاق: وأما الذي يتجشأ٣ فيظهر على لسانه شيء من الطعام، أو يقطع صلاته، فإن عليه','351','43','0');
INSERT INTO [t3125] VALUES (156,'[١١٣-] قال إسحاق: وأما الوضوء من لحم الجزور٥ فقد صحت السنة أن أول ما كان من أمر النبي صلى الله','352','43','0');
INSERT INTO [t3125] VALUES (157,'[١١٤-[] ظ-٦/أ] قال إسحاق: وأما المصلي إذا صلى ثم رأى في ثوبه قذرا أيعيد الصلاة١؟ فإنه لا يعيد من','360','43','0');
INSERT INTO [t3125] VALUES (158,'[١١٥-] قال٣ إسحاق: والماء الذي يقطر من قضبان الكرم٤ أيجوز الوضوء٥ به [ع-٧/ب] ؟','361','43','0');
INSERT INTO [t3125] VALUES (159,'[١١٦-] قال إسحاق: وأما من٦ يخرج من دبره الدود٧','362','43','0');
INSERT INTO [t3125] VALUES (160,'[١١٧-] قال إسحاق: وقص الشارب وتقليم الأظفار يعيد الوضوء، أم لا؟','363','43','0');
INSERT INTO [t3125] VALUES (161,'[١١٨-] قال إسحاق: وأما عقد النية، عند إحداث الوضوء١ والصلاة فسنة؛ لأنه لابد له من أن ينوي ذلك، لقول','364','43','0');
