INSERT INTO [t3125] VALUES (515,'[٤٧٦-] قال إسحاق: وأما رفع اليدين عند الركوع فإن ذلك سنة٤ يرفع','767','43','0');
INSERT INTO [t3125] VALUES (516,'[٤٧٧-] قال إسحاق: وأما التسهيل٢ فإنه سنة٣، ولا يدع ذلك على حال٤.','768','43','0');
INSERT INTO [t3125] VALUES (517,'[٤٧٨-] قال إسحاق: وأما إذا صلى بالقوم وهو على غير وضوء، أو كان جنبا فعليه الإعادة ولا إعادة على من','769','43','0');
INSERT INTO [t3125] VALUES (518,'[٤٧٩-] قال إسحاق: وأما الرجل يسبق (ببعض) ١ الصلاة فإن الذي نختار له إذا قام أن يكون يقضي آخر','770','43','0');
INSERT INTO [t3125] VALUES (519,'[٤٨٠-] قال إسحاق: وأما الصلاة في جلود٥ الثعالب','770','43','0');
INSERT INTO [t3125] VALUES (520,'[٤٨١-] قال إسحاق: وأما الصلاة في جلود الميتة إذا دبغت٧ وكانت إبلا','772','43','0');
INSERT INTO [t3125] VALUES (521,'[٤٨٢-] قال إسحاق: وأما الصلاة في المواضع التي أصابها الأقذار فإن ذلك لا يجوز إذا سجد عليها أو قام','775','43','0');
INSERT INTO [t3125] VALUES (522,'[٤٨٣-] قال إسحاق: وأما دخول أهل الذمة٣ المسجد فإن ذلك مكروه؛ لما قال عمر بن عبد العزيز٤ لأصحابه','775','43','0');
INSERT INTO [t3125] VALUES (523,'[٤٨٥-] قال إسحاق: وأما أذان الفجر فقد كان يؤذن بليل فمن أذن بليل١ فهو متبع للسنة٢، وذلك أن بلالا كان','777','43','0');
INSERT INTO [t3125] VALUES (524,'[٤٨٦-] قال إسحاق: وأما الصلاة في النعال والخفاف سنة٣ إذا لم يكن','777','43','0');
INSERT INTO [t3125] VALUES (525,'[٤٨٧-] قال إسحاق: وأما إمامة ولد الزنا٣ والأقلف٤ والمخنث٥ فإن أموا فإمامتهم جائزة. وولد الزنا أحسنهم','778','43','0');
INSERT INTO [t3125] VALUES (526,'[٤٨٨-] قال إسحاق: وأما المؤذن إذا أخذ في الإقامة وهو إمام، فليس له أن يمشي في الإقامة حتى يفرغ منها،','778','43','0');
INSERT INTO [t3125] VALUES (527,'[٤٨٩-] قال إسحاق: ولا ينبغي للإمام أن يكبر حتى يفرغ المؤذن من الإقامة كلها٧، ويستوي الصف خلفه،','778','43','0');
INSERT INTO [t3125] VALUES (528,'[٤٩٠-] سئل إسحاق عن الرجل يدرك مع الإمام ثلاث ركعات، وعلى الإمام سهو، يسهو مع الإمام أم يقوم إلى','779','43','0');
INSERT INTO [t3125] VALUES (529,'[٤٩١-] قال إسحاق: وأما الرجلان يصليان في شهر رمضان التطوع في مسجد واحد، أحدهما يأتم بالآخر','780','43','0');
INSERT INTO [t3125] VALUES (530,'[٤٩٢-] قال إسحاق: وأما الإمام إذ صلى بالقوم ترويحة أو ترويحتين، ثم قام من آخر الليل فأرسل إلى قوم','781','43','0');
INSERT INTO [t3125] VALUES (531,'[٤٩٣-] قال إسحاق: وأما ما سألت عن الصلاة في الثعالب والفنك٣، فإن الصلاة في جلود السباع كلها محرم٤','783','43','0');
INSERT INTO [t3125] VALUES (532,'[٤٩٤-] قال إسحاق: أما القهقهة٤ في الصلاة فإن الذي يعتمد عليه ما صح عن جابر بن عبد الله٥ وأبي موسى','785','43','0');
INSERT INTO [t3125] VALUES (533,'[٤٩٥-] قال إسحاق: وأما المصلي وحده وهو ينظر في المصحف أو يقلب','787','43','0');
INSERT INTO [t3125] VALUES (534,'[٤٩٦-] قال إسحاق: وأما المصلي بغير القبلة وهو لا يعلم ثم علم، فإن ذلك إذا كان في مصر من الأمصار','789','43','0');
INSERT INTO [t3125] VALUES (535,'[٤٩٧-] قلت لإسحاق: وجانبا الصف إذا تقدما أمام الإمام حتى وجه كل الجانبين إلى غير القبلة وكان الذي','789','43','0');
INSERT INTO [t3125] VALUES (536,'[٤٩٨-] قلت لإسحاق: وكم صلى جبريل بالنبي صلى الله عليهما وسلم أربعة أو ركعتين، وأين صلى به؟','791','43','0');
INSERT INTO [t3125] VALUES (537,'[٤٩٩-] قلت لأحمد: إذا لم يقدر أن يصلي؟','792','43','0');
INSERT INTO [t3125] VALUES (538,'[٥٠٠-] [ع-٢٤/أ] قال إسحاق: وأما من قرأ الحمد حتى بلغ غير المغضوب فأحدث','793','43','0');
INSERT INTO [t3125] VALUES (539,'[٥٠١-] قلت لإسحاق: هل على من خلف الإمام أن يقول: سمع الله لمن حمده اللهم ربنا لك الحمد، كما يقول:','793','43','0');
INSERT INTO [t3125] VALUES (540,'[٥٠٢-] قلت: اللبن يقع فيه قطرة دم أيحل أكله؟','794','43','0');
INSERT INTO [t3125] VALUES (541,'[٥٠٣-] قلت لإسحاق: قوله: الصلاة خلف كل بر١ وفاجر٢ ما يعني٣ به؟','796','43','0');
INSERT INTO [t3125] VALUES (542,'[٥٠٤-] قلت لإسحاق: رجل صلى صلوات فلم يقرأ بسم الله الرحمن الرحيم؟','797','43','0');
INSERT INTO [t3125] VALUES (543,'[٥٠٥-] قلت لأحمد: متى يحرم البيع والشراء٤ يوم الجمعة؟','797','43','0');
INSERT INTO [t3125] VALUES (544,'[٥٠٦-] قلت: متى رخص له في ترك الجمعة؟','798','43','0');
INSERT INTO [t3125] VALUES (545,'[٥٠٧-] قلت: فالخائف؟','799','43','0');
INSERT INTO [t3125] VALUES (546,'[٥٠٨-] قلت: إذا خطب رجل يوم الجمعة يصلي آخر؟','800','43','0');
INSERT INTO [t3125] VALUES (547,'[٥٠٩-] قلت: إذا أدرك من الجمعة ركعة؟','800','43','0');
INSERT INTO [t3125] VALUES (548,'[٥١٠-] قلت: تكره الصلاة نصف النهار في الشتاء، والصيف؟','801','43','0');
INSERT INTO [t3125] VALUES (549,'[٥١١-] قلت: هل من إمام يترك الجمعة معه؟','802','43','0');
INSERT INTO [t3125] VALUES (550,'[٥١٢-] قلت: هل يجمع أهل القرى؟','803','43','0');
INSERT INTO [t3125] VALUES (551,'[٥١٣-] قلت: على من تجب الجمعة؟','805','43','0');
INSERT INTO [t3125] VALUES (552,'[٥١٤-] قلت: لا جمعة ولا تشريق٢ إلا في مصر جامع؟','806','43','0');
INSERT INTO [t3125] VALUES (553,'[٥١٥-] قلت: على المسافر جمعة؟','807','43','0');
INSERT INTO [t3125] VALUES (554,'[٥١٦-] قلت: على العبد جمعة؟','807','43','0');
INSERT INTO [t3125] VALUES (555,'[٥١٧-] قلت: متى يترك البيع والشراء يوم الجمعة؟','807','43','0');
INSERT INTO [t3125] VALUES (556,'[٥١٨-] قلت: إذا اغتسل أول النهار يوم الجمعة ثم أحدث؟','808','43','0');
INSERT INTO [t3125] VALUES (557,'[٥١٩-] قلت: على النساء غسل يوم الجمعة؟','808','43','0');
INSERT INTO [t3125] VALUES (558,'[٥٢٠-] قلت: متى يستقبل الإمام بوجهه يوم الجمعة؟','809','43','0');
INSERT INTO [t3125] VALUES (559,'[٥٢١-] قلت: هل يذكر الله عز وجل المرء والإمام يخطب؟','809','43','0');
INSERT INTO [t3125] VALUES (560,'[٥٢٢-] قلت: إذا جاء والإمام يخطب يوم الجمعة يصلي ركعتين؟','810','43','0');
INSERT INTO [t3125] VALUES (561,'[٥٢٣-] قلت: إذا عطس الرجل والإمام يخطب يوم الجمعة أشمته٣؟','811','43','0');
INSERT INTO [t3125] VALUES (562,'[٥٢٤-] قلت: وهل يرد السلام والإمام يخطب؟','812','43','0');
INSERT INTO [t3125] VALUES (563,'[٥٢٥-] قلت: كم يصلي قبل الجمعة وبعدها؟','812','43','0');
INSERT INTO [t3125] VALUES (564,'[٥٢٦-] قلت: من زحم يوم الجمعة فلم يقدر على الركوع والسجود كيف يصنع؟','813','43','0');
INSERT INTO [t3125] VALUES (565,'[٥٢٧-] قلت: يسافر يوم الجمعة؟','814','43','0');
INSERT INTO [t3125] VALUES (566,'[٥٢٨-] قلت: الرجل يحتبي يوم الجمعة والإمام يخطب؟','815','43','0');
INSERT INTO [t3125] VALUES (567,'[٥٢٩-] قلت: الساعة التي ترجى في يوم الجمعة متى هي؟','815','43','0');
INSERT INTO [t3125] VALUES (568,'[٥٣٠-] قلت: قال سفيان: جلس رجل عن الجمعة فصلى في بيته أربعا، ثم بدا له أن يأتي الجمعة؟','817','43','0');
INSERT INTO [t3125] VALUES (569,'[٥٣١-] قلت: قال سفيان: إن أحدث الإمام يوم الجمعة قبل أن يدخل في الصلاة فلا يقدمن إلا من شهد','818','43','0');
INSERT INTO [t3125] VALUES (570,'[٥٣٢-] قلت: قال سفيان: إذا لم يخطب الإمام يوم الجمعة فصل أربعا لا يكون جمعة إلا بخطبة، وإن جمع','819','43','0');
INSERT INTO [t3125] VALUES (571,'[٥٣٣-] قلت: قال سألت سفيان عن رجل أمره الأمير أن يخطب يوم الجمعة فخطب وصلى الأمير؟','820','43','0');
INSERT INTO [t3125] VALUES (572,'[٥٣٤-] قلت: قال سفيان في إمام خطب يوم الجمعة فلما فرغ من الخطبة جاء أمير غيره قال: يصلي الذي','820','43','0');
INSERT INTO [t3125] VALUES (573,'[٥٣٥-] قلت: سئل سفيان عن رجل صلى مع الإمام يوم الجمعة ركعة، ثم رعف فخرج فتوضأ، ثم جاء وقد','821','43','0');
INSERT INTO [t3125] VALUES (574,'[٥٣٦-] قلت: قال- يعني سفيان-: ما ترى في المرأة تكبر٣ أيام التشريق؟','821','43','0');
INSERT INTO [t3125] VALUES (575,'[٥٣٧-] قلت: سئل سفيان عن مسافر انتهى إلى الإمام يوم الجمعة وهو جالس في آخر صلاته؟','822','43','0');
INSERT INTO [t3125] VALUES (576,'[٥٣٨-] قلت: سئل سفيان عن رجل نسي صلاة الغداة حتى دخل في صلاة الجمعة؟','823','43','0');
INSERT INTO [t3125] VALUES (577,'[٥٣٩-] قلت: قوله: يوم الجمعة صلاة كله؟','823','43','0');
INSERT INTO [t3125] VALUES (578,'[٥٤٠-] قلت: الجمعة قبل الزوال أم بعد الزوال؟','824','43','0');
INSERT INTO [t3125] VALUES (579,'[٥٤١-] قلت: الإمام لا يركع في المسجد بعد الجمعة؟','825','43','0');
INSERT INTO [t3125] VALUES (580,'[٥٤٢-] قلت: الصلاة على الميت في المسجد؟','826','43','0');
INSERT INTO [t3125] VALUES (581,'[٥٤٣-] قلت: سئل سفيان عن الإمام إذا صلى على النبي صلى الله عليه','826','43','0');
INSERT INTO [t3125] VALUES (582,'[٥٤٤-] قلت: قال رجل من أهل خراسان٤ لسفيان: إن مسجد مرو أخذ غصبا وهدم ما حوله دورا وأدخل في','827','43','0');
INSERT INTO [t3125] VALUES (583,'[٥٤٥-] قال إسحاق: نظرنا اختلاف الروايات عن رسول الله صلى الله عليه وسلم في التقصير في إقامته وفي','828','43','0');
INSERT INTO [t3125] VALUES (584,'كتاب الزكاة','839','0','0');
INSERT INTO [t3125] VALUES (585,'وصف النسخ الخطية ومنهج التحقيق لكتاب الزكاة','839','584','0');
INSERT INTO [t3125] VALUES (586,'[٥٤٦ -] قال: قلت لأبي عبد الله - رضي الله عنه -: سئل سفيان الثوري أيعطي من في','848','584','0');
INSERT INTO [t3125] VALUES (587,'[٥٤٧ -] قلت: قال الحسن٣: كل وارث يجبر على وارثه إذا لم يكن له حيلة٤.','849','584','0');
INSERT INTO [t3125] VALUES (588,'[٥٤٨ -] قلت: قال: سألت الحسن عن الرجل يشتري أباه من الزكاة،','851','584','0');
INSERT INTO [t3125] VALUES (589,'[٥٤٩ -] قلت: قال: شهدت سفيان وسألته امرأة عن أخ لها يرهق١ له مال، فتأمره بالزكاة،','854','584','0');
INSERT INTO [t3125] VALUES (590,'[٥٥٠ -] قلت: قال سفيان: لأن يعطيها على وجهها أحب إلي، وإن أعطى العروض٢ أجزأه٣.','855','584','0');
INSERT INTO [t3125] VALUES (591,'[٥٥٢ -] قلت: المعدن٦ لا يؤخذ منه زكاة؟','856','584','0');
INSERT INTO [t3125] VALUES (592,'[٥٥٣ -] قلت: رجل ورث مالا، عليه فيه زكاة قبل أن يحول عليه الحول؟','858','584','0');
INSERT INTO [t3125] VALUES (593,'[٥٥٤ -] قلت: من استفاد ماشية من إبل أو بقر أو غنم، متى تجب فيها الزكاة؟ قال: حتى','859','584','0');
INSERT INTO [t3125] VALUES (594,'[٥٥٥ -] قلت: الرجل تجب عليه الصدقة وإبله مائة بعير، فلا يأتيه الساعي حتى تجب عليه','859','584','0');
INSERT INTO [t3125] VALUES (595,'[٥٥٦ -] قلت: الحنطة والشعير والسلت٢ صنف، والتمر صنف، والزبيب','860','584','0');
INSERT INTO [t3125] VALUES (596,'[٥٥٧ -] قلت: الحمص والعدس واللوبيا والجلبان٦ ونحو ذلك صنف','863','584','0');
INSERT INTO [t3125] VALUES (597,'[٥٥٨ -] قلت: قال٣: إذا أخرج زكاته من هذه الأصناف كلها، التمر والزبيب والحبوب، ثم','865','584','0');
INSERT INTO [t3125] VALUES (598,'[٥٥٩ -] قلت: سئل سفيان عن نصراني أعتق [ع-٢٧/أ] عبده نصرانيا، عليه الخراج٣؟','866','584','0');
INSERT INTO [t3125] VALUES (599,'[٥٦٠ -] قلت: عمر -رضي الله عنه - ضرب الجزية٤ على أهل الذهب','867','584','0');
INSERT INTO [t3125] VALUES (600,'[٥٦١ -] قلت فتوضع الجزية عن من أسلم من أهل الجزية؟','869','584','0');
INSERT INTO [t3125] VALUES (601,'[٥٦٢ -] قلت: هل على نساء أهل الذمة وصبيانهم، ونخيلهم٣،','870','584','0');
INSERT INTO [t3125] VALUES (602,'[٥٦٣ -] قلت: جاء رجل إلى عمر -رضي الله عنه - فقال: "إني قد أسلمت، فضع الخراج','872','584','0');
INSERT INTO [t3125] VALUES (603,'[٥٦٤ -] قال: قلت: جاء رجل إلى عمر -رضي الله عنه - فقال: إن أرض كذا وكذا يطيقون','874','584','0');
INSERT INTO [t3125] VALUES (604,'[٥٦٥ -] قلت: المسلم يعتق عبده النصراني، قال سفيان: يؤخذ منه الخراج١.','875','584','0');
INSERT INTO [t3125] VALUES (605,'[٥٦٦ -] قال: سألت أبا عبد الله أحمد بن محمد بن حنبل –رضي الله عنه - عن السواد٤؟.','875','584','0');
INSERT INTO [t3125] VALUES (606,'[٥٦٧ -] قلت: فخيبر٣؟','886','584','0');
INSERT INTO [t3125] VALUES (607,'[٥٦٨ -] قال الإمام أحمد: لما مسح عمر -رضي الله عنه - العامر والغامر، فالعامر قد بان','888','584','0');
INSERT INTO [t3125] VALUES (608,'[٥٦٩ - ١] قلت [لأحمد بن حنبل] ٢: قال سفيان: ما كان من أرض صولح عليها، ثم أسلم أهلها','889','584','0');
INSERT INTO [t3125] VALUES (609,'[٥٧٠ -] قال٥: وما كان من أرض أخذت عنوة، ثم أسلم صاحبها وضعت عنه الجزية، وأقر','889','584','0');
INSERT INTO [t3125] VALUES (610,'[٥٧١ -] قلت: [قد] ٢ سمعت الأوزاعي يقول: جمع٣ أصحابنا خصلتي سوء؛ دخلوا في','890','584','0');
INSERT INTO [t3125] VALUES (611,'[٥٧٢ -] قال أحمد: ليس في مال مكاتب١ زكاة؛ لأنه ليس بمالك لماله تاما، ولا للسيد أن يأخذ','892','584','0');
INSERT INTO [t3125] VALUES (612,'[٥٧٣ -] قال أحمد: لا يعطى من عنده خمسون درهما، أو من الحلي ذهب٤، أو فضة [ع-','892','584','0');
INSERT INTO [t3125] VALUES (613,'[٥٧٤ -] قال [الإمام] ٣ أحمد: يعجبني أن يعطي من زكاة ماله الجيران مع قرابته٤.','894','584','0');
INSERT INTO [t3125] VALUES (614,'[٥٧٥ -] [ظ-١٦/ب] قال [الإمام] ٣ أحمد: -[رضي الله عنه] ٤-: ولا يعطي من','895','584','0');
INSERT INTO [t3125] VALUES (615,'[٥٧٦ -] قلت [لأحمد] ٢ كيف تؤخذ الصدقة من الغنم؟','896','584','0');
INSERT INTO [t3125] VALUES (616,'[٥٧٧ -] قلت: في مال العبد زكاة؟','896','584','0');
INSERT INTO [t3125] VALUES (617,'[٥٧٨ -] قلت: الحلي فيه زكاة٣؟','897','584','0');
INSERT INTO [t3125] VALUES (618,'[٥٧٩ -] قلت: يعطى الأخ و٤ الأخت، أو الخالة، من الزكاة؟','898','584','0');
INSERT INTO [t3125] VALUES (619,'[٥٨٠ -] قلت: ما سقت السماء، والأنهار، والعيون، العشر. وما سقي بالرشاء١، فنصف','899','584','0');
INSERT INTO [t3125] VALUES (620,'[٥٨٢ -] قلت: قول ابن عباس: في أموال أهل الذمة العفو١؟ ^ [] قال أحمد: عمر -[رضي الله عنه] ٢ - جعل عليهم ما قد بلغك٣. كأنه لم ير ما قال ابن عباس','900','584','0');
INSERT INTO [t3125] VALUES (621,'[٥٨٣ -] قلت: قال سفيان: والغنم بمنزلة الورق٥، ليس٦ عليها صدقة، حتى يحول عليها','901','584','0');
INSERT INTO [t3125] VALUES (622,'[٥٨٤ -] قلت: سئل سفيان عن رجل باع غنما، قد حلت فيها الزكاة، ببقر١، قد حلت فيها','902','584','0');
INSERT INTO [t3125] VALUES (623,'[٥٨٥ -] قلت: قال سفيان في رجل ورث من أبيه غنما، أو إبلا، أو بقرا: يستقبل بها حولا٥،','903','584','0');
INSERT INTO [t3125] VALUES (624,'[٥٨٦ -] قلت: قال سفيان: ليس فيما دون الثلاثين من البقر شيء٧، وفي','903','584','0');
INSERT INTO [t3125] VALUES (625,'[٥٨٧ -] قلت: قال إبراهيم٥: إذا زادت على عشرين ومائة، استأنف الفرائض٦،','906','584','0');
INSERT INTO [t3125] VALUES (626,'[٥٨٨ -] قلت: قول علي – [رضي الله عنه]-١: إذا أخذ سنا دون٢ سن، أو سنا فوق٣ سن٤.','910','584','0');
INSERT INTO [t3125] VALUES (627,'[٥٨٩ -] قلت: قال سفيان: لولا ما جاء [في] ١ الأثر كان ما بين القيمتين ما بين السنين، ولكن','914','584','0');
INSERT INTO [t3125] VALUES (628,'[٥٩٠ -] قلت [لأحمد: قيل] ٣ له – يعني سفيان -: في ست وتسعين ومائة، قال: أربع حقاق٤.','915','584','0');
INSERT INTO [t3125] VALUES (629,'[٥٩١ -] قلت لأحمد٢: قال سفيان: إذا أخذت٣ سنين أوأكثر من ذلك، كان ما بين القيمتين٤.','917','584','0');
INSERT INTO [t3125] VALUES (630,'[٥٩٢ -] قلت: قال ابن عيينة١: إن سفيان كان يقول: كل شيء أخرجت الأرض، مما يكال،','919','584','0');
INSERT INTO [t3125] VALUES (631,'[٥٩٣ -] قلت [لأحمد] ١: طاوس والشعبي٢ وعطاء٣ قالوا: إذا زكيت طعامك، أو شعيرك','920','584','0');
INSERT INTO [t3125] VALUES (632,'[٥٩٤ -] قلت: قال سفيان: إذا كان عندك طعام من زرعك، وقد زكيته في شعبان، فبعته','921','584','0');
INSERT INTO [t3125] VALUES (633,'[٥٩٥ -] قلت: قال سفيان: إذا كان الطعام الذي بعت تجب فيه الزكاة٦، فإذا كان الطعام لم','922','584','0');
INSERT INTO [t3125] VALUES (634,'[٥٩٦ -] قلت: قال سفيان: وإذا باع زرعا أخضر بقلا، أو نخلا١ فيه طلع، فليس على البائع','924','584','0');
INSERT INTO [t3125] VALUES (635,'[٥٩٧ -] قلت: قال سفيان: إن٤ كان ابتاع الزرع والنخل للتجارة٥، قومه قيمته٦، إذا حال','925','584','0');
INSERT INTO [t3125] VALUES (636,'[٥٩٨ -] قلت: وإن كان اشتراه لغير التجارة١، فأدرك زكاه، وإن كان٢ قبل ذلك بشهر؟','926','584','0');
INSERT INTO [t3125] VALUES (637,'[٥٩٩ -] قلت: قال سفيان: وإذا باع الزرع، والنخل وقد أدرك، فالزكاة على البائع٧.','926','584','0');
INSERT INTO [t3125] VALUES (638,'[٦٠٠ -] [قلت] ٢ قال: قلت لسفيان: أرأيت إن باع نخله، أو عنبه، أو زرعه، وقد بلغ','927','584','0');
INSERT INTO [t3125] VALUES (639,'[٦٠١ -] قلت: قال سفيان: وبلغني عن الحسن أنه قال: وما أكل يحسب عليه١.','928','584','0');
INSERT INTO [t3125] VALUES (640,'[٦٠٢ -] قلت [لأحمد] ٢: سئل سفيان عن رجل له أرض حرة، منحها٣ رجلا، فزرعها؟','929','584','0');
INSERT INTO [t3125] VALUES (641,'[٦٠٣ -] قلت: كان٢ سفيان والأوزاعي يقولان: على أرض الخراج الزكاة حيث مازرع','930','584','0');
INSERT INTO [t3125] VALUES (642,'[٦٠٤ -] قلت: قال مكحول١ في المصدق يأتي المال، فلا يجد فيها السن التي عليه.','931','584','0');
INSERT INTO [t3125] VALUES (643,'[٦٠٥ -] قلت: قول شريح١: لا حبس عن فرائض الله – [عز وجل] ٢ -؟ ٣.','933','584','0');
INSERT INTO [t3125] VALUES (644,'[٦٠٧ -] قلت: قال سفيان: إذا استفاد الرجل ألف درهم، ثم استفاد قبل أن يحول على الألف','935','584','0');
INSERT INTO [t3125] VALUES (645,'[٦٠٨ -] قلت: قال: فإن سرقت الألف قبل الحول، فليس فيما استفاد بعد شيء، حتى يحول','935','584','0');
INSERT INTO [t3125] VALUES (646,'[٦٠٩ -] قلت لأحمد: قال سفيان: إذا كانت خمسمائة [درهم] ٥ تزكيها، فذهبت وأنفقت٦','936','584','0');
INSERT INTO [t3125] VALUES (647,'[٦١١ -] قلت: سئل سفيان عن رجل أخذ مالا مضاربة، فربح فيه، أيؤدي زكاته أو ينتظر','939','584','0');
INSERT INTO [t3125] VALUES (648,'[٦١٢ -] قلت: قال سفيان: لو أن رجلا اصطاد بقر وحش، أو حمر وحش، أو ظباء، أو سمكا،','940','584','0');
INSERT INTO [t3125] VALUES (649,'[٦١٣ -] قلت: قال سفيان: وإن ورث طعاما، أو٣ ثيابا، أو آنية، أو سفنا، فليس عليه [فيه] ٤','942','584','0');
INSERT INTO [t3125] VALUES (650,'[٦١٤ -] قلت: قال سفيان: إذا ابتعت غلاما، أو جارية٥ للتجارة٦، ثم بدا لك أن تمسكه لغير','943','584','0');
INSERT INTO [t3125] VALUES (651,'[٦١٥ -] قلت: قال سفيان: إذا كان لك على رجل دين، فدخل عليه٥ الزكاة، فما أخذت منه','944','584','0');
INSERT INTO [t3125] VALUES (652,'[٦١٦ -] قلت: [قال] ٣: سألت٤ سفيان عن رجل أسلف٥ في أثواب','945','584','0');
INSERT INTO [t3125] VALUES (653,'[٦١٨ -] قلت: قال: [سألت] ١ سفيان في رجل اشترى متاعا بمائة، وهو ثمن مائتين، يوم','948','584','0');
INSERT INTO [t3125] VALUES (654,'[٦١٩ -] قلت: سئل سفيان عن مكاتب له فضل مال عما عليه.','949','584','0');
INSERT INTO [t3125] VALUES (655,'[٦٢٠ -] قلت: وسئل٧ [فقيل] ٨: ليس٩ على سيده زكاة؟','949','584','0');
INSERT INTO [t3125] VALUES (656,'[٦٢١ -] [قلت: سئل سفيان عن زكاة مال المملوك على من هو؟','950','584','0');
INSERT INTO [t3125] VALUES (657,'[٦٢٢ -] قلت: قال سفيان: كان حماد٢، وابن أبي ليلى يقولان: إذا كان على الرجل دين، فعليه','951','584','0');
INSERT INTO [t3125] VALUES (658,'[٦٢٣ -] قلت: سئل سفيان عن رجل كان له على رجل ألف درهم دينا، فارتد، فكان٣ عليه','952','584','0');
INSERT INTO [t3125] VALUES (659,'[٦٢٤ -] قلت١: قال الحسن: إذا حلت الزكاة، فسرق المال، فهو ضامن٢.','955','584','0');
INSERT INTO [t3125] VALUES (660,'[٦٢٥ -] قلت: سئل سفيان عن دراهم، وجبت فيها الزكاة خمسة٣ وعشرين درهما، فسرق','956','584','0');
INSERT INTO [t3125] VALUES (661,'[٦٢٦ -] قلت: سئل سفيان١٠ عن الرجل إذا وجبت١١ عليه الزكاة،','957','584','0');
INSERT INTO [t3125] VALUES (662,'[٦٢٧ -] قلت: قال سفيان: ولا يطعم عن الآبق٤.','959','584','0');
INSERT INTO [t3125] VALUES (663,'[٦٢٨ -] قلت: قال سفيان: رقيق امرأته، ليس بواجب عليه، إن شاء فعل وإن شاء لم يفعل؛','960','584','0');
INSERT INTO [t3125] VALUES (664,'[٦٣٠ -] قلت [لأحمد] ١: قال سفيان: فإذا أهل هلال شوال، فمن ولد له [ولد] ٢، أو اشترى','963','584','0');
INSERT INTO [t3125] VALUES (665,'[٦٣١ -] قلت: قال سفيان: لا تدفع الصدقة إلى غني، ولا عبد، ولا تستأجر عليها منها، ولا','963','584','0');
INSERT INTO [t3125] VALUES (666,'[٦٣٢ -] قلت: قيل له -يعني سفيان-: أربعون جملا فيها مسنة؟','968','584','0');
INSERT INTO [t3125] VALUES (667,'[٦٣٣ -] قلت: ما كان من خليطين [ع-٣١/أ] يتراجعان بالسوية؟','968','584','0');
INSERT INTO [t3125] VALUES (668,'[٦٣٤ -] قلت: إذا كان عند الرجل طعام، من زرع وقد زكاه في شعبان،','969','584','0');
INSERT INTO [t3125] VALUES (669,'[٦٣٥ -] قلت: في المال المستفاد، زكاة؟','970','584','0');
INSERT INTO [t3125] VALUES (670,'[٦٣٦ -] قلت: زكاة الحلي؟','970','584','0');
INSERT INTO [t3125] VALUES (671,'٦٣٧ - قلت: في الدين، زكاة؟','971','584','0');
INSERT INTO [t3125] VALUES (672,'[٦٣٨ -] قلت: في العروض زكاة إذا كانت للتجارة؟','973','584','0');
INSERT INTO [t3125] VALUES (673,'[٦٣٩ -] قلت: في مال المملوك، زكاة؟','973','584','0');
INSERT INTO [t3125] VALUES (674,'[٦٤٠ -] قلت: ما زاد على المائتين؟','976','584','0');
INSERT INTO [t3125] VALUES (675,'[٦٤١ -] قلت: زكاة مال اليتيم؟','977','584','0');
INSERT INTO [t3125] VALUES (676,'[٦٤٣ -] قلت: إذا أوصى الرجل بحج أو زكاة كان من الثلث أم لا؟','980','584','0');
INSERT INTO [t3125] VALUES (677,'[٦٤٤ -] قلت: إذا أخرج زكاة ماله، ثم سرقت٣ أو ضاعت؟','980','584','0');
INSERT INTO [t3125] VALUES (678,'[٦٤٥ -] قلت: إذا حلت الزكاة، فسرق المال؟','981','584','0');
INSERT INTO [t3125] VALUES (679,'[٦٤٦ -] قلت: متى يعطي زكاة الفطر؟','981','584','0');
INSERT INTO [t3125] VALUES (680,'[٦٤٧ -] قلت: يعطي صدقة الفطر عن المكاتب؟','982','584','0');
INSERT INTO [t3125] VALUES (681,'[٦٤٨ -] قلت: وعن رقيق امرأته؟','982','584','0');
