INSERT INTO [t3125] VALUES (683,'[٦٥٠ -] قلت: يعطي٢ عن العبد إذا كان للتجارة؟','983','584','0');
INSERT INTO [t3125] VALUES (684,'[٦٥١ -] قلت [لأحمد] ٥: العسل، والعنبر٦، فيهما زكاة؟','983','584','0');
INSERT INTO [t3125] VALUES (685,'[٦٥٢ -] قلت: الركاز أين يكون في أرض الإسلام أو في أهل٣ الشرك؟','986','584','0');
INSERT INTO [t3125] VALUES (686,'[٦٥٣ -] قلت: في أي شيء يكون الركاز؟','987','584','0');
INSERT INTO [t3125] VALUES (687,'[٦٥٤ -] قلت [لأحمد] ٤: من تحل له الصدقة٥؟','988','584','0');
INSERT INTO [t3125] VALUES (688,'[٦٥٦ -] [قلت: يعطى من] ٢ الزكاة مشرك، أو عبد، أو نصراني، أو يهودي؟','992','584','0');
INSERT INTO [t3125] VALUES (689,'[٦٥٧ -] قلت: يخرج الزكاة من بلده [إلى بلد] ٣؟','993','584','0');
INSERT INTO [t3125] VALUES (690,'[٦٥٨ -] [قلت لأحمد: إذا] ٢ كان مملوك٣ بين اثنين [من] ٤ يؤدي عنه صدقة الفطر؟','994','584','0');
INSERT INTO [t3125] VALUES (691,'[٦٥٩ -] قلت: يشتري أباه، وأمه١ من الزكاة، فيعتقهما؟','995','584','0');
INSERT INTO [t3125] VALUES (692,'[٦٦٠ -] [قلت: إلى] ٤ من يدفع الزكاة أحب إليك؛ السلطان، أو يقسمها هو؟','995','584','0');
INSERT INTO [t3125] VALUES (693,'[٦٦١ -] قلت: الشاء٣ إذا كانت للتجارة؟','996','584','0');
INSERT INTO [t3125] VALUES (694,'[٦٦٢ -] قلت: ما يأخذه١ العشار يحتسب [به] ٢ من الزكاة؟','997','584','0');
