INSERT INTO [t3125] VALUES (696,'[٦٦٤ -] قلت: قوله "المعتدي٤ في الصدقة، كمانعها"٥؟','998','584','0');
INSERT INTO [t3125] VALUES (697,'[٦٦٥ -] قلت: صدقة الفطر؟','999','584','0');
INSERT INTO [t3125] VALUES (698,'[٦٦٦ -] قلت: زكاة٢ الفطر، على من جرت عليه نفقته٣؟','1000','584','0');
INSERT INTO [t3125] VALUES (699,'[٦٦٧ -] قلت: العبد يكون في الماشية أو الحائط عليه صدقة الفطر؟','1000','584','0');
INSERT INTO [t3125] VALUES (700,'[٦٦٨ -] قلت: سئل الأوزاعي عن المرأة تؤدي زكاة مهرها، إذا كان زوجها مليا.','1001','584','0');
INSERT INTO [t3125] VALUES (701,'[٦٦٩ -] قلت: إن رجلا أتى عليا٤ بزكاة ماله، فقال: هل نعطيك','1001','584','0');
INSERT INTO [t3125] VALUES (702,'[٦٧٠ -] قلت: الأكار٧ إذا أخرج في نصيبه ما يجب فيه العشر، أيعطي؟','1002','584','0');
INSERT INTO [t3125] VALUES (703,'[٦٧١ -] قال إسحاق: وأما الرجل [الذي] ٢ يملك الدار وقيمتها عشرة آلاف درهم، ولا شيء له','1009','584','0');
INSERT INTO [t3125] VALUES (704,'كتاب الصيام','1014','0','0');
INSERT INTO [t3125] VALUES (705,'وصف نسخ الكتاب (كتاب الصيام) :','1014','704','0');
INSERT INTO [t3125] VALUES (706,'[٦٧٣-] (قلت لأحمد) :٢ من رأى هلال رمضان وحده (يصوم) ٣ ومن رأى هلال شوال','1020','704','0');
INSERT INTO [t3125] VALUES (707,'[٦٧٤-] قلت: إذا رأى هلال شوال١ بالعشي٢ (يفطر) ؟ ٣','1021','704','0');
INSERT INTO [t3125] VALUES (708,'[٦٧٥-] قلت:٦ إذا قدم من سفر في رمضان وهو مفطر وامرأته مفطرة حين طهرت من حيضتها؟','1021','704','0');
INSERT INTO [t3125] VALUES (709,'[٦٧٦-] قلت: من أفطر يوما من قضاء رمضان بإصابة أهله؟','1023','704','0');
INSERT INTO [t3125] VALUES (710,'[٦٧٧-] قلت: صيام العبد في التظاهر؟','1023','704','0');
INSERT INTO [t3125] VALUES (711,'[٦٧٨-] قلت: من أكل أو شرب في رمضان؟','1024','704','0');
INSERT INTO [t3125] VALUES (712,'[٦٧٩-] قيل [له] ٢: فالعمد والخطأ في الجماع واحد؟','1025','704','0');
INSERT INTO [t3125] VALUES (713,'[٦٨٠-] قلت:٢ الصائم يدخل الحمام؟','1027','704','0');
INSERT INTO [t3125] VALUES (714,'[٦٨١-] قلت: من واصل من السحر٢ إلى السحر تكرهه؟','1028','704','0');
INSERT INTO [t3125] VALUES (715,'[٦٨٢-] قلت: الحقنة٣ للصائم وغير الصائم تكرهها؟','1029','704','0');
INSERT INTO [t3125] VALUES (716,'[٦٨٣-] قال أحمد:١ الإفطار في السفر أحب إلي من الصوم.٢','1031','704','0');
INSERT INTO [t3125] VALUES (717,'[٦٨٤-] (قلت: رجل استنشق فدخل الماء إلى حلقه، وهو صائم؟) .٤','1031','704','0');
INSERT INTO [t3125] VALUES (718,'[٦٨٥-] قلت:٢ الكحل للصائم؟','1032','704','0');
INSERT INTO [t3125] VALUES (719,'[٦٨٦-] قلت:٣ رجل أكل وهو يرى أن عليه ليلا،٤ وقد أصبح؟ ٥','1033','704','0');
INSERT INTO [t3125] VALUES (720,'[٦٨٧-] قلت: تجوز شهادة رجل على رؤية الهلال لرمضان أو شوال؟','1034','704','0');
INSERT INTO [t3125] VALUES (721,'[٦٨٨-] قلت:٣ من مات وعليه [ع-٣٣/أ] صوم شهر؟','1035','704','0');
INSERT INTO [t3125] VALUES (722,'[٦٨٩-] قلت:٢ من أفطر يوما من صيام شهرين متتابعين؟','1036','704','0');
INSERT INTO [t3125] VALUES (723,'[٦٩٠-] قلت:٣ رجل أصبح صائما في السفر ثم قدم أهله من يومه ذلك فأفطر؟','1037','704','0');
INSERT INTO [t3125] VALUES (724,'[٦٩١-] قال أحمد: وإذا أصبح مفطرا في السفر، فدخل أهله فأكل، فليس عليه شيء، ويعجبني أن لا يأكل.','1038','704','0');
INSERT INTO [t3125] VALUES (725,'[٦٩٢-] قلت: إذا خرج مسافرا متى يفطر؟','1038','704','0');
INSERT INTO [t3125] VALUES (726,'[٦٩٣-] قلت: [الصائم] ٢ يمضغ٣ العلك؟ ٤','1040','704','0');
INSERT INTO [t3125] VALUES (727,'[٦٩٤-] قلت: الفطر قبل المغرب٣ أحب إليك؟','1041','704','0');
INSERT INTO [t3125] VALUES (728,'[٦٩٥-] قلت: يصوم يوما ويفطر يوما أحب إليك؟','1042','704','0');
INSERT INTO [t3125] VALUES (729,'[٦٩٦-] قلت: من قال: أنت بالخيار إلى آخر النظرين؟ ٣','1042','704','0');
INSERT INTO [t3125] VALUES (730,'[٦٩٧-] قلت عاشوراء٤ يوم التاسع أو العاشر؟','1043','704','0');
INSERT INTO [t3125] VALUES (731,'[٦٩٨-] قلت١: رجل أغمي عليه في شهر رمضان؟','1045','704','0');
INSERT INTO [t3125] VALUES (732,'[٦٩٩-] قال أحمد٣: يحتاج في شهر رمضان أن يجمع٤ كل يوم على الصوم٥.','1046','704','0');
INSERT INTO [t3125] VALUES (733,'[٧٠٠-] قلت: صيام اليوم الذي يشك فيه٢ من رمضان؟','1047','704','0');
INSERT INTO [t3125] VALUES (734,'[٧٠١-] قلت لأحمد: المسافر يقدم في بعض النهار، والنصراني واليهودي يسلمان يصومون؟ ٩.','1052','704','0');
INSERT INTO [t3125] VALUES (735,'[٧٠٢-] قلت: (من أكل أو شرب ناسيا) ٣؟','1053','704','0');
INSERT INTO [t3125] VALUES (736,'[٧٠٣-] قلت: إذا أفطر [في] ١ يوم غيم يصوم يوما مكانه؟','1054','704','0');
INSERT INTO [t3125] VALUES (737,'[٧٠٤-] قلت١: من أصبح صائما ثم بدا له فأفطر؟','1055','704','0');
INSERT INTO [t3125] VALUES (738,'[٧٠٥-] قلت٤: صيام يوم الجمعة مفردا؟','1055','704','0');
INSERT INTO [t3125] VALUES (739,'[٧٠٦-] قلت١: قوله:٢ " [لا صيام] ٣ لمن لم يجمع٤ الصيام من الليل".٥','1056','704','0');
INSERT INTO [t3125] VALUES (740,'[٧٠٧-] قلت: الصائم يقبل أو يباشر٢؟','1057','704','0');
