INSERT INTO [t3125] VALUES (1219,'[١١٨١-] قلت: قال سفيان: إذا اشترى عجوزا وقد يئست من المحيض','1549','1040','0');
INSERT INTO [t3125] VALUES (1220,'[١١٨٢-] قال إسحاق: الأمر في ذلك أن يستبرئها أربعين ليلة عجوزا كانت أو ممن قاربت أن','1550','1040','0');
INSERT INTO [t3125] VALUES (1221,'[١١٨٣-] قلت: قال١ سفيان: وقد كان بعض من يشار إليه من أهل العلم يقول: إذا ابتاع','1552','1040','0');
INSERT INTO [t3125] VALUES (1222,'[١١٨٤-] قلت: سئل سفيان عن رجل اشترى جارية لم تحض وقد حاض مثلها؟','1553','1040','0');
INSERT INTO [t3125] VALUES (1223,'[١١٨٥-] قلت: قال الحسن في رجل قال لامرأته: أنت طالق إن شاء الله تعالى، كان يلزمه١؟','1554','1040','0');
INSERT INTO [t3125] VALUES (1224,'[١١٨٦-] قلت: لم؟','1554','1040','0');
INSERT INTO [t3125] VALUES (1225,'[١١٨٧-] قلت: وكذلك العتق؟','1554','1040','0');
INSERT INTO [t3125] VALUES (1226,'[١١٨٨-] قلت: قال الحسن ٤ في العبد يأبق وله امرأة: هي فرقة.','1557','1040','0');
INSERT INTO [t3125] VALUES (1227,'[١١٨٩-] قلت: قال سفيان: إذا كانا مشركين لهما عهد فأسلما، فهما على نكاحهما.','1558','1040','0');
INSERT INTO [t3125] VALUES (1228,'[١١٩٠-] سئل٢ إسحاق٣ عن مجوسي تزوج مجوسية صغيرة ثم أسلم قبل أن يدخل بها،','1559','1040','0');
INSERT INTO [t3125] VALUES (1229,'[١١٩١-] قيل: فإن أسلمت في العدة؟','1559','1040','0');
INSERT INTO [t3125] VALUES (1230,'[١١٩٢-] قلت: قال الحسن٢ في النصرانية تسلم وزوجها نصراني أو٣ المجوسية تكون تحت','1560','1040','0');
INSERT INTO [t3125] VALUES (1231,'[١١٩٣-] قلت: قال الشعبي٣: كل فرقة طلاق.','1561','1040','0');
INSERT INTO [t3125] VALUES (1232,'[١١٩٤-] قلت: سئل سفيان عن رجل دخل بامرأته فلم يصل إليها١ أن يجامعها ثم طلقها','1564','1040','0');
INSERT INTO [t3125] VALUES (1233,'[١١٩٥-] قلت٣: فالميراث؟','1564','1040','0');
INSERT INTO [t3125] VALUES (1234,'[١١٩٦-] قلت: سئل سفيان عن المرأة إذا قالت: إن زوجها لا يستطيع أن يجامعها؟','1565','1040','0');
INSERT INTO [t3125] VALUES (1235,'[١١٩٧-] قلت: المرتد كم تعتد٤ امرأته؟','1565','1040','0');
INSERT INTO [t3125] VALUES (1236,'[١١٩٨-] قلت: فإن قتل؟','1566','1040','0');
INSERT INTO [t3125] VALUES (1237,'[١١٩٩-] قلت: فإن تاب؟','1566','1040','0');
INSERT INTO [t3125] VALUES (1238,'[١٢٠٠-] قلت: لمن٥ ميراثه؟','1566','1040','0');
INSERT INTO [t3125] VALUES (1239,'[١٢٠١-] قلت: قال سفيان إذا ارتدت المرأة عن الإسلام ولها زوج قبل أن يدخل بها، فلا','1568','1040','0');
INSERT INTO [t3125] VALUES (1240,'[١٢٠٢-] قلت: قال سفيان: إذا ارتدت المرأة عن الإسلام ثم رجعت إلى','1568','1040','0');
INSERT INTO [t3125] VALUES (1241,'[١٢٠٣-] قلت: سئل سفيان عن رجل وطئ جاريته فولدت، فمات الرجل ولم يدع ولده ولم','1569','1040','0');
INSERT INTO [t3125] VALUES (1242,'[١٢٠٤-] قلت: سئل سفيان٣ عن رجل قال لرجل: قد زوجتك ابنتي إن رضيت أمها وهي','1570','1040','0');
INSERT INTO [t3125] VALUES (1243,'[١٢٠٥-] قلت: وإذا قال زوجتك إلا أن يكره فلان أو أمها؟','1571','1040','0');
INSERT INTO [t3125] VALUES (1244,'[١٢٠٦-] قلت١: قال سفيان في رجل زوج ابنه وهو غائب؟','1572','1040','0');
INSERT INTO [t3125] VALUES (1245,'[١٢٠٧-] قلت: سئل سفيان عن رجل تزوج امرأة فقال: تزوجت امرأة حراما؟','1573','1040','0');
INSERT INTO [t3125] VALUES (1246,'[١٢٠٨-] قلت: سئل سفيان عن رجل زوج ابنه صغيرا وضمن المهر، فمات الأب ولم يدع','1574','1040','0');
INSERT INTO [t3125] VALUES (1247,'[١٢٠٩-] قلت: رجل تزوج امرأة ولها ابن رضيع من غيره، فأرادت أن ترضعه [ومنعها','1575','1040','0');
INSERT INTO [t3125] VALUES (1248,'[١٢١٠-] قلت: قال سفيان: امرأة كانت تحت رجل ففارقها ولها ابنة٤، [ثم تزوجت رجلا','1575','1040','0');
INSERT INTO [t3125] VALUES (1249,'[١٢١١-] قلت: قال سفيان إذا أحل له فرجها فوقع عليها، فهي مملوكة لسيدها الأول، والولد','1576','1040','0');
INSERT INTO [t3125] VALUES (1250,'[١٢١٢-] سئل أحمد عن امرأة أحلت جاريتها لأبيها فوطئها [ع-٦٠/أ] ؟','1577','1040','0');
INSERT INTO [t3125] VALUES (1251,'[١٢١٣-] سئل٤ سفيان عن شهادة رجل مكان رجل في الطلاق؟','1577','1040','0');
INSERT INTO [t3125] VALUES (1252,'[١٢١٤-] قلت١ لأحمد: سئل سفيان عن رجل خلا بامرأته وهي حائض؟','1580','1040','0');
INSERT INTO [t3125] VALUES (1253,'[١٢١٥-] قلت: سئل سفيان عن رجل تزوج امرأة على خادم١، ثم زوجها غلامه فولدت','1581','1040','0');
INSERT INTO [t3125] VALUES (1254,'[١٢١٦-] قلت: سئل سفيان عن رجل قال لامرأته: يوم أخرج من البلد فأمرك بيدك. فخرج','1582','1040','0');
INSERT INTO [t3125] VALUES (1255,'[١٢١٧-] قال: حدثنا إسحاق بن منصور المروزي قال: قلت: لأبي عبد الله أحمد بن محمد بن','1584','1040','0');
INSERT INTO [t3125] VALUES (1256,'[١٢١٨-] قلت: قال سفيان: إذا طلقها ثلاثا وهو مريض ثم صح ثم مات، فإنها ترثه، وإن','1585','1040','0');
INSERT INTO [t3125] VALUES (1257,'[١٢١٩-] قلت٢: قال سفيان في رجل تزوج امرأة ودخل بها، ثم تزوج أخرى فدخل بها وهي','1586','1040','0');
INSERT INTO [t3125] VALUES (1258,'[١٢٢٠-] قلت: قال: [فإن لم يكن دخل بالأخرى] ٤ فنكاح الأولى','1586','1040','0');
INSERT INTO [t3125] VALUES (1259,'[١٢٢١-] قلت: قال: فإن تزوج الابنة والأم في يوم٣ واحد, ودخل بهما في يوم واحد، فلا','1587','1040','0');
INSERT INTO [t3125] VALUES (1260,'[١٢٢٢-] قلت: قال سفيان في رجل تزوج امرأة وهي ثيب، ثم تزوج صبية فعمدت امرأته إلى','1588','1040','0');
INSERT INTO [t3125] VALUES (1261,'[١٢٢٣-] قلت:٢ قال سفيان: يكره أن يستأجر الظئر٣ إلى أن يفطم','1588','1040','0');
INSERT INTO [t3125] VALUES (1262,'[١٢٢٤-] قلت٢: يكره لبن ولد الزنى أن يرضع به؟','1590','1040','0');
INSERT INTO [t3125] VALUES (1263,'[١٢٢٥-] قلت١: قال سفيان في رجل تزوج امرأة وله ابنة من غيرها فزوجها رجلا فمات','1591','1040','0');
INSERT INTO [t3125] VALUES (1264,'[١٢٢٦-] قلت٤: قال سفيان في رجل طلق امرأته ثلاثا ثم تزوجها رجل بغير ولي ثم طلقها:','1592','1040','0');
INSERT INTO [t3125] VALUES (1265,'[١٢٢٧-] قلت٢: سئل سفيان عن رجل تزوج امرأة وهو يريد أن يحلها لزوجها، ثم بدا له','1594','1040','0');
INSERT INTO [t3125] VALUES (1266,'[١٢٢٨-] قلت: قال سفيان: وإذا كان عند الرجل مملوكة فطلقها تطليقتين فوقع عليها','1595','1040','0');
INSERT INTO [t3125] VALUES (1267,'[١٢٢٩-] قلت: قال سفيان في رجل قال لامرأته: كل امرأة أتزوجها ما','1596','1040','0');
INSERT INTO [t3125] VALUES (1268,'[١٢٣٠-] قلت: من قال إذا بدأ١ بالطلاق: وقع وإن بر؟','1599','1040','0');
INSERT INTO [t3125] VALUES (1269,'[١٢٣١-] قلت: قال سفيان في رجل قال لامرأته: لا تخرجي، قالت: والله لأخرجن، قال: إن','1599','1040','0');
INSERT INTO [t3125] VALUES (1270,'[١٢٣٢-] قلت: رجل قال لامرأته: إن لم أفعل كذا وكذا فأنت طالق، فيموت أو تموت،','1600','1040','0');
INSERT INTO [t3125] VALUES (1271,'[١٢٣٣-] قلت: قيل لسفيان: يجامع امرأته ما لم يحنث؟','1601','1040','0');
INSERT INTO [t3125] VALUES (1272,'[١٢٣٤-] قلت١: قال سفيان إذا وقت وقتا فحان ذلك الوقت وهما حيان، وقع الطلاق٢.','1602','1040','0');
INSERT INTO [t3125] VALUES (1273,'[١٢٣٥-] قلت: قال رجل حلف على امرأته فقال: إن فعلت كذا وكذا','1602','1040','0');
INSERT INTO [t3125] VALUES (1274,'[١٢٣٦-] قلت: قال سفيان: وإن كان شيء يملك الرجعة فإن الحنث عليه كما هو وإن١ سمى','1604','1040','0');
INSERT INTO [t3125] VALUES (1275,'[١٢٣٧-] قلت٣: قال سفيان في رجل طلق امرأته تطليقة، فقال مكانه: إن راجعتك فأنت طالق','1604','1040','0');
INSERT INTO [t3125] VALUES (1276,'[١٢٣٨-] قلت٣: قال: قال إبراهيم٤: رجل حلف أن لا يلبس من غزل امرأته فحاكت ثوبا','1605','1040','0');
INSERT INTO [t3125] VALUES (1277,'[١٢٣٩-] قلت١: سئل سفيان عن رجل تزوج امرأة فولدت لخمسة أشهر، فأقامت المرأة البينة','1606','1040','0');
INSERT INTO [t3125] VALUES (1278,'[١٢٤٠-] قلت٥: قال سفيان إن جاءت المملوكة بولد وزوجها حر','1606','1040','0');
INSERT INTO [t3125] VALUES (1279,'[١٢٤١-] قلت: قال سفيان, وإذا٣ كانت حرة تحت المملوك فجاء بولد وانتفى منه ألزق به','1607','1040','0');
INSERT INTO [t3125] VALUES (1280,'[١٢٤٢-] قلت١: سئل سفيان عن رجل قذف امرأته وهي صماء خرساء.','1608','1040','0');
INSERT INTO [t3125] VALUES (1281,'[١٢٤٣-] قلت: سئل سفيان عن رجل وطئ جاريته فولدت ولم يدع الولد ولم ينفه, قال١: ما','1609','1040','0');
INSERT INTO [t3125] VALUES (1282,'[١٢٤٤-] قلت٣: سئل سفيان عن رجل قال لامرأته: ما في بطنك ليس مني؟','1609','1040','0');
INSERT INTO [t3125] VALUES (1283,'[١٢٤٥-] قلت١: قال إبراهيم وعطاء: ٢ إذا ضرب الرجل في القذف ثم قذف امرأته، يضرب','1611','1040','0');
INSERT INTO [t3125] VALUES (1284,'[١٢٤٦-] قلت: سئل سفيان عن رجل رأى في حجر امرأته ولدا فقال: هذا ليس بولدي، ولا','1612','1040','0');
INSERT INTO [t3125] VALUES (1285,'[١٢٤٧-] قلت: فإن قال رجل لامرأته معها ولد: ليس هذا بولدك؟','1613','1040','0');
INSERT INTO [t3125] VALUES (1286,'[١٢٤٨-] قلت: قال الشعبي رجل قذف امرأته [بالزنى] ١ فقال: زنى بك فلان فلاعنته امرأته','1614','1040','0');
INSERT INTO [t3125] VALUES (1287,'[١٢٤٩-] قلت١: قال سفيان إذا وقت وقتا في الظهار فذهب ذلك الوقت، كأنه٢ [ع-٦٢/أ]','1615','1040','0');
INSERT INTO [t3125] VALUES (1288,'[١٢٥٠-] قلت: قال٤ رجل يظاهر فأخذ في الصوم فجامع بالليل،','1616','1040','0');
INSERT INTO [t3125] VALUES (1289,'[١٢٥١-] قلت: [قال:] ٢ فإن أطعم فجامع يطعم ليس هذا من نحو هذا، يعني الصوم.','1617','1040','0');
INSERT INTO [t3125] VALUES (1290,'[١٢٥٢-] قلت: قال سفيان: إذا قال الرجل لامرأته: أنت علي كظهر أمي إن قربتك؟','1618','1040','0');
INSERT INTO [t3125] VALUES (1291,'[١٢٥٣-] قلت: قال سفيان: لا يعلم الإيلاء إلا في الجماع، وهو الذي نأخذ به.','1620','1040','0');
INSERT INTO [t3125] VALUES (1292,'[١٢٥٤-] قلت٣: قال سفيان: رجل آلى من امرأته فمضت أربعة أشهر فقال الرجل بعد','1620','1040','0');
INSERT INTO [t3125] VALUES (1293,'[١٢٥٥-] قلت: قال سفيان في رجل آلى من امرأته وهو مريض فمضت أربعة أشهر وهو','1621','1040','0');
INSERT INTO [t3125] VALUES (1294,'[١٢٥٦-] قلت١: قال: فإذا آلى وهو صحيح فمضت أربعة أشهر وهو مريض ثم مات في','1623','1040','0');
INSERT INTO [t3125] VALUES (1295,'[١٢٥٧-] قلت٤: قال سفيان: إذا حلف الرجل ألا يجامع امرأته في بيت أو دار أو منزل ثم','1623','1040','0');
INSERT INTO [t3125] VALUES (1296,'[١٢٥٨-] قلت٢: قال: سألت سفيان عن رجل حلف أن لا يجامع امرأته إن شاء الله تعالى لا','1624','1040','0');
INSERT INTO [t3125] VALUES (1297,'[١٢٥٩-] قلت٢: قال سفيان في رجل غاب عن امرأته فجاءها نعي زوجها أنه قتل، فاعتدت٣','1625','1040','0');
INSERT INTO [t3125] VALUES (1298,'[١٢٦٠-] قلت١: المرأة تستدين على زوجها وهو غائب؟','1627','1040','0');
INSERT INTO [t3125] VALUES (1299,'[١٢٦١-] قلت١: قال: قلت لسفيان: مرت امرأة على رجل فحلف بالله لا يجامعها، ثم تزوجها','1628','1040','0');
INSERT INTO [t3125] VALUES (1300,'[١٢٦٢-] قلت١: قال: سألت سفيان عن رجل حلف أن لا يجامع امرأته أربعة أشهر، فمضى','1629','1040','0');
INSERT INTO [t3125] VALUES (1301,'[١٢٦٣-] قلت: قال سفيان في نصراني آلى من امرأته فمضى أربعة أشهر ثم أسلما بعد،','1630','1040','0');
INSERT INTO [t3125] VALUES (1302,'[١٢٦٤-] قلت٢: قال سفيان٣: إذا اختلعت المرأة من زوجها وهي مريضة إن اختلعت منه','1630','1040','0');
INSERT INTO [t3125] VALUES (1303,'[١٢٦٥-] قلت٢: قال سفيان٣: إذا أقر الرجل لامرأته بدين٤ في مرضه وقد فارقها في','1631','1040','0');
INSERT INTO [t3125] VALUES (1304,'[١٢٦٦-] قلت لأحمد: قال سفيان: إذا قالت المرأة في مرض الرجل: لم يطلقني زوجي، يسأل','1632','1040','0');
INSERT INTO [t3125] VALUES (1305,'[١٢٦٧-] قلت: سئل سفيان عن رجل أم قوما وفيهم رجل قد حلف أن','1632','1040','0');
INSERT INTO [t3125] VALUES (1306,'[١٢٦٨-] قلت٢: قال سفيان في نصراني طلق امرأته ثلاثا؟','1633','1040','0');
INSERT INTO [t3125] VALUES (1307,'[١٢٦٩-] قلت٤: الرجل يتزوج المرأة وهو عقيم لا يولد له؟','1634','1040','0');
INSERT INTO [t3125] VALUES (1308,'[١٢٧٠-] قلت: قال سفيان: إذا قالوا نزوجك إن جئت بالمهر إلى كذا وكذا وإلا فليس بيننا','1635','1040','0');
INSERT INTO [t3125] VALUES (1309,'[١٢٧١-] قلت٤: سئل سفيان عن الرجل يتزوج المرأة يشترط لها شرطا لازما لا يخرجها','1636','1040','0');
INSERT INTO [t3125] VALUES (1310,'[١٢٧٢-] قلت٤: قال سفيان في رجل عنده أربع نسوة فتزوج الخامسة فولدت أولادا؟','1637','1040','0');
INSERT INTO [t3125] VALUES (1311,'[١٢٧٣-] قلت١: رجل حلف على يمين لا يدري ما هي أطلاق أم غيره؟','1639','1040','0');
INSERT INTO [t3125] VALUES (1312,'[١٢٧٤-] قلت: الجمع بين ابنتي عم؟','1639','1040','0');
INSERT INTO [t3125] VALUES (1313,'[١٢٧٥-] قلت: ١ صيام العبد في التظاهر؟','1640','1040','0');
INSERT INTO [t3125] VALUES (1314,'[١٢٧٦-] قلت: ٣ من قال الفيء الجماع، فإن كان مريضا يفيء بلسانه؟','1640','1040','0');
INSERT INTO [t3125] VALUES (1315,'[١٢٧٧-] قلت: يتسرى العبد؟','1641','1040','0');
INSERT INTO [t3125] VALUES (1316,'[١٢٧٨-] قلت: العبد إذا أبق وله امرأة.','1641','1040','0');
INSERT INTO [t3125] VALUES (1317,'[١٢٧٩-] سئل أحمد عن رجل أعطى جارية له عبده على التسري١ فأبق عبده؟','1642','1040','0');
INSERT INTO [t3125] VALUES (1318,'[١٢٨٠-] سئل أحمد عن رجل زوج جاريته من عبده فأبق عبده؟','1642','1040','0');
INSERT INTO [t3125] VALUES (1319,'[١٢٨١-] قلت: قول علي [كرم الله وجهه] ١ حين جاءته المرأة فقال: اتقي الله واجلسي في','1643','1040','0');
INSERT INTO [t3125] VALUES (1320,'[١٢٨٢-] قلت٦: المرأة إذا ارتدت تبين من زوجها؟','1643','1040','0');
INSERT INTO [t3125] VALUES (1321,'[١٢٨٣-] قلت٣: النصرانية تسلم وهي تحت النصراني؟','1645','1040','0');
INSERT INTO [t3125] VALUES (1322,'[١٢٨٤-] قلت: على الأمة أن تنتقب؟ ٦','1645','1040','0');
INSERT INTO [t3125] VALUES (1323,'[١٢٨٥-] قلت: إذا أسر المسلم فتنصر، تبين منه امرأته؟','1646','1040','0');
INSERT INTO [t3125] VALUES (1324,'[١٢٨٦-] قال أحمد: يجبر على الطلاق في الإيلاء.٣','1646','1040','0');
