INSERT INTO [t3125] VALUES (1683,'[١٦٤٦-] قلت: سئل سفيان عن رجل جاوز الميقات فأهل، ثم جامع؟','2012','1404','0');
INSERT INTO [t3125] VALUES (1684,'[١٦٤٧-] قلت: قال سفيان في مملوك جاوز (الميقات) ١ (بغير) ٢ إحرام منعه مواليه أن يحرم حتى وقف','2013','1404','0');
INSERT INTO [t3125] VALUES (1685,'[١٦٤٨-] قلت: قال سفيان: سمعنا أن (الحرم) ٢ ميقات [أهل مكة] ٣ فمن خرج من الحرم ولم (يهل) ٤ أمرته','2014','1404','0');
INSERT INTO [t3125] VALUES (1686,'[١٦٤٩-] قلت: قال سفيان: إذا لبس قميصه عشرة أيام ناسيا لم أر عليه شيئا.٤','2016','1404','0');
INSERT INTO [t3125] VALUES (1687,'[١٦٥٠-] (قلت: قال سفيان: إذا وجد ريحه أو طعمه فلا أرى به بأسا) ٤ إذا مسته النار.٥','2017','1404','0');
INSERT INTO [t3125] VALUES (1688,'[١٦٥١-] قلت:٢ سئل سفيان عن رجل أهل لا يدري بحج أو (بعمرة) .٣','2018','1404','0');
INSERT INTO [t3125] VALUES (1689,'[١٦٥٢-] قلت: (قال) ٥ سفيان: إذا طاف الرجل بالصبي والمريض يجزي عنهما.٦','2019','1404','0');
INSERT INTO [t3125] VALUES (1690,'[١٦٥٣-] قلت: قال سفيان في رجل قدم مكة وقد فاته الحج: يطوف طوافين طوافا لحجه وطوافا لعمرته،٣','2021','1404','0');
INSERT INTO [t3125] VALUES (1691,'[١٦٥٤-] قلت: قال سفيان من رمى الجمرتين ولم يقم (عندهما) ٢ فليذبح شاة أو يتصدق بصاع.','2022','1404','0');
INSERT INTO [t3125] VALUES (1692,'[١٦٥٥-] قلت:٢ قال سفيان: إذا أدركه المساء بغير منى في اليومين ثم أتى منى لحاجة بعدما أمسى بغيرها،','2023','1404','0');
INSERT INTO [t3125] VALUES (1693,'[١٦٥٦-] قلت: قال سفيان: من رمى قبل الزوال يعيد الرمي؟ ٥','2024','1404','0');
INSERT INTO [t3125] VALUES (1694,'[١٦٥٧-] قلت: قال:١ ومن رمى (بالخذف) ٢ والمدر٣، لم يعد الرمي؟ ٤','2025','1404','0');
INSERT INTO [t3125] VALUES (1695,'[١٦٥٨-] قلت: قال سفيان: فيمن نسي [رمي] ١ الجمار (أو) ٢ تركه عمدا، فعليه كفارة واحدة؟ ٣','2026','1404','0');
INSERT INTO [t3125] VALUES (1696,'[١٦٥٩-] قلت: قال سفيان: في الذي يؤخر الرمي إلى الليل ناسيا أو متعمدا يهريق دما.١','2027','1404','0');
INSERT INTO [t3125] VALUES (1697,'[١٦٦٠-] قلت: قال سفيان في رجل أمر رجلا أن يرمي عنه وهو مريض فنسي [أن يرمي] ١ الذي أمر حتى','2028','1404','0');
INSERT INTO [t3125] VALUES (1698,'[١٦٦١-] قلت: قال سفيان: من خرج ولم يودع (فإن) ٤ عليه الكفارة،٥ قال عباد بن كثير:٦ ليس عليه','2028','1404','0');
INSERT INTO [t3125] VALUES (1699,'[١٦٦٢-] قلت: سئل سفيان تسافر المرأة مع زوج ابنتها؟','2029','1404','0');
INSERT INTO [t3125] VALUES (1700,'[١٦٦٣-] قلت: سئل سفيان عن امرأة طافت وهي حائض (الطواف) ٣ الواجب، أو على غير وضوء أو جنب','2030','1404','0');
INSERT INTO [t3125] VALUES (1701,'[١٦٦٤-] قلت: فإن كان أصاب أهله؟','2031','1404','0');
INSERT INTO [t3125] VALUES (1702,'[١٦٦٥-] قلت: قال سفيان: إذا لم تكمل سبعة١ فهي بمنزلة (من لم تطف تكون حراما) ٢ حتى ترجع، فتقضي','2032','1404','0');
INSERT INTO [t3125] VALUES (1703,'[١٦٦٦-] قلت: سئل سفيان عن رجل حج عن رجل فأصابه أذى من رأسه أو تطيب أو لبس ثوبا أو أصاب','2032','1404','0');
INSERT INTO [t3125] VALUES (1704,'[١٦٦٧-] قلت: قال سفيان: إذا قال الرجل: حجوا عني بألف درهم يحج بها رجال،١ وإذا قال: حجوا عني','2034','1404','0');
INSERT INTO [t3125] VALUES (1705,'[١٦٦٨-] قلت: وإذا قال: لفلان ألف درهم يحج بها، يعطى ألف درهم، فإن شاء حج وإن شاء لم يحج.','2036','1404','0');
INSERT INTO [t3125] VALUES (1706,'[١٦٦٩-] قلت:٣ قال سفيان: إذا أعطى الرجل دراهم يحج (عن)','2036','1404','0');
INSERT INTO [t3125] VALUES (1707,'[١٦٧٠-] قلت: قال سفيان: أكره أن يستأجر الرجل عن (أبويه) ٦ يحج','2037','1404','0');
INSERT INTO [t3125] VALUES (1708,'[١٦٧١-] قلت: [قال:] ٣ وإذا حج الرجل عن آخر أكره أن يأخذ ما فضل من حجه، شرطا كان عليه أو غير','2038','1404','0');
INSERT INTO [t3125] VALUES (1709,'[١٦٧٢-] قلت: قال سفيان: إن شاء صام الثلاثة أيام (متفرقة) ٢، والوصال أحب [إلي] .٣','2039','1404','0');
INSERT INTO [t3125] VALUES (1710,'[١٦٧٣-] قلت: قال سفيان فإن لم يصم في العشر فعليه دم؟ ٥','2039','1404','0');
INSERT INTO [t3125] VALUES (1711,'[١٦٧٤-] قلت: (سئل) ٢ سفيان (عن) ٣ رجل اشترى بدنة فنتجت؟','2040','1404','0');
INSERT INTO [t3125] VALUES (1712,'[١٦٧٥-] قلت: (سئل) ٢ سفيان (عن) ٣ رجل نحر فلم يطعم منه حتى سرق؟','2041','1404','0');
INSERT INTO [t3125] VALUES (1713,'[١٦٧٦-] قلت: سئل سفيان: عن رجل أهل بالحج والعمرة فقرن بين الطواف بالبيت والسعي بين الصفا','2041','1404','0');
INSERT INTO [t3125] VALUES (1714,'[١٦٧٧-] قلت: (قال) ٣ سفيان في المحصر إذا حل، ثم جامع قبل أن يحلق أو أصاب صيدا: (ليس) ٤ عليه','2042','1404','0');
INSERT INTO [t3125] VALUES (1715,'[١٦٧٨-] قلت: قال سفيان في الطيب كفارة، وفي الثياب كفارة، وفي الشعر كفارة؟','2043','1404','0');
INSERT INTO [t3125] VALUES (1716,'[١٦٧٩-] قلت: قال سفيان إذا (قرن) ١ بين الحج والعمرة فأصاب شيئا من طيب، أو شعر، أو لباس، فعليه','2044','1404','0');
INSERT INTO [t3125] VALUES (1717,'[١٦٨٠-] قلت: قال سفيان: (نحن نقول) ٢ إذا طاوعته امرأته فعلى كل واحد منهما كفارة،٣ وإن باشرها وليس','2045','1404','0');
INSERT INTO [t3125] VALUES (1718,'[١٦٨١-] قلت: قال سفيان: وأصحابنا يقولون: (إن) ٣ قبل فأمنى فبدنة، وإن قبل فأمذى فبقرة، وإن قبل [قبلة] ٤','2046','1404','0');
INSERT INTO [t3125] VALUES (1719,'[١٦٨٢-] قلت: سئل سفيان عن المحرم يجامع [أهله] ٢ في غير الفرج وينزل [قال] ٣: يقولون: عليه بدنة، وتم','2048','1404','0');
INSERT INTO [t3125] VALUES (1720,'[١٦٨٣-] قلت: سئل سفيان عن رجل أهل بعمرة في أشهر الحج ثم جامع أهله قبل أن يطوف بالبيت ثم أقام إلى','2049','1404','0');
INSERT INTO [t3125] VALUES (1721,'[١٦٨٤-] قلت:١ قال سفيان في رجل حج ثم ارتد ثم أسلم قال: يستأنف الحج، لا تجزيه حجته تلك.٢','2050','1404','0');
INSERT INTO [t3125] VALUES (1722,'[١٦٨٥-] قلت: قال سفيان: (إذا) ٢ لمس من شهوة فعليه دم.٣','2051','1404','0');
INSERT INTO [t3125] VALUES (1723,'[١٦٨٦-] قلت: قال سفيان في رجل قارن (قدم مكة) ٥ فطاف بالبيت وسعى بين الصفا والمروة لعمرته ثم','2051','1404','0');
INSERT INTO [t3125] VALUES (1724,'[١٦٨٧-] قلت: فإن أهل بحج وساق، أله أن يفسخه ويجعله عمرة؟','2053','1404','0');
INSERT INTO [t3125] VALUES (1725,'[١٦٨٨-] قلت: محرم تزوج؟','2053','1404','0');
INSERT INTO [t3125] VALUES (1726,'[١٦٨٩-] قلت: قال سفيان في رجل أرسل كلبه في الحل على [شيء] ٢ فطرده حتى دخل الحرم فأخذه:','2054','1404','0');
INSERT INTO [t3125] VALUES (1727,'[١٦٩٠-] قلت: قال: لو رمى شيئا في الحل (فدخلت) ٥ رميته الحرم فأصابت شيئا ضمن، لأن يده التي جنت.','2054','1404','0');
INSERT INTO [t3125] VALUES (1728,'[١٦٩١-] قلت:٢ قال سفيان: إذا طردت في الحرم شيئا فأصابه شيء قبل أن يقع أو حين (يقع) ٣ ضمنت،٤','2055','1404','0');
INSERT INTO [t3125] VALUES (1729,'[١٦٩٢-] قلت:٨ قال سفيان في شجرة أصلها في الحرم وأغصانها في الحل','2055','1404','0');
INSERT INTO [t3125] VALUES (1730,'[١٦٩٣-] قلت: وقال: شجرة أصلها في الحل وأغصانها في الحرم وعليها','2056','1404','0');
INSERT INTO [t3125] VALUES (1731,'[١٦٩٤-] قلت: سئل سفيان عن صيد رمي في الحل، فتحامل فدخل (في الحرم) ٢ فمات؟','2058','1404','0');
INSERT INTO [t3125] VALUES (1732,'[١٦٩٥-] [قلت: قال سفيان له إذا أحرم الرجل في بيته صيد فهو ضامن له.','2059','1404','0');
INSERT INTO [t3125] VALUES (1733,'[١٦٩٦-] قلت: قال سفيان فيمن طاف يوم النحر (لم ينو به طواف) ٣ الزيارة يجزيه منه.٤','2059','1404','0');
INSERT INTO [t3125] VALUES (1734,'[١٦٩٧-] (قلت: قال) :٣ (سئل) ٤ سفيان عن السنور٥ الأهلي؟','2060','1404','0');
INSERT INTO [t3125] VALUES (1735,'[١٦٩٨-] قلت: سئل سفيان: أرأيت إن كثر عليه القمل، أترى أن يلقيها ويكفر؟','2061','1404','0');
INSERT INTO [t3125] VALUES (1736,'[١٦٩٩-] قلت: قال سفيان في رجل أصاب صيدا وعنده طعام لا يتم جزاء الصيد: صام، لا يكون بعضه','2062','1404','0');
INSERT INTO [t3125] VALUES (1737,'[١٧٠٠-] قلت: [سئل سفيان] : ٤ أرأيت إن كان جزاؤه مدا أو','2062','1404','0');
INSERT INTO [t3125] VALUES (1738,'[١٧٠١-] قلت:٤ سئل سفيان عن الضفدع؟','2063','1404','0');
INSERT INTO [t3125] VALUES (1739,'[١٧٠٢-] قلت: سئل سفيان عن المغمى عليه هل يلبى عنه؟ ٣','2064','1404','0');
INSERT INTO [t3125] VALUES (1740,'[١٧٠٣-] قلت:٢ امرأة (أرادها) ٣ زوجها، فلبت بحج (أو عمرة) ؟ ٤','2066','1404','0');
INSERT INTO [t3125] VALUES (1741,'[١٧٠٤-] قلت: سئل سفيان إن أكل منه شيئا؟','2067','1404','0');
INSERT INTO [t3125] VALUES (1742,'[١٧٠٥-] قلت: قال سفيان: لا تجزي المهزولة في جزاء الصيد ولا في المتعة.','2068','1404','0');
INSERT INTO [t3125] VALUES (1743,'[١٧٠٦-] قلت: قال سفيان في قارن قدم [مكة] ١ فاته الحج ومعه بدنة يطوف طوافين طوافا لحجه وطوافا','2069','1404','0');
INSERT INTO [t3125] VALUES (1744,'[١٧٠٧-] قلت: قال سفيان: إذا حج رجل عن رجل ففاته الحج، فهو ضامن.','2070','1404','0');
INSERT INTO [t3125] VALUES (1745,'[١٧٠٨-] قلت:٧ قال سفيان: إذا حج رجل عن رجل ثم أقام بعد النفر،','2071','1404','0');
INSERT INTO [t3125] VALUES (1746,'[١٧٠٩-] قلت: قال سفيان: (أكره) ٥ أن يستأجر الرجل الرجل أن يحج عن','2072','1404','0');
INSERT INTO [t3125] VALUES (1747,'[١٧١٠-] قلت:١ قال الحسن في امرأة تريد أن تحج (فلم يأذن لها) ٢ زوجها: أن تحج بغير إذن زوجها وليس','2074','1404','0');
INSERT INTO [t3125] VALUES (1748,'[١٧١١-] قلت: قال: قلت [يعني] ١ لسفيان: إذا أوصى بحجة؟','2075','1404','0');
INSERT INTO [t3125] VALUES (1749,'[١٧١٢-] قال أحمد: الأربعة الأشهر: ذو القعدة وذو الحجة والمحرم ورجب.','2075','1404','0');
INSERT INTO [t3125] VALUES (1750,'[١٧١٣-] قال أحمد: إذا كان الناس محتاجين، فالصدقة أحب إلي من الحج، يعني من بعد الحج.١','2076','1404','0');
INSERT INTO [t3125] VALUES (1751,'[١٧١٤-] قال أحمد: (يولى) ٢ على أهل مكة رجل ليس من أهلها، هكذا السنة في ذلك٣.','2076','1404','0');
INSERT INTO [t3125] VALUES (1752,'[١٧١٥-] سئل أحمد [عن] ٢ من قال: لا يقصر إلا في حج أو عمرة أو غزو؟','2077','1404','0');
INSERT INTO [t3125] VALUES (1753,'[١٧١٦-] [قلت: لأحمد] ٤ إن النبي صلى الله عليه وسلم (أقام) ٥ بمكة','2078','1404','0');
INSERT INTO [t3125] VALUES (1754,'[١٧١٧-] قال أحمد: (وإذا) ٤ رمى عند طلوع الشمس','2080','1404','0');
INSERT INTO [t3125] VALUES (1755,'[١٧١٨-] (قلت:) ٨ امرأة طافت بالبيت ثم حاضت قبل أن تطوف بالصفا؟','2081','1404','0');
INSERT INTO [t3125] VALUES (1756,'[١٧١٩-] قال إسحاق: وأما الرجل الذي عليه الحج (وقد ذهب٢) ماله ولم يحج، وكان مفرطا أله أن يحج','2082','1404','0');
INSERT INTO [t3125] VALUES (1757,'[١٧٢٠-] سئل أحمد عن وصي حج عن الميت؟','2083','1404','0');
INSERT INTO [t3125] VALUES (1758,'[١٧٢١-] قلت لأحمد: يجب على المرأة إذا لم يحج بها محرمها (أن تعطيه٥)','2084','1404','0');
INSERT INTO [t3125] VALUES (1759,'[١٧٢٢-] (قلت:) ٢ فإن أبى زوجها (أن يدعها تخرج) ٣ وهي تجد محرما؟','2085','1404','0');
INSERT INTO [t3125] VALUES (1760,'[١٧٢٣-] سئل أحمد: عن رجل عزل٥ ألف درهم للحج فمات قبل','2085','1404','0');
INSERT INTO [t3125] VALUES (1761,'[١٧٢٤-] قلت لإسحاق: رجل أوصى [رجلا] ٤ أن يحج عنه بألف درهم، فاتجر الوصي بالألف فربح؟','2086','1404','0');
INSERT INTO [t3125] VALUES (1762,'[١٧٢٥-] سئل أحمد عن رجل قال: اعطوا فلانا دراهم يحج عني،','2086','1404','0');
INSERT INTO [t3125] VALUES (1763,'[١٧٢٦-] قال أحمد: إنما يكره أن يظلل المحرم إذا كان راكبا، وأما إذا كان على القرار٤ فلا بأس به.','2087','1404','0');
INSERT INTO [t3125] VALUES (1764,'[١٧٢٧-] قلت: إذا أهل [بالحج] ١ [من] ٢ دون الميقات ثم ترك إحرامه؟ ٣','2088','1404','0');
INSERT INTO [t3125] VALUES (1765,'كتاب الكفارات','2089','0','0');
INSERT INTO [t3125] VALUES (1766,'[١٧٢٨-] قلت:١، ٢حديث أبي لبابة٣ حين قال: أنخلع من مالي صدقة إلى الله عز وجل وإلى رسوله صلى الله','2090','1765','0');
INSERT INTO [t3125] VALUES (1767,'[١٧٢٩-] قلت: يكره أن يحلف الرجل بالمصحف؟ [ع-٧٩/أ]','2096','1765','0');
INSERT INTO [t3125] VALUES (1768,'[١٧٣٠-] قلت: [الرجل] ١ يقول حلفت وأقسمت؟','2097','1765','0');
INSERT INTO [t3125] VALUES (1769,'[١٧٣١-] قلت: من أقسم على رجل فلم يبره؟','2097','1765','0');
INSERT INTO [t3125] VALUES (1770,'[١٧٣٢-] قلت: من قال "يمينك على ما يصدقك عليه صاحبك؟ "١','2098','1765','0');
INSERT INTO [t3125] VALUES (1771,'[١٧٣٣-] قلت: فيمن جعلت مالها في سبيل الله عز وجل (وأعتقت) ١ جاريتها حديث امرأة من ذي أصبح؟ ٢','2100','1765','0');
INSERT INTO [t3125] VALUES (1772,'[١٧٣٤-] [قلت: فيمن جعل مملوكه حرا إن لم يفعل كذا وكذا؟','2101','1765','0');
INSERT INTO [t3125] VALUES (1773,'[١٧٣٥-] قلت: "إذا استلجج٤ أحدكم باليمين في أهله، فإنه آثم','2101','1765','0');
INSERT INTO [t3125] VALUES (1774,'[١٧٣٦-] قلت (من) ١ يحلف على أمور شتى أو على شيء واحد مرارا (وفي مجلس) ٢ أو في مجالس؟','2103','1765','0');
INSERT INTO [t3125] VALUES (1775,'[١٧٣٧-] قلت: من قال: علي عتق رقبة فحنث؟','2104','1765','0');
INSERT INTO [t3125] VALUES (1776,'[١٧٣٨-] قلت: من قال: كل حلال عليه حرام إذا كانت له امرأة؟','2104','1765','0');
INSERT INTO [t3125] VALUES (1777,'[١٧٣٩-] (قلت) :٢ افتداء اليمين؟','2105','1765','0');
INSERT INTO [t3125] VALUES (1778,'[١٧٤٠-] قلت: كم في كفارة اليمين من الطعام؟','2106','1765','0');
INSERT INTO [t3125] VALUES (1779,'[١٧٤١-] قلت: (يجزي المكاتب) ٢ في الرقبة الواجبة؟','2106','1765','0');
INSERT INTO [t3125] VALUES (1780,'[١٧٤٢-] قلت: يجزي ولد الزنى في الرقبة الواجبة؟','2107','1765','0');
INSERT INTO [t3125] VALUES (1781,'[١٧٤٣-] قلت: الذي يحنث متى يجب عليه الصوم ممن لا يجد الكفارة؟','2107','1765','0');
