INSERT INTO [t3125] VALUES (2010,'[١٩٧٤-] قلت: قال: قلت: -يعني لسفيان-: ما ترى٢ في السلف في البيض، والرمان؟','2356','1821','0');
INSERT INTO [t3125] VALUES (2011,'[١٩٧٥-] قلت: رجل سلف رجلا٣، مائة دينار، في شيء، فلما ذهب ليزن له الدنانير، قال: أعطني بها دراهم،','2357','1821','0');
INSERT INTO [t3125] VALUES (2012,'[١٩٧٦-] قلت: قال الثوري: رجل٢ باع من رجل سلعة بدينار، ثم جاءه بعد٣ فقال: أعطني بالدينار دراهم','2358','1821','0');
INSERT INTO [t3125] VALUES (2013,'[١٩٧٧-] قلت: إذا أسلفت رجلا ها هنا طعاما، فأعطاكه بأرض أخرى؟','2360','1821','0');
INSERT INTO [t3125] VALUES (2014,'[١٩٧٨-] قلت: رجل ابتاع رقيقا جملة، فإذا في أحدهم عيب؟','2360','1821','0');
INSERT INTO [t3125] VALUES (2015,'[١٩٧٩-] قلت: لا يبرأ من العيوب حتى يبين على حديث ابن عمر وعثمان؟','2362','1821','0');
INSERT INTO [t3125] VALUES (2016,'[١٩٨٠-] قلت: إذا كان لرجل٢ على قوم حق، يأخذ منهم من شاء بجميع حقه؟','2362','1821','0');
INSERT INTO [t3125] VALUES (2017,'[١٩٨١-] قلت: قال الثوري: ليس على الشريك ضمان، إذا كفل لشريكه عن غريم لهما؛ لأنه [ع-١٣٠/أ] لا','2363','1821','0');
INSERT INTO [t3125] VALUES (2018,'[١٩٨٢-] قلت: قال الثوري: الكتاب يكون فيه: (ومن قام بهذا الحق، فهو ولي بما فيه) فقام به رجل؟','2363','1821','0');
INSERT INTO [t3125] VALUES (2019,'[١٩٨٣-] قلت: قال الزهري: رجل اشترى غنما فنمت، ثم جاء أمر يرد','2363','1821','0');
INSERT INTO [t3125] VALUES (2020,'[١٩٨٤-] قال الثوري: اللبن والأولاد يرد في البيع الفاسد، إذا كان هذا نماء رد مع السلعة، والدراهم، والزرع،','2365','1821','0');
INSERT INTO [t3125] VALUES (2021,'[١٩٨٥-] قلت: الرجل يقول للرجل: قد كانت لك عندي وديعة، فدفعتها إليك؟','2366','1821','0');
INSERT INTO [t3125] VALUES (2022,'[١٩٨٦-] قلت: قال الثوري: كل إنسان استعار شيئا فرهنه بإذن صاحبه، فذهب الرهن، رد المستعير إلى','2367','1821','0');
INSERT INTO [t3125] VALUES (2023,'[١٩٨٧-] قلت: قال الثوري: رجل قال لرجل: استودعتك هذا الثوب، فقال: صدقت، ثم قال: استودعنيه رجل','2369','1821','0');
INSERT INTO [t3125] VALUES (2024,'[١٩٨٨-] قلت: إذا بعت ثوبا، فحل الأجل، فوجدته بعينه فقال: اشتره مني؟','2369','1821','0');
INSERT INTO [t3125] VALUES (2025,'[١٩٨٩-] قلت: يبيع١ الرجل شاته [ع-١٣١/أ] ممن يذبحها لصنمه؟','2370','1821','0');
INSERT INTO [t3125] VALUES (2026,'[١٩٩٠-] قلت: قال الثوري في نصراني أسلف نصرانيا في الخمر، ثم أسلم أحدهما؟','2370','1821','0');
INSERT INTO [t3125] VALUES (2027,'[١٩٩١-] قلت: قال الثوري: إذا أقرض أحدهما صاحبه خمرا، فإن أسلم المقرض، لم يأخذ شيئا، وإذا أسلم','2371','1821','0');
INSERT INTO [t3125] VALUES (2028,'[١٩٩٢-] قلت: رجل باع بقرة واشترط رأسها، ثم بدا له فأمسكها، فقضى زيد بشراء رأسها.٢','2371','1821','0');
INSERT INTO [t3125] VALUES (2029,'[١٩٩٣-] قلت: رجل أخذ عبدا آبقا، فأبق منه؟','2372','1821','0');
INSERT INTO [t3125] VALUES (2030,'[١٩٩٤-] قلت: قال الزهري:١ رجل اكترى من رجل بعيرا، فمات في الطريق؟','2373','1821','0');
INSERT INTO [t3125] VALUES (2031,'[١٩٩٥-] قلت: قال الزهري: رجل اكترى إلى مكة، واشترط عليه نفقته، قال: إن لم يعطه ورقا، فلا بأس به،','2374','1821','0');
INSERT INTO [t3125] VALUES (2032,'[١٩٩٦-] قلت: رجل استأجر سفينة فانكسرت أو غرقت؟','2377','1821','0');
INSERT INTO [t3125] VALUES (2033,'[١٩٩٧-] قلت: رجل دفع إلى حائك غزلا، فأفسد حياكته؟','2378','1821','0');
INSERT INTO [t3125] VALUES (2034,'[١٩٩٨-] قلت: قال الثوري في رجل سلف رجلا، دنانير ودراهم في طعام فوجد في الدراهم زيوفا؟','2379','1821','0');
INSERT INTO [t3125] VALUES (2035,'[١٩٩٩-] قلت: قال الثوري وإذا أسلفت رجلا عشرة دراهم في فرقين٣','2379','1821','0');
INSERT INTO [t3125] VALUES (2036,'[٢٠٠٠-] قلت: قال الثوري: رجل سلف دينارين في حلة بذرع معلوم، فوجد أحد الدينارين زيفا؟','2381','1821','0');
INSERT INTO [t3125] VALUES (2037,'[٢٠٠١-] قلت: قال الثوري في رجل قال لرجل: بعني ثوبك هذا بهذه المائة الدرهم، فلما دفع إليه الدراهم، إذا','2382','1821','0');
INSERT INTO [t3125] VALUES (2038,'[٢٠٠٢-] قلت: قال الثوري: رجل قال لرجل: بعني سلعتك بهذه الدراهم، وأراها إياه وهي طيب غير أنها','2383','1821','0');
INSERT INTO [t3125] VALUES (2039,'[٢٠٠٣-] قلت: قال الثوري في رجل١ يبتاع السلعة بدنانير كوفية، ثم جاء الشام فقيل له: بكم أخذتها؟','2384','1821','0');
INSERT INTO [t3125] VALUES (2040,'[٢٠٠٤-] قلت: قال الثوري: كل بيع اشتراه قوم جماعة، فلا يبيعوا بعضه مرابحة.','2385','1821','0');
INSERT INTO [t3125] VALUES (2041,'[٢٠٠٥-] قلت: قال الثوري: إذا اشتريا متاعا، ثم تقاوماه، فأخذ كل واحد منهما بعضه، فليس له أن يبيعه،','2385','1821','0');
INSERT INTO [t3125] VALUES (2042,'[٢٠٠٦-] قلت: قال الثوري: إذا رهن ثوبين بعشرة٤، فجاء بخمسة وقال: أعطني نصف الرهن، فلا يدفع إليه','2385','1821','0');
INSERT INTO [t3125] VALUES (2043,'[٢٠٠٧-] قلت: قال قتادة:٣ رجل ارتهن وليدة، فأبقت من الذي ارتهنها إلى سيدها، فأصابها؟ ٤','2386','1821','0');
INSERT INTO [t3125] VALUES (2044,'[٢٠٠٨-] قلت: رجل رهن [ع-١٣٢/أ] جارية له أن يصيبها؟','2388','1821','0');
INSERT INTO [t3125] VALUES (2045,'[٢٠٠٩-] قلت: جارية رهنت فولدت الولد من الرهن؟ ٢','2389','1821','0');
INSERT INTO [t3125] VALUES (2046,'[٢٠١٠-] قلت: قال الثوري: رجل رهن رهنا، فأعطاه الراهن٤ بعض الحق ثم هلك الرهن٥ يرد المرتهن ما','2389','1821','0');
INSERT INTO [t3125] VALUES (2047,'[٢٠١١-] قلت: قال الثوري: رجل دفع إلى رجل ألف درهم مضاربة، فجاء بألف درهم، فقال: هذا ربح وقد','2390','1821','0');
INSERT INTO [t3125] VALUES (2048,'[٢٠١٢-] قلت: رجل دفع إلى رجل مالا مضاربة، فقال صاحب المال: بالثلث، وقال الآخر: بالنصف؟','2391','1821','0');
INSERT INTO [t3125] VALUES (2049,'[٢٠١٣-] قلت: من كره أن يدفع إلى رجل مالا مضاربة، ويحمل له بضاعة؟','2392','1821','0');
INSERT INTO [t3125] VALUES (2050,'[٢٠١٤-] قلت: رجل دفع إلى رجل مالا مضاربة، وشرط عليه أن كل ما أعجبني مما تأتي به، أخذته بالثمن؟','2393','1821','0');
INSERT INTO [t3125] VALUES (2051,'[٢٠١٥-] قلت: رجل دفع إلى رجل مالا مضاربة، [ع-١٣٢/ب] على الشطر قال: اعمل فيه بما أراك الله عز','2394','1821','0');
INSERT INTO [t3125] VALUES (2052,'[٢٠١٦-] قلت: قال الثوري رجل دفع إلى رجل مالا مضاربة، وقال: ادان علي؟','2394','1821','0');
INSERT INTO [t3125] VALUES (2053,'[٢٠١٧-] قلت: قال: سألت الزهري عن رجل، قارض٢ رجلا فابتاع متاعا فوضعه في البيت، ثم قال لصاحب','2395','1821','0');
INSERT INTO [t3125] VALUES (2054,'[٢٠١٨-] قلت: شريكين٤ في سلعة، باع أحدهما السلعة، ولم يستأذن صاحبه؟','2396','1821','0');
INSERT INTO [t3125] VALUES (2055,'[٢٠١٩-] قلت: إذا أعطاه العروض مضاربة، له أجر مثله؟','2397','1821','0');
INSERT INTO [t3125] VALUES (2056,'[٢٠٢٠-] قلت: قال الثوري: رجل دفع إلى رجل٤ ألف درهم، مضاربة على النصف، ثم مكث يوما، ثم دفع','2397','1821','0');
INSERT INTO [t3125] VALUES (2057,'[٢٠٢١-] قلت: حديث شريح في شاة، باعها أحدهما صاحبه بعشرين درهما، وهو شريكه فيها، فباعها','2398','1821','0');
INSERT INTO [t3125] VALUES (2058,'[٢٠٢٢-] قلت: قال قتادة: رجل أخذ من رجل مالا مضاربة، فعمل فيه١ وخلط٢ فيه مالا،٣ ولم يعلم الآخر،','2399','1821','0');
INSERT INTO [t3125] VALUES (2059,'[٢٠٢٣-] قلت: قال الثوري: كل بيع ليس فيه كيل، ولا وزن، ولا عدد فجذاذه، وحمله، ونقصه على المشتري،','2400','1821','0');
INSERT INTO [t3125] VALUES (2060,'[٢٠٢٤-] قلت: قال الثوري: إذا قال الرجل٤ للرجل: بعتك هذه النخلة، فجداده على المشتري.٥','2401','1821','0');
INSERT INTO [t3125] VALUES (2061,'[٢٠٢٥-] قلت: من كره أن يبيع النخل، ويستثنى منه كيلا معلوما؟','2402','1821','0');
INSERT INTO [t3125] VALUES (2062,'[٢٠٢٦-] قلت: كم الجائحة؟ ٥','2402','1821','0');
INSERT INTO [t3125] VALUES (2063,'[٢٠٢٧-] قلت: إذا أفلس الرجل، فوجد الرجل من متاعه النصف، أو الثلث، أو الربع اقتضى من ثمنه شيئا؟','2404','1821','0');
INSERT INTO [t3125] VALUES (2064,'[٢٠٢٨-] قلت: الموت والإفلاس واحد؟','2406','1821','0');
INSERT INTO [t3125] VALUES (2065,'[٢٠٢٩-] قلت: رجل تزوج امرأة، وهو مفلس ولم تعلم المرأة يفرق بينهما؟','2407','1821','0');
INSERT INTO [t3125] VALUES (2066,'[٢٠٣٠-] قلت: رجل أحال رجلا على آخر فلم يقضه شيئا؟','2408','1821','0');
INSERT INTO [t3125] VALUES (2067,'[٢٠٣١-] قلت: قال الثوري: درهم بين رجلين، قال أحدهما: لي نصفه، وقال الآخر: لي كله. قال ابن أبي ليلى،','2410','1821','0');
INSERT INTO [t3125] VALUES (2068,'[٢٠٣٢-] قلت: إذا أذن الرجل لعبده في التزويج، فعلى من المهر؟','2411','1821','0');
INSERT INTO [t3125] VALUES (2069,'[٢٠٣٣-] قلت: بيع البصل، والجزر، والفجل، والبطيخ، وكل شيء يكون تحت الأرض تكرهه؟','2412','1821','0');
INSERT INTO [t3125] VALUES (2070,'[٢٠٣٤-] قلت: يشارك المسلم اليهودي والنصراني؟','2412','1821','0');
INSERT INTO [t3125] VALUES (2071,'[٢٠٣٥-] قلت: يؤاجر نفسه منه؟','2413','1821','0');
INSERT INTO [t3125] VALUES (2072,'[٢٠٣٦-] قلت: قال: سألت الثوري عن رجل باع شفعة لرجل من آخر إلى أجل، فجاء الشفيع، فقال: أنا آخذها','2414','1821','0');
INSERT INTO [t3125] VALUES (2073,'[٢٠٣٧-] قلت: قال الثوري [ع-١٣٤/أ] في عبد بين رجلين، أراد أحدهما أن يبيع وأبى الآخر؟','2415','1821','0');
INSERT INTO [t3125] VALUES (2074,'[٢٠٣٨-] قلت: قال الثوري: إن٢ باع الرجل٣ رجلا ثوبا بثوب، أو عرضا بعرض فتشاكسا،٤ فقال أحدهما:','2416','1821','0');
INSERT INTO [t3125] VALUES (2075,'[٢٠٣٩-] قلت: قال الثوري: وإن كان عرضا، بدنانير أو دراهم، يكون عدلا بينهما، لا يدفع إليه العرض حتى','2417','1821','0');
INSERT INTO [t3125] VALUES (2076,'[٢٠٤٠-] قلت: سئل الثوري عن رقيق العجم يخرجون من البحر، أو من غيره هل يباعون من اليهود','2418','1821','0');
INSERT INTO [t3125] VALUES (2077,'[٢٠٤١-] قلت: قال الثوري: وإن٢ كانوا على دين مثل الهند، والزنج ونحوهم، فإن المسلم [ع-١٣٤/ب] لا','2419','1821','0');
INSERT INTO [t3125] VALUES (2078,'[٢٠٤٢-] قلت: قال الثوري: إذا كان العجم صغارا، عند المسلم صلى عليهم، وإن لم يكن خرج بهم من','2421','1821','0');
INSERT INTO [t3125] VALUES (2079,'[٢٠٤٣-] قلت: لا يجبرون على الإسلام إذا كان معه أبواه، أو أحدهما؟','2422','1821','0');
INSERT INTO [t3125] VALUES (2080,'[٢٠٤٤-] قلت: قال الثوري: رجل باع بيعا، فقال: لقد بعتك، وأنا صغير، فقال المبتاع بعتني وأنت بالغ، ولم','2423','1821','0');
INSERT INTO [t3125] VALUES (2081,'[٢٠٤٥-] قال أحمد:٤ إذا اختلفا في البيع، وهو قائم فقال ذا: بعتك','2423','1821','0');
INSERT INTO [t3125] VALUES (2082,'[٢٠٤٦-] قلت: إذا قال الرجل لعبده: أصبعك حر؟','2425','1821','0');
INSERT INTO [t3125] VALUES (2083,'[٢٠٤٧-] قلت: قال إياس بن معاوية١ في بيع الخلاص: إذا باعه، وهو يرى أنه له، ثم جاء رجل بعد٢ ذلك،','2426','1821','0');
INSERT INTO [t3125] VALUES (2084,'[٢٠٤٨-[] ظ-٦٠/ب] قلت: اشترى ثلاث نسوة دارا، فقلن: هي للمطلقة، والأيم، والمحتاجة منا، فماتت واحدة','2427','1821','0');
INSERT INTO [t3125] VALUES (2085,'[٢٠٤٩-] قلت: قال سفيان: رجل سلف، فسمى الأجل، ولم يسم المكان؟','2428','1821','0');
INSERT INTO [t3125] VALUES (2086,'[٢٠٥٠-] قلت: قال سفيان: رجل سلف في طعام إلى أجل يوفيه بمكة فلقيه بغير مكة، فقال: خذ مني طعامك،','2429','1821','0');
INSERT INTO [t3125] VALUES (2087,'[٢٠٥١-] قلت: سئل عن رجل اشترى من رجل طعاما، بجدة يدا بيد، فلقيه بمكة، فقال: أحمل طعامي، وأوفيك','2429','1821','0');
INSERT INTO [t3125] VALUES (2088,'[٢٠٥٢-] قلت: أرأيت إن حمله صاحب الطعام الذي اشتراه منه فوافا الطعام بمكة، من غير أن يأمره','2430','1821','0');
INSERT INTO [t3125] VALUES (2089,'[٢٠٥٣-] قلت: رجل سلف مائة درهم، في مائة فرق، إلى أجل مسمى وقال: أنقدك الآن خمسين، وخمسين إلى','2431','1821','0');
INSERT INTO [t3125] VALUES (2090,'[٢٠٥٤-] قلت: إذا أسلف الرجل في شيء، فكان في دراهمه زيف؟','2432','1821','0');
INSERT INTO [t3125] VALUES (2091,'[٢٠٥٥-] قلت: الرجل يسلف٢ ثلاثمائة درهم في ثلاثمائة فرق في أصناف شتى، مائة فرق حنطة، ومائة [ع-','2432','1821','0');
INSERT INTO [t3125] VALUES (2092,'[٢٠٥٦-] قلت: رجل سلف إلى رجل على أن يوفيه بمكان كذا وكذا، فلقيه دون مكانه، فقال: خذ مني طعامك،','2433','1821','0');
INSERT INTO [t3125] VALUES (2093,'[٢٠٥٧-] قلت: رجل اشترى قثاء وزنا، فوزنه البائع، فقال للمشتري: قد وزنته، هو كذا وكذا، ولم يحضر','2434','1821','0');
INSERT INTO [t3125] VALUES (2094,'[٢٠٥٨-] قلت: قال: إذا ابتعت ثوبا بشرط، فسم أياما، ولا تذهب به بغير شرط.','2435','1821','0');
INSERT INTO [t3125] VALUES (2095,'[٢٠٥٩-] قلت: قال المشتري: ابتعت٢ وشرطت لي الخيار إلى غد،','2435','1821','0');
INSERT INTO [t3125] VALUES (2096,'[٢٠٦٠-] قلت: قال البائع بعتك بالنقد، وقال المشتري: اشتريت٢ منك إلى أجل٣، فالقول٤ قول البائع.','2436','1821','0');
INSERT INTO [t3125] VALUES (2097,'[٢٠٦١-] قلت: فإن أقاما جميعا البينة، أخذ بينة الذي يدعي النسيئة؟','2437','1821','0');
INSERT INTO [t3125] VALUES (2098,'[٢٠٦٢-] قلت: وإن٣ قال المشتري: اشتريت بشرط. وقال البائع: لم أشترطه؟','2437','1821','0');
INSERT INTO [t3125] VALUES (2099,'[٢٠٦٣-] قلت: إن قال البائع: بعت، وأنا عليه بالخيار يومين أو ثلاثة، وأنكر المشتري؟','2438','1821','0');
INSERT INTO [t3125] VALUES (2100,'[٢٠٦٤-] قلت: إن١ ادعى البائع أنه باع٢ بنقد، وقال المشتري بنسيئة، فأخذت بقول البائع يحلف البائع؟ ٣','2438','1821','0');
INSERT INTO [t3125] VALUES (2101,'[٢٠٦٥-] قلت: رجل دفع إلى رجل سلعة يبيعها، فباعها وانتقد الثمن ودفعه إلى صاحبه الذي أمره، ثم ادعى','2438','1821','0');
INSERT INTO [t3125] VALUES (2102,'[٢٠٦٦-] قلت: رجلان اشترى أحدهما من الآخر، سلعة بسلعة، فوجد أحدهما بسلعته عيبا؟','2439','1821','0');
INSERT INTO [t3125] VALUES (2103,'[٢٠٦٧-] قلت: كان ابن أبي ليلى إذا جاءه الرجل وقد اشترى سلعة من رجل، فادعى عيبا، ولم يكن للبائع بينة','2440','1821','0');
INSERT INTO [t3125] VALUES (2104,'[٢٠٦٨-] قلت: قال ابن أبي ليلى في الرجل يشتري العبد الآبق فأبق منه؟','2441','1821','0');
INSERT INTO [t3125] VALUES (2105,'[٢٠٦٩-] قلت: سئل عن رجل ابتاع عبدا آبقا، فجاء المشتري، فأقام البينة أنه باعه عبدا آبقا، وقد أبق العبد من','2443','1821','0');
INSERT INTO [t3125] VALUES (2106,'[٢٠٧٠-] قلت: رجل٥ ابتاع عبدا فكاتبه، فوجد به عيبا، بعد ما','2443','1821','0');
INSERT INTO [t3125] VALUES (2107,'[٢٠٧١-] قلت: لو أن رجلا باع جارية، أو باع شاة، فولدت، أو نخلا له ثمرة، فوجد به عيبا، أو٣ استحق: أخذ','2444','1821','0');
INSERT INTO [t3125] VALUES (2108,'[٢٠٧٢-] قلت: الصبي يسرق ويشرب الخمر، ويأبق؟','2445','1821','0');
INSERT INTO [t3125] VALUES (2109,'[٢٠٧٣-] قلت: قال ابن أبي ليلى في الرجل يبني البناء في الأرض بغير إذن أهلها، ثم يؤاجرها؟','2446','1821','0');
INSERT INTO [t3125] VALUES (2110,'[٢٠٧٤-] قلت: رجل باع من رجل١ جارية بمائة دينار، فأخذ بها منه ألف درهم، ثم وجد بالجارية عيبا،','2447','1821','0');
INSERT INTO [t3125] VALUES (2111,'[٢٠٧٥-] قلت: هل يجوز بيع الصبي؟','2448','1821','0');
INSERT INTO [t3125] VALUES (2112,'[٢٠٧٦-] قلت: قال: سألت الأوزاعي عن شراء١ الخبز بالدقيق، والحنطة؟','2449','1821','0');
INSERT INTO [t3125] VALUES (2113,'[٢٠٧٧-] قلت: فاشتراء١ الدقيق بالقمح، كيلا بكيل؟','2450','1821','0');
INSERT INTO [t3125] VALUES (2114,'[٢٠٧٨-] قلت: قال: سألت الأوزاعي: عن النوى بالتمر، صاعا بصاع؟ ١','2452','1821','0');
INSERT INTO [t3125] VALUES (2115,'[٢٠٧٩-] قلت: سألت الأوزاعي عن الشعر بالصوف رطلا برطلين؟','2453','1821','0');
INSERT INTO [t3125] VALUES (2116,'[٢٠٨٠-] قلت: قال سفيان في رجل استقرض من رجل دانق فلوس،٢ وعشرون٣ فلسا بدانق، فصار عشرة','2453','1821','0');
INSERT INTO [t3125] VALUES (2117,'[٢٠٨١-] قلت: إذا قال: بعني فلوسا بدانق، فله دانق فضة زاد أو نقص؟','2454','1821','0');
INSERT INTO [t3125] VALUES (2118,'[٢٠٨٢-] قلت: سئل [ع-١٣٧/أ] عن رجل قال لرجل: بعني فلوسا بدرهم، والدانق٣: ستة عشر فلسا؟','2454','1821','0');
INSERT INTO [t3125] VALUES (2119,'[٢٠٨٣-] قلت: رجل ابتاع ثمانية دراهم بدينار، فوجد فيها أربعة زيوفا','2455','1821','0');
INSERT INTO [t3125] VALUES (2120,'[٢٠٨٤-] قلت: رجل ابتاع من رجل فضة، واشترط عليه أن يصوغ له خاتما؟','2458','1821','0');
INSERT INTO [t3125] VALUES (2121,'[٢٠٨٥-] قلت: قال: سئل سفيان عن السيف المحلى والخاتم نسيئة فكرهه.','2458','1821','0');
INSERT INTO [t3125] VALUES (2122,'[٢٠٨٦-] قلت: سئل سفيان عن الخبز بالحنطة، والدقيق بالحنطة نسيئة؟','2460','1821','0');
INSERT INTO [t3125] VALUES (2123,'[٢٠٨٧-] قلت: سئل سفيان عن تمرة بتمرتين، وتمرة بتمرة؟','2460','1821','0');
INSERT INTO [t3125] VALUES (2124,'[٢٠٨٨-] قلت: قال الثوري في رجل قال لرجل: بعني نصف٢ دارك مما يلى داري؟','2461','1821','0');
INSERT INTO [t3125] VALUES (2125,'[٢٠٨٩-] قلت: قال الثوري: إذا صرفت بدينار عشرة ونصفا، فلا تأخذ','2461','1821','0');
INSERT INTO [t3125] VALUES (2126,'[٢٠٩٠-] قلت: قال ابن سيرين في رجل كانت عليه مائة دينار، وازنة، فأسلفنى مائة دينار ناقصة؟','2462','1821','0');
INSERT INTO [t3125] VALUES (2127,'[٢٠٩١-] قلت: سئل سفيان: إن هو كسر طنبور١ معاهد؟','2463','1821','0');
INSERT INTO [t3125] VALUES (2128,'[٢٠٩٢-] قلت: قال سفيان: من كسر شيئا صحيحا: فقيمته صحيحا؟','2465','1821','0');
INSERT INTO [t3125] VALUES (2129,'[٢٠٩٣-] قلت: قال٢: فإذا كسر الذهب، فقيمته بالفضة، وإذا كسر','2465','1821','0');
INSERT INTO [t3125] VALUES (2130,'[٢٠٩٤-] قلت: سئل سفيان عن الغنم تقع في الزرع؟','2466','1821','0');
INSERT INTO [t3125] VALUES (2131,'[٢٠٩٥-] قلت: سئل سفيان: إذا أخذ عبدا ليجتعل عليه فأبق منه؟','2467','1821','0');
INSERT INTO [t3125] VALUES (2132,'[٢٠٩٦-] قلت: السكنى، والغلة، والخدمة يرجع؟','2467','1821','0');
INSERT INTO [t3125] VALUES (2133,'[٢٠٩٧-] قلت: قال الحسن، وابن سيرين، في رجل له على رجل دين، فقضاه من الربا والقمار؟','2468','1821','0');
INSERT INTO [t3125] VALUES (2134,'[٢٠٩٨-] قلت: إذا أحالك رجل على آخر، وأنت تعلم أنه ربا، فلا بأس به؟','2469','1821','0');
INSERT INTO [t3125] VALUES (2135,'[٢٠٩٩-] قلت: أبيع السلعة ممن أعلم أنه يدلسها؟','2469','1821','0');
INSERT INTO [t3125] VALUES (2136,'[٢١٠٠-] قلت: الرجل يمر بالعشار١ فيقول: تعطيني، أو أحل متاعك فيعطيه شيئا، ولا يحل متاعه؟','2470','1821','0');
INSERT INTO [t3125] VALUES (2137,'[٢١٠١-] قلت: امرأة أسلمت غلاما لها في عمل، فماتت فورثتها، أختها','2470','1821','0');
INSERT INTO [t3125] VALUES (2138,'[٢١٠٢-] قلت: قال: البيع يقطع من المستكري كراه، وعلى المستأجر أجره، قال أحمد: ليس هذا بشيء.','2471','1821','0');
INSERT INTO [t3125] VALUES (2139,'[٢١٠٣-] قلت: قال سفيان: المرأة إذا ادعت ولدا، لم تصدق إلا أن تجىء ببينة أنها ولدته.','2472','1821','0');
INSERT INTO [t3125] VALUES (2140,'[٢١٠٤-] قلت: قال عمر بن عبد العزيز: إذا كان خادم ومنزل لم يبع ماله، ولم يسجن إذا لم يكن له غير هذا.','2474','1821','0');
INSERT INTO [t3125] VALUES (2141,'[٢١٠٥-] قلت: قال سفيان: إذا فلس القاضي الرجل، فليس له٣ بيع، ولا صدقة، ولا عتق.','2474','1821','0');
INSERT INTO [t3125] VALUES (2142,'[٢١٠٦-] قلت: قال سفيان في رجل ابتاع متاعا٣ من رجل وقلبه، ونظر إليه واشتراه إلى أجل، فقال٤: أقبضه','2475','1821','0');
INSERT INTO [t3125] VALUES (2143,'[٢١٠٧-] قلت: قال سفيان: وإن اشترى١ بنقد ولم يقبض، فجاءه ينقده من الغد، وقد مات البائع، فهو أحق به،','2476','1821','0');
INSERT INTO [t3125] VALUES (2144,'[٢١٠٨-] قلت: سئل سفيان عن رجلين أخوين ورثا صكا من أبيهما فذهبا إلى الذي عليه الحق، فتقاضياه،','2477','1821','0');
INSERT INTO [t3125] VALUES (2145,'[٢١٠٩-] قلت: قال سفيان في رجلين شريكين لهما على رجل مائة درهم، فوهب أحدهما نصيبه من المائة','2479','1821','0');
INSERT INTO [t3125] VALUES (2146,'[٢١١٠-] قلت: سئل سفيان عن رجلين باعا من رجل، طعاما بمائة درهم وكتبا الصك جميعا باسميهما، فأخذ','2479','1821','0');
INSERT INTO [t3125] VALUES (2147,'[٢١١١-] قلت: الزبد١ باللبن؟','2480','1821','0');
INSERT INTO [t3125] VALUES (2148,'[٢١١٢-] قلت: الزبد بالرايب؟','2480','1821','0');
INSERT INTO [t3125] VALUES (2149,'[٢١١٣-] قلت: قال سفيان: أكره سمن البقر، بسمن الغنم اثنين بواحد؟','2481','1821','0');
INSERT INTO [t3125] VALUES (2150,'[٢١١٤-] قلت: سئل سفيان عن سيف بسيفين، وقدح بقدحين، وسكين بسكينين، وطست بطستين، يدا بيد واحدا','2482','1821','0');
INSERT INTO [t3125] VALUES (2151,'[٢١١٥-] قلت: قال١: السلف في الفلوس لا يرون به بأسا، يقولون: يجوز برؤوسها؟','2483','1821','0');
INSERT INTO [t3125] VALUES (2152,'[٢١١٦-] قلت: قال سفيان: وأناس لا يرون به٢ بأسا، الإبرة بالإبرتين، والفلس بالفلسين، والسيف بالسيفين.','2484','1821','0');
INSERT INTO [t3125] VALUES (2153,'[٢١١٧-] قلت لأحمد٣: سئل عن السمن بالزبد اثنان بواحد، قال سفيان: إذا خرج من الزبد، مثله، فلا بأس به،','2485','1821','0');
INSERT INTO [t3125] VALUES (2154,'[٢١١٨-] قلت: سئل سفيان: [ع-١٤١/أ] يكره أن يسلف في الرطب في غير حينه وليس في أيدى الناس منه','2486','1821','0');
INSERT INTO [t3125] VALUES (2155,'[٢١١٩-] قلت: رجل سلف مائة درهم في مائة قفيز على أن يضع الدراهم على يد العدل٣، فإذا جاء٤ الأجل،','2487','1821','0');
INSERT INTO [t3125] VALUES (2156,'[٢١٢٠-] قلت: رجل سلف رجلا١ دراهم على أن يعطيه من أندرة٢ هذا طعاما إلى عشرة أيام؟','2488','1821','0');
INSERT INTO [t3125] VALUES (2157,'[٢١٢١-] قلت: الرجل يقول: أسلفك في طعام أرضك التي بمكان كذا وكذا؟','2489','1821','0');
INSERT INTO [t3125] VALUES (2158,'[٢١٢٣-] قلت: رجل أسلف مائة درهم في حنطة إلى أجل، فلما حل الأجل، جاء يكتاله منه، فقامت البينة أن','2490','1821','0');
INSERT INTO [t3125] VALUES (2159,'[٢١٢٤-] قلت: إذا أسلفت رجلا مائة درهم كل درهم٤ في قفيز، ولم','2490','1821','0');
INSERT INTO [t3125] VALUES (2160,'[٢١٢٥-] قلت: سئل سفيان: أتكره أن أشتري عصيرا فأتخذه خلا؟','2492','1821','0');
INSERT INTO [t3125] VALUES (2161,'[٢١٢٦-] قلت: نصراني أسلف نصرانيا في خمر، فأسلم الذي سلف وأبى الآخر أن يسلم؟','2493','1821','0');
INSERT INTO [t3125] VALUES (2162,'[٢١٢٧-] قلت: نصراني أقرض نصرانيا خمرا، فأسلم الذي أقرض؟','2494','1821','0');
INSERT INTO [t3125] VALUES (2163,'[٢١٢٨-] قلت: فإن أسلم المستقرض، ولم يسلم المقرض؟','2494','1821','0');
INSERT INTO [t3125] VALUES (2164,'[٢١٢٩-] قلت: رجلان أحدهما نصراني، والآخر مسلم بينهما عنب، فعصره النصراني خمرا، قال: يضمن له','2495','1821','0');
INSERT INTO [t3125] VALUES (2165,'[٢١٣٠-] قلت: الراعي المشترك يجىء بالجلد، فيقول: قد مات؟','2495','1821','0');
INSERT INTO [t3125] VALUES (2166,'[٢١٣١-[] ظ-٦٣/ب] قلت: سئل عن رجل أجر غلامه، أشهرا وأخذ الكراء، ثم بدا لصاحب الغلام أن يأخذ','2497','1821','0');
INSERT INTO [t3125] VALUES (2167,'[٢١٣٢-] قلت: قال سفيان: كل صناع١ عمل لك، ففرغ منه، صباغ، أو حائك، فسرق، فليس عليك شيء حتى','2498','1821','0');
INSERT INTO [t3125] VALUES (2168,'[٢١٣٣-] قلت: والسفينة٣ إذا غرقت؟','2498','1821','0');
INSERT INTO [t3125] VALUES (2169,'[٢١٣٤-] قلت: سئل: أرأيت [ع-١٤٢/ب] لو بنى لي بناء٢، فوقع قبل أن يسلمه إلي؟','2499','1821','0');
INSERT INTO [t3125] VALUES (2170,'[٢١٣٥-] قلت: قال: سمعت الأوزاعي قال: إذا اكترى دابة فجاوز بها الوقت، فإن سلمت الدابة كان له كراء','2500','1821','0');
INSERT INTO [t3125] VALUES (2171,'[٢١٣٦-] قلت: قال: سألت سفيان عن رجل تكارى حمارا يوما، بدرهم على أن لا يخرجه من الكوفة فأخرجه؟','2500','1821','0');
INSERT INTO [t3125] VALUES (2172,'[٢١٣٧-] قلت: قال سفيان: إذا اكتريت إلى الري٢ ففرغت من الكراء؟','2501','1821','0');
INSERT INTO [t3125] VALUES (2173,'[٢١٣٨-] قلت: سئل سفيان عن الإجارة الفاسدة فيها ضمان؟','2503','1821','0');
INSERT INTO [t3125] VALUES (2174,'[٢١٣٩-] قلت: رجل قال لرجل: أكرني دابتك إلى مكان كذا وكذا، فذهب بها إلى المكان الذي أكراه، فلم يحمل','2504','1821','0');
INSERT INTO [t3125] VALUES (2175,'[٢١٤٠-] قلت: إذا اكترى دابة فذهب بها، فجاء فقال: قد ماتت في بعض الطريق، فالقول قول المستكري؟','2505','1821','0');
INSERT INTO [t3125] VALUES (2176,'[٢١٤١-] قلت: رجل اكترى دابة من مكة إلى جدة بكذا وكذا، فإن ذهب من جدة إلى عسفان١، فبكذا وكذا؟','2506','1821','0');
INSERT INTO [t3125] VALUES (2177,'[٢١٤٢-] قلت: إذا استأجر الرجل أجيرا شهرا معلوما، فجاء في نصف ذلك الشهر؟','2507','1821','0');
INSERT INTO [t3125] VALUES (2178,'[٢١٤٣-] قلت: رجل اكترى غلاما، فقال: فر مني؟','2507','1821','0');
INSERT INTO [t3125] VALUES (2179,'[٢١٤٤-] قلت١: رجل استأجر من رجل غلاما، فقال المستأجر: مرض عندي فلم يعمل، وقال الغلام: قد','2508','1821','0');
INSERT INTO [t3125] VALUES (2180,'[٢١٤٥-] قلت: الرجل يؤاجر داره كل شهر بعشرة دراهم؟','2509','1821','0');
INSERT INTO [t3125] VALUES (2181,'[٢١٤٦-] قلت: قال سفيان: كل أجير استأجرته أو دار بشيء يكال، أو','2510','1821','0');
INSERT INTO [t3125] VALUES (2182,'[٢١٤٧-] قلت: إذا استأجرت إنسانا يوما، فذهب ذلك اليوم، فليس عليه غيره.','2512','1821','0');
INSERT INTO [t3125] VALUES (2183,'[٢١٤٨-] قلت: الخياط يدفع إليه الثوب ليخيطه اليوم بدرهم وغدا بنصف درهم؟','2512','1821','0');
INSERT INTO [t3125] VALUES (2184,'[٢١٤٩-] قلت: قال سفيان: إذا قال الرجل للرجل: ابتع لي ثوبا دابة شيئا لم يصفه، فليس بشيء، إن شاء الآمر','2513','1821','0');
INSERT INTO [t3125] VALUES (2185,'[٢١٥٠-] قلت: رجل قال لرجل: ابتع لي ثوبا بعشرة دراهم، ولم يدفع إليه الدراهم، فجاء فقال: قد اشتريت،','2515','1821','0');
INSERT INTO [t3125] VALUES (2186,'[٢١٥١-] قلت: إذا دفعت إليه عشرة دراهم، فقلت: اشتر لي ثوبا فاشترى ولم ينقد، فهلك الثوب، والدراهم','2515','1821','0');
INSERT INTO [t3125] VALUES (2187,'[٢١٥٢-] قلت: أمر رجلا أن يشتري له سلعة بمائة دينار، ووصف له الصفة التي يريد، فاشترى له بأقل، فإن','2516','1821','0');
INSERT INTO [t3125] VALUES (2188,'[٢١٥٣-] قلت: قال: اشتر لي سلعة ولم يصف له، فإن اشترى بأقل أو بأكثر ضمن؟','2517','1821','0');
INSERT INTO [t3125] VALUES (2189,'[٢١٥٤-] قلت: قال سفيان: رجل أمر رجلا أن يبتاع له جارية بمائة دينار، فاشتراها الرجل بمائة دينار، ثم','2518','1821','0');
INSERT INTO [t3125] VALUES (2190,'[٢١٥٥-] قلت: شريح كان لا يجيز الغلط؟','2521','1821','0');
INSERT INTO [t3125] VALUES (2191,'[٢١٥٦-] قلت: رجل أخذ ثوبا بمائة، فقال: أخذته بمائتين، فقال له صاحبه: لك ربح عشرين على مائتين، أو ده','2522','1821','0');
INSERT INTO [t3125] VALUES (2192,'[٢١٥٧-] قلت٢: رجل ابتاع بيعا بنسيئة، فباعه مرابحة ولم يبين؟','2523','1821','0');
INSERT INTO [t3125] VALUES (2193,'[٢١٥٨-] قلت: رجل ابتاع ثوبا بمائة، فقال: قد أخذته بتسعين لينفق عنه؟','2525','1821','0');
INSERT INTO [t3125] VALUES (2194,'[٢١٥٩-] قلت: رجل باع ثوبا فجاء رجل فأقام البينة أنه اشتراه بمائة، وأقام الآخر البينة أنه اشتراها بمائتين،','2525','1821','0');
INSERT INTO [t3125] VALUES (2195,'[٢١٦٠-] قلت: قال سفيان: كل صانع دفعت إليه عملا، يعمله ليس لك','2526','1821','0');
INSERT INTO [t3125] VALUES (2196,'[٢١٦١-] قلت: رجل في يده ثوب، فقال له آخر: ثوبي بعتك بعشرة دراهم، وقال الآخر: بل وهبته لي؟','2527','1821','0');
INSERT INTO [t3125] VALUES (2197,'[٢١٦٢-] قلت: سئل عن رجل اشترى جارية، فوقع عليها ثم باعها مرابحة؟','2528','1821','0');
INSERT INTO [t3125] VALUES (2198,'[٢١٦٣-] قلت: إذا ابتاع ثيابا بمائة درهم، فلا يبيعن بعضه مرابحة، ولكن يبيعه','2528','1821','0');
INSERT INTO [t3125] VALUES (2199,'[٢١٦٤-] قلت: إذا ابتاع١ برا بمائة درهم، ثم باعه على شراء مائتين غلطا فربحوه على المائتين بألفي المائة،','2530','1821','0');
INSERT INTO [t3125] VALUES (2200,'[٢١٦٥-] [ظ-٦٥/أ] قلت: سئل سفيان عن رجل باع ثوبا، فقال: أبيعكه وعلي خياطته','2530','1821','0');
INSERT INTO [t3125] VALUES (2201,'[٢١٦٦-] قلت: الرجل يستأجر البيت إذا شاء أخرجه، وإذا شاء خرج؟','2532','1821','0');
INSERT INTO [t3125] VALUES (2202,'[٢١٦٧-] قلت: قال سفيان في رجل تكارى دابة فضربها، فماتت؟','2533','1821','0');
INSERT INTO [t3125] VALUES (2203,'[٢١٦٨-] قلت: قال شريح في رجل باع سمنا، فوجد فيه ربا٢؟','2533','1821','0');
INSERT INTO [t3125] VALUES (2204,'[٢١٦٩-] قلت: رجل دفع إلى رجل ثوبا يبيعه، ولم يسم نقدا ولا نسيئة؟','2535','1821','0');
INSERT INTO [t3125] VALUES (2205,'[٢١٧٠-] قلت١ لأبي عبد الله أحمد: وإذا وكل الرجل الرجل أن يبيع شيئا ثم قال بعد: إني رجعت؟','2537','1821','0');
INSERT INTO [t3125] VALUES (2206,'[٢١٧١-] قلت: وإذا ابتعت١ شيئا بدينار إلى أجل فحل الأجل يأخذ٢ بالدينار ما شاء من ذلك النوع؟','2538','1821','0');
INSERT INTO [t3125] VALUES (2207,'[٢١٧٢-] قلت: رجل اشترى سلعة من رجل فندم فيها، قال: أقلنى ولك','2538','1821','0');
INSERT INTO [t3125] VALUES (2208,'[٢١٧٣-] قلت: رجل اشترى بعيرا ببعيرين، وقال: آتيك به غدا؟','2539','1821','0');
INSERT INTO [t3125] VALUES (2209,'[٢١٧٤-] قلت: رجل باع من رجل إلى سنة، فإن١ خرج عطاؤه٢ قبل ذلك حل حقي؟','2540','1821','0');
INSERT INTO [t3125] VALUES (2210,'[٢١٧٥-] قلت: قال: سلف ما يكال فيما يوزن ولا يكال؟','2540','1821','0');
INSERT INTO [t3125] VALUES (2211,'[٢١٧٦-] [ظ-٦٥/ب] قلت: من كره اللحم بالبر١ نسيئة٢؟','2542','1821','0');
INSERT INTO [t3125] VALUES (2212,'[٢١٧٧-] قلت: قال الثوري: يكره نسيئة الحنطة بالدقيق، ولا نرى بأسا','2542','1821','0');
INSERT INTO [t3125] VALUES (2213,'[٢١٧٨-] قلت: الحنطة بالدقيق وزنا [ع-١٤٥/ب] بوزن؟','2543','1821','0');
INSERT INTO [t3125] VALUES (2214,'[٢١٧٩-] قلت: الثوب بالثوبين نسيئة؟','2543','1821','0');
INSERT INTO [t3125] VALUES (2215,'[٢١٨٠-] قلت: بيع العنب وقد أطعم بالطعام يدا بيد؟','2544','1821','0');
INSERT INTO [t3125] VALUES (2216,'[٢١٨١-] قلت: بعير ببعيرين يدا بيد، ودراهم، والدراهم نسيئة؟','2544','1821','0');
INSERT INTO [t3125] VALUES (2217,'[٢١٨٢-] قلت: الحديد بالنحاس نسيئة؟','2545','1821','0');
INSERT INTO [t3125] VALUES (2218,'[٢١٨٣-] قلت: قال الزهري كل شيء يوزن: فهو يجرى مجرى الذهب والفضة، وكل شيء يكال: فهو يجري','2545','1821','0');
INSERT INTO [t3125] VALUES (2219,'[٢١٨٤-] قلت: اشترى جارية فوضعها على يدي رجل حتى يستبرئها، فماتت قبل أن تحيض.','2546','1821','0');
INSERT INTO [t3125] VALUES (2220,'[٢١٨٥-] قلت: رجل اشترى سلعة على الرضا، وسمى الثمن فهلكت؟','2546','1821','0');
INSERT INTO [t3125] VALUES (2221,'[٢١٨٦-] قلت: ذهب بها على سوم، ولم يسم الثمن فهلكت؟','2547','1821','0');
INSERT INTO [t3125] VALUES (2222,'[٢١٨٧-] قلت: رجل أخذ ثوبا من رجل فقال: اذهب به، فإن رضيته أخذته، فباعه؟','2548','1821','0');
INSERT INTO [t3125] VALUES (2223,'[٢١٨٨-] ٢ قلت: قال سفيان الثوري: حد الشفعة عندنا ثلاثة أيام، إذا','2549','1821','0');
INSERT INTO [t3125] VALUES (2224,'[٢١٨٩-] قلت: إلى كم يقضى للغائب بالشفعة؟','2551','1821','0');
INSERT INTO [t3125] VALUES (2225,'[٢١٩٠-] قلت: رجل قال لرجل: أكتري منك إلى مكة بكذا وكذا، فإن سرت شهرا، [ع-١٤٦/أ] أو كذا شيئا','2552','1821','0');
INSERT INTO [t3125] VALUES (2226,'[٢١٩١-] قلت: إذا باع الشفعة فبناها، ثم جاء الشفيع بعد، فالقيمة أو يقلع بناءه.','2553','1821','0');
INSERT INTO [t3125] VALUES (2227,'[٢١٩٣-] قلت: قال سفيان: رجل باع دارا بألف درهم، (ثم باع بابها بألف درهم) ١ ثم جاء الشفيع فقومت الدار','2554','1821','0');
INSERT INTO [t3125] VALUES (2228,'[٢١٩٤-] قلت: الرجلان تكون بينهما الدار والأرض، فيقول أحدهما','2554','1821','0');
INSERT INTO [t3125] VALUES (2229,'[٢١٩٥-] قلت: قال الثوري: إذا قلت: أبتاع منك ما في هذا البيت ما بلغ٤ كل كر بكذا وكذا، فهو مكروه حتى','2556','1821','0');
INSERT INTO [t3125] VALUES (2230,'[٢١٩٦-] قلت: قال سفيان: إذا بادل مصحفا بمصحف وزاد دراهم أو أخذ دراهم، قال: لا بأس به.١','2558','1821','0');
INSERT INTO [t3125] VALUES (2231,'[٢١٩٧-] قلت: سئل سفيان عن رجل باع جارية، واشترط ما في بطنها إن كان بها [ع-١٤٧/أ] حبل؟','2558','1821','0');
INSERT INTO [t3125] VALUES (2232,'[٢١٩٨-] قلت: قال: سألت سفيان عن رجل اشترى بدرهم لحما، والدرهم ليس بجيد، فقال له اللحام: آخذ منك','2559','1821','0');
INSERT INTO [t3125] VALUES (2233,'[٢١٩٩-] قلت: قال سفيان في رجل قال لرجل: أبيعك هذه الدار وهي ألف ذراع وأراه الحدود، فاشتراها،','2561','1821','0');
INSERT INTO [t3125] VALUES (2234,'[٢٢٠٠-] قلت: قال سفيان إن باع جرابا فيه مائة ثوب، أو طعاما فقال: الثياب خمسون ثوبا، والطعام كر،','2562','1821','0');
INSERT INTO [t3125] VALUES (2235,'[٢٢٠١-] قلت: قال سفيان: وإذا باع شيئا واحدا نحو الدار والثوب فأراه ونظر إلى حدوده، فقال: أبيعك هذه','2563','1821','0');
INSERT INTO [t3125] VALUES (2236,'[٢٢٠٢-] قلت: قال: وإذا كان شيئا مفترقا فزاد: فهو مردود، وأما الكيل والوزن إن زاد: أخذ الذي له، ورد','2563','1821','0');
INSERT INTO [t3125] VALUES (2237,'[٢٢٠٣-] قلت: قال سفيان: إذا اشترى مائة ثوب كل ثوب بعشرة دراهم، فوجدها تسعين: فالمشتري بالخيار،','2564','1821','0');
INSERT INTO [t3125] VALUES (2238,'[٢٢٠٤-] قلت: سئل عن رجل اشترى مائة ثوب بألف درهم فزاد، أو نقص فالبيع مردود.','2564','1821','0');
INSERT INTO [t3125] VALUES (2239,'[٢٢٠٥-] قلت: سئل٣ عن رجل أخذ ثوبين من رجلين أحدهما بعشرة،','2564','1821','0');
INSERT INTO [t3125] VALUES (2240,'[٢٢٠٦-] قلت: سئل سفيان يكره شراء حجارة المعادن والسلف فيه؟','2566','1821','0');
INSERT INTO [t3125] VALUES (2241,'[٢٢٠٧-] قلت: قال سفيان: إذا قال: بعني حنطة هذا البيدر٢ أو تبن٣ هذا البيدر، فهو مكروه، لأنه لا يدري ما','2566','1821','0');
INSERT INTO [t3125] VALUES (2242,'[٢٢٠٨-] قلت: قال سفيان: إذا باع جميع الأشياء جزافا، فخلى بينه وبينها، وأقر بالقبض: فهو جائز، إذا لم يسم','2567','1821','0');
INSERT INTO [t3125] VALUES (2243,'[٢٢٠٩-] قلت لأحمد: قال سفيان الثوري: في رجل ابتاع أعطابا٤ كيلا. فيقول: كل لي عطبا منها، وآخذ ما','2567','1821','0');
INSERT INTO [t3125] VALUES (2244,'[٢٢١٠-] قلت: رجل لزم رجلا، فقال رجل: دعه فما لك عليه، فهو علي؟','2568','1821','0');
INSERT INTO [t3125] VALUES (2245,'[٢٢١١-] قلت: إذا ضمن عن الرجل بغير أمره، أله أن يرجع عليه؟','2569','1821','0');
INSERT INTO [t3125] VALUES (2246,'[٢٢١٢-] قلت: قال سفيان: إذا بعت رجلا بيعا بنقد، ولم يقضك، وعسر عليه الثمن، فقال: تاركني وأزيدك،','2571','1821','0');
INSERT INTO [t3125] VALUES (2247,'[٢٢١٣-] قلت: سئل سفيان: عن رجل كانت عنده دابة مسروقة، فقال: هي وديعة عندي؟','2572','1821','0');
INSERT INTO [t3125] VALUES (2248,'[٢٢١٤-] قلت: سئل عن رجل وجد عنده ثوب مسروق، فقال: اشتريته؟','2573','1821','0');
INSERT INTO [t3125] VALUES (2249,'[٢٢١٥-] قلت: سئل عن التجارة في جلود السباع.','2573','1821','0');
INSERT INTO [t3125] VALUES (2250,'[٢٢١٦-] قلت: سئل عن بيع الهر؟ ٢','2574','1821','0');
INSERT INTO [t3125] VALUES (2251,'[٢٢١٧-] قلت: سئل عن بيع البنادق؟','2577','1821','0');
INSERT INTO [t3125] VALUES (2252,'[٢٢١٨-] قلت: سئل عن بيع الدفوف٢؟','2578','1821','0');
INSERT INTO [t3125] VALUES (2253,'[٢٢١٩-] قلت: سئل سفيان عن رجلين اشتركا فجاء أحدهما بدنانير وجاء الآخر بدراهم؟','2579','1821','0');
INSERT INTO [t3125] VALUES (2254,'[٢٢٢٠-] قلت: سئل عن رجل اشترى طعاما، فقبضه ثم اشترك فيه آخر؟','2581','1821','0');
INSERT INTO [t3125] VALUES (2255,'[٢٢٢١-] قلت: سئل سفيان عن رجلين اشتركا بالعروض فربحا؟','2581','1821','0');
INSERT INTO [t3125] VALUES (2256,'[٢٢٢٢-] قلت: سئل عن رجلين اشتركا بغير رؤوس أموال، قال كل واحد منهما: ما اشتريت فهو بيني وبينك؟','2582','1821','0');
INSERT INTO [t3125] VALUES (2257,'[٢٢٢٣-] قلت: قال سفيان في خمسة نفر، بينهم خمسة أبيات في دار، فباع أحدهم نصيبه في بيت: لا أجيزه،','2584','1821','0');
INSERT INTO [t3125] VALUES (2258,'[٢٢٢٤-] قلت: قال سفيان: في رجل دفع إلى رجل مالا مضاربة، فابتاع به متاعا، فقبض المتاع ولم٢ ينقد','2585','1821','0');
INSERT INTO [t3125] VALUES (2259,'[٢٢٢٥-] قلت: سئل سفيان عن المضارب يجيء بالبز١، فيطلبونه بنسيئة٢ إلى أجل، فقال المضارب لصاحب','2586','1821','0');
INSERT INTO [t3125] VALUES (2260,'[٢٢٢٦-] قلت: قال سفيان: إذا كان لك قرض، فلا تجعله مضاربة، [ع-١٤٩/ب] إلا أن تأمره أن يدفعه إلى','2587','1821','0');
INSERT INTO [t3125] VALUES (2261,'[٢٢٢٧-] قلت٢: قال سفيان في مضارب، ابتاع خمرا قال: إن كان','2588','1821','0');
INSERT INTO [t3125] VALUES (2262,'[٢٢٢٨-] قلت: قال سفيان في رجل أخذ مالا مضاربة، واشترى به بزا، فقدم به، فقال صاحب المال: لا تبعه،','2589','1821','0');
INSERT INTO [t3125] VALUES (2263,'[٢٢٢٩-] قلت: قال سفيان في رجل دفع إلى رجل خمسين دينارا، مضاربة فأخذ منها خمسة دنانير فضمنها،','2590','1821','0');
INSERT INTO [t3125] VALUES (2264,'[٢٢٣٠-] قلت: قال سفيان في رجل دفع إلى رجل خمسين دينارا مضاربة، فقال: اشتر بها ما شئت، فاشترى','2590','1821','0');
INSERT INTO [t3125] VALUES (2265,'[٢٢٣١-] قلت: سئل سفيان عن رجلين كفلا عن رجل بدين فأخذا منه رهنا.١ فقال أحد الكفيلين: أنا آخذ','2592','1821','0');
INSERT INTO [t3125] VALUES (2266,'[٢٢٣٢-] قلت: سئل سفيان عن رجل ارتهن دابة، فعلفها من غير أن','2592','1821','0');
INSERT INTO [t3125] VALUES (2267,'[٢٢٣٣-] قلت: قال سفيان في رجل أذن لعبده في التجارة، فجرح إنسانا؟','2593','1821','0');
INSERT INTO [t3125] VALUES (2268,'[٢٢٣٤-] قلت: سئل سفيان عن العبد المأذون له في التجارة عن إقراره؟','2593','1821','0');
INSERT INTO [t3125] VALUES (2269,'[٢٢٣٥-] قلت: قال سفيان: إذا قال الرجل للرجل: له علي مائة دينار، ولي عنده دينار؟','2594','1821','0');
INSERT INTO [t3125] VALUES (2270,'[٢٢٣٦-] قلت: قال سفيان: وإذا قال: لك عندى مائة دينار إلا فرسا، إلا ثوبا: هذا محال من الكلام، يؤخذ','2595','1821','0');
INSERT INTO [t3125] VALUES (2271,'[٢٢٣٧-] قلت: قال سفيان: إذا شهد رجل على رجل بألف درهم، أو مائة دينار، فإن له دراهم ذلك البلد،','2597','1821','0');
INSERT INTO [t3125] VALUES (2272,'[٢٢٣٨-] قلت: سئل سفيان عن رجل كان له على رجل مال، فقال له: أقرضنى وأقضيك، وكان له عليه عين','2598','1821','0');
INSERT INTO [t3125] VALUES (2273,'[٢٢٣٩-] قلت: قال: سألت ابن عيينة٢، قلت له: الرجل يكون له على','2598','1821','0');
INSERT INTO [t3125] VALUES (2274,'[٢٢٤٠-] قلت: قال سفيان: إذا كان لك على رجل طعام قرضا، فبعه من الذي عليه بنقد، ولا تبعه منه بنسيئة،','2599','1821','0');
INSERT INTO [t3125] VALUES (2275,'[٢٢٤١-] قلت: قال سفيان: إذا أقرضت رجلا قرضا، دراهم أو دنانير: فلا تأخذن من غيره عرضا بمالك','2601','1821','0');
INSERT INTO [t3125] VALUES (2276,'[٢٢٤٢-] قلت: سئل سفيان عن رجل ابتاع لصاحبه شيئا، فاستزاده فزاده؟','2601','1821','0');
INSERT INTO [t3125] VALUES (2277,'[٢٢٤٣-] قلت: قال سفيان: لا يكون في الوديعة، والبضاعة، والمضاربة، والعارية ضمان.','2602','1821','0');
INSERT INTO [t3125] VALUES (2278,'[٢٢٤٤-] قلت: قال سفيان: كل شيء، أصله ضمان فاشترط: أن ليس عليه ضمان: فهو ضامن.','2603','1821','0');
INSERT INTO [t3125] VALUES (2279,'[٢٢٤٥-] قلت: قال سفيان: كل شيء أصله أمانة، [ع-١٥١/أ] فليس على صاحبه ضمان، وإن اشترط أنه','2604','1821','0');
INSERT INTO [t3125] VALUES (2280,'[٢٢٤٦-] قلت: سئل سفيان عن رجل استودع رجلا ألف درهم، فجاءه فقال: ادفع إلي دراهمي، قال: قد دفعتها','2605','1821','0');
INSERT INTO [t3125] VALUES (2281,'[٢٢٤٧-] قلت: سئل سفيان عن رجل استودع رجلا دراهم٤، بيضا','2605','1821','0');
INSERT INTO [t3125] VALUES (2282,'[٢٢٤٨-] قلت: قال سفيان إذا أكرى رجل دابة، أو أعار، أو استودع','2606','1821','0');
INSERT INTO [t3125] VALUES (2283,'[٢٢٤٩-] قلت: رجل أخذ من رجل مالا مضاربة من أين نفقته؟','2607','1821','0');
INSERT INTO [t3125] VALUES (2284,'[٢٢٥٠-] قلت: قال سفيان: إذا دفعت إلى رجل ألف درهم مضاربة، فجاء بألفين، فقال: هذا ألف رأس المال،','2608','1821','0');
INSERT INTO [t3125] VALUES (2285,'[٢٢٥١-] قلت: سئل سفيان عن رجل دفع إلى رجل مالا مضاربة.','2609','1821','0');
INSERT INTO [t3125] VALUES (2286,'[٢٢٥٢-] قلت: سئل سفيان: إذا اختلفا فقال المدفوع إليه: هذا ربح وقد دفعت إليك رأس المال؟','2609','1821','0');
INSERT INTO [t3125] VALUES (2287,'[٢٢٥٣-] قلت: رجل دفع إلى رجل مالا مضاربة، فاشترى أخته، فوقع عليها؟','2610','1821','0');
INSERT INTO [t3125] VALUES (2288,'[٢٢٥٤-] قلت: المضارب يشترط عليه أن لا يخرج من البلد، فخرج؟','2611','1821','0');
INSERT INTO [t3125] VALUES (2289,'[٢٢٥٥-] قلت: قال: الشفعة لا تباع، ولا توهب، ولا تورث؟','2611','1821','0');
INSERT INTO [t3125] VALUES (2290,'[٢٢٥٦-] قلت: رجل أمر رجلا أن يبني له في أرضه، فيقيم سنة أله أن يخرجه قبل السنة؟','2612','1821','0');
INSERT INTO [t3125] VALUES (2291,'[٢٢٥٧-] قلت: قال ابن أبي ليلى: الرجل يبني في الأرض البناء بغير إذن أهلها، ثم يؤاجرها، قال: الغلة على','2612','1821','0');
INSERT INTO [t3125] VALUES (2292,'[٢٢٥٨-] قلت: رجل ابتاع دارا، فقال المشتري: أبتاعها منك، فإن أدرك فيها أحد شيئا: فعليك مثل ذرعها من','2613','1821','0');
INSERT INTO [t3125] VALUES (2293,'[٢٢٥٩-] قلت: سئل سفيان عن رجل من أهل القرى، جاء فاشتريت منه','2613','1821','0');
INSERT INTO [t3125] VALUES (2294,'[٢٢٦٠-] قلت: سئل سفيان عن رجل باع شيئا، فقال: إن لم تحمله غدا فلا بيع١ بينى وبينك؟','2616','1821','0');
INSERT INTO [t3125] VALUES (2295,'[٢٢٦١-] قلت: سئل سفيان عن رجل باع بيعا، فقال: إن لم تأتني بنقدي غدا، فلا بيع بيني وبينك؟','2617','1821','0');
INSERT INTO [t3125] VALUES (2296,'[٢٢٦٢-] قلت: رجل باع ثوبا بشرط، فمات المشتري؟','2617','1821','0');
INSERT INTO [t3125] VALUES (2297,'[٢٢٦٣-] قلت: قال سفيان، فإن باع ثوبا، بشرط، فمات البائع قبل أن يمضى الأجل، يقال للمشتري: قد صار','2618','1821','0');
INSERT INTO [t3125] VALUES (2298,'[٢٢٦٤-] قلت: فإن قال ورثة البائع: لا نعطيه حتى يمضي شرط المشتري، ليس ذلك للوارث، إن شاء أخذ،','2618','1821','0');
INSERT INTO [t3125] VALUES (2299,'[٢٢٦٥-] قلت: قال سفيان في الرجل يبيع الخادمة١، ويشترط خدمتها، نحن نكره هذا، فإن هلكت الخادمة:','2619','1821','0');
INSERT INTO [t3125] VALUES (2300,'[٢٢٦٦-] قلت: الرجل يشتري الجارية بشرط. يطأها؟','2619','1821','0');
INSERT INTO [t3125] VALUES (2301,'[٢٢٦٧-] قلت: قال سفيان: أكره أن يجيء الرجل إلى الرجل، فيقول: أكرني ثيابك، أو حليك حتى أرهنه، أجر','2619','1821','0');
INSERT INTO [t3125] VALUES (2302,'[٢٢٦٨-] قلت: قال سفيان: في رجل قال لرجل: أرهني كذا وكذا، وأعطيك مائة درهم، فأخذ الرهن، فجاء','2620','1821','0');
INSERT INTO [t3125] VALUES (2303,'[٢٢٦٩-] قلت: قال سفيان في رجل جاء إلى رجل فقال: أعرني ثوبك أرهنه، فأعطاه فرهنه، فسرق الثوب؟','2620','1821','0');
INSERT INTO [t3125] VALUES (2304,'[٢٢٧٠-] قلت: سئل سفيان عن رجل ارتهن عبدا، فجنى عنده جناية؟','2621','1821','0');
INSERT INTO [t3125] VALUES (2305,'[٢٢٧١-] قلت: سئل فإن جنى العبد جناية لا تحيط بثمنه؟','2621','1821','0');
INSERT INTO [t3125] VALUES (2306,'[٢٢٧٢-] قلت: سئل سفيان عن رجل رهن رهنا شيئا، فقال المرتهن: أعطيتك مائتي درهم، ورهنتني ثوبا.','2622','1821','0');
INSERT INTO [t3125] VALUES (2307,'[٢٢٧٣-] قلت: فإن استهلك الرهن، فقال الراهن: رهني ثمن كذا وكذا، وقال المرتهن: ثمن كذا وكذا، فبينة','2622','1821','0');
INSERT INTO [t3125] VALUES (2308,'[٢٢٧٤-] قلت: سئل سفيان: فإن استأجرت دابة من رجل، ورهنته ثوبا، فهلك الثوب، أو الدابة١؟ فليس على','2624','1821','0');
INSERT INTO [t3125] VALUES (2309,'[٢٢٧٥-] قلت: قال سفيان: إذا رهنت رهنا، فدفعت إليه بعض الذي له، أو كله، فسرق الرهن الذي له رد الذي','2624','1821','0');
INSERT INTO [t3125] VALUES (2310,'[٢٢٧٦-] قلت: قال سفيان: إذا رهنت رهنا، فأتيته ببعض حقه، فقلت: خذ مني وأعطني بحساب ذلك، فليس','2625','1821','0');
INSERT INTO [t3125] VALUES (2311,'[٢٢٧٧-] قلت: قال سفيان: والرهن قيمته يوم دفعه، ولا يؤخذ بأكثر من ذلك إذا هلك.','2626','1821','0');
INSERT INTO [t3125] VALUES (2312,'[٢٢٧٨-] قلت: قال سفيان: الرهن إذا كان بأكثر، كان بما فيه، وإذا كان بأقل رد الراهن الفضل.','2626','1821','0');
INSERT INTO [t3125] VALUES (2313,'[٢٢٧٩-] قلت: قال سفيان: لو أن الراهن قال للمرتهن: إلبسه، أو أعره، أو أكره، فقد خرج من الضمان،','2627','1821','0');
INSERT INTO [t3125] VALUES (2314,'[٢٢٨٠-] قلت: سئل سفيان عن رجل ارتهن دارا ثم أكراها من صاحب الدار؟','2630','1821','0');
INSERT INTO [t3125] VALUES (2315,'[٢٢٨١-] قلت: قال سفيان في رجل ارتهن عبدا، فقام إلى سيده فقتله؟','2630','1821','0');
INSERT INTO [t3125] VALUES (2316,'[٢٢٨٢-] قلت لأحمد: قال سفيان في رجل رهن عبدا، من رجل فقتل العبد عمدا، فاقتص السيد من الذي قتله:','2631','1821','0');
INSERT INTO [t3125] VALUES (2317,'[٢٢٨٣-] قلت: قال سفيان: فإن أخذ السيد الدية: يأخذ المرتهن ثمن رهنه.','2633','1821','0');
INSERT INTO [t3125] VALUES (2318,'[٢٢٨٤-] قلت: سئل سفيان عن رجل وضع رهنا، على يدي صاحبه بحق له، فقال: إن جئت إلى كذا وكذا،','2633','1821','0');
INSERT INTO [t3125] VALUES (2319,'[٢٢٨٥-] قلت: قال سفيان: إذا كانت دار بين اثنين، فقال أحدهما: أبيعك نصف هذه الدار؟ قال: لا يجوز، إنما','2634','1821','0');
INSERT INTO [t3125] VALUES (2320,'[٢٢٨٦-] قلت: سئل عن رجل١ يلتمس من رجل بيعا بألف درهم نسيئة، إلى سنة٢ فكره أن يبايعه، فدفع إلى٣','2635','1821','0');
INSERT INTO [t3125] VALUES (2321,'[٢٢٨٧-] قلت: قال سفيان: لا تستقيم المفاوضة بالثلث، والربع، حتى يكون مال هذا مثل مال هذا.','2636','1821','0');
INSERT INTO [t3125] VALUES (2322,'[٢٢٨٨-] قلت: سئل عن شريكين متفاوضين، وسوس أحدهما سبعة عشر عاما؟','2637','1821','0');
INSERT INTO [t3125] VALUES (2323,'[٢٢٨٩-] قلت: رجل باع ثوبا بعشرة دراهم بنقد، ولم ينتقد الثمن، فقال للمشتري: تعطينيه بعشرين؟','2637','1821','0');
INSERT INTO [t3125] VALUES (2324,'[٢٢٩٠-] قلت: قال سفيان في رجل اشترى زوج نعال، أو مصراعين١ فقبضهما، فجاء يدعي في أحد الفردين','2639','1821','0');
INSERT INTO [t3125] VALUES (2325,'[٢٢٩١-] قلت: قال سفيان في رجل باع ثوبا، فمات المشتري قبل أن ينقده شيئا، فجاء البائع فقال: قد بعت من','2639','1821','0');
INSERT INTO [t3125] VALUES (2326,'[٢٢٩٢-] قلت: سئل: فإن قال الميت: ابتعت هذا الثوب من فلان، ولم أنقده الثمن؟','2640','1821','0');
INSERT INTO [t3125] VALUES (2327,'[٢٢٩٣-] قلت: سئل سفيان عن رجل باع ثوبا بشرط، وسمى الثمن فجاء بثوب، فقال: هذا ثوبك. وقال صاحب','2641','1821','0');
INSERT INTO [t3125] VALUES (2328,'[٢٢٩٤-] قلت: سئل: فإن ذهب بالثوب بشرط أن يريه أهله، فهلك الثوب؟','2642','1821','0');
INSERT INTO [t3125] VALUES (2329,'[٢٢٩٥-] قلت٣: فإن ذهب بالثوب بغير ثمن. وقال: إن رضيته ساومتك به بعد، فذهب؟','2642','1821','0');
INSERT INTO [t3125] VALUES (2330,'[٢٢٩٦-] قلت: سئل سفيان عن الرجل يبيع البيع٣ بشرط، ولا يسمي أجلا؟','2643','1821','0');
INSERT INTO [t3125] VALUES (2331,'[٢٢٩٧-] قلت: قضى عمرو بن حريث٦ في رجل باع سلعة، ولم','2643','1821','0');
INSERT INTO [t3125] VALUES (2332,'[٢٢٩٨-] قلت: قال سفيان: لا حمالة، ولا كفالة٢ للعبد حتى يأذن له','2644','1821','0');
INSERT INTO [t3125] VALUES (2333,'[٢٢٩٩-] قلت: قال سفيان: إذا قال رجل لرجل: إكفل عني ولك ألف درهم، الكفالة جائزة، ويرد عليه ألف','2646','1821','0');
INSERT INTO [t3125] VALUES (2334,'[٢٣٠٠-] قلت: قال: وإذا قال: استقرض لي من فلان ألف درهم ولك عشرة دراهم: هذا لا خير فيه، لأنه','2646','1821','0');
INSERT INTO [t3125] VALUES (2335,'[٢٣٠١-] قلت: قال سفيان في رجلين لهما على رجل ألف درهم، فذهبا يتقاضيانه، فقال أحدهما: أنا أحبسه أو','2647','1821','0');
INSERT INTO [t3125] VALUES (2336,'[٢٣٠٢-] قلت: سئل الأوزاعي عن العير تقدم بالبضاعة، أيشتريها الرجل؟','2647','1821','0');
INSERT INTO [t3125] VALUES (2337,'[٢٣٠٣-] قلت: قال: سألت الأوزاعي عن السفينة تقدم بالبضاعة؟','2648','1821','0');
INSERT INTO [t3125] VALUES (2338,'[٢٣٠٤-] قلت: قال: سمعت سفيان ذكر العرة، فقال: أنا أكره بيعه وشراءه.','2649','1821','0');
INSERT INTO [t3125] VALUES (2339,'[٢٣٠٥-] قلت: قال: قيل له يعني سفيان: مجوسي باع مجوسيا خمرا، ثم أسلما٢؟','2650','1821','0');
INSERT INTO [t3125] VALUES (2340,'[٢٣٠٦-] قلت: قال في الشيء يجعل على يدي عدل: هو له، قال','2650','1821','0');
INSERT INTO [t3125] VALUES (2341,'[٢٣٠٧-] قلت: قال: قيل له١: ماترى في استئجار الحلي؟','2652','1821','0');
INSERT INTO [t3125] VALUES (2342,'[٢٣٠٨-] قلت: قال: قلت: يعني - لسفيان -: إستكريت أجيرا يستقي لي على بعيري، فقلت: اذهب إلى الحيرة،','2653','1821','0');
INSERT INTO [t3125] VALUES (2343,'[٢٣٠٩-] قلت: قال: قلت: يعنى سفيان: الرجل يشتري المتاع إلى الأجل؟','2655','1821','0');
INSERT INTO [t3125] VALUES (2344,'[٢٣١٠-] قلت: سئل الأوزاعي عن الرجل تكون له الجارية النصرانية، أيكرهها على أكل اللحم في صومها،','2655','1821','0');
INSERT INTO [t3125] VALUES (2345,'[٢٣١١-] سئل إسحاق عن شري الزرع وهو حنطة، بالحنطة، والشعير، بالشعير، وهو قصيل١ لم يدرك','2657','1821','0');
INSERT INTO [t3125] VALUES (2346,'[٢٣١٢-] سئل إسحاق عن الرجل يدفع الثوب ينسج على النصف، والجلد يدبغ، فيهلك في يده قبل أن يسلمه،','2659','1821','0');
INSERT INTO [t3125] VALUES (2347,'[٢٣١٣-] سئل إسحاق عن جلود الثعالب٢؟','2660','1821','0');
INSERT INTO [t3125] VALUES (2348,'[٢٣١٤-] سئل إسحاق عن المكروهات، من وقف عليها أنها حرام؟','2662','1821','0');
INSERT INTO [t3125] VALUES (2349,'[٢٣١٥-] سئل إسحاق عن شريكين متفاوضين اشترى أحدهما سلعة، ولم ينقد، [ع-١٥٥/أ] ثم غاب، فجاء','2663','1821','0');
INSERT INTO [t3125] VALUES (2350,'[٢٣١٦-] سئل إسحاق عن رجل اشترى ثوبا بعشرة دراهم، ولم ينقد','2664','1821','0');
INSERT INTO [t3125] VALUES (2351,'[٢٣١٧-] قال إسحاق: وأما الحجام الذي يجز شعور الناس، ويأخذ على ذلك أجرا، إنما إرادته أن يفعله لمنفعة،','2665','1821','0');
INSERT INTO [t3125] VALUES (2352,'[٢٣١٨-] قال إسحاق: وأما من خلط مالا خبيثا، ومالا طيبا، ثم دعا الناس إلى طعامه: فإن الداعي إذا كان','2667','1821','0');
INSERT INTO [t3125] VALUES (2353,'[٢٣١٩-] قال إسحاق: وأما العارية: فهي مؤداة على كل حال، وإن هلكت العارية، فلم يجد صاحبها سبيلا إلى','2667','1821','0');
INSERT INTO [t3125] VALUES (2354,'[٢٣٢٠-] قال إسحاق: [ع-١٥٥/ب] وأما قول رسول الله صلى الله عليه وسلم: "البيعان بالخيار، ما لم','2668','1821','0');
INSERT INTO [t3125] VALUES (2355,'[٢٣٢١-] قال إسحاق: وأما الخان١ الذي في القرية السابلة٢ لمن يسكنه من المنتابين، فباعه قوم من رؤساء','2670','1821','0');
INSERT INTO [t3125] VALUES (2356,'[٢٣٢٢-] قال إسحاق: وأما مبادلة الأرض بالأرض أيكون للشفيع في ذلك شفعة، فإن أهل العلم اختلفوا في','2672','1821','0');
INSERT INTO [t3125] VALUES (2357,'[٢٣٢٣-] قال إسحاق: وأما طلب الشفعة، فإن طلبه إذا سمع بالشراء، فذلك الطلب الذي يوجب له الشفعة، وإن','2674','1821','0');
INSERT INTO [t3125] VALUES (2358,'[٢٣٢٤-] قال إسحاق: إذا كان لرجل شفعة في دار، فغاب، وباع المشتري الدار بفضل ما اشترى٢: من أيهما','2680','1821','0');
INSERT INTO [t3125] VALUES (2359,'[٢٣٢٥-] قال إسحاق: وأما الرجل يدفع المتاع إلى رجل، ليحمله إلى مصرـ فرجع الرسول، فقال: قد سرق','2681','1821','0');
INSERT INTO [t3125] VALUES (2360,'[٢٣٢٦-] قال إسحاق: وأما الرجل الذي٥ يسلم الغلام إلى أهل الصناعات على أن يعلمه الصانع الحرفة','2682','1821','0');
INSERT INTO [t3125] VALUES (2361,'[٢٣٢٧-] قال إسحاق: وأما تصحيح طعام الغلام، وكسوته إلى أن يعلمه، فإن السنة مضت، في استئجار','2683','1821','0');
INSERT INTO [t3125] VALUES (2362,'[٢٣٢٨-] قال إسحاق: مضت السنة من النبي صلى الله عليه وسلم أن من أحيا أرضا مواتا، فقد ملك رقبتها،','2688','1821','0');
INSERT INTO [t3125] VALUES (2363,'[٢٣٢٩-] قال إسحاق: وهذا إذا كان دون دعوة من القرية، أو المصر، رجوت أن يكون كما وصفوا. وأما ما','2697','1821','0');
INSERT INTO [t3125] VALUES (2364,'[٢٣٣٠-] سئل أحمد عن الرجل يعتق عبده، وله مال؟','2708','1821','0');
INSERT INTO [t3125] VALUES (2365,'[٢٣٣١-] قلت لأحمد: رجل في حائط جاره، شجرة وأغصانها في حائطه، أله أن يمنعه ويأمر بقطعها؟','2709','1821','0');
INSERT INTO [t3125] VALUES (2366,'[٢٣٣٢-] قلت لأحمد: الأم تأخذ من مال ولدها؟','2710','1821','0');
INSERT INTO [t3125] VALUES (2367,'[٢٣٣٣-] قلت لأحمد: تجبر الأم على الولد إذا لم يكن للأب شيء١؟','2711','1821','0');
INSERT INTO [t3125] VALUES (2368,'[٢٣٣٤-] قال إسحاق: رجل اشترى دابة، ولم يرها فضاعت، أو ماتت قبل أن تدفع إليه، فعلى من الضمان؟','2712','1821','0');
INSERT INTO [t3125] VALUES (2369,'[٢٣٣٥-] سئل أحمد عن عتق النسمة؟','2713','1821','0');
INSERT INTO [t3125] VALUES (2370,'[٢٣٣٥-] سئل أحمد عن عتق النسمة؟','2714','1821','0');
INSERT INTO [t3125] VALUES (2371,'[٢٣٣٦-] سئل أحمد عن رجل قال لآخر: ابعث إلي بثوبين فبعث بهما إليه١ على يدى الغلام، [ع-١٥٨/ب]','2716','1821','0');
INSERT INTO [t3125] VALUES (2372,'[٢٣٣٧-] سئل أحمد٤ عن رجل قال: اشتروا دابة للسبيل، فعجزت النفقة','2716','1821','0');
INSERT INTO [t3125] VALUES (2373,'[٢٣٣٨-] قال أحمد: إذا رهن جاريته٣، فنفقتها على الراهن، ولو ماتت يكفنها، و٤ملك الراهن فيها.','2717','1821','0');
INSERT INTO [t3125] VALUES (2374,'[٢٣٣٩-] قلت لإسحاق: رجل اضطر إلى الماء، فاشتراه، وأنكر الثمن، يقول: "إن النبي صلى الله عليه وسلم','2718','1821','0');
INSERT INTO [t3125] VALUES (2375,'[٢٣٤٠-] قلت لإسحاق: رجل دفع إلى رجل مائة درهم، فقال: بع مني طعاما، ولم يقبضه يومئذ، حتى ارتفع','2720','1821','0');
INSERT INTO [t3125] VALUES (2376,'[٢٣٤١-] قلت لإسحاق: رجل سأل امرأته أن تهب له مهرها، فوهبته له، أله٥ أن يمسكها بغير مهر؟','2721','1821','0');
INSERT INTO [t3125] VALUES (2377,'[٢٣٤٢-] قلت لإسحاق: رجل له على رجل حنطة، فأخذ شعيرا بسعر يومه؟','2723','1821','0');
INSERT INTO [t3125] VALUES (2378,'[٢٣٤٣-] سئل إسحاق عن رجل قال لآخر: أبيعك ضيعتي هذه، فجاء بالدراهم. فقال: خذ هذه، ولم يكن بايعه،','2725','1821','0');
INSERT INTO [t3125] VALUES (2379,'[٢٣٤٤-] قلت لإسحاق: الوصي يأخذ من نفسه مال اليتيم مضاربة، قدر ما لا يكون فيه حيف؟','2725','1821','0');
INSERT INTO [t3125] VALUES (2380,'[٢٣٤٥-] قلت إسحاق: رجل٢ كتب إلى وكيله أن٣ أعط فلانا ألف درهم فضمن له الوكيل. ثم قدم الرجل','2727','1821','0');
INSERT INTO [t3125] VALUES (2381,'[٢٣٤٦-] سئل أحمد عن الرجل وقف١ وقفا هل يستثنى لنفسه شيئا؟','2729','1821','0');
INSERT INTO [t3125] VALUES (2382,'[٢٣٤٧-] سئل أحمد عن من يأخذ من مال رجل، ثم يقول: اجعلني في حل؟','2730','1821','0');
INSERT INTO [t3125] VALUES (2383,'[٢٣٤٨-] قلت٢ لأحمد: إذا٣ أمر رجل رجلا أن يبيع له شيئا، فباعه','2730','1821','0');
INSERT INTO [t3125] VALUES (2384,'[٢٣٤٩-] قلت لأحمد: إذا قال: أخذت ثوبا من هذه الثياب بعشرة دراهم، فأعطاه ثوبا: فالبيع فاسد؟','2731','1821','0');
INSERT INTO [t3125] VALUES (2385,'[٢٣٥٠-] قال إسحاق: السنة في الحوالة ما قال رسول الله٢ صلى الله عليه وسلم: "من أحيل على مليء،','2732','1821','0');
