INSERT INTO [t3125] VALUES (2629,'[٢٥٩٢-] قلت: رجل قتل محرما في الحرم في الشهر الحرام؟','3066','2390','0');
INSERT INTO [t3125] VALUES (2630,'[٢٥٩٣-] قلت لأحمد ٢: إذا كسرت [يده] ٣ أو فخذه ٤ أو رجله؟','3068','2390','0');
INSERT INTO [t3125] VALUES (2631,'[٢٥٩٤-] قلت: [رجل] ١ أفضى ٢ إلى جارية فخرقها فماتت أو لم تمت؟','3069','2390','0');
INSERT INTO [t3125] VALUES (2632,'[٢٥٩٥-] قلت: رجل ١ عالج امرأة فكسر ٢ سنها؟','3070','2390','0');
INSERT INTO [t3125] VALUES (2633,'[٢٥٩٦-] قلت: عبد فقأ عين حر، وعلى العبد دين؟','3071','2390','0');
INSERT INTO [t3125] VALUES (2634,'[٢٥٩٧-] قلت: قوم قتلوا رجلا خطأ، على كل واحد منهم رقبة مع الدية [أو] ٣ رقبة تجزيهم؟','3072','2390','0');
INSERT INTO [t3125] VALUES (2635,'[٢٥٩٨-] ١ حدثنا ٢ إسحاق بن منصور المرزوي قال: قلت: لأحمد ٣ بن محمد بن حنبل:','3074','2390','0');
INSERT INTO [t3125] VALUES (2636,'[٢٥٩٩-] قلت: أم ولد إذا جنت جناية أنه يضمن سيدها، وليس له أن يسلمها، وليس عليه أن يحمل من جنايتها','3075','2390','0');
INSERT INTO [t3125] VALUES (2637,'[٢٦٠٠-] قلت: الضالة ١ المكتومة؟ ٢','3076','2390','0');
INSERT INTO [t3125] VALUES (2638,'[٢٦٠١-] قال أحمد رضي الله عنه القسامة - الذي أذهب فيه إلى حديث ٣','3077','2390','0');
INSERT INTO [t3125] VALUES (2639,'باب القسامة','3077','0','0');
INSERT INTO [t3125] VALUES (2640,'[٢٦٠٢-] قلت: فإن نقص من أولياء المقتول من الخمسين، أو لم يكن إلا رجل واحد؟','3080','2639','0');
INSERT INTO [t3125] VALUES (2641,'[٢٦٠٣-] قلت: ما قسامة الخطأ؟','3082','2639','0');
INSERT INTO [t3125] VALUES (2642,'[٢٦٠٤-] قلت: القسامة ما هي؟','3082','2639','0');
INSERT INTO [t3125] VALUES (2643,'[٢٦٠٥-] قلت [لأحمد] ٣ إذا شربت المرأة الدواء عمدا فأسقطت جنينها؟','3085','2639','0');
INSERT INTO [t3125] VALUES (2644,'[٢٦٠٦-] قلت: قال ابن شبرمة ٣: في رجل فقأ عين رجل ثم عمي؟','3086','2639','0');
INSERT INTO [t3125] VALUES (2645,'[٢٦٠٧-] قلت: فإن قال القاتل عمدا: ليس لي مال، اقتص مني؟','3087','2639','0');
INSERT INTO [t3125] VALUES (2646,'[٢٦٠٨-] قلت: قال الزهري: رجل فقأ عين رجل، فقام إليه ابن عمه فقتله؟','3088','2639','0');
INSERT INTO [t3125] VALUES (2647,'[٢٦٠٩-] قلت: رجل قتل رجلا عمدا، ثم قتل هو [ع-١١٠/ب] خطأ، لمن ديته؟','3089','2639','0');
INSERT INTO [t3125] VALUES (2648,'[٢٦١٠-] قلت: رجل قتل رجلا خطأ، ثم قتل آخر عمدا، أو قتل عمدا ثم قتل خطأ؟','3090','2639','0');
INSERT INTO [t3125] VALUES (2649,'[٢٦١١-] قلت: حر وعبد قتلا حرا خطأ؟.','3090','2639','0');
INSERT INTO [t3125] VALUES (2650,'[٢٦١٢-] قلت: قال سفيان: في رجل أفزع رجلا بالليل فذهب عقله؟','3091','2639','0');
INSERT INTO [t3125] VALUES (2651,'[٢٦١٣-] قلت: دية الخطأ أخماس: عشرون حقة، وعشرون جذعة وعشرون بنت مخاض، وعشرون بنت','3092','2639','0');
INSERT INTO [t3125] VALUES (2652,'[٢٦١٤-] قلت: دية شبه العمد أرباع: خمس وعشرون بنت ٥ لبون،','3092','2639','0');
INSERT INTO [t3125] VALUES (2653,'[٢٦١٥-] قلت: سئل سفيان عن رجل أوضح رجلا فبرئت الموضحة ولم ينبت عليه الشعر، ثم أوضحه رجل','3094','2639','0');
INSERT INTO [t3125] VALUES (2654,'[٢٦١٦-] قلت: إن رجلا جاء إلى أهل أبيات فاستسقاهم، فلم يسقوه حتى مات؟','3094','2639','0');
INSERT INTO [t3125] VALUES (2655,'[٢٦١٧-] قلت: قال الزهري رجل أعتق ما في بطن جاريته، فضربها رجل فوقع ميتا، ديته دية المملوك؟ ١','3096','2639','0');
INSERT INTO [t3125] VALUES (2656,'[٢٦١٨-] قلت: إذا ضربها [ع-١١١/أ] فأسقطت حيا ثم مات؟','3097','2639','0');
INSERT INTO [t3125] VALUES (2657,'[٢٦١٩-] قلت: امرأة مسحت بطن امرأة ٢ فأسقطت، فرفع ذلك إلى عمر [رضي الله عنه] فأمرها أن تعتق','3098','2639','0');
INSERT INTO [t3125] VALUES (2658,'[٢٦٢٠-] قلت: قال سفيان: [تقاد] ٢ الثنية بالثنية، والضرس بالضرس، والشمال بالشمال، واليمين باليمين؟ ٣','3099','2639','0');
INSERT INTO [t3125] VALUES (2659,'[٢٦٢١-] قلت [لأحمد] ٣ قال سفيان: مدبر خرق ثوبا ٤، هو دين عليه؟ ٥','3100','2639','0');
INSERT INTO [t3125] VALUES (2660,'[٢٦٢٢-] قلت: قال سفيان: في رجل أمر مملوك ٢ رجل ٣ أن يقتل سيده فقتله؟','3101','2639','0');
INSERT INTO [t3125] VALUES (2661,'[٢٦٢٣-] قلت: قال سفيان: إذا أرسلت صبيا فعثر ٣ فمات، فقد ضمنت؟ ٤','3102','2639','0');
INSERT INTO [t3125] VALUES (2662,'[٢٦٢٤-] قلت: سئل سفيان: عن رجل أذن لعبده في التجارة فجرح إنسانا؟','3103','2639','0');
INSERT INTO [t3125] VALUES (2663,'[٢٦٢٥-] قلت: قال سفيان: في رجل قال لعبده: شجني، فشجه.','3104','2639','0');
INSERT INTO [t3125] VALUES (2664,'[٢٦٢٦-] قلت ٤: وإن ٥ قال العبد للحر شجني، فشجه؟','3104','2639','0');
INSERT INTO [t3125] VALUES (2665,'[٢٦٢٧-] قلت: سئل سفيان عن صبي ومجنون قتلا أباهما، أو حفرا حفرة في غير حدهما فوقع أبوهما ٧ فيها','3105','2639','0');
INSERT INTO [t3125] VALUES (2666,'[٢٦٢٨-] قلت: قال سفيان: إذا قال الرجل لعبد رجل: اسقني فما جنى','3106','2639','0');
INSERT INTO [t3125] VALUES (2667,'[٢٦٢٩-] قلت: قال سفيان: وإذا أرسل صبيا فما جنى فهو على الصبي ٤، وإن جني عليه فالذي أرسله','3107','2639','0');
INSERT INTO [t3125] VALUES (2668,'[٢٦٣٠-] قلت: سئل سفيان: عن رجل شج مملوكا، وآخر قطع يده ضرباه جميعا، لا يدرى من أيهما مات؟','3108','2639','0');
INSERT INTO [t3125] VALUES (2669,'[٢٦٣١-] قلت: قال سفيان: إذا كان الحائط قائما، وهو مشقوق لم يجبروا ٣ على نقضه، فإن كان مائلا جبروا','3108','2639','0');
INSERT INTO [t3125] VALUES (2670,'[٢٦٣٢-] قلت: قال سفيان: فإن أخذوا في نقضه فوقع على أحدهم فهم ضامنون. ٣','3109','2639','0');
INSERT INTO [t3125] VALUES (2671,'[٢٦٣٣-] قلت: قال سفيان: وإذا ألقى رجل كيسا فيه دراهم على الطريق، فأصاب رجل رجل فعقره، فعلى','3110','2639','0');
INSERT INTO [t3125] VALUES (2672,'[٢٦٣٤-] قلت: فإن ألقى الذي أصيب رجله الدراهم في البئر ١، فهو ضامن؟','3111','2639','0');
INSERT INTO [t3125] VALUES (2673,'[٢٦٣٥-] قلت: قال سفيان: إن قاد الرجل ٣ دابته في داره فأصابت إنسانا، فعليه الضمان؟ ٤','3111','2639','0');
INSERT INTO [t3125] VALUES (2674,'[٢٦٣٦-] قلت: قال سفيان: إذا وجدوا ٤ القتيل في دار قوم ليس به أثر لم يعقل إلا ببينة أن أحدا قتله، وإن ٥','3112','2639','0');
INSERT INTO [t3125] VALUES (2675,'[٢٦٣٧-] قلت: قال سفيان في رجل قتل في القبيلة، وفيهم سكان يعني قوما ٤ [في دور] ٥ بكذا.','3113','2639','0');
INSERT INTO [t3125] VALUES (2676,'[٢٦٣٨-] قلت: قال سفيان: وكان ابن أبي ليلى يجعل عليهم الدية.','3114','2639','0');
INSERT INTO [t3125] VALUES (2677,'[٢٦٣٩-] قلت لأحمد ٤: قال سفيان في صبي يتيم قطعت يده فشهد رجلان على','3114','2639','0');
INSERT INTO [t3125] VALUES (2678,'[٢٦٤٠-] قلت: سئل سفيان: عن رجل حد عبده، ثم أعتقه بعد فشهد.','3115','2639','0');
INSERT INTO [t3125] VALUES (2679,'[٢٦٤١-] قلت ١: سئل -يعني سفيان- عن رجل قتل عبدا عمدا؟','3117','2639','0');
INSERT INTO [t3125] VALUES (2680,'[٢٦٤٢-] قلت: سئل سفيان عن رجل قتل مشركا عمدا ١؟','3118','2639','0');
INSERT INTO [t3125] VALUES (2681,'[٢٦٤٣-] قلت ٤: قيل له [رجل] ٥ حدد بعود أو بعظم ٦ فخرق به بطن','3118','2639','0');
INSERT INTO [t3125] VALUES (2682,'[٢٦٤٤-] قلت: قال: ديته على العاقلة من مات في القصاص ١.','3120','2639','0');
INSERT INTO [t3125] VALUES (2683,'[٢٦٤٥-] قلت: من يقتل في القصاص يغسل؟','3120','2639','0');
INSERT INTO [t3125] VALUES (2684,'[٢٦٤٦-] قلت: قال سمعت يعني سفيان يقول: من قتل بعصا أو بحديدة وهو مظلوم [لم ٢ يغسل] ٣.','3121','2639','0');
INSERT INTO [t3125] VALUES (2685,'[٢٦٤٧-] قلت ٢: سئل سفيان عن المحنة أن يأخذ السلطان الرجل فيمتحنه فيقول: فعلت كذا، وفعلت كذا، فلا','3122','2639','0');
INSERT INTO [t3125] VALUES (2686,'[٢٦٤٨-] قلت لأحمد: قال الحسن: [ع-١١٢/ب] لا يجرد في حد، ولا يمد. ٢','3124','2639','0');
INSERT INTO [t3125] VALUES (2687,'[٢٦٤٩-] قلت: قال الحسن ضرب الزنى أشد من ضرب القذف، والقذف أشد من الشرب، والشرب أشد من','3126','2639','0');
INSERT INTO [t3125] VALUES (2688,'[٢٦٥٠-] قلت: قال الشعبي: النساء يضربن ضربا دون ضرب، وسوطا دون سوط، ولا يجردن، ولا يمددن،','3127','2639','0');
INSERT INTO [t3125] VALUES (2689,'[٢٦٥١-] قلت: سئل سفيان عن النساء يجلدن قعودا، أو قياما؟','3128','2639','0');
INSERT INTO [t3125] VALUES (2690,'[٢٦٥٢-] قلت: قال الحسن [البصري رحمه الله تعالى] ٤: يضرب المحدود على ثياب زمانه، إن كان في','3128','2639','0');
INSERT INTO [t3125] VALUES (2691,'[٢٦٥٣-] قلت: قال سفيان: إن جامع الرجل ٤ جارية صغيرة ٥ يحد ٦؟','3129','2639','0');
INSERT INTO [t3125] VALUES (2692,'[٢٦٥٤-] قلت: قال: سألت سفيان عن صبي افتض صبية؟','3131','2639','0');
INSERT INTO [t3125] VALUES (2693,'[٢٦٥٥-] قلت: [قال] ١ سفيان ٢: استفتى يوسف ٣ بن عمر ابن أبي ليلى في هذا فقال: لها مهر مثلها في ماله','3132','2639','0');
INSERT INTO [t3125] VALUES (2694,'[٢٦٥٦-] قلت: قال الحسن: إذا أصاب الرجل الجارية التي لم تدرك جلد وغرب، وإذا أصاب الغلام المرأة','3133','2639','0');
INSERT INTO [t3125] VALUES (2695,'[٢٦٥٧-] قلت: قال الشعبي في المملوك يقذف، ثم يعتق، ثم يرجع ٥ بعدما','3133','2639','0');
INSERT INTO [t3125] VALUES (2696,'[٢٦٥٨-] قلت: قال الشعبي: امرأة شهد عليها أربعة [نفر] ٨ بالزنى فنظر','3134','2639','0');
INSERT INTO [t3125] VALUES (2697,'[٢٦٥٩-] قلت: تجوز شهادة امرأة؟','3136','2639','0');
INSERT INTO [t3125] VALUES (2698,'[٢٦٥٩-] قلت: تجوز شهادة امرأة؟','3137','2639','0');
INSERT INTO [t3125] VALUES (2699,'[٢٦٦١-] [قلت ١: قال الحكم: ٢ إذا قال زنيت وأنت مشركة، لا يضرب؟','3139','2639','0');
INSERT INTO [t3125] VALUES (2700,'[٢٦٦٢-] قلت: سئل سفيان: في ٦ رجل ٧ قال: قذفتك وأنت مشرك.','3140','2639','0');
INSERT INTO [t3125] VALUES (2701,'[٢٦٦٣-] قلت: سئل سفيان: عن رجل قال لجارية لم تحض: يا زانية؟','3142','2639','0');
INSERT INTO [t3125] VALUES (2702,'[٢٦٦٤-] قلت: قال سفيان: إذا قال الرجل للرجل: يا يهودي يا نصراني يا مجوسي؟','3144','2639','0');
INSERT INTO [t3125] VALUES (2703,'[٢٦٦٥-] قلت: سئل سفيان: عن ١ رجل قال لرجل: أنت أكثر زنى من فلان، وقد ضرب فلان في الزنى؟','3145','2639','0');
INSERT INTO [t3125] VALUES (2704,'[٢٦٦٦-] قلت: قال إبراهيم: في الرجل يقول للرجل العربي وأمه أم ولد، أو يهودية، أو نصرانية لست لأبيك؟','3145','2639','0');
INSERT INTO [t3125] VALUES (2705,'[٢٦٦٧-] قلت: قال سفيان: في العبد تكون أمه حرة فيقول له رجل: لست لأبيك، ليس عليه حد، إنما هي نفي','3147','2639','0');
INSERT INTO [t3125] VALUES (2706,'[٢٦٦٨-] قلت: سئل سفيان عن رجل قال لرجل: يا فاجر؟','3148','2639','0');
INSERT INTO [t3125] VALUES (2707,'[٢٦٦٩-] قلت: قال الشعبي: من رمي ببهيمة، أو وقع على بهيمة، فليس عليه حد ٢.','3150','2639','0');
INSERT INTO [t3125] VALUES (2708,'[٢٦٧٠-] قلت: قال سفيان: إذا قذف الرجل أمه، أو ذات محرم منه ٣.','3151','2639','0');
INSERT INTO [t3125] VALUES (2709,'[٢٦٧١-] قلت: قال: سألت ١ سفيان، عن رجل قال: إن كنت دخلت [ع-١١٤/أ] دار فلان فامرأته زانية، فشهد','3152','2639','0');
INSERT INTO [t3125] VALUES (2710,'[٢٦٧٢-] قلت: قال علي [رضي الله عنه] في ١ رجل وجد مع امرأة في لحافها: يجلد مائة. ٢','3153','2639','0');
INSERT INTO [t3125] VALUES (2711,'[٢٦٧٣-] قلت: رجل زوج جاريته، ثم وقع عليها؟','3156','2639','0');
INSERT INTO [t3125] VALUES (2712,'[٢٦٧٤-] قلت: سئل سفيان: عن رجل قال لرجل ٣: ما كان فلان ليلد مثلك؟','3156','2639','0');
INSERT INTO [t3125] VALUES (2713,'[٢٦٧٥-] قلت: [سئل] ٢ سفيان عن رجل ادعى قبل رجل أنه قذفه وليست له بينة، أيحلف؟.','3157','2639','0');
INSERT INTO [t3125] VALUES (2714,'[٢٦٧٦-] [ظ-٨٣/ب] قلت: سئل سفيان عن رجل قال لرجل: لو كنت من ولد فلان','3160','2639','0');
INSERT INTO [t3125] VALUES (2715,'[٢٦٧٧-] قلت: سئل سفيان عن رجل قذف امرأة، ثم زنت؟','3160','2639','0');
INSERT INTO [t3125] VALUES (2716,'[٢٦٧٨-] قلت: سئل عن رجل قذف مجبوبا؟','3161','2639','0');
INSERT INTO [t3125] VALUES (2717,'[٢٦٧٩-] قلت لأحمد: ١ سئل سفيان عن رجل قذف خصيا؟','3162','2639','0');
INSERT INTO [t3125] VALUES (2718,'[٢٦٨٠-] قلت: سئل [سفيان] ١ عن أربعة عميان شهدوا على امرأة بالزنى؟','3163','2639','0');
INSERT INTO [t3125] VALUES (2719,'[٢٦٨١-] قلت: قال عطاء: إذا افتضت البكر غصبا فالمهر والحد. ١','3164','2639','0');
INSERT INTO [t3125] VALUES (2720,'[٢٦٨٢-] قلت: قال سفيان في رجل زنى، أو سرق، أو شرب الخمر: أقيم عليه الحد ٢، ولو كان بعد عشرين','3165','2639','0');
INSERT INTO [t3125] VALUES (2721,'[٢٦٨٣-] [ع-١١٥/أ] قلت: سئل سفيان عن محدود في القذف أقر على نفسه بالزنى','3167','2639','0');
INSERT INTO [t3125] VALUES (2722,'[٢٦٨٤-] قلت: سئل سفيان: عن رجل تزوج أمه، أو أخته، أو ذات محرم [له] ٢، أترى عليه حدا؟','3167','2639','0');
INSERT INTO [t3125] VALUES (2723,'[٢٦٨٥-] قلت: سئل سفيان: عن رجل سرق، ثم زنى، ثم ارتد عن الإسلام، ثم تاب؟','3171','2639','0');
INSERT INTO [t3125] VALUES (2724,'[٢٦٨٦-] قلت: سئل سفيان عن امرأة شهدوا عليها بالزنى فرجمت، فرجعوا وقالوا ٧: تعمدنا؟.','3172','2639','0');
INSERT INTO [t3125] VALUES (2725,'[٢٦٨٧-] قلت: قال سفيان في الآبق: لا يقطعه مولاه ٥ قد عيب ذلك','3173','2639','0');
INSERT INTO [t3125] VALUES (2726,'[٢٦٨٨-] قلت: قال الحسن: لا يقطع الآبق إذا سرق ٣، وبه يأخذ','3175','2639','0');
INSERT INTO [t3125] VALUES (2727,'[٢٦٨٩-] قلت: سئل سفيان عن مكاتب سرق من مولاه ترى عليه حدا؟','3176','2639','0');
INSERT INTO [t3125] VALUES (2728,'[٢٦٩٠-] قلت [لأحمد] ٣ سئل ٤ سفيان: عن ثلاثة نفر اجتمعوا فسرقوا عشرة دراهم، يقطعون؟','3177','2639','0');
INSERT INTO [t3125] VALUES (2729,'[٢٦٩١-] [ظ-٨٤/أ] قلت [لأحمد] : ٤ سئل سفيان يستحب أن يقطع في','3178','2639','0');
