INSERT INTO [t3125] VALUES (2957,'[٢٩١٩-] قلت: شهادة أهل الكتاب بعضهم على بعض؟','3509','2784','0');
INSERT INTO [t3125] VALUES (2958,'[٢٩٢٠-] قلت: الوارث يعترف بدين على الميت؟','3511','2784','0');
INSERT INTO [t3125] VALUES (2959,'[٢٩٢١-] قلت: اليمين مع الشاهد؟','3513','2784','0');
INSERT INTO [t3125] VALUES (2960,'[٢٩٢٢-] قلت: لا تجوز شهادة الوالد لولده، ولا الولد لوالده، ولا الشريك لشريكه، ولا العبد لسيده، ولا السيد','3514','2784','0');
INSERT INTO [t3125] VALUES (2961,'[٢٩٢٣-] قالت: ما العدل في المسلمين؟','3517','2784','0');
INSERT INTO [t3125] VALUES (2962,'[٢٩٢٤-] قلت: شهادة العبد والمكاتب؟','3518','2784','0');
INSERT INTO [t3125] VALUES (2963,'[٢٩٢٥-] قلت: إذا شهدت الأمة في الاستهلال والرضاع؟','3519','2784','0');
INSERT INTO [t3125] VALUES (2964,'[٢٩٢٦-] قلت: يحلف الرجل مع بينته أم لا؟ ٢','3520','2784','0');
INSERT INTO [t3125] VALUES (2965,'[٢٩٢٧-] قلت: الرجل يغير شهادته ويزيد وينقص؟','3522','2784','0');
INSERT INTO [t3125] VALUES (2966,'[٢٩٢٨-] قلت: إذا شهد بشهادة ثم رجع فيها؟','3523','2784','0');
INSERT INTO [t3125] VALUES (2967,'[٢٩٢٩-] قلت: إذا استويت الشهود والسلعة في يد أحدهما؟','3524','2784','0');
INSERT INTO [t3125] VALUES (2968,'[٢٩٣٠-] قلت: شهادة المختبئ؟ ٢','3525','2784','0');
INSERT INTO [t3125] VALUES (2969,'[٢٩٣١-] قلت لأحمد: ١ رجل شهد بخمسمائة والآخر بألف؟','3527','2784','0');
INSERT INTO [t3125] VALUES (2970,'[٢٩٣٢-] قلت: من رأى أن يرد اليمين ٥ أو يحلف الرجل مع بينته؟','3527','2784','0');
INSERT INTO [t3125] VALUES (2971,'[٢٩٣٣-] قلت: الرجل يدعي على الرجل الشيء فيحلفه ثم يأتي بالبينة؟','3528','2784','0');
INSERT INTO [t3125] VALUES (2972,'[٢٩٣٤-] قلت: كان ابن أبي ليلى يجيز شهادة صاحب هوى إذا كان فيهم عدلا لا يستحل شهادة الزور. ٢','3529','2784','0');
INSERT INTO [t3125] VALUES (2973,'[٢٩٣٥-] قلت: قال سفيان: إذا ضرب المملوك في القذف ثم أعتق ١ لم تجز شهادته، وإذا ضرب النصراني','3532','2784','0');
INSERT INTO [t3125] VALUES (2974,'[٢٩٣٦-] قلت: في ١ الاستهلال يجوز شهادة امرأة واحدة، والحيض، والعدة، والسقط [والحمام] ؟ ٢','3534','2784','0');
INSERT INTO [t3125] VALUES (2975,'[٢٩٣٧-] قلت: قال سفيان: لا تجوز شهادة الأمة في ٣ الاستهلال؟ ٤','3534','2784','0');
INSERT INTO [t3125] VALUES (2976,'[٢٩٣٨-] قلت: قال سفيان: الرجل إذا كانت عنده شهادة، فقيل له','3534','2784','0');
INSERT INTO [t3125] VALUES (2977,'[٢٩٣٩-] قلت: قال سفيان: والشاهد عند القاضي يغير شهادته ويزيد فيها وينقص ما لم يقض فيها القاضي. ٢','3536','2784','0');
INSERT INTO [t3125] VALUES (2978,'[٢٩٤٠-] قلت: سئل سفيان عن رجل خاصم في خصومة مرة، ثم نزع ٤ بعد، ثم شهد بعد، أله شهادة؟ ٥','3536','2784','0');
INSERT INTO [t3125] VALUES (2979,'[٢٩٤١-] قلت: قال سفيان: إذا قال الرجل: أشهد أن لي ولفلان على هذا خمسمائة درهم، فقد بطلت شهادته','3537','2784','0');
INSERT INTO [t3125] VALUES (2980,'[٢٩٤٢-] قلت: قال سفيان: في رجل مات وترك ابنين: أحدهما نصراني، والآخر مسلم، فقال النصراني: مات','3538','2784','0');
INSERT INTO [t3125] VALUES (2981,'[٢٩٤٣-] قال سفيان: فإن ادعى النصراني أنه كان نصرانيا وادعى المسلم أنه كان مسلما، فالميراث بينهما. ١','3540','2784','0');
INSERT INTO [t3125] VALUES (2982,'[٢٩٤٤-] قلت: قال ابن أبي ليلى: السمع سمعان: إذا قال: سمعت فلانا أجزته، وإذا قال: سمعت فلانا يقول:','3541','2784','0');
INSERT INTO [t3125] VALUES (2983,'[٢٩٤٥-] قلت: قال سفيان: إذا كان مسافرا فأشهد اليهودي والنصراني لم تجز شهادتهم إذا كان معهم','3542','2784','0');
INSERT INTO [t3125] VALUES (2984,'[٢٩٤٦-] قلت: قال سفيان في النصراني مات فجاء رجل مسلم ٤ فأقام','3544','2784','0');
INSERT INTO [t3125] VALUES (2985,'[٢٩٤٧-] قلت: قال: سئل سفيان عن مسلم باع نصرانيا دابة فجاء النصراني ببينة من النصارى أنها دابته.','3546','2784','0');
INSERT INTO [t3125] VALUES (2986,'[٢٩٤٨-] قلت: قال سفيان في نصراني مات وترك ألف درهم، فجاء النصراني ببينة من المسلمين بألف','3547','2784','0');
INSERT INTO [t3125] VALUES (2987,'[٢٩٤٩-] قال أحمد: إذا شهد رجلان من الورثة، وكانا عدلين جازت شهادتهما على الورثة.','3548','2784','0');
INSERT INTO [t3125] VALUES (2988,'[٢٩٥٠-] قلت: ٣ سئل سفيان، عن رجل مات وترك ابنه، وترك ألف درهم، فجاء رجل فقال: لي على أبيك','3548','2784','0');
INSERT INTO [t3125] VALUES (2989,'[٢٩٥١-] قلت [لأحمد: قال] : سئل سفيان عن رجل مات وترك ألفي','3549','2784','0');
INSERT INTO [t3125] VALUES (2990,'[٢٩٥٢-] قلت: قال أبو هريرة [رضي الله عنه] لا تجوز شهادة أصحاب الخمر. ٤','3550','2784','0');
INSERT INTO [t3125] VALUES (2991,'[٢٩٥٣-] قلت: قال ٣ سفيان في رجل كتب وصيته فختم عليها، وقال: اشهدوا بما فيها؟','3551','2784','0');
INSERT INTO [t3125] VALUES (2992,'[٢٩٥٤-] قلت: قال سفيان: إذا سئل المريض عن الشىء فأومأ برأسه أو بيده، فليس بشىء حتى يتكلم؟ ٣','3552','2784','0');
INSERT INTO [t3125] VALUES (2993,'[٢٩٥٥-] قلت: سئل سفيان عن محدود استقضي فقضى بقضايا؟','3553','2784','0');
INSERT INTO [t3125] VALUES (2994,'[٢٩٥٦-] قلت: سئل سفيان عن صبي يتيم شهد له رجلان، أن هذا أخذ منه ألف درهم، وجاء آخران فشهدا أن','3554','2784','0');
INSERT INTO [t3125] VALUES (2995,'[٢٩٥٧-] قلت: قال سفيان: إن شريحا كان لا يقبل البينة بعد الجحود. ٤','3555','2784','0');
INSERT INTO [t3125] VALUES (2996,'[٢٩٥٨-] قلت: سئل - يعني سفيان - عن شهادة الوصي؟','3556','2784','0');
INSERT INTO [t3125] VALUES (2997,'[٢٩٥٩-] قلت: قال سفيان: تجوز شهادة رجل وامرأتين في الأهلة؟ ٥','3557','2784','0');
INSERT INTO [t3125] VALUES (2998,'[٢٩٦٠-] قلت: سئل سفيان عن شهادة المحدودين في الإهلال؟','3559','2784','0');
INSERT INTO [t3125] VALUES (2999,'[٢٩٦١-] قلت: قال سفيان في الرجل الذي يخنق في كل شهر تجوز','3559','2784','0');
INSERT INTO [t3125] VALUES (3000,'[٢٩٦٢-] قلت: قال سفيان: لا يجوز إلا قول طبيبين في الموضحة، ولا يجوز إلا قول بيطارين ٤ في الدابة','3560','2784','0');
INSERT INTO [t3125] VALUES (3001,'[٢٩٦٣-] قلت: قال: فإذا سرح القاضي إلى البيطارين لينظر ما يقولان فلا يسرح إلا رجلين، لأنهما شاهدان','3562','2784','0');
INSERT INTO [t3125] VALUES (3002,'[٢٩٦٤-] سئل إسحاق عن شهادة امرأة في الرضاع أنها أرضعت رجلا وامرأته قبل التزويج أو بعد التزويج؟','3563','2784','0');
INSERT INTO [t3125] VALUES (3003,'[٢٩٦٥-] قلت لأحمد رضي الله عنه الخنثى ٢ من أين يورث؟','3564','2784','0');
INSERT INTO [t3125] VALUES (3004,'[٢٩٦٦-] قلت: زوج وأم، وإخوة لأب وأم، وإخوة لأم ٢ هل يشرك','3565','2784','0');
INSERT INTO [t3125] VALUES (3005,'[٢٩٦٧-] قلت: في ابنتين، وابنة ١ ابن، وابن ابن.','3567','2784','0');
INSERT INTO [t3125] VALUES (3006,'[٢٩٦٨-] قلت: تورث الأسير؟','3568','2784','0');
INSERT INTO [t3125] VALUES (3007,'[٢٩٦٩-] قلت: كم تورث من الجدات؟','3568','2784','0');
INSERT INTO [t3125] VALUES (3008,'[٢٩٧٠-] قلت: تورث الجدة مع ابنها؟','3569','2784','0');
INSERT INTO [t3125] VALUES (3009,'[٢٩٧١-] قلت: المملوكون، واليهود والنصارى يحجبون؟ ١','3571','2784','0');
INSERT INTO [t3125] VALUES (3010,'[٢٩٧٢-] قلت: القاتل لا يرث خطأ أو ٤ عمدا؟ ٥','3571','2784','0');
INSERT INTO [t3125] VALUES (3011,'[٢٩٧٣-] قلت: لا يتوارث أهل ملتين شتى، لا يرث اليهودي ١ النصراني؟','3573','2784','0');
INSERT INTO [t3125] VALUES (3012,'[٢٩٧٤-] قلت: ميراث المرتد للمسلمين يقتل ويؤخذ ماله؟ ١ مات أو قتل واحد، لأن دمه كان مباحا، واحتج','3574','2784','0');
INSERT INTO [t3125] VALUES (3013,'[٢٩٧٥-] قلت: رجل مات، ولم يدع [ع-١٦٥/ب] وارثا إلا ابن أخته؟','3575','2784','0');
INSERT INTO [t3125] VALUES (3014,'[٢٩٧٦-] قلت: من قال لا يرد ٣ على ابنة ابن مع ابنة [لـ] صلب ولا','3576','2784','0');
INSERT INTO [t3125] VALUES (3015,'[٢٩٧٧-] قلت: العمة والخالة؟','3578','2784','0');
INSERT INTO [t3125] VALUES (3016,'[٢٩٧٨-] قلت: أدنى العصبة ١ الابن، ثم ابن الابن، ثم الأب ثم الجد، ثم الأخ، ثم ابن الأخ، ثم العم، ثم ابن','3579','2784','0');
INSERT INTO [t3125] VALUES (3017,'[٢٩٧٩-] حدثنا إسحاق ابن منصور ١ قال: أخبرنا أحمد، ٢ عن يحي ابن آدم ٣ قال: أخبرنا ابن أبي زائدة ٤','3580','2784','0');
INSERT INTO [t3125] VALUES (3018,'[٢٩٨٠-] حدثنا إسحاق قال: أخبرنا أحمد قال: ٢ سمعت سفيان بن عيينة يقول: قال ابن عباس [رضي الله','3583','2784','0');
INSERT INTO [t3125] VALUES (3019,'[٢٩٨١-] حدثنا إسحاق قال: أخبرنا ٢ أحمد عن وكيع ٣ عن سفيان ٤ عن منصور ٥ عن','3586','2784','0');
INSERT INTO [t3125] VALUES (3020,'[٢٩٨٢-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا عبد الرحمن بن مهدي ٢','3589','2784','0');
INSERT INTO [t3125] VALUES (3021,'[٢٩٨٣-] حدثنا إسحاق، قال أخبرنا أحمد، قال حدثنا أبو معاوية ١ عن الأعمش، ٢ عن إبراهيم قال: كان','3591','2784','0');
INSERT INTO [t3125] VALUES (3022,'[٢٩٨٤-] حدثا إسحاق قال: أخبرنا أحمد قال: حدثنا عبد الصمد ٢ قال: حدثني أبي ٣ قال: حدثنا','3593','2784','0');
INSERT INTO [t3125] VALUES (3023,'[٢٩٨٥-] حدثا إسحاق قال: أخبرنا أحمد قال: أخبرنا وكيع قال: حدثنا إسماعيل بن عبد الملك ١ قال: سألت','3595','2784','0');
INSERT INTO [t3125] VALUES (3024,'[٢٩٨٦-] حدثا إسحاق قال: أخبرنا أحمد قال: حدثنا هشيم ١ قال كان شعبة حدثنا بهذا الحديث عن سهم','3597','2784','0');
INSERT INTO [t3125] VALUES (3025,'[٢٩٨٧-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا وكيع قال: حدثنا سفيان عن الأعمش قال: كان ابن مسعود','3599','2784','0');
INSERT INTO [t3125] VALUES (3026,'[٢٩٨٨-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا محمد بن جعفر ١ قال: حدثنا شعبة ٢ عن عمرو بن مرة','3601','2784','0');
INSERT INTO [t3125] VALUES (3027,'[٢٩٨٩-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا عبد الرحمن ١ قال: حدثنا سفيان ٢ عن معبد بن خالد ٣','3604','2784','0');
INSERT INTO [t3125] VALUES (3028,'[٢٩٩٠-] حدثنا أحمد عن عبد الرحمن حدثنا أبو شهاب ٣ عن الحسن بن عمرو ٤ عن الحكم عن علي [رضي','3605','2784','0');
INSERT INTO [t3125] VALUES (3029,'[٢٩٩١-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا عبد الرحمن بن مهدي عن سفيان، ١ عن سلمة ٢ عن','3607','2784','0');
INSERT INTO [t3125] VALUES (3030,'[٢٩٩٢-] قلت لأحمد: ٥ المملوكون وأهل الكتاب [والقاتل] والمكاتب لا يحجبون ولا يورثون؟ ٦','3608','2784','0');
INSERT INTO [t3125] VALUES (3031,'[٢٩٩٣-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا يحيى بن آدم قال: حدثنا ابن أبي زائدة عن أبيه عن فراس','3609','2784','0');
INSERT INTO [t3125] VALUES (3032,'[٢٩٩٤-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا يزيد بن هارون قال: أخبرنا همام ٢ عن قتادة ٣ عن','3611','2784','0');
INSERT INTO [t3125] VALUES (3033,'[٢٩٩٥-] حدثا إسحاق قال: أخبرنا أحمد قال: حدثنا وكيع قال: حدثنا','3613','2784','0');
INSERT INTO [t3125] VALUES (3034,'[٢٩٩٦-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا عبد الوهاب الثقفي ٢ قال: حدثنا خالد ٣ عن محمد ٤ في','3616','2784','0');
INSERT INTO [t3125] VALUES (3035,'[٢٩٩٧-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا محمد بن جعفر ٣ قال: حدثنا سعيد ٤ عن قتادة عن رجاء','3617','2784','0');
INSERT INTO [t3125] VALUES (3036,'[٢٩٩٨-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا محمد بن جعفر قال: حدثنا سعيد عن قتادة عن الحسن ٢','3619','2784','0');
INSERT INTO [t3125] VALUES (3037,'[٢٩٩٩-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا جرير ٢ عن مغيرة ٣ عن الشعبي قال: إذا شهد رجلان،','3621','2784','0');
INSERT INTO [t3125] VALUES (3038,'[٣٠٠٠-] حدثنا إسحاق قال: حدثنا أحمد قال: حدثنا يزيد بن هارون قال: أخبرنا حبيب بن أبي حبيب ٢ عن','3622','2784','0');
INSERT INTO [t3125] VALUES (3039,'[٣٠٠١-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا وكيع قال: قال سفيان: ٣ في مجوسي تزوج ابنته فأصاب','3623','2784','0');
INSERT INTO [t3125] VALUES (3040,'[٣٠٠٢-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا إسماعيل ٣ عن','3624','2784','0');
INSERT INTO [t3125] VALUES (3041,'[٣٠٠٣-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا وكيع قال: حدثنا سفيان ٢ عن الشيباني ٣ عن الشعبي عن','3626','2784','0');
INSERT INTO [t3125] VALUES (3042,'[٣٠٠٤-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا هشيم ٣ قال: أخبرنا مغيرة ٤ عن شباك ٥ عن إبراهيم ٦','3628','2784','0');
INSERT INTO [t3125] VALUES (3043,'[٣٠٠٥-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا يحيى بن آدم قال','3629','2784','0');
INSERT INTO [t3125] VALUES (3044,'[٣٠٠٦-] قلت لأحمد: الخال وارث من لا وارث له؟','3631','2784','0');
INSERT INTO [t3125] VALUES (3045,'[٣٠٠٧-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا محمد بن جعفر ١ قال: حدثنا سعيد ٢ عن قتادة عن بكر','3632','2784','0');
INSERT INTO [t3125] VALUES (3046,'[٣٠٠٨-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا يحيى بن آدم قال','3633','2784','0');
INSERT INTO [t3125] VALUES (3047,'[٣٠٠٩-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا عبد الرحمن بن مهدي قال: أخبرنا شعبة عن منصور عن','3635','2784','0');
INSERT INTO [t3125] VALUES (3048,'[٣٠١٠-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا وكيع قال: حدثنا سفيان عن عبد الله بن شريك العامري ٢','3636','2784','0');
INSERT INTO [t3125] VALUES (3049,'[٣٠١١-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا وكيع عن سفيان عن قيس بن مسلم ٢ عن أبي مالك ٣','3638','2784','0');
INSERT INTO [t3125] VALUES (3050,'[٣٠١٢-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا معاذ ١ عن أشعث عن الحسن ٢ قال: لا ترث النساء ٣','3641','2784','0');
INSERT INTO [t3125] VALUES (3051,'[٣٠١٣-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا هشيم ٣ قال الشيباني ٤ أخبرنا عن الشعبي عن شريح،','3642','2784','0');
INSERT INTO [t3125] VALUES (3052,'[٣٠١٤-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا وكيع قال: حدثنا أبان بن صمعة ٢ عن عكرمة ٣ عن ابن','3644','2784','0');
INSERT INTO [t3125] VALUES (3053,'[٣٠١٥-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا معتمر ٤ عن','3645','2784','0');
INSERT INTO [t3125] VALUES (3054,'[٣٠١٦-] حدثا إسحاق قال: أخبرنا أحمد قال: حدثنا وكيع قال: حدثنا ابن أبي خالد ٤ عن الشعبي أن مولى','3646','2784','0');
INSERT INTO [t3125] VALUES (3055,'[٣٠١٧-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا عبد الله بن إدريس قال: سمعت الشيباني عن الحكم ٢','3650','2784','0');
INSERT INTO [t3125] VALUES (3056,'[٣٠١٨-] حدثنا إسحاق قال: أخبرنا أحمد قال:','3651','2784','0');
INSERT INTO [t3125] VALUES (3057,'[٣٠١٩-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا هشيم قال: أخبرنا يونس عن الحسن في رجل أعتق','3654','2784','0');
INSERT INTO [t3125] VALUES (3058,'[٣٠٢٠-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا وكيع قال: حدثنا الأعمش عن أبي وائل ٣ قال: كتب عمر','3655','2784','0');
INSERT INTO [t3125] VALUES (3059,'[٣٠٢١-] حدثنا إسحاق قال: أخبرنا أحمد قال: حدثنا عبد الرحمن بن مهدي قال: حدثنا مالك بن مغول ١ قال:','3656','2784','0');
INSERT INTO [t3125] VALUES (3060,'[٣٠٢٢-] قال أحمد في بني الأخ وجد، ٥ لا يكونوا بمنزلة','3657','2784','0');
INSERT INTO [t3125] VALUES (3061,'[٣٠٢٣-] قلت لأحمد: ٤ علي، إلى كم كان يقاسم الجد مع الإخوة؟','3658','2784','0');
INSERT INTO [t3125] VALUES (3062,'[٣٠٢٤-] قلت: من يحجب الجد ممن له فريضة؟ ٣','3660','2784','0');
INSERT INTO [t3125] VALUES (3063,'[٣٠٢٥-] قلت: مع من يرث الجد من أهل ١ الفرائض؟','3661','2784','0');
INSERT INTO [t3125] VALUES (3064,'[٣٠٢٦-] قلت: أخت لأب وأم، وأخ وأخت لأب، وجد؟','3662','2784','0');
INSERT INTO [t3125] VALUES (3065,'[٣٠٢٧-] قلت: في أخ لأب وأم، وأخ لأب، وجد، للأخ للأب والأم النصف، وللجد النصف، وسقط الأخ للأب','3666','2784','0');
INSERT INTO [t3125] VALUES (3066,'[٣٠٢٨-] قلت: كان ١ علي [رضي الله عنه كان] لايزيد الجد مع الولد على السدس ٢ إلا أن يكون غيره،','3667','2784','0');
INSERT INTO [t3125] VALUES (3067,'[٣٠٢٩-] [قلت: القوم يموتون جميعا لا يدرى أيهم مات قبل؟','3667','2784','0');
INSERT INTO [t3125] VALUES (3068,'[٣٠٣٠-] قلت: الحميل؟ ٢','3668','2784','0');
INSERT INTO [t3125] VALUES (3069,'[٣٠٣١-] قلت: إذا ملك أخاه من الرضاعة؟','3669','2784','0');
INSERT INTO [t3125] VALUES (3070,'[٣٠٣٢-] قلت: رجل توفي وترك أخاه وجده ومولى، فمات المولى؟ قال: الولاء بينهم على الميراث نصفان. ١','3670','2784','0');
INSERT INTO [t3125] VALUES (3071,'[٣٠٣٣-] قلت: امرأة أعتقت رجلا ولاؤه لولدها ما بقي منهم ذكر، فإذا انقرضوا كان الولاء لعصبة أمهم؟','3670','2784','0');
INSERT INTO [t3125] VALUES (3072,'[٣٠٣٤-] قلت: امرأة اشترت أباها فأعتقته، ثم توفي وترك ابنتيه، إحداهما التي أعتقته. قال لهما الثلثان، وما','3671','2784','0');
INSERT INTO [t3125] VALUES (3073,'[٣٠٣٥-] قلت: الأب يجر الولاء؟','3671','2784','0');
INSERT INTO [t3125] VALUES (3074,'[٣٠٣٦-] قلت: قال سمعت يعني سفيان: لا يرث من النساء إلا ستة: [ع-١٧٠/أ] الابنة، وابنة الابن، والأخت،','3672','2784','0');
INSERT INTO [t3125] VALUES (3075,'[٣٠٣٧-] قال أحمد: قضى النبي صلى الله عليه [وسلم] في ابنة حمزة لخالتها بقضائها للخالة، حتى إذا','3673','2784','0');
INSERT INTO [t3125] VALUES (3076,'[٣٠٣٨-] سئل إسحاق عن مجوسي مات ولم يدع الا ابن عمه مسلما؟','3674','2784','0');
INSERT INTO [t3125] VALUES (3077,'[٣٠٣٩-] حدثنا إسحاق بن منصور المروزي٣ [رضي الله عنه]','3678','2784','0');
INSERT INTO [t3125] VALUES (3078,'[٣٠٤٠-] قلت لأحمد بن حنبل١ [رضي الله عنه] : بكم يوصي الرجل عند موته؟','3683','2784','0');
INSERT INTO [t3125] VALUES (3079,'[٣٠٤١-] قلت: للرجل أن يوصي بماله كله إن٣ لم يكن له وارث؟','3684','2784','0');
INSERT INTO [t3125] VALUES (3080,'[٣٠٤٢-] قال: قلت إذا أوصى في غير أقاربه١ يرد ذلك إلى أقاربه؟','3686','2784','0');
INSERT INTO [t3125] VALUES (3081,'[٣٠٤٣-] قلت [لأحمد] : متى٥ تجوز وصية الغلام؟','3686','2784','0');
INSERT INTO [t3125] VALUES (3082,'[٣٠٤٤-] [قال] : قلت: إذا أوصى بوصية له أن يرجع فيها؟','3688','2784','0');
INSERT INTO [t3125] VALUES (3083,'[٣٠٤٥-] قلت [لأحمد] يوصي الرجل إلى المرأة؟ ٢','3689','2784','0');
INSERT INTO [t3125] VALUES (3084,'[٣٠٤٦-] [قال] ١: قلت: رجل وهب لرجل هبة، أو أوصى له بوصية وهو غائب، فمات','3690','2784','0');
INSERT INTO [t3125] VALUES (3085,'[٣٠٤٧-] قلت [لأحمد] رضي الله عنه النحل؟ ٣','3691','2784','0');
INSERT INTO [t3125] VALUES (3086,'[٣٠٤٨-] قال قلت: [لأحمد] إذا اعتدى في وصيته يرد [ذلك] إلى الحق؟','3693','2784','0');
INSERT INTO [t3125] VALUES (3087,'[٣٠٤٩-] [قال] قلت: من وهب هبة يرجو ثوابها يرجع في هبته إن لم يرض منها؟','3694','2784','0');
INSERT INTO [t3125] VALUES (3088,'[٣٠٥٠-] [قال] : قلت: إذا وهب هبة قيمة يرجع فيها؟','3695','2784','0');
INSERT INTO [t3125] VALUES (3089,'[٣٠٥١-] قال قلت: لأحمد هبة المرأة لزوجها وهبة الرجل لامرأته؟','3696','2784','0');
INSERT INTO [t3125] VALUES (3090,'[٣٠٥٢-] قلت: يقبض الرجل٢ من مال ولده ما أعطاه من ماله؟','3697','2784','0');
INSERT INTO [t3125] VALUES (3091,'[٣٠٥٣-] قلت: رجل تصدق بصدقة فرجعت إليه في الميراث؟','3698','2784','0');
INSERT INTO [t3125] VALUES (3092,'[٣٠٥٤-] قلت: الرجل يقول: إن اشتريت فلانا فهو حر؟','3699','2784','0');
INSERT INTO [t3125] VALUES (3093,'[٣٠٥٥-] قلت: الرجل يستأذن ورثته عند موته أن يوصي بأكثر من','3700','2784','0');
INSERT INTO [t3125] VALUES (3094,'[٣٠٥٦-] قال:١ أحمد: إذا كان له ألف درهم لا يوصي بشيء؟ ٢','3702','2784','0');
INSERT INTO [t3125] VALUES (3095,'[٣٠٥٧-] قال٣ أحمد: الوصي لا يشتري من مال الذي يلي شيئا؟ ٤','3702','2784','0');
INSERT INTO [t3125] VALUES (3096,'[٣٠٥٨-] قلت: قال مالك:١ يغير الرجل وصيته إلا [في] التدبير؟','3703','2784','0');
INSERT INTO [t3125] VALUES (3097,'[٣٠٥٩-] قلت: وصية الضعيف في عقله والسفيه والمصاب١ الذي يخنق أحيانا؟','3704','2784','0');
INSERT INTO [t3125] VALUES (3098,'[٣٠٦٠-] قلت: رجل أوصى أن يحج عنه ولم يكن حج، من أي المال٢ يكون؟','3705','2784','0');
INSERT INTO [t3125] VALUES (3099,'[٣٠٦١-] قلت: [ع-٦٨/ب] رجل أوصى بثلث ماله لرجل، ثم١ قتل٢ خطأ أو استفاد مالا؟','3707','2784','0');
INSERT INTO [t3125] VALUES (3100,'[٣٠٦٢-] قلت:١ وصية الأسير؟','3709','2784','0');
INSERT INTO [t3125] VALUES (3101,'[٣٠٦٣-] قلت: رجل مات ولم يوص، على الورثة أن يوصوا عنه؟','3709','2784','0');
INSERT INTO [t3125] VALUES (3102,'[٣٠٦٤-] قلت: قوله: {وإذا حضر القسمة أولو القربى} .١','3710','2784','0');
INSERT INTO [t3125] VALUES (3103,'[٣٠٦٥-] قلت: تجوز وصية الحامل؟','3711','2784','0');
INSERT INTO [t3125] VALUES (3104,'[٣٠٦٦-] قلت: سئل سفيان عن وصي الوصي؟','3712','2784','0');
INSERT INTO [t3125] VALUES (3105,'[٣٠٦٧-] قلت: سئل سفيان عن وكالة الوكيل؟','3714','2784','0');
INSERT INTO [t3125] VALUES (3106,'[٣٠٦٨-] قلت: سئل سفيان عن الوصي يبيع العقار؟','3714','2784','0');
INSERT INTO [t3125] VALUES (3107,'[٣٠٦٩-] قلت: رجل أوصى لرجل بسهم من ماله؟','3716','2784','0');
INSERT INTO [t3125] VALUES (3108,'[٣٠٧٠-] قلت: هل يكاتب الوصي؟','3717','2784','0');
INSERT INTO [t3125] VALUES (3109,'[٣٠٧١-] قلت: إذا كان الوصي متهما تنزع منه الوصية؟','3718','2784','0');
INSERT INTO [t3125] VALUES (3110,'[٣٠٧٢-] قلت: يعمل الوصي٢ بمال اليتيم ما يرى أنه أصلح له،٣ فإن توى٤ المال؟','3720','2784','0');
INSERT INTO [t3125] VALUES (3111,'[٣٠٧٣-] قلت: قال سفيان: لا وصية لأهل الإسلام في أر ض الحرب، ويجوز وصيتهم في أهل الإسلام.١','3721','2784','0');
INSERT INTO [t3125] VALUES (3112,'[٣٠٧٤-] قلت: إذا أوصى لقرابته فهو لأقربهم ببطن الذكر والأنثى فيه سواء؟','3722','2784','0');
INSERT INTO [t3125] VALUES (3113,'[٣٠٧٥-] قلت: المدبر من الثلث؟','3723','2784','0');
INSERT INTO [t3125] VALUES (3114,'[٣٠٧٦-] قلت: الرجل يوصي بوصايا وبعتاقة، بأيهما يبدأ؟','3724','2784','0');
INSERT INTO [t3125] VALUES (3115,'[٣٠٧٧-] قلت: قوله: {فليأكل بالمعروف} ؟ ١','3725','2784','0');
INSERT INTO [t3125] VALUES (3116,'[٣٠٧٨-] قلت:١ يكره أن يتصدق الرجل عند موته بماله كله؟','3727','2784','0');
INSERT INTO [t3125] VALUES (3117,'[٣٠٧٩-] قلت: إذا حضر القتال، ووقع الطاعون، وركب البحر، لم يجز إلا الثلث، فإن عاش، وكان قد أعتق','3728','2784','0');
INSERT INTO [t3125] VALUES (3118,'[٣٠٨٠-] قلت: بكم توصي الحامل؟ ٣','3729','2784','0');
INSERT INTO [t3125] VALUES (3119,'[٣٠٨١-] قلت: الرجل١ يوصي بوصية ثم يوصي بأخرى ولا يغير الأولى؟','3730','2784','0');
INSERT INTO [t3125] VALUES (3120,'[٣٠٨٢-] قلت: رجل أوصى لرجل٢ بعبد ولم يسمه، وله رقيق؟','3731','2784','0');
INSERT INTO [t3125] VALUES (3121,'[٣٠٨٣-] قلت: ليس بين الرجل وبين امرأته حيازة إذا وهبت له أو وهب لها؟','3732','2784','0');
INSERT INTO [t3125] VALUES (3122,'[٣٠٨٤-] قلت: عطية المرأة؟','3732','2784','0');
INSERT INTO [t3125] VALUES (3123,'[٣٠٨٥-] قلت: هل للزوج١ أن يمنع امرأته أن تصدق من مالها ما شاءت؟','3734','2784','0');
INSERT INTO [t3125] VALUES (3124,'[٣٠٨٦-] قلت: قال سفيان: كان ابن أبي ليلى إذا كاتب الوصي١ رد [هـ]','3736','2784','0');
INSERT INTO [t3125] VALUES (3125,'[٣٠٨٧-] قلت: قال سفيان: إذا التقى الصفان فما صنع فهو وصية١.','3737','2784','0');
INSERT INTO [t3125] VALUES (3126,'[٣٠٨٨-] قلت: قال سفيان: إذا تزوج في مرضه لم يحسب من الثلث؟ ١','3738','2784','0');
INSERT INTO [t3125] VALUES (3127,'[٣٠٨٩-] قلت: قال سفيان: إذا كان له١ ابنان فأقر لأحدهما بدين في مرضه، ثم مات الابن وترك ابنا والأب','3739','2784','0');
INSERT INTO [t3125] VALUES (3128,'[٣٠٩٠-] قلت: سئل سفيان عن رجل قال في مرضه: أعطوا فلانا من [أحد] كيسي مئة درهم، ولم٢ يكن في','3740','2784','0');
INSERT INTO [t3125] VALUES (3129,'[٣٠٩١-] قلت [لأحمد] : قال سفيان: [ع-٧٠/أ] إذا قال لفلان وفلان مائة درهم وأحدهما ميت، فهو للحي؟ ٢','3741','2784','0');
INSERT INTO [t3125] VALUES (3130,'[٣٠٩٢-] قلت: قال سفيان: إذا قال بين فلان٤ وفلان مائة درهم','3742','2784','0');
INSERT INTO [t3125] VALUES (3131,'[٣٠٩٣-] قلت: سئل سفيان عن رجل أوصى فقال: أعتقوا عني أحد عبدي هذين؟','3744','2784','0');
INSERT INTO [t3125] VALUES (3132,'[٣٠٩٤-] قلت: قيل لسفيان: فإن أبى الورثة؟','3745','2784','0');
INSERT INTO [t3125] VALUES (3133,'[٣٠٩٥-] قلت: قال سفيان: إذا أوصى الرجل١ في مرضه فقال: اشتروا [لي] عبد فلان بألف درهم فأعتقوه،','3746','2784','0');
INSERT INTO [t3125] VALUES (3134,'[٣٠٩٦-] قلت: قال سفيان في رجل ترك مائتي دينار وعبدا قيمته مائة دينار، وأوصى لرجل بالعبد، فسرقت','3747','2784','0');
INSERT INTO [t3125] VALUES (3135,'[٣٠٩٧-] قلت: قال سفيان من١ أوصي له بشيء بعينه فذهب فليس له شيء، فإن ذهب الذي للورثة، وبقي','3749','2784','0');
INSERT INTO [t3125] VALUES (3136,'[٣٠٩٨-] قلت: قال سفيان: من أوصي له بشيء فلم يأخذه زمانا فإنما','3749','2784','0');
INSERT INTO [t3125] VALUES (3137,'[٣٠٩٩-] قلت: قال٥ سفيان في٦ رجل مات وترك ألفي درهم، وترك دارا قيمتها ألف درهم، وأوصى لرجل','3750','2784','0');
INSERT INTO [t3125] VALUES (3138,'[٣١٠٠-] قلت: قال سفيان: هذا قولنا وأناس يقولون [ظ-٩٩/أ] للموصى له بالدار ثلاثة [ع-٧٠/ب] أرباع،','3751','2784','0');
INSERT INTO [t3125] VALUES (3139,'[٣١٠١-] قلت: سئل سفيان عن الوصي إذا أخذ المال لنفسه؟','3752','2784','0');
INSERT INTO [t3125] VALUES (3140,'[٣١٠٢-] قلت: قال عطاء في رجل أوصى لبني هاشم ليس لمواليهم شيء٢','3754','2784','0');
INSERT INTO [t3125] VALUES (3141,'[٣١٠٣-] قلت: قال إبراهيم إذا أوصى الرجل ما٣ دون الثلث أو إلى الثلث بعشرين أو بثلاثين درهما فهو من','3755','2784','0');
INSERT INTO [t3125] VALUES (3142,'[٣١٠٤-] قلت: يكره عتق اليهودي والنصراني؟','3756','2784','0');
INSERT INTO [t3125] VALUES (3143,'[٣١٠٥-] قلت: قال سفيان في رجل قال: ثلث مالي لفلان، ثم برأ فليس بشيء، لا يكون له إلا أن يقبض في','3757','2784','0');
INSERT INTO [t3125] VALUES (3144,'[٣١٠٦-] قلت: قال سفيان: إذا قال الرجل: فرسي هذا لفلان، صار له بإقراره.','3758','2784','0');
INSERT INTO [t3125] VALUES (3145,'[٣١٠٧-] قلت: قال إبراهيم في الرجل يوصي بوصايا وبعتاقة، يبدأ بالعتاقة٢. سئل سفيان: أليس هذا إذا كان','3760','2784','0');
INSERT INTO [t3125] VALUES (3146,'[٣١٠٨-] قلت: يوصى للقرابة من أهل الكتاب؟','3762','2784','0');
INSERT INTO [t3125] VALUES (3147,'[٣١٠٩-] قلت: إذا وكل الرجل الوكيل بخصومة فأقر على صاحبه الذي وكله جاز.','3763','2784','0');
INSERT INTO [t3125] VALUES (3148,'[٣١١٠-] قلت: سئل سفيان عن رجل أوصى بالسكنى؟','3764','2784','0');
INSERT INTO [t3125] VALUES (3149,'[٣١١١-] قلت: قوله ما كانوا يحبسون إلا الكراع١ والسلاح.٢','3765','2784','0');
INSERT INTO [t3125] VALUES (3150,'[٣١١٢-] قلت: قال سفيان: لا يقبض للصبي إلا أب،١ أو وصي، أو قاض.','3767','2784','0');
INSERT INTO [t3125] VALUES (3151,'[٣١١٣-] قلت: إذا وهب الرجل لامرأته شيئا ولم تقبض، لم يجز؟','3768','2784','0');
INSERT INTO [t3125] VALUES (3152,'[٣١١٤-] قلت [لإسحاق] :٥ حديث أبي بكر [رحمة الله تعالى ورضوانه عليه] فيما نحل عائشة [رحمها الله','3768','2784','0');
INSERT INTO [t3125] VALUES (3153,'[٣١١٥-] قلت: قال سفيان: إذا أوصى اليوم إلى رجل، وغدا إلى رجل، ثم أوصى إلى رجل، هم أوصياء','3772','2784','0');
INSERT INTO [t3125] VALUES (3154,'[٣١١٦-] قال إسحاق في المرأة تهب مهرها لزوجها ثم ندمت، فإن السنة٣ في ذلك إذا وهبت رغبة أورهبة لم','3773','2784','0');
INSERT INTO [t3125] VALUES (3155,'[٣١١٧-] سئل إسحاق: وأما الموصي لأقربائه من ثلثه وبباقي الثلث للمساكين٣، فأعطى الأقرباء [ع-٧١/ب]','3776','2784','0');
INSERT INTO [t3125] VALUES (3156,'[٣١١٨-] قال إسحاق: وأما الموصي إذا كان له على الأقرباء دين٥ فجعل دينه لهم بما أوصى، فإن ذلك لا','3778','2784','0');
INSERT INTO [t3125] VALUES (3157,'[٣١١٩-] قال إسحاق: وأما ما ذكرت من الوصية للمساكين، فأعطى٢ الوصي المساكين بذلك عروضا من','3780','2784','0');
INSERT INTO [t3125] VALUES (3158,'[٣١٢٠-] قال إسحاق: وأما الموصي لولد ولده سدس ماله، وهم لا يرثونه وقال: السدس٣ الباقي اجعلوه','3781','2784','0');
INSERT INTO [t3125] VALUES (3159,'[٣١٢١-] قلت: لإسحاق: إذا مات الرجل، وأوصى إلى رجل وله أولاد ثم ماتت٤ الأم، وأوصت إلى غير','3782','2784','0');
INSERT INTO [t3125] VALUES (3160,'[٣١٢٢-] قلت لإسحاق: للرجل أن يتوكل٢ بطلب دم، فإن وكل وسلم','3783','2784','0');
INSERT INTO [t3125] VALUES (3161,'[٣١٢٣-] قال إسحاق: وأما ما ذكرت عن رجل أوصى للمساكين فقيل له: توصي للصغير بشيء فتؤجر؟','3784','2784','0');
INSERT INTO [t3125] VALUES (3162,'[٣١٢٤-] قال إسحاق: وأما٢ ما وصفت أنها قالت [لك] : لا تدفع الدراهم التي عندك إلى ابني يشرب بها٣','3785','2784','0');
INSERT INTO [t3125] VALUES (3163,'[٣١٢٥-] قلت: سئل سفيان عن رجل أوصى فقال: ثلثي لفلان، ثم قال ثلثي لفلان، ثم قال ثلثي لفلان؟ ١','3787','2784','0');
INSERT INTO [t3125] VALUES (3164,'[٣١٢٦-] قال أحمد٣ [رضي الله عنه] إذا عجزت الوصية رجع على كل بقدر، وإن كان فيها عتاقة','3787','2784','0');
INSERT INTO [t3125] VALUES (3165,'[٣١٢٧-] قلت: سئل سفيان عن رجل أوصى لرجل بثوب فقطعه الورثة قميصا [١٩٩ظ] أو بأرض فبنوها، أو','3787','2784','0');
INSERT INTO [t3125] VALUES (3166,'[٣١٢٨-] قلت:٤ قال سفيان: في رجل مات وترك ألف درهم، وأوصى لرجل بألف درهم، وأوصى لرجل','3788','2784','0');
INSERT INTO [t3125] VALUES (3167,'[٣١٢٩-] قلت لأحمد: ١ سئل سفيان عن عبد بين رجلين كاتب أحدهما نصيبه؟','3791','2784','0');
INSERT INTO [t3125] VALUES (3168,'[٣١٣٠-] قلت: قال سفيان:٢ فإذا أعتق أحدهما وكان موسرا يوم يعتق وقع الضمان عليه، فإن أفلس قبل أن','3793','2784','0');
INSERT INTO [t3125] VALUES (3169,'[٣١٣١-] قلت: سئل سفيان عن رجل دبر غلامه، ثم كاتبه؟','3794','2784','0');
INSERT INTO [t3125] VALUES (3170,'[٣١٣٢-] قلت: سئل سفيان عن رجل يدبر غلامه فيموت وعليه دين للناس؟','3796','2784','0');
INSERT INTO [t3125] VALUES (3171,'[٣١٣٣-] قلت: سئل سفيان عن رجل قال لجاريته: أول ولد تلدينه فهو حر، فولدت اثنين، لا يدرى أيهما قبل','3797','2784','0');
INSERT INTO [t3125] VALUES (3172,'[٣١٣٤-] قلت: سئل سفيان عن رجل قال لجاريته: أنت حرة [ع-٧٣/أ] إن كنت لي في مال إلى شهر، فوقع','3798','2784','0');
INSERT INTO [t3125] VALUES (3173,'[٣١٣٥-] قلت: سئل سفيان عن مكاتب ملك أباه وابنه وعمه وخاله؟','3799','2784','0');
INSERT INTO [t3125] VALUES (3174,'[٣١٣٦-] قلت: سئل سفيان عن اللقيط٢ ولاؤه للذي التقطه؟','3800','2784','0');
INSERT INTO [t3125] VALUES (3175,'[٣١٣٧-] قلت: قال سفيان ليس عليه شيء إلا أن يكون أتى به سلطانا، فأمره أن ينفق عليه.٢','3802','2784','0');
INSERT INTO [t3125] VALUES (3176,'[٣١٣٨-] قلت: سئل سفيان٤ عن أم ولد نصراني إذا أسلمت؟','3804','2784','0');
INSERT INTO [t3125] VALUES (3177,'[٣١٣٩-] قلت [لأحمد] : سئل سفيان عن بيع خدمة١ المدبر، ثم يموت السيد.','3806','2784','0');
INSERT INTO [t3125] VALUES (3178,'[٣١٤٠-] قلت: قال سفيان في مكاتب عجز، وعليه دين للناس، إن شاء سيده أدى٢ عنه، وإلا سلمه إلى','3807','2784','0');
INSERT INTO [t3125] VALUES (3179,'[٣١٤١-] قلت: سئل سفيان عن المكاتب إذا لم يؤد للمواقيت؟','3807','2784','0');
INSERT INTO [t3125] VALUES (3180,'[٣١٤٢-] قلت: سئل سفيان عن الرجل١ يضمن٢ عن المكاتب للمولى؟','3810','2784','0');
INSERT INTO [t3125] VALUES (3181,'[٣١٤٣-] قلت: سئل سفيان عن عبد بين ثلاثة، جاءهم٢ بثلاثمائة درهم فقال:٣ بيعوني نفسي!','3811','2784','0');
INSERT INTO [t3125] VALUES (3182,'[٣١٤٤-] قلت سئل سفيان عن شروطهم أن لا يتزوج ولا يبرح، وأشباه هذا؟','3813','2784','0');
INSERT INTO [t3125] VALUES (3183,'[٣١٤٥-] قلت: قال إبراهيم في عبد دفع إلى رجل مالا فقال: ابتعني من','3814','2784','0');
INSERT INTO [t3125] VALUES (3184,'[٣١٤٦-] قلت: سئل سفيان عن الرجل يكاتب غلامه، فيقول الغلام: بألف [درهم] ، ويقول السيد: بألفي٢','3816','2784','0');
INSERT INTO [t3125] VALUES (3185,'[٣١٤٧-] قلت: قيل لسفيان٢: الرجل يوصي بالعبد ثم يوصي به لآخر؟','3817','2784','0');
INSERT INTO [t3125] VALUES (3186,'[٣١٤٨-] قلت: الرجل يقول: إن اشتريت فلانا فهو حر؟','3817','2784','0');
INSERT INTO [t3125] VALUES (3187,'[٣١٤٩-] قلت: للرجل أن يمنع غلامه من الكتابة إذا أراد ذلك؟','3818','2784','0');
INSERT INTO [t3125] VALUES (3188,'[٣١٥٠-] قلت: الرجل يكاتب غلامه، وينجم عليه١ نجوما، فيجيء بكتابته جميعا، فيأبى السيد أن يأخذه إلا','3820','2784','0');
INSERT INTO [t3125] VALUES (3189,'[٣١٥١-] قلت: مكاتب بين شركاء، قاطعه١ بعضهم، أيضمن لشركائه؟','3822','2784','0');
INSERT INTO [t3125] VALUES (3190,'[٣١٥٢-] قلت: ابن عمر رضي الله عنهما نهى أن يقاطع المكاتب إلا بالعروض.٣','3822','2784','0');
INSERT INTO [t3125] VALUES (3191,'[٣١٥٣-] قلت لأحمد: في الذي يعتق جاريته ويشترط ما في بطنها، ويكاتب١ ويشترط ما في بطنها؟ ٢','3824','2784','0');
INSERT INTO [t3125] VALUES (3192,'[٣١٥٤-] قلت: من أعتق شقصا له في عبد، ضمن إن كان له مال. فإن لم يكن له مال، يستسعي٣ في قيمته؟','3825','2784','0');
INSERT INTO [t3125] VALUES (3193,'[٣١٥٥-] قلت: عتق ولد الزنى؟','3828','2784','0');
INSERT INTO [t3125] VALUES (3194,'[٣١٥٦-] قلت: من كره أن يكاتب عبده إذا لم يكن له٣ حرفة؟','3828','2784','0');
INSERT INTO [t3125] VALUES (3195,'[٣١٥٧-] قلت: المكاتب عبد ما بقي عليه شيء؟','3830','2784','0');
INSERT INTO [t3125] VALUES (3196,'[٣١٥٨-] قلت: المكاتب إذا مات وترك وفاء الكتابة؟','3830','2784','0');
INSERT INTO [t3125] VALUES (3197,'[٣١٥٩-] قلت: في قوم كاتبوا جميعا فمات بعضهم؟','3832','2784','0');
INSERT INTO [t3125] VALUES (3198,'[٣١٦٠-] قلت: على المكاتب زكاة؟','3832','2784','0');
INSERT INTO [t3125] VALUES (3199,'[٣١٦١-] قلت: فيمن أعتق عبده وله مال؟','3834','2784','0');
INSERT INTO [t3125] VALUES (3200,'[٣١٦٢-] قلت: المكاتب يعجل لسيده ويضع له من كتابته؟','3836','2784','0');
INSERT INTO [t3125] VALUES (3201,'[٣١٦٣-] قلت: الكتابة على الوصفاء؟ ٢','3837','2784','0');
INSERT INTO [t3125] VALUES (3202,'[٣١٦٤-] قال أحمد: إذا أعتق ستة أعبد يقرع بينهم، إن١ كانت مستوية أقرع بينهم فيخرج٢ الثلث.','3839','2784','0');
INSERT INTO [t3125] VALUES (3203,'[٣١٦٥-] قلت: عبد بين رجلين كاتبا، فأدى إلى أحدهما كتابته، وهو يسعى للآخر [فمات] ، لمن ميراثه؟','3840','2784','0');
INSERT INTO [t3125] VALUES (3204,'[٣١٦٦-] قلت: رجلان٣ ورثا ولاء رجل عن أبيهما ثم','3841','2784','0');
INSERT INTO [t3125] VALUES (3205,'[٣١٦٧-] قلت: الرجل يطأ مكاتبته، يجلد؟','3843','2784','0');
INSERT INTO [t3125] VALUES (3206,'[٣١٦٨-] قلت: رجل كاتب٢ غلامه، وشرط عليه سهما في ماله؟','3844','2784','0');
INSERT INTO [t3125] VALUES (3207,'[٣١٦٩-] قلت: عبد بين رجلين أعتق أحدهما، وأمسك الآخر، لمن ولاؤه وميراثه؟','3845','2784','0');
INSERT INTO [t3125] VALUES (3208,'[٣١٧٠-] قلت: رجل كاتب عبده، وله ولد من أمته (و) لم يعلم بهم السيد؟','3846','2784','0');
INSERT INTO [t3125] VALUES (3209,'[٣١٧١-] قلت: رجل قال لرجل: أعتق عبدك هذا عني وعلي ثمنه؟','3847','2784','0');
INSERT INTO [t3125] VALUES (3210,'[٣١٧٢-] قلت: إذا أوصى لرجل بشيء يكون عليه واجبا حج أو كفارة يمين أو صيام أو ظهار؟','3848','2784','0');
INSERT INTO [t3125] VALUES (3211,'[٣١٧٣-] قلت: ما يجوز للمرأة من مالها أن تصدق؟','3849','2784','0');
INSERT INTO [t3125] VALUES (3212,'[٣١٧٤-] قلت:١ سئل سفيان عن رجل قال: فلان حر بعد موتي بشهر؟','3850','2784','0');
INSERT INTO [t3125] VALUES (3213,'[٣١٧٥-] قلت: عبد بين ثلاثة، أعتقه أحدهم، وكاتبه أحدهم، وأمسك','3850','2784','0');
INSERT INTO [t3125] VALUES (3214,'[٣١٧٦-] قلت: المدبر وصية ما يعني به؟','3852','2784','0');
INSERT INTO [t3125] VALUES (3215,'[٣١٧٧-] قلت: بيع المدبر من غير حاجة؟','3852','2784','0');
INSERT INTO [t3125] VALUES (3216,'[٣١٧٨-] قلت: رجل اشترى جارية وهو مريض، فأعتقها عند موته، فجاء الذين باعوها لثمنها فلم يجدوا [ظ-','3853','2784','0');
INSERT INTO [t3125] VALUES (3217,'[٣١٧٩-] قلت: رجل أعتق ثلث عبده عند موته، وأوصى ببقية الثلث لناس١ وسماهم؟','3854','2784','0');
INSERT INTO [t3125] VALUES (3218,'[٣١٨٠-] قلت: رجل كاتب عبدا له، ثم توفي السيد، وترك ابنين له، فصار المكاتب لأحدهما، فقضى حتى عتق','3855','2784','0');
INSERT INTO [t3125] VALUES (3219,'[٣١٨١-] قلت: رجل وامرأة ورثا مكاتبا، فأدى إليهما، لمن ولاؤه؟','3856','2784','0');
INSERT INTO [t3125] VALUES (3220,'[٣١٨٢-] قلت: قوم٤ ورثوا مكاتبا رجالا ونساء، فمات المكاتب وقد','3856','2784','0');
INSERT INTO [t3125] VALUES (3221,'[٣١٨٣-] قلت: إذا عجز المكاتب فرد في الرق، وقد كان٦ يصدق عليه؟','3857','2784','0');
INSERT INTO [t3125] VALUES (3222,'[٣١٨٤-] قلت: من كاتب نصيبا له في عبد أو قاطعه لم يؤد إلى هذا شيئا إلا أدى إلى هؤلاء٥ مثله، فإذا أعتق','3858','2784','0');
INSERT INTO [t3125] VALUES (3223,'[٣١٨٥-] قلت: قال قتادة:٢ كل كتابة كانت قبل العتاقة٣ فلا ضمان فيها على الذي قاطع.٤','3859','2784','0');
INSERT INTO [t3125] VALUES (3224,'[٣١٨٦-] قلت: قال قتادة: الرجل يطأ مكاتبته يجلد مائة إلا سوطا، ويغرم العقر إن كان استكرهها.','3860','2784','0');
INSERT INTO [t3125] VALUES (3225,'[٣١٨٧-] قلت: قال قتادة: إذا ابتاع المكاتبان أحدهما الآخر، هذا هذا من سيده، وهذا هذا من سيده، فالبيع','3861','2784','0');
INSERT INTO [t3125] VALUES (3226,'[٣١٨٨-] قلت: قال ابن شبرمة: من كاتب أو قاطع ضمن.٢','3862','2784','0');
INSERT INTO [t3125] VALUES (3227,'[٣١٨٩-] قلت لأحمد: كتب عمرو٢ إلى عمر [رضي الله عنه] أن رجلا كان ديوانه في قوم كان يعقل٣ معهم','3863','2784','0');
INSERT INTO [t3125] VALUES (3228,'[٣١٩٠-] قلت: قال قتادة: إذا اشترط في كتابته أني أوالي من شئت، فهو جائز؟ ١','3865','2784','0');
INSERT INTO [t3125] VALUES (3229,'[٣١٩١-] قلت [لأحمد] : قال قتادة: إذا أدى المكاتب جميع ما عليه فيوالي من شاء.٣','3865','2784','0');
INSERT INTO [t3125] VALUES (3230,'[٣١٩٢-] قال أحمد: إذا أعتق عبده في مرضه ليس له مال غيره يعتق منه الثلث، والثلثان عبد، لا يستسعى','3866','2784','0');
INSERT INTO [t3125] VALUES (3231,'[٣١٩٣-] قال أحمد: إذا قال كل عبد أشتريه فهو حر، أجبن عنه بعض الجبن، وأما الطلاق فهو أكثر.','3867','2784','0');
INSERT INTO [t3125] VALUES (3232,'[٣١٩٤-] قلت: المكاتب لمن ولاؤه؟','3868','2784','0');
INSERT INTO [t3125] VALUES (3233,'[٣١٩٥-] قلت: الرجل يسلم على يدي الرجل؟.','3868','2784','0');
INSERT INTO [t3125] VALUES (3234,'[٣١٩٦-] قلت: السائبة٢ أين يضع ماله؟','3869','2784','0');
INSERT INTO [t3125] VALUES (3235,'[٣١٩٧-] قلت: إذا والى قوما بإذن مواليه؟ ١','3872','2784','0');
INSERT INTO [t3125] VALUES (3236,'[٣١٩٨-] قلت: المدبرة، وولدها٢ بمنزلتها، إذا ولدت وهي مدبرة؟','3873','2784','0');
INSERT INTO [t3125] VALUES (3237,'[٣١٩٩-] قلت: إن عليا والزبير [رضي الله عنهما] اختصما في مولى لصفية رضي الله عنها٢ فقضى عمر','3874','2784','0');
INSERT INTO [t3125] VALUES (3238,'[٣٢٠٠-] ٢ قلت: قال إبراهيم في امرأة ماتت، وتركت أباها، وابنها (١٥١ع) وتركت','3876','2784','0');
INSERT INTO [t3125] VALUES (3239,'[٣٢٠١-] قلت- قال عطاء: رجل مات وترك جده وأخاه، وترك مولى:','3876','2784','0');
INSERT INTO [t3125] VALUES (3240,'[٣٢٠٢-] قلت: سئل سفيان٤ عن رجلين أعتقا رجلا، فمات أحدهما','3877','2784','0');
INSERT INTO [t3125] VALUES (3241,'[٣٢٠٣-] قلت: قال إبراهيم: في عبد كان٣ لقوم فأذنوا له أن يبتاع عبدا فيعتقه، ثم باعوا العبد، الولاء لمواليه','3878','2784','0');
INSERT INTO [t3125] VALUES (3242,'[٣٢٠٤-] قلت: قال إبراهيم في عبد وابنه أعتق هذا قوم، وأعتق هذا قوم، يتوارثان بالأرحام، والعقل على','3879','2784','0');
INSERT INTO [t3125] VALUES (3243,'[٣٢٠٥-] قلت: قال الشعبي: إذا ماتت المرأة، وتركت موالي وتركت ولدا، قالوا: الولاء للولد والعقل عليهم،','3880','2784','0');
INSERT INTO [t3125] VALUES (3244,'[٣٢٠٦-] قلت: قال سفيان: كل أم ولد ومدبرة ومكاتبة ولدت وأبوهم حر فالولاء لموالي أمهم، لا يجر الأب','3880','2784','0');
INSERT INTO [t3125] VALUES (3245,'[٣٢٠٧-] قلت: قال سفيان: وإذا ولدت أم الولد والمدبرة بعد موت السيد دون٤ ستة أشهر لم يجر الأب الولاء،','3881','2784','0');
INSERT INTO [t3125] VALUES (3246,'[٣٢٠٨-] قلت: سئل سفيان: أرأيت إن قال: ما في بطنك حر؟ (١٥٣ع) .','3881','2784','0');
INSERT INTO [t3125] VALUES (3247,'[٣٢٠٩-] قلت:١ قال سفيان: المكاتبة إذا أدت، أو أعتقت عتق ولدها، وأم الولد والمدبرة إذا أعتقت لم يعتق','3883','2784','0');
INSERT INTO [t3125] VALUES (3248,'[٣٢١٠-] قلت: سئل سفيان عن رجل عليه رقبة فقال لرجل: أعتق عني؟ ١','3884','2784','0');
INSERT INTO [t3125] VALUES (3249,'[٣٢١١-] قلت: سئل سفيان عن رجل قال لغلامه: أنت حر إلا أن تكره أي [ذلك] وأبوه غائب.','3884','2784','0');
INSERT INTO [t3125] VALUES (3250,'[٣٢١٢-] قلت: قال سمعت سفيان يقول في رجل قال: كل مملوك لي حر، وله مكاتب، ومدبر، يجري على','3885','2784','0');
INSERT INTO [t3125] VALUES (3251,'[٣٢١٣-] قلت: إذا طلب دينا على أبيه يحلف على علمه: أو البتة؟','3886','2784','0');
INSERT INTO [t3125] VALUES (3252,'[٣٢١٤-] قال أحمد: إذا شهد رجلان من الورثة، وكانا عدلين جازت شهادتهما على الورثة.','3886','2784','0');
INSERT INTO [t3125] VALUES (3253,'[٣٢١٥-] قلت: سئل سفيان عن مكاتبة وقع [ع-٧٨/أ] عليها سيدها؟','3887','2784','0');
INSERT INTO [t3125] VALUES (3254,'[٣٢١٦-] قلت: قال سفيان: إذا قال: أنت حر إلى أن يقدم فلان، فلا أرى شيئا وقع بعد.٢','3888','2784','0');
INSERT INTO [t3125] VALUES (3255,'[٣٢١٧-] قلت: قال سفيان: الكفيل إذا كفل بمال إلى أجل، ثم مات الكفيل قبل الأجل، أخذ من ورثة٢ الكفيل، قد','3889','2784','0');
INSERT INTO [t3125] VALUES (3256,'[٣٢١٨-] قلت: قال سفيان: إذا لزم الرجل الرجل فجاءه رجل فقال: دعه فما [كان] ٣ لك عليه٤ من حق فهو','3891','2784','0');
INSERT INTO [t3125] VALUES (3257,'[٣٢١٩-] قلت: كفالة العبد؟.','3892','2784','0');
INSERT INTO [t3125] VALUES (3258,'[٣٢٢٠-] قلت: سئل سفيان عن رجل يستدين ويتجر. فقال ابن له في حياة أبيه: اشهدوا أن [كل] ما كان على','3893','2784','0');
INSERT INTO [t3125] VALUES (3259,'[٣٢٢١-] قلت: في الكفالة إذا كتب: أيهما شئت أخذت بحقي؟','3894','2784','0');
INSERT INTO [t3125] VALUES (3260,'[٣٢٢٢-] قلت: من أسلم على ميراث قبل أن يقسم؟','3894','2784','0');
INSERT INTO [t3125] VALUES (3261,'[٣٢٢٣-] قلت: إذا أقر الرجل لوارث عند الموت, أو غير الوارث؟','3896','2784','0');
INSERT INTO [t3125] VALUES (3262,'[٣٢٢٤-] قلت لأحمد: وإذا أقر لامرأة بدين في مرضه, ثم تزوجها, ثم مات وهي وارثته لم يجز؟','3897','2784','0');
INSERT INTO [t3125] VALUES (3263,'[٣٢٢٥-] قلت: إذا سئل المريض عن شيء, فأومأ برأسه, أو بيده يجوز أم لا؟','3898','2784','0');
INSERT INTO [t3125] VALUES (3264,'[٣٢٢٦-] قلت: إذا أقر القاضي بأنه قضى بكذا وكذا, تجوز شهادته أو شهادته شهادة رجل؟','3899','2784','0');
INSERT INTO [t3125] VALUES (3265,'[٣٢٢٧-] قلت: رجل قال: فلان وارثي, ليس لي وارث غيره؟','3900','2784','0');
INSERT INTO [t3125] VALUES (3266,'[٣٢٢٨-] قلت: السائبة لمن ميراثه؟','3900','2784','0');
INSERT INTO [t3125] VALUES (3267,'[٣٢٢٩-] قلت لأحمد: الصدقة والهبة؟','3901','2784','0');
INSERT INTO [t3125] VALUES (3268,'[٣٢٣٠-] قلت: اللقيط على من نفقته؟','3901','2784','0');
INSERT INTO [t3125] VALUES (3269,'[٣٢٣١-] قلت: العبد يكون نصفه حرا, ونصفه مسترقا، لمن ماله؟','3902','2784','0');
INSERT INTO [t3125] VALUES (3270,'[٣٢٣٢-] قلت لأحمد: رجل قال لعبد رجل: أنت حر في مالي, فبلغ ذلك السيد, فقال: قد رضيت, وأبى الآخر؟','3902','2784','0');
INSERT INTO [t3125] VALUES (3271,'[٣٢٣٣-] قلت: إذا قال الرجل للرجل: أعتق عبدك هذا عني, وعلي','3902','2784','0');
INSERT INTO [t3125] VALUES (3272,'[٣٢٣٤-] قلت: قال: وولاؤه للسيد كما اعتقه، وعلى الحميل ما تحمل؟','3903','2784','0');
INSERT INTO [t3125] VALUES (3273,'[٣٢٣٥-] قلت: لا بأس أن يشتري العبد خدمته من سيده؟','3904','2784','0');
INSERT INTO [t3125] VALUES (3274,'[٣٢٣٦-] قلت: إذا قال: أنت حر على أن تخدمني كذا وكذا؟','3904','2784','0');
INSERT INTO [t3125] VALUES (3275,'[٣٢٣٧-] قلت: إذا اشترى هذه الخدمة من صاحب الذي شرط له؟ ٢','3906','2784','0');
INSERT INTO [t3125] VALUES (3276,'[٣٢٣٨-] قلت: رجل أعتق جارية له حاملا، واستثنى ما في بطنها؟','3907','2784','0');
INSERT INTO [t3125] VALUES (3277,'[٣٢٣٩-] قلت: من ملك ذا رحم محرم فهو حر؟','3907','2784','0');
INSERT INTO [t3125] VALUES (3278,'[٣٢٤٠-] قلت: ما المحرم؟','3907','2784','0');
INSERT INTO [t3125] VALUES (3279,'[٣٢٤١-] قلت: قال سفيان: رد ابن أبي ليلى عبدا أعتقه سيده عند الموت وعليه دين.','3909','2784','0');
INSERT INTO [t3125] VALUES (3280,'[٣٢٤٢-] قلت: قال: سئل سفيان عن رجل أقر لابن ابنه بدين في مرضه وهو وارثه, وأقر لامرأته بدين,','3910','2784','0');
INSERT INTO [t3125] VALUES (3281,'[٣٢٤٣-] قلت: سئل سفيان عن رجل مات وترك أربع بنين, وترك دارا وعليه دين, فجاء الغرماء يبيعون','3910','2784','0');
INSERT INTO [t3125] VALUES (3282,'[٣٢٤٤-] قلت: يجوز اعتراف للوارث بدين عند الموت؟','3911','2784','0');
INSERT INTO [t3125] VALUES (3283,'[٣٢٤٥-] قلت: سئل سفيان عن رجل أقر بدين لرجل, وعليه دين لقوم ببينة وهو مفلس؟','3911','2784','0');
INSERT INTO [t3125] VALUES (3284,'[٣٢٤٦-] قلت: سئل فإن كان في مرضه, وعليه دين ببينة, وأقر لقوم آخرين بدين؟','3912','2784','0');
INSERT INTO [t3125] VALUES (3285,'[٣٢٤٧-] قلت: سئل سفيان إذا حبس الرجل فطال حبسه وله مال لا يريد أن يبيع ماله؟','3913','2784','0');
INSERT INTO [t3125] VALUES (3286,'[٣٢٤٨-] قلت: سئل سفيان عن رجل مات وترك ابنيه فجاء رجل فقال: أنا أخوكما, فقال أحدهما: أنت أخي,','3914','2784','0');
INSERT INTO [t3125] VALUES (3287,'[٣٢٤٩-] قلت: يجزئ أم الولد, والمدبر من الرقبة؟','3915','2784','0');
INSERT INTO [t3125] VALUES (3288,'[٣٢٥٠-] قلت: يجوز اليهودي, والنصراني في الظهار واليمين.','3916','2784','0');
INSERT INTO [t3125] VALUES (3289,'[٣٢٥١-] قلت: ما يجوز في قتل النفس خطأ، أيجوز فيها أعرج, أعمى, ولد الزنى؟','3918','2784','0');
INSERT INTO [t3125] VALUES (3290,'[٣٢٥٢-] قلت: قوم ورثوا مكاتبا رجال ونساء فأعتقوه، لمن ولاؤه؟','3919','2784','0');
INSERT INTO [t3125] VALUES (3291,'مسائل شتى','3920','0','0');
INSERT INTO [t3125] VALUES (3292,'وصف النسخ الخطية وطريقة العمل في التحقيق','3920','3291','0');
INSERT INTO [t3125] VALUES (3293,'[٣٢٥٣-*] قال: قلت لأحمد رضي الله عنه: بلغك في شيء من الحديث أن السيئة تكتب','3925','3291','0');
INSERT INTO [t3125] VALUES (3294,'[٣٢٥٤-*] قلت: يحرق المصحف إذا كان فيه ذكر الله عز وجل؟','3926','3291','0');
INSERT INTO [t3125] VALUES (3295,'[٣٢٥٤-*] أشار إلى هذه المسألة عن أحمد: الزركشي في البرهان: ١/٤٧٧ ولم ينسبها.','3926','3291','0');
INSERT INTO [t3125] VALUES (3296,'[٣٢٥٥-*] قلت: يحلف الرجل مع بينته أم لا؟','3927','3291','0');
INSERT INTO [t3125] VALUES (3297,'[٣٢٥٥-*] تقدمت هذه المسألة في باب الشهادات برقم (٢٩٢٦) وبرقم (٢٩٣٢) مع بعض الاختلاف','3927','3291','0');
INSERT INTO [t3125] VALUES (3298,'[٣٢٥٦-] قلت لأحمد: أبو سعيد الخدري قال: قال رسول الله - صلى الله عليه وسلم -: "لا تحل الصدقة – ^ [] ^ [] لغني ٢- إلا لخمسة: لعامل عليها، أو لغني اشتراها بماله، أو غاز٣ في سبيل الله عز وجل، أو مسكين تصدق','3928','3291','0');
INSERT INTO [t3125] VALUES (3299,'[٣٢٥٧-] قلت: إن عمر رضي الله عنه كتب إليهم أن أعطوا من الصدقة من أبقت٣ له السنة٤ غنما وراعيا،','3929','3291','0');
INSERT INTO [t3125] VALUES (3300,'[٣٢٥٨-*] قلت: الرجل ينكح المرأة ثم تموت قبل أن يصيبها؟','3930','3291','0');
INSERT INTO [t3125] VALUES (3301,'[٣٢٥٨-*] تقدمت هذه المسألة في باب النكاح برقم (٩١٨) وفيها توسع الإمام أحمد في الإجابة، واستدل','3930','3291','0');
INSERT INTO [t3125] VALUES (3302,'[٣٢٥٩-] قلت: إذا وجد السرقة عند رجل.','3931','3291','0');
INSERT INTO [t3125] VALUES (3303,'[٣٢٦٠-*] قلت: الخلع تطليقة، فإن ندم وندمت؟','3931','3291','0');
INSERT INTO [t3125] VALUES (3304,'[٣٢٦٠-*] تقدمت هذه الرواية بنصها في النكاح برقم: (١٢٨٨) وبمعناها برقم: (١١٩٣) ورقم: (١٣٥٤) ،','3931','3291','0');
INSERT INTO [t3125] VALUES (3305,'[٣٢٦١-*] قلت: إن [ع-١٧٠/ب] عمر رضي الله عنه وقف بني عم منفوس٢ - بني عم كلالة - بالنفقة','3933','3291','0');
INSERT INTO [t3125] VALUES (3306,'[٣٢٦١-*] تقدم نحو هذه المسألة في الزكاة برقم: (٥٤٦) ، وفي النكاح برقم: (٩٨٨) . وأشار إليها صاحب','3933','3291','0');
INSERT INTO [t3125] VALUES (3307,'[٣٢٦٢-*] قلت: المرتد، لمن ميراثه إذا قتل أو مات؟','3934','3291','0');
INSERT INTO [t3125] VALUES (3308,'[٣٢٦٢-*] تقدمت هذه المسألة في النكاح برقم: (١٢٠٠) وفي الفرائض برقم (٢٩٧٤) بأوسع مما هنا واحتج','3934','3291','0');
INSERT INTO [t3125] VALUES (3309,'[٣٢٦٣-*] قلت: التطوع في البيت أفضل، أو في المسجد؟','3935','3291','0');
INSERT INTO [t3125] VALUES (3310,'[٣٢٦٣-*] تقدمت هذه المسألة في كتاب الصلاة: (٣٠٣) بأخصر مما هنا.','3935','3291','0');
INSERT INTO [t3125] VALUES (3311,'[٣٢٦٤-*] قلت: حديث النبي - صلى الله عليه وسلم -: "من قرأ قل هو الله أحد فكأنما قرأ ثلث القرآن"؟ ١','3937','3291','0');
INSERT INTO [t3125] VALUES (3312,'[*٣٢٦٤-] نقل هذه المسألة عن الإمامين أحمد، وإسحاق: ابن عبد البر في التمهيد: ١٩/٢٣٢ بسنده إلى','3937','3291','0');
INSERT INTO [t3125] VALUES (3313,'[٣٢٦٥-*] قلت: كان أبو بكر رضي الله عنه إذا أعطى الناس أعطياتهم سأل الرجل: هل عندك من مال وجبت','3939','3291','0');
INSERT INTO [t3125] VALUES (3314,'[٣٢٦٥-*] معنى هذه المسألة أن المال المستفاد أثناء الحول كالعطاء ونحوه لا يزكى حتى يحول عليه الحول.','3939','3291','0');
INSERT INTO [t3125] VALUES (3315,'[٣٢٦٦-] قلت: رجل كانت عنده عشرة دنانير فحال عليها الحول، ثم اشترى بها سلعة فربح فيها عشرة دنانير','3941','3291','0');
INSERT INTO [t3125] VALUES (3316,'[٣٢٦٧-] قلت: إذا أصاب العدو شيئا من المسلمين، فأصابه المسلمون فصاحبه أحق به ما لم يقسم؟','3941','3291','0');
INSERT INTO [t3125] VALUES (3317,'[٣٢٦٣-] تقدمت هذه المسألة بنحوها في الجهاد برقم (٢٧٥٤) ونقل نحوها ابن هانئ: ٢/١٢٤-١٢٦،','3941','3291','0');
INSERT INTO [t3125] VALUES (3318,'[٣٢٦٨-] قلت: فإذا قسم؟','3942','3291','0');
INSERT INTO [t3125] VALUES (3319,'[٣٢٦٩-] قلت: إلا بالثمن؟','3942','3291','0');
INSERT INTO [t3125] VALUES (3320,'[٣٢٧٠-*] قلت: امرأة طافت بالبيت ثم حاضت قبل أن تطوف بالصفا والمروة؟','3943','3291','0');
INSERT INTO [t3125] VALUES (3321,'[٣٢٧٠-*] تقدمت هذه المسألة في المناسك برقم: (١٧١٨) .','3943','3291','0');
INSERT INTO [t3125] VALUES (3322,'[٣٢٧١-] قلت: ولا تطوف بالصفا والمروة؟','3944','3291','0');
INSERT INTO [t3125] VALUES (3323,'[٣٢٧٢-*] قلت الصلاة على الميت في المسجد؟','3944','3291','0');
INSERT INTO [t3125] VALUES (3324,'[٣٢٧٢-*] تقدمت هذه المسألة في كتاب الصلاة: برقم (٥٤١) ، وروى ما يوافقها أبو داود في مسائله: ١٥٧،','3944','3291','0');
INSERT INTO [t3125] VALUES (3325,'[٣٢٧٣-* [] قلت لأحمد] ٢:" من اعتبط٣ مؤمنا قتلا فهو قود، إلا أن يرضى والي٤ المقتول"٥؟','3945','3291','0');
INSERT INTO [t3125] VALUES (3326,'[٣٢٧٣-*] تقدمت هذه المسألة في الحدود والديات برقم: (٢٧٢١) .','3945','3291','0');
INSERT INTO [t3125] VALUES (3327,'[٣٢٧٤-*] قلت لأحمد: امرأة موسرة ليس لها محرم؟','3946','3291','0');
INSERT INTO [t3125] VALUES (3328,'[٣٢٧٤-*] هذه المسألة لا توجد في: (ظ) .','3946','3291','0');
INSERT INTO [t3125] VALUES (3329,'[٣٢٧٥-*] قلت: رجل جامع امرأته، ثم أصاب صيدا، أو حلق رأسه، وأشباه ذلك؟','3947','3291','0');
INSERT INTO [t3125] VALUES (3330,'[٣٢٧٥-*] تقدمت هذه المسألة في المناسك برقم: (١٥٨٦) ولفظ إسحاق يختلف عما هنا فقد قال هناك: أعجب','3947','3291','0');
INSERT INTO [t3125] VALUES (3331,'[٣٢٧٦-*] قلت: محرما مس طيبا، ولبس ثوبا، وحلق رأسه، ولبس الخفين وأشباه ذلك؟','3948','3291','0');
INSERT INTO [t3125] VALUES (3332,'[٣٢٧٧-] قلت: فإنك قلت فيه مرة: عليه كفارة واحدة. فقال: هاه، دعه.','3948','3291','0');
INSERT INTO [t3125] VALUES (3333,'[٣٢٧٦-*] تقدمت هذه المسألة في المناسك برقم: (١٥٥٨) مع اختلاف يسير، ونقل تلك التي في المناسك عن','3948','3291','0');
INSERT INTO [t3125] VALUES (3334,'[٣٢٧٨-*] قلت: بيع الرجل بالدرهم الزيف١؟','3949','3291','0');
INSERT INTO [t3125] VALUES (3335,'[٣٢٧٨-*] هذه المسألة تفيد على الأقل كراهة الإمام أحمد للمعاملة بالدراهم المزيفة.','3949','3291','0');
INSERT INTO [t3125] VALUES (3336,'[٣٢٧٩-*] قلت لأحمد: من قال الفيء الجماع،٢ فإن كان [مريضا] ٣ يفئ بلسانه؟ ٤','3950','3291','0');
INSERT INTO [t3125] VALUES (3337,'[٣٢٧٩-*] تقدمت هذه المسألة في النكاح برقم (١٢٧٦) .','3950','3291','0');
INSERT INTO [t3125] VALUES (3338,'[٣٢٨٠-*] قال أحمد: إذا أطعم ستين مسكينا، يطعم مدا مدا لكل مسكين.٣','3951','3291','0');
INSERT INTO [t3125] VALUES (3339,'[٣٢٨٠-*] تقدم في باب الكفارات رواية نحوها في كفارة اليمين برقم: (١٧٤٠) و (١٧٥١) ، وكذا في مسائل','3951','3291','0');
INSERT INTO [t3125] VALUES (3340,'[٣٢٨١-*] قال أحمد: إذا ارتدا١ جميعا أو أحدهما ثم تابا أو تاب فهو أحق بها ما لم تنقض٢ العدة.٣','3952','3291','0');
INSERT INTO [t3125] VALUES (3341,'[٣٢٨٢-*] قلت: إذا وجد القتيل صلي عليه وعقل، فإذا [وجد] ٤ رأس أو رجل لم يصلى عليه ولم يعقل؟','3952','3291','0');
INSERT INTO [t3125] VALUES (3342,'[٣٢٨١-*] نقل هذه الرواية بنصها ابن قدامة في المغني: ١٠/٤٠. وقد تقدمت في النكاح برقم: (١١٩٩) .','3952','3291','0');
INSERT INTO [t3125] VALUES (3343,'[٣٢٨٢-*] نقل هذه الرواية أبو يعلى في الروايتين: ١/٢٠٤، وابن قدامة في المغني: ٣/٤٨٠.','3952','3291','0');
INSERT INTO [t3125] VALUES (3344,'[٣٣٨٧-*] قلت: رجل أشرف على جاره، على من السترة؟','3953','3291','0');
INSERT INTO [t3125] VALUES (3345,'[٣٣٨٧-*] نقل هذه المسألة أبو يعلى في الأحكام السلطانية: ٣٠٣. ونقل نحوها عن محمد ابن يحيى الكحال.','3953','3291','0');
INSERT INTO [t3125] VALUES (3346,'[٣٢٨٣-] قال أحمد: أما القتيل فيصلى عليه، إلا أن يدعي أولياؤه على قوم فتكون قسامة.٢','3954','3291','0');
INSERT INTO [t3125] VALUES (3347,'[٣٢٨٤-] قلت لأحمد: قوله - صلى الله عليه وسلم -: "ألحقوا المال بالفرائض، فما تركت الفرائض فلأولى','3954','3291','0');
INSERT INTO [t3125] VALUES (3348,'[٣٢٨٥-] قلت [لأحمد] ١: أبو سلمة بن عبد الرحمن٢ سمع من عبد الله ابن سلام رضي الله عنه في الساعة','3956','3291','0');
INSERT INTO [t3125] VALUES (3349,'[٣٢٨٦-*] قال أحمد: أبو داود النخعي١ من أكذب الناس، وأبو مريم الأنصاري٢ كان رافضيا.٣','3957','3291','0');
INSERT INTO [t3125] VALUES (3350,'[٣٢٨٦-*] روى هذه المسألة إلى قوله: أكذب الناس:','3957','3291','0');
INSERT INTO [t3125] VALUES (3351,'[٣٢٨٧-*] قال أحمد: كان خالد بن القاسم١ يزيد في الإسناد.','3958','3291','0');
INSERT INTO [t3125] VALUES (3352,'[٣٢٨٨-*] قال أحمد: أبو البختري وهب بن وهب البغدادي كان من أكذب الناس.٢','3958','3291','0');
INSERT INTO [t3125] VALUES (3353,'[٣٢٨٧-*] روى هذه المسألة ابن أبي حاتم في الجرح والتعديل: ٣/٣٤٧ بسنده إلى إسحاق بن منصور قال:','3958','3291','0');
INSERT INTO [t3125] VALUES (3354,'[* ٣٢٨٨-] روى هذه المسألة ابن أبي حاتم في الجرح والتعديل: ٩/٢٦ بسنده إلى إسحاق ابن منصور قال:','3958','3291','0');
INSERT INTO [t3125] VALUES (3355,'[٣٢٨٩-] قلت: قال: شيع علي رسول الله - صلى الله عليه وسلم - في غزوة تبوك، ولم يتلقه١؟','3959','3291','0');
