INSERT INTO [t32384] VALUES (621,'فصل في الكلام الذي يؤدي إليه قول المعترض الذي اعترف به','1011','0','0');
INSERT INTO [t32384] VALUES (622,'كلام المعترض فيه اعتراف بعد البحث التام والتدقيق في المعاني أن قوله يوجب الارتباك بعد الارتباك','1011','621','0');
INSERT INTO [t32384] VALUES (623,'إنصاف المجيب للمعترض بأنه لو وفى بموجب ما ذكره لكان قوله أقرب إلى الحق من قول الأصحاب','1012','621','0');
INSERT INTO [t32384] VALUES (624,'الأقسام المعلقة بصيغة النذر نوعان','1013','621','0');
INSERT INTO [t32384] VALUES (625,'أحدهما: نذر التبرر','1013','621','0');
INSERT INTO [t32384] VALUES (626,'الثاني: نذر اليمين، وهو على وجهين','1013','621','0');
INSERT INTO [t32384] VALUES (627,'فصل في الكلام على زعم المعترض أنه قليل الهجوم على ما لم يسبق إليه','1016','0','0');
INSERT INTO [t32384] VALUES (628,'فصل في الكلام على قول المعترض: (ونشأ مما قلته أن النذر هاهنا يكون بمثابة اليمين المعلقة على الفعل)','1018','0','0');
INSERT INTO [t32384] VALUES (629,'ثناء المجيب على هذا الموضع من كلام المعترض وأنه من أجود ما قاله ويحتاج إلى تمام','1018','628','0');
INSERT INTO [t32384] VALUES (630,'فصل في الكلام على قول المعترض: (وينشأ عليه أن التكفير قبل فوات الفعل بمنزلة التكفير قبل الحنث. . . إلخ)','1019','0','0');
INSERT INTO [t32384] VALUES (631,'فصل في الكلام عن قول المعترض: (ولو صح لي هذا المعنى كنت أقول بالكفارة عينا. . . إلخ)','1022','0','0');
INSERT INTO [t32384] VALUES (632,'قول المعترض: (إن فعلت الحج هو التزام تخلصه الكفارة. . . إلخ) قول يقول به جميع من قال بالكفارة في هذه اليمين','1022','631','0');
INSERT INTO [t32384] VALUES (633,'فصل في الكلام على قول المعترض: (ولهذا ذهب بعض الأصحاب إلى أنا وإن قلنا الواجب الكفارة. . . إلخ)','1023','0','0');
INSERT INTO [t32384] VALUES (634,'فصل في الكلام على قول المعترض: (ولا شك أنه متى ثبت ذلك كان التفريع عليه دليلا. . . إلخ)','1024','0','0');
INSERT INTO [t32384] VALUES (635,'فصل في الجواب عن قول المعترض: (ولكن الشأن في ثبوته بالتخيير على أنه ثبت الملزوم في ذمته. . . إلخ)','1024','0','0');
INSERT INTO [t32384] VALUES (636,'فصل في الجواب عن قول المعترض: (والألفاظ الواردة عن السلف في الكفارة كثير منها يمكن تأويله بأنها تجزئ لا أنها محتمة)','1025','0','0');
INSERT INTO [t32384] VALUES (637,'فصل في الجواب عن قول المعترض: (ومتى قلنا: إن قصد الحث أو المنع مانع من ثبوت الملتزم وأنه ناقل للكلام. . . إلخ)','1026','0','0');
INSERT INTO [t32384] VALUES (638,'الرد على قول المعترض: (أنه ناقل للكلام عن معنى نذر الإعتاق إلى معنى الحلف)','1027','637','0');
INSERT INTO [t32384] VALUES (639,'المعترض ليس بحاجة لقوله: (لا غير) بل الكلام تضمن معنى يمينين','1027','637','0');
INSERT INTO [t32384] VALUES (640,'قول المعترض: (أنه ناقل للكلام. . . إلخ) لا يحتاج إليه','1029','637','0');
INSERT INTO [t32384] VALUES (641,'فصل في الجواب عن قول المعترض: (ويمكن أن يقال فيه: أنا إنما نجعل قصد المنع صارفا لما صورته صورة النذر. . . إلخ)','1030','0','0');
INSERT INTO [t32384] VALUES (642,'فصل: قال المعترض: (ولقد أجاد الماوردي حيث لم يحك وجوب الكفارة يمينا عن أحد. . . إلخ)','1031','0','0');
INSERT INTO [t32384] VALUES (643,'فصل: قال المعترض: (ويحكى أن القفال كان ينكر كون تعيين الكفارة قولا للشافعي. . . إلخ)','1031','0','0');
INSERT INTO [t32384] VALUES (644,'فصل في الجواب عن منع المعترض أن يكون نذر اللجاج والغضب يسمى يمينا','1033','0','0');
INSERT INTO [t32384] VALUES (645,'فصل في الجواب عن زعم المعترض أن التعليق المتنازع فيه لا يسمى يمينا، وإن سمي به فعلى وجه المجاز','1038','0','0');
INSERT INTO [t32384] VALUES (646,'الجواب عن قول المعترض: (فمن ادعى أن إطلاق اسم اليمين حقيقة لغوية أو شرعية فعليه إقامة الدليل على ذلك)','1039','645','0');
INSERT INTO [t32384] VALUES (647,'فصل في الجواب عن قول المعترض: (وقد صرح في مبسوط الحنيفة أن أهل اللغة لا يعرفون ذلك)','1040','0','0');
INSERT INTO [t32384] VALUES (648,'فصل في الكلام على حكم الإيلاء بغير اسم الله','1042','0','0');
INSERT INTO [t32384] VALUES (649,'الجواب من وجهين','1042','648','0');
INSERT INTO [t32384] VALUES (650,'الوجه الأول: أن جمهور العلماء على أن الإيلاء ينعقد إما بكل يمين مكفرة، وإما بكل يمين من أيمان المسلمين','1042','649','0');
INSERT INTO [t32384] VALUES (651,'الوجه الثاني: أن نزاع بعض من خالف في ذلك أنها يمين منهي عنها فلا ينعقد بها الإيلاء لا أنها ليست يمينا','1043','649','0');
INSERT INTO [t32384] VALUES (652,'فصل في إيلاء بغير الله داخل في آية: "للذين يؤلون من نسائهم." الآية','1046','0','0');
INSERT INTO [t32384] VALUES (653,'الوجه الأول: أن كلامنا مع من يسلم بأن هذا إيلاء داخل في الآية، وأن نذر اللجاج والغضب داخل فيها','1046','652','0');
INSERT INTO [t32384] VALUES (654,'الوجه الثاني: أنه تبين أن هذا يمين بما تقدم من الأدلة','1046','652','0');
INSERT INTO [t32384] VALUES (655,'الوجه الثالث: كما قستم نذر اللجاج على اليمين في الكفارة وقستموه على اليمين في الاستثناء فقيسوه على اليمين في الكفارة','1046','652','0');
INSERT INTO [t32384] VALUES (656,'خاتمة النسخة','1047','0','0');
INSERT INTO [t32384] VALUES (657,'فهرس المراجع','1048','0','0');
