INSERT INTO [t3525] VALUES (1,'إنكم لمغفلون أفضل العبادة: التواضع','6','0','0');
INSERT INTO [t3525] VALUES (2,'هذه مع الدنيا، ثم تقدم فقاتل حتى قتل','7','0','0');
INSERT INTO [t3525] VALUES (3,'كان عيسى عليه السلام يأكل الشجر، ويلبس الشعر، ويبيت حيث يمسي، ولا يخبئ لغد، ولم يكن له','8','0','0');
INSERT INTO [t3525] VALUES (4,'من الذين يرثون دار قدسك؟ قال: يا موسى، هم النقية أيديهم، الطاهرة قلوبهم، الذين إذا ذكرت','9','0','0');
INSERT INTO [t3525] VALUES (5,'أتشكوني إلى عبادي؟ قال: أي رب، خطيئة أخطأتها، وذنب أذنبته، فاغفر لي','10','0','0');
INSERT INTO [t3525] VALUES (6,'إن أحببتم أن تكونوا من أصفياء الله، ونور بني آدم من خلقه، فأصلحوا بين الناس، وأحبوا من','11','0','0');
INSERT INTO [t3525] VALUES (7,'لا ينفخك روح السلطان؛ فإنك إنما خلقت من تراب، وإلى تراب تعود، ورثت مكان من كان قبلك،','12','0','0');
INSERT INTO [t3525] VALUES (8,'موتى غدا يبكون على ميت اليوم؟ ‍','13','0','0');
INSERT INTO [t3525] VALUES (9,'فإنه كان للأوابين غفورا} [الإسراء: ٢٥] ، قال: التوابين','14','0','0');
INSERT INTO [t3525] VALUES (10,'ثم أورثنا الكتاب} [فاطر: ٣٢] ، قالت: السابق بالخيرات: محمد صلى الله عليه وسلم، والمقتصد:','15','0','0');
INSERT INTO [t3525] VALUES (11,'كأنك تهاونت بركعتيك قبل، لئن نقدر على مثلها أحب إلينا من كذا وكذا، إنكم تعملون ولا','16','0','0');
INSERT INTO [t3525] VALUES (12,'ما زلت في هدم عمرك منذ سقطت من بطن أمك','17','0','0');
INSERT INTO [t3525] VALUES (13,'أدركت صدر هذه الأمة ينامون على أطرافهم، يفترشون وجوههم، قد جرت دموعهم على خدودهم، يناجون','18','0','0');
INSERT INTO [t3525] VALUES (14,'كان فراش رسول الله صلى الله عليه وسلم من أدم حشوه ليف. حدثنا أبو حاتم قال: حدثنا الأويسي','19','0','0');
INSERT INTO [t3525] VALUES (15,'حضر ملك الموت رجلا يموت، فشق أعضاءه فلم يجده عمل خيرا، ثم شق قلبه فلم يجد فيه خيرا، ثم فك','20','0','0');
INSERT INTO [t3525] VALUES (16,'أمر الله عز وجل بعبد إلى النار، فلما وقف على شفتها التفت فقال: أما والله يا رب إن كان ظني','21','0','0');
INSERT INTO [t3525] VALUES (17,'ما من عبد مؤمن يخرج من عينه دموع من خشية الله، وإن كان مثل رأس الذباب، فتصيب شيئا، إلا','22','0','0');
INSERT INTO [t3525] VALUES (18,'أمن هو قانت آناء الليل} [الزمر: ٩] ، قال: ساعات الليل، أوله وآخره','23','0','0');
INSERT INTO [t3525] VALUES (19,'وتأكلون التراث} [الفجر: ١٩] ، قال: التراث نصيبه ونصيب صاحبه، حلاله','24','0','0');
INSERT INTO [t3525] VALUES (20,'فلولا أنه كان من المسبحين} [الصافات: ١٤٣] ، قال: لولا أنه قدم عملا صالحا للبث في','25','0','0');
INSERT INTO [t3525] VALUES (21,'كذلك نبلوهم بما كانوا يفسقون} [الأعراف: ١٦٣] ، قال: بما يعملون قبل ذلك من','26','0','0');
INSERT INTO [t3525] VALUES (22,'اللؤلؤ والمرجان} [الرحمن: ٢٢] ، قال: اللؤلؤ: العظام، والمرجان: الصغار','27','0','0');
INSERT INTO [t3525] VALUES (23,'وإذا أردنا أن نهلك قرية أمرنا مترفيها} [الإسراء: ١٦] ، قال: كان أهل الجاهلية إذا كثر','28','0','0');
INSERT INTO [t3525] VALUES (24,'وإنا لجاعلون ما عليها صعيدا جرزا} [الكهف: ٨] ، قال: ليس عليه شيء','29','0','0');
