INSERT INTO [t3525] VALUES (31,'ذرأنا لجهنم} [الأعراف: ١٧٩] ، قال: خلقنا لجهنم','36','0','0');
INSERT INTO [t3525] VALUES (32,'فلما آسفونا} [الزخرف: ٥٥] ، قال: أغضبونا','37','0','0');
