INSERT INTO [t3525] VALUES (41,'كم من يد فاجرة قد همت، فمنعها الله وكفها حدثنا أبو حاتم قال: هذا من غرر','46','0','0');
INSERT INTO [t3525] VALUES (42,'أنكالا} [المزمل: ١٢] قال: القيود','47','0','0');
