INSERT INTO [t3525] VALUES (52,'فنادوا ولات حين مناص} [ص: ٣] ، قال: حين لا نزو، ولا فرار','57','0','0');
INSERT INTO [t3525] VALUES (53,'وأنى لهم التناوش} [سبأ: ٥٢] ، قال: الرد','58','0','0');
INSERT INTO [t3525] VALUES (54,'يخرج من النار من ذكرني يوما، أو خافني في مقام','59','0','0');
INSERT INTO [t3525] VALUES (55,'اليوم يومك، لا يوم لك غيره، فتعمل في ذلك اليوم ما شاء الله أن تعمل، فإذا أمست قالت: يا','60','0','0');
INSERT INTO [t3525] VALUES (56,'جنب قلبك الريب وما تخشى فساده','61','0','0');
