INSERT INTO [t3525] VALUES (59,'المشهود يوم القيامة، والموعود يوم القيامة، وقرأ: {ذلك يوم مجموع له الناس، وذلك يوم مشهود}','64','0','0');
INSERT INTO [t3525] VALUES (60,'فلا تسمع إلا همسا} [طه: ١٠٨] ، قال: همس أقدامهم','65','0','0');
