INSERT INTO [t3525] VALUES (63,'كان إذا قرأ: {فكيف إذا جئنا من كل أمة بشهيد وجئنا بك على هؤلاء شهيدا} [النساء: ٤١]','68','0','0');
INSERT INTO [t3525] VALUES (64,'الملائكة تفرح للمؤمن بالشتاء: أن ليله طويل يقومه، وأن نهاره قصير يصومه','69','0','0');
