INSERT INTO [t3525] VALUES (95,'من حدث الناس بما رأته عيناه، أو سمعت أذناه، أو أخذته يداه، فهو من {الذين يحبون أن تشيع','100','0','0');
INSERT INTO [t3525] VALUES (96,'إن استطعت أن تغير خلقك بأحسن فافعل، وإلا فيسعك من أخلاقنا ما ضاق عليك من','101','0','0');
