INSERT INTO [t3525] VALUES (98,'يكره أن يقول له الرجل من إخوانه: أمتع الله بك، قال: إنما هي دعوة محدثة','103','0','0');
INSERT INTO [t3525] VALUES (99,'ما علي وقد لبست درعي، اليوم أموت عند طاقتي','104','0','0');
INSERT INTO [t3525] VALUES (100,'كانت كسوة بكر بن عبد الله المزني تبلغ أربعة آلاف','105','0','0');
INSERT INTO [t3525] VALUES (101,'لا يصلح أن تركب على بضعة من رسول الله صلى الله عليه وسلم','106','0','0');
