INSERT INTO [t3525] VALUES (105,'ورأيت الضحاك يصلي في لبادة ويده في جوفها','110','0','0');
INSERT INTO [t3525] VALUES (106,'لأقعدن لهم صراطك المستقيم} [الأعراف: ١٦] ، قال: طريق مكة','111','0','0');
