INSERT INTO [t358] VALUES (1,'مقدمة المؤلف.','2','0','0');
INSERT INTO [t358] VALUES (2,'الباب الأول: في معنى التأويل، لغة واصطلاحا.','3','0','0');
INSERT INTO [t358] VALUES (3,'الفرق بين «آل» و «حال»، وبين «حال» و «استحال».','3','2','0');
INSERT INTO [t358] VALUES (4,'اشتقاق التأويل، وإطلاقاته، ومعنى تأويل الرؤيا، وتأويل الفعل، وتأويل اللفظ.','4','2','0');
INSERT INTO [t358] VALUES (5,'معنى قوله - صلى الله عليه وسلم - لابن عباس: «علمه التأويل».','6','2','0');
INSERT INTO [t358] VALUES (6,'الباب الثاني: مقدمة في الصدق والكذب.','7','0','0');
INSERT INTO [t358] VALUES (7,'نعمة الكلام على الناس وفضيلة الصدق.','8','6','0');
INSERT INTO [t358] VALUES (8,'تشديد الشارع في الكذب.','9','6','0');
INSERT INTO [t358] VALUES (9,'علامات المنافق المذكورة في الحديث تدور كلها على الكذب.','10','6','0');
INSERT INTO [t358] VALUES (10,'الترخيص في بعض ما يسمى كذبا.','11','6','0');
INSERT INTO [t358] VALUES (11,'المقصود بالكذب المرخص به عند الضرورة.','12','6','0');
INSERT INTO [t358] VALUES (12,'عدم خلو الكذب من المفاسد ولو كان لضرورة.','12','6','0');
INSERT INTO [t358] VALUES (13,'كذبات إبراهيم عليه الصلاة والسلام كانت من باب التورية، مع تسميتها كذبات.','14','6','0');
INSERT INTO [t358] VALUES (14,'ثلاثة أنواع أخرى من الكلام داخل تحت التورية، وأمثلة عليها.','15','6','0');
INSERT INTO [t358] VALUES (15,'مؤدى ما سبق استحالة كذب الله تعالى أو رسوله - صلى الله عليه وسلم - في كلامهما.','18','6','0');
INSERT INTO [t358] VALUES (16,'الباب الثالث: في حكم التأويل.','19','0','0');
INSERT INTO [t358] VALUES (17,'اللفظ المراد تأويله لا يخلو عن أن يكون: في العقائد، أو الأخبار، أو الأحكام.','19','16','0');
INSERT INTO [t358] VALUES (18,'الفصل الأول: تأويل النصوص الواردة في العقيدة.','20','16','0');
INSERT INTO [t358] VALUES (19,'نصوص العقيدة على ضربين: ما ورد في عقيدة كلف الناس باعتقاده، أوبخلافه.','20','18','0');
INSERT INTO [t358] VALUES (20,'العقيدة الواجبة هي الإيمان بالله وملائكته وكتبه ورسله والبعث بعد الموت والقدر','20','18','0');
INSERT INTO [t358] VALUES (21,'تفاصيل العقيدة راجعة إلى أركان الإيمان الستة الآنف ذكرها.','21','18','0');
INSERT INTO [t358] VALUES (22,'عامة ما ذكر من العقيدة الواجبة يمكن إدراكه بالعقل.','22','18','0');
INSERT INTO [t358] VALUES (23,'محدودية عقل المخلوق، والجهل بحكمة بعض الأمور لا يخدش في موافقتها للعقل.','23','18','0');
INSERT INTO [t358] VALUES (24,'الضروريات في الإيمان معلومة من الدين بالضرورة وتأويلها بما ينافي ذلك كفر.','25','18','0');
INSERT INTO [t358] VALUES (25,'الاختلاف في نصوص الصفات بين مجريها على الظاهر وبين مؤوليها بقرائن متوهمة.','26','18','0');
INSERT INTO [t358] VALUES (26,'الجواب من وجهين عن احتجاج المؤولين للصفات بتأويل السلفيين لمعية الله.','26','18','0');
INSERT INTO [t358] VALUES (27,'قام البرهان على وجوب حمل النصوص على ظواهرها','26','18','0');
INSERT INTO [t358] VALUES (28,'من تفصيلات ضروريات الإيمان ما لا يتوقف الإيمان بها أو بالعلم بها.','26','18','0');
INSERT INTO [t358] VALUES (29,'اللفظ الظاهر قد يكون ظاهرا في نفسه ولكن اقترن به ما يصير له معنى آخر، ومثاله','27','18','0');
INSERT INTO [t358] VALUES (30,'العقل لا يصح أن يكون قرينة لصرف اللفظ عن ظاهره إلا إذا كان بديهيا للمخاطبين','27','18','0');
INSERT INTO [t358] VALUES (31,'عدم إرادة المتكلم للظاهر مع انتفاء القرينة المدركة لصرفه عنه = يجعل كلامه كذبا.','27','18','0');
INSERT INTO [t358] VALUES (32,'وجوه في الجواب عن شبهة من جعل آية {ليس كمثله شيء} ونحوها قرينة للتأويل.','28','18','0');
INSERT INTO [t358] VALUES (33,'الفرق بين آيات الصفات وآيات التحليل والتحريم.','29','18','0');
INSERT INTO [t358] VALUES (34,'كثير من القوانين لا تطابق الحكمة في كل فرد، وإنما روعي مطابقتها في الأغلب.','29','18','0');
INSERT INTO [t358] VALUES (35,'إبطال قول من زعم أن الشرع استحسن الكذب بما يوافق اعتقاد الناس لإصلاحهم.','31','18','0');
INSERT INTO [t358] VALUES (36,'عند هؤلاء أن عامة الصحابة والتابعين وغالب الأمة مخطئون في اعتقادهم.','31','18','0');
INSERT INTO [t358] VALUES (37,'لو سلم أن الكذب قد يكون حسنا، فإنما ذلك من الإنسان العاجز المحتاج.','32','18','0');
INSERT INTO [t358] VALUES (38,'كان في الصحابة جماعة من أهل الذكاء يلازمون النبي - صلى الله عليه وسلم - فكان ينبغي أن يبوح لهم بالحقيقة.','32','18','0');
INSERT INTO [t358] VALUES (39,'إبطال قول من زعم أن الصحابة أمروا بكتمان حقيقة ما يعتقده العامة من الكذب.','33','18','0');
INSERT INTO [t358] VALUES (40,'مذهب بعض نفاة الصفات أن ما ورد في هذا الباب من الأمور المحيرة.','34','18','0');
INSERT INTO [t358] VALUES (41,'مذاهب من أثبت الصفات على ظاهرها، بين مؤول ومثبت لها على الظاهر دون تأويل','34','18','0');
INSERT INTO [t358] VALUES (42,'إبطال قول من جعل مذهب مثبتي الصفات على ظاهرها كمذهب المشبهة والمجسمة.','35','18','0');
INSERT INTO [t358] VALUES (43,'غالب صفات الأشياء يختلف تصورها تبعا لاختلاف تصور الموصوف بها.','35','18','0');
INSERT INTO [t358] VALUES (44,'لا ندرك من صفات الله تعالى إلا بما يشبه ما يتصف به المخلوق بقدر مشترك في الجملة.','35','18','0');
INSERT INTO [t358] VALUES (45,'باتفاق العقلاء لا يدرك شيء إلا إن تناوله الإحساس أوبفرد مماثل له مع قدر مشترك.','35','18','0');
INSERT INTO [t358] VALUES (46,'اليأس من إيجاد صورة ذهنية لصفة تليق بالله ينتج اعترافا بالعجز أوإنكارا للصفة.','37','18','0');
INSERT INTO [t358] VALUES (47,'ضرب أمثلة واقعية لبيان أن الإنسان يجحد بما لا يحس به من الأمور وبما لا يشبهه.','38','18','0');
INSERT INTO [t358] VALUES (48,'ضلال النفاة لأحد أمور: قلة معرفة بالشرع، أوتقديس للفلاسفة، أوتطلعهم لما لا يدرك.','40','18','0');
INSERT INTO [t358] VALUES (49,'مدى العقل كضعف البصر، فلكليهما حد ينتهي إليه، ثم يتوهم بعد ذلك خطأ.','40','18','0');
INSERT INTO [t358] VALUES (50,'كل شبهة عقلية زعم أنها برهان قاطع وجد من ينقضها ثم ثالث يدفع النقض وهكذا.','41','18','0');
INSERT INTO [t358] VALUES (51,'من شبه العقلانيين الاستقراء، وهو استقراء مبني على ما تطاله حواسهم، وأمثلة عليه.','42','18','0');
INSERT INTO [t358] VALUES (52,'العقل ينفي بعض الأشياء لأنه لم يدركها، أو لم يدرك صحتها، أو مطابقتها للحكمة.','43','18','0');
INSERT INTO [t358] VALUES (53,'من صفات الله ما لا شبهة فيه للمنكر، ومنها ما شبهتها من الفلاسفة فيجب ردها.','45','18','0');
INSERT INTO [t358] VALUES (54,'من صفات الله ما تعرض فيه الشبهة لكل أحد، وهذه يجب صرف الذهن عنها.','45','18','0');
INSERT INTO [t358] VALUES (55,'الاختلاف بين أوائل الفلاسفة وأواخرهم وتخطئة بعضهم بعضا يدل أن عقولهم قاصرة.','45','18','0');
INSERT INTO [t358] VALUES (56,'تفصيل القول في آية: {هو الذي أنزل عليك الكتاب منه آيات محكمات هن أم الكتاب .. }.','46','18','0');
INSERT INTO [t358] VALUES (57,'{هو الذي أنزل عليك الكتاب منه آيات محكمات .. } كادت تصير متشابهة للاختلاف فيها!','46','18','0');
INSERT INTO [t358] VALUES (58,'القرآن كله محكم وكله متشابه، ومنه محكم ومنه متشابه، ومعنى ذلك بالتفصيل.','47','18','0');
INSERT INTO [t358] VALUES (59,'معنى كون الآيات متشابهات، ومعاني المتشابه فيها.','47','18','0');
INSERT INTO [t358] VALUES (60,'متى يكون ابتغاء الآيات المتشابهة من الزيغ المذموم؟ ومعنى قوله: {والراسخون في العلم}؟','49','18','0');
INSERT INTO [t358] VALUES (61,'الرسوخ في العلم حال قلبية، وليس عن كثرة العلم، والناس متفاوتون فيه.','53','18','0');
INSERT INTO [t358] VALUES (62,'علامة الراسخ في العلم والزائغ فيه، وما دلت عليه الدلائل من علامتهما.','55','18','0');
INSERT INTO [t358] VALUES (63,'ذكر كلام الراغب الأصبهاني، ومناقشته في أضرب مشتبه المعنى واللفظ في القرآن.','57','18','0');
INSERT INTO [t358] VALUES (64,'من شبه أو أؤل صفات الله التي لا يعلم حقيقتها إلا هو فقد زعم أنه أدرك حقيقة معناها.','60','18','0');
INSERT INTO [t358] VALUES (65,'مثبت صفة الله على ظاهرها ومحيل علمه بكنهها إليه هو أحق بقوله: {آمنا به كل من عند ربنا}.','61','18','0');
INSERT INTO [t358] VALUES (66,'بيان الفائدة من إنزال المتشابه في القرآن مع أنه نزل هدى للعالمين وأمرنا بتدبره مطلقا.','61','18','0');
INSERT INTO [t358] VALUES (67,'من فوائد إنزال المتشابه في القرآن الإيمان به وإن لم يدرك كنهه، ومن فوائده الابتلاء به.','62','18','0');
INSERT INTO [t358] VALUES (68,'الفصل الثاني: تأويل النصوص الشرعية الواردة في الإخبار عن الوقائع.','64','16','0');
INSERT INTO [t358] VALUES (69,'الإخبار بما لا نحس به وبما ليس من جنسه حكمه كالعقائد في وجوب الإيمان وترك التأويل.','64','68','0');
INSERT INTO [t358] VALUES (70,'رد حجة المؤولين للأخبار عن بعض المحسوسات بحجة مخالفتها للعقل أوالحس أوالتواتر.','64','68','0');
INSERT INTO [t358] VALUES (71,'حل شبهة أن ترك بعض الأخبار على ظاهرها دون تأويل يسلط أعداء الإسلام عليه.','66','68','0');
INSERT INTO [t358] VALUES (72,'اليقين الحاصل بالبراهين على صحة خبر الله ورسوله - صلى الله عليه وسلم - يوجب رد الشبهات المخالفة له.','67','68','0');
INSERT INTO [t358] VALUES (73,'حل كل الشبهات حلا يقنع الخصم لا يمكن إلا بتحرى الخصم لطلب الحق وتركه التقليد.','67','68','0');
INSERT INTO [t358] VALUES (74,'رد المؤلف على من كذب أهل الفلك والطبيعة فيما ظنه مخالفا من قولهم لظاهر الوحي.','68','68','0');
INSERT INTO [t358] VALUES (75,'الشرع لبيان أحكام الدين، ولم يأت لتعليم العلوم الكونية، وما يقع منه يكون عرضا.','68','68','0');
INSERT INTO [t358] VALUES (76,'من العلوم الكونية ما لا فائدة في علمه، ومنها ما فيه فائدة، ولكنه لا يتوقف على الوحي.','69','68','0');
INSERT INTO [t358] VALUES (77,'العلوم الكونية متسعة جدا وقد قضى الله تعالى أن يكون ظهورها في أوقات متراخية.','69','68','0');
INSERT INTO [t358] VALUES (78,'من الأحكام الشرعية ما لا يدرك بالنظر، وما يدرك به فهو مظنة الاختلاف وجور الحكام.','69','68','0');
INSERT INTO [t358] VALUES (79,'ليس كل حاكم كاملا في العقل والفهم والنظر حتى يدرك بنظره جميع الأحكام بعدل.','69','68','0');
INSERT INTO [t358] VALUES (80,'اجتماع جماعة العقلاء لوضع القوانين لا يكفي؛ لقصر نظرهم، واحتمال ميلهم وتعصبهم.','69','68','0');
INSERT INTO [t358] VALUES (81,'غالب القوانين الوضعية تختل الحكمة المقصودة منها في كثير من الجزئيات الداخلة فيها.','69','68','0');
INSERT INTO [t358] VALUES (82,'القوانين الشرعية يؤمن فيها الغلط والميل والعصبية ويقبلها الناس طيبة بها نفوسهم.','70','68','0');
INSERT INTO [t358] VALUES (83,'أمثلة على حصول الظن والخطأ من النبي - صلى الله عليه وسلم - عند الكلام على بعض أمور الدنيا.','70','68','0');
INSERT INTO [t358] VALUES (84,'مناقشة المؤلف للطحاوي في حمل همه - صلى الله عليه وسلم - النهي عن الغيل على الظن من ثلاثة وجوه.','71','68','0');
INSERT INTO [t358] VALUES (85,'أمثلة على إتيان الشرع بما يشير لمسائل طبيعية بمعرض ديني إجمالا إن دعت ضرورة إليه.','73','68','0');
INSERT INTO [t358] VALUES (86,'ليس المقصود مما جاء في الشرع من علوم الطبيعة التعريف بكنهها وحقيقتها وكيفيتها، وإنما ورد تنبيها على الآيات والمثلات، وليس من مقتضى هذا جواز أن يكون الواقع ظاهرها.','74','68','0');
INSERT INTO [t358] VALUES (87,'المتكلم يعتني بالمعنى المقصود بالذات، وما ذكر عرضا لا يعتني به، فيوكل تحقيقه إلى موضعه.','74','68','0');
INSERT INTO [t358] VALUES (88,'المسألة إذا ذكرت في غير بابها استطرادا، ثم ذكرت في بابها مع مخالفة فالمعتمد فيها ما في بابها.','74','68','0');
INSERT INTO [t358] VALUES (89,'المتكلم في علم قد يذكر أثنائه قاعدة من علم آخر يكون ظاهر كلامه أنها كلية ولكن لا يعتد بها.','74','68','0');
INSERT INTO [t358] VALUES (90,'قاعدتا: قلب الياء ألفا لتحركها وانفتاح ما قبلها، وحذف أحد الساكنين إذا التقيا= ليستا مطلقتين.','75','68','0');
INSERT INTO [t358] VALUES (91,'الكتب الموضوعة يذكر فيها ما يكون أقرب لفهم المبتدئين، وإن لم يكن صحيحا في نفسه.','75','68','0');
INSERT INTO [t358] VALUES (92,'على المعلم تجنب ما يشغل أذهان الطلبة عما لا يفيدهم في علمهم، وكان النبي - صلى الله عليه وسلم - يفعل ذلك.','76','68','0');
INSERT INTO [t358] VALUES (93,'قد يخبر الوحي عن علوم الطبيعة بشيء يكون ظاهره مرادا مخالفا للحقيقة وقد لا يكون مرادا.','76','68','0');
INSERT INTO [t358] VALUES (94,'أجاز الجمهور تأخير البيان إلى وقت الحاجة، كورود نص عاما ثم تخصيصه عند العمل.','76','68','0');
INSERT INTO [t358] VALUES (95,'جرى من عادة خطاب الناس بينهم أن مجمل كلامهم أوعمومه يحصل بيانه وقت الحاجة إليه.','77','68','0');
INSERT INTO [t358] VALUES (96,'إذا ورد نص ظاهر على حكم كان ظاهرا لفظا لا معنى، فإن جاء وقت العمل به ولم يبين أنه خلاف الظاهر علم أنه مراد من جهة المعنى أيضا، ولا يعد تأخير بيانه كذبا.','78','68','0');
INSERT INTO [t358] VALUES (97,'أمثلة على أن معرفة الصفة الطبيعية للشيء والاطلاع عليه يحصل به مراد الشارع من كلامه.','78','68','0');
