INSERT INTO [t359] VALUES (1,'مقدمة المؤلف، بذكر سبب تأليف الرسالة، وما عابه على المؤلفات التي سبقته.','2','0','0');
INSERT INTO [t359] VALUES (2,'زعم صاحب البدعة أن بدعته من الدين يبطله أن الدين وضع إلهي، وضعه الله تعالى، وبلغه النبي - صلى الله عليه وسلم -.','3','0','0');
INSERT INTO [t359] VALUES (3,'إما أن يعترف المنافح عن بدعة بأنها ليست من الدين، أو يصر فيطلب منه إبراز دليله عليه.','3','0','0');
INSERT INTO [t359] VALUES (4,'ما يراه صاحب البدعة دليلا على مشروعية بدعته لا يخرج عن أحد أربعة أضرب، وهي','3','0','0');
INSERT INTO [t359] VALUES (5,'الضرب الأول: ما ليس شبهة دليل عند العلماء، كالاستحسان، والرؤيا، ونحوهما.','3','4','0');
INSERT INTO [t359] VALUES (6,'الضرب الثاني: ما فيه شبهة دليل عند العامي، وهو تقليده لأهل العلم أوالفضل.','4','4','0');
INSERT INTO [t359] VALUES (7,'الضرب الثالث: ما يجوز التمسك به، لكنه لم يثبت، أوعارضه قول المجتهد، وهو أولى منه.','4','4','0');
INSERT INTO [t359] VALUES (8,'الضرب الرابع: ما هو دليل، لكنه لم يثبت أوعارضه أولى منه، كتاب أوسنة أوإجماع أوقياس.','4','4','0');
INSERT INTO [t359] VALUES (9,'دفع الضرب الأول إجمالا: أن الاستدلال به ليس من الدين الذي بلغه محمد - صلى الله عليه وسلم -.','4','0','0');
INSERT INTO [t359] VALUES (10,'البرهان على مشروعية البدعة لا بد من أن يكون قطعيا؛ لأن الله قال: {إن الظن لا يغني من الحق شيئا}.','4','0','0');
INSERT INTO [t359] VALUES (11,'العمل في الفروع بخبر الواحد لا يفيد إلا الظن لكن وجوب العمل بخبر الواحد ثابت قطعا.','4','0','0');
INSERT INTO [t359] VALUES (12,'ما يستحسنه الإنسان لا يكون من الدين، وتفصيل القول في الاستحسان عند أبي حنيفة ومالك.','5','0','0');
INSERT INTO [t359] VALUES (13,'رد الاحتجاج بالرؤيا المنامية للتشريع بأنها قد تكون من الشيطان أولها تأويل، وأمثلة عليها.','6','0','0');
INSERT INTO [t359] VALUES (14,'يرد الاحتجاج للتشريع بالتجربة بأن صحة التجربة ابتلاء أواستدراج، وأمثلة عديدة على ذلك.','8','0','0');
INSERT INTO [t359] VALUES (15,'بعض الناس يعتمد في أمور دنياه على القرعة والفأل، وقد يغلو فيعتمد مثله للتشريع، وهو باطل.','13','0','0');
INSERT INTO [t359] VALUES (16,'الاعتماد في التشريع على الفأل من الاستقسام بالأزلام، والبديل الشرعي له صلاة الاستخارة.','15','0','0');
INSERT INTO [t359] VALUES (17,'من الاحتجاج الباطل بالتجربة العوذ المشتملة على تعظيم الجن ونحوهم، والذبح لهم ونحو ذلك.','16','0','0');
INSERT INTO [t359] VALUES (18,'نشوء رقاة لا دين لهم أويقين ولا نفع لرقيتهم بالآيات والدعاء = دفع الناس لاسترضاء الشياطين.','16','0','0');
INSERT INTO [t359] VALUES (19,'قصة مرض ولد المؤلف وزوجه، وموقفه الحازم من عمل شيء محظور شرعا طلبا للعلاج.','17','0','0');
INSERT INTO [t359] VALUES (20,'الانتفاع حاصل بدعاء الصالحين ورقيتهم، وبالأعمال المحظورة شرعا، وذكر الفرق بينهما في التأثير.','18','0','0');
INSERT INTO [t359] VALUES (21,'الصرع ليس من فعل الشيطان، بل يعرض لمن يعتريه ما يضعف عقله فتتضاعف عليه عوارضه.','18','0','0');
INSERT INTO [t359] VALUES (22,'دفع الاستدلال بالضرب الثاني: وهو تقليد أهل العلم أوالفضل، وفيه شبهة دليل عند العامي.','20','0','0');
INSERT INTO [t359] VALUES (23,'لا يجوز احتجاج العامي بعالم أوصالح إن كان مقلدا لا مجتهدا، بنص العلماء على حرمة الافتاء له.','20','0','0');
INSERT INTO [t359] VALUES (24,'عدم نقل استحباب أمر عن الأئمة وسكوتهم عنه كاف في الحجة؛ لأن ما لا يستحب لا يتناهى.','20','0','0');
INSERT INTO [t359] VALUES (25,'استحسان إمام أمرا قد يكون معذورا فيه، ولكن مقلده لا يعذر بعد بيان بدعيته، لأمور.','21','0','0');
INSERT INTO [t359] VALUES (26,'اشتهار عمل في جهة لا يصلح حجة على استحسان البدع بها، حتى عمل أهل المدينة ليس بمسلم.','23','0','0');
INSERT INTO [t359] VALUES (27,'المبتدعة أربعة أقسام','24','0','0');
INSERT INTO [t359] VALUES (28,'الأول: من يعلم بدعية فعله ويزعم أن الشارع يحبه، فقد جمع كذبا وتشريعا.','24','27','0');
INSERT INTO [t359] VALUES (29,'القسم الثاني: من يشك في بدعية فعله ولكنه يجزم أنها من دين الإسلام، فهو كالقسم الأول.','26','27','0');
INSERT INTO [t359] VALUES (30,'القسم الثالث: من يجزم أن بدعته من دين الإسلام، وليس عنده برهان عليه، وهذا ثلاثة أضرب','26','27','0');
INSERT INTO [t359] VALUES (31,'الأول: مجتهد بشبهة دليل، فهو معذور إن خفي عليه اختلال شرط أوقيام معارض دون علمه.','26','30','0');
INSERT INTO [t359] VALUES (32,'متى تبين للمجتهد ـ أو لمقلده ـ خطأ اجتهاده المعذور فيه ابتداء = فأصر على رأيه فهو هالك.','26','31','0');
INSERT INTO [t359] VALUES (33,'الثاني: من لم يبلغ درجة الاجتهاد وينظر في الأدلة ويحكم دون موافقة مجتهد، فهذا ضال مضل.','27','30','0');
INSERT INTO [t359] VALUES (34,'أكثر البدع من اختراع العباد، الذين لا يعتد بأقوالهم، إذ لم يكونوا في العلوم بدرجة الاجتهاد.','28','33','0');
INSERT INTO [t359] VALUES (35,'الثالث: من يقيس على نصوص المجتهدين ويستنبط منها، وهو الذي يسمى «مجتهد المذهب».','28','30','0');
INSERT INTO [t359] VALUES (36,'الاستنباط من نص المجتهد تحصيل لدلالات ظنية قد تضعف جدا، كعموم فاته فيه بعض أفراده.','29','35','0');
INSERT INTO [t359] VALUES (37,'عموم نصوص الله ورسوله - صلى الله عليه وسلم - يشمل الصور النادرة؛ لأنه مبني على علم معصوم بخلاف غيرهما.','29','35','0');
INSERT INTO [t359] VALUES (38,'مناقشة ومنع الاستنباط من نص المجتهد بدلالة ظنية، كالإشارة ومفهوم الموافقة والمخالفة.','29','35','0');
INSERT INTO [t359] VALUES (39,'مناقشة ورد استحسان البدع والمحدثات بناء على القياس على نصوص مجتهدي المذاهب.','31','35','0');
INSERT INTO [t359] VALUES (40,'قياس مجتهد المذهب على نص إمامه المستند في اجتهاده على قياس= باطل؛ لأنه قياس على قياس.','32','35','0');
INSERT INTO [t359] VALUES (41,'أكثر مسائل الفروع لا من نص إمام المذهب ولا مستنبطة من كلامه، بل كل متأخر يستنبط ممن قبله!','32','35','0');
INSERT INTO [t359] VALUES (42,'من أسباب استحسان البدع: ميل العالم لأهل البدع، أو لأهل الدنيا، أو منافسة علماء عصره.','33','35','0');
INSERT INTO [t359] VALUES (43,'الشريعة كجداول نبعت من جبل، فيها ما صفي وفيها ما كدر، فمن لم يقدر على المنبع فليحتط.','33','35','0');
INSERT INTO [t359] VALUES (44,'استنباط المتأهل للاستنباط من المذاهب جائز إن اضطر إليه ولم يقدر على تحصيل ما هو أوثق منه.','34','35','0');
INSERT INTO [t359] VALUES (45,'قد يكون المستحسن لبدعة خيرا في نفسه صالحا وليا لله، ولكن لا يلزم من ذلك عصمته عن الخطأ.','35','35','0');
INSERT INTO [t359] VALUES (46,'لا يلزم من كون المجتهد معذورا مأجورا في اجتهاده أن يكون كل من وافقه على ذلك كذلك.','35','35','0');
