INSERT INTO [t3610] VALUES (1,'أي الأعمال أحب إلى الله عز وجل؟ قال: فهبنا أن يقول منا أحد. قال: فأرسل إلينا رسول الله','2','0','0');
INSERT INTO [t3610] VALUES (2,'لو كنا نعلم أي الأعمال أفضل أو أحب إلى الله، فنزلت: {يا أيها الذين آمنوا هل أدلكم على','3','0','0');
INSERT INTO [t3610] VALUES (3,'نزل قوله: {لم تقولون ما لا تفعلون} [الصف: ٢] إلى قوله: {صفا كأنهم بنيان مرصوص} [الصف: ٤]','4','0','0');
INSERT INTO [t3610] VALUES (4,'إن الله اشترى من المؤمنين أنفسهم وأموالهم بأن لهم الجنة} [التوبة: ١١١] فقال: ثامنهم الله','5','0','0');
INSERT INTO [t3610] VALUES (5,'عمل صالح قبل الغزو، فإنكم إنما تقاتلون بأعمالكم','6','0','0');
INSERT INTO [t3610] VALUES (6,'القتل في سبيل الله يغسل الدرن، والقتل قتلان كفارة ودرجة','7','0','0');
INSERT INTO [t3610] VALUES (7,'القتلى ثلاثة رجال: رجل مؤمن جاهد بنفسه وماله في سبيل الله، حتى إذا لقي العدو قاتلهم حتى','8','0','0');
INSERT INTO [t3610] VALUES (8,'الناس في الغزو جزءان: فجزء خرجوا يكثرون ذكر الله والتذكير به، ويجتنبون الفساد في المسير،','9','0','0');
INSERT INTO [t3610] VALUES (9,'إذا التقى الزحفان نزلت الملائكة، فتكتب الناس على منازلهم، فلان يقاتل للدنيا، وفلان يقاتل','10','0','0');
INSERT INTO [t3610] VALUES (10,'من الناس ناسا يقاتلون ابتغاء الدنيا، وإن من الناس ناسا يقاتلون رياء وسمعة، وإن من الناس','11','0','0');
INSERT INTO [t3610] VALUES (11,'مثل المجاهد في سبيل الله - والله أعلم بمن يجاهد في سبيله - كمثل القائم الصائم الخاشع','12','0','0');
INSERT INTO [t3610] VALUES (12,'إني أقف المواقف أريد وجه الله، وأحب أن يرى موطني، فلم يرد عليه رسول الله صلى الله عليه','13','0','0');
INSERT INTO [t3610] VALUES (13,'مثل المجاهد في سبيل الله كالصائم القائم بآيات الله آناء الليل وآناء النهار، مثل هذه','14','0','0');
INSERT INTO [t3610] VALUES (14,'لو أنفقت ما في الأرض ما أدركت فضل غدوتهم','15','0','0');
INSERT INTO [t3610] VALUES (15,'لكل أمة رهبانية، ورهبانية هذه الأمة الجهاد في سبيل الله','16','0','0');
INSERT INTO [t3610] VALUES (16,'لكل أمة رهبانية، ورهبانية هذه الأمة الجهاد في سبيل الله','17','0','0');
INSERT INTO [t3610] VALUES (17,'أبدلنا الله بذلك الجهاد في سبيل الله، والتكبير على كل شرف','18','0','0');
INSERT INTO [t3610] VALUES (18,'روحة في سبيل الله أو غدوة خير من الدنيا وما فيها، أو ما عليها','19','0','0');
INSERT INTO [t3610] VALUES (19,'لا تجف الأرض من دمه حتى تبتدره زوجتاه كأنهما ظئران أضلتا فصيلهما في براح من الأرض بيداء،','20','0','0');
INSERT INTO [t3610] VALUES (20,'إذا التقى الصفان أهبط الله الحور العين إلى السماء الدنيا، فإذا رأين الرجل يرضين مقدمه،','21','0','0');
INSERT INTO [t3610] VALUES (21,'اذكروا نعمة الله عليكم، ما أحسن أثر نعمة الله عليكم فلو ترون ما أرى من بين أصفر وأحمر','22','0','0');
INSERT INTO [t3610] VALUES (22,'غدوة في سبيل الله أو روحة خير من الدنيا وما فيها، ولقاب قوس أو قيد أحدكم في الجنة خير من','23','0','0');
INSERT INTO [t3610] VALUES (23,'لو أن خيرة من خيرات حسان اطلعت من السماء لأضاءت لها الأرض، ولقهر ضوء وجهها الشمس والقمر،','24','0','0');
INSERT INTO [t3610] VALUES (24,'للشهيد غرفة كما بين صنعاء والجابية، أعلاها الدر والياقوت، وجوفها المسك والكافور قال:','25','0','0');
INSERT INTO [t3610] VALUES (25,'ما من نفس تموت لها عند الله خير يسرها أن ترجع إلى الدنيا، ولها الدنيا وما فيها إلا الشهيد','26','0','0');
INSERT INTO [t3610] VALUES (26,'لولا أن أشق على أمتي أو قال على الناس لأحببت أن لا أتخلف عن سرية تخرج في سبيل الله، ولكن','27','0','0');
INSERT INTO [t3610] VALUES (27,'ما من أحد يدخل الجنة يحب أن يرجع إلى الدنيا وأن له ما على الأرض من شيء إلا الشهيد، فإنه','28','0','0');
INSERT INTO [t3610] VALUES (28,'مثل المجاهد في سبيل الله مثل رجل يصوم النهار ويقوم الليل حتى يرجع متى ما','29','0','0');
INSERT INTO [t3610] VALUES (29,'لا يجتمع غبار في سبيل الله ودخان جهنم في منخري عبد مسلم أبدا','30','0','0');
INSERT INTO [t3610] VALUES (30,'ما شحب وجه، ولا اغبر قدم في عمل يبتغى به درجات الجنة بعد الصلاة المفروضة كجهاد في سبيل','31','0','0');
INSERT INTO [t3610] VALUES (31,'من اغبرت قدماه في سبيل الله حرمه الله على النار فأعجب مالكا قوله، وسار حتى إذا كان حيث','32','0','0');
INSERT INTO [t3610] VALUES (32,'من اغبرت قدماه في سبيل الله ساعة من نهار، فهما حرام على النار وأصلح دابتي لتغنيني عن','33','0','0');
INSERT INTO [t3610] VALUES (33,'ما من حال أحرى أن يستجاب للعبد فيه إلا أن يكون في سبيل الله من أن يكون عافرا وجهه','34','0','0');
INSERT INTO [t3610] VALUES (34,'إذا رجف قلب العبد في سبيل الله تحاتت خطاياه كما تتحات عذق النخلة وذكر من الصلاة مثل','35','0','0');
INSERT INTO [t3610] VALUES (35,'لروحة صعلوك من صعاليك المهاجرين يجر سوطه في سبيل الله أفضل من صدقة ابن','36','0','0');
INSERT INTO [t3610] VALUES (36,'مثل المجاهد في سبيل الله كمثل الصائم القانت، الذي لا يفتر عن صيام وقيام حتى','37','0','0');
INSERT INTO [t3610] VALUES (37,'لا يكلم أحد في سبيل الله - والله أعلم بمن يكلم في سبيله - إلا جاء كهيئته يوم القيامة،','38','0','0');
INSERT INTO [t3610] VALUES (38,'تكفل الله لمن خرج من بيته مجاهدا في سبيل الله لا ينهزه إلا الجهاد في سبيله، وتصديق كلمته،','39','0','0');
INSERT INTO [t3610] VALUES (39,'كل كلم يكلمه المسلم في سبيل الله يكون يوم القيامة كهيأتها، إذا طعنت تفجر دما، فاللون لون','40','0','0');
INSERT INTO [t3610] VALUES (40,'الجريء كل الجريء الذي إذا حضر العدو ولى فرارا , والجبان كل الجبان الذي إذا حضر العدو حمل','41','0','0');
INSERT INTO [t3610] VALUES (41,'يجيء الله تبارك وتعالى في ظلل من الغمام والملائكة , ثم ينادي مناد: سيعلم أهل الجمع لمن','42','0','0');
INSERT INTO [t3610] VALUES (42,'ينادي مناد: أين المفجعون في سبيل الله , فلا يقوم إلا المجاهدون','43','0','0');
INSERT INTO [t3610] VALUES (43,'إذا قاتل الشجاع والجبان , فأعظمهما أجرا الجبان , وإذا تصدق البخيل والسخي فأعظمهما أجرا','44','0','0');
INSERT INTO [t3610] VALUES (44,'فصعق من في السموات ومن في الأرض إلا من شاء الله قال هم الشهداء، هم ثنية الله حول العرش','45','0','0');
INSERT INTO [t3610] VALUES (45,'عرض علي أول ثلاثة يدخلون الجنة , وأول ثلاثة يدخلون النار , فأما أول ثلاثة يدخلون الجنة:','46','0','0');
INSERT INTO [t3610] VALUES (46,'ثلاثة يحبهم الله , وثلاثة يشنؤهم الله , فلقيته , فقلت: يا أبا ذر , ما حدثت؟ بلغني عنك','47','0','0');
INSERT INTO [t3610] VALUES (47,'أفضل الشهداء عند الله الذين يلقون في الصف فلا يلفتون وجوههم حتى يقتلوا , أولئك يتلبطون في','48','0','0');
INSERT INTO [t3610] VALUES (48,'ألا أخبركم بأفضل الشهداء عند الله منزلة يوم القيامة الذين يلقون العدو في الصف , فإذا','49','0','0');
INSERT INTO [t3610] VALUES (49,'ألا أنبئك يا هزاز بن مالك بأفضل الشهداء عند الله يوم القيامة؟ قال: بلى. قال: المحتسب','50','0','0');
INSERT INTO [t3610] VALUES (50,'من عقر جواده وأهريق دمه','51','0','0');
INSERT INTO [t3610] VALUES (51,'الشهداء أمناء الله , قتلوا أو ماتوا على فرشهم','52','0','0');
INSERT INTO [t3610] VALUES (52,'لقد طلبت القتل مظانه , فلم يقدر لي إلا أن أموت على فراشي , وما من عمل شيء أرجى عندي بعد','53','0','0');
INSERT INTO [t3610] VALUES (53,'خل عني يا خالد , فإنه قد كان لك مع رسول الله صلى الله عليه وسلم سابقة , وإني وأبي كنا من','54','0','0');
INSERT INTO [t3610] VALUES (54,'رأيت في المنام كأن أبا جهل أتاني فبايعني , فلما أسلم خالد بن الوليد، قيل صدق الله رؤياك','55','0','0');
INSERT INTO [t3610] VALUES (55,'كتاب ربي , وكلام ربي.','56','0','0');
INSERT INTO [t3610] VALUES (56,'نزلت هذه الآية {ليس لك من الأمر شيء} [آل عمران: ١٢٨] , فقال: كان رسول الله صلى الله عليه','57','0','0');
INSERT INTO [t3610] VALUES (57,'إذا رفع رأسه من الركوع في الركعة الآخرة من الفجر يقول: اللهم العن فلانا وفلانا. بعد ما','58','0','0');
INSERT INTO [t3610] VALUES (58,'ولا تحسبن الذين قتلوا في سبيل الله أمواتا بل أحياء عند ربهم يرزقون} [آل عمران: ١٦٩] . قال:','59','0','0');
INSERT INTO [t3610] VALUES (59,'الشهداء في قباب من رياض بفناء الجنة , يبعث لهم حوت وثور يعتركان فيلهون بهما , فإذا اشتهوا','60','0','0');
INSERT INTO [t3610] VALUES (60,'جنة المأوى فيها طير خضر ترتعي فيها أرواح الشهداء','61','0','0');
INSERT INTO [t3610] VALUES (61,'لما أصيب إخوانكم بأحد جعل الله أرواحهم في أجواف طير خضر , ترد أنهار الجنة , وتأكل من','62','0','0');
INSERT INTO [t3610] VALUES (62,'إذا استشهد الشهيد أخرج الله له جسدا كأحسن جسد , ثم أمر بروحه , فأدخل فيه , فينظر إلى جسده','63','0','0');
INSERT INTO [t3610] VALUES (63,'أنزل في الذين قتلوا ببئر معونة قرآن قرأناه حتى نسخ بعد: بلغوا قومنا أنا لقينا ربنا، فرضي','64','0','0');
INSERT INTO [t3610] VALUES (64,'هذا من الثمانين الذين صبروا معك يوم حنين، أرزاقهم وأرزاق أولادهم على الله في','65','0','0');
INSERT INTO [t3610] VALUES (65,'ما أبالي من أي حفرتيهما بعثت , إن الله تبارك وتعالى يقول {والذين هاجروا في سبيل الله ثم','66','0','0');
INSERT INTO [t3610] VALUES (66,'من وضع رجله في ركابه فاصلا في سبيل الله فلدغته هامة أو وقصته دابة أو مات بأي حتف مات فهو','67','0','0');
INSERT INTO [t3610] VALUES (67,'الشهداء سبع سوى القتل في سبيل الله: المبطون شهيد , والغريق شهيد , والمطعون شهيد , وصاحب','68','0','0');
INSERT INTO [t3610] VALUES (68,'شهداءكم إذا لقليل , إن من يتردى من الجبال , ويغرق في البحور , وتأكله السباع شهداء عند','69','0','0');
INSERT INTO [t3610] VALUES (69,'أيستطيع أحدكم أن يقوم فلا يفتر , ويصوم فلا يفطر , ما كان حيا؟ فقيل له: يا أبا هريرة , ومن','70','0','0');
INSERT INTO [t3610] VALUES (70,'لقد شغلتكم عن الجهاد حتى حقت علي وعليكم , فمن أحب أن يلحق بالشام , فليفعل , ومن أحب أن','71','0','0');
INSERT INTO [t3610] VALUES (71,'يوم في سبيل الله خير من ألف يوم فيما سواه , فلينظر كل امرئ منكم لنفسه','72','0','0');
INSERT INTO [t3610] VALUES (72,'كتب عليكم القتال وهو كره لكم} [البقرة: ٢١٦] . قال: فنزلت آية القتال فكرهوها , فلما بين','73','0','0');
INSERT INTO [t3610] VALUES (73,'ما لكم لا تقاتلون في سبيل الله} [النساء: ٧٥] . قال وفي المستضعفين','74','0','0');
INSERT INTO [t3610] VALUES (74,'ولما رأى المؤمنون الأحزاب قالوا هذا ما وعدنا الله ورسوله وصدق الله ورسوله} [الأحزاب: ٢٢] .','75','0','0');
INSERT INTO [t3610] VALUES (75,'لم يشهد بدرا مع رسول الله صلى الله عليه وسلم , فكبر عليه , فقال: أول مشهد شهده رسول الله','76','0','0');
INSERT INTO [t3610] VALUES (76,'قرأ رسول الله صلى الله عليه وسلم يوم بدر: {وسارعوا إلى مغفرة من ربكم وجنة عرضها السموات','77','0','0');
INSERT INTO [t3610] VALUES (77,'أرأيت إن قتلت اليوم أطأ بعرجتي هذه الجنة؟ قال: نعم قال: فوالذي بعثك بالحق لأطأن بها الجنة','78','0','0');
INSERT INTO [t3610] VALUES (78,'لو كان غيرها آثرتك به , فخرج سعد مع النبي صلى الله عليه وسلم , فقتل يوم بدر , ثم قتل','79','0','0');
INSERT INTO [t3610] VALUES (79,'لما طعن حرام بن ملحان , وكان خاله يوم بئر معونة. قال بالدم هكذا , فنضحه على وجهه ورأسه ,','80','0','0');
INSERT INTO [t3610] VALUES (80,'زعم عروة بن الزبير أن عامر بن فهيرة، قتل يومئذ , فلم يوجد جسده حين دفنوه يرون أن الملائكة','81','0','0');
INSERT INTO [t3610] VALUES (81,'دعا رسول الله صلى الله عليه وسلم على الذين قتلوا أصحاب بئر معونة ثلاثين غداة يدعو على','82','0','0');
INSERT INTO [t3610] VALUES (82,'إنها جنات كثيرة وإن حارثة في الفردوس الأعلى','83','0','0');
INSERT INTO [t3610] VALUES (83,'يرمي بين يدي رسول الله صلى الله عليه وسلم , فكان النبي صلى الله عليه وسلم يرفع رأسه من','84','0','0');
INSERT INTO [t3610] VALUES (84,'اللهم إني أقسم عليك أن نلقى العدو , فإذا لقينا العدو أن يقتلوني , ثم يبقروا بطني , ثم','85','0','0');
INSERT INTO [t3610] VALUES (85,'منعتموني الجنة ببدر , والله , لئن بقيت.. . فبلغ ذلك عمر , فلقيه , فقال: أنت القائل كذا','86','0','0');
INSERT INTO [t3610] VALUES (86,'رأيت أباه يستن يوم أحد بسيفه كما يستن الجمل','87','0','0');
INSERT INTO [t3610] VALUES (87,'من رجل يبيع لنا نفسه , فوثب فتية من الأنصار خمسة فيهم: زياد بن السكن , فقتلوا حتى كان','88','0','0');
INSERT INTO [t3610] VALUES (88,'أصيب مع رسول الله صلى الله عليه وسلم يوم أحد نحو من ثلاثين، كلهم يجيء حتى يجثو بين يديه ,','89','0','0');
INSERT INTO [t3610] VALUES (89,'إن كان محمد قد قتل , فقد بلغ , فقاتلوا عن دينكم','90','0','0');
INSERT INTO [t3610] VALUES (90,'كنت في أول من فاء يوم أحد , فرأيت رجلا مع رسول الله صلى الله عليه وسلم يقاتل دونه أراه','91','0','0');
INSERT INTO [t3610] VALUES (91,'طلحة رجع بسبع وثلاثين أو خمس وسبعين بين ضربة وطعنة ورمية , ربع فيها جبينه , وقطع فيها عرق','92','0','0');
INSERT INTO [t3610] VALUES (92,'أوجب طلحة','93','0','0');
INSERT INTO [t3610] VALUES (93,'من ينظر لي ما فعل سعد بن الربيع؟ فقال رجل من الأنصار: أنا يا رسول الله. قال: فخرج يطوف في','94','0','0');
INSERT INTO [t3610] VALUES (94,'ائتوهم وزوروهم وسلموا عليهم، فوالذي نفسي بيده، لا يسلم عليهم أحد إلى يوم القيامة إلا ردوا','95','0','0');
INSERT INTO [t3610] VALUES (95,'قتل مصعب بن عمير، وهو خير مني، فكفن في بردة، إن غطي رأسه بدت رجلاه، وإن غطي رجلاه بدا','96','0','0');
INSERT INTO [t3610] VALUES (96,'لا تختلفوا، فتشقوا علينا. ثم قال: رحمك الله أبا العبيدين، إنما أصحاب محمد صلى الله عليه و','97','0','0');
INSERT INTO [t3610] VALUES (97,'من كان له قتيل فليأت قتيله يعني قتلى أحد قال: فأخرجناهم رطابا يتثنون، قال فأصابت المسحاة','98','0','0');
INSERT INTO [t3610] VALUES (98,'لما استشهد الشهداء بأحد، ونزلوا منازلهم، رأوا منازل أناس من أصحابهم لم يستشهدوا، وهم','99','0','0');
INSERT INTO [t3610] VALUES (99,'دعي القوم ودعيتم، فأسرعوا وأبطأتم، أما والله لما سبقوكم به من الفضل فيما لا ترون أشد','100','0','0');
INSERT INTO [t3610] VALUES (100,'ما خرجت رغبة بنفسي عن أنفسكم، ولا اختيار بلد عن بلدكم، ولكن كان هذا الأمر فخرجت فيه رجال','101','0','0');
INSERT INTO [t3610] VALUES (101,'إن كنت إنما أعتقتني لله، فدعني أذهب إلى الله، وإن كنت أعتقتني لنفسك فاحبسني عندك. فأذن','102','0','0');
INSERT INTO [t3610] VALUES (102,'أبت البحوث يعني سورة التوبة قال الله تبارك وتعالى: {انفروا خفافا وثقالا} [التوبة: ٤١] .','103','0','0');
INSERT INTO [t3610] VALUES (103,'انفروا خفافا وثقالا} [التوبة: ٤١] فقال: أمرنا الله تبارك وتعالى واستنفرنا شيوخا وشبابا،','104','0','0');
INSERT INTO [t3610] VALUES (104,'الله بعثني بالسيف بين يدي الساعة، وجعل رزقي تحت ظل رمحي، وجعل الذل والصغار على من خالفني،','105','0','0');
INSERT INTO [t3610] VALUES (105,'ما أدري من أي يومين أفر، يوم أراد الله أن يهدي لي فيه شهادة، أو من يوم أراد أن يهدي لي','106','0','0');
INSERT INTO [t3610] VALUES (106,'ما من ليلة يهدى إلي فيها عروس أنا لها محب، أو أبشر فيها بغلام أحب إلي من ليلة شديدة البرد','107','0','0');
INSERT INTO [t3610] VALUES (107,'ما أحب أن امرأتي أصبحت نفسا بغلام، ولا أن فرسي أصبحت بعطفة على مهرة، ولوددت أنه لا يأتي','108','0','0');
INSERT INTO [t3610] VALUES (108,'نعم الفتى سمرة، لو أخذ من لأمته وشمر من مئزره. ففعل ذلك، أخذ من لأمته وشمر','109','0','0');
INSERT INTO [t3610] VALUES (109,'رأى ابن أم مكتوم يوما من أيام الكوفة، عليه درع سابغة يجرها في الصف','110','0','0');
INSERT INTO [t3610] VALUES (110,'شر ما في الرجل شح هالع، وجبن خالع','111','0','0');
INSERT INTO [t3610] VALUES (111,'كم من مشهد شهدته، وكم من مجمع حضرته، ولم أرزق الشهادة، لا نامت عيون','112','0','0');
INSERT INTO [t3610] VALUES (112,'إن كنتم تعلمون إنما النصر من عند العزيز الحكيم، فقاتلوا القوم، وإن كنتم تنتظرون نصرا من','113','0','0');
INSERT INTO [t3610] VALUES (113,'مر عمرو بن العاص، فطاف بالبيت، فرأى حلقة من قريش جلوسا، فلما رأوه قالوا: أهشام كان أفضل','114','0','0');
INSERT INTO [t3610] VALUES (114,'أسلمنا فأحببنا رسول الله صلى الله عليه وسلم , وناصحناه , فذكر يوم اليرموك , فقال: أخذ','115','0','0');
INSERT INTO [t3610] VALUES (115,'انطلقت يوم اليرموك أطلب ابن عمي , ومعي شنة من ماء وإناء، فقلت: إن كان به رماق سقيته من','116','0','0');
INSERT INTO [t3610] VALUES (116,'ترافقت أنا وعبد الله بن مخرمة، وسالم مولى أبي حذيفة عام اليمامة، فكان الرعي على كل امرئ','117','0','0');
INSERT INTO [t3610] VALUES (117,'بئس حامل القرآن أنا إذا. فقطعت يمينه، فأخذ اللوى بيساره، فقطعت يساره، فاعتنق اللوى وهو','118','0','0');
INSERT INTO [t3610] VALUES (118,'وكأين من نبي قاتل معه ربيون كثير} [آل عمران: ١٤٦] قال جعفر: علماء صبر. وقال المبارك:','119','0','0');
INSERT INTO [t3610] VALUES (119,'الحمد لله الذي جعل في أمتي مثلك','120','0','0');
INSERT INTO [t3610] VALUES (120,'أف لهؤلاء وما يصنعون. وقال للعدو: أف لهؤلاء وما يعبدون. خلوا عن سبيله يعني فرسه حتى أصلي','121','0','0');
INSERT INTO [t3610] VALUES (121,'إنك لست من أهل النار، ولكنك من أهل الجنة','122','0','0');
INSERT INTO [t3610] VALUES (122,'ألا ترضى أن تعيش حميدا، وتقتل شهيدا، ويدخلك الله الجنة؟ قال: بلى يا رسول الله. قال فعاش','123','0','0');
INSERT INTO [t3610] VALUES (123,'ينتهي الإثم إلى أن يشرك العبد بالله عز وجل، وينكح أمه، وينتهي البر إلى أن يهراق دم العبد','124','0','0');
INSERT INTO [t3610] VALUES (124,'ما كنت مفشيا من حديثك، فأفشه إلى هذا. فقام إلى كسوة في البيت، فأخرج كراسة فيها ثلاثة','125','0','0');
INSERT INTO [t3610] VALUES (125,'الشهداء أربعة: مؤمن جيد الإيمان لقي العدو، وصدق الله عز وجل حتى قتل، فذلك الذي يرفع إليه','126','0','0');
INSERT INTO [t3610] VALUES (126,'والسابقون السابقون} [الواقعة: ١٠] قال: أولهم رواحا إلى المسجد , وأولهم خروجا في سبيل الله','127','0','0');
INSERT INTO [t3610] VALUES (127,'أفلا أخبركم عن خلال كان عليها إخوانكم؟ أولها: لقاء الله عز وجل كان أحب إليهم من الشهد،','128','0','0');
INSERT INTO [t3610] VALUES (128,'الشهيد؟ قال: الذي يحتسب نفسه','129','0','0');
INSERT INTO [t3610] VALUES (129,'إنا لمتوجهون إلى مهران، ومعنا رجل من الأزد يقال له: أبو أثابة، فجعل يبكي، فقلنا: أجزع','130','0','0');
INSERT INTO [t3610] VALUES (130,'مر عليه يوم القادسية، وقد انتثر قصبه، فقال لبعض من مر عليه: ضم التي منه؛ لعلي أدنو في','131','0','0');
INSERT INTO [t3610] VALUES (131,'اللهم إن حدبة سوداء بذيئة يعني امرأته فزوجني اليوم مكانها من الحور العين. فمروا عليه وهو','132','0','0');
INSERT INTO [t3610] VALUES (132,'مر يوم الجسر يوم أبي عبيد برجل قد قطعت يداه ورجلاه، وهو يقول: {مع الذين أنعم الله عليهم','133','0','0');
INSERT INTO [t3610] VALUES (133,'عليك السلام يا مدينا، شأنك تأوينا','134','0','0');
INSERT INTO [t3610] VALUES (134,'اللهم أرمل المرأة، وأيتم الولد، وأكرم نوفا بالشهادة. قال: فغزوا، فلما انصرفوا فكانوا','135','0','0');
INSERT INTO [t3610] VALUES (135,'ما من خطوة يخطوها، يتقدمها إلى عدو لي إلا هي أحب إلي من أربعة آلاف','136','0','0');
INSERT INTO [t3610] VALUES (136,'ما من شيء عليها أحسن في نفسي من دم','137','0','0');
INSERT INTO [t3610] VALUES (137,'سألت الله عز وجل ثلاثا، فأعطاني اثنتين، وأنا أنتظر الثالثة. سألته أن يزهدني في الدنيا،','138','0','0');
INSERT INTO [t3610] VALUES (138,'ما أحسن هذا المرج، وما أحسن هذا الآن لو أن مناديا نادى: يا خيل الله، اركبي، فخرج رجل فكان','139','0','0');
INSERT INTO [t3610] VALUES (139,'ما أحسن حمرة الدم على البياض، فسمع أبوه ذلك، فقال: أقسمت عليك لتنزلن. قال: فنزل، ثم اعتزل','140','0','0');
INSERT INTO [t3610] VALUES (140,'اللهم إن حممة يزعم أنه يحب لقاءك، فإن كان حممة صادقا، فاعزم له عليه بصدقه، وإن كان كاذبا','141','0','0');
INSERT INTO [t3610] VALUES (141,'لقد رأيتني خرجت في غزاة لنا، فدعي الناس إلى مصافهم في يوم شديد الريح، والناس يثوبون إلى','142','0','0');
INSERT INTO [t3610] VALUES (142,'خذ هذه السفرة فاملأها من هذا العنب، ثم أدركنا به في المنزل. قال: فلما دخل الكرم، نظر إلى','143','0','0');
INSERT INTO [t3610] VALUES (143,'إن شبهتموني فشبهوني برجل صالح. قالوا: فإنه كان عندنا رجل في ركائب يعلفها، فاستنفر الناس','144','0','0');
INSERT INTO [t3610] VALUES (144,'أفضي بي إلى غرفة من ياقوتة أو زبرجدة، وأفضي بي إلى فرش موضونة بعضها إلى بعض، فبين يدي ذلك','145','0','0');
INSERT INTO [t3610] VALUES (145,'أعطني مخلاتي حتى آتيكم من هذا العنب، فأخذها، ثم دفع فرسه، فبينما هو في الكرم، فإذا هو','146','0','0');
INSERT INTO [t3610] VALUES (146,'كنا مع أبي محذورة قعودا، إذ جاءنا بذلك العنب، فوضعه، فدعا بقرطاس ودواة، فكتب وصيته، فلما','147','0','0');
INSERT INTO [t3610] VALUES (147,'أتحب أن ترى الحور العين؟ قال: نعم. قال: فادخل الصخرة، ثم اخرج إلى الصفة، فخرج عليهم، فإذا','148','0','0');
INSERT INTO [t3610] VALUES (148,'فتى غزا زمانا، وتعرض للشهادة، فلم يصبها، فحدث نفسه، فقال: والله ما أراني إلا لو قفلت إلى','149','0','0');
INSERT INTO [t3610] VALUES (149,'عمل قليلا، وأجر كثيرا، صلوا على أخيكم. فصلينا عليه، ثم دفناه في موقفنا، وسرنا قال عبد','150','0','0');
INSERT INTO [t3610] VALUES (150,'من أحب أن ينظر إلى رجل يطأ خضرة الجنة بقدميه غدا، فلينظر إلى هذا. فانطلق ذكوان إلى أهله','151','0','0');
INSERT INTO [t3610] VALUES (151,'رأيتني في المنام كأني في رهط وخلفنا رجل مع السيف شاهره، فجعل لا يأتي على أحد منا إلا ضرب','152','0','0');
INSERT INTO [t3610] VALUES (152,'رأيت كأنك أتيت على شجرة ظليلة فأصبت تحتها ثلاث شهادات، فأعطيتني واحدة، وأمسكت اثنتين،','153','0','0');
INSERT INTO [t3610] VALUES (153,'خيرا رأيت، تستشهد، وأستشهد أنا وابني قال: فلما كان يوم يزيد بن زياد، لقيهم الترك بسجستان،','154','0','0');
INSERT INTO [t3610] VALUES (154,'اللهم إن هذه نفسي تزعم في الرخاء أنها تحب لقاءك، فإن كانت صادقة، فارزقها ذاك، وإن كانت','156','0','0');
INSERT INTO [t3610] VALUES (155,'اللهم أحيني ما كانت الحياة خيرا لي، وإذا كانت خيرا لي فتوفني وفاة طاهرة طيبة يغبطني بها','157','0','0');
INSERT INTO [t3610] VALUES (156,'رأيت كأني أرى أبا رفاعة على ناقة سريعة، وأنا على جمل قطوف، فيردها علي حتى حين أقول الآن','158','0','0');
INSERT INTO [t3610] VALUES (157,'انتهيت إلى رسول الله صلى الله عليه وسلم وهو يخطب، فقلت: يا رسول الله، رجل غريب يسأل عن','159','0','0');
INSERT INTO [t3610] VALUES (158,'مالي ولكم، تطئون عقبي في كل سكة، وأنا إنسان ضعيف، تكون لي الحاجة فلا أقدر عليها معكم، فلا','160','0','0');
INSERT INTO [t3610] VALUES (159,'وانتضى صاحب القطيفة سيفه، وكسر جفنه، فألقاه، ثم جعل يقول: تمنوا، تمنوا، لتمت وجوه، ثم لا','161','0','0');
INSERT INTO [t3610] VALUES (160,'توجه بالناس يوم اليمامة، فأتوا على نهر، فجعلوا أسافل أمتعتهم في حجزهم، فعبروا النهر،','162','0','0');
INSERT INTO [t3610] VALUES (161,'قبح الله ما بقي منك، فطرحه، ثم جاء إلى سيفه فأخذه','163','0','0');
INSERT INTO [t3610] VALUES (162,'خير الناس رجل بلغه الإسلام وهو في داره وأهله وماله، فعمد إلى صرمة من إبله، فحدرها إلى دار','164','0','0');
INSERT INTO [t3610] VALUES (163,'أي الناس خير منزلة عند الله عز وجل بعد أنبيائه وأصفيائه؟ قال: المجاهد في سبيل الله عز وجل','165','0','0');
INSERT INTO [t3610] VALUES (164,'أي الناس خير منزلة عند الله عز وجل؟ قال: رجل على متن فرسه يخيف العدو، ويخيفونه. ثم أشار','166','0','0');
INSERT INTO [t3610] VALUES (165,'ألا أنبئكم بخير الناس وشر الناس؟ إن خير الناس رجل عمل في سبيل الله عز وجل على ظهر فرسه،','167','0','0');
INSERT INTO [t3610] VALUES (166,'ألا أخبركم بخير الناس منزلا؟ قال: قلنا بلى يا رسول الله. قال: رجل آخذ برأس فرسه في سبيل','168','0','0');
INSERT INTO [t3610] VALUES (167,'يا أيها الذين آمنوا اصبروا وصابروا ورابطوا} [آل عمران: ٢٠٠] قال: أمرهم أن يصبروا على','169','0','0');
INSERT INTO [t3610] VALUES (168,'صابروا المشركين، ورابطوا في سبيل الله','170','0','0');
INSERT INTO [t3610] VALUES (169,'رباط يوم وليلة - أو يوم أو ليلة - كصيام شهر وقيامه، ومن مات مرابطا، أجري عليه مثل ذلك من','171','0','0');
INSERT INTO [t3610] VALUES (170,'من مات على مرتبة من هذه المراتب، بعثه الله عز وجل عليها يوم القيامة. قال حيوة: رباط وحج','172','0','0');
INSERT INTO [t3610] VALUES (171,'كل ميت يختم على عمله الذي مات عليه، إلا المرابط في سبيل الله عز وجل فإنه ينمو له عمله إلى','173','0','0');
INSERT INTO [t3610] VALUES (172,'المجاهد من جاهد نفسه بنفسه','174','0','0');
INSERT INTO [t3610] VALUES (173,'أيما عبد مؤمن مات وهو على مرتبة من هذه الأعمال، بعثه الله عز وجل عليها يوم القيامة فأحببت','175','0','0');
INSERT INTO [t3610] VALUES (174,'إنكم ستجندون أجنادا، وتكون لكم ذمة، وخراج، وسيكون لكم على سيف البحر مدائن وقصور، فمن أدرك','176','0','0');
INSERT INTO [t3610] VALUES (175,'من نزل منزلا يخيف فيه المشركين، ويخيفونه، حتى يدركه الموت، كتب له كأجر ساجد لا يرفع رأسه','177','0','0');
INSERT INTO [t3610] VALUES (176,'فيمن يموت مرابطا: أنه يأمن من الفزع الأكبر يوم القيامة','180','0','0');
INSERT INTO [t3610] VALUES (177,'يبعث الله عز وجل يوم القيامة أقواما يمرون على الصراط كهيئة الريح، ليس عليهم حساب ولا','181','0','0');
INSERT INTO [t3610] VALUES (178,'رباط يوم في سبيل الله عز وجل خير من صيام شهر وقيامه، ومن مات مرابطا في سبيل الله عز وجل','182','0','0');
INSERT INTO [t3610] VALUES (179,'يوشك أن يأتي على الناس زمان، خير الناس فيه منزلا رجل أخذ بعنان فرسه في سبيل الله، كلما','183','0','0');
INSERT INTO [t3610] VALUES (180,'من لم يكرم ضيفه، فليس من محمد ولا إبراهيم، طوبى لعبد أمسى متعلقا برأس فرسه في سبيل الله','184','0','0');
INSERT INTO [t3610] VALUES (181,'سيكون في أمتي قوم يسد بهم الثغور، تؤخذ منهم الحقوق، ولا يعطون حقوقهم، أولئك مني وأنا','185','0','0');
INSERT INTO [t3610] VALUES (182,'من حرس ليلة في سبيل الله عز وجل كان له من كل إنسان ودابة قيراط قيراط','186','0','0');
INSERT INTO [t3610] VALUES (183,'لأن أبيت حارسا وخائفا في سبيل الله عز وجل أحب إلي من أن أتصدق بمائة','187','0','0');
INSERT INTO [t3610] VALUES (184,'ثلاثة أعين لا تحرقهم النار أبدا: عين بكت من خشية الله، وعين سهرت بكتاب الله، وعين حرست في','188','0','0');
INSERT INTO [t3610] VALUES (185,'خرجنا مع رسول الله صلى الله عليه وسلم في غزوة ذات الرقاع، فأصاب رجل من المسلمين امرأة رجل','189','0','0');
INSERT INTO [t3610] VALUES (186,'إنكم ستجندون أجنادا: جندا بالشام، وجندا بالعراق، وجندا باليمن. فقال ابن الخولاني: أخبرني','190','0','0');
INSERT INTO [t3610] VALUES (187,'لا تسبوا أهل الشام جما غفيرا، فإن فيهم قوما كارهون لما ترون، وإن فيهم','191','0','0');
INSERT INTO [t3610] VALUES (188,'ليأتين على الناس زمان لا يبقى مؤمن إلا لحق بالشام','192','0','0');
INSERT INTO [t3610] VALUES (189,'النفقة في أرض الهجرة مضاعفة بسبع مائة ضعف، وأنتم المهاجرون أهل الشام، لو أن رجلا اشترى','193','0','0');
INSERT INTO [t3610] VALUES (190,'لا يزال في أمتي سبعة لا يدعون الله عز وجل بشيء إلا استجيب لهم، بهم تنصرون، وبهم تمطرون.','194','0','0');
INSERT INTO [t3610] VALUES (191,'من لم يدرك الغزو معي فليغز في البحر، فإن قتال يوم في البحر خير من قتال يومين في البر، وإن','195','0','0');
INSERT INTO [t3610] VALUES (192,'من لم يدرك الغزو معي فعليه بغزو البحر','196','0','0');
INSERT INTO [t3610] VALUES (193,'خمس من قبض في شيء منهن فهو شهيد: القتيل في سبيل الله شهيد، والغريق في سبيل الله عز وجل','197','0','0');
INSERT INTO [t3610] VALUES (194,'غزوت البحر زمان معاوية، ومعنا أبو أيوب الأنصاري عام المد','198','0','0');
INSERT INTO [t3610] VALUES (195,'عجبت من أناس من أمتي عرضوا علي آنفا على سرر أمثال الملوك، يركبون هذا البحر الأخضر في سبيل','200','0','0');
INSERT INTO [t3610] VALUES (196,'غزوة في البحر أحب إلي من قنطار متقبلا','201','0','0');
INSERT INTO [t3610] VALUES (197,'ما كنت لأحمل أحدا من المسلمين على هذا ما بقيت','202','0','0');
INSERT INTO [t3610] VALUES (198,'عليك بالبر، لا تؤذي، ولا تؤذى. قال: إني أردت البحر. قال عبد الله: إن حفظت ستا استوجبت','203','0','0');
INSERT INTO [t3610] VALUES (199,'لأن أغزو على ناقة ذلول صموت أحب إلي من ركوب البحر','204','0','0');
INSERT INTO [t3610] VALUES (200,'كان يصلي على الرجل الذي يراه يخدم أصحابه','205','0','0');
INSERT INTO [t3610] VALUES (201,'سيد القوم خادمهم في السفر','206','0','0');
INSERT INTO [t3610] VALUES (202,'صحبت ابن عمر لأخدمه فكان يخدمني','207','0','0');
INSERT INTO [t3610] VALUES (203,'تعلموا المهن، فإن احتاج الرجل إلى مهنته، انتفع بها قال: وحدثنا أشياخنا أن معاوية بن أبي','208','0','0');
INSERT INTO [t3610] VALUES (204,'يشترط على أصحابه أن يكون خادمهم قال: فخرج في الرعي في يوم حار، فأتاه بعض أصحابه، فإذا هو','209','0','0');
INSERT INTO [t3610] VALUES (205,'من خدم أصحابه في سبيل الله عز وجل فضل على كل إنسان منهم بقيراط من','210','0','0');
INSERT INTO [t3610] VALUES (206,'أريد أن أصحبكم على أن تعطوني من أنفسكم ثلاث خصال. فيقولون: ما هي؟ قال: أكون لكم خادما، لا','211','0','0');
INSERT INTO [t3610] VALUES (207,'يشترط على الرجل إذا سافر معه على أن لا يسافر معه بجلاله، ولا ينازعه في الأذان، ولا','212','0','0');
INSERT INTO [t3610] VALUES (208,'كلكم خير منه','213','0','0');
INSERT INTO [t3610] VALUES (209,'من استطاع منكم أن يموت حاجا، أو معتمرا، أو غازيا، أو في نقل الغزاة فليفعل، ولا يموتن','214','0','0');
INSERT INTO [t3610] VALUES (210,'بلغ عمر بن الخطاب رضي الله عنه أن أبا عبيدة حصر بالشام، وتألب عليه العدو، فكتب إليه عمر:','219','0','0');
INSERT INTO [t3610] VALUES (211,'رأيتني يوم مؤتة اندق بيدي تسعة أسياف، فصبرت في يدي صفيحة يمانية','220','0','0');
INSERT INTO [t3610] VALUES (212,'من رمى بسهم فبلغه، فله درجة في الجنة. قال رجل: يا نبي الله، إن رميت فبلغت، فلي درجة؟ قال:','221','0','0');
INSERT INTO [t3610] VALUES (213,'من شاب شيبة في سبيل الله عز وجل كانت له نورا يوم القيامة','222','0','0');
INSERT INTO [t3610] VALUES (214,'أيما رجل مسلم أعتق رجلا مسلما، فإن الله عز وجل جاعل وقاء كل عظم من عظامه عظما من عظام','223','0','0');
INSERT INTO [t3610] VALUES (215,'لولا ثلاث: لولا أن أسير في سبيل الله عز وجل، أو يغبر جبيني في السجود، أو أقاعد قوما','224','0','0');
INSERT INTO [t3610] VALUES (216,'أغمي على رجل من الصدر الأول، فبكى، فاشتد بكاؤه، فقالوا له: إن الله عز وجل رحيم، إنه غفور،','225','0','0');
INSERT INTO [t3610] VALUES (217,'غدوة في سبيل الله عز وجل أو روحة خير مما طلعت عليه الشمس وغربت','226','0','0');
INSERT INTO [t3610] VALUES (218,'لسفرة في سبيل الله عز وجل أفضل من خمسين حجة','227','0','0');
INSERT INTO [t3610] VALUES (219,'لأن أمتع بسوط في سبيل الله عز وجل أحب إلي من حجة في إثر حجة','228','0','0');
INSERT INTO [t3610] VALUES (220,'رجل يريد الجهاد في سبيل الله عز وجل، وهو يبتغي عرضا من الدنيا. فقال رسول الله صلى الله','229','0','0');
INSERT INTO [t3610] VALUES (221,'أبواب الجنة تحت ظلال السيوف فقام رجل رث الهيئة فقال: يا أبا موسى، أنت سمعت رسول الله صلى','233','0','0');
INSERT INTO [t3610] VALUES (222,'أبواب الجنة تحت ظلال السيوف فقام شاب قد،. ..، فقال: كيف قلت يا أبا موسى؟ فأعاد عليه','234','0','0');
INSERT INTO [t3610] VALUES (223,'ومن يولهم يومئذ دبره} [الأنفال: ١٦] قال: ذلك يوم بدر','235','0','0');
INSERT INTO [t3610] VALUES (224,'ومن يولهم يومئذ دبره} [الأنفال: ١٦] قال: ذلك يوم بدر، فأما اليوم فينحاز إلى فئة، أو','236','0','0');
INSERT INTO [t3610] VALUES (225,'لو فاءوا إلي لكنت لهم فئة','239','0','0');
INSERT INTO [t3610] VALUES (226,'إن يكن منكم عشرون صابرون} [الأنفال: ٦٥] إلى آخر الآيتين قال: إن فر رجل من ثلاثة، لم يفر،','240','0','0');
INSERT INTO [t3610] VALUES (227,'ومن يولهم يومئذ دبره} [الأنفال: ١٦] قال: هذه منسوخة بالآية التي في الأنفال: {الآن خفف','241','0','0');
INSERT INTO [t3610] VALUES (228,'نزلت: {إن يكن منكم عشرون صابرون يغلبوا مائتين} فشق ذلك على المسلمين حين فرض عليهم أن لا','242','0','0');
INSERT INTO [t3610] VALUES (229,'رجلا كان في شرب أصاب حدا، فلم يقم عليه بينهم ذلك الحد، ثم بدا له ليقيمه عليه، فامتنع','243','0','0');
INSERT INTO [t3610] VALUES (230,'صلاة الخوف. قال: يقوم الإمام معه طائفة من الناس، وتكون طائفة بينهم وبين العدو، فيسجد سجدة','245','0','0');
INSERT INTO [t3610] VALUES (231,'صلى النبي صلى الله عليه وسلم بإحدى الطائفتين ركعة، والأخرى مقبلة على العدو، ثم انصرفت هذه','246','0','0');
INSERT INTO [t3610] VALUES (232,'صف أصحابه صفين، وما بهم يومئذ كبير خوف، ولكنه أحب أن يعلمهم دينهم، فصلى بطائفة ركعة،','247','0','0');
INSERT INTO [t3610] VALUES (233,'صلى رسول الله صلى الله عليه وسلم وصف خلفه صفا، وصف موازي العدو، وهم في صلاة كلهم، فكبر','248','0','0');
INSERT INTO [t3610] VALUES (234,'يصف صفا موازي العدو، وليسوا في صلاة، ويصف صفا خلف الإمام، فيصلي بهم ركعة، ثم يذهب هؤلاء','249','0','0');
INSERT INTO [t3610] VALUES (235,'فإن خفتم فرجالا أو ركبانا} [البقرة: ٢٣٩] قال: تصلي حيث توجهت راكبا وماشيا، وحيث توجهت بك','250','0','0');
INSERT INTO [t3610] VALUES (236,'ما له خالف، خولف به','251','0','0');
INSERT INTO [t3610] VALUES (237,'أرغبت عن صلاتي. قال: لست مثلك، أنت تسعى في عنق، ونحن نسعى في رفق. فلم يعب عليه ما','252','0','0');
INSERT INTO [t3610] VALUES (238,'صلاة المطاردة قال: ركعة وسجدتين يومئ إيماء','254','0','0');
INSERT INTO [t3610] VALUES (239,'فرجالا} [البقرة: ٢٣٩] قال: عند المسايفة ركعة واحدة، إنما الركوع والسجود وأنت تمشي، أو','255','0','0');
INSERT INTO [t3610] VALUES (240,'صلاة عند المسايفة قالوا: ركعة تلقاء وجهك','256','0','0');
INSERT INTO [t3610] VALUES (241,'عند المسايفة تجري تكبيرة قال سفيان: ركعتين ركعتين يومئ إيماء أو قال عن جويبر، عن الضحاك','257','0','0');
INSERT INTO [t3610] VALUES (242,'القصر واحدة عند القتال، وإن ركعتين ليستا بقصر','258','0','0');
INSERT INTO [t3610] VALUES (243,'يصلي حيث كان وجهه، يومئ إيماء، ويجعل سجوده أخفض من ركوعه، ولا يدع الوضوء ولا','259','0','0');
INSERT INTO [t3610] VALUES (244,'فإن خفتم فرجالا أو ركبانا} [البقرة: ٢٣٩] قال: إذا طلب الأعداء، فقد حل لهم أن يصلوا قبل أي','260','0','0');
INSERT INTO [t3610] VALUES (245,'صلوا على ظهر دوابكم، فمر برجل قائم يصلي بالأرض قال: ما هذا؟ يخالف خالف الله به، فإذا هو','261','0','0');
INSERT INTO [t3610] VALUES (246,'الرجل يطلب عدوه، وهم منهزمون، فحضرت الصلاة، أيصلي على ظهر فرسه؟ قال: بل ينزل، فيستقبل','262','0','0');
INSERT INTO [t3610] VALUES (247,'إن كنت الطالب فانزل فصل، وإن كنت المطلوب فأومئ إيماء','263','0','0');
INSERT INTO [t3610] VALUES (248,'رأيت سعيد بن جبير وعطاء يومئان إليه، والإمام يخطب','264','0','0');
INSERT INTO [t3610] VALUES (249,'كان يومئ والحجاج يخطب','265','0','0');
INSERT INTO [t3610] VALUES (250,'الوليد أجرى الصلاة بالخيف، فقلت لعطاء: وكيف صنعت؟ قال: أومأت قال داود: خطب يومئذ بعد','266','0','0');
INSERT INTO [t3610] VALUES (251,'أما أني في أول جيش، أو قال: في أول سرية دخلت أرض الروم، زمن عمر بن الخطاب رضي الله عنه،','267','0','0');
INSERT INTO [t3610] VALUES (252,'أنا فئة كل مسلم','268','0','0');
