INSERT INTO [t36110] VALUES (118,'فصل (٢٥): استحباب هجر من ترك الفرائض من باب التغرير بترك السلام على تارك الصلاة وشارب الخمر','415','92','0');
INSERT INTO [t36110] VALUES (119,'فصل (٢٦): هجران أهل البدع والمتظاهرين بالمعاصي فرض كفاية','417','92','0');
INSERT INTO [t36110] VALUES (120,'فصل (٢٧): على الآمر الناهي أن يسلك مع العصاة والفساق مسالك بحسب مراتبهم في مخالفة أوامر الله','423','92','0');
INSERT INTO [t36110] VALUES (121,'فصل (٢٨): تباين درجات الهجر بحسب أحوال المهجورين فإن كان الهجر يضعف شرهم وجب الهجر كأسلوب للزجر وإن كان يزيد من شرهم وجبت مخالطتهم وتغريرهم','425','92','0');
INSERT INTO [t36110] VALUES (122,'فصل (٢٩): تباين درجات الهجر بحسب درجات اعتقاد وسلوك الجماهير','427','92','0');
INSERT INTO [t36110] VALUES (123,'فصل (٣٠): الهجر أسلوب شرعي للزجر لتكون كلمة الله هي العليا','430','92','0');
INSERT INTO [t36110] VALUES (124,'فصل (٣١): لا فرق بين وجوب هجر ذي الرحم والأجنبي إذا كان الهجر لتعدي حق الله','431','92','0');
INSERT INTO [t36110] VALUES (125,'فصل (٣٢): عدم جواز الهجر المسلم للتهمة مداومة للصفاء والمحبة','432','92','0');
INSERT INTO [t36110] VALUES (126,'فصل','433','92','0');
INSERT INTO [t36110] VALUES (127,'فصل (٣٣): هجر المسلم العدل في اعتقاده وأفعاله كبيرة','438','92','0');
INSERT INTO [t36110] VALUES (128,'فصل (٣٤): قال ابن مفلح -رحمه الله- ولا هجرة مع السلام','440','92','0');
INSERT INTO [t36110] VALUES (129,'فصل (٣٥): استحباب التقرب إلى الله بحب أهل الطاعة وبغض أهل المعصية','441','92','0');
INSERT INTO [t36110] VALUES (130,'فصل (٣٦): استحبالب تواضع الآمر الناهي في أمره ونهييه بلا افتخار أو تعاظم','446','92','0');
INSERT INTO [t36110] VALUES (131,'فصل (٣٧): استحباب استعانة الآمر الناهي بالله والاعتصام به وخاصة عند عجزه عن مجاهدة نفسه وعن القيام بحقوق الله','455','92','0');
INSERT INTO [t36110] VALUES (132,'فصل (٣٨): استحباب طلب الآمر الناهي إعانة الله','460','92','0');
INSERT INTO [t36110] VALUES (133,'فصل (٣٩): استحباب تحلي الآمر الناهي بالصبر والاحتمال','462','92','0');
INSERT INTO [t36110] VALUES (134,'فصل (٤٠): صبر النبي على أذى قريش عشرين عاما وعفوه عنهم بعد أن أظفره الله بهم','470','92','0');
INSERT INTO [t36110] VALUES (135,'فصل (٤١): يبتلي المرء على قدر دينه والمرء يبتلي على قدر دينه وقوة يقينه','474','92','0');
INSERT INTO [t36110] VALUES (136,'فصل (٤٢): إذا تحقق المصاب من أن المصيبة بتقدير الله وإرادته هانت عليه والمصاب إذا علم وتحقق أن المصيبة بتقدير الله وإرادته هانت عليه','479','92','0');
INSERT INTO [t36110] VALUES (137,'فصل (٤٣): وعد الله المؤمنين الذين يعملون الصالحات باستخلافهم في الأرض وتمكين دينهم لهم','482','92','0');
INSERT INTO [t36110] VALUES (138,'فصل (٤٤): من أخص آداب الأمر بالمعروف والنهي عن المنكر','490','92','0');
INSERT INTO [t36110] VALUES (139,'فصل (٤٥): وجوب تحمل الآمر الناهي الصبر المترتب على أمره ونهيه','491','92','0');
INSERT INTO [t36110] VALUES (140,'فصل (٤٦): قال العلامة ابن القيم: وللعبد فيما يصيبه من أذى الخلق وجنايتهم عليه احد عشر مشهدا','495','92','0');
INSERT INTO [t36110] VALUES (141,'فصل (٤٧): وقوع المحن على قدر قوى الآمرين الناهين ومراتبهم','500','92','0');
