INSERT INTO [t36165] VALUES (68,'الشبهة الثانية: كون قبر النبي - صلى الله عليه وآله وسلم - في مسجده الشريف، ولو كان ذلك لا يجوز لما دفنوه - صلى الله عليه وآله وسلم - في مسجده!','101','62','0');
INSERT INTO [t36165] VALUES (69,'الشبهة الثالثة: أن النبي - صلى الله عليه وآله وسلم - صلى في مسجد الخيف وقد ورد في الحديث أن فيه قبر سبعين نبيا!','102','62','0');
INSERT INTO [t36165] VALUES (70,'الشبهة الرابعة: ما ذكر في بعض الكتب أن قبر إسماعيل - عليه السلام - وغيره في الحجر من المسجد الحرام، وهو أفضل مسجد يتحرى المصلى فيه.','103','62','0');
INSERT INTO [t36165] VALUES (71,'الشبهة الخامسة: بناء أبي جندل - رضي الله عنه - مسجدا على قبر أبي بصير - رضي الله عنه - في عهد النبي - صلى الله عليه وآله وسلم - كما جاء في (الاستيعاب) لابن عبد البر.','104','62','0');
INSERT INTO [t36165] VALUES (72,'الشبهة السادسة: زعم بعضهم أن المنع من اتخاذ القبورمساجد إنما كان لعلة خشية الافتتان بالمقبور، زالت برسوخ التوحيد في قلوب المؤمنين، فزال المنع!','106','62','0');
INSERT INTO [t36165] VALUES (73,'الشبهة السابعة: روى ثابت البناني عن أنس - رضي الله عنه - عنه قال: «كنت أصلي قريبا من قبر، فرآني عمر بن الخطاب، فقال: القبر القبر. فرفعت بصري إلى السماء وأنا أحسبه يقول: القمر!».','107','62','0');
