INSERT INTO [t36165] VALUES (77,'الشبهة الثالثة: إن حديث (لا تشد الرحال الا إلى ثلاثة مساجد: المسجد الحرام والمسجد الأقصى ومسجدى هذا)','111','74','0');
INSERT INTO [t36165] VALUES (78,'سابعا: الحلف بغير الله','112','0','0');
INSERT INTO [t36165] VALUES (79,'الشبهة الأولى: قوله - صلى الله عليه وآله وسلم - لما سئل: أي الصدقة أفضل؟ فقال: «أما وأبيك لتنبأنه»، وقوله - صلى الله عليه وآله وسلم -: «أفلح وأبيه إن صدق» رواهما مسلم.','114','78','0');
INSERT INTO [t36165] VALUES (80,'الشبهة الثانية: إن الله أقسم ببعض المخلوقات! كقوله تعالى: {والشمس وضحاها} (الشمس: ١) وقوله {لا أقسم بهذا البلد} (البلد: ١) وقوله {والليل إذا يغشى} (الليل: ١) وما أشبه ذلك من المخلوقات التي أقسم الله بها.','115','78','0');
INSERT INTO [t36165] VALUES (81,'الشبهة الثالثة: إن الملائكة حلفت بحياة لوط - عليه السلام - فقالوا: {لعمرك إنهم لفي سكرتهم يعمهون} (الحجر:٧٢) فهو دليل على جواز حلف المخلوق بالمخلوق.','115','78','0');
