INSERT INTO [t364] VALUES (1,'مقدمة الرسالة، وسبب تأليفها طلب بعض إخوانه، ونقده لمن سبقه ممن رد بأسلوب حاد.','2','0','0');
INSERT INTO [t364] VALUES (2,'ذكر بداية أمر المردود عليه (حسن الضالعي)، وبيان حقيقة أمره بكلام من عرفه من الثقات.','4','0','0');
INSERT INTO [t364] VALUES (3,'استدلال الضالعي بالآيات والأحاديث بتحريف معانيها، ودعواه العلم والنسب والتصوف.','6','0','0');
INSERT INTO [t364] VALUES (4,'قول الضالعي بعقيدة وحدة الوجود، وذكر بعض كلامه الذي نقله من رآه ورد عليه.','14','0','0');
INSERT INTO [t364] VALUES (5,'ذكر مقتطفات من كتاب «كشف الغطا» وذيله للشيخ سالم باصهي الذي رد به على الضالعي.','15','0','0');
INSERT INTO [t364] VALUES (6,'اعتراف الضالعي باعتقاده معتقد محيي الدين ابن عربي وعبد الكريم الجيلي في وحدة الوجود.','16','0','0');
INSERT INTO [t364] VALUES (7,'من اعتقد عقيدة وحدة الوجود فإنه كافر بإجماع المسلمين؛ لأنه مكذب بآيات القرآن.','16','0','0');
INSERT INTO [t364] VALUES (8,'مقتطفات من رسالة الشيخ عبدالله بن طاهر إلى الشيخ عبد الله الفوري في حقيقة الضالعي.','19','0','0');
INSERT INTO [t364] VALUES (9,'مقتطفات من رسالة الشيخ باشيخ في حقيقة الضالعي، وفيها قوله برفع التكاليف عن الناس.','19','0','0');
INSERT INTO [t364] VALUES (10,'إنكار الضالعي لنبوة محمد - صلى الله عليه وسلم -، ومناقشة لطيفة في طريق إثبات صحة القرآن بإثبات التوراة.','21','0','0');
INSERT INTO [t364] VALUES (11,'الرد على أسئلته المشككة، كتسمية المسيح، وولادته المعجزة، وبعض ما خصه الله من الآيات.','24','0','0');
INSERT INTO [t364] VALUES (12,'مناقشة نفيه التحريف عن التوراة بحجة بشارته بالمسيح، ومراد اليهود الخبيث من ترك ذكره.','27','0','0');
INSERT INTO [t364] VALUES (13,'مناقشته في دعواه ثبوت صحة التوراة والإنجيل بالتواريخ ونفي ذلك عن القرآن الكريم.','30','0','0');
INSERT INTO [t364] VALUES (14,'الرد عليه في نفيه الإعجاز عن القرآن، بأن العرب عجزت عن التحدي، وذكر أنواع إعجازه.','30','0','0');
INSERT INTO [t364] VALUES (15,'من وجوه الإعجاز: ما في القرآن من أخبار الأمم السابقة واللاحقة، مع أن نبينا - صلى الله عليه وسلم - كان أميا.','31','0','0');
INSERT INTO [t364] VALUES (16,'معارضة الشعراء والخطباء حصلت سابقا ولاحقا، بخلاف القرآن فلم ينجح أحد بمعارضته.','32','0','0');
INSERT INTO [t364] VALUES (17,'مناقشته فيما ذكره من تشكيكه بصحة القرآن؛ بحجة ذكره لمعاصي الأنبياء المنافية لعصمتهم.','34','0','0');
INSERT INTO [t364] VALUES (18,'مناقشته فيما احتج به للنصارى في مسألة التثليث، بحجة تركب الإنسان من ثلاث حقائق.','39','0','0');
INSERT INTO [t364] VALUES (19,'مناقشة نفيه وجود آيات تدل على صحة نبوة محمد - صلى الله عليه وسلم - إلا القرآن، وذكر بعض الآيات العقلية.','40','0','0');
INSERT INTO [t364] VALUES (20,'مقارنة لطيفة بين حال بعثة نبينا محمد - صلى الله عليه وسلم - إلى أهل مكة مع بعثة موسى وعيسى إلى قوميهما.','42','0','0');
INSERT INTO [t364] VALUES (21,'من أعظم آيات صدق نبوته - صلى الله عليه وسلم - صدق قوله، وتجرده عن الهوى وعن طلب ملاذ الدنيا.','45','0','0');
INSERT INTO [t364] VALUES (22,'ذكر بعض آياته - صلى الله عليه وسلم - المشهودة؛ كبركة الطعام، ونبع الماء بين كفيه، وانشقاق القمر، وغيرها.','47','0','0');
INSERT INTO [t364] VALUES (23,'نقل مسهب عن ابن حزم ذكر فيه أمثلة مستفيضة في التحريف الواقع في التوراة والإنجيل.','48','0','0');
INSERT INTO [t364] VALUES (24,'اختلاف يهود السامرية وغيرهم في نسخ التوراة التي عندهم، ودعوى كل منهما تحريف الآخر.','48','0','0');
INSERT INTO [t364] VALUES (25,'من تناقض التوراة اختلافها في مكان ولادة بنيامين بن يعقوب، ومدة بقاء بني إسرائيل بمصر.','49','0','0');
INSERT INTO [t364] VALUES (26,'من تحريف التوراة المبالغة بذكر عدد المدن التي عمرها بنوإسرائيل وعدد أبنائه بعد موته.','51','0','0');
INSERT INTO [t364] VALUES (27,'من إقحام ما ليس في التوراة ما فيها من ذكر موضع وفاة موسى وسنه وخفاء قبره بعد موته.','52','0','0');
INSERT INTO [t364] VALUES (28,'نقل ابن حزم في الفصل اتفاق كل النصارى على أن ما بين أيديهم ليس هو كتاب الله المنزل.','53','0','0');
INSERT INTO [t364] VALUES (29,'إلزام النصارى في زعمهم التصديق بالتوراة مع ما فيها من مناقضة لما في كتبهم، كعمر الدنيا.','54','0','0');
INSERT INTO [t364] VALUES (30,'التناقض البين في عدة مواضع من نسخ التوراة التي عندهم، والنصارى يقرون بالنسختين!','55','0','0');
INSERT INTO [t364] VALUES (31,'ذكر أمثلة عديدة يظهر بها التحريف والتناقض بين أناجيل النصارى التي بين أيديهم.','56','0','0');
INSERT INTO [t364] VALUES (32,'الجواب عن الشبه التي استدل بها من الكتاب والسنة بزعم إقرارها بصحة كتب بني إسرائيل.','58','0','0');
INSERT INTO [t364] VALUES (33,'ذكر بعض الشبه التي استدل بها من الكتاب والسنة بزعم إقرارها بصحة كتب بني إسرائيل.','58','0','0');
INSERT INTO [t364] VALUES (34,'إقرار المسلمين بصحة نزول التوراة والإنجيل لا يتعارض مع اعتقادهم بتحريفها وتبديلها.','58','0','0');
INSERT INTO [t364] VALUES (35,'الاستشهاد من التوراة والإنجيل ليس إقرارا بصحتها كلها بل من باب إلزامهم بما يؤمنون.','61','0','0');
INSERT INTO [t364] VALUES (36,'بطلان حديث: «آمنت بما فيك»، ومعنى قوله: {لستم على شيء حتى تقيموا التوراة والإنجيل}','62','0','0');
INSERT INTO [t364] VALUES (37,'معنى قوله: {قل فأتوا بالتوراة فاتلوها إن كنتم صادقين} أنها من باب إلزامهم بما يؤمنون.','63','0','0');
INSERT INTO [t364] VALUES (38,'معنى قوله: {يحكم بها النبيون} يحكم أنبيائهم بها قبل تحريفها، وليس فيه نفي للتحريف.','63','0','0');
INSERT INTO [t364] VALUES (39,'معنى قوله: {وليحكم أهل الإنجيل بما أنزل الله فيه} أمر بالحكم بما أنزل في الإنجيل الصحيح.','65','0','0');
INSERT INTO [t364] VALUES (40,'قوله: {ياأيها الذين أوتوا الكتاب آمنوا بما نزلنا مصدقا لما معكم} عموم أريد به الخصوص.','65','0','0');
INSERT INTO [t364] VALUES (41,'ذكر أمثلة مستفيضة من صور التحريف والتناقض الموجود في نسخ التوراة التي بين أيديهم','65','0','0');
INSERT INTO [t364] VALUES (42,'المقصود بأنصار الله الممدوحين من النصارى في القرآن هم المتبعون لعيسى، وليس المحرفين.','67','0','0');
