INSERT INTO [t366] VALUES (1,'من قوى النفس البشرية «العين»، وذكر ما ورد في إثبات صحتها من الكتاب والسنة.','2','0','0');
INSERT INTO [t366] VALUES (2,'الكلام عن «التنويم المغناطيسي» والتسليم بها عند فلاسفة العصر، وبيان معناها وتأثيرها.','3','0','0');
INSERT INTO [t366] VALUES (3,'التفريق في حكم الإصابة بالعين بين ما يحصل اكتسابا، وما يحصل دون اكتساب اختياري.','4','0','0');
INSERT INTO [t366] VALUES (4,'من قوى النفس ما للرقاة من الحية والعقرب من الأثر على الشفاء بتلاوتهم بألفاظ لا معنى لها.','5','0','0');
INSERT INTO [t366] VALUES (5,'بعض أنواع قوى النفس البشرية ما يمكن حصوله اكتسابا وارتياضا، كارتياض البدن.','5','0','0');
INSERT INTO [t366] VALUES (6,'بعض الوسائل التي يحصل بها الارتياض لكسب قوى النفس، كالتجويع والتأمل ونحوهما.','6','0','0');
INSERT INTO [t366] VALUES (7,'القوى النفسية المكتسبة بالرياضة معروفة عند قدماء اليونان والهنود وغيرهم.','6','0','0');
INSERT INTO [t366] VALUES (8,'لم تلق الرياضات الوثنية معارضة كما تلقت الفلسفة ذلك؛ لأن في الإسلام ما يشبهها جملة.','6','0','0');
INSERT INTO [t366] VALUES (9,'من الرياضات الإسلامية التي شابهت غيرها الصيام والقيام، واعتزال الناس خشية الفتنة.','6','0','0');
INSERT INTO [t366] VALUES (10,'وجود زهاد التابعين وغيرهم ممن بالغ في العبادات الإسلامية سبب فشو الرياضات الوثنية.','6','0','0');
INSERT INTO [t366] VALUES (11,'من أسباب فشو الرياضات الوثنية أن الناقلين لها تلطفوا في إدراجها في العبادات الشرعية.','7','0','0');
INSERT INTO [t366] VALUES (12,'أغراض المرتاضين مختلفة إما لإضعاف شهوة أومحاولة كشف أولغرض سياسي أولغير ذلك.','7','0','0');
INSERT INTO [t366] VALUES (13,'لا يشترط متأخروا العارفين بحقيقة تلك الرياضات دينا أو مذهبا خاصا في طلبها أو تعليمها!','8','0','0');
INSERT INTO [t366] VALUES (14,'نهى الشرع عما يقرب من الغلو في العبادة كصيام الدهر وقيام جميع الليل، وعن تعاطي السحر.','9','0','0');
INSERT INTO [t366] VALUES (15,'يعترف المتصوفة بولع الشياطين بمن ارتاض بطريقتهم فتخدمه وتحصل له قوة السحر.','9','0','0');
INSERT INTO [t366] VALUES (16,'من تأثير النفوس سحر الأبصار الذي ذكره الله، وذكر جملة من القصص في ذلك.','10','0','0');
INSERT INTO [t366] VALUES (17,'سحر الأبصار إما أن يكون لها حقيقة فترى ما لم يكن، أو سحر للأدمغة فتتخيل ما لم يكن.','12','0','0');
INSERT INTO [t366] VALUES (18,'التنويم المغناطيسي هو من جنس سحر الأدمغة، فيخيل للمنوم ما لا وجود له في الحقيقية.','13','0','0');
INSERT INTO [t366] VALUES (19,'الجواب عما يقال من أن القول بسحر الأدمغة يفقد الثقة بالمحسوسات وعذر منكري المعجزات.','13','0','0');
INSERT INTO [t366] VALUES (20,'ذكر مجاورة النبي - صلى الله عليه وسلم - وخلوته بحراء للتعبد شهرا كل عام، ونزول الوحي عليه برمضان فيه.','14','0','0');
INSERT INTO [t366] VALUES (21,'المتقرر في الشريعة صيام رمضان واعتكاف العشر الأواخر منه في أي مسجد، وأحكامه معروفة.','15','0','0');
INSERT INTO [t366] VALUES (22,'خلوة الصوفية الأربعينية لا حجة شرعية لها، وقد يحتج لها بالرؤى أوالإلهام أوالكشف أونحوها.','16','0','0');
INSERT INTO [t366] VALUES (23,'احتجاج هؤلاء بما ليس نصا شرعيا على بدعهم يرد بأمور','16','0','0');
INSERT INTO [t366] VALUES (24,'الأول: أن ما ليس نصا في الاحتجاج فمنه ما نفته الشريعة، ومنه ما لا يعلم إثباته أو نفيه، ومنه ما ثبت جملة، ثم تفصيل أحكامها.','16','23','0');
INSERT INTO [t366] VALUES (25,'الثاني: ما يصح جملة مما ادعي دليلا قد يشتبه بتضليل الشيطان والهوى والتخيل ونحو ذلك.','17','23','0');
INSERT INTO [t366] VALUES (26,'الثالث: ما أوضح الله لعباده من طريق يعرف بها الحق معصوم جملة، وغير ذلك فليس بمعصوم.','18','23','0');
INSERT INTO [t366] VALUES (27,'مناقشة القول بحرمة قتال الكفار وإيذائهم بما لم تجر العادة في قتالهم به، كالقوى النفسية فيه!','19','0','0');
INSERT INTO [t366] VALUES (28,'مناقشة القول بحرمة التصرف بالقوى النفسية إلا بإذن أهل الديوان، لأنهم المقررون لقضاء الله!','21','0','0');
INSERT INTO [t366] VALUES (29,'تغليط القول بأن تلك القوى من كرامة الله لمكتسبها، والخلط بينها وبين المعجزات والكرامات.','22','0','0');
INSERT INTO [t366] VALUES (30,'القوى النفسية المكتسبة بالشياطين لا يمكن استخدامها إلا بإذن قدري إلهي، وله في ذلك حكم.','23','0','0');
INSERT INTO [t366] VALUES (31,'آيات الأنبياء غير مكتسبة، وإنما هي إمداد من الله لهم لنصرة دعوتهم، وقد يبذلون سببا لحصولها.','25','0','0');
INSERT INTO [t366] VALUES (32,'مناقشة دعوى أن ترك السلف لهذه الرياضات لصفاء نفوسهم، وذكر سبب وقوعها للمسلمين.','27','0','0');
INSERT INTO [t366] VALUES (33,'سرد تاريخي لكيفية تسلل هذه الرياضات إلى عباد المسلمين من طريق الزهد والتقلل من الدنيا.','28','0','0');
INSERT INTO [t366] VALUES (34,'التطور في القرنين الثاني والثالث بتقصد الجوع ونحوه، وخلط تصوفهم بما عند الأمم الكافرة.','33','0','0');
INSERT INTO [t366] VALUES (35,'مناقشة جعل (الجوع) من أركان الرياضة، وإلصاقه بالدين، والاحتجاج له بما لا حجة فيه.','35','0','0');
INSERT INTO [t366] VALUES (36,'الكلام عن إسناد وفقه حديث: «بحسب ابن آدم لقيمات» وتحقيق سماع يحيى بن جابر من المقدام.','36','0','0');
INSERT INTO [t366] VALUES (37,'زيادة: «ثلاث» عند الحاكم منكرة في الحديث، والضبط الصحيح لـ «أكلات» بضم الألف والكاف.','37','0','0');
INSERT INTO [t366] VALUES (38,'يمكن للإنسان ضبط ثلث الطعام بأمرين: ترك استيفاء شهوته من طعامه، تقدير ما يثقل به منه.','38','0','0');
INSERT INTO [t366] VALUES (39,'مناقشة جعل (السهر) من أركان الرياضة، وإلصاقه بالدين، والاحتجاج له بما لا حجة فيه.','40','0','0');
INSERT INTO [t366] VALUES (40,'قيام الليل ليس مقصودا من السهر، والمقصود العبادة بالصلاة والذكر، والسهر المجرد لا فضل فيه.','40','0','0');
INSERT INTO [t366] VALUES (41,'أفضل القيام حدده - صلى الله عليه وسلم - بقوله: «أفضل القيام قيام داود كان ينام نصف الليل ويقوم ثلثه وينام سدسه».','41','0','0');
INSERT INTO [t366] VALUES (42,'ليس المقصود بالليل من غروب الشمس، بل هو وقت القيام، ما بعد الفراغ من العشاء للفجر.','41','0','0');
INSERT INTO [t366] VALUES (43,'مناقشة جعلهم ترك أكل ذي الروح وما يخرج منه من الرياضة، واحتجاجهم له بما لا حجة فيه.','42','0','0');
INSERT INTO [t366] VALUES (44,'إبطال احتجاجهم بأثر عمر رضي الله عنه قال: «إن لهذا اللحم ضراوة كضرواة الخمر» من ثلاثة وجوه.','43','0','0');
INSERT INTO [t366] VALUES (45,'إبطال قولهم إن المرتاض إذا حصل له الفتح تحصل له القوة النفسية المذكورة، وهي غير شرعية.','43','0','0');
