INSERT INTO [t38171] VALUES (1,'المقدمة','8','0','0');
INSERT INTO [t38171] VALUES (2,'تخريج العراقي','8','1','0');
INSERT INTO [t38171] VALUES (3,'تخريج [ابن] السبكي','10','1','0');
INSERT INTO [t38171] VALUES (4,'تخريج الزبيدي','10','1','0');
INSERT INTO [t38171] VALUES (5,'منهج الاستخراج','11','1','0');
INSERT INTO [t38171] VALUES (6,'مقدمة الزبيدي','12','1','0');
INSERT INTO [t38171] VALUES (7,'١ - أخبرنا شيخنا المسند الجليل عمر بن أحمد بن عقيل فيما شافهني فيه','13','6','0');
INSERT INTO [t38171] VALUES (8,'٢ - أخبرنا عبد الخالق بن أبي بكر بن المزين ومحمد بن علاء الدين بن عبد الباقي وإسماعيل بن عبد الله بن علي الحنفيون ومحمد بن الطيب بن محمد وآخرون سماعا عليهم قالوا أخبرنا أبو طاهر محمد بن إبراهيم بن حسن','13','6','0');
INSERT INTO [t38171] VALUES (9,'٣ - أخبرنا السيد المحدث سليمان بن يحيى بن عمر بن عبد القادر الحسيني الزبيدي سماعا والسيد القطب','14','6','0');
INSERT INTO [t38171] VALUES (10,'٤ - أخبرنا المسند عمر بن أحمد بن عقيل أخبرنا عبد الله بن سالم بن محمد وأحمد بن محمد بن أحمد والحسن بن علي بن يحيى قالوا','15','6','0');
INSERT INTO [t38171] VALUES (11,'٥ - مرويات الغزالي من نسخة المولد بالسند إليه قال أخبرنا أبو عبد الله الحواري','16','6','0');
INSERT INTO [t38171] VALUES (12,'٦ - (قال الحافظ عماد الدين ابن كثير في طبقاته قرأت على شيخنا الحافظ أبي الحجاج (المزي).','16','6','0');
INSERT INTO [t38171] VALUES (13,'٧ - قال ابن كثير وبالإسناد المتقدم إلى الغزالي حدثنا أحمد بن محمد بن عمر الخفاف','17','6','0');
INSERT INTO [t38171] VALUES (14,'٧/ أ- قال الشافعي:- فيما رواه عنه بإسناد حسن:','17','13','0');
INSERT INTO [t38171] VALUES (15,'٧/ ب- حديث إن الله تعالى يبعث لهذه الأمة على رأس كل مائة سنة من يجدد لها أمر دينها.','17','13','0');
INSERT INTO [t38171] VALUES (16,'٧/جـ - قوله - صلى الله عليه وسلم -:','18','13','0');
INSERT INTO [t38171] VALUES (17,'٧/ د- قال - صلى الله عليه وسلم -:','18','13','0');
INSERT INTO [t38171] VALUES (18,'ربع العبادات','19','0','0');
INSERT INTO [t38171] VALUES (19,'كتاب العلم','19','18','0');
INSERT INTO [t38171] VALUES (20,'٨ - (قال رسول الله - صلى الله عليه وسلم -:','20','19','0');
INSERT INTO [t38171] VALUES (21,'٩ - (قال عليه السلام: العلماء ورثة الأنبياء).','20','19','0');
INSERT INTO [t38171] VALUES (22,'١٠ - وقال عليه السلام (يستغفر للعالم ما في السموات والأرض).','21','19','0');
INSERT INTO [t38171] VALUES (23,'١١ - قال عليه السلام (إن الحكمة تزيد الشريف شرفا وترفع المملوك حتى تجلسه مجالس الملوك).','22','19','0');
INSERT INTO [t38171] VALUES (24,'١٢ - (قال - صلى الله عليه وسلم - خصلتان لا يكونان في منافق حسن سمت وفقه في الدين).','23','19','0');
INSERT INTO [t38171] VALUES (25,'١٣ - (وقال عليه السلام الإيمان عريان ولباسه التقوى وزينته الحياء وثمرته العلم).','23','19','0');
INSERT INTO [t38171] VALUES (26,'١٤ - (وقال عليه السلام أفضل الناس المؤمن العالم الذي إن احتيج إليه نفع وإن استغنى عنه أغنى نفسه).','24','19','0');
INSERT INTO [t38171] VALUES (27,'١٥ - قال عليه السلام (أقرب الناس من درجة النبوة أهل العلم وأهل الجهاد أما أهل العلم فدلوا الناس على ما جاءت به الرسل وأما أهل الجهاد فجاهدوا بأسيافهم على ما جاءت به الرسل).','24','19','0');
INSERT INTO [t38171] VALUES (28,'١٦ - (قال عليه السلام لموت قبيلة أيسر من موت عالم).','25','19','0');
INSERT INTO [t38171] VALUES (29,'١٧ - (قال عليه السلام الناس معادن فخيارهم في الجاهلية خيارهم في الإسلام إذا فقهوا).','25','19','0');
INSERT INTO [t38171] VALUES (30,'١٨ - قال عليه السلام (يوزن يوم القيامة مداد العلماء ودم الشهداء).','26','19','0');
INSERT INTO [t38171] VALUES (31,'١٩ - قال عليه السلام (من حفظ على أمتي أربعين حديثا حتى يؤديها إليهم كنت له شفيعا وشهيدا يوم القيامة).','26','19','0');
INSERT INTO [t38171] VALUES (32,'٢٠ - قال عليه السلام (من حمل من أمتي أربعين حديثا لقي الله يوم القيامة فقيها عالما).','27','19','0');
INSERT INTO [t38171] VALUES (33,'٢١ - (قال عليه السلام من تفقه في دين الله كفاه الله همه ورزقه من حيث لا يحتسب).','32','19','0');
INSERT INTO [t38171] VALUES (34,'٢٢ - (قال - صلى الله عليه وسلم - أوحى الله إلى نبيه إبراهيم أني عليم أحب كل عليم).','34','19','0');
INSERT INTO [t38171] VALUES (35,'٢٣ - (قال عليه السلام العالم أمين الله في الأرض)','34','19','0');
INSERT INTO [t38171] VALUES (36,'٢٤ - (قال عليه السلام صنفان من أمتي إذا صلحوا صلح الناس وإذا فسدوا فسد الناس الأمراء والفقهاء)','34','19','0');
INSERT INTO [t38171] VALUES (37,'٢٥ - (قال عليه السلام إذا أتى علي يوم لا أزداد فيه علما يقربني إلى الله عز وجل فلا بورك في ذلك اليوم)','35','19','0');
INSERT INTO [t38171] VALUES (38,'٢٦ - (وقال عليه الصلاة والسلام فضل العالم على العابد كفضلى على أدنى رجل من أصحابي)','35','19','0');
INSERT INTO [t38171] VALUES (39,'٢٧ - (وقال عليه الصلاة والسلام فضل العالم على العابد كفضل القمر ليلة البدر على سائر الكواكب)','36','19','0');
INSERT INTO [t38171] VALUES (40,'٢٨ - (وقال - صلى الله عليه وسلم - يشفع يوم القيامة ثلاثة الأنبياء ثم العلماء ثم الشهداء)','37','19','0');
INSERT INTO [t38171] VALUES (41,'٢٩ - (قال عليه السلام ما عبد الله بشيء أفضل من فقه في دين ولفقيه واحد أشد على الشيطان من ألف عابد ولكل شيء عماد وعماد الدين الفقه)','37','19','0');
INSERT INTO [t38171] VALUES (42,'٣٠ - (قال عليه السلام خير دينكم أيسره وأفضل العبادة الفقه)','39','19','0');
INSERT INTO [t38171] VALUES (43,'٣١ - (قال عليه السلام فضل المؤمن العالم على المؤمن العابد سبعون درجة).','41','19','0');
INSERT INTO [t38171] VALUES (44,'٣٢ - (قال عليه السلام إنكم أصبحتم في زمان كثير فقهاؤه قليل خطباؤه قليل سائلوه كثير معطوه العمل فيه خير من العلم وسيأتي على الناس زمان قليل فقهاؤه كثير خطباؤه قليل معطوه كثير سائلوه والعلم فيه خير من العمل)','42','19','0');
INSERT INTO [t38171] VALUES (45,'٣٣ - (قال عليه السلام بين العالم والعابد مائة درجة بين كل درجتين حضر الجواد المضمر سبعين سنة).','43','19','0');
INSERT INTO [t38171] VALUES (46,'٣٤ - (وقال عليه السلام لما قيل له يا رسول الله أي الأعمال أفضل فقال العلم بالله عز وجل فقيل الأعمال نريد فقال العلم بالله فقيل له نسأل عن العمل وتجيب عن العلم فقال إن قليل العمل ينفع مع العلم وأن كثير العمل لا ينفع مع الجهل)','45','19','0');
INSERT INTO [t38171] VALUES (47,'٣٥ - (قال عليه السلام يبعث الله يوم القيامة العباد ثم يبعث العلماء ثم يقول يا معشر العلماء إني لم أضع علمي بينكم إلا لعلمي بكم ولم أضع علمي فيكم لأعذبكم اذهبوا فقد غفرت لكم)','46','19','0');
INSERT INTO [t38171] VALUES (48,'٣٦ - (قال أبو عبد الرحمن عبد الله ابن مسعود).','47','19','0');
INSERT INTO [t38171] VALUES (49,'٣٧ - العلم للعلماء كالحلي للناهد، وقد روي مثل ذلك في فضل حسن الخط وليس إسناده بمستقيم.','48','19','0');
INSERT INTO [t38171] VALUES (50,'٣٨ - (قال أبو بكر محمد بن مسلم بن عبيد الله بن عبد الله بن شهاب الزهري).','48','19','0');
INSERT INTO [t38171] VALUES (51,'٣٩ - (قوله عليه الصلاة والسلام من سلك طريقا يطلب فيه علما سلك الله به طريقا إلى الجنة)','49','19','0');
INSERT INTO [t38171] VALUES (52,'٤٠ - (وقال - صلى الله عليه وسلم - إن الملائكة لتضع أجنحتها لطالب العلم رضا بما يصنع)','50','19','0');
INSERT INTO [t38171] VALUES (53,'٤١ - (قال - صلى الله عليه وسلم - لأن تغدو فتتعلم بابا من العلم).','52','19','0');
INSERT INTO [t38171] VALUES (54,'٤٢ - (وقال - صلى الله عليه وسلم - باب من العلم يتعلمه الرجل خير له من الدنيا وما فيها).','53','19','0');
INSERT INTO [t38171] VALUES (55,'٤٣ - (وقال - صلى الله عليه وسلم - طلب العلم فريضة على كل مسلم).','54','19','0');
INSERT INTO [t38171] VALUES (56,'٤٤ - (قال - صلى الله عليه وسلم - اطلبوا العلم ولو بالصين).','56','19','0');
INSERT INTO [t38171] VALUES (57,'٤٥ - (وقال عليه الصلاة والسلام العلم خزائن مفاتيحها السؤال ألا فاسألوا فإنه يؤجر فيه أربعة السائل والعالم والمستمع والمحب لهم).','57','19','0');
INSERT INTO [t38171] VALUES (58,'٤٦ - (وقال - صلى الله عليه وسلم - لا ينبغي للجاهل أن يسكت على جهله ولا للعالم أن يسكت على علمه).','58','19','0');
INSERT INTO [t38171] VALUES (59,'٤٧ - (وفي حديث أبي ذر).','58','19','0');
INSERT INTO [t38171] VALUES (60,'٤٨ - (قال - صلى الله عليه وسلم - من جاءه الموت وهو يطلب العلم ليحيى به الإسلام فبينه وبين الأنبياء درجة واحدة).','59','19','0');
INSERT INTO [t38171] VALUES (61,'٤٧/ أ- (قال أبو الدرداء أيضا العالم والمتعلم شريكان في الخير وسائر الناس همج لا خير فيهم)','61','60','0');
INSERT INTO [t38171] VALUES (62,'٤٧/ ب- (قال أبو الدرداء أيضا كن عالما أو متعلما أو مستمعا ولا تكن رابعا فتهلك)','62','60','0');
INSERT INTO [t38171] VALUES (63,'٤٩ - : أخرج الطبراني بإسناد لا بأس به عن ابن عباس رفعه: (من كتم علما يعلمه ألجم بلجام من نار)','63','19','0');
INSERT INTO [t38171] VALUES (64,'٥٠ - : (قال النبي - صلى الله عليه وسلم - ما آتى الله عالما علما إلا أخذ عليه من الميثاق ما أخذ من النبيين أن يبينه للناس ولا يكتمه)','63','19','0');
INSERT INTO [t38171] VALUES (65,'٥١ - : (وقال - صلى الله عليه وسلم - لما بعث معاذا إلى اليمن لأن يهدى الله بك رجلا واحدا خير لك من الدنيا وما فيها) وفي نسخة خير لك من حمر النعم','64','19','0');
INSERT INTO [t38171] VALUES (66,'٥٢ - : (قال - صلى الله عليه وسلم - من علم وعمل وعلم فذاك يدعى عظيما في ملكوت السماوات) لم يخرجه العراقي وفي بعض النسخ وقال عيسى عليه السلام وهكذا','65','19','0');
INSERT INTO [t38171] VALUES (67,'٥٣ - : (قال - صلى الله عليه وسلم - من تعلم بابا من العلم ليعلم الناس أعطى ثواب سبعين صديقا)','66','19','0');
INSERT INTO [t38171] VALUES (68,'٥٤ - : (وقال - صلى الله عليه وسلم - إذا كان يوم القيامة يقول الله تعالى للعابدين والمجاهدين ادخلوا الجنة فيقول العلماء بفضل علمنا تعبدوا وجاهدوا فيقول الله تعالى أنتم عندي كبعض ملائكتي اشفعوا تشفعوا فيشفعون ثم يدخلون الجنة)','66','19','0');
INSERT INTO [t38171] VALUES (69,'٥٥ - (وقال - صلى الله عليه وسلم - إن الله لا ينزع العلم انتزاعا من الناس بعد أن يؤتيهم إياه ولكن يذهب بذهاب العلماء فكلما ذهب عالم ذهب بما معه من العلم حتى إذا لم يبق إلا رؤساء جهالا أن يسألوا أفتوا بغير علم فيضلون ويضلون).','68','19','0');
INSERT INTO [t38171] VALUES (70,'٥٦ - (قال - صلى الله عليه وسلم - من علم علما فكتمه ألجم يوم القيامة بلجام من نار).','69','19','0');
INSERT INTO [t38171] VALUES (71,'٥٧ - (قال - صلى الله عليه وسلم - نعم العطية ونعم الهدية كلمة حكمة تسمعها فتطوي عليها ثم تحملها إلى أخ لك مسلم فتعلمه إياها تعدل عبادة سنة):','73','19','0');
INSERT INTO [t38171] VALUES (72,'٥٨ - (قال - صلى الله عليه وسلم - الدنيا ملعونة ملعون ما فيها إلا ذكر الله سبحانه وما والاه أو معلما أو متعلما)','73','19','0');
INSERT INTO [t38171] VALUES (73,'٥٩ - (قال - صلى الله عليه وسلم - أن الله وملائكته وأهل سماواته وأرضه حتى النملة في جحرها وحتى الحوت في البحر ليصلون على معلم الناس الخير)','74','19','0');
INSERT INTO [t38171] VALUES (74,'٦٠ - (وقال - صلى الله عليه وسلم - ما أفاد المسلم أخاه فائدة أفضل من حديث حسن بلغه فبلغه)','74','19','0');
INSERT INTO [t38171] VALUES (75,'٦١ - (وقال - صلى الله عليه وسلم - كلمة من الخير يسمعها المؤمن فيعمل بها ويعملها خير له من عبادة سنة صيام نهارها وقيام ليلها).','75','19','0');
INSERT INTO [t38171] VALUES (76,'٦٢ - (وخرج رسول الله - صلى الله عليه وسلم - ذات يوم فرأى مجلسين أحدهما يدعون الله).','76','19','0');
INSERT INTO [t38171] VALUES (77,'٦٣ - (قال - صلى الله عليه وسلم - مثل ما بعثني الله به من العلم والهدى كمثل الغيث الكثير أصاب أرضا فكانت منها بقعة قبلت الماء فأنبتت الكلأ والعشب الكثير وكانت منها بقعة أمسكت الماء فنفع الله بها الناس شربوا منها وسقوا وزرعوا وكانت منها طائفة لا تمسك ماء ولا تنبت كلا).','77','19','0');
INSERT INTO [t38171] VALUES (78,'٦٤ - (قال - صلى الله عليه وسلم - إذا مات ابن آدم انقطع عمله إلا من ثلاث علم ينتفع به أو صدقة جارية أو ولد صالح يدعو له)','78','19','0');
INSERT INTO [t38171] VALUES (79,'٦٥ - (وقال - صلى الله عليه وسلم - الدال على الخير كفاعله)','81','19','0');
INSERT INTO [t38171] VALUES (80,'٦٦ - (قال - صلى الله عليه وسلم - لا حسد إلا في اثنتين رجل أتاه الله حكمة فهو يقضي بها ويعلمها الناس ورجل آتاه الله مالا وسلطه الله على هلكته في الحق فهو ينفق منه آناء الليل وآناء النهار).','82','19','0');
INSERT INTO [t38171] VALUES (81,'٦٧ - (قال - صلى الله عليه وسلم - على خلفائي رحمة الله قيل ومن خلفاؤك قال الذين يحيون سنتي ويعلمونها عباد الله).','83','19','0');
INSERT INTO [t38171] VALUES (82,'٦٨ - (قال معاذ بن جبل).','85','19','0');
INSERT INTO [t38171] VALUES (83,'٦٩ - : الدراهم والدنانير .. يسر الله تعالى قضاء الحاجة بهما ..','89','19','0');
INSERT INTO [t38171] VALUES (84,'٧٠ - الدنيا مزرعة الآخرة','89','19','0');
INSERT INTO [t38171] VALUES (85,'٧١ - : (قوله - صلى الله عليه وسلم - بني الإسلام على خمس).','89','19','0');
INSERT INTO [t38171] VALUES (86,'٧٢ - : (قال - صلى الله عليه وسلم -).','91','19','0');
INSERT INTO [t38171] VALUES (87,'٧٣:- إن الذي أنزل الداء أنزل الدواء روى ابن ماجه عن ابن مسعود رفعه: (ما أنزل الله داء إلا أنزل له الدواء) ورواه هو أيضا وأبو نعيم في الطب عن أبي هريرة بلفظ: (إلا أنزل الله له شفاء) ورواه بهذا اللفظ الحاكم عن ابن مسعود وعند الخطيب في حديث أبي هريرة زيادة وهي: (علمه من علمه وجهله من جهله) وهو عند البخاري في الطب بلفظ ابن ماجه، وزاد مسلم: (فإذا أصبت دواء الداء برئ بإذن الله تعالى).','92','19','0');
INSERT INTO [t38171] VALUES (88,'٧٤ - : (روى مسندا).','93','19','0');
INSERT INTO [t38171] VALUES (89,'٧٧ - (قال هلا شققت عن قلبه فنظرت أصادق هو أم كاذب قاله (في الذي قتل من تكلم بكلمة الإسلام) أي كلمة الشهادة (معتذرا بأنه) إنما (قال ذلك من خوف السيف)','94','19','0');
INSERT INTO [t38171] VALUES (90,'٧٨ - (قال - صلى الله عليه وسلم - أمرت أن أقاتل الناس حتى يقولوا لا إله إلا الله فإذا قالوها عصموا مني دماءهم وأموالهم).','94','19','0');
INSERT INTO [t38171] VALUES (91,'٧٩ - (قال - صلى الله عليه وسلم - دع ما يريبك إلى ما لا يريبك).','97','19','0');
INSERT INTO [t38171] VALUES (92,'٨٠ - (وقال - صلى الله عليه وسلم - الإثم حزاز القلوب).','98','19','0');
INSERT INTO [t38171] VALUES (93,'٨٢ - (قال - صلى الله عليه وسلم - لوابصة).','100','19','0');
INSERT INTO [t38171] VALUES (94,'٨٣ - (صلى الله عليه وسلم بقوله إن من العلم كهيئة المكنون لا يعرفه إلا أهل المعرفة بالله فإذا نطقوا به لم يجهله إلا أهل الاغترار به فلا تحقروا).','101','19','0');
INSERT INTO [t38171] VALUES (95,'٨٤ - (قال - صلى الله عليه وسلم - لما قيل له كيف نفعل إذا جاءنا أمر لم نجده في كتاب الله ولا السنة).','103','19','0');
INSERT INTO [t38171] VALUES (96,'٨٥ - (ما فضل أبو بكر بكثرة صلاة ولا بكثرة صيام، ولكن بسر وقر في صدره).','105','19','0');
INSERT INTO [t38171] VALUES (97,'٨٦ - : (وقال - صلى الله عليه وسلم - اختلاف أمتي رحمة)','105','19','0');
INSERT INTO [t38171] VALUES (98,'٨٧ - : (قال - صلى الله عليه وسلم - المدينة خير لهم لو كانوا يعلمون)','107','19','0');
INSERT INTO [t38171] VALUES (99,'٨٨:- (قال - صلى الله عليه وسلم - المدينة تنفي خبثها كما ينفى الكير خبث الحديد).','108','19','0');
INSERT INTO [t38171] VALUES (100,'٨٩ - : (سحر رسول الله عليه وسلم ومرض بسببه حتى أخبره','109','19','0');
INSERT INTO [t38171] VALUES (101,'٩٠ - (قال رسول الله - صلى الله عليه وسلم - إذ ذكر القدر فامسكوا وإذا ذكر النجوم فأمسكوا إذا ذكر أصحابي فامسكوا).','111','19','0');
INSERT INTO [t38171] VALUES (102,'٩١ - (قال - صلى الله عليه وسلم - أخاف على أمتي بعدي ثلاثا حيف الأئمة وإيمان بالنجوم وتكذيب بالقدر).','112','19','0');
INSERT INTO [t38171] VALUES (103,'٩٢ - (قال عمر بن الخطاب رضي الله عنه تعلموا من النجوم ما تهتدون به في البر والبحر ثم أمسكوا).','113','19','0');
INSERT INTO [t38171] VALUES (104,'٩٣ - (مر رسول الله - صلى الله عليه وسلم - برجل والناس مجتمعون عليه فقال ما هذا).','114','19','0');
INSERT INTO [t38171] VALUES (105,'٩٤ - (قال - صلى الله عليه وسلم - إنما العلم آية محكمة أو سنة قائمة أو فريضة عادلة).','116','19','0');
INSERT INTO [t38171] VALUES (106,'٩٥ - (قوله - صلى الله عليه وسلم - نعوذ بالله من علم لا ينفع)','116','19','0');
INSERT INTO [t38171] VALUES (107,'٩٦ - (قال النبي - صلى الله عليه وسلم - إن من العلم جهلا وإن من القول عيالا).','117','19','0');
INSERT INTO [t38171] VALUES (108,'٩٧ - (وقال - صلى الله عليه وسلم - أيضا قليل من التوفيق خير من كثير من العلم).','117','19','0');
INSERT INTO [t38171] VALUES (109,'٩٨ - (وقال - صلى الله عليه وسلم - علماء حكماء فقهاء).','118','19','0');
INSERT INTO [t38171] VALUES (110,'٩٩ - (وقال - صلى الله عليه وسلم - ألا أنبئكم بالفقيه كل الفقيه قالوا بلى قال من لم يقنط الناس من رحمة الله ولم يؤمنهم من مكر الله ولم يؤيسهم من روح الله ولم يدع القرآن رغبة عنه إلى ما سواه).','119','19','0');
INSERT INTO [t38171] VALUES (111,'١٠٠ - (ولما روى أنس بن مالك)','119','19','0');
INSERT INTO [t38171] VALUES (112,'١٠١ - (قال - صلى الله عليه وسلم - لا يفقه العبد كل الفقه حتى يمقت الناس في ذات الله وحتى يرى للقرآن وجوها كثيرة).','121','19','0');
INSERT INTO [t38171] VALUES (113,'١٠٢ - (قوله - صلى الله عليه وسلم - إذا مررتم برياض الجنة فارتعوا قيل وما رياض الجنة قال مجالس الذكر).','122','19','0');
INSERT INTO [t38171] VALUES (114,'١٠٣ - (في الحديث إن لله تعالى ملائكة سياحين في الهواء سوى ملائكة الخلق إذا رأوا مجالس الذكر ينادي بعضهم بعضا ألا هلموا إلى بغيتكم فيأتونهم ويحفون بهم ويستمعون ألا فاذكروا الله تعالى وذكروا بأنفسكم).','123','19','0');
INSERT INTO [t38171] VALUES (115,'١٠٤ - (وقالوا لم يكن ذلك).','124','19','0');
INSERT INTO [t38171] VALUES (116,'١٠٥ - (حديث أبي ذر).','125','19','0');
INSERT INTO [t38171] VALUES (117,'١٠٦ - (قال - صلى الله عليه وسلم - لعبد الله بن رواحة).','126','19','0');
INSERT INTO [t38171] VALUES (118,'١٠٧ - : (قال - صلى الله عليه وسلم - لما قال ذلك الرجل).','127','19','0');
INSERT INTO [t38171] VALUES (119,'١٠٨ - : (قال رسول الله - صلى الله عليه وسلم - إن من الشعر لحكمة)','129','19','0');
INSERT INTO [t38171] VALUES (120,'١٠٩ - : (قال - صلى الله عليه وسلم - ما حدث أحدكم قوما بحديث لا يفهمونه إلا كان فتنة عليهم)','130','19','0');
INSERT INTO [t38171] VALUES (121,'١١٠ - : (قال - صلى الله عليه وسلم - كلموا الناس بما يعرفون ودعوا ما ينكرون أتريدون أن يكذب الله ورسوله).','130','19','0');
INSERT INTO [t38171] VALUES (122,'١١١ - (قوله - صلى الله عليه وسلم - تسحروا فإن في السحور بركة)','131','19','0');
INSERT INTO [t38171] VALUES (123,'١١٢ - كان يقول (هلموا إلى الغذاء المبارك).','132','19','0');
INSERT INTO [t38171] VALUES (124,'١١٣ - (لقوله - صلى الله عليه وسلم - من فسر القرآن برأيه فليتبوأ مقعده من النار)','132','19','0');
INSERT INTO [t38171] VALUES (125,'١١٤ - (قال - صلى الله عليه وسلم - لابن عباس).','133','19','0');
INSERT INTO [t38171] VALUES (126,'١١٥ - (قوله - صلى الله عليه وسلم - من كذب علي متعمدا فليتبوأ مقعده من النار).','133','19','0');
INSERT INTO [t38171] VALUES (127,'١١٦ - (وقال - صلى الله عليه وسلم - كلمة من الحكمة يتعلمها الرجل خير له من الدنيا وما فيها)','141','19','0');
INSERT INTO [t38171] VALUES (128,'١١٧ - (لما سئل - صلى الله عليه وسلم - عن شر الخلق أبى).','141','19','0');
INSERT INTO [t38171] VALUES (129,'١١٨ - (وقد صح قول رسول الله - صلى الله عليه وسلم - بدأ الإسلام غريبا وسيعود كما بدا فطوبى للغرباء).','142','19','0');
INSERT INTO [t38171] VALUES (130,'١١٨ - (وفي حديث آخر الغرباء ناس قليل صالحون بين ناس كثير من يبغضهم أكثر ممن يحبهم)','144','19','0');
INSERT INTO [t38171] VALUES (131,'١١٩ - (وفي الحديث ما ضل قوم بعد هدى كانوا عليه إلا أوتوا الجدل ثم قرأ ما ضربوه لك إلا جدلا بل هم قوم خصمون).','144','19','0');
INSERT INTO [t38171] VALUES (132,'١٢٠ - (وفي الحديث في معنى قوله تعالى فأما الذين في قلوبهم زيغ).','145','19','0');
INSERT INTO [t38171] VALUES (133,'١٢١ - (وفي بعض الأخبار إنكم في زمان الهمتم فيه وسيأتي قوم يلهمون الجدل).','145','19','0');
INSERT INTO [t38171] VALUES (134,'١٢٢ - (في الخبر المشهور) عن رسول الله - صلى الله عليه وسلم - قال (أبغض الخلق إلى الله الألد الخصم)','146','19','0');
INSERT INTO [t38171] VALUES (135,'١٢٣ - : (وفي الخبر ما أوتى قوم المنطق إلا منعوا العمل)','146','19','0');
INSERT INTO [t38171] VALUES (136,'١٢٤ - (روى أنس رضي الله عنه قيل يا رسول الله متى يترك الأمر بالمعروف والنهي عن المنكر فقال إذا ظهرت المداهنة).','146','19','0');
INSERT INTO [t38171] VALUES (137,'١٢٥ - (قال - صلى الله عليه وسلم - الحسد يأكل الحسنات كما تأكل النار الحطب).','147','19','0');
INSERT INTO [t38171] VALUES (138,'١٢٦ - (قال - صلى الله عليه وسلم - من تكبر وضعه الله ومن تواضع رفعه الله).','148','19','0');
INSERT INTO [t38171] VALUES (139,'١٢٧ - (وقال - صلى الله عليه وسلم - حكاية عن الله عز وجل العظمة إزاري والكبرياء ردائي فمن نازعني فيهما قصمته).','149','19','0');
INSERT INTO [t38171] VALUES (140,'١٢٨ - (المؤمن منهى عن إذلال نفسه)','150','19','0');
INSERT INTO [t38171] VALUES (141,'١٢٩ - (قال - صلى الله عليه وسلم - إذا تعلم الناس العلم وتركوا العمل وتحابوا بالألسن وتباغضوا بالقلوب وتقطاعوا بالأرحام لعنهم الله عند ذلك فأصمهم وأعمى أبصارهم).','151','19','0');
INSERT INTO [t38171] VALUES (142,'١٣٠ - (قال من ترك المراء وهو مبطل بني له بيت في ربض الجنة ومن ترك المراء وهو محق بني له بيت في أعلى الجنة).','152','19','0');
INSERT INTO [t38171] VALUES (143,'١٣١ - (قال - صلى الله عليه وسلم - فيهم إن الله ليؤيد هذا الدين بأقوام لا خلاق لهم)','153','19','0');
INSERT INTO [t38171] VALUES (144,'١٣٢ - (قال - صلى الله عليه وسلم - إن الله تعالى ليؤيد هذا الدين بالرجل الفاجر).','153','19','0');
INSERT INTO [t38171] VALUES (145,'١٣٣ - (قال عليه الصلاة والسلام بني الدين على النظافة)','155','19','0');
INSERT INTO [t38171] VALUES (146,'١٣٤ - (قال عليه الصلاة والسلام) لا تدخل الملائكة بيتا فيه كلب).','156','19','0');
INSERT INTO [t38171] VALUES (147,'١٣٥ - (يحشر كل شخص على صورته المعنوية التي مات عليها)','156','19','0');
INSERT INTO [t38171] VALUES (148,'١٣٦ - (صلى زيد بن ثابت).','157','19','0');
INSERT INTO [t38171] VALUES (149,'١٣٧ - (قال - صلى الله عليه وسلم - ليس من أخلاق المؤمن الملق إلا في طلب العلم).','158','19','0');
INSERT INTO [t38171] VALUES (150,'١٣٨ - (الحكمة ضالة المؤمن يغتنمها حيث يظفر بها)','160','19','0');
INSERT INTO [t38171] VALUES (151,'١٣٩ - (أبيح له) - الجمع بين (تسع نسوة)','161','19','0');
INSERT INTO [t38171] VALUES (152,'١٤٠ - (لو وزن) إيمانه (بإيمان العالمين) أجمعين (لرجح كما شهد له به سيد البشر - صلى الله عليه وسلم -).','161','19','0');
INSERT INTO [t38171] VALUES (153,'١٤١ - كان (يفضلهم) (أبو بكر) رضي الله عنه (بالسر الذي وقر في صدره)','162','19','0');
INSERT INTO [t38171] VALUES (154,'١٤٢ - روي عن الحسن عن حذيفة:','162','19','0');
INSERT INTO [t38171] VALUES (155,'١٤٣ - (قال - صلى الله عليه وسلم - إنما أنا لكم مثل الوالد).','162','19','0');
INSERT INTO [t38171] VALUES (156,'١٤٤ - أخرج الحسين بن محمد التفليسي في كتاب الإعداد بسند فيه مجاهيل عن آنس رفعه:','163','19','0');
INSERT INTO [t38171] VALUES (157,'١٤٥ - (قال رسول الله - صلى الله عليه وسلم - وهو مرشد لكل معلم).','164','19','0');
INSERT INTO [t38171] VALUES (158,'١٤٦ - : (قال نحن معاشر الأنبياء أمرنا أن ننزل الناس منازلهم','164','19','0');
INSERT INTO [t38171] VALUES (159,'١٤٧ - (قال - صلى الله عليه وسلم - ما أحد يحدث قوما بحديث لا تبلغه عقولهم إلا كان فتنة على بعضهم).','166','19','0');
INSERT INTO [t38171] VALUES (160,'١٤٨ - (من سن) في الإسلام (سنة سيئة فعليه وزرها ووزر من عمل بها)','167','19','0');
INSERT INTO [t38171] VALUES (161,'١٤٩ - (ويروى عنه - صلى الله عليه وسلم - لا يكون المرء عالما حتى يكون بعلمه عاملا).','167','19','0');
INSERT INTO [t38171] VALUES (162,'١٥٠ - (وقال - صلى الله عليه وسلم - العلم علمان علم على اللسان فذلك حجة الله عز وجل على ابن آدم وعلم في القلب فذلك العلم النافع).','168','19','0');
INSERT INTO [t38171] VALUES (163,'١٥١ - (وقال - صلى الله عليه وسلم - يكون في آخر الزمان عباد جهال وعلماء فساق).','168','19','0');
INSERT INTO [t38171] VALUES (164,'١٥٢ - (وقال - صلى الله عليه وسلم - لا تتعلموا العلم لتباهوا به العلماء وتماروا به السفهاء ولتصرفوا به وجوه الناس اليكم فمن فعل ذلك فهو في النار).','169','19','0');
INSERT INTO [t38171] VALUES (165,'١٥٣ - (قال من كتم علما نافعا جاء يوم القيامة ملجما بلجام من نار).','171','19','0');
INSERT INTO [t38171] VALUES (166,'١٥٣/ أ- (قال - صلى الله عليه وسلم - من كتم علما عنده ألجم بلجام من نار).','172','165','0');
INSERT INTO [t38171] VALUES (167,'١٥٤ - (قال - صلى الله عليه وسلم - لأنا من غير الدجال أخوف عليكم من الدجال فقيل وما ذاك فقال من الأئمة المضلين).','172','19','0');
INSERT INTO [t38171] VALUES (168,'١٥٥ - (وقال - صلى الله عليه وسلم - من ازداد علما ولم يزدد هدى لم يزد من الله إلا بعدا).','173','19','0');
INSERT INTO [t38171] VALUES (169,'١٥٦ - (قال عمر رضي الله عنه إن أخوف ما أخاف على هذه الأمة المنافق العليم قالوا وكيف يكون منافقا عليما قال عليم اللسان جاهل القلب والعمل).','174','19','0');
INSERT INTO [t38171] VALUES (170,'١٥٧ - (وقال الفضيل بن عياض رحمه الله إني لأرحم ثلاثة عزيز قوم ذل وغني قوم افتقر وعالما تلعب به الدنيا).','175','19','0');
INSERT INTO [t38171] VALUES (171,'١٥٨ - (وقال - صلى الله عليه وسلم - إن العالم ليعذب عذابا يطيف به أهل النار استعظاما لشدة عذابه).','175','19','0');
INSERT INTO [t38171] VALUES (172,'١٥٩ - (قال أسامة بن زيد سمعت رسول الله - صلى الله عليه وسلم - يقول يؤتى بالعالم يوم القيامة فيلقى في النار فتندلق أقتابه فيدور بها كما يدور','175','19','0');
INSERT INTO [t38171] VALUES (173,'١٦٠ - (صلى الله عليه وسلم - قال من طلب علما مما يبتغي به وجه الله ليصيب به عرضا من الدنيا لم يجد عرف الجنة يوم القيامة).','176','19','0');
INSERT INTO [t38171] VALUES (174,'١٦١ - (روى أبو الدرداء)','177','19','0');
INSERT INTO [t38171] VALUES (175,'١٦٢ - (روى الضحاك) ولفظ القوت وقد روينا عن الضحاك (عن ابن عباس)','178','19','0');
INSERT INTO [t38171] VALUES (176,'١٦٣ - (روي عن معاذ بن جبل) رضي الله عنه (موقوفا)','180','19','0');
INSERT INTO [t38171] VALUES (177,'١٦٤ - في خبر آخر إن العبد لينشر له من الثناء ما بين المشرق والمغرب وما يزن عند الله جناح بعوضة) هكذا أورده صاحب القوت.','182','19','0');
INSERT INTO [t38171] VALUES (178,'١٦٥ - (روى عن جابر) بن عبد الله الأنصاري رضي الله عنه (موقوفا)','182','19','0');
INSERT INTO [t38171] VALUES (179,'١٦٦ - (وقال رسول الله - صلى الله عليه وسلم - مررت ليلة أسري بي بقوم تقرض شفاههم بمقاريض من نار فقلت من أنتم فقالوا إنا كنا نأمر بالخير ولا نفعله وننهي عن الشر ونأتيه)','184','19','0');
INSERT INTO [t38171] VALUES (180,'١٦٧ - (قال - صلى الله عليه وسلم - هلاك أمتي عالم فاجر وعابد جاهل وشر الشرار شرار العلماء وخير الخيار خيار العلماء)','186','19','0');
INSERT INTO [t38171] VALUES (181,'١٦٨ - (وقال أبو عمر وعبد الرحمن بن عمرو بن أبي عمرو الأوزاعي)','187','19','0');
INSERT INTO [t38171] VALUES (182,'١٦٩ - (وقال أبو الدرداء رضي الله عنه) (ويل لمن لا يعلم مرة وويل لمن يعلم ولا يعمل سبع مرات)','188','19','0');
INSERT INTO [t38171] VALUES (183,'١٧٠ - (قال أبو عمرو عامر بن شراحيل الشعبي)','189','19','0');
INSERT INTO [t38171] VALUES (184,'١٧١ - روى أبو عبد الله (مكحول) الشامي فقيه ثقة كثير الإرسال مات سنة بضع عشرة ومائة (عن عبد الرحمن بن غنم)','189','19','0');
INSERT INTO [t38171] VALUES (185,'١٧٢ - (وقال حذيفة رضي الله عنه)','191','19','0');
INSERT INTO [t38171] VALUES (186,'١٧٣ - (قال - صلى الله عليه وسلم - القضاة ثلاثة قاض قضى بالحق وهو يعلم','191','19','0');
INSERT INTO [t38171] VALUES (187,'١٧٤ - (روي عنه - صلى الله عليه وسلم - أنه قال إن الشيطان ربما يسبقكم بالعلم).','192','19','0');
INSERT INTO [t38171] VALUES (188,'١٧٥ - (وفي الخبر مما أخاف على أمتي زلة العالم وجدال منافق في القرآن).','193','19','0');
INSERT INTO [t38171] VALUES (189,'١٧٦ - (روي أن رجلا جاء إلى رسول الله - صلى الله عليه وسلم - وقال له علمني من غرائب العلم فقال له ما صنعت في رأس العلم قال وما رأس العلم فقال له - صلى الله عليه وسلم - هل عرفت الرب سبحانه قال نعم قال فما صنعت في معرفته قال ما شاء الله قال هل عرفت الموت قال نعم قال فما أعددت له قال ما شاء الله قال اذهب فأحكم ما هناك ثم تعال نعلمك من غرائب العلم).','195','19','0');
INSERT INTO [t38171] VALUES (190,'١٧٧ - (نزع القميص المطرز بالعلم) أي المعلم بعلم قال العراقي المعروف نزعه للخميصة المعلمة اهـ.','196','19','0');
INSERT INTO [t38171] VALUES (191,'١٧٨ - : (نزع الخاتم الذهب) ونبذه (فى أثناء الخطبة)','197','19','0');
INSERT INTO [t38171] VALUES (192,'١٧٩ - (وقال - صلى الله عليه وسلم - من بدا جفا يعني من سكن البادية جفا ومن اتبع الصيد غفل ومن أتى السلطان افتتن)','197','19','0');
INSERT INTO [t38171] VALUES (193,'١٨٠ - (وقال - صلى الله عليه وسلم - ستكون عليكم أمراء تعرفون منهم وتنكرون فمن أنكر فقد برئ ومن كره فقد سلم ولكن من رضى وتابع أبعده الله قيل أفلا نقاتلهم قال لا ما صلوا)','198','19','0');
INSERT INTO [t38171] VALUES (194,'١٨١ - (وقال رسول الله - صلى الله عليه وسلم - العلماء أمناء الرسل على عباد الله تعالى ما لم يخالطوا السلاطين فإذا فعلوا ذلك فقد خانوا الرسل فاحذروهم واعتزلوهم)','199','19','0');
INSERT INTO [t38171] VALUES (195,'١٨٢ - : (قال أحد العلماء الأثبات (سعيد بن المسيب)','200','19','0');
INSERT INTO [t38171] VALUES (196,'١٨٣ - (قال - صلى الله عليه وسلم - شرار العلماء الذين يأتون الأمراء وخيار الأمراء الذين يأتون العلماء)','201','19','0');
INSERT INTO [t38171] VALUES (197,'١٨٤ - (وفي الخبر العلم ثلاثة كتاب ناطق) أي بين واضح (وسنة قائمة) أي ثابتة دائمة محافظ عليها معمول بها عملا متصلا وفي رواية ماضية أي جارية مستمرة (ولا أدري) أي قول المجيب لمن سأله عن مسألة لا يعلم حكمها لا أدري هكذا أورده صاحب القوت.','202','19','0');
INSERT INTO [t38171] VALUES (198,'١٨٥ - (قال - صلى الله عليه وسلم - في مسائل سئل عنها فقال لا أدري وناهيك بهذا مستندا فقد ثبت عنه - صلى الله عليه وسلم - أنه قال ما أدرى أعزير نبي أم لا وما أدري تبع ملعون أم لا وما أدري ذو القرنين نبي أم لا).','203','19','0');
INSERT INTO [t38171] VALUES (199,'١٨٦ - (لما سئل رسول الله - صلى الله عليه وسلم - عن خير البقاع وشرها فقال - صلى الله عليه وسلم - لا أدري حتى نزل جبريل عليه السلام فسأله فقال لا أدري إلى أن أعلمه الله عز وجل أن خير البقاع المساجد).','204','19','0');
INSERT INTO [t38171] VALUES (200,'١٨٧ - (كان شغل الصحابة والتابعين) لهم بإحسان (في خمسة أشياء قراءة القرآن دراسة وتعليما (وعمارة المساجد) بالصلوات في الجماعات (وذكر الله تعالى) سرا وجهرا في كل أحيان (والأمر بالمعروف والنهي عن المنكر) شرعا نقله صاحب القوت عن بعض السلف.','205','19','0');
INSERT INTO [t38171] VALUES (201,'١٨٨ - (وفي الخبر إذا رأيتم الرجل قد أوتى صمتا وزهدا فاقتربوا منه فإنه يلقن الحكمة كذا في نسخ الكتاب والرواية يلقي الحكمة هكذا أورده صاحب القوت بلا إسناد.','206','19','0');
INSERT INTO [t38171] VALUES (202,'١٨٩ - (كان قد آخى بينهما رسول الله - صلى الله عليه وسلم -)','206','19','0');
INSERT INTO [t38171] VALUES (203,'١٩٠ - (قال - صلى الله عليه وسلم - من عمل بما علم ورثه الله علم ما لم يعلم)','206','19','0');
INSERT INTO [t38171] VALUES (204,'١٩١ - (وقال - صلى الله عليه وسلم - فيما يرويه عن ربه عز وجل لا يزال العبد يتقرب إلي بالنوافل حتى أحبه فإذا أحببته كنت له سمعا وبصرا الحديث).','207','19','0');
INSERT INTO [t38171] VALUES (205,'١٩٢ - (قال رسول الله - صلى الله عليه وسلم - اليقين الإيمان كله).','208','19','0');
INSERT INTO [t38171] VALUES (206,'١٩٣ - (قال - صلى الله عليه وسلم - تعلموا اليقين) قال صاحب القوت (ومعناه جالسوا الموقنين) أي المتصفين بعلم اليقين (واسمعوا منهم علم اليقين) لأنهم علماؤه إلى هنا نص القوت زاد المصنف (وواظبوا على الاقتداء بهم) أي بأفعالهم في حركاتهم وعند سكونهم (ليقوي يقينكم كما قوي يقينهم).','209','19','0');
INSERT INTO [t38171] VALUES (207,'١٩٤ - (قال رسول الله - صلى الله عليه وسلم - لما قيل له)','209','19','0');
INSERT INTO [t38171] VALUES (208,'١٩٥ - (قال - صلى الله عليه وسلم - من أقل ما أوتيتم اليقين وعزيمة الصبر ومن أعطى حظه منهما لم يبال ما فاته من قيام الليل وصيام النهار)','210','19','0');
INSERT INTO [t38171] VALUES (209,'١٩٦ - (وقال عمر بن الخطاب رضي الله عنه تعلموا العلم وتعلموا للعلم السكينة والحلم وتواضعوا لمن تعلمون منه وليتواضع لكم من يتعلم منكم ولا تكونوا جبابرة العلماء فلا يقوم علمكم بجهلكم)','211','19','0');
INSERT INTO [t38171] VALUES (210,'١٩٧ - (وفي الخبر) ونص القوت وقد روينا معناه في الأثر (من آتاه الله زهدا وتواضعا وحسن خلق فهو إمام المتقين)','211','19','0');
INSERT INTO [t38171] VALUES (211,'١٩٨ - (في الخبر إن من خيار أمتي قوما يضحكون جهرا من سعة رحمة الله عز وجل ويبكون سرا من خوف عذاب الله أبدانهم في الأرض وقلوبهم في السماء أرواحهم في الدنيا وعقولهم في الآخرة)','212','19','0');
INSERT INTO [t38171] VALUES (212,'١٩٩ - : (وروى أنه قيل يا رسول الله أي الأعمال أفضل قال اجتناب المحارم ولا يزال فوك رطبا من ذكر الله تعالى قيل فأي الأصحاب خير قال صاحب إن ذكرت أعانك وإن نسيت ذكرك قيل فأي الأصحاب شر قال صاحب إن نسيت لم يذكرك وإن ذكرت لم يعنك قيل فأي الناس أعلم قال أشدهم لله خشية قيل فأخبرنا بخيارنا نجالسهم قال الذين إذا رؤا ذكر الله تعالى قالوا فأي الناس شر قال اللهم غفرا قالوا أخبرنا يا رسول الله قال العلماء إذا فسدوا)','213','19','0');
INSERT INTO [t38171] VALUES (213,'٢٠٠ - (قال - صلى الله عليه وسلم - إن أكثر الناس أمانا)','214','19','0');
INSERT INTO [t38171] VALUES (214,'٢٠١ - (وقال ابن عمر رضي الله عنهما عشنا برهة)','215','19','0');
INSERT INTO [t38171] VALUES (215,'٢٠٢ - (في خبر آخر بمثل معناه) ونص القوت بمعناه (كنا أصحاب رسول الله - صلى الله عليه وسلم - أوتينا الإيمان قبل القرآن وسيأتي بعدكم قوم يؤتون القرآن قبل الإيمان ويقيمون حروفه ويضيعون حدوده ويقولون قرأنا القرآن فمن أقرأ منا وعلمنا فمن أعلم منا فذلك حظهم) منه (وفي لفظ آخر أولئك شرار هذه الأمة)','215','19','0');
INSERT INTO [t38171] VALUES (216,'٢٠٣ - (ولما تلا رسول الله - صلى الله عليه وسلم - قوله تعالى (فمن يرد الله أن يهديه يشرح صدره للإسلام فقيل) يا رسول الله (ما هذا الشرح فقال إن النور إذا قذف في القلب انشرح له الصدر وانفسح قيل فهل لذلك من علامة قال نعم التجافي) أي التباعد (عن دار الغرور والإنابة) أي الرجوع (إلى دار الخلود والاستعداد للموت قبل نزوله).','216','19','0');
INSERT INTO [t38171] VALUES (217,'٢٠٤ - (وقيل) قالوا (لحذيفة نراك تتكلم بكلام لا يسمع من غيرك من الصحابة) رضوان الله عليهم (فمن أين) ونص القوت فممن (أخذته فقال خصنى به رسول الله - صلى الله عليه وسلم - كان الناس يسألونه عن الخير وكنت أسأله عن الشر مخافة أن أقع فيه) رواه البخاري ومسلم هكذا مختصرا وفي آخره زيادة من رواية أبي إدريس الخلافي أنه سمع حذيفة بن اليمان يقول كان الناس يسألون رسول الله - صلى الله عليه وسلم - عن الخير وكنت أسأله عن الشر مخافة أن يدركني.','217','19','0');
INSERT INTO [t38171] VALUES (218,'٢٠٤ - (وكان حذيفة رضي الله عنه أيضا قد خص بعلم المنافقين وأفرد بمعرفة علم النفاق وأسبابه ودقائق الفتن)','219','19','0');
INSERT INTO [t38171] VALUES (219,'٢٠٥ - (كان عمر وعثمان وأكابر الصحابة رضي الله عنهم يسألونه عن الفتن العامة والخاصة) ويرجعون إليه في العلم الذي خص به فروى الأئمة الستة خلا أبا داود من رواية شقيق عن حذيفة قال كنا عند عمر فقال أيكم يحفظ حديث رسول الله - صلى الله عليه وسلم - في الفتنة.','221','19','0');
INSERT INTO [t38171] VALUES (220,'٢٠٦ - قال ابن عباس) رضي الله عنهما (ما من أحد إلا ويؤخذ من علمه ويترك إلا رسول الله - صلى الله عليه وسلم -).','222','19','0');
INSERT INTO [t38171] VALUES (221,'٢٠٧ - أخرج اللالكائي في السنة من رواية إبراهيم بن أبي حفصة قال:','222','19','0');
INSERT INTO [t38171] VALUES (222,'٢٠٨ - أخرج اللالكائي في السنة من رواية شبابة قال: حدثنا هشام بن الغاز عن نافع عن ابن عمر قال: (كل بدعة ضلالة وإن رآها الناس حسنة).','222','19','0');
INSERT INTO [t38171] VALUES (223,'٢٠٩ - (روى عن ابن مسعود) رضي الله عنه (موقوفا) عليه (و) روى أيضا (مسندا)','222','19','0');
INSERT INTO [t38171] VALUES (224,'٢١٠ - (في خطبة رسول الله - صلى الله عليه وسلم - طوبى لمن شغله عيبه عن عيوب الناس وأنفق من مال اكتسبه من غير معصية وخالط أهل الفقه والحكمة طوبى لمن ذل في نفسه وحسنت خليقته وصلحت سريرته وعزل عن الناس شره طوبى لمن عمل بعلمه وأنفق الفضل من ماله وأمسك الفضل من أقواله ووسعته السنة ولم يعدها الى بدعة).','224','19','0');
INSERT INTO [t38171] VALUES (225,'٢١١ - (النبي - صلى الله عليه وسلم - كان يتوكأ في خطبة العيد والاستسقاء على قوس أو عصا لا على المنبر).','225','19','0');
INSERT INTO [t38171] VALUES (226,'٢١٢ - كان أحمد بن حنبل يقول: (تركوا العلم وأقبلوا على الغرائب، ما أقل العلم فيهم، والله المستعان)','226','19','0');
INSERT INTO [t38171] VALUES (227,'٢١٣ - (وفي الحديث المشهور)','226','19','0');
INSERT INTO [t38171] VALUES (228,'٢١٤ - (وفي حديث آخر من غش أمتى فعليه لعنة الله والملائكة والناس أجمعين قيل يا رسول الله وما غش أمتك قال: أن يبتدع بدعة يحمل الناس عليها).','227','19','0');
INSERT INTO [t38171] VALUES (229,'٢١٥ - (وقال النبي - صلى الله عليه وسلم - إن لله ملكا ينادي كل يوم من خالف سنة محمد - صلى الله عليه وسلم - لم ينل شفاعته).','227','19','0');
INSERT INTO [t38171] VALUES (230,'٢١٦ - (وقال - صلى الله عليه وسلم - عليكم بالنمط الأوسط الذي يرجع إليه العالي ويرتفع إليه التالي)','228','19','0');
INSERT INTO [t38171] VALUES (231,'٢١٧ - (قال - صلى الله عليه وسلم - الشيخ في قومه كالنبي في أمته)','228','19','0');
INSERT INTO [t38171] VALUES (232,'٢١٨ - وقال - صلى الله عليه وسلم - يا أيها الناس اعقلوا عن ربكم وتواصوا بالعقل تعرفوا ما أمرتم به وما نهيتم عنه واعلموا أنه ينجدكم عند ربكم واعلموا أن العاقل من أطاع الله وإن كان دميم المنظر حقير الخطر دنيء المنزلة رث الهيئة وأن الجاهل من عصى الله تعالى وإن كان جميل المنظر عظيم الخطر شريف المنزلة حسن الهيئة فصيحا نطوقا فالقردة والخنازير أعقل عند الله تعالى ممن عصاه ولا تغتروا بتعظيم أهل الدنيا إياكم فإنهم من الخاسرين.','229','19','0');
INSERT INTO [t38171] VALUES (233,'٢١٩ - قال رسول الله - صلى الله عليه وسلم - أول ما خلق الله العقل فقال له أقبل فأقبل ثم قال له أدبر فأدبر ثم قال الله عز وجل وعزتي وجلالي ما خلقت خلقا أكرم علي منك بك آخذ وبك أعطى وبك أثيب وبك أعاقب.','230','19','0');
INSERT INTO [t38171] VALUES (234,'٢٢٠ - قال داود بن المحبر في كتاب العقل حدثنا سلام بن المنذر عن موسى بن جابان عن (أنس) بن مالك رضي الله عنه قال (أثنى قوم على رجل عند رسول الله - صلى الله عليه وسلم - حتى بالغوا".','234','19','0');
INSERT INTO [t38171] VALUES (235,'٢٢١ - وقال داود بن المحبر أيضا في كتابه المذكور حدثنا عباد عن زيد ابن أسلم عن أبيه عن (عمر) بن الخطاب (رضي الله عنه أن رسول الله - صلى الله عليه وسلم - قال ما اكتسب رجل مثل فضل عقل).','235','19','0');
INSERT INTO [t38171] VALUES (236,'٢٢٢ - قال داود بن المحبر أيضا في كتابه المذكور حدثنا مقاتل بن سليمان عن عمرو بن شعيب عن أبيه عن جده عن (النبي - صلى الله عليه وسلم -) قال (إن الرجل ليدرك بحسن خلقه درجة الصائم القائم ولا يتم لرجل حسن خلقه حتى يتم عقله فعند ذلك يتم إيمانه).','235','19','0');
INSERT INTO [t38171] VALUES (237,'٢٢٣ - قال داود بن المحبر أيضا في كتابه المذكور حدثنا عباد حدثنا سهل (عن أبيه عن أبي سعيد الخدري) رضي الله عنه (إنه صلى الله عليه وسلم قال لكل شيء دعامة ودعامة المؤمن عقله فبقدر عقله تكون عبادته لربه عز وجل (أما سمعتم قول الفاجر) عند ندامته (لو كنا نسمع أو نعقل ما كنا في أصحاب السعير).','236','19','0');
INSERT INTO [t38171] VALUES (238,'٢٢٤ - (قال داود بن المحبر أيضا في كتابه المذكور حدثنا عباد عن زيد بن أسلم عن أبيه.','236','19','0');
INSERT INTO [t38171] VALUES (239,'٢٢٥ - قال داود بن المحير أيضا في كتابه المذكور حدثنا غياث ابن إبراهيم عن الربيع بن لوط الأنصاري عن أبيه عن جده.','236','19','0');
INSERT INTO [t38171] VALUES (240,'٢٢٦ - قال داود بن المحبر أيضا في كتابه المذكور حدثنا عباد بن عبد الله ابن طاوس (عن أبي هريرة) رضي الله عنه (قال لما رجع رسول الله - صلى الله عليه وسلم - من غزوة أحد).','237','19','0');
INSERT INTO [t38171] VALUES (241,'٢٢٧ - قال داود بن المحبر أيضا في كتابه المذكور حدثنا ميسرة عن حنظلة بن وداعة الدؤلي عن أبيه.','237','19','0');
INSERT INTO [t38171] VALUES (242,'٢٢٨ - قال داود في كتابه المذكور أيضا حدثنا ميسرة عن محمد بن زيد عن عمرو.','238','19','0');
INSERT INTO [t38171] VALUES (243,'٢٢٩ - قال داود بن المحبر أيضا في كتابه المذكور حدثنا ميسرة عن غالب عن ابن جبير.','239','19','0');
INSERT INTO [t38171] VALUES (244,'٢٣٠ - قال داود بن المحبر أيضا في كتابه المذكور حدثنا مسيرة عن محمد عن سالم بن عبد الله عن أبيه أن النبي - صلى الله عليه وسلم -) قال (أن أحب المؤمنين إلى الله عز وجل من نصب في طاعة الله ونصح لعباده وكمل عقله ونصح نفسه)','240','19','0');
INSERT INTO [t38171] VALUES (245,'٢٣١ - قال داود بن المحبر أيضا في كتابه المذكور حدثنا ميسرة عن محمد بن زيد عن أبي قتادة رضي الله عنه قال قلت يا رسول الله أرأيت قول الله عز وجل أيكم أحسن عملا فقال (- صلى الله عليه وسلم - أتمكم عقلا أشدكم لله خوفا وأحسنكم فيما أمركم به ونهى عنه نظرا).','240','19','0');
INSERT INTO [t38171] VALUES (246,'٢٣٢ - (قوله - صلى الله عليه وسلم - ما خلق الله عز وجل خلقا أكرم عليه من العقل).','241','19','0');
INSERT INTO [t38171] VALUES (247,'٢٣٣ - (قال - صلى الله عليه وسلم -) لعلي رضي الله عنه (إذا تقرب الناس بأبواب البر فتقرب أنت بعقلك).','241','19','0');
INSERT INTO [t38171] VALUES (248,'٢٣٤ - (قول رسول الله - صلى الله عليه وسلم - لأبي الدرداء) رضي الله عنه فيما','241','19','0');
INSERT INTO [t38171] VALUES (249,'٢٣٥ - روى داود بن المحبر في كتاب العقل فقال حدثنا ميسرة عن محمد بن زيد (عن سعيد بن المسيب)','242','19','0');
INSERT INTO [t38171] VALUES (250,'٢٣٦ - (قال - صلى الله عليه وسلم - في حديث آخر) رواه ابن المحبر في العقل فقال حدثنا عدي عن ابن أبي ذئب عن الزهري عن سعيد بن المسيب قال أشرف النبي - صلى الله عليه وسلم - على خيبر فذكر زيادة في أوله ثم قال (إنما العاقل من آمن بالله وصدق رسله وعمل بطاعته).','243','19','0');
INSERT INTO [t38171] VALUES (251,'٢٣٧ - وقد ورد في الحديث:','243','19','0');
INSERT INTO [t38171] VALUES (252,'٢٣٨ - (رسول الله - صلى الله عليه وسلم - حيث قال إن روح القدس).','243','19','0');
INSERT INTO [t38171] VALUES (253,'٢٣٩ - (روي أن ابن سلام)','244','19','0');
INSERT INTO [t38171] VALUES (254,'٢٤٠ - أورد المصنف في فضل العقل أحاديث غالبها من كتاب داود بن المحبر','245','19','0');
INSERT INTO [t38171] VALUES (255,'كتاب قواعد العقائد','248','18','0');
INSERT INTO [t38171] VALUES (256,'٢٤١ - لا أول له اشتهر وصف الباري تعالى بالقديم في عبارات المتكلمين','249','255','0');
INSERT INTO [t38171] VALUES (257,'٢٤٢ - (قال - صلى الله عليه وسلم - لو علمتم).','249','255','0');
INSERT INTO [t38171] VALUES (258,'٢٤٣ - (قوله - صلى الله عليه وسلم - إن لله سبحانه سبعين حجابا من نور لو كشفها لأحرقت سبحات وجهه كل من أدركه بصره).','250','255','0');
INSERT INTO [t38171] VALUES (259,'٢٤٤ - (قوله - صلى الله عليه وسلم - الحجر الأسود يمين الله في أرضه).','251','255','0');
INSERT INTO [t38171] VALUES (260,'٢٤٥ - (قوله - صلى الله عليه وسلم - قلب المؤمن بين أصبعين من أصابع الرحمن).','251','255','0');
INSERT INTO [t38171] VALUES (261,'٢٤٦ - (قوله - صلى الله عليه وسلم - إني لأجد نفس الرحمن من جانب اليمن).','251','255','0');
INSERT INTO [t38171] VALUES (262,'٢٤٧ - قال مالك بن أنس أمام المدينة رحمه الله تعالى (لما سئل عن) معنى (الاستواء)','252','255','0');
INSERT INTO [t38171] VALUES (263,'٢٤٨ - (انشقاق القمر)','254','255','0');
INSERT INTO [t38171] VALUES (264,'٢٥٠ - (تسبيح الحصى)','255','255','0');
INSERT INTO [t38171] VALUES (265,'٢٥١ - (إنطاق العجماء)','256','255','0');
INSERT INTO [t38171] VALUES (266,'٢٥٢ - (ما تفجر من بين أصابعه) الشريفة (من الماء)','259','255','0');
INSERT INTO [t38171] VALUES (267,'٢٥٣ - الصراط','261','255','0');
INSERT INTO [t38171] VALUES (268,'٢٥٤ - (قوله - صلى الله عليه وسلم - الأئمة من قريش).','262','255','0');
INSERT INTO [t38171] VALUES (269,'٢٥٥ - (حديث جبريل عليه السلام لما سأله عن الإيمان فقال أن تؤمن بالله وملائكته وكتبه ورسله وبالبعث بعد الموت وبالحساب والقدر خيره وشره قال فما الإسلام فذكر الخمس خصال) هكذا هو نص القوت ووجد في بعض نسخ الإحياء زيادة واليوم الآخر بعد قوله ورسله (فعبر بالإسلام عن تسليم الظاهر بالقول والعمل) فدل على اختلافهما في الحكم.','263','255','0');
INSERT INTO [t38171] VALUES (270,'٢٥٦ - (حديث سعد) بن أبي وقاص الزهري رضي الله عنه أحد العشرة المبشرة المشهود لهم بالجنة وآخر من توفي منهم سنة سبع وخمسين (أنه - صلى الله عليه وسلم - أعطى رجلا عطاء ولم يعط الآخر فقال له سعد يا رسول الله تركت فلانا لم تعطه وهو مؤمن فقال - صلى الله عليه وسلم - أو مسلم فرد عليه فأعاده رسول الله - صلى الله عليه وسلم) هكذا أورده صاحب القوت.','264','255','0');
INSERT INTO [t38171] VALUES (271,'٢٥٨ - روي عن رسول الله - صلى الله عليه وسلم - (لا يكفر أحد إلا بجحوده بما أقر به)','266','255','0');
INSERT INTO [t38171] VALUES (272,'٢٥٩ - (قال - صلى الله عليه وسلم - لا يزني الزاني حين يزني وهو مؤمن).','266','255','0');
INSERT INTO [t38171] VALUES (273,'٢٦٠ - (قال - صلى الله عليه وسلم - فيما يروى) عنه (في بعض الأخبار الإيمان يزيد وينقص).','267','255','0');
INSERT INTO [t38171] VALUES (274,'٢٦١ - (قال - صلى الله عليه وسلم - الإيمان بضع وسبعون بابا).','268','255','0');
INSERT INTO [t38171] VALUES (275,'٢٦٢ - روى الفقيه أبو الليث السمرقندي في تفسيره عند قوله تعالى: (وإذا ما أنزلت سورة فمنهم من يقول أيكم زادته هذه إيمانا). فقال:','268','255','0');
INSERT INTO [t38171] VALUES (276,'٢٦٣ - (قال - صلى الله عليه وسلم - لما دخل المقابر) أي مقبرة المدينة وإنما جمعها باعتبار ما حولها (السلام عليكم أهل دار قوم مؤمنين وإنا إن شاء الله بكم لاحقون).','269','255','0');
INSERT INTO [t38171] VALUES (277,'٢٦٤ - (قوله - صلى الله عليه وسلم - أربع من كن فيه فهو منافق خالص وإن صام وصلى وزعم أنه مؤمن من إذا حدث كذب وإذا وعد أخلف وإذا ائتمن خان وإذا خاصم فجر).','270','255','0');
INSERT INTO [t38171] VALUES (278,'٢٦٥ - (في حديث أبي سعيد الخدري)','270','255','0');
INSERT INTO [t38171] VALUES (279,'٢٦٦ - (قال - صلى الله عليه وسلم - أكثر منافقي هذه الأمة قراؤها).','271','255','0');
INSERT INTO [t38171] VALUES (280,'٢٦٧ - في حديث آخر (الشرك أخفي في أمتي من دبيب النمل على الصفا) هكذا أورده صاحب القوت.','272','255','0');
INSERT INTO [t38171] VALUES (281,'٢٦٨ - (قال حذيفة رضي الله عنه كان الرجل يتكلم بالكلمة على عهد رسول الله - صلى الله عليه وسلم - يصير بها منافقا إلى أن يموت وأني لأسمعها من أحدكم في اليوم عشر مرات).','273','255','0');
INSERT INTO [t38171] VALUES (282,'٢٦٩ - وقال حذيفة رضي الله عنه (المنافقون اليوم أكثر منهم على عهد رسول الله - صلى الله عليه وسلم - فكانوا إذ ذاك يخفونه وهم الآن يظهرونه).','273','255','0');
INSERT INTO [t38171] VALUES (283,'٢٧٠ - سمع ابن عمر) هو عبد الله بن عمر (رجلا يتعرض للحجاج).','273','255','0');
INSERT INTO [t38171] VALUES (284,'٢٧١ - (قال أيضا - صلى الله عليه وسلم - شر الناس ذو الوجهين الذي يأتي هؤلاء بوجه وهولاء بوجه)','274','255','0');
INSERT INTO [t38171] VALUES (285,'٢٧٢ - وروى أن رسول الله - صلى الله عليه وسلم - وفي القوت وفي الخبر أن رسول الله (كان جالسا في جماعة من أصحابه فذكروا رجلا وأكثروا الثناء عليه)','274','255','0');
INSERT INTO [t38171] VALUES (286,'٢٧٣ - (وقال - صلى الله عليه وسلم - في دعائه اللهم إني أستغفرك لما علمت وما لم أعلم فقيل له أتخاف يا رسول الله فقال وما يؤمنني والقلوب بين أصبعين من أصابع الرحمن يقلبها كيف شاء).','275','255','0');
INSERT INTO [t38171] VALUES (287,'٢٧٤ - وفي الحديث من قال أنا مؤمن فهو كافر ومن قال أنا عالم فهو جماهل هكذا هو في القوت.','275','255','0');
INSERT INTO [t38171] VALUES (288,'كتاب الطهارة','277','18','0');
INSERT INTO [t38171] VALUES (289,'٢٧٥ - (قال - صلى الله عليه وسلم - مفتاح الصلاة الطهور).','278','288','0');
INSERT INTO [t38171] VALUES (290,'٢٧٦ - (قال - صلى الله عليه وسلم - الطهور نصف الإيمان)','278','288','0');
INSERT INTO [t38171] VALUES (291,'٢٧٧ - وقال أبو هريرة وغيره من أهل الصفة كلنا نأكل الشواء فتقام الصلاة فتدخل أصابعنا في الحصى ثم نفركها بالتراب ونكبر.','279','288','0');
INSERT INTO [t38171] VALUES (292,'٢٧٨ - (قال عمر بن الخطاب رضي الله عنه ما كنا نعرف الأشنان على عهد رسول الله - صلى الله عليه وسلم - وإنما كانت مناديلنا بواطن أرجلنا كنا إذا أكلنا الغمر مسحنا بها).','280','288','0');
INSERT INTO [t38171] VALUES (293,'٢٧٩ - عند الترمذي من حديث أنس ما رأى النبي - صلى الله عليه وسلم -','280','288','0');
INSERT INTO [t38171] VALUES (294,'٢٨٠ - (قال رسول الله - صلى الله عليه وسلم - لما نزع نعليه في الصلاة وأخبره جبريل)','281','288','0');
INSERT INTO [t38171] VALUES (295,'٢٨١ - قوله - صلى الله عليه وسلم - إذا بلغ الماء قلتين لم يحمل خبثا.','281','288','0');
INSERT INTO [t38171] VALUES (296,'٢٨٢ - قوله - صلى الله عليه وسلم - خلق الماء طهورا لا ينجسه إلا ما غير طعمه أو ريحه','283','288','0');
INSERT INTO [t38171] VALUES (297,'٢٨٣ - (قال عمر رضي الله عنه رآني النبي - صلى الله عليه وسلم - وأنا أبول قائما فقال يا عمر لا تبل قائما).','285','288','0');
INSERT INTO [t38171] VALUES (298,'٢٨٤ - (روى حذيفة) بن اليمان رضى الله عنه (أنه - صلى الله عليه وسلم - بال قائما فأتيته بوضوء فتوضأ ومسح على خفيه).','285','288','0');
INSERT INTO [t38171] VALUES (299,'٢٨٥ - (قال رسول الله - صلى الله عليه وسلم - عامة الوسواس منه).','285','288','0');
INSERT INTO [t38171] VALUES (300,'٢٨٦ - يقول بالتعوذ الوارد (عند الدخول) أي عند إرادته (بسم الله أعوذ بالله من الخبيث المخبث الشيطان الرجيم)','286','288','0');
INSERT INTO [t38171] VALUES (301,'٢٨٧ - في الخبر أن النبي - صلى الله عليه وسلم - فعله أعني رش الماء.','287','288','0');
INSERT INTO [t38171] VALUES (302,'٢٨٨ - (حديث سلمان رضي الله عنه علمنا رسول الله - صلى الله عليه وسلم - كل شيء حتى الخراءة أمرنا أن لا نستنجي بعظم ولا روث ونهانا أن نستقبل القبلة ببول ولا غائط).','287','288','0');
INSERT INTO [t38171] VALUES (303,'٢٨٩ - من الرخصة أن يبول الإنسان قريبا من صاحبه مستترا','287','288','0');
INSERT INTO [t38171] VALUES (304,'٢٩٠ - يقول بعد الفراغ من الاستنجاء اللهم طهر قلبي من النفاق وحصن فرجي من الفواحش)','288','288','0');
INSERT INTO [t38171] VALUES (305,'٢٩١ - ورد أنه لما نزل قوله عز وجل (فيه رجال يحبون أن يتطهروا والله يحب المتطهرين)','288','288','0');
INSERT INTO [t38171] VALUES (306,'٢٩٢ - لم ير رسول الله - صلى الله عليه وسلم - قط خارجا من الغائط إلا توضأ','289','288','0');
INSERT INTO [t38171] VALUES (307,'٢٩٣ - قال النبي - صلى الله عليه وسلم - إن أفواهكم طرق القرآن فطيبوها بالسواك.','289','288','0');
INSERT INTO [t38171] VALUES (308,'٢٩٤ - قال صلى الله عليه وسلم صلاة في اثر سواك أفضل من خمس وسبعين صلاة من غير سواك.','290','288','0');
INSERT INTO [t38171] VALUES (309,'٢٩٥ - قال - صلى الله عليه وسلم - لولا أن أشق على أمتي لأمرتهم بالسواك عند كل صلاة.','290','288','0');
INSERT INTO [t38171] VALUES (310,'٢٩٦ - (قال - صلى الله عليه وسلم - مالي أراكم تدخلون علي قلحا استاكوا)','291','288','0');
INSERT INTO [t38171] VALUES (311,'٢٩٧ - كان - صلى الله عليه وسلم - يستاك من الليل مرارا وفي بعض النسخ في الليلة مرارا.','291','288','0');
INSERT INTO [t38171] VALUES (312,'٢٩٨ - عن ابن عباس رضي الله عنهما أنه قال لم يزل يأمرنا رسول الله - صلى الله عليه وسلم - (بالسواك حتى ظننا أنه سينزل عليه فيه شيء)','291','288','0');
INSERT INTO [t38171] VALUES (313,'٢٩٩ - قال - صلى الله عليه وسلم - (عليكم بالسواك فإنه مطهرة للفم ومرضاة للرب عز وجل)','292','288','0');
INSERT INTO [t38171] VALUES (314,'٣٠٠ - كان أصحاب النبي - صلى الله عليه وسلم - يروحون والسواك على آذانهم.','292','288','0');
INSERT INTO [t38171] VALUES (315,'٣٠١ - قال - صلى الله عليه وسلم - لا وضوء إن لم يسم الله تعالى.','292','288','0');
INSERT INTO [t38171] VALUES (316,'٣٠٢ - قوله - صلى الله عليه وسلم - مسح الرقبة أمان من الغل.','293','288','0');
INSERT INTO [t38171] VALUES (317,'٣٠٣ - توضأ رسول الله - صلى الله عليه وسلم - ثلاثا وقال من زاد فقد ظلم وأساء.','294','288','0');
INSERT INTO [t38171] VALUES (318,'٣٠٤ - قال - صلى الله عليه وسلم - سيكون قوم من هذه الأمة يعتدون في الدعاء والطهور).','294','288','0');
INSERT INTO [t38171] VALUES (319,'٣٠٥ - يقال من وهن علم الرجل.','295','288','0');
INSERT INTO [t38171] VALUES (320,'٣٠٦ - يكره أن ينفض اليد فيرش الماء أي بعد الفراغ من الوضوء لما روى أنه - صلى الله عليه وسلم -','295','288','0');
INSERT INTO [t38171] VALUES (321,'٣٠٧ - قالوا أي القائلين بالكراهة (الوضوء يوزن) في كفة الحسنات أي ماؤه (قاله سعيد بن المسيب والزهري)','295','288','0');
INSERT INTO [t38171] VALUES (322,'٣٠٨ - روى معاذ بن جبل (رضي الله عنه أنه - صلى الله عليه وسلم - مسح وجهه بطرف ثوبه).','295','288','0');
INSERT INTO [t38171] VALUES (323,'٣٠٩ - روت عائشة رضي الله عنها أنه - صلى الله عليه وسلم - كانت له منشفة هو في سنن الترمذي أخبرنا سفيان بن وكيع حدثنا عبد الله بن وهب عن زيد بن حباب عن أبي معاذ عن الزهري عن عروة عن عائشة رضي الله عنها.','296','288','0');
INSERT INTO [t38171] VALUES (324,'٣١٠ - (قال - صلى الله عليه وسلم - من توضأ فأسبغ الوضوء).','296','288','0');
INSERT INTO [t38171] VALUES (325,'٣١١ - قال النبي - صلى الله عليه وسلم - أيضا ألا أنبئكم بما يكفر الله به الخطايا ويرفع به الدرجات إسباغ الوضوء في المكاره ونقل الأقدام إلى المساجد وانتظار الصلاة بعد الصلاة فذلكم الرباط.','297','288','0');
INSERT INTO [t38171] VALUES (326,'٣١٢ - توضأ رسول الله - صلى الله عليه وسلم - مرة وقال هذا وضوء لا يقبل الله الصلاة','297','288','0');
INSERT INTO [t38171] VALUES (327,'٣١٣ - (توضأ مرتين) كذا في النسخ وفي بعضها مرتين مرتين وهكذا هو في القوت (وقال من توضأ مرتين آتاه الله أجره مرتين)','298','288','0');
INSERT INTO [t38171] VALUES (328,'٣١٤ - (توضأ ثلاثا ثلاثا وقال هذا وضوئي ووضوء الأنبياء من قبلي ووضوء خليل الرحمن إبراهيم - صلى الله عليه وسلم -)','298','288','0');
INSERT INTO [t38171] VALUES (329,'٣١٥ - (قال - صلى الله عليه وسلم - من ذكر الله عز وجل عند طهوره طهر الله جسده كله ومن لم يذكره الله تعالى لم يطهر منه إلا ما أصاب الماء)','298','288','0');
INSERT INTO [t38171] VALUES (330,'٣١٦ - (وقال - صلى الله عليه وسلم - من توضأ على طهر كتب الله له عشر حسنات)','299','288','0');
INSERT INTO [t38171] VALUES (331,'٣١٧ - (قال - صلى الله عليه وسلم - الوضوء على الوضوء نور علي نور)','299','288','0');
INSERT INTO [t38171] VALUES (332,'٣١٨ - (قال - صلى الله عليه وسلم - إذا توضأ العبد المسلم أو المؤمن فتمضمض خرجت الخطايا من فيه فإذا استنثر خرجت الخطايا من أنفه فإذا','299','288','0');
INSERT INTO [t38171] VALUES (333,'٣١٩ - (يروى أن الطاهر كالصائم)','301','288','0');
INSERT INTO [t38171] VALUES (334,'٣٢٠ - (قال - صلى الله عليه وسلم - من توضأ فأحسن الوضوء)','301','288','0');
INSERT INTO [t38171] VALUES (335,'٣٢١ - (وقال مجاهد)','302','288','0');
INSERT INTO [t38171] VALUES (336,'٣٢٢ - (وكان رسول الله - صلى الله عليه وسلم - يدهن الشعر)','302','288','0');
INSERT INTO [t38171] VALUES (337,'٣٢٣ - (قال - صلى الله عليه وسلم - من كانت له شعره فليكرمها أي ليصنها)','303','288','0');
INSERT INTO [t38171] VALUES (338,'٣٢٤ - (ودخل عليه) - صلى الله عليه وسلم - (رجل ناثر الرأس)','303','288','0');
INSERT INTO [t38171] VALUES (339,'٣٢٥ - (وفي الخبر المشهور إنه - صلى الله عليه وسلم - كان لا يفارقه المشط والمدرى في سفر ولا حضر)','303','288','0');
INSERT INTO [t38171] VALUES (340,'٣٢٦ - (في الخبر غريب أنه - صلى الله عليه وسلم - كان يسرح لحيته في اليوم مرتين)','304','288','0');
INSERT INTO [t38171] VALUES (341,'٣٢٨ - (وفي حديث أغرب منه)','304','288','0');
INSERT INTO [t38171] VALUES (342,'٣٢٩ - (أمرهم رسول الله - صلى الله عليه وسلم - بغسل البراجم)','305','288','0');
INSERT INTO [t38171] VALUES (343,'٣٣٠ - (وقت لهم رسول الله - صلى الله عليه وسلم - قلم الأظفار ونتف الإبط وحلق العانة أربعين يوما)','305','288','0');
INSERT INTO [t38171] VALUES (344,'٣٣١ - (أمر - صلى الله عليه وسلم - بتنظيف ما تحت الأظفار)','306','288','0');
INSERT INTO [t38171] VALUES (345,'٣٣٢ - (جاء في الأثر أن النبي - صلى الله عليه وسلم - استبطأ الوحي فلما هبط عليه جبريل عليه السلام قال له كيف ننزل عليكم وأنتم لا تغسلون براجمكم ولا تنظفون رواجبكم وقلحالا تستاكون مر أمتك بذلك)','306','288','0');
INSERT INTO [t38171] VALUES (346,'٣٣٣ - (روى بعض الصحابة أن رسول الله - صلى الله عليه وسلم - نزل منزلا في بعض أسفاره فنام على بطنه)','307','288','0');
INSERT INTO [t38171] VALUES (347,'٣٣٤ - (وقال - صلى الله عليه وسلم - لا يحل للرجل أن يدخل حليلته الحمام وفي البيت مستحم)','307','288','0');
INSERT INTO [t38171] VALUES (348,'٣٣٥ - (المشهور على ألسنة الناس) (حرام على الرجال دخول الحمام إلا بمئزر وحرام على المرأة دخول الحمام إلا نفساء أو مريضة)','308','288','0');
INSERT INTO [t38171] VALUES (349,'٣٣٦ - (دخلت عائشة رضي الله عنها حماما من سقم بها)','308','288','0');
INSERT INTO [t38171] VALUES (350,'٣٣٧ - (الله سبحانه وتر يحب الوتر)','308','288','0');
INSERT INTO [t38171] VALUES (351,'٣٣٩ - (قال - صلى الله عليه وسلم - الختان سنة للرجال ومكرمة للنساء)','309','288','0');
INSERT INTO [t38171] VALUES (352,'٣٤٠ - (قال - صلى الله عليه وسلم - لأم عطية)','309','288','0');
INSERT INTO [t38171] VALUES (353,'٣٤١ - (قوله - صلى الله عليه وسلم - خير شبابكم من تشبه بشيوخكم وشر شيوخكم من تشبه بشبابكم)','310','288','0');
INSERT INTO [t38171] VALUES (354,'٣٤٢ - نهى رسول الله - صلى الله عليه وسلم - (عن الخضاب بالسواد).','310','288','0');
INSERT INTO [t38171] VALUES (355,'٣٤٣ - قال - صلى الله عليه وسلم - (هو خضاب أهل النار) أي الخضاب بالسواد (وفي لفظ آخر الخضاب بالسواد خضاب الكفار).','310','288','0');
INSERT INTO [t38171] VALUES (356,'٣٤٤ - (عن ابن عباس رضي الله عنه عن النبي - صلى الله عليه وسلم - يكون في آخر الزمان قوم يخضبون بالسواد كحواصل الحمام لا يريحون رائحة الجنة)','311','288','0');
INSERT INTO [t38171] VALUES (357,'٣٤٥ - قال رسول الله - صلى الله عليه وسلم - الصفرة خضاب المسلمين والحمرة خضاب المؤمنين','311','288','0');
INSERT INTO [t38171] VALUES (358,'٣٤٦ - (كان أنس رضي الله عنه يقول قبض رسول الله - صلى الله عليه وسلم - وليس في رأسه ولحيته عشرون شعرة بيضاء فقيل له يا أبا حمزة)','311','288','0');
INSERT INTO [t38171] VALUES (359,'٣٤٧ - يقال أن يحيى بن أكثم التميمي أبو محمد المرزي القاضي روى عن عبد العزيز بن أبي حازم وابن المبارك وعن الترمذي والسراج','312','288','0');
INSERT INTO [t38171] VALUES (360,'٣٤٨ - نهى عليه السلام عن نتف الشيب وقال هو نور المؤمن.','313','288','0');
INSERT INTO [t38171] VALUES (361,'كتاب الصلاة','314','18','0');
INSERT INTO [t38171] VALUES (362,'٣٤٩ - (قال - صلى الله عليه وسلم - ثلاثة يوم القيامة على كثيب).','315','361','0');
INSERT INTO [t38171] VALUES (363,'٣٥٠ - (وقال - صلى الله عليه وسلم - لا يسمع نداء المؤذن جن ولا إنس ولا شيء إلا شهد له يوم القيامة).','315','361','0');
INSERT INTO [t38171] VALUES (364,'٣٥١ - (وقال - صلى الله عليه وسلم - يد الرحمن على رأس المؤذن حتى يفرغ من أذانه).','316','361','0');
INSERT INTO [t38171] VALUES (365,'٣٥٢ - (قال - صلى الله عليه وسلم - إذا سمعتم النداء فقولوا مثل ما يقول المؤذن).','316','361','0');
INSERT INTO [t38171] VALUES (366,'٣٥٣ - وعند الفراغ من إجابة المؤذن (يقول اللهم بحق هذه الدعوة التامة والصلاة القائمة آت محمدا الوسيلة والفضيلة وابعثه المقام المحمود الذي وعدته أنك لا تخلف الميعاد)','317','361','0');
INSERT INTO [t38171] VALUES (367,'٣٥٤ - (قال - صلى الله عليه وسلم - خمس صلوات كتبهن الله).','318','361','0');
INSERT INTO [t38171] VALUES (368,'٢٥٦ - (قال - صلى الله عليه وسلم - إن الصلوات كفارة لما بينهن من الصغائر ما اجتنبت الكبائر).','319','361','0');
INSERT INTO [t38171] VALUES (369,'٣٥٧ - (وقال - صلى الله عليه وسلم - بيننا وبين المنافقين شهود).','320','361','0');
INSERT INTO [t38171] VALUES (370,'٣٥٨ - (وقال - صلى الله عليه وسلم - من لقي الله وهو مضيع للصلاة).','320','361','0');
INSERT INTO [t38171] VALUES (371,'٣٥٩ - (وقال - صلى الله عليه وسلم - الصلاة عماد الدين فمن تركها فقد هدم الدين).','320','361','0');
INSERT INTO [t38171] VALUES (372,'٣٦٠ - (وسئل رسول الله - صلى الله عليه وسلم - أي الأعمال أفضل قال الصلاة لمواقيتها)','321','361','0');
INSERT INTO [t38171] VALUES (373,'٣٦١ - (وقال - صلى الله عليه وسلم - من حافظ على الخمس).','321','361','0');
INSERT INTO [t38171] VALUES (374,'٣٦١ - ذكر أبي بن خلف مع هؤلاء إشارة إلى أنه أشقى هذه الأمة','322','361','0');
INSERT INTO [t38171] VALUES (375,'٣٦٢ - (قال - صلى الله عليه وسلم - مفتاح الجنة الصلاة).','322','361','0');
INSERT INTO [t38171] VALUES (376,'٣٦٣ - (قال - صلى الله عليه وسلم - ما افترض الله على خلقه بعد التوحيد أحب إليه من الصلاة لو كان شيء أحب إليه منها لتعبد به ملائكته فمنهم راكع ومنهم ساجد ومنهم قائم وقاعد).','323','361','0');
INSERT INTO [t38171] VALUES (377,'٣٦٤ - (قال - صلى الله عليه وسلم - من ترك صلاة متعمدا فقد كفر).','323','361','0');
INSERT INTO [t38171] VALUES (378,'٣٦٥ - (قال - صلى الله عليه وسلم - من ترك صلاة متعمدا فقد برئ من ذمة محمد - صلى الله عليه وسلم -).','323','361','0');
INSERT INTO [t38171] VALUES (379,'٣٦٦ - (قال أبو هريرة رضي الله عنه من توضأ فأحسن وضوءه ثم خرج عامدا).','324','361','0');
INSERT INTO [t38171] VALUES (380,'٣٦٧ - يروى أن أول ما ينظر فيه من عمل العبد يوم القيامة الصلاة','324','361','0');
INSERT INTO [t38171] VALUES (381,'٣٦٨ - (قال - صلى الله عليه وسلم - مثل الصلاة المكتوبة كمثل الميزان من أوفى استوفى).','325','361','0');
INSERT INTO [t38171] VALUES (382,'٣٦٩ - (قال يزيد) بن أبان (الرقاشي) تابعي عن أنس (كانت صلاة رسول الله - صلى الله عليه وسلم - مستوية كأنها موزونة).','325','361','0');
INSERT INTO [t38171] VALUES (383,'٣٦٩/ أ- (قال - صلى الله عليه وسلم - إن الرجلين من أمتي ليقومان إلى الصلاة وركوعهما وسجودهما واحد وإن ما بين صلاتيهما ما بين السماء والأرض وأشار - صلى الله عليه وسلم - (إلى الخشوع)','326','382','0');
INSERT INTO [t38171] VALUES (384,'٣٧٠ - (قال - صلى الله عليه وسلم - لا ينظر الله يوم القيامة إلى عبد لا يقيم صلبه بين ركوعه وسجوده).','326','361','0');
INSERT INTO [t38171] VALUES (385,'٣٧١ - (قال - صلى الله عليه وسلم - أما يخاف الذي يحول وجهه في الصلاة أن يحول الله وجهه وجه حمار).','326','361','0');
INSERT INTO [t38171] VALUES (386,'٣٧٢ - (قال - صلى الله عليه وسلم - من صلى صلاة)','327','361','0');
INSERT INTO [t38171] VALUES (387,'٣٧٣ - (قال - صلى الله عليه وسلم - أسوأ الناس سرقة الذي يسرق من صلاته).','327','361','0');
INSERT INTO [t38171] VALUES (388,'٣٧٤ - (قال - صلى الله عليه وسلم - صلاة الجماعة تفضل صلاة الفذ بسبع وعشرين درجة).','328','361','0');
INSERT INTO [t38171] VALUES (389,'٣٧٥ - (وقال عثمان) ابن عفان رضي الله عنه فيما روى عنه (مرفوعا) أي رفعه إلى رسول الله - صلى الله عليه وسلم - (من شهد العشاء)','329','361','0');
INSERT INTO [t38171] VALUES (390,'٣٧٦ - (قال - صلى الله عليه وسلم - من صلى صلاة في جماعة فقد ملأ نحره عبادة).','329','361','0');
INSERT INTO [t38171] VALUES (391,'٤٧٦ - (قال - صلى الله عليه وسلم - من صلى أربعين يوما الصلوات) الخمس','329','361','0');
INSERT INTO [t38171] VALUES (392,'٣٧٧ - (قال رسول الله - صلى الله عليه وسلم - ما تقرب العبد إلى الله بشيء أفضل من سجود خفي).','330','361','0');
INSERT INTO [t38171] VALUES (393,'٣٧٨ - (قال - صلى الله عليه وسلم - ما من مسلم يسجد لله سجدة إلا رفعه الله بها درجة وحط عنه بها سيئة).','331','361','0');
INSERT INTO [t38171] VALUES (394,'٣٧٩ - (روى أن رجلا قال لرسول الله - صلى الله عليه وسلم - ادع الله أن يجعلني من أهل شفاعتك وأن يرزقني مرافقتك)','332','361','0');
INSERT INTO [t38171] VALUES (395,'٣٨٠ - (قال - صلى الله عليه وسلم - إذا قرأ ابن آدم السجدة).','332','361','0');
INSERT INTO [t38171] VALUES (396,'٣٨١ - (قال النبي - صلى الله عليه وسلم - من صلى ركعتين لم يحدث نفسه فيهما بشيء من الدنيا غفر له ما تقدم من ذنبه).','332','361','0');
INSERT INTO [t38171] VALUES (397,'٣٨٢ - (وقال النبي - صلى الله عليه وسلم - إنما الصلاة تمسكن وتواضع وتضرع وتأوه وتنادم وتضع يديك فتقول اللهم اللهم فمن لم يفعل فهي خداج).','333','361','0');
INSERT INTO [t38171] VALUES (398,'٣٨٣ - (وروى عن الله سبحانه في الكتب السالفة)','333','361','0');
INSERT INTO [t38171] VALUES (399,'٣٨٤ - (قال - صلى الله عليه وسلم - إنما فرضت الصلاة وأمر بالحج والطواف وأشعرت المناسك لإقامة ذكر الله تعالى).','334','361','0');
INSERT INTO [t38171] VALUES (400,'٣٨٥ - (قال - صلى الله عليه وسلم - وقد رأى أنس بن مالك رضي الله عنه رجلا يتوضأ) فقال (إذا صليت فصل صلاة موع).','334','361','0');
INSERT INTO [t38171] VALUES (401,'٣٨٦ - (قال - صلى الله عليه وسلم - من لم تنهه صلاته عن الفحشاء والمنكر لم يزدد من الله إلا بعدا).','334','361','0');
INSERT INTO [t38171] VALUES (402,'٣٨٧ - (عن عائشة رضي الله عنها قالت كان رسول الله - صلى الله عليه وسلم - يحدثنا ونحدثه فإذا حضرت الصلاة فكأنه لم يعرفنا ولم نعرفه اشتغالا بعظمة الله عز وجل).','335','361','0');
INSERT INTO [t38171] VALUES (403,'٣٨٧/ أ- (وقال لا ينظر الله إلى صلاة لا يحضر) بضم المثناة التحتية وكسر ثالثة (الرجل فيها قلبه مع بدنه).','335','402','0');
INSERT INTO [t38171] VALUES (404,'٣٨٨ - (رأى رسول الله - صلى الله عليه وسلم - رجلا يعبث بلحيته في الصلاة فقال لو خشع قلب هذا لخشعت جوارحه).','335','361','0');
INSERT INTO [t38171] VALUES (405,'٣٨٩ - (وقال - صلى الله عليه وسلم - من بنى لله مسجدا ولو كمفحص قطاة بنى الله له قصرا في الجنة).','336','361','0');
INSERT INTO [t38171] VALUES (406,'٣٩٠ - (قال - صلى الله عليه وسلم - من ألف المسجد).','337','361','0');
INSERT INTO [t38171] VALUES (407,'٣٩١ - (وقال - صلى الله عليه وسلم - لا صلاة).','338','361','0');
INSERT INTO [t38171] VALUES (408,'٣٩٢ - (وقال - صلى الله عليه وسلم - الملائكة تصلي على أحدكم ما دام في مصلاه الذي يصلي فيه).','339','361','0');
INSERT INTO [t38171] VALUES (409,'٣٩٣ - (قال - صلى الله عليه وسلم - يأتى في آخر الزمان ناس من أمتي يأتون المساجد فيقعدون فيها حلقا).','339','361','0');
INSERT INTO [t38171] VALUES (410,'٣٩٤ - (وقال - صلى الله عليه وسلم - قال الله عز وجل في بعض الكتب).','340','361','0');
INSERT INTO [t38171] VALUES (411,'٣٩٥ - (وقال - صلى الله عليه وسلم - إذا رأيتم الرجل يعتاد المسجد فاشهدوا له بالإيمان).','340','361','0');
INSERT INTO [t38171] VALUES (412,'٣٩٦ - مرفوعا إلى رسول الله - صلى الله عليه وسلم - الحديث في المسجد يأكل الحسنات كما تأكل البهائم الحشيش.','341','361','0');
INSERT INTO [t38171] VALUES (413,'٣٩٧ - يبسط الأصابع ولا يقبضها ولا يتكلف فيها تفريجا ولا ضما بل يتركها على مقتضى طبعها إذ نقل في الأثر النشر والضم وهذا بينهما فهو أولى).','341','361','0');
INSERT INTO [t38171] VALUES (414,'٣٩٨ - (في بعض الروايات أنه - صلى الله عليه وسلم - كان إذا كبر أرسل يديه وإذا أراد أن يقرأ وضع اليمنى على اليسرى)','342','361','0');
INSERT INTO [t38171] VALUES (415,'٤٠٠ - قال العراقي: في تخريجه حديث حذف السلام سنة','350','361','0');
INSERT INTO [t38171] VALUES (416,'٤٠١ - (نهى رسول الله - صلى الله عليه وسلم - في الصلاة عن الصفن والصفد)','351','361','0');
INSERT INTO [t38171] VALUES (417,'٤٠٣ - جاء النهي (عن السدل)','352','361','0');
INSERT INTO [t38171] VALUES (418,'٤٠٤ - جاء النهي (عن الكف) في الصلاة','352','361','0');
INSERT INTO [t38171] VALUES (419,'٤٠٥ - جاء النهي (عن الاختصار) في الصلاة','352','361','0');
INSERT INTO [t38171] VALUES (420,'٤٠٦ - جاء النهي (عن الصلب) في الصلاة.','353','361','0');
INSERT INTO [t38171] VALUES (421,'٤٠٧ - جاء النهي (عن المواصلة) في الصلاة.','353','361','0');
INSERT INTO [t38171] VALUES (422,'٤٠٨ - جاء النهي (عن صلاة الحاقن)','353','361','0');
INSERT INTO [t38171] VALUES (423,'٤٠٩ - عن صلاة (الحاقب) بالباء الموحدة.','353','361','0');
INSERT INTO [t38171] VALUES (424,'٤١٠ - عن صلاة (الحازق) بالزاي والقاف.','353','361','0');
INSERT INTO [t38171] VALUES (425,'٤١١ - (وقد يكون الكف في شعر الرأس فلا يصلين)','353','361','0');
INSERT INTO [t38171] VALUES (426,'٤١٢ - (وفي الحديث أمرت أن أسجد على سبعة أعضاء ولا أكف شعرا ولا ثوبا)','354','361','0');
INSERT INTO [t38171] VALUES (427,'٤١٤ - (قوله - صلى الله عليه وسلم - إذا حضر العشاء) بفتح العين أي الطعام الذي يؤكل آخر النهار (وأقيمت الصلاة فابدؤا بالعشاء).','355','361','0');
INSERT INTO [t38171] VALUES (428,'٤١٥ - (في الخبر لا يدخل أحدكم الصلاة وهو مغضب)','356','361','0');
INSERT INTO [t38171] VALUES (429,'٤١٦ - وفي الحديث سبعة أشياء في الصلاة من الشيطان الرعاف والنعاس والوسوسة والتثاؤب والحكاك والالتفات والعبث بالشيء وزاد بعضهم السهو والشك.','356','361','0');
INSERT INTO [t38171] VALUES (430,'٤١٧ - (نهى أيضا عن أن يشبك أصابعه) في الصلاة.','356','361','0');
INSERT INTO [t38171] VALUES (431,'٤١٨ - (أو يفرقع أصابعه)','357','361','0');
INSERT INTO [t38171] VALUES (432,'٤١٩ - (ويستر وجهه)','357','361','0');
INSERT INTO [t38171] VALUES (433,'٤٢١ - (يكره أيضا أن ينفخ في الأرض عند السجود للتطهير)','358','361','0');
INSERT INTO [t38171] VALUES (434,'٤٢٢ - (قال - صلى الله عليه وسلم - كم من قائم حظه من صلاته)','359','361','0');
INSERT INTO [t38171] VALUES (435,'٤٢٣ - (قال - صلى الله عليه وسلم - ليس للعبد من صلاته إلا ما عقل).','359','361','0');
INSERT INTO [t38171] VALUES (436,'٤٢٤ - (المصلى مناج ربه عز وجل كما ورد به الخبر)','360','361','0');
INSERT INTO [t38171] VALUES (437,'٤٢٥ - قال رسول الله - صلى الله عليه وسلم - أن العبد ليصلي الصلاة لا يكتب له سدسها ولا عشرها وإنما يكتب للعبد من صلاته ما عقل منها).','360','361','0');
INSERT INTO [t38171] VALUES (438,'٤٢٦ - (قال النبي - صلى الله عليه وسلم - لعثمان بن شيبة)','361','361','0');
INSERT INTO [t38171] VALUES (439,'٤٢٧ - (كما روي أنه - صلى الله عليه وسلم - لما لبس الخميصة)','362','361','0');
INSERT INTO [t38171] VALUES (440,'٤٢٨ - (أمر رسول الله - صلى الله عليه وسلم - بتجديد شراك نعله)','363','361','0');
INSERT INTO [t38171] VALUES (441,'٤٢٩ - وكان - صلى الله عليه وسلم - قد اتخذ وفي نسخة احتذى (نعلين)','363','361','0');
INSERT INTO [t38171] VALUES (442,'٤٣٠ - (كان - صلى الله عليه وسلم - في يده خاتم ذهب قبل التحريم وكان على المنبر فرماه وقال شغلني هذا نظرة إليه ونظرة إليكم).','364','361','0');
INSERT INTO [t38171] VALUES (443,'٤٣١ - روي أن أبا طلحة زيد بن سهل بن الأسود بن حرام الأنصاري','364','361','0');
INSERT INTO [t38171] VALUES (444,'٤٣٢ - حبها (رأس كل خطيئة وأساس كل نقصان ومنبع كل فساد)','364','361','0');
INSERT INTO [t38171] VALUES (445,'٤٣٣ - (قال - صلى الله عليه وسلم - أرحنا يا بلال).','365','361','0');
INSERT INTO [t38171] VALUES (446,'٤٣٤ - قال - صلى الله عليه وسلم - (إذا قام العبد إلى صلاته فكان هواه).','365','361','0');
INSERT INTO [t38171] VALUES (447,'٤٣٥ - (قال - صلى الله عليه وسلم - تستحي منه كما تستحي من الرجل الصالح من أهلك).','366','361','0');
INSERT INTO [t38171] VALUES (448,'٤٣٦ - (قال عز وجل فيما أخبر عنه نبينا - صلى الله عليه وسلم - لا إله إلا الله حصني فمن دخل حصني أمن من عذابي).','367','361','0');
INSERT INTO [t38171] VALUES (449,'٤٣٧ - قال الله تعالى فيما أخبر عنه النبي - صلى الله عليه وسلم - قسمت الصلاة بيني وبين عبدي نصفين نصفها لي ونصفها لعبدي ولعبدي ما سأل يقول العبد الحمد لله رب العالمين فيقول الله عز وجل','369','361','0');
INSERT INTO [t38171] VALUES (450,'٤٣٨ - (قال النبي - صلى الله عليه وسلم - إن الله يقبل على المصلي ما لم يلتفت).','370','361','0');
INSERT INTO [t38171] VALUES (451,'٤٣٨ - (قال - صلى الله عليه وسلم - للذي أوصاه صل صلاة مودع).','371','361','0');
INSERT INTO [t38171] VALUES (452,'٤٣٩ - في الخبر أن العبد إذا قام إلى الصلاة رفع الله سبحانه الحجاب','371','361','0');
INSERT INTO [t38171] VALUES (453,'٤٤٠ - (ورد جبر نقصان الفرائض بالنوافل)','371','361','0');
INSERT INTO [t38171] VALUES (454,'٤٤١ - (قال النبي - صلى الله عليه وسلم - قال الله عز وجل لا ينجو مني عبدي إلا بإداء ما افترضت عليه).','372','361','0');
INSERT INTO [t38171] VALUES (455,'٤٤٢ - (يروى أن النبي - صلى الله عليه وسلم - صلى صلاة فترك من قراءته)','372','361','0');
INSERT INTO [t38171] VALUES (456,'٤٤٣ - في الحديث ثلاثة لا تجاوز صلاتهم رؤوسهم العبد الآبق وامرأة زوجها ساخط عليها وإمام أم قوما وهم له كارهون.','373','361','0');
INSERT INTO [t38171] VALUES (457,'٤٤٤ - (قوله - صلى الله عليه وسلم - الإمام ضامن والمؤذن مؤتمن).','374','361','0');
INSERT INTO [t38171] VALUES (458,'٤٤٥ - (قال - صلى الله عليه وسلم - الإمام أمير فإذا ركع فاركعوا وإذا سجد فاسجدوا).','374','361','0');
INSERT INTO [t38171] VALUES (459,'٤٤٦ - (وفي الحديث فإن أتم فله ولهم وإن نقص فعليه ولا عليهم)','375','361','0');
INSERT INTO [t38171] VALUES (460,'٤٤٧ - (في الخبر من أذن في مسجد سبع سنين وجبت له الجنة بلا حساب ومن أذن أربعين عاما دخل الجنة بغير حساب).','375','361','0');
INSERT INTO [t38171] VALUES (461,'٤٤٨ - (قوله - صلى الله عليه وسلم - ليوم واحد من ذي سلطان عادل أفضل من عبادة سبعين سنة).','375','361','0');
INSERT INTO [t38171] VALUES (462,'٤٤٩ - (قال - صلى الله عليه وسلم - أئمتكم شفعاؤكم إلى الله أو قال وفدكم إلى الله فإن أردتم أن تزكو) أي تنمو (صلاتكم فقدموا خياركم).','376','361','0');
INSERT INTO [t38171] VALUES (463,'٤٥٠ - (وبهذه الحجة احتج الصحابة)','376','361','0');
INSERT INTO [t38171] VALUES (464,'٤٥١ - (ما قدموا بلالا)','377','361','0');
INSERT INTO [t38171] VALUES (465,'٤٥٢ - (روي أنه - صلى الله عليه وسلم - قال له رجل يا رسول الله دلني على عمل أدخل به الجنة فقال كن مؤذنا فقال لا أستطيع فقال له كن إماما فقال لا أستطيع قال صل بإزاء الإمام)','377','361','0');
INSERT INTO [t38171] VALUES (466,'٤٥٣ - (فضل أول الوقت على آخره كفضل الآخرة على الدنيا)','377','361','0');
INSERT INTO [t38171] VALUES (467,'٤٥٤ - (وفي الحديث أن العبد ليصلي الصلاة ولم تفته ولما فاته من أول وقتها خير له من الدنيا وما فيها)','378','361','0');
INSERT INTO [t38171] VALUES (468,'٤٥٥ - (تأخر رسول الله - صلى الله عليه وسلم - عن صلاة الفجر وكانوا في سفر)','378','361','0');
INSERT INTO [t38171] VALUES (469,'٤٥٦ - (تأخر - صلى الله عليه وسلم - في صلاة الظهر فقدم أبا بكر رضي الله عنه حتى جاء - صلى الله عليه وسلم - وهم في الصلاة فقام إلى جانبه)','378','361','0');
INSERT INTO [t38171] VALUES (470,'٤٥٧ - (قال واتخذ مؤذنا لا يأخذ على الأذان أجر)','379','361','0');
INSERT INTO [t38171] VALUES (471,'٤٥٨ - (في الخبر ليتمهل المؤذن بين الأذان والإقامة بقدر ما يفرغ الآكل من طعامه والمعتصر من اعتصاره) هكذا أورده صاحب القوت','379','361','0');
INSERT INTO [t38171] VALUES (472,'٤٥٩ - مسألة الجهر بالتسمية في الصلاة','380','361','0');
INSERT INTO [t38171] VALUES (473,'(أحاديث الإخفاء)','395','472','0');
INSERT INTO [t38171] VALUES (474,'(فصل) وأما أقوال التابعين في ذلك','400','472','0');
INSERT INTO [t38171] VALUES (475,'٤٦٠ - ذكر العراقي في تخريجه الصغير:','403','361','0');
INSERT INTO [t38171] VALUES (476,'٤٦١ - (روي أنه - صلى الله عليه وسلم - قرأ بعض سورة يونس فلما انتهى إلى ذكر موسى)','403','361','0');
INSERT INTO [t38171] VALUES (477,'٤٦٢ - (روي) أنه - صلى الله عليه وسلم - (قرأ في) الأولى من ركعتي (الفجر أية من)','403','361','0');
INSERT INTO [t38171] VALUES (478,'٤٦٣ - (وسمع) - صلى الله عليه وسلم - (بلالا)','404','361','0');
INSERT INTO [t38171] VALUES (479,'٤٦٤ - (آخر صلاة صلاها رسول الله - صلى الله عليه وسلم - المغرب قرأ فيها بسورة والمرسلات)','404','361','0');
INSERT INTO [t38171] VALUES (480,'٤٦٥ - (كان معاذ بن جبل)','404','361','0');
INSERT INTO [t38171] VALUES (481,'٤٦٦ - روي أيضا','406','361','0');
INSERT INTO [t38171] VALUES (482,'٤٦٧ - (روي مجملا أنهم قالوا كنا نسبح وراء رسول الله - صلى الله عليه وسلم - في الركوع والسجود عشرا عشرا)','406','361','0');
INSERT INTO [t38171] VALUES (483,'٤٦٨ - (روي حديث في رفع اليدين في القنوت فإذا صح الحديث استحب ذلك).','406','361','0');
INSERT INTO [t38171] VALUES (484,'٤٦٩ - (قال - صلى الله عليه وسلم - من ترك الجمعة).','407','361','0');
INSERT INTO [t38171] VALUES (485,'٤٦٩ - (في لفظ آخر فقد نبذ الإسلام وراء ظهره).','408','361','0');
INSERT INTO [t38171] VALUES (486,'٤٧٠ - في الخبر أن أهل الكتابين أي اليهود والنصارى (أعطوا يوم الجمعة فاختلفوا فيه فصرفوا عنه وهدانا الله تعالى له)','408','361','0');
INSERT INTO [t38171] VALUES (487,'٤٧١ - وفي حديث أنس بن مالك رضي الله عنه (عن النبي - صلى الله عليه وسلم - أنه قال أتاني جبريل) عليه السلام (في كفه مرآة)','409','361','0');
INSERT INTO [t38171] VALUES (488,'٤٧٢ - (قال - صلى الله عليه وسلم - خير يوم طلعت عليه وفي رواية فيه الشمس يوم الجمعة','411','361','0');
INSERT INTO [t38171] VALUES (489,'٤٧٣ - في حديث أنس بن مالك رضي الله عنه (عن رسول الله - صلى الله عليه وسلم - قال إذا سلمت الجمعة)','412','361','0');
INSERT INTO [t38171] VALUES (490,'٤٧٤ - (وفي الخبر أن الله عز وجل في كل يوم جمعة ستمائة ألف عتيق من النار)','413','361','0');
INSERT INTO [t38171] VALUES (491,'٤٧٥ - (قال - صلى الله عليه وسلم - إن الجحيم تسعر).','413','361','0');
INSERT INTO [t38171] VALUES (492,'٤٧٦ - (وقال - صلى الله عليه وسلم - من مات يوم الجمعة كتب له أجر شهيد ووقي فتنة القبر)','413','361','0');
INSERT INTO [t38171] VALUES (493,'٤٧٧ - (يستحب أن يجامع أهله)','414','361','0');
INSERT INTO [t38171] VALUES (494,'٤٧٨ - (قال - صلى الله عليه وسلم - غسل الجمعة واجب على كل محتلم)','416','361','0');
INSERT INTO [t38171] VALUES (495,'٤٧٩ - (والمشهور من حديث نافع)','416','361','0');
INSERT INTO [t38171] VALUES (496,'٤٨٠ - (قال - صلى الله عليه وسلم - من شهد الجمعة من الرجال والنساء فليغتسل)','417','361','0');
INSERT INTO [t38171] VALUES (497,'٤٨١ - (قال أمير المؤمنين عمر بن الخطاب لعثمان بن عفان رضي الله عنهما)','417','361','0');
INSERT INTO [t38171] VALUES (498,'٤٨٢ - (روي عن رسول الله - صلى الله عليه وسلم - أنه قال من توضأ يوم الجمعة فبها ونعمت ومن اغتسل فالغسل أفضل)','418','361','0');
INSERT INTO [t38171] VALUES (499,'٤٨٣ - وأحب طيب الرجال ما ظهر ريحه وخفي لونه','419','361','0');
INSERT INTO [t38171] VALUES (500,'٤٨٤ - (رسول الله - صلى الله عليه وسلم - قال إن الله) تعالى (وملائكته يصلون على أصحاب العمائم) أي الذين يلبسون العمائم).','419','361','0');
INSERT INTO [t38171] VALUES (501,'٤٨٥ - (حديث من راح إلى الجمعة فقد قرب بدنه)','420','361','0');
INSERT INTO [t38171] VALUES (502,'٤٨٦ - (قال - صلى الله عليه وسلم - ثلاث).','421','361','0');
INSERT INTO [t38171] VALUES (503,'٤٨٧ - (في الخبر إذا كان يوم الجمعة قعدت الملائكة على أبواب المسجد بأيديهم صحف من فضة وأقلام من ذهب يكتبون الأول فالأول)','421','361','0');
INSERT INTO [t38171] VALUES (504,'٤٨٨ - (وجاء في الآثار أن الملائكة يتفقدون العبد إذا تأخر عن وقته يوم الجمعة فيسأل بعضهم بعضا عنه ما فعل فلان وما الذي أخره عن وقته','422','361','0');
INSERT INTO [t38171] VALUES (505,'٤٨٩ - ورد في الأخبار الصحيحة (وعيد شديد في تخطي الرقاب وهو)','422','361','0');
INSERT INTO [t38171] VALUES (506,'٤٩٠ - (روى ابن جريج)','423','361','0');
INSERT INTO [t38171] VALUES (507,'٤٩١ - (وفي حديث مسند)','424','361','0');
INSERT INTO [t38171] VALUES (508,'٤٩٢ - (قال النبي - صلى الله عليه وسلم - لأن يقف أربعين سنة).','425','361','0');
INSERT INTO [t38171] VALUES (509,'٤٩٣ - (قال - صلى الله عليه وسلم - لأن يكون الرجل رمادا رمديدا).','425','361','0');
INSERT INTO [t38171] VALUES (510,'٤٩٤ - (سوى في حديث آخر بين المار والمصلي حيث صلى على الطريق)','425','361','0');
INSERT INTO [t38171] VALUES (511,'٤٩٥ - (قال - صلى الله عليه وسلم - ليدفعه فإن أبى فليقاتله فإنه شيطان).','426','361','0');
INSERT INTO [t38171] VALUES (512,'٤٩٦ - حديث من دنا واستمع الخطبة.','428','361','0');
INSERT INTO [t38171] VALUES (513,'٤٩٧ - في الخبر من غسل واغتسل وبكر وابتكر ودنا من الإمام واستمع','429','361','0');
INSERT INTO [t38171] VALUES (514,'٤٩٧/ ١ - ونظر سفيان الثوري إلى شعيب بن حرب عند المنبر يستمع','429','513','0');
INSERT INTO [t38171] VALUES (515,'٤٩٨ - قال سعيد بن عامر هو تابعي مجهول','429','361','0');
INSERT INTO [t38171] VALUES (516,'٩٤٩ - يقال الأعمال بالنيات','430','361','0');
INSERT INTO [t38171] VALUES (517,'٥٠٠ - (وقد روي عن علي وعثمان رضي الله عنهما أنهما قالا من استمع)','430','361','0');
INSERT INTO [t38171] VALUES (518,'٥٠١ - (في حديث أبي ذر)','431','361','0');
INSERT INTO [t38171] VALUES (519,'٥٠٢ - (روى ابن عمر)','433','361','0');
INSERT INTO [t38171] VALUES (520,'٥٠٣ - (وروى أبو هريرة)','433','361','0');
INSERT INTO [t38171] VALUES (521,'٥٠٤ - (روى علي وعبد الله بن عباس)','434','361','0');
INSERT INTO [t38171] VALUES (522,'٥٠٥ - النبي - صلى الله عليه وسلم - كان يصلي قبلها أربعا.','435','361','0');
INSERT INTO [t38171] VALUES (523,'٥٠٦ - (قال النبي - صلى الله عليه وسلم - يأتي على الناس زمان يكون حديثهم في مساجدهم في أمر دنياهم ليس لله فيهم حاجة فلا تجالسوهم).','436','361','0');
INSERT INTO [t38171] VALUES (524,'٥٠٧ - (روى عبد الله بن عمر) رضي الله عنهما (أن النبي - صلى الله عليه وسلم - نهى عن التحلق يوم الجمعة قبل الصلاة).','436','361','0');
INSERT INTO [t38171] VALUES (525,'٥٠٨ - الخبر المشهور أن في الجمعة ساعة لا يوافقها عبد مسلم يسأل الله تعالى فيها شيئا إلا أعطاه إياه.','437','361','0');
INSERT INTO [t38171] VALUES (526,'٥٠٩ - (قال - صلى الله عليه وسلم - إن لربكم في أيام دهركم نفحات ألا فتعرضوا لها).','437','361','0');
INSERT INTO [t38171] VALUES (527,'٥١٠ - (وقد قال كعب)','438','361','0');
INSERT INTO [t38171] VALUES (528,'٥١١ - (قال - صلى الله عليه وسلم - من صلى علي في يوم الجمعة ثمانين مرة غفر الله له ذنوب ثمانين سنة قيل يا رسول الله كيف الصلاة عليك قال تقول اللهم صل على محمد عبدك ونبيك ورسولك النبي الآمي وتعقد واحدة)','441','361','0');
INSERT INTO [t38171] VALUES (529,'٥١٢ - (روى ابن عباس وأبو هريرة رضي الله عنهم مرفوعا)','442','361','0');
INSERT INTO [t38171] VALUES (530,'٥١٣ - (كان العابدون)','445','361','0');
INSERT INTO [t38171] VALUES (531,'٥١٤ - (كان يقرأ في صلاة المغرب ليلة الجمعة قل يا أيها الكافرون وقل هو الله أحد وكان يقرأ في صلاة العشاء الأخيرة سورة الجمعة وسورة المنافقين).','446','361','0');
INSERT INTO [t38171] VALUES (532,'٥١٥ - (وروى أنه - صلى الله عليه وسلم - كان يقرؤهما) أي هاتين السورتين الجمعة والمنافقين (في ركعتي الجمعة)','446','361','0');
INSERT INTO [t38171] VALUES (533,'٥١٦ - (وكان يقرأ في الصبح يوم الجمعة بسجدة لقمان وسورة هل أتى على الإنسان)','446','361','0');
INSERT INTO [t38171] VALUES (534,'٥١٧ - (يستحب) للمرء (إذا دخل) المسجد (الجامع أن لا يجلس حتى يصلي أربع ركعات)','447','361','0');
INSERT INTO [t38171] VALUES (535,'٥١٨ - يستحب للداخل في المسجد أن (لا يدع ركعتي التحية وإن كان الإمام يخطب ولكن يخفف أمر رسول الله - صلى الله عليه وسلم - بذلك)','448','361','0');
INSERT INTO [t38171] VALUES (536,'٥١٩ - (في حديث غريب أنه - صلى الله عليه وسلم - سكت للداخل حتى فرغ)','450','361','0');
INSERT INTO [t38171] VALUES (537,'٥٢١ - روي أن من سافر في ليلة الجمعة دعا عليه ملكاه أي كاتب اليمين والشمال.','452','361','0');
INSERT INTO [t38171] VALUES (538,'٥٢٣ - روى أبو هريرة رضي الله عنه (عن النبي - صلى الله عليه وسلم - أنه قال إذا صلى أحدكم)','453','361','0');
INSERT INTO [t38171] VALUES (539,'٥٢٤ - روي عن عبد الله بن السائب بن أبي السائب','453','361','0');
INSERT INTO [t38171] VALUES (540,'٥٢٥ - (قال جبير بن مطعم)','454','361','0');
INSERT INTO [t38171] VALUES (541,'٥٢٦ - (روى بعض الصحابة أنه - صلى الله عليه وسلم - رأى في القبلة نخامة)','455','361','0');
INSERT INTO [t38171] VALUES (542,'٥٢٧ - (قال أما يخشى الذي يرفع رأسه قبل الإمام أن يحول الله رأسه رأس حمار)','457','361','0');
INSERT INTO [t38171] VALUES (543,'٥٢٨ - (قال - صلى الله عليه وسلم - ويل للعالم من الجاهل حيث لا يعلمه).','458','361','0');
INSERT INTO [t38171] VALUES (544,'٥٢٩ - (جاء في الحديث أن بلالا)','458','361','0');
INSERT INTO [t38171] VALUES (545,'٥٣٠ - (من دخل المسجد ينبغي أن يقصد يمين الصف)','459','361','0');
INSERT INTO [t38171] VALUES (546,'٥٣١ - (قال رسول الله - صلى الله عليه وسلم - ركعتا الفجر خير من الدنيا وما فيها)','459','361','0');
INSERT INTO [t38171] VALUES (547,'٥٣٢ - (صلى الله عليه وسلم قال إذا أقيمت الصلاة فلا صلاة إلا المكتوبة).','459','361','0');
INSERT INTO [t38171] VALUES (548,'٣٣٧ - (وكان - صلى الله عليه وسلم - لا يدع أربعا بعد الزوال يطيلهن)','461','361','0');
INSERT INTO [t38171] VALUES (549,'٥٣٥ - (روي عن أم حبيبة)','462','361','0');
INSERT INTO [t38171] VALUES (550,'٥٣٦ - (قال ابن عمر حفظت عن رسول الله - صلى الله عليه وسلم - في كل يوم عشر ركعات).','464','361','0');
INSERT INTO [t38171] VALUES (551,'٥٣٧ - روى أبو هريرة رضي الله عنه (عن رسول الله - صلى الله عليه وسلم - أنه قال رحم الله عبدا صلى أربعا قبل العصر).','465','361','0');
INSERT INTO [t38171] VALUES (552,'٥٣٨ - (كان المؤذن إذا أذن لصلاة المغرب ابتدر أصحاب رسول الله - صلى الله عليه وسلم - السواري)','466','361','0');
INSERT INTO [t38171] VALUES (553,'٥٣٩ - (قال بعضهم كنا نصلي الركعتين قبل المغرب حتى يدخل الداخل فيحسب).','466','361','0');
INSERT INTO [t38171] VALUES (554,'٥٤٠ - (قال - صلى الله عليه وسلم - إذا أقبل الليل).','466','361','0');
INSERT INTO [t38171] VALUES (555,'٥٤١ - (قال - صلى الله عليه وسلم - الصلاة خير موضوع فمن شاء أكثر ومن شاء أقل).','467','361','0');
INSERT INTO [t38171] VALUES (556,'٢٤٢ - (قال أنس بن مالك) رضي الله عنه (كان رسول الله - صلى الله عليه وسلم - يوتر بعد العشاء بثلاث ركعات يقرأ في الأولى بسبح اسم ربك الأعلى وفي الثانية قل يا أيها الكافرون وفي الثالثة قل هو الله أحد).','468','361','0');
INSERT INTO [t38171] VALUES (557,'٥٤٣ - (في خبر أنه - صلى الله عليه وسلم - كان يصلي بعد الوتر جالسا ركعتين)','470','361','0');
INSERT INTO [t38171] VALUES (558,'٥٤٤ - (وفي بعض الأخبار إذا أراد أن يدخل إلى فراشه زحف إليه وصلى فوقه ركعتين قبل أن يرقد يقرأ فيهما إذا زلزلت الأرض زلزالها وسورة ألهاكم)','470','361','0');
INSERT INTO [t38171] VALUES (559,'٥٤٥ - (أوتر رسول الله - صلى الله عليه وسلم - بركعة)','471','361','0');
INSERT INTO [t38171] VALUES (560,'٥٤١ - (الوتر الذي هو خير من حمر النعم كما ورد به الخبر)','472','361','0');
INSERT INTO [t38171] VALUES (561,'٥٤٧ - (روت أم هانئ)','473','361','0');
INSERT INTO [t38171] VALUES (562,'٥٤٨ - (في حديث مفرد أن النبي - صلى الله عليه وسلم - كان يصلي الضحى ست ركعات).','475','361','0');
INSERT INTO [t38171] VALUES (563,'٥٤٩ - إذا انبسطت الشمس وكانت في ربع السماء من جانب الشرق صلى أربعا.','475','361','0');
INSERT INTO [t38171] VALUES (564,'٥٥٠ - (صلى النبي - صلى الله عليه وسلم - بين العشاءين ست ركعات).','476','361','0');
INSERT INTO [t38171] VALUES (565,'٥٥١ - (قد روي عنه - صلى الله عليه وسلم - أنه قال من صلى ما بين المغرب والعشاء فإنها من صلاة الأوابين).','477','361','0');
INSERT INTO [t38171] VALUES (566,'٥٥٢ - وقال - صلى الله عليه وسلم - من عكف نفسه فيما بين المغرب والعشاء في مسجد جماعة لم يتكلم إلا بصلاة أو بقرآن كان حقا على الله أن يبني له قصرين في الجنة مسيرة كل قصر منهما مائة عام ويغرس له بينهما غراسا لو طافه أهل الأرض لوسعهم.','477','361','0');
INSERT INTO [t38171] VALUES (567,'٥٥٣ - يوم الأحد','478','361','0');
INSERT INTO [t38171] VALUES (568,'٥٥٤ - قد روي عن علي رضي الله عنه عن النبي - صلى الله عليه وسلم -','479','361','0');
INSERT INTO [t38171] VALUES (569,'٥٥٥ - يوم الإثنين','480','361','0');
INSERT INTO [t38171] VALUES (570,'٥٥٦ - (وروى أنس بن مالك)','482','361','0');
INSERT INTO [t38171] VALUES (571,'٥٥٧ - (يوم الثلاثاء)','483','361','0');
INSERT INTO [t38171] VALUES (572,'٥٥٨ - (يوم الأربعاء)','483','361','0');
INSERT INTO [t38171] VALUES (573,'٥٥٩ - (يوم الخميس)','484','361','0');
INSERT INTO [t38171] VALUES (574,'٥٦٠ - (يوم الجمعة)','485','361','0');
INSERT INTO [t38171] VALUES (575,'٥٦١ - يوم السبت','487','361','0');
INSERT INTO [t38171] VALUES (576,'٥٦٢ - (ليلة الأحد روى أنس بن مالك)','488','361','0');
INSERT INTO [t38171] VALUES (577,'٥٦٣ - (ليلة الاثنين روى الأعمش)','490','361','0');
INSERT INTO [t38171] VALUES (578,'٥٦٤ - (ليلة الثلاثاء','492','361','0');
INSERT INTO [t38171] VALUES (579,'٥٦٥ - (ليلة الأربعاء','492','361','0');
INSERT INTO [t38171] VALUES (580,'٥٦٦ - (ليلة الخميس قال أبو هريرة)','493','361','0');
INSERT INTO [t38171] VALUES (581,'٥٦٧ - (ليلة الجمعة قال جابر)','494','361','0');
INSERT INTO [t38171] VALUES (582,'٥٦٨ - (وقال - صلى الله عليه وسلم - أكثروا من الصلاة علي في الليلة الغراء واليوم الأزهر ليلة الجمعة ويوم الجمعة)','495','361','0');
INSERT INTO [t38171] VALUES (583,'٥٦٩ - (ليلة السبت قال أنس)','496','361','0');
INSERT INTO [t38171] VALUES (584,'٥٧٠ - يخرج من طريق ويرجع من طريق أخرى هكذا فعل رسول الله - صلى الله عليه وسلم -.','497','361','0');
INSERT INTO [t38171] VALUES (585,'٥٧١ - (كان - صلى الله عليه وسلم - يأمر بإخراج العواتق)','497','361','0');
INSERT INTO [t38171] VALUES (586,'٥٧٢ - (ضحى رسول الله - صلى الله عليه وسلم - بكبشين أملحين وذبح بيده وقال بسم الله والله أكبر هذا عني وعمن لم يضح من أمتي)','498','361','0');
INSERT INTO [t38171] VALUES (587,'٥٧٣ - (وقال - صلى الله عليه وسلم - من رأى هلال ذي الحجة وأراد أن يضحي فلا يأخذن من شعره ولا من أظفاره).','499','361','0');
INSERT INTO [t38171] VALUES (588,'٥٧٤ - (وقال أبو أيوب الأنصاري كان الرجل يضحي على عهد رسول الله - صلى الله عليه وسلم - بالشاة عن أهل بيته فيأكلون ويطعمون).','499','361','0');
INSERT INTO [t38171] VALUES (589,'٥٧٥ - (قال سفيان) بن سعيد (الثوري) رحمه الله تعالى (يستحب أن يصلي بعد عيد الفطر اثنتي عشرة ركعة وبعد عيد الأضحى ستا وقال هو من السنة)','500','361','0');
INSERT INTO [t38171] VALUES (590,'٥٧٦ - قول الرجل لغيره يوم العيد تقبل الله منا ومنك','500','361','0');
INSERT INTO [t38171] VALUES (591,'٥٧٧ - الحديث المسلسل','500','361','0');
INSERT INTO [t38171] VALUES (592,'٥٧٨ - (وخرج رسول الله - صلى الله عليه وسلم - فيها ليلتين أو ثلاثا للجماعة ثم لم يخرج وقال أخاف أن توجب عليكم)','505','361','0');
INSERT INTO [t38171] VALUES (593,'٥٧٩ - (قوله - صلى الله عليه وسلم - فضل صلاة التطوع في بيته على صلاته في المسجد كفضل صلاة المكتوبة في المسجد على صلاته في البيت).','506','361','0');
INSERT INTO [t38171] VALUES (594,'٥٨٠ - (روي أنه - صلى الله عليه وسلم - قال صلاة في مسجدي هذا)','507','361','0');
INSERT INTO [t38171] VALUES (595,'٥٨٢ - (روي عن الحسن أنه قال)','514','361','0');
INSERT INTO [t38171] VALUES (596,'٥٨٣ - (قال رسول الله - صلى الله عليه وسلم - إن الشمس والقمر آيتان من أيات الله لا يخسفان لموت أحد ولا لحياته فإذا رأيتم ذلك فافزعوا إلى ذكر الله والصلاة).','517','361','0');
INSERT INTO [t38171] VALUES (597,'٥٨٤ - لقوله - صلى الله عليه وسلم - (لولا صبيان رضع) جمع راضع (ومشايخ ركع) جمع راكع (وبهائم رتع) جمع راتعة (لصب عليكم البلاء صبا)','519','361','0');
INSERT INTO [t38171] VALUES (598,'٥٨٥ - وليكن الاستغفار معظم الخطبتين وينبغي في وسط الخطبة الثانية أن يستدبر الناس يستقبل القبلة ويحول رداءه في هذه الساعة تفاؤلا بتحويل الحال هكذا فعل رسول الله - صلى الله عليه وسلم -.','520','361','0');
INSERT INTO [t38171] VALUES (599,'٥٨٦ - (قال - صلى الله عليه وسلم - على جنازة فحفظت من دعائه وهو يقول اللهم اغفر لي وله وارحمني وارحمه وعافني وعافه واعف عنه وأكرم نزله ووسع مدخله واغسله بالماء والثلج والبرد ونقه من الخطايا كما ينقي الثوب الأبيض من الدنس وأبدله دارا خيرا من داره وأهلا خيرا من أهله وزوجا خيرا من زوجه وأدخله الجنة وأعذه من عذاب القبر ومن عذاب النار قال عوف)','521','361','0');
INSERT INTO [t38171] VALUES (600,'٥٨٧ - (والأخبار) الصحيحة (الواردة في فضل صلاة الجنازة وتشييعها مشهورة) في الكتب (فلا نطول بإيرادها)','522','361','0');
INSERT INTO [t38171] VALUES (601,'٥٨٨ - (وعن ابن عباس)','523','361','0');
INSERT INTO [t38171] VALUES (602,'٥٨٩ - قال في الروضة لا تكره الصلاة على الميت في المسجد','524','361','0');
INSERT INTO [t38171] VALUES (603,'٥٩١ - (قال - صلى الله عليه وسلم - دخلت الجنة فرأيت بلالا فيها فقلت لبلال بم سبقتني إلى الجنة فقال لا أعرف شيئا إلا أني لا أحدث وضوأ إلا أصلي عقيبه ركعتين)','527','361','0');
INSERT INTO [t38171] VALUES (604,'٥٩٢ - (عن أبي هريرة) رضي الله عنه (قال قال رسول الله - صلى الله عليه وسلم - إذا خرجت)','528','361','0');
INSERT INTO [t38171] VALUES (605,'٥٩٣ - ورد في الأخبار المروية (ركعتان عند الإحرام) بحج أو عمرة (وركعتان عند ابتداء السفر)','529','361','0');
INSERT INTO [t38171] VALUES (606,'٥٩٤ - (قال رسول الله - صلى الله عليه وسلم - كل أمر ذي بال)','531','361','0');
INSERT INTO [t38171] VALUES (607,'٥٩٥ - (رواه جابر بن عبد الله)','532','361','0');
INSERT INTO [t38171] VALUES (608,'٥٩٦ - (روي عن) أبي عثمان ويقال أبو أمية (وهيب بن الورد)','533','361','0');
INSERT INTO [t38171] VALUES (609,'٥٩٧ - حديث صلاة التسبيح','539','361','0');
INSERT INTO [t38171] VALUES (610,'٥٩٨ - (قال رسول الله - صلى الله عليه وسلم - إن الشمس لتطلع ومعها قرن الشيطان).','559','361','0');
INSERT INTO [t38171] VALUES (611,'٥٩٩ - (والإنسان) كما قيل (حريص على ما منع منه)','559','361','0');
INSERT INTO [t38171] VALUES (612,'كتاب الزكاة','561','18','0');
INSERT INTO [t38171] VALUES (613,'٦٠٠ - (قال رسول الله - صلى الله عليه وسلم - لا زكاة في مال حتى يحول عليه الحول).','562','612','0');
INSERT INTO [t38171] VALUES (614,'٦٠١ - حديث كتاب الصدقات','562','612','0');
INSERT INTO [t38171] VALUES (615,'٦٠٢ - (قال رسول الله - صلى الله عليه وسلم - أدوا صدقة الفطر عمن تمونون).','563','612','0');
INSERT INTO [t38171] VALUES (616,'٦٠٣ - (قد قدم رسول الله - صلى الله عليه وسلم - نفقة الولد على نفقة الزوجة ونفقة الزوجة على نفقة الخادم).','565','612','0');
INSERT INTO [t38171] VALUES (617,'٦٠٤ - (ولذلك قال - صلى الله عليه وسلم - في) حال (إحرامه) للحج (لبيك بحجة حقا تعبدا ورقا).','565','612','0');
INSERT INTO [t38171] VALUES (618,'٦٠٥ - (قال رسول الله - صلى الله عليه وسلم - ثلاث مهلكات شح مطاع وهوى متبع وإعجاب المرء بنفسه).','565','612','0');
INSERT INTO [t38171] VALUES (619,'٦٠٦ - (كان - صلى الله عليه وسلم - أجود الخلق في رمضان وكان فيه كالريح المرسلة لا يمسك فيه شيئا).','565','612','0');
INSERT INTO [t38171] VALUES (620,'٦٠٧ - (قال - صلى الله عليه وسلم - أفضل الصدقة جهد المقل إلى فقير في سر).','566','612','0');
INSERT INTO [t38171] VALUES (621,'٦٠٩ - الحديث المشهور سبعة يظلهم الله يوم لا ظل إلا ظله أحدهم رجل تصدق بصدقة فلم تعلم شماله بما أعطت يمينه.','567','612','0');
INSERT INTO [t38171] VALUES (622,'٦١٠ - (وفي الخبر صدقة السر تطفئ غضب الرب).','570','612','0');
INSERT INTO [t38171] VALUES (623,'٦١١ - (جاء أبو بكر رضي الله عنه) إلى رسول الله - صلى الله عليه وسلم - (بجميع ماله وعمر رضي الله عنه بشطر ماله فقال النبي - صلى الله عليه وسلم - ماذا أبقيت لأهلك فقال مثله وقال لأبى بكر ماذا أبقيت لأهلك فقال الله ورسوله).','571','612','0');
INSERT INTO [t38171] VALUES (624,'٦١١/ أ- قال - صلى الله عليه وسلم - لا يقبل الله من مسمع ولا مراء ولا منان بصدقته يطلب السمعة والمعطي في ملأ من الناس يبغي الرياء والإخفاء والسكوت هو المخلص منه','572','623','0');
INSERT INTO [t38171] VALUES (625,'٦١٢ - (قال - صلى الله عليه وسلم - من ألقى جلباب الحياء فلا غيبة له).','572','612','0');
INSERT INTO [t38171] VALUES (626,'٦١٣ - (قال - صلى الله عليه وسلم - لا يقبل الله صدقة منان).','573','612','0');
INSERT INTO [t38171] VALUES (627,'٦١٤ - (قال رسول الله - صلى الله عليه وسلم - إن الصدقة تقع بيد الله تعالى قبل أن تقع في يد السائل).','574','612','0');
INSERT INTO [t38171] VALUES (628,'٦١٥ - (صلحاء الأغنياء يدخلون الجنة بعد الفقراء بخمسمائة عام)','574','612','0');
INSERT INTO [t38171] VALUES (629,'٦١٦ - (وفي الخبر سبق درهم مائة ألف درهم).','575','612','0');
INSERT INTO [t38171] VALUES (630,'٦١٧ - (قال - صلى الله عليه وسلم - لا تأكل إلا طعام تقي ولا يأكل طعامك إلا تقي).','575','612','0');
INSERT INTO [t38171] VALUES (631,'٦١٨ - (وقال - صلى الله عليه وسلم - أطعموا طعامكم الأتقياء وأولوا معروفكم المؤمنين).','575','612','0');
INSERT INTO [t38171] VALUES (632,'٦١٩ - (وفي خبر آخر أضف بطعامك من تحبه في الله تعالى).','575','612','0');
INSERT INTO [t38171] VALUES (633,'٦٢٠ - وقد روي أنه - صلى الله عليه وسلم - بعث معروفا إلى بعض الفقراء وقال للرسول','575','612','0');
INSERT INTO [t38171] VALUES (634,'٦٢١ - (وقال - صلى الله عليه وسلم - لرجل تب فقال أتوب إلى الله ولا أتوب إلى محمد فقال - صلى الله عليه وسلم - عرف الحق لأهله).','576','612','0');
INSERT INTO [t38171] VALUES (635,'٦٢٢ - (ولما نزلت براءة عائشة رضي الله عنها في قصة الإفك)','577','612','0');
INSERT INTO [t38171] VALUES (636,'٦٢٣ - (كان - صلى الله عليه وسلم - يعطي العطاء على قدر العيلة)','577','612','0');
INSERT INTO [t38171] VALUES (637,'٦٢٤ - (قال النبي - صلى الله عليه وسلم - الكسب).','578','612','0');
INSERT INTO [t38171] VALUES (638,'٦٢٥ - (قال - صلى الله عليه وسلم - من لم يشكر الناس لم يشكر الله).','578','612','0');
INSERT INTO [t38171] VALUES (639,'٦٢٦ - (وقد قال - صلى الله عليه وسلم - من أسدى إليكم معروفا فكافئوه فإن لم تستطيعوا فادعوا له حتى تروا أنكم قد كافأتموه).','579','612','0');
INSERT INTO [t38171] VALUES (640,'٦٢٧ - (روى) سهل (ابن الحنظلية)','580','612','0');
INSERT INTO [t38171] VALUES (641,'٦٢٨ - (روى) عبد الله (بن مسعود) رضي الله عنه (أنه - صلى الله عليه وسلم - قال من سأل وله مال يغنيه جاء يوم القيامة وفي وجهه خموش فسئل ما غناه قال خمسون درهما أو قيمتها من الذهب).','580','612','0');
INSERT INTO [t38171] VALUES (642,'٦٢٩ - (رواه عطاء بن يسار)','581','612','0');
INSERT INTO [t38171] VALUES (643,'٦٣٠ - (فقال - صلى الله عليه وسلم - اجعله في قرابتك فهو خير لك).','582','612','0');
INSERT INTO [t38171] VALUES (644,'٦٣١ - (قوله - صلى الله عليه وسلم - تصدقوا ولو بتمرة فإنها تسد من الجائع وتطفئ الخطيئة كلما يطفئ الماء النار).','582','612','0');
INSERT INTO [t38171] VALUES (645,'٦٣٢ - (قال - صلى الله عليه وسلم - اتقوا النار ولو بشق تمرة فإن لم تجدوا فبكلمة طيبة).','582','612','0');
INSERT INTO [t38171] VALUES (646,'٦٣٣ - (قال - صلى الله عليه وسلم - ما من عبد يتصدق بصدقة من كسب طيب ولا يقبل الله إلا طيبا إلا كان الله عز وجل هو يأخذها بيمينه فيربيها له كما يربي أحدكم فصيله أو فلوه) (حتى تبلغ التمرة مثل أحد).','583','612','0');
INSERT INTO [t38171] VALUES (647,'٦٣٤ - (وقال - صلى الله عليه وسلم - لأبي الدرداء)','583','612','0');
INSERT INTO [t38171] VALUES (648,'٦٣٥ - (وقال - صلى الله عليه وسلم - ما أحسن عبد الصدقة إلا أحسن الله الخلافة على تركته).','584','612','0');
INSERT INTO [t38171] VALUES (649,'٦٣٦ - (وقال - صلى الله عليه وسلم - كل امرئ في ظل صدقته يوم القيامة) أي حين تدنو الشمس من الرؤوس (حتى يقضي بين الناس).','584','612','0');
INSERT INTO [t38171] VALUES (650,'٦٣٧ - (وقال - صلى الله عليه وسلم - الصدقة تسد سبعين بابا من الشر) كذا في النسخ وفي بعضها من السوء.','584','612','0');
INSERT INTO [t38171] VALUES (651,'٦٣٨ - (وقال - صلى الله عليه وسلم - صدقة السر تطفئ غضب الرب عز وجل).','585','612','0');
INSERT INTO [t38171] VALUES (652,'٦٣٩ - (وقال - صلى الله عليه وسلم - ما المعطي من سعة بأفضل أجرا من الذي يقبل من حاجة).','585','612','0');
INSERT INTO [t38171] VALUES (653,'٦٤٥ - وسئل رسول الله - صلى الله عليه وسلم - أي الصدقة أفضل قال أن تصدق وأنت صحيح شحيح تأمل البقاء وتخشى الفاقة ولا تمهل حتى إذا بلغت الحلقوم قلت لفلان كذا ولفلان كذا وقد كان لفلان.','586','612','0');
INSERT INTO [t38171] VALUES (654,'٦٤١ - (وقال - صلى الله عليه وسلم - لأصحابه يوما تصدقوا فقال رجل إن عندي دينارا فقال أنفقه على نفسك فقال إن عندي آخر فقال أنفقه على زوجك فقال إن عندي آخر قال أنفقه على ولدك قال إن عندي آخر قال انفقه على خادمك قال إن عندي آخر قال أنت أبصر به).','586','612','0');
INSERT INTO [t38171] VALUES (655,'٦٤٢ - (قال - صلى الله عليه وسلم - لا تحل الصدقة لآل محمد إنما هي أوساخ الناس).','586','612','0');
INSERT INTO [t38171] VALUES (656,'٦٤٣ - (قال - صلى الله عليه وسلم - ردوا مذمة السائل).','588','612','0');
INSERT INTO [t38171] VALUES (657,'٦٤٤ - (وقال - صلى الله عليه وسلم - لو صدق السائل ما أفلح من رده).','588','612','0');
INSERT INTO [t38171] VALUES (658,'٦٤٥ - (وكان نبينا - صلى الله عليه وسلم - لا يكل خصلتين إلى غيره) أي لا يستعين بأحد فيهما (كان يضع طهوره)','589','612','0');
INSERT INTO [t38171] VALUES (659,'٦٤٦ - (قال - صلى الله عليه وسلم - ليس المسكين الذي ترده التمرة والتمرتان واللقمة واللقمتان إنما المسكين المتعفف اقرأوا إن شئتم لا يسألون الناس إلحافا)','589','612','0');
INSERT INTO [t38171] VALUES (660,'٦٤٧ - (وقال - صلى الله عليه وسلم - ما من مسلم يكسو مسلما إلا كان في حفظ الله عز وجل ما دامت عليه منه رقعة)','590','612','0');
INSERT INTO [t38171] VALUES (661,'٦٤٨ - (وقال بعض العلماء ثلاث من كنوز البر منها إخفاء الصدقة وقد روى أيضا مسندا)','590','612','0');
INSERT INTO [t38171] VALUES (662,'٦٤٩ - (وقال عبيد بن عمير)','591','612','0');
INSERT INTO [t38171] VALUES (663,'٦٥٠ - (قال - صلى الله عليه وسلم - من أهدي إليه هدية وعنده قوم فهم شركاؤه فيها)','592','612','0');
INSERT INTO [t38171] VALUES (664,'٦٥١ - (قال - صلى الله عليه وسلم - أفضل ما أهدى الرجل إلى أخيه ورقا أو يطعمه خبزا).','592','612','0');
INSERT INTO [t38171] VALUES (665,'٦٥٢ - (وقال - صلى الله عليه وسلم - إذا أنعم الله على عبد نعمة أحب أن ترى عليه).','593','612','0');
INSERT INTO [t38171] VALUES (666,'٦٥٣ - (لما قالت المهاجرون في الشكر يا رسول الله ما رأينا خيرا من قوم نزلنا عندهم)','593','612','0');
INSERT INTO [t38171] VALUES (667,'٦٥٤ - (ولذا قال - صلى الله عليه وسلم - للرجل الذي مدح بين يديه ضربتم عنقه).','594','612','0');
INSERT INTO [t38171] VALUES (668,'٦٥٥ - (يقال لواحد)','594','612','0');
INSERT INTO [t38171] VALUES (669,'٦٥٦ - (وقال لآخر) ممن يسمع (إذا جاءكم) وفي القوت أتاكم (كريم قوم فأكرموه).','594','612','0');
INSERT INTO [t38171] VALUES (670,'٦٥٧ - (وسمع) - صلى الله عليه وسلم - (كلام رجل) تكلم بكلام فصل (فأعجبه فقال إن من البيان لسحرا).','595','612','0');
INSERT INTO [t38171] VALUES (671,'٦٥٨ - (وقال) - صلى الله عليه وسلم - (إذا علم أحدكم من أخيه خيرا فليخبره فإنه يزداد رغبة في الخير).','595','612','0');
INSERT INTO [t38171] VALUES (672,'٦٥٩ - (وقال) - صلى الله عليه وسلم - (إذا مدح المؤمن ربا) أي زاد (الإيمان في قلبه).','595','612','0');
INSERT INTO [t38171] VALUES (673,'كتاب الصوم','596','18','0');
INSERT INTO [t38171] VALUES (674,'٦٦٠ - (وله - صلى الله عليه وسلم - الصوم نصف الصبر).','597','673','0');
INSERT INTO [t38171] VALUES (675,'٦٦١ - (قوله - صلى الله عليه وسلم - الصبر نصف الإيمان).','597','673','0');
INSERT INTO [t38171] VALUES (676,'٦٦٢ - (قال الله تعالى فيما حكاه عنه نبيه - صلى الله عليه وسلم - كل حسنة بعشر أمثالها إلى سبعمائة ضعف إلا الصيام فإنه لي وأنا أجزي به).','597','673','0');
INSERT INTO [t38171] VALUES (677,'٦٦٣ - (قوله - صلى الله عليه وسلم - والذي نفسي بيده لخلوف فم الصائم أطيب عند الله من ريح المسك يقول) الله (تعالى إنما يذر شهوته وطعامه وشرابه من أجلي والصوم لي وأنا أجزي به).','597','673','0');
INSERT INTO [t38171] VALUES (678,'٦٦٤ - (قال - صلى الله عليه وسلم - للجنة باب يقال له الريان لا يدخله إلا الصائمون).','598','673','0');
INSERT INTO [t38171] VALUES (679,'٦٦٥ - (قال - صلى الله عليه وسلم - للصائم فرحتان فرحة عند الإفطار وفرحة عند لقاء ربه).','598','673','0');
INSERT INTO [t38171] VALUES (680,'٦٦٦ - (قال - صلى الله عليه وسلم - لكل شيء باب وباب العبادة الصوم).','599','673','0');
INSERT INTO [t38171] VALUES (681,'٦٦٧ - (وقال - صلى الله عليه وسلم - نوم الصائم عبادة).','599','673','0');
INSERT INTO [t38171] VALUES (682,'٦٦٨ - (وروى أبو هريرة) رضي الله عنه (إنه - صلى الله عليه وسلم - قال إذا دخل شهر رمضان فتحت أبواب الجنة وغلقت أبواب النار وصفدت الشياطين).','599','673','0');
INSERT INTO [t38171] VALUES (683,'٦٦٩ - (يقال إن الله تعالى يباهي ملائكته بالشاب العابد)','600','673','0');
INSERT INTO [t38171] VALUES (684,'٦٧٠ - (قال - صلى الله عليه وسلم - في الصائم يقول الله تعالى يا ملائكتي انظروا إلى عبدي ترك شهوته وطعامه وشرابه من أجلي).','601','673','0');
INSERT INTO [t38171] VALUES (685,'٦٧١ - (قال - صلى الله عليه وسلم - إن الشيطان ليجري من ابن آدم مجرى الدم فضيقوا مجاريه بالجوع).','601','673','0');
INSERT INTO [t38171] VALUES (686,'٦٧٢ - (قال - صلى الله عليه وسلم - لعائشة رضي الله عنها داومي قرع باب الجنة','601','673','0');
INSERT INTO [t38171] VALUES (687,'٦٧٣ - (قال - صلى الله عليه وسلم - لولا أن الشياطين يحومون على قلوب بني آدم لنظروا إلى ملكوت السماء).','602','673','0');
INSERT INTO [t38171] VALUES (688,'٦٧٥ - (كان إذا دخل العشر الأواخر طوى الفراش وشد المئزر ودأب وأدأب معه أهله).','602','673','0');
INSERT INTO [t38171] VALUES (689,'٦٧٦ - (كان رسول - صلى الله عليه وسلم - لا يخرج) أي من معتكفه (إلا لحاجة الإنسان).','602','673','0');
INSERT INTO [t38171] VALUES (690,'٦٧٧ - وقوله (ولا يسأل عن المريض إلا مارا).','603','673','0');
INSERT INTO [t38171] VALUES (691,'٦٧٩ - (قال - صلى الله عليه وسلم - النظرة سهم مسموم من سهام إبليس فمن تركها خوفا من الله آتاه الله إيمانا يجد حلاوته في قلبه).','605','673','0');
INSERT INTO [t38171] VALUES (692,'٦٨٠ - (وروى جابر عن أنس عن رسول الله - صلى الله عليه وسلم - أنه قال خمس يفطرن الصائم الكذب والغيبة والنميمة واليمين الكاذبة والنظر بشهوة)','605','673','0');
INSERT INTO [t38171] VALUES (693,'٦٨١ - (قد قال رسول الله - صلى الله عليه وسلم - إنما الصوم جنة فإذا كان أحدكم صائما فلا يرفث ولا يجهل فإن امرؤ قاتله أو شاتمه فليقل إني صائم إني صائم).','606','673','0');
INSERT INTO [t38171] VALUES (694,'٦٨٢ - (وجاء في الخبر أن امرأتين صامتا على عهد رسول الله - صلى الله عليه وسلم - فأجهدهما أي أتعبهما الجوع والعطش من آخر النهار حتى كادتا أن تتلفا)','607','673','0');
INSERT INTO [t38171] VALUES (695,'٦٨٣ - (قال - صلى الله عليه وسلم - المغتاب والمستمع شريكان في الإثم)','607','673','0');
INSERT INTO [t38171] VALUES (696,'٦٨٤ - (وقال - صلى الله عليه وسلم - كم من صائم ليس له من صومه إلا الجوع والعطش)','608','673','0');
INSERT INTO [t38171] VALUES (697,'٦٨٥ - (وقد قال - صلى الله عليه وسلم - إنما الصوم أمانة فليحفظ أحدكم أمانته)','608','673','0');
INSERT INTO [t38171] VALUES (698,'٦٨٦ - ولما تلا - صلى الله عليه وسلم - (قوله عز وجل إن الله يأمركم أن تؤدوا الأمانات إلى أهلها وضع يده على سمعه وبصره فقال السمع أمانة والبصر أمانة)','608','673','0');
INSERT INTO [t38171] VALUES (699,'٦٨٧ - (كان رسول الله - صلى الله عليه وسلم - يكثر صوم شعبان حتى كان يظن أنه من رمضان)','608','673','0');
INSERT INTO [t38171] VALUES (700,'٦٨٨ - (وفي الخبر أفضل الصيام بعد شهر رمضان شهر الله','608','673','0');
INSERT INTO [t38171] VALUES (701,'٦٨٩ - (قال - صلى الله عليه وسلم - صوم يوم من شهر حرام أفضل من صوم ثلاثين من غيره وصوم يوم من رمضان أفضل من ثلاثين من شهر حرام)','609','673','0');
INSERT INTO [t38171] VALUES (702,'٦٩٠ - (في الخبر من صام ثلاثة أيام من شهر حرام الخميس والجمعة والسبت كتب الله تعالى له عبادة سبعمائة عام)','609','673','0');
INSERT INTO [t38171] VALUES (703,'٦٩١ - (في الخبر إذا كان النصف من شعبان فلا صوم حتى رمضان)','609','673','0');
INSERT INTO [t38171] VALUES (704,'٦٩٢ - (فإن وصل شعبان برمضان فجائز فعل ذلك رسول الله - صلى الله عليه وسلم - مرة)','611','673','0');
INSERT INTO [t38171] VALUES (705,'٦٩٣ - (وفي الخبر ما من أيام العمل فيهن أفضل وأحب إلى الله من أيام عشر ذي الحجة إن صوم يوم فيه يعدل صيام سنة وقيام ليلة منه يعدل ليلة القدر)','611','673','0');
INSERT INTO [t38171] VALUES (706,'٦٩٤ - (وردت أخبار تدل على كراهة صوم الأبد)','612','673','0');
INSERT INTO [t38171] VALUES (707,'٦٩٥ - (وقد قال النبي - صلى الله عليه وسلم - فيما رواه أبو موسى الأشعري)','612','673','0');
INSERT INTO [t38171] VALUES (708,'٦٩٦ - (قال النبي - صلى الله عليه وسلم - عرضت علي مفاتيح خزائن الدنيا و) مفاتيح (كنوز الأرض فرددتها) أي على الملك الذي جاء بها (وقلت أجوع يوما وأشبع يوما أحمدك إذا شبعت وأتضرع إليك إذا جعت).','613','673','0');
INSERT INTO [t38171] VALUES (709,'٦٩٧ - (وقال - صلى الله عليه وسلم - أفضل الصيام صوم أخي داود كان يصوم يوما ويفطر يوما)','613','673','0');
INSERT INTO [t38171] VALUES (710,'٦٩٨ - (قال - صلى الله عليه وسلم - صم يوما وأفطر يوما فقال إني أريد أفضل من ذلك فقال - صلى الله عليه وسلم - لا أفضل من ذلك).','614','673','0');
INSERT INTO [t38171] VALUES (711,'٦٩٩ - (وقد روى أنه - صلى الله عليه وسلم - ما صام شهرا كاملا قط إلا رمضان).','615','673','0');
INSERT INTO [t38171] VALUES (712,'٧٠٠ - (كان يصوم حتى يقال إنه لا يفطر ويفطر حتى يقال لا يصوم).','616','673','0');
INSERT INTO [t38171] VALUES (713,'كتاب الحج','617','18','0');
INSERT INTO [t38171] VALUES (714,'٧٠١ - (قال - صلى الله عليه وسلم - من مات ولم يحج فليمت إن شاء يهوديا وإن شاء نصرانيا).','618','713','0');
INSERT INTO [t38171] VALUES (715,'٧٠٢ - (قال - صلى الله عليه وسلم - من حج البيت فلم يرفث ولم يفسق خرج من ذنوبه كيوم ولدته أمه).','618','713','0');
INSERT INTO [t38171] VALUES (716,'٧٠٣ - حديث صغار الشيطان يوم عرفة.','619','713','0');
INSERT INTO [t38171] VALUES (717,'٧٠٤ - (يقال إن من الذنوب ذنوبا لا يكفرها إلا الوقوف بعرفة وقد أسنده جعفر بن محمد)','619','713','0');
INSERT INTO [t38171] VALUES (718,'٧٠٥ - (وقال - صلى الله عليه وسلم - من خرج من بيته حاجا أو معتمرا فمات) أي في الطريق (أجري له أجر الحاج المعتمر).','619','713','0');
INSERT INTO [t38171] VALUES (719,'٧٥٦ - (ومن مات في أحد الحرمين لم يعرض ولم يحاسب وقيل له ادخل الجنة).','620','713','0');
INSERT INTO [t38171] VALUES (720,'٧٠٧ - (قال رسول الله - صلى الله عليه وسلم - حجة مبرورة خير من الدنيا وما فيها وحجة مبرورة ليس لها جزاء إلا الجنة)','620','713','0');
INSERT INTO [t38171] VALUES (721,'٧٠٨ - (وقال - صلى الله عليه وسلم - الحجاج والعمار وفد الله تعالى وزواره إن سألوه أعطاهم وإن استغفروه غفر لهم وإن دعوه استجاب لهم وإن شفعوا شفعوا).','620','713','0');
INSERT INTO [t38171] VALUES (722,'٧٠٩ - (وفي حديث مسند من طريق أهل البيت أعظم الناس ذنبا من وقف بعرفة فظن أن الله لم يغفر له).','621','713','0');
INSERT INTO [t38171] VALUES (723,'٧١٠ - (روى ابن عباس) رضي الله عنهما (عن رسول الله - صلى الله عليه وسلم - أنه قال ينزل على هذا البيت في كل يوم مائة وعشرون رحمة ستون للطائفين وأربعون للمصلين وعشرون للناظرين).','621','713','0');
INSERT INTO [t38171] VALUES (724,'٧١١ - (في الخبر استكثروا من الطواف بالبيت فإنه من أجل شيء تجدونه في صحفكم يوم القيامة وأغبط عمل تجدونه)','622','713','0');
INSERT INTO [t38171] VALUES (725,'٧١٢ - (وفي الخبر من طاف أسبوعا حافيا)','623','713','0');
INSERT INTO [t38171] VALUES (726,'٧١٣ - (وقال بعض السلف) ولفظ القوت وزعم بعض السلف (إذا وافق يوم عرفة يوم جمعة غفر لكل أهل عرفة).','624','713','0');
INSERT INTO [t38171] VALUES (727,'٧١٤ - (قوله عز وجل اليوم أكملت لكم دينكم وأتممت عليكم نعمتي ورضيت لكم الإسلام دينا قال أهل الكتاب لو أنزلت هذه الآية علينا لجعلناها يوم عيد فقال عمر رضي الله عنه أشهد لقد أنزلت هذه الآية في يوم عيدين اثنين يوم عرفة ويوم جمعة على رسول الله - صلى الله عليه وسلم - وهو واقف بعرفة).','624','713','0');
INSERT INTO [t38171] VALUES (728,'٧١٥ - (قال - صلى الله عليه وسلم - اغفر للحاج ولمن استغفر له الحاج).','625','713','0');
INSERT INTO [t38171] VALUES (729,'٧١٦ - (روي أن علي بن الموفق)','625','713','0');
INSERT INTO [t38171] VALUES (730,'٧١٧ - (قال رسول الله - صلى الله عليه وسلم - إن الله تعالى قد وعد هذا البيت أن يحجه في كل سنة ستمائة ألف فإن نقصوا).','625','713','0');
INSERT INTO [t38171] VALUES (731,'٧١٨ - (وفي الخبر أن الحجر ياقوتة من يواقيت الجنة وأنه يبعث يوم القيامة وله عينان ولسان ينطق به يشهد لمن استلمه بحق وصدق) هكذا هو في القوت.','625','713','0');
INSERT INTO [t38171] VALUES (732,'٧١٩ - (وكان رسول الله - صلى الله عليه وسلم - يقبله كثيرا) هكذا في القوت.','626','713','0');
INSERT INTO [t38171] VALUES (733,'٧٢٠ - (وروي أنه - صلى الله عليه وسلم - سجد عليه) كذا في القوت بلفظ وروينا أنه سجد عليه.','626','713','0');
INSERT INTO [t38171] VALUES (734,'٧٢١ - وكان - صلى الله عليه وسلم - (يطوف على الراحلة فيضع المحجن عليه ثم يقبل المحجن)','627','713','0');
INSERT INTO [t38171] VALUES (735,'٧٢٢ - (ثم بكى حتى علا نشيجه) أي صوته (فالتفت إلى ورائه فرأى عليا كرم الله وجهه فقال يا أبا الحسن هاهنا تسكب العبرات)','627','713','0');
INSERT INTO [t38171] VALUES (736,'٧٢٣ - (وقبله عمر رضي الله عنه ثم قال والله إني لا أعلم أنك حجر لا تضر ولا تنفع ولولا أني رأيت رسول الله - صلى الله عليه وسلم - يقبلك لما قبلتك)','627','713','0');
INSERT INTO [t38171] VALUES (737,'٧٢٤ - (في الخبر الصحيح عن النبي - صلى الله عليه وسلم - عمرة في رمضان كحجة)','628','713','0');
INSERT INTO [t38171] VALUES (738,'٧٢٥ - (وقال - صلى الله عليه وسلم - أنا أول من تنشق عنه الأرض ثم آتى أهل البقيع فيحشرون معي ثم آتي أهل مكة فأحشر بين الحرمين)','629','713','0');
INSERT INTO [t38171] VALUES (739,'٧٢٥/ أ- (وفي الخبر أن آدم عليه السلام لما قضى مناسكه لقيته الملائكة فقالوا له بر حجك يا آدم لقد حججنا هذا البيت قبلك بألفي عام) هكذا أورده صاحب القوت.','629','738','0');
INSERT INTO [t38171] VALUES (740,'٧٢٦ - (وفي الخبر استكثروا من الطواف بهذا البيت قبل أن يرفع فقد هدم مرتين ويرفع في الثالثة)','630','713','0');
INSERT INTO [t38171] VALUES (741,'٧٢٧ - (يروى عن علي رضي الله عنه عن النبي - صلى الله عليه وسلم - أنه قال قال الله تعالى إني إذا أردت أن أخرب الدنيا بدأت ببيتي فخربته ثم أخرب الدنيا على أثره)','630','713','0');
INSERT INTO [t38171] VALUES (742,'٧٢٨ - (ولما عاد رسول الله - صلى الله عليه وسلم - إلى مكة استقبل الكعبة وقال إنك لخير أرض الله وأحب بلاد الله إلي ولولا إني أخرجت منك لما خرجت)','630','713','0');
INSERT INTO [t38171] VALUES (743,'٧٢٩ - (قال رسول الله - صلى الله عليه وسلم - صلاة في مسجدي هذا خير من ألف صلاة فيما سواه)','631','713','0');
INSERT INTO [t38171] VALUES (744,'٧٣٠ - (روي عن ابن عباس رضي الله عنهما عن النبي - صلى الله عليه وسلم - أنه قال صلاة في مسجد المدينة بعشرة آلاف صلاة وصلاة في المسجد الأقصى بألف صلاة وصلاة في المسجد الحرام بمائة ألف صلاة)','632','713','0');
INSERT INTO [t38171] VALUES (745,'٧٣١ - (وقال رسول الله - صلى الله عليه وسلم - لا يصبر على شدتها ولأوائها أحد إلا كنت له شفيعا يوم القيامة)','633','713','0');
INSERT INTO [t38171] VALUES (746,'٧٣٢ - (وقال - صلى الله عليه وسلم - من استطاع أن يموت بالمدينة)','633','713','0');
INSERT INTO [t38171] VALUES (747,'٧٣٣ - حديث لا تشد الرحال إلا إلى ثلاثة مساجد.','634','713','0');
INSERT INTO [t38171] VALUES (748,'٧٣٤ - (قال - صلى الله عليه وسلم - البلاد بلاد الله عز وجل والخلق عباده فأي موضع رأيت فيه رفقا فأقم واحمد الله تعالى).','634','713','0');
INSERT INTO [t38171] VALUES (749,'٧٣٥ - (وفي الخبر) المرفوع (من بورك له في شيء فليلزمه)','635','713','0');
INSERT INTO [t38171] VALUES (750,'٧٣٦ - (من جعلت معيشته في شيء فلا ينتقل عنه حتى يتغير عليه).','635','713','0');
INSERT INTO [t38171] VALUES (751,'٧٣٧ - (السنة في الوداع أن يقول أستودع الله دينكم وأمانتكم وخواتم أعمالكم)','635','713','0');
INSERT INTO [t38171] VALUES (752,'٧٣٨ - (كان النبي - صلى الله عليه وسلم - يقول لمن أراد السفر في حفظ الله وكنفه زودك الله التقوى وغفر ذنبك ووجهك للخير أينما توجهت).','636','713','0');
INSERT INTO [t38171] VALUES (753,'٧٣٩ - أخرج الطبراني في الدعاء من حديث أنس رفعه من قال إذا خرج من بيته بسم الله توكلت على الله لا حول ولا قوة إلا بالله','636','713','0');
INSERT INTO [t38171] VALUES (754,'٧٤٠ - قال النسائي حدثنا سليمان بن عبيد الله عن بهز بن أسد عن شعبة عن منصور بن المعتمر قال سمعت الشعبي يحدث عن أم سلمة قالت كان رسول الله - صلى الله عليه وسلم - يقول إذا خرج من بيته اللهم إني أعوذ بك من أن أضل أو أزل أو أظلم أو أجهل أو يجهل علي','637','713','0');
INSERT INTO [t38171] VALUES (755,'٧٤٣ - (قال - صلى الله عليه وسلم - عليكم بالدلجة فإن الأرض تطوى بالليل ما لا تطوى بالنهار).','640','713','0');
INSERT INTO [t38171] VALUES (756,'٧٤٤ - قال الطبراني في الدعاء حدثنا القاسم بن عباد حدثنا سويد بن سعيد حدثنا حفص بن ميسرة عن موسى بن عتبة عن عطاء بن أبي مروان عن أبيه أن كعبا حلف بالله الذي فلق البحر لموسى عليه السلام أن صهيبا رضي الله عنه','640','713','0');
INSERT INTO [t38171] VALUES (757,'٧٤٥ - (فإذا نزلت المنزل فصل فيه ركعتين)','642','713','0');
INSERT INTO [t38171] VALUES (758,'٧٤٦ - (فإذا جن عليه الليل فليقل يا أرض ربي وربك الله أعوذ بالله من شرك وشر ما فيك وشر ما دب عليك أعوذ بالله من شر كل أسد وأسود وحية وعقرب ومن ساكن البلد ووالد وما ولد وله ما سكن في الليل والنهار وهو السميع العليم)','644','713','0');
INSERT INTO [t38171] VALUES (759,'٧٤٧ - (فإن نام في أول الليل افترش ذراعه وإن نام في آخر الليل نصب ذراعه وجعل رأسه في كفه هكذا كان ينام رسول الله - صلى الله عليه وسلم - في أسفاره)','644','713','0');
INSERT INTO [t38171] VALUES (760,'٧٤٨ - (والأحب بالليل أن يتناوب الرفيقان في الحراسة فإذا نام أحدهما حرس الآخر وذلك هو السنة)','645','713','0');
INSERT INTO [t38171] VALUES (761,'٧٤٩ - الصحيح من حديث ابن عمر قال كان النبي - صلى الله عليه وسلم - إذا قفل من الحج أو العمرة كلما أوفى على فدفد أو ثنية كبر ثلاث تكبيرات','645','713','0');
INSERT INTO [t38171] VALUES (762,'٧٥٠ - (ومهما خاف الوحشة في سفره قال سبحان الملك القدوس رب الملائكة والروح جللت السماوات بالعزة والجبروت)','646','713','0');
INSERT INTO [t38171] VALUES (763,'٧٥١ - (رؤى وبيص المسك) أي بريقه (على مفرق رسول الله - صلى الله عليه وسلم - بعد الإحرام مما كان استعمله قبل الإحرام)','646','713','0');
INSERT INTO [t38171] VALUES (764,'٧٥٢ - (فإنه لا ينادي أصم ولا غائبا كما ورد في الخبر)','646','713','0');
INSERT INTO [t38171] VALUES (765,'٧٥٣ - (كان رسول الله - صلى الله عليه وسلم - إذا أعجبه شيء قال لبيك إن العيش عيش الآخرة).','647','713','0');
INSERT INTO [t38171] VALUES (766,'٧٥٤ - ومما يدعي به (اللهم أنت السلام ومنك السلام ودارك دار السلام تباركت يا ذا الجلال والاكرام)','648','713','0');
INSERT INTO [t38171] VALUES (767,'٧٥٥ - من الأدعية الماثورة (اللهم إن هذا بيتك عظمته وكرمته وشرفته','648','713','0');
INSERT INTO [t38171] VALUES (768,'٧٥٦ - (يقول قبل مجاوزة الحجر بل في ابتداء الطواف بسم الله والله أكبر اللهم وإيمانا بك وتصديقا بكتابك ووفاء بعهدك واتباعا','649','713','0');
INSERT INTO [t38171] VALUES (769,'٧٥٧ - (يبلغ الركن العراقي فعنده يقول اللهم إني أعوذ بك من الشرك والشك والكفر والنفاق والشقاق وسوء الأخلاق وسوء المنقلب في الأهل والمال والولد)','650','713','0');
INSERT INTO [t38171] VALUES (770,'٧٥٨ - (ليقل بين الركن اليماني والحجر الأسود اللهم ربنا آتنا في الدنيا حسنة وفي الآخرة حسنة وقنا برحمتك عذاب القبر وعذاب النار)','651','713','0');
INSERT INTO [t38171] VALUES (771,'٧٥٩ - (كان يستلم اليماني ويقبله ويضع خده عليه)','652','713','0');
INSERT INTO [t38171] VALUES (772,'٧٦٠ - قال محمد بن شهاب (الزهري مضت السنة أن يصلي لكل أسبوع ركعتين).','652','713','0');
INSERT INTO [t38171] VALUES (773,'٧٦١ - (وإن قرن بين أسابيع)','652','713','0');
INSERT INTO [t38171] VALUES (774,'٧٦٢ - (قال رسول الله - صلى الله عليه وسلم - من طاف بالبيت أسبوعا وصلى ركعتين فله من الأجر كعتق رقبة).','653','713','0');
INSERT INTO [t38171] VALUES (775,'٧٦٣ - (رقى رسول الله - صلى الله عليه وسلم - فيه حتى بدت له الكعبة).','654','713','0');
INSERT INTO [t38171] VALUES (776,'٧٦٤ - (اللهم إنك تسمع كلامي وترى مكاني وتعلم سري وعلانيتي ولا يخفى عليك شيء من أمري أنا البائس الفقير المستغيث المستجير الوجل المشفق المعترف بذنبه أسألك مسألة المسكين وأبتهل إليك ابتهال المذنب الذليل وأدعوك دعاء الخائف الضرير)','654','713','0');
INSERT INTO [t38171] VALUES (777,'٧٦٥ - (قال اتقوا الله وسيروا سيرا جميلا لا تطؤا ضعيفا ولا تؤذوا مسلما).','655','713','0');
INSERT INTO [t38171] VALUES (778,'٧٦٦ - (قال - صلى الله عليه وسلم - خير الأضحية الكبش الأقرن).','655','713','0');
INSERT INTO [t38171] VALUES (779,'٧٦٧ - (لا يبيت إلا بمنى كان رسول الله - صلى الله عليه وسلم - يفعل ذلك)','655','713','0');
INSERT INTO [t38171] VALUES (780,'٧٦٨ - (والنظر إلى البيت عبادة)','656','713','0');
INSERT INTO [t38171] VALUES (781,'٧٦٩ - (قال النبي - صلى الله عليه وسلم - ماء زمزم لما شرب له).','656','713','0');
INSERT INTO [t38171] VALUES (782,'٧٧٠ - (قال رسول الله - صلى الله عليه وسلم - من زارني بعد وفاتي فكأنما زارني في حياتي).','658','713','0');
INSERT INTO [t38171] VALUES (783,'٧٧١ - (وقال - صلى الله عليه وسلم - من وجد سعة ولم يغد إلي فقد جفاني).','658','713','0');
INSERT INTO [t38171] VALUES (784,'٧٧٢ - (وقال - صلى الله عليه وسلم - من جاءني زائرا لا يهمه إلا زيارتي كان حقا علي أن أكون له شفيعا).','659','713','0');
INSERT INTO [t38171] VALUES (785,'٧٧٣ - (قول رسول الله - صلى الله عليه وسلم - ما بين قبري ومنبري روضة من رياض الجنة وقوله - صلى الله عليه وسلم - منبري على حوضي).','660','713','0');
INSERT INTO [t38171] VALUES (786,'٧٧٤ - (روي أن رسول الله - صلى الله عليه وسلم - قال من خرج من بيته حتى يأتي مسجد قباء ويصلي فيه كان عدل عمرة).','660','713','0');
INSERT INTO [t38171] VALUES (787,'٧٧٥ - (ويقال إن النبي - صلى الله عليه وسلم - تفل فيها من ريقه).','662','713','0');
INSERT INTO [t38171] VALUES (788,'٧٧٦ - (كان رسول الله - صلى الله عليه وسلم - إذا قفل)','662','713','0');
INSERT INTO [t38171] VALUES (789,'٧٧٨ - (قول رسول الله - صلى الله عليه وسلم - يدخل الله سبحانه بالحجة الواحدة ثلاثة الجنة الموصي بها والمنفذ لها ومن حج عن أخيه)','663','713','0');
INSERT INTO [t38171] VALUES (790,'٧٧٩ - وفي الخبر مثل الذي يغزو في سبيل الله عز وجل ويأخذ أجرا','663','713','0');
INSERT INTO [t38171] VALUES (791,'٧٨٠ - (وقال رسول الله - صلى الله عليه وسلم - الحج المبرور ليس له جزاء إلا الجنة)','664','713','0');
INSERT INTO [t38171] VALUES (792,'٧٨١ - (حج رسول الله - صلى الله عليه وسلم - على راحلة وكان تحته رحل رث وقطيفة خلقة قيمتها أربعة دراهم)','664','713','0');
INSERT INTO [t38171] VALUES (793,'٧٨٢ - (قال - صلى الله عليه وسلم - خذوا عني مناسككم)','664','713','0');
INSERT INTO [t38171] VALUES (794,'٧٨٣ - (وأمر - صلى الله عليه وسلم - بالشعث والاحتفاء)','664','713','0');
INSERT INTO [t38171] VALUES (795,'٧٨٤ - (نهى عن التنعم والرفاهية في حديث فضالة بن عبيد)','665','713','0');
INSERT INTO [t38171] VALUES (796,'٧٨٥ - (في الخبر إنما الحاج الشعث التفل)','665','713','0');
INSERT INTO [t38171] VALUES (797,'٧٨٦ - (يقول الله عز وجل) لملائكته (انظروا إلى زوار بيتي فقد جاءوني شعثا غبرا من كل فج عميق)','665','713','0');
INSERT INTO [t38171] VALUES (798,'٧٨٧ - (روي أنه - صلى الله عليه وسلم - كان في سفر فنزل أصحابه منزلا)','665','713','0');
INSERT INTO [t38171] VALUES (799,'٧٨٨ - (قال النبي - صلى الله عليه وسلم - لا تتخذوا ظهور دوابكم كراسي)','666','713','0');
INSERT INTO [t38171] VALUES (800,'٧٨٩ - (ويستحب أن ينزل عن دابته غدوة وعشية ويروحهما بذلك فهو سنة)','666','713','0');
INSERT INTO [t38171] VALUES (801,'٧٩٠ - (روى ابن عمر أن عمر رضي الله عنهما أهدى نجيبة)','666','713','0');
INSERT INTO [t38171] VALUES (802,'٧٩١ - (وسئل رسول الله - صلى الله عليه وسلم - ما بر الحج فقال العج والثج)','666','713','0');
INSERT INTO [t38171] VALUES (803,'٧٩٢ - (روت عائشة رضي الله عنها أن رسول الله - صلى الله عليه وسلم - قال ما عمل ابن آدم يوم النحر أفضل من إهراق دم)','667','713','0');
INSERT INTO [t38171] VALUES (804,'٧٩٣ - (وفي الخبر لكم بكل صوفة من جلدها حسنة وبكل قطرة من دمها حسنة وإنها لتوضع في الميزان فأبشروا) كذا في القوت','667','713','0');
INSERT INTO [t38171] VALUES (805,'٧٩٤ - (سأله أهل الملل) ممن أسلم منهم (عن السياحة)','668','713','0');
INSERT INTO [t38171] VALUES (806,'٧٩٥ - (وسئل - صلى الله عليه وسلم - عن) معنى (السائحين) في الآية (فقال هم الصائمون)','668','713','0');
INSERT INTO [t38171] VALUES (807,'٧٩٦ - قوله بلغني أن من حج ألخ','669','713','0');
INSERT INTO [t38171] VALUES (808,'٧٩٧ - (من تشبه بقوم فهو منهم)','669','713','0');
INSERT INTO [t38171] VALUES (809,'٧٩٨ - (وقد روى ابن عباس عن النبي - صلى الله عليه وسلم - أنه قال الحجر الأسود يمين الله عز وجل في الأرض يصافح بها كما صافح الرجل أخاه)','669','713','0');
INSERT INTO [t38171] VALUES (810,'٧٩٩ - (فليكمل الهدى وأجزاءه وليرج) من الله (أن يعتق بكل جزء منه جزأ من أجزائه من النار فهكذا ورد الوعد)','670','713','0');
INSERT INTO [t38171] VALUES (811,'٨٠٠ - (قال النبي - صلى الله عليه وسلم - يرفع إلي أقوام فيقولون يا محمد يا محمد فأقول يا رب أصحابي فيقول إنك لا تدري ما أحدثوا بعدك فأقول بعدا وسحقا).','670','713','0');
INSERT INTO [t38171] VALUES (812,'٨٠١ - (روي عنه - صلى الله عليه وسلم - أن الله تعالى وكل بقبره ملكا يبلغه سلام من سلم عليه من أمته)','670','713','0');
INSERT INTO [t38171] VALUES (813,'٨٠٢ - (قال - صلى الله عليه وسلم - ومن صلى علي مرة صلى الله عليه عشرا)','671','713','0');
INSERT INTO [t38171] VALUES (814,'كتاب آداب تلاوة القرآن','672','18','0');
INSERT INTO [t38171] VALUES (815,'٨٠٣ - (قال رسول الله - صلى الله عليه وسلم - من قرأ القرآن ثم رأى أن أحدا أوتي أفضل مما أوتي فقد استصغر ما عظمه الله تعالى).','673','814','0');
INSERT INTO [t38171] VALUES (816,'٨٠٤ - (وقال - صلى الله عليه وسلم - ما من شفيع أفضل منزلة عند الله يوم القيامة من القرآن لا نبي ولا ملك ولا غيره).','673','814','0');
INSERT INTO [t38171] VALUES (817,'٨٠٥ - (قال - صلى الله عليه وسلم - لو كان القرآن في إهاب ما مسته النار).','673','814','0');
INSERT INTO [t38171] VALUES (818,'٨٠٦ - (وقال - صلى الله عليه وسلم - أفضل عبادة أمتي تلاوة القرآن). (وقال - صلى الله عليه وسلم -','673','814','0');
INSERT INTO [t38171] VALUES (819,'٨٠٧ - (وقال - صلى الله عليه وسلم - إن الله عز وجل قرأ طه ويس قبل أن يخلق الخلق بألف عام فلما سمعت الملائكة القرآن قالت طوبى لأمة ينزل عليهم هذا وطوبى لأجواف تحمل هذا وطوبى لألسنة تنطق بهذا).','674','814','0');
INSERT INTO [t38171] VALUES (820,'٨٠٨ - (قال - صلى الله عليه وسلم - خيركم من تعلم القرآن وعلمه).','674','814','0');
INSERT INTO [t38171] VALUES (821,'٨٠٩ - (قال - صلى الله عليه وسلم - يقول الله تبارك وتعالى من شغله قراءة القرآن عن دعائي ومسألتي أعطيته أفضل ثواب الشاكرين).','675','814','0');
INSERT INTO [t38171] VALUES (822,'٨١٠ - (وقال - صلى الله عليه وسلم - أهل القرآن) هم (أهل الله وخاصته).','676','814','0');
INSERT INTO [t38171] VALUES (823,'٨١١ - (وقال - صلى الله عليه وسلم - إن هذه القلوب تصدأ كما يصدأ الحديد فقيل يا رسول الله ما جلاؤها قال تلاوة القرآن وذكر الموت).','676','814','0');
INSERT INTO [t38171] VALUES (824,'٨١٢ - (وقال - صلى الله عليه وسلم - لله أشد أذنا) بالتحريك أي استماعا وإصغاءا (إلى قارئ القرآن من صاحب القينة إلى قينته) هي أمته المغنية.','676','814','0');
INSERT INTO [t38171] VALUES (825,'٨١٣ - (قال) ابن مسعود (أيضا اقرءوا القرآن) أي لازموا على قراءته (فإنكم تؤجرون عليه بكل حرف منه عشر حسنات أما إني لا أقول ألم حرف ولكن أقول ألف حرف واللام حرف والميم حرف)','677','814','0');
INSERT INTO [t38171] VALUES (826,'٨١٤ - (وقال عمرو بن العاص) رضي الله عنه (كل آية في القرآن درجة)','677','814','0');
INSERT INTO [t38171] VALUES (827,'٨١٥ - (وقال أيضا من قرأ القرآن فقد أدرجت النبوة بين جنبيه إلا أنه لا يوحي إليه)','677','814','0');
INSERT INTO [t38171] VALUES (828,'٨١٦ - (قال أيضا حامل القرآن حامل راية الإسلام)','678','814','0');
INSERT INTO [t38171] VALUES (829,'٨١٧ - (يروى أن خالد بن عقبة) بن أبي معيط (جاء إلى رسول الله - صلى الله عليه وسلم - فقال اقرأ علي القرآن)','678','814','0');
INSERT INTO [t38171] VALUES (830,'٨١٨ - (وقال - صلى الله عليه وسلم - أكثر منافقي هذه الأمة قراؤها).','679','814','0');
INSERT INTO [t38171] VALUES (831,'٨١٩ - (وقال - صلى الله عليه وسلم - اقرأ القرآن ما نهاك فإن لم ينهك فلست تقرؤه).','679','814','0');
INSERT INTO [t38171] VALUES (832,'٨٢٠ - (وقال - صلى الله عليه وسلم - ما آمن بالقرآن من استحل محارمه).','680','814','0');
INSERT INTO [t38171] VALUES (833,'٨٢١ - حديث أبي ذر (جندب) بن جنادة الغفار رضي الله عنهم قالا (لقد عشنا دهرا)','680','814','0');
INSERT INTO [t38171] VALUES (834,'٨٢٢ - (قول رسول الله - صلى الله عليه وسلم - من قرأ القرآن في أقل من ثلاث لم يفقه).','680','814','0');
INSERT INTO [t38171] VALUES (835,'٨٢٣ - (قالت عائشة رضي الله عنها لما سمعت رجلا يهذر القرآن هذرا إن هذا ما قرأ القرآن ولا سكت).','682','814','0');
INSERT INTO [t38171] VALUES (836,'٨٢٤ - (وأمر النبي - صلى الله عليه وسلم - عبد الله بن عمرو) بن العاص (رضي الله عنهما أن يختم القرآن في كل سبع).','682','814','0');
INSERT INTO [t38171] VALUES (837,'٨٢٥ - (نعتت أم سلمة رضي الله عنها قراءة رسول الله - صلى الله عليه وسلم - فإذا هي تنعت قراءته مفسرة حرفا حرفا).','683','814','0');
INSERT INTO [t38171] VALUES (838,'٨٢٦ - (قال رسول الله - صلى الله عليه وسلم - اتلوا القرآن وابكوا فإن لم تبكوا فتباكوا).','683','814','0');
INSERT INTO [t38171] VALUES (839,'٨٢٧ - (قال - صلى الله عليه وسلم - ليس منا من لم يتغن بالقرآن).','684','814','0');
INSERT INTO [t38171] VALUES (840,'٨٢٨ - (قال النبي - صلى الله عليه وسلم - إن القرآن نزل بحزن فإذا قرأتموه فتحازنوا).','684','814','0');
INSERT INTO [t38171] VALUES (841,'٨٢٩ - (كان لا يمر بآية عذاب إلا استعاذ ولا باآية رحمة إلا سأل ولا باآية تنزيه إلا سبح)','685','814','0');
INSERT INTO [t38171] VALUES (842,'٨٣٠ - (روي عن رسول الله - صلى الله عليه وسلم - أنه قال فضل قراءة السر على قراءة العلانية كفضل صدقة السر على صدقة العلانية)','686','814','0');
INSERT INTO [t38171] VALUES (843,'٨٣١ - (وفي الخبر العام يفضل عمل السر على عمل العلانية بسبعين ضعفا) هكذا في القوت','687','814','0');
INSERT INTO [t38171] VALUES (844,'٨٣٢ - (قوله - صلى الله عليه وسلم - خير الرزق ما يكفي وخير الذكر الخفي)','687','814','0');
INSERT INTO [t38171] VALUES (845,'٨٣٣ - (في القراءة بين المغرب والعشاء)','687','814','0');
INSERT INTO [t38171] VALUES (846,'٨٣٤ - (روي عن رسول الله - صلى الله عليه وسلم - أنه سمع جماعة من الصحابة يجهرون بالقراءة في صلاة الليل فصوب ذلك)','688','814','0');
INSERT INTO [t38171] VALUES (847,'٨٣٥ - (قال - صلى الله عليه وسلم - إذا قام أحدكم من الليل يصلي فليجهر بقراءته فإن الملائكة وعمار الدار يستمعون إلى قراءته ويصلون بصلاته)','688','814','0');
INSERT INTO [t38171] VALUES (848,'٨٣٦ - (ومر رسول الله - صلى الله عليه وسلم - على ثلاثة من أصحابه مختلفي الأحوال)','688','814','0');
INSERT INTO [t38171] VALUES (849,'٨٣٧ - (النظر في المصحف أيضا عبادة)','689','814','0');
INSERT INTO [t38171] VALUES (850,'٨٣٨ - (قال - صلى الله عليه وسلم - زينوا القرآن بأصواتكم)','690','814','0');
INSERT INTO [t38171] VALUES (851,'٨٣٩ - (وقال - صلى الله عليه وسلم - ما أذن الله تعالى)','691','814','0');
INSERT INTO [t38171] VALUES (852,'٨٤٠ - روي أن رسول الله - صلى الله عليه وسلم - كان ليلة ينتظر عائشة رضي الله عنها فأبطأت عليه فقال لها رسول الله - صلى الله عليه وسلم - ما حبسك فقالت يا رسول الله كنت أسمع قراءة رجل ما سمعت أحسن صوتا منه فقام - صلى الله عليه وسلم - حتى أستمع إليه طويلا ثم رجع فقال هذا سالم مولى أبي حذيفة (الحمد لله الذي جعل في أمتي مثله)','692','814','0');
INSERT INTO [t38171] VALUES (853,'٨٤١ - (واستمع) - صلى الله عليه وسلم - (أيضا ذات ليلة إلى عبد الله بن مسعود) رضي الله عنه وهو يقرأ (ومعه أبو بكر وعمر رضي الله عنهما فوقفوا طويلا ثم قال من أراد أن يقرأ القرآن غضا كما أنزل فليقرأه على قراءة ابن أم عبد).','693','814','0');
INSERT INTO [t38171] VALUES (854,'٨٤٢ - (قال - صلى الله عليه وسلم -) ذات يوم (لابن مسعود اقرأ علي فقال يا رسول الله اقرأ عليك وعليك أنزل فقال إني أحب أن أسمعه من غيري فكان يقرأ وعينا رسول الله - صلى الله عليه وسلم - تفيضان)','693','814','0');
INSERT INTO [t38171] VALUES (855,'٨٤٣ - (واستمع - صلى الله عليه وسلم - إلى قراءة أبي موسى)','694','814','0');
INSERT INTO [t38171] VALUES (856,'٨٤٤ - (قال - صلى الله عليه وسلم - من استمع إلى آية) أي أصغى إلى قراءة آية (من كتاب الله)','695','814','0');
INSERT INTO [t38171] VALUES (857,'٨٤٥ - (روي أن رسول الله - صلى الله عليه وسلم - قرأ بسم الله الرحمن الرحيم فرددها عشرين مرة)','696','814','0');
INSERT INTO [t38171] VALUES (858,'٨٤٦ - (عن أبي ذر قام رسول الله - صلى الله عليه وسلم - بنا ليلة فقام بآية يرددها وهي إن تعذبهم فإنهم عبادك وإن تغفر لهم فإنك أنت العزيز الحكيم).','696','814','0');
INSERT INTO [t38171] VALUES (859,'٨٤٧ - (وقام تميم) بن أوس (الداري) رضي الله عنه (ليلة بهذه الآية أم حسب الذين اجترحوا السيآت أن نجعلهم كالذين أمنوا وعملوا الصالحات الآية)','697','814','0');
INSERT INTO [t38171] VALUES (860,'٨٤٨ - (وقام سعيد بن جبير ليلة بهذه الآية يرددها وامتازوا اليوم أيها المجرمون)','698','814','0');
INSERT INTO [t38171] VALUES (861,'٨٤٩ - علي بن أبي طالب (رضي الله عنه في قوله ما أسر إلي رسول الله - صلى الله عليه وسلم - شيئا كتمه عن الناس إلا أن يؤتى الله تعالى فهما في كتابه).','699','814','0');
INSERT INTO [t38171] VALUES (862,'٨٥٠ - قال - صلى الله عليه وسلم - إذا عظمت أمتي الدينار والدرهم نزع منها هيبة الإسلام وإذا تركوا الأمر بالمعروف والنهي عن المنكر حرموا بركة الوحي قال الفضيل يعني حرموا فهم القرآن.','699','814','0');
INSERT INTO [t38171] VALUES (863,'٨٥١ - (قال رسول الله - صلى الله عليه وسلم - اقرأوا القرآن ما ائتلفت)','700','814','0');
INSERT INTO [t38171] VALUES (864,'٨٥٢ - (وقال - صلى الله عليه وسلم - إن أحسن الناس صوتا بالقرآن الذى إذا سمعته يقرأ رأيت أنه يخشى الله تعالى','700','814','0');
INSERT INTO [t38171] VALUES (865,'٨٥٣ - (قال - صلى الله عليه وسلم - لا يسمع القرآن من أحد أشهى منه ممن يخشى الله عز وجل)','701','814','0');
INSERT INTO [t38171] VALUES (866,'٨٥٤ - لما جاء رجل إلى رسول الله - صلى الله عليه وسلم - يتعلم العلم فلما كان عند باب المسجد سمع النبي - صلى الله عليه وسلم - وهو يقرأ فمن يعمل مثقال ذرة خيرا يره ومن يعمل مثقال ذرة شرا يره فقال يكفيني هذا فانصرف فقال النبي - صلى الله عليه وسلم - انصرف الرجل وهو فقيه).','702','814','0');
INSERT INTO [t38171] VALUES (867,'٨٥٥ - (قال - صلى الله عليه وسلم - من فسر القرآن برأيه فليتبوأ مقعده من النار)','702','814','0');
INSERT INTO [t38171] VALUES (868,'٨٥٦ - (قال - صلى الله عليه وسلم - إن للقرآن ظهرا وبطنا وحدا ومطلعا)','703','814','0');
INSERT INTO [t38171] VALUES (869,'٨٥٧ - (وقال أبو الدرداء) رضي الله عنه (لا يفقه الرجل حتى يجعل للقرآن وجوها)','703','814','0');
INSERT INTO [t38171] VALUES (870,'٨٥٨ - (قال رسول الله - صلى الله عليه وسلم - اقرأوا القرآن والتمسوا غرائبه)','704','814','0');
INSERT INTO [t38171] VALUES (871,'٨٥٩ - (وقال - صلى الله عليه وسلم - في حديث علي رضي الله عنه والذي نفسي بيده)','704','814','0');
INSERT INTO [t38171] VALUES (872,'٨٦٠ - (حديث حذيفة) بن اليمان رضى الله عنه (لما أخبره رسول الله - صلى الله عليه وسلم - بالاختلاف والفرقة بعده قال:','705','814','0');
INSERT INTO [t38171] VALUES (873,'٨٦١ - (قال - صلى الله عليه وسلم - يبعث زيد بن عمرو بن نفيل أمه وحده)','706','814','0');
INSERT INTO [t38171] VALUES (874,'كتاب الأذكار والدعوات','707','18','0');
INSERT INTO [t38171] VALUES (875,'٨٦٢ - (قال رسول الله - صلى الله عليه وسلم - ذاكر الله في الغافلين كالشجرة الخضراء في وسط الهشيم)','708','874','0');
INSERT INTO [t38171] VALUES (876,'٨٦٣ - (قال - صلى الله عليه وسلم - ذاكر الله في الغافلين كالمقاتل بين الفارين).','708','874','0');
INSERT INTO [t38171] VALUES (877,'٨٦٤ - (قال - صلى الله عليه وسلم - يقول الله تعالى أنا مع عبدي ما ذكرني وتحركت بي شفتاه).','708','874','0');
INSERT INTO [t38171] VALUES (878,'٨٦٥ - (قال - صلى الله عليه وسلم - ما عمل ابن آدم) وفي رواية آدمي (من عمل أنجى له من عذاب الله من ذكر الله).','709','874','0');
INSERT INTO [t38171] VALUES (879,'٨٦٦ - (قال - صلى الله عليه وسلم - من أحب أن يرتع في رياض الجنة فليكثر ذكر الله عز وجل).','709','874','0');
INSERT INTO [t38171] VALUES (880,'٨٦٧ - (سئل رسول الله - صلى الله عليه وسلم - أي الأعمال أفضل فقال أن تموت ولسانك رطب بذكر الله عز وجل).','710','874','0');
INSERT INTO [t38171] VALUES (881,'٨٦٨ - (قال - صلى الله عليه وسلم - أصبح وأمس ولسانك رطب بذكر الله عز وجل تصبح وتمسي وليس عليك خطيئة).','710','874','0');
INSERT INTO [t38171] VALUES (882,'٨٦٩ - (قال - صلى الله عليه وسلم - لذكر الله) عز وجل (بالغداة والعشى أفضل من حطم السيوف في سبيل الله ومن إعطاء المال سحا).','711','874','0');
INSERT INTO [t38171] VALUES (883,'٨٧٠ - (وقال - صلى الله عليه وسلم -) فيما يرويه عن ربه تبارك وتعالى (قال الله عز وجل إذا ذكرني عبدي في نفسه ذكرته في نفسي وإذا ذكرني في ملأ ذكرته في ملأ خير من ملئه وإذا تقرب إلي شبرا تقربت منه ذراعا وإذا تقرب مني ذراعا تقربت منه باعا وإذا مشى إلي هرولت إليه).','711','874','0');
INSERT INTO [t38171] VALUES (884,'٨٧١ - (قال أبو الدرداء) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - ألا أنبئكم بخير أعمالكم وأزكاها عند مليككم).','712','874','0');
INSERT INTO [t38171] VALUES (885,'٨٧٢ - (قال - صلى الله عليه وسلم - قال الله عز وجل من شغله ذكري عن مسألتي أعطيته أفضل ما أعطي السائلين).','713','874','0');
INSERT INTO [t38171] VALUES (886,'٨٧٤ - (قال - صلى الله عليه وسلم - ما من قوم اجتمعوا يذكرون الله تعالى لا يريدون بذلك إلا وجهه إلا ناداهم مناد من السماء قوموا مغفورا لكم وقد بدلت لكم سيآتكم حسنات).','714','874','0');
INSERT INTO [t38171] VALUES (887,'٨٧٥ - (وقال - صلى الله عليه وسلم - ما قعد قوم مقعدا لم يذكروا الله تعالى فيه ولم يصلوا علي إلا كان حسرة عليهم يوم القيامة).','715','874','0');
INSERT INTO [t38171] VALUES (888,'٨٧٦ - (وقال النبي - صلى الله عليه وسلم - المجلس الصالح يكفر عن المؤمن ألفي ألف مجلس من مجالس السوء).','715','874','0');
INSERT INTO [t38171] VALUES (889,'٨٧٧ - (عن أبي هريرة رضي الله عنه أنه دخل السوق)','715','874','0');
INSERT INTO [t38171] VALUES (890,'٨٧٩ - (قال - صلى الله عليه وسلم - من قال لا إله إلا الله وحده لا شريك له له الملك وله الحمد وهو على كل شيء قدير في يوم مائة مرة كانت له عدل عشر رقاب وكتبت له مائة حسنة ومحيت عنه مائة سيئة وكانت له حرزا من الشيطان يومه).','717','874','0');
INSERT INTO [t38171] VALUES (891,'٨٨٠ - وقال - صلى الله عليه وسلم - ليس على أهل لا إله إلا الله وحشة في قبورهم ولا في نشورهم كأني أنظر إليهم عند الصيحة ينفضون رؤسهم من التراب ويقولون الحمد لله الذي أذهب عنا الحزن إن ربنا لغفور شكور.','717','874','0');
INSERT INTO [t38171] VALUES (892,'٨٨١ - (وقال - صلى الله عليه وسلم - لأبي هريرة يا أبا هريرة إن كل حسنة تعملها توزن يوم القيامة إلا شهادة أن لا إله إلا الله فإنها لا توضع في ميزان لأنها لو وضعت في ميزان من قالها صادقا ووضعت السماوات والأرضون السبع وما فيهن كانت لا إله إلا الله أرجح من ذلك)','718','874','0');
INSERT INTO [t38171] VALUES (893,'٨٨٢ - (وقال - صلى الله عليه وسلم - لو جاء قائل لا إله إلا الله صادقا بقراب الأرض ذنوبا لغفر له ذلك).','718','874','0');
INSERT INTO [t38171] VALUES (894,'٨٨٣ - (وقال - صلى الله عليه وسلم - يا أبا هريرة لقن الموتى شهادة أن لا إله إلا الله فإنها تهدم الذنوب هدما قلت يا رسول الله هذا للموتى فكيف للأحياء فقال هي أهدم وأهدم)','719','874','0');
INSERT INTO [t38171] VALUES (895,'٨٨٤ - (قال النبي - صلى الله عليه وسلم - من قال لا إله إلا الله مخلصا دخل الجنة)','719','874','0');
INSERT INTO [t38171] VALUES (896,'٨٨٥ - (وقال - صلى الله عليه وسلم - لتدخلن الجنة كلكم إلا من أبى وشرد شرود البعير على أهله فقيل يا رسول الله ومن يأبى قال من لم يقل لا إله إلا الله)','720','874','0');
INSERT INTO [t38171] VALUES (897,'٨٨٦ - (أكثروا)','720','874','0');
INSERT INTO [t38171] VALUES (898,'٨٨٧ - (هي كلمة التوحيد)','720','874','0');
INSERT INTO [t38171] VALUES (899,'٨٨٨ - (هي كلمة الإخلاص)','720','874','0');
INSERT INTO [t38171] VALUES (900,'٨٨٩ - (وهي كلمة التقوى)','720','874','0');
INSERT INTO [t38171] VALUES (901,'٨٩٠ - (هي الكلمة الطبية)','721','874','0');
INSERT INTO [t38171] VALUES (902,'٨٩١ - (هي دعوة الحق)','721','874','0');
INSERT INTO [t38171] VALUES (903,'٨٩٢ - (هي العروة الوثقى)','721','874','0');
INSERT INTO [t38171] VALUES (904,'٨٩٣ - (هي ثمن الجنة)','721','874','0');
INSERT INTO [t38171] VALUES (905,'٨٩٤ - (روى البراء بن عازب)','721','874','0');
INSERT INTO [t38171] VALUES (906,'٨٩٥ - (قال من قال في يوم مائتي مرة لا إله إلا الله وحده لا شريك له له الملك وله الحمد وهو على كل شيء قدير لم يسبقه أحد كان قبله ولا يدركه أحد كان بعده إلا من عمل بأفضل من عمله)','721','874','0');
INSERT INTO [t38171] VALUES (907,'٨٩٦ - (قال عمر)','722','874','0');
INSERT INTO [t38171] VALUES (908,'٨٩٧ - (ويروى أن العبد إذا قال لا إله إلا الله أتت على صحيفته فلا تمر على خطيئة إلا محيت حتى تجد حسنة مثلها فتجلس إلى جانبها)','722','874','0');
INSERT INTO [t38171] VALUES (909,'٨٩٨ - (وفي الصحيح عن أبي أيوب)','722','874','0');
INSERT INTO [t38171] VALUES (910,'٨٩٩ - (وفي الصحيح أيضا عن عبادة بن الصامت)','723','874','0');
INSERT INTO [t38171] VALUES (911,'٩٠٠ - (قال النبي - صلى الله عليه وسلم - من سبح دبر كل صلاة)','723','874','0');
INSERT INTO [t38171] VALUES (912,'٩٠١ - (وقال - صلى الله عليه وسلم - من قال سبحان الله وبحمده في يوم مائة مرة حطت خطاياه ولو كانت مثل زبد البحر)','724','874','0');
INSERT INTO [t38171] VALUES (913,'٩٠٢ - (وروي أن رجلا جاء إلى رسول الله - صلى الله عليه وسلم - فقال تولت عني الدنيا وقلت ذات يدي)','724','874','0');
INSERT INTO [t38171] VALUES (914,'٩٠٣ - (قال - صلى الله عليه وسلم - إذا قال العبد الحمد لله ملأت ما بين السماء والأرض وإذا قال الحمد لله).','724','874','0');
INSERT INTO [t38171] VALUES (915,'٩٠٤ - (وقال رفاعه)','725','874','0');
INSERT INTO [t38171] VALUES (916,'٩٠٥ - (وقال - صلى الله عليه وسلم - الباقيات الصالحات هن لا إله إلا الله وسبحان الله والله أكبر والحمد لله ولا حول ولا قوة إلا بالله)','725','874','0');
INSERT INTO [t38171] VALUES (917,'٩٠٦ - (قال - صلى الله عليه وسلم - ما على الأرض رجل يقول لا إله إلا الله والله أكبر وسبحان الله والحمد لله ولا حول ولا قوة إلا بالله إلا غفرت ذنوبه ولو كانت مثل زبد البحر رواه ابن عمر)','726','874','0');
INSERT INTO [t38171] VALUES (918,'٩٠٧ - (روى النعمان بن بشير)','726','874','0');
INSERT INTO [t38171] VALUES (919,'٩٠٨ - (روى أبو هريرة: رضي الله عنه أن النبي - صلى الله عليه وسلم - قال لأن','726','874','0');
INSERT INTO [t38171] VALUES (920,'٩٠٩ - (قال - صلى الله عليه وسلم - أحب الكلام إلى الله عز وجل أربع سبحان الله والحمد لله ولا إله إلا الله والله أكبر لا يضرك بأيهن بدأت رواه سمرة بن جندب الفزاري)','727','874','0');
INSERT INTO [t38171] VALUES (921,'٩١٠ - (وروى أبو مالك الأشعري)','727','874','0');
INSERT INTO [t38171] VALUES (922,'٩١١ - (قال أبو هريرة رضي الله عنه قال النبي - صلى الله عليه وسلم - كلمتان خفيفتان على اللسان ثقيلتان في الميزان حبيبتان إلى الرحمن سبحان الله وبحمده سبحان الله العظيم).','728','874','0');
INSERT INTO [t38171] VALUES (923,'٩١٢ - (قال أبو ذر) جندب بن جنادة الغفاري (رضي الله عنه قلت لرسول الله - صلى الله عليه وسلم - أي الكلام أحب إلى الله عز وجل قال ما اصطفى الله عز وجل لملائكته سبحان الله وبحمده سبحان الله العظيم)','728','874','0');
INSERT INTO [t38171] VALUES (924,'٩١٣ - (قال أبو هريرة) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - إن الله عز وجل اصطفى من الكلام)','729','874','0');
INSERT INTO [t38171] VALUES (925,'٩١٤ - (قال جابر) بن عبد الله الأنصاري رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - من قال سبحان الله وبحمده غرست له نخلة في الجنة).','729','874','0');
INSERT INTO [t38171] VALUES (926,'٩١٥ - (عن أبي ذر رضي الله عنه أنه قال قال الفقراء لرسول الله - صلى الله عليه وسلم - ذهب أهل الدثور)','730','874','0');
INSERT INTO [t38171] VALUES (927,'٩١٦ - (قال أبو ذر) رضي الله عنه (قلت لرسول الله - صلى الله عليه وسلم - سبق أهل الأموال بالأجر يقولون كما نقول وينفقون) من فضول أموالهم (ولا ننفق فقال - صلى الله عليه وسلم - أفلا أدلك على عمل إذا أنت فعلته أدركت من قبلك وفقت من بعدك إلا من قال مثل قولك تسبيح بعد كل صلاة) أي من المكتوبات (ثلاثا وثلاثين) مرة (وتحمد ثلاثا وثلاثين) مرة (وتكبر أربعا وثلاثين)','731','874','0');
INSERT INTO [t38171] VALUES (928,'٩١٧ - (وروت يسيرة)','731','874','0');
INSERT INTO [t38171] VALUES (929,'٩١٨ - (وقال ابن عمر) هكذا في سائر نسخ الكتاب ويعني به عبد الله بن عمر بن الخطاب (رأيته صلى الله عليه وسلم يعقد التسبيح).','732','874','0');
INSERT INTO [t38171] VALUES (930,'٩١٩ - (وقد قال - صلى الله عليه وسلم - فيما شهد عليه أبو هريرة وأبو سعيد الخدري) رضي الله عنهما (أنه - صلى الله عليه وسلم - قال إذا قال العبد لا إله إلا الله والله أكبر قال الله عز وجل صدق عبدي لا إله إلا أنا وأنا أكبر وإذا قال العبد لا إله إلا الله وحده لا شريك له قال الله تعالى صدق عبدي لا إله إلا أنا لا شريك لي وإذا قال لا إله إلا الله لا حول ولا قوة إلا بالله يقول الله سبحانه صدق عبدي لا حول ولا قوة إلا بي ومن قالهن عند الموت لا تمسه النار).','732','874','0');
INSERT INTO [t38171] VALUES (931,'٩٢٠ - (روى مصعب بن سعد)','733','874','0');
INSERT INTO [t38171] VALUES (932,'٩٢١ - (قال رسول الله - صلى الله عليه وسلم - يا عبد الله بن قيس)','734','874','0');
INSERT INTO [t38171] VALUES (933,'٩٢٢ - (وقال أبو هريرة) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - عمل من كنز الجنة ومن تحت العرش قول لا حول ولا قوة إلا بالله يقول الله تعالى أسلم عبدي واستسلم).','735','874','0');
INSERT INTO [t38171] VALUES (934,'٩٢٣ - (وقال - صلى الله عليه وسلم - من قال حين يصبح رضيت بالله ربا وبالإسلام دينا وبمحمد - صلى الله عليه وسلم - نبيا كان حقا على الله أن يرضيه يوم القيامة).','735','874','0');
INSERT INTO [t38171] VALUES (935,'٩٢٤ - (وفي رواية من قال ذلك رضى الله عنه)','736','874','0');
INSERT INTO [t38171] VALUES (936,'٩٢٥ - (قال مجاهد) بن جبر التابعي مرسلا (إذا خرج الرجل من بيته فقال بسم الله قال الملك حديث فإذا قال توكلت على الله قال الملك كفيت وإذا قال لا حول ولا قوة إلا بالله قال الملك وقيت فتفرق عنه الشياطين فيقولون ما تريدون من رجل قد هدي وكفي ووقي).','736','874','0');
INSERT INTO [t38171] VALUES (937,'٩٢٦ - (وورد فيها من الفضائل ما لا يحصى فمن ذلك أنه لما استشهد عبد الله بن عمرو الأنصاري رضي الله عنه (يوم أحد قال رسول الله - صلى الله عليه وسلم - لجابر ابنه ألا أبشرك يا جابر قال بلى بشرك الله الخير قال إن الله عز وجل أحيا أباك وأقعده بين يديه وليس بينه وبينه ستر فقال الله تعالى تمن على عبدي ما شئت أعطيته فقال يا رب تردني إلى الدنيا حتى أقتل فيك وفي نبيك) - صلى الله عليه وسلم - (مرة رواه الترمذي وقال حسن).','737','874','0');
INSERT INTO [t38171] VALUES (938,'٩٢٧ - قال رسول الله - صلى الله عليه وسلم - لجابر ابنه ألا أبشرك يا جابر قال بلى بشرك الله بالخير','737','874','0');
INSERT INTO [t38171] VALUES (939,'٩٢٨ - (فضل رسول الله - صلى الله عليه وسلم - قول لا إله إلا الله على سائر الأذكار).','737','874','0');
INSERT INTO [t38171] VALUES (940,'٩٢٩ - (من أحب لقاء الله سبحانه أحب الله لقاءه ومن كره لقاء الله عز وجل كره الله لقاءه)','738','874','0');
INSERT INTO [t38171] VALUES (941,'٩٣٠ - (قال الدعاء هو العبادة ثم قرأ ادعوني أستجب لكم الآية).','738','874','0');
INSERT INTO [t38171] VALUES (942,'٩٣١ - (وقال - صلى الله عليه وسلم - الدعاء مخ العبادة).','739','874','0');
INSERT INTO [t38171] VALUES (943,'٩٣٢ - (وروى أبو هريرة أنه - صلى الله عليه وسلم - قال ليس شيء أكرم)','739','874','0');
INSERT INTO [t38171] VALUES (944,'٩٣٣ - (وقال - صلى الله عليه وسلم - إن العبد لا يخطئه من الدنيا إحدى ثلاث إما ذنب يغفر له وإما خير يعجل له وإما خير يدخر له).','739','874','0');
INSERT INTO [t38171] VALUES (945,'٩٣٤ - (وقال - صلى الله عليه وسلم - سلوا الله تعالى من فضله فإنه تعالى يحب أن يسأل وأفضل العبادة انتظار الفرج).','740','874','0');
INSERT INTO [t38171] VALUES (946,'٩٣٥ - (قوله - صلى الله عليه وسلم - ينزل الله تعالى كل ليلة إلى سماء الدنيا حين يبقى ثلث الليل الأخير فيقول من يدعوني فأستجيب له من يسألني فأعطيه من يستغفرني فأغفر له).','741','874','0');
INSERT INTO [t38171] VALUES (947,'٩٣٦ - (وقال - صلى الله عليه وسلم - الدعاء بين الآذان والإقامة لا يرد).','742','874','0');
INSERT INTO [t38171] VALUES (948,'٩٣٧ - (وقال - صلى الله عليه وسلم - الصائم لا ترد دعوته).','743','874','0');
INSERT INTO [t38171] VALUES (949,'٩٣٨ - (قال رسول الله - صلى الله عليه وسلم - إن ربكم حيي كريم يستحي من','743','874','0');
INSERT INTO [t38171] VALUES (950,'٩٣٩ - (وروى أنس) بن مالك رضي الله عنه (أن رسول الله - صلى الله عليه وسلم - كان يرفع يديه حتى يرى بياض إبطيه في الدعاء ولا يشير بإصبعه).','744','874','0');
INSERT INTO [t38171] VALUES (951,'٩٤٥ - (روى أبو هريرة) رضي الله عنه (إنه - صلى الله عليه وسلم - مر على إنسان يدعو وهو يشير بإصبعيه السبابتين فقال - صلى الله عليه وسلم - أحد أحد).','744','874','0');
INSERT INTO [t38171] VALUES (952,'٩٤١ - (قال عمر) بن الخطاب (رضي الله عنه كان رسول الله - صلى الله عليه وسلم - إذا مد يديه في الدعاء لم يردهما حتى يمسح بهما وجهه).','745','874','0');
INSERT INTO [t38171] VALUES (953,'٩٤٢ - (قال ابن عباس) رضي الله عنهما (كان رسول الله - صلى الله عليه وسلم - إذا دعا ضم كفيه وعل بطونهما مما يلي','745','874','0');
INSERT INTO [t38171] VALUES (954,'٩٤٣ - (قال رسول الله - صلى الله عليه وسلم - لينتهين أقوام عن رفع أبصارهم إلى السماء عند الدعاء أو لتخطفن أبصارهم).','745','874','0');
INSERT INTO [t38171] VALUES (955,'٩٤٤ - (روي أن أبا موسى) عبد الله بن قيس (الأشعري) رضي الله عنه (قال قدمنا مع رسول الله - صلى الله عليه وسلم - فلما دنونا من المدينة كبر وكبر الناس ورفعوا أصواتهم فقال - صلى الله عليه وسلم - يا أيها الناس إن الذي تدعون ليس بأصم ولا غائب إن الذي تدعون بينكم وبين أعناق ركابكم).','746','874','0');
INSERT INTO [t38171] VALUES (956,'٩٤٥ - (قالت عائشة رضي الله عنها في قوله عز وجل ولا تجهر بصلاتك ولا تخافت بها أي بدعائك).','747','874','0');
INSERT INTO [t38171] VALUES (957,'٩٤٦ - (قال النبي - صلى الله عليه وسلم - سيكون قوم يعتدون في الدعاء).','748','874','0');
INSERT INTO [t38171] VALUES (958,'٩٤٧ - (روى عن معاذ) بن جبل (رضي الله عنه أن العلم يحتاج إليه في الجنة إذ يقال لأهل الجنة تمنوا فلا يدرون كيف يتمنون حتى يتعلموا من العلماء).','748','874','0');
INSERT INTO [t38171] VALUES (959,'٩٤٨ - (وقد قال - صلى الله عليه وسلم - إياكم والسجع في الدعاء بحسب أحدكم أن يقول اللهم إني أسألك الجنة وما قرب إليها من قول وعمل وأعوذ بك من النار وما قرب إليها من قول وعمل)','748','874','0');
INSERT INTO [t38171] VALUES (960,'٩٤٩ - (قال النبي - صلى الله عليه وسلم - لا يقل أحدكم إذا دعا اللهم اغفر لي إن شئت اللهم ارحمني إن شئت ليعزم المسألة فإنه لا مكره له)','749','874','0');
INSERT INTO [t38171] VALUES (961,'٩٥٠ - (وقال - صلى الله عليه وسلم - إذا دعا أحدكم فليعظم الرغبة فإن الله تعالى لا يتعاظمه شيء)','749','874','0');
INSERT INTO [t38171] VALUES (962,'٩٥١ - (وقال - صلى الله عليه وسلم - ادعوا الله)','749','874','0');
INSERT INTO [t38171] VALUES (963,'٩٥٢ - وقال - صلى الله عليه وسلم - ادعو الله وأنتم موقنون بالإجابة واعلموا أن الله عز وجل لا يستجيب دعاء من قلب غافل.','749','874','0');
INSERT INTO [t38171] VALUES (964,'٩٥٣ - والإلحاح في الدعاء مما يفتح باب الإجابة ويدل على إقبال القلب ويحصل بتكراره مرتين وثلاثا','750','874','0');
INSERT INTO [t38171] VALUES (965,'٩٥٤ - (لقوله - صلى الله عليه وسلم - يستجاب لأحدكم ما لم يعجل فيقول قد دعوت فلم يستحب لي)','750','874','0');
INSERT INTO [t38171] VALUES (966,'٩٥٥ - ومن أبطأ عنه شيء من ذلك فليقل الحمد لله على كل حال.','750','874','0');
INSERT INTO [t38171] VALUES (967,'٩٥٦ - (وقال سلمة بن الأكوع) رضي الله عنه (ما سمعت رسول الله - صلى الله عليه وسلم - يستفتح الدعاء إلا استفتحه فقال سبحان ربي العلي الأعلى الوهاب)','751','874','0');
INSERT INTO [t38171] VALUES (968,'٩٥٧ - (روي في الخبر عن رسول الله - صلى الله عليه وسلم - أنه قال إذا سألتم الله حاجة فابدأوا بالصلاة علي فإن الله أكرم من أن يسأل حاجتين فيقضي إحداهما ويرد الأخرى رواه أبو طالب المكي)','751','874','0');
INSERT INTO [t38171] VALUES (969,'٩٥٨ - (قال إنه جاءني جبريل عليه السلام فقال) لي (أما ترضى يا محمد لا يصلي عليك أحد من أمتك ألا صليت عليه عشرا ولا يسلم عليك أحد من أمتك ألا سلمت عليه عشرا)','752','874','0');
INSERT INTO [t38171] VALUES (970,'٩٥٩ - (وقال - صلى الله عليه وسلم - إن أولى الناس بي أكثرهم علي صلاة)','752','874','0');
INSERT INTO [t38171] VALUES (971,'٩٦٠ - وقال - صلى الله عليه وسلم - بحسب المؤمن من البخل أن أذكر عنده فلا يصلي علي','752','874','0');
INSERT INTO [t38171] VALUES (972,'٩٦١ - (وقال - صلى الله عليه وسلم - أكثروا من الصلاة علي يوم الجمعة)','753','874','0');
INSERT INTO [t38171] VALUES (973,'٩٦٢ - (وقال - صلى الله عليه وسلم - من صلى علي من أمتي كتبت لى عشر حسنات ومحيت عنه عشر سيآت)','753','874','0');
INSERT INTO [t38171] VALUES (974,'٩٦٤ - (قال - صلى الله عليه وسلم - من صلى علي في كتاب لم تزل الملائكة يستغفرون له ما دام اسمي في ذلك الكتاب)','754','874','0');
INSERT INTO [t38171] VALUES (975,'٩٦٥ - (قال - صلى الله عليه وسلم - ليس أحد يسلم علي إلا رد الله علي روحي حتى أرد عليه السلام)','755','874','0');
INSERT INTO [t38171] VALUES (976,'٩٦٦ - (قيل يا رسول الله كيف نصلي عليك فقال صلى الله عليه وسلم قولوا اللهم صل على محمد وعلى آل محمد وأزواجه وذريته كما باركت على إبراهيم إنك حميد مجيد)','755','874','0');
INSERT INTO [t38171] VALUES (977,'٩٦٧ - (حديث حنين الجذع) قال العراقي','756','874','0');
INSERT INTO [t38171] VALUES (978,'٩٦٨ - (ثم صليت الصبح من ليلتك)','756','874','0');
INSERT INTO [t38171] VALUES (979,'٩٦٩ - (ولعقت أصابعك تواضعا منك صلى الله عليك)','756','874','0');
INSERT INTO [t38171] VALUES (980,'٩٧٠ - (وروي عن أبي الحسين الشافعي)','756','874','0');
INSERT INTO [t38171] VALUES (981,'٩٧١ - (كان رسول الله - صلى الله عليه وسلم - يكثر أن يقول سبحانك وبحمدك','757','874','0');
INSERT INTO [t38171] VALUES (982,'٩٧٢ - (وقال - صلى الله عليه وسلم - من أكثر من الاستغفار جعل الله عز وجل له من كل هم فرجا ومن كل ضيق مخرجا ورزقه من حيث لا يحتسب)','758','874','0');
INSERT INTO [t38171] VALUES (983,'٩٧٣ - (قال - صلى الله عليه وسلم - إني لأستغفر الله سبحانه وأتوب إليه في اليوم سبعين مرة).','758','874','0');
INSERT INTO [t38171] VALUES (984,'٩٧٤ - (قال - صلى الله عليه وسلم - إنه ليغان على قلبي).','758','874','0');
INSERT INTO [t38171] VALUES (985,'٩٧٥ - (وقال - صلى الله عليه وسلم - من قال حين يأوي إلى فراشه)','758','874','0');
INSERT INTO [t38171] VALUES (986,'٩٧٦ - (وقال - صلى الله عليه وسلم - في حديث آخر من قال ذلك غفرت ذنوبه وإن كان فارا من الزحف).','759','874','0');
INSERT INTO [t38171] VALUES (987,'٩٧٧ - (قال أبو عبد الله حذيفة)','760','874','0');
INSERT INTO [t38171] VALUES (988,'٩٧٨ - (وقالت عائشة رضي الله عنها قال) لي (رسول الله - صلى الله عليه وسلم - إن كنت ألممت بذنب فاستغفري الله وتوبي إليه فإن التوبة من الذنب الندم والاستغفار).','760','874','0');
INSERT INTO [t38171] VALUES (989,'٩٧٩ - (كان - صلى الله عليه وسلم - يقول في الاستغفار اللهم اغفر لي خطيئتي)','761','874','0');
INSERT INTO [t38171] VALUES (990,'٩٨٠ - (قال علي رضي الله عنه كنت رجلا إذا سمعت من','761','874','0');
INSERT INTO [t38171] VALUES (991,'٩٨١ - (روى أبو هريرة) رضي الله عنه (عن النبي - صلى الله عليه وسلم - أنه قال إن المؤمن إذا أذنب ذنبا كانت نكتة سوداء في قلبه فإن تاب ونزع واستغفر)','762','874','0');
INSERT INTO [t38171] VALUES (992,'٩٨٢ - (وروى أبو هريرة رضي الله عنه أن رسول الله - صلى الله عليه وسلم - قال إن الله سبحانه ليرفع الدرجة للعبد فى الجنة)','763','874','0');
INSERT INTO [t38171] VALUES (993,'٩٨٢/ أ- (وروت عائشة رضي الله عنها أنه - صلى الله عليه وسلم - قال اللهم اجعلني من الذين إذا أحسنوا استبشروا:','763','992','0');
INSERT INTO [t38171] VALUES (994,'٩٨٣ - (وقال - صلى الله عليه وسلم - ما أصر)','764','874','0');
INSERT INTO [t38171] VALUES (995,'٩٨٤ - (وقال - صلى الله عليه وسلم - إذا أذنب العبد ذنبا فقال اللهم اغفر لي يقول الله عز وجل أذنب عبدي ذنبا فعلم أن له ربا يأخذ بالذنب ويغفر الذنب عبدي اعمل ما شئت فقد غفرت لك).','764','874','0');
INSERT INTO [t38171] VALUES (996,'٩٨٥ - (وقال - صلى الله عليه وسلم - إن رجلا لم يعمل خيرا قط نظر إلى السماء)','764','874','0');
INSERT INTO [t38171] VALUES (997,'٩٨٦ - (وقال - صلى الله عليه وسلم - من أذنب ذنبا فعلم أن الله قد اطلع عليه غفر له وإن لم يستغفر).','765','874','0');
INSERT INTO [t38171] VALUES (998,'٩٨٧ - (قال - صلى الله عليه وسلم - يقول عز وجل يا عبادي)','765','874','0');
INSERT INTO [t38171] VALUES (999,'٩٨٨ - (وقال - صلى الله عليه وسلم - من قال سبحانك ظلمت نفسي وعملت سوءا فاغفر لي إنه لا يغفر الذنوب إلا أنت غفرت له ذنوبه وإن كانت كمدب النمل).','766','874','0');
INSERT INTO [t38171] VALUES (1000,'٩٨٩ - (يروى أن أفضل الاستغفار)','766','874','0');
INSERT INTO [t38171] VALUES (1001,'٩٩٥ - (فمنها دعاء رسول الله - صلى الله عليه وسلم - بعد ركعتي الفجر)','767','874','0');
INSERT INTO [t38171] VALUES (1002,'٩٩٢ - (دعاء عائشة رضي الله عنها)','769','874','0');
INSERT INTO [t38171] VALUES (1003,'٩٩٣ - (دعاء فاطمة رضي الله عنها)','770','874','0');
INSERT INTO [t38171] VALUES (1004,'٩٩٤ - (دعاء أبي بكر الصديق رضي الله عنه علمه رسول الله - صلى الله عليه وسلم - أبا بكر الصديق) رضي الله عنه (أن يقول اللهم إني أسألك بمحمد نبيك وإبراهيم خليلك وموسى نجيك وعيسى كلمتك وروحك)','770','874','0');
INSERT INTO [t38171] VALUES (1005,'٩٩٥ - (دعاء بريدة)','772','874','0');
INSERT INTO [t38171] VALUES (1006,'٩٩٦ - (دعاء قبيصة بن المخارق)','772','874','0');
INSERT INTO [t38171] VALUES (1007,'٩٩٧ - (دعاء أبي الدرداء رضي الله عنه) (قيل لأبي الدرداء رضي الله عنه أدرك دارك وكانت النار وقعت في محلته فقال ما كان الله ليفعل ذلك ثم أتاه آت فقال له ذلك ثلاثا كل ذلك يقول ما كان الله ليفعل ذلك ثم أتاه آت فقال له إن النار لما دنت من دارك طفئت قال قد علمت فقيل له ما ندري أي قوليك أعجب قال إني سمعت رسول الله - صلى الله عليه وسلم - يقول من قال هؤلاء الكلمات في ليل أو نهار لم يضره شيء وقد قلتهن)','774','874','0');
INSERT INTO [t38171] VALUES (1008,'٩٩٨ - (دعاء) نبي الله (عيسى عليه السلام)','774','874','0');
INSERT INTO [t38171] VALUES (1009,'٩٩٩ - (دعاء الخضر عليه السلام)','775','874','0');
INSERT INTO [t38171] VALUES (1010,'١٠٠٠ - (دعاء علي بن أبي طالب رضي الله عنه)','777','874','0');
INSERT INTO [t38171] VALUES (1011,'١٠٠١ - (قل اللهم فاطر السماوات والأرض عالم الغيب والشهادة رب كل شيء ومليكه أشهد أن لا إله إلا أنت أعوذ بك من شر نفسي ومن شر الشيطان وشركه).','778','874','0');
INSERT INTO [t38171] VALUES (1012,'١٠٠٢ - (قل اللهم إني أسألك العفو والعافية في ديني ودنياي)','779','874','0');
INSERT INTO [t38171] VALUES (1013,'١٠٠٣ - (اللهم لا تؤمني مكرك ولا تولني غيرك)','779','874','0');
INSERT INTO [t38171] VALUES (1014,'١٠٠٤ - (قل اللهم عافني فى بدني) من الأسقام والآلام (وعافني في سمعي) أي القوة المودعة في الجارحة وإرادة الاستماع بعيدة (وعافني في بصري).','780','874','0');
INSERT INTO [t38171] VALUES (1015,'١٠٠٥ - (قل اللهم إني أسألك الرضا بعد القضاء وبرد العيش بعد الموت ولذة النظر إلى وجهك الكريم وشوقا إلى لقائك من غير ضراء مضرة ولا فتنة مضلة وأعوذ بك أن أظلم أو أظلم أو اعتدي أو يعتدي علي أو أكسب خطيئة أو ذنبا لا تغفره اللهم أني أسألك الثبات في الأمر والعزيمة في الرشد وأسألك شكر نعمتك وحسن عبادتك).','780','874','0');
INSERT INTO [t38171] VALUES (1016,'١٠٠٦ - (اللهم إني أسألك إيمانا لا يرتد)','781','874','0');
INSERT INTO [t38171] VALUES (1017,'١٠٠٧ - (اللهم إني أسألك الطيبات)','781','874','0');
INSERT INTO [t38171] VALUES (1018,'١٠٠٨ - اللهم بعلمك الغيب وقدرتك على الخلق أحيني ما كانت الحياة خير لي وتوفني ما كانت الوفاة خيرا لي أسألك خشيتك في الغيب والشهادة وكلمة العدل في الرضا والغضب والقصد في الغنى والفقر ولذة النظر إلى وجهك والشوق إلى لقائك وأعوذ بك من ضراء مضرة وفتنة مضلة اللهم زينا بزينة الإيمان واجعلنا هداة مهتدين.','783','874','0');
INSERT INTO [t38171] VALUES (1019,'١٠٠٩ - اللهم اقسم لنا من خشيتك ما تحول به بيننا وبين معاصيك ومن طاعتك ما تبلغنا به جنتك ومن اليقين ما تهون به علينا مصائب الدنيا والآخرة.','783','874','0');
INSERT INTO [t38171] VALUES (1020,'١٠١٠ - (اللهم املأ وجوهنا منك حياء وقلوبنا منك خوفا)','784','874','0');
INSERT INTO [t38171] VALUES (1021,'١٠١١ - (اللهم اجعل أول يومنا هذا إصلاحا)','784','874','0');
INSERT INTO [t38171] VALUES (1022,'١٠١٢ - (الحمد لله الذي تواضع كل شيء لعظمته وذل كل شيء لعزته وخضع كل شيء لملكه واستسلم كل شيء لقدرته والحمد لله الذي سكن كل شيء لهيبته وأظهر كل شيء بحكمته وتصاغر كل شيء لكبريائه).','785','874','0');
INSERT INTO [t38171] VALUES (1023,'١٠١٣ - (اللهم صل على محمد وعلى آله وأزواجه وذريته وبارك على محمد وأزواجه وذريته كما باركت على إبراهيم في العالمين إنك حميد مجيد).','785','874','0');
INSERT INTO [t38171] VALUES (1024,'١٠١٤ - (اللهم اجعلنا من أوليائك المتقين وحزبك المفلحين وعبادك الصالحين واستعملنا بما يرضيك عنا ووفقنا لمحابك منا وصرفنا بحسن اختيارك لنا).','786','874','0');
INSERT INTO [t38171] VALUES (1025,'١٠١٥ - (نسألك جوامع الخير وفواتحه وخواتمه ونعوذ بك من جوامع الشر وفواتحه وخواتمه).','786','874','0');
INSERT INTO [t38171] VALUES (1026,'١٠١٦ - (اللهم بقدرتك علي تب علي إنك أنت التواب الرحيم وبحلمك عني اعف عني إنك أنت الغفار وبعلمك بي ارفق بي إنك أنت الرحمن وبملكك لي ملكني نفسي ولا تسلطها علي إنك أنت الملك الجبار).','787','874','0');
INSERT INTO [t38171] VALUES (1027,'١٠١٧ - (سبحانك اللهم وبحمدك لا إله إلا أنت عملت سوءا وظلمت نفسي فاغفر لي ذنبي إنك أنت ربي إنه لا يغفر الذنوب إلا أنت).','787','874','0');
INSERT INTO [t38171] VALUES (1028,'١٠١٨ - (اللهم ألهمني رشدي وقني شر نفسي).','787','874','0');
INSERT INTO [t38171] VALUES (1029,'١٠١٩ - (اللهم ارزقني حلالا لا تعاقبني عليه وقنعني بما رزقتني واستعملني به صالحا تقبله مني).','788','874','0');
INSERT INTO [t38171] VALUES (1030,'١٠٢٠ - (أسألك العفو والعافية وحسن اليقين والمعافاة في الدنيا والآخرة).','788','874','0');
INSERT INTO [t38171] VALUES (1031,'١٠٢١ - (يا من لا تضره الذنوب ولا تنقصه المغفرة هب لي ما لا يضرك واعطني ما لا ينقصك).','789','874','0');
INSERT INTO [t38171] VALUES (1032,'١٠٢٢ - (رب اغفر لي ولوالدي وارحمهما كما ربياني صغيرا واغفر للمؤمنين والمؤمنات والمسلمين والمسلمات الأحياء منهم والأموات).','789','874','0');
INSERT INTO [t38171] VALUES (1033,'١٠٢٣ - (رب اغفر وارحم وتجاوز عما تعلم وأنت الأعز الأكرم وأنت خير الراحمين وخير الغافرين)','790','874','0');
INSERT INTO [t38171] VALUES (1034,'١٠٢٤ - اللهم إني أعوذ بك من البخل وأعوذ بك من الجبن وأعوذ بك من أن أرد إلى أرذل العمر وأعوذ بك من فتنة الدنيا وأعوذ بك من عذاب القبر.','790','874','0');
INSERT INTO [t38171] VALUES (1035,'١٠٢٥ - اللهم إني أعوذ بك من علم لا ينفع وقلب لا يخشع ودعاء لا يسمع ونفس لا تشبع وأعوذ بك من الجوع فإنه بئس الضجيع ومن الخيانة فإنها بئست البطانة ومن الكسل والبخل والجبن والهرم ومن أن أرد إلى أرذل العمر ومن فتنة الدجال وعذاب القبر ومن فتنة المحيا والممات اللهم إنا نسألك قلوبا أواهة مخبتة منيبة في سبيلك اللهم إني أسألك عزائم مغفرتك وموجبات رحمتك والسلامة من كل إثم والغنيمة من كل بر والفوز بالجنة والنجاة من النار.','791','874','0');
INSERT INTO [t38171] VALUES (1036,'١٠٢٦ - اللهم إني أعوذ بك من التردي وأعوذ بك من الغم والغرق والهدم','791','874','0');
INSERT INTO [t38171] VALUES (1037,'١٠٢٧ - (اللهم إني أعوذ بك من شر ما علمت ومن شر ما لم أعلم)','792','874','0');
INSERT INTO [t38171] VALUES (1038,'١٠٢٨ - اللهم جنبني منكرات الأخلاق والأعمال والأدواء والأهواء.','793','874','0');
INSERT INTO [t38171] VALUES (1039,'١٠٢٩ - اللهم إني أعوذ بك من جهد البلاء ودرك الشقاء وسوء القضاء وشماتة الأعداء اللهم إني أعوذ بك من الكفر والدين والفقر وأعوذ بك من عذاب جهنم وأعوذ بك من فتنة الدجال.','793','874','0');
INSERT INTO [t38171] VALUES (1040,'١٠٣٠ - اللهم إني أعوذ بك من شر سمعي وبصري وشر لساني وقلبي وشر مني.','794','874','0');
INSERT INTO [t38171] VALUES (1041,'١٠٣١ - (اللهم إني أعوذ بك من جار السوء)','794','874','0');
INSERT INTO [t38171] VALUES (1042,'١٠٣٢ - اللهم إني أعوذ بك من القسوة والغفلة والعيلة والذلة والمسكنة','794','874','0');
INSERT INTO [t38171] VALUES (1043,'١٠٣٣ - اللهم إني أعوذ بك من زوال نعمتك ومن تحول عافيتك ومن فجاءة نقمتك ومن جميع سخطك اللهم إني أعوذ بك من عذاب النار وفتنة النار وعذاب القبر وفتنة القبر وشر فتنة الغنى وشر فتنة الفقر وشر فتنة المسيح الدجال وأعوذ بك من المغرم والمأثم.','795','874','0');
INSERT INTO [t38171] VALUES (1044,'١٠٣٤ - اللهم إني أعوذ بك من نفس لا تشبع وقلب لا يخشع وصلاة لا تنفع ودعوة لا تستجاب وأعوذ بك من شر الغم ومن ضيق الصدر.','796','874','0');
INSERT INTO [t38171] VALUES (1045,'١٠٣٥ - اللهم إني أعوذ بك من غلبة الدين وغلبة العدو وشماتة الأعداء.','796','874','0');
INSERT INTO [t38171] VALUES (1046,'١٠٣٦ - فإذا خرجت إلى المسجد فقل اللهم اجعل في قلبي نورا وفي لساني نورا واجعل في سمعي نورا واجعل في بصري نورا واجعل خلفي نورا وأمامي نورا واجعل من فوقي نورا اللهم أعطني نورا.','797','874','0');
INSERT INTO [t38171] VALUES (1047,'١٠٣٧ - وقل أيضا اللهم إني أسألك بحق السائلين عليك وبحق ممشاي هذا إليك فإني لم أخرج أشرا ولا بطرا ولا رياء ولا سمعة خرجت اتقاء سخطك وابتغاء مرضاتك فأسألك أن تنقذني من النار وأن تغفر لي ذنوبي إنه لا يغفر الذنوب إلا أنت.','797','874','0');
INSERT INTO [t38171] VALUES (1048,'١٠٣٨ - فإن خرجت من المنزل لحاجة فقل بسم الله رب أعوذ بك أن أظلم أو أظلم أو أجهل أو يجهل علي بسم الله الرحمن الرحيم لا حول ولا قوة إلا بالله العلي العظيم بسم الله التكلان على الله.','798','874','0');
INSERT INTO [t38171] VALUES (1049,'١٠٣٩ - فإذا رأيت في المسجد من يبيع أو يبتاع فقل لا أربح الله تجارتك وإذا رأيت من ينشد ضالة في المسجد فقل لا ردها الله عليك أمر به رسول الله - صلى الله عليه وسلم - (فإذا رأيت في المسجد من يبيع فيه أو يبتاع) أي يشتري (فقل لا أربح الله تجارتك وإذا رأيت من ينشد) أي يطلب (ضالة في المسجد فقل لا ردها الله عليك أمر بذلك رسول الله - صلى الله عليه وسلم -).','801','874','0');
INSERT INTO [t38171] VALUES (1050,'١٠٤٠ - (إذا ركعت) في صلاتك (فقل) هذا الدعاء (اللهم لك ركعت ولك خشعت وبك آمنت ولك أسلمت وعليك توكلت أنت ربي خشع سمعي وبصري ومخي وعظمي وعصبي وما استقلت به) أي حملت (قدمى لله رب العالمين).','803','874','0');
INSERT INTO [t38171] VALUES (1051,'١٠٤١ - (إن ركعت فقل سبحان ربي العظيم ثلاث مرات).','804','874','0');
INSERT INTO [t38171] VALUES (1052,'١٠٤٢ - (سبوح قدوس رب الملائكة والروح).','805','874','0');
INSERT INTO [t38171] VALUES (1053,'١٠٤٣ - (قال عبد بن حميد حدثنا محمد بن عبيد حدثنا الأعمش عن عبيد بن الحسن عن عبد الله بن أبي أوفى رضي الله عنهما قال كان رسول الله - صلى الله عليه وسلم - إذا رفع رأسه من الركوع قال سمع الله لمن','805','874','0');
INSERT INTO [t38171] VALUES (1054,'١٠٤٤ - (إذا سجدت فقل)','807','874','0');
INSERT INTO [t38171] VALUES (1055,'١٠٤٥ - (اللهم سجد لك سوادي) أي شخصي (وخيالي)','808','874','0');
INSERT INTO [t38171] VALUES (1056,'١٠٤٦ - (تقول سبحان ربي الأعلى ثلاث مرات).','808','874','0');
INSERT INTO [t38171] VALUES (1057,'١٠٤٧ - (فإذا فرغت من الصلاة فقل اللهم أنت السلام ومنك السلام تباركت ياذا الجلال والإكرام).','809','874','0');
INSERT INTO [t38171] VALUES (1058,'١٠٤٨ - (فإذا قمت من مجلس وأردت دعاء يكفر لغو المجلس فقل سبحانك اللهم وبحمدك أشهد أن لا إله إلا أنت أستغفرك وأتوب إليك عملت سوء أو ظلمت نفسي فاغفر لي فإنه لا يغفر الذنوب إلا أنت).','809','874','0');
INSERT INTO [t38171] VALUES (1059,'١٠٤٩ - (إذا دخلت السوق فقل لا إله إلا الله وحده لا شريك له له الملك وله الحمد يحيي ويميت وهو حي لا يموت بيده الخير وهو على كل شيء قدير).','810','874','0');
INSERT INTO [t38171] VALUES (1060,'١٠٥٠ - (فإن كان عليك دين) عجزت عن أدائه (فقل اللهم اكفني بحلالك عن حرامك واغنني) بقطع الهمزة (بفضلك عمن سواك).','811','874','0');
INSERT INTO [t38171] VALUES (1061,'١٠٥١ - (فإذا لبست ثوبا جديدا فقل اللهم كسوتني هذا الثوب)','814','874','0');
INSERT INTO [t38171] VALUES (1062,'١٠٥٢ - (وإذا رأيت شيئا من الطيرة)','815','874','0');
INSERT INTO [t38171] VALUES (1063,'١٠٥٣ - وإذا رأى الهلال قال اللهم أهله علينا -الحديث','815','874','0');
INSERT INTO [t38171] VALUES (1064,'١٠٥٤ - (وتقول هلال رشد وهلال خير آمنت بخالقك)','816','874','0');
INSERT INTO [t38171] VALUES (1065,'١٠٥٥ - (اللهم إني أسألك خير هذا الشهر وخير القدر)','816','874','0');
INSERT INTO [t38171] VALUES (1066,'١٠٥٦ - (وتكبر قبل الدعاء أولا ثلاثا)','817','874','0');
INSERT INTO [t38171] VALUES (1067,'١٠٥٧ - (وإذا هبت الريح)','817','874','0');
INSERT INTO [t38171] VALUES (1068,'١٠٥٨ - (إذا بلغك وفاة أحد)','818','874','0');
INSERT INTO [t38171] VALUES (1069,'١٠٥٩ - (وإذا سمعت صوت الرعد فقل سبحان من يسبح الرعد بحمده والملائكة من خيفته)','819','874','0');
INSERT INTO [t38171] VALUES (1070,'١٠٦٠ - فإن رأيت الصواعق فقل اللهم لا تقتلنا بغضبك ولا تهلكنا بعذابك وعافنا قبل ذلك قاله كعب.','820','874','0');
INSERT INTO [t38171] VALUES (1071,'١٠٦١ - فإذا أمطرت السماء فقل اللهم سقيا هنيئا وصيبا نافعا اللهم اجعله صيب رحمة ولا تجعله صيب عذاب فإذا غضبت فقل اللهم اغفر لي ذنبي وأذهب غيظ قلبي وأجرني من الشيطان الرجيم إذا أمطرت السماء فقل اللهم سيبا هنيئا وصيبا نافعا)','820','874','0');
INSERT INTO [t38171] VALUES (1072,'١٠٦٢ - (فإذا غضبت) على أحد (فقل اللهم اغفر لي ذنبي واذهب غيظ قلبي وأجرني من الشيطان الرجيم)','821','874','0');
INSERT INTO [t38171] VALUES (1073,'١٠٦٣ - (فإذا خفت قوما)','821','874','0');
INSERT INTO [t38171] VALUES (1074,'١٠٦٤ - (إذا غزوت) الكفار (فقل اللهم أنت عضدي)','821','874','0');
INSERT INTO [t38171] VALUES (1075,'١٠٦٥ - (وإذا طنت أذنك فصل على محمد - صلى الله عليه وسلم - وقل ذكر الله بخير من ذكرني).','822','874','0');
INSERT INTO [t38171] VALUES (1076,'١٠٦٦ - (وإذا سمعت أذان المغرب فقل اللهم هذا استقبال ليلك وإدبار نهارك وأصوات دعاتك) جمع داع وهم المؤذنون (وحضور صلواتك أسألك أن تغفر لي).','822','874','0');
INSERT INTO [t38171] VALUES (1077,'١٠٦٧ - فإذا أصابك هم فقل اللهم إني عبدك وابن عبدك وابن أمتك ناصيتي بيدك ماض في حكمك نافذ في قضاؤك أسألك بكل اسم هو لك سميت به نفسك وأنزلته في كتابك أو أعطيته أحدا من خلقك أو استأثرت به في علم الغيب عندك أن تجعل القرآن ربيع قلبي ونور صدري وجلاء غمي وذهاب حزني وهمي قال رسول الله - صلى الله عليه وسلم - ما أصاب أحدا حزن فقال هذا إلا أذهب الله عز وجل همه وأبدل مكانه فرحا فقيل يا رسول الله أفلا نتعلمها فقال - صلى الله عليه وسلم - ينبغي لمن سمعها أن يتعلمها).','823','874','0');
INSERT INTO [t38171] VALUES (1078,'١٠٦٨ - (كان - صلى الله عليه وسلم - إذا اشتكى إنسان قرحة أو جرحا وضع سبابته على الأرض ثم رفعها)','824','874','0');
INSERT INTO [t38171] VALUES (1079,'١٠٦٩ - (وإذا وجدت وجعا في جسدك فضع يدك)','825','874','0');
INSERT INTO [t38171] VALUES (1080,'١٠٧٠ - (وإذا أصابك كرب فقل لا إله إلا الله العلي الحليم لا إله إلا الله رب العرش العظيم لا إله إلا الله رب السماوات والأرض ورب العرش الكريم).','825','874','0');
INSERT INTO [t38171] VALUES (1081,'١٠٧١ - (كبر الله أربعا وثلاثين) تكبيرة (وسبحه ثلاثا وثلاثين) تسبيحة (واحمده ثلاثا وثلاثين) تحميدة فتلك المائة.','827','874','0');
INSERT INTO [t38171] VALUES (1082,'١٠٧٢ - (اللهم إني أعوذ برضاك من سخطك وبمعافاتك من عقوبتك وأعوذ بك منك اللهم لا أستطيع أن أبلغ ثناء عليك ولو حرصت ولكن أنت كما أثنيت على نفسك).','828','874','0');
INSERT INTO [t38171] VALUES (1083,'١٠٧٣ - (اللهم باسمك أحيا وأموت)','828','874','0');
INSERT INTO [t38171] VALUES (1084,'١٠٧٤ - (اللهم رب السماوات ورب الأرض ورب كل شيء ومليكه فالق الحب والنوى ومنزل التوراة والإنجيل والفرقان أعوذ بك من شر كل ذي شر ومن شر كل دابة أنت آخذ بناصيتها أنت الأول ليس قبلك شيء وأنت الآخر فليس بعدك شيء وأنت الظاهر فليس فوقك شيء وأنت الباطن فليس دونك شيء اقض عني الدين وأغنني من الفقر).','829','874','0');
INSERT INTO [t38171] VALUES (1085,'١٠٧٥ - (اللهم إنك خلقت نفسي وأنت تتوفاها)','829','874','0');
INSERT INTO [t38171] VALUES (1086,'١٠٧٦ - (باسمك ربي وضعت جنبي فاغفر لي ذنبي)','830','874','0');
INSERT INTO [t38171] VALUES (1087,'١٠٧٧ - (اللهم قني عذابك يوم تجمع عبادك) أي يوم النشور','830','874','0');
INSERT INTO [t38171] VALUES (1088,'١٠٧٨ - (اللهم أسلمت نفسي إليك وفوضت أمري إليك وألجأت ظهري إليك رغبة ورهبة إليك)','831','874','0');
INSERT INTO [t38171] VALUES (1089,'١٠٧٩ - (اللهم أيقظني في أحب الساعات إليك واستعملني بأحب الأعمال لديك تقربني إليك زلفي وتبعدني من سخطك بعدا','831','874','0');
INSERT INTO [t38171] VALUES (1090,'١٠٨٠ - (أصبحنا وأصبح الملك لله والعظمة والسلطان لله والقوة والقدرة لله)','832','874','0');
INSERT INTO [t38171] VALUES (1091,'١٠٨١ - (أصبحنا على فطرة الإسلام)','833','874','0');
INSERT INTO [t38171] VALUES (1092,'١٠٨٢ - (اللهم بك أصبحنا وبك أمسينا وبك نحيا وبك نموت وإليك النشور)','833','874','0');
INSERT INTO [t38171] VALUES (1093,'١٠٨٣ - (اللهم إنا نسألك أن تبعثنا في هذا اليوم إلى كل خير ونعوذ بك أن نجترح فيه)','834','874','0');
INSERT INTO [t38171] VALUES (1094,'١٠٨٤ - (روى أبو منصور الديلمي في مسند الفردوس من','834','874','0');
INSERT INTO [t38171] VALUES (1095,'١٠٨٥ - (بسم الله ما شاء الله لا قوة إلا بالله ما شاء الله كل نعمة فمن الله ما شاء الله الخير كله بيد الله ما شاء الله لا يصرف السوء إلا الله).','835','874','0');
INSERT INTO [t38171] VALUES (1096,'١٠٨٦ - (من قال حين يصبح وحين يمسي ثلاث مرات (رضيت بالله ربا وبالإسلام دينا وبمحمد - صلى الله عليه وسلم - نبيا)','835','874','0');
INSERT INTO [t38171] VALUES (1097,'١٠٨٧ - (يقول مع ذلك) في أدعية الصباح والمساء (أعوذ بكلمات الله التامات وأسمائه كلها من شر ما ذرأ وبرأ ومن شر كل ذي شر ومن شر كل دابة ربي آخد بناصيتها إن ربي على صراط مستقيم).','836','874','0');
INSERT INTO [t38171] VALUES (1098,'١٠٨٨ - (وإذا نظرت وجهك في المرآة)','837','874','0');
INSERT INTO [t38171] VALUES (1099,'١٠٨٩ - (وإذا اشتريت خادما)','838','874','0');
INSERT INTO [t38171] VALUES (1100,'١٠٩٠ - (وإذا هنأت)','838','874','0');
INSERT INTO [t38171] VALUES (1101,'١٠٩١ - (قال - صلى الله عليه وسلم - إنما جزاء السلف الحمد والأداء).','839','874','0');
INSERT INTO [t38171] VALUES (1102,'١٠٩٢ - (الدعاء والبلاء يتعالجان)','839','874','0');
INSERT INTO [t38171] VALUES (1103,'١٠٩٣ - (قال رسول الله - صلى الله عليه وسلم - أحب عباد الله إلى الله الذين يراعون الشمس والقمر والأظلة).','840','874','0');
INSERT INTO [t38171] VALUES (1104,'١٠٩٤ - ويروى عن أنس بن مالك رضي الله عنه عن النيي - صلى الله عليه وسلم - أنه قال','840','874','0');
INSERT INTO [t38171] VALUES (1105,'١٠٩٥ - (أستغفر الله الذي لا إله إلا هو الحي القيوم وأتوب إليه)','841','874','0');
INSERT INTO [t38171] VALUES (1106,'١٠٩٦ - (قال النبي - صلى الله عليه وسلم - كان فيما يذكر من رحمة الله يقول إنه يقول يا ابن آدم أذكرني من بعد صلاة الفجر ساعة وبعد صلاة العصر ساعة أكفك ما بينهما).','842','874','0');
INSERT INTO [t38171] VALUES (1107,'١٠٩٧ - (الأولى قوله لا إله إلا الله وحده لا شريك له له الملك وله الحمد يحي ويميت وهو حي لا يموت بيده الخير وهو على كل شيء قدير).','842','874','0');
INSERT INTO [t38171] VALUES (1108,'١٠٩٨ - (الثانية قوله سبحان الله العظيم والحمد لله ولا إله إلا الله والله أكبر ولا حول ولا قوة إلا بالله العلي العظيم).','844','874','0');
INSERT INTO [t38171] VALUES (1109,'١٠٩٩ - (الثالثة قوله سبوح قدوس رب الملائكة والروح).','844','874','0');
INSERT INTO [t38171] VALUES (1110,'١١٠٠ - (الرابعة قوله سبحان الله العظيم وبحمده).','844','874','0');
INSERT INTO [t38171] VALUES (1111,'١١٠١ - (الخامسة قوله أستغفر الله الذي لا إله إلا هو الحي القيوم وأسأله التوبة).','845','874','0');
INSERT INTO [t38171] VALUES (1112,'١١٠١/ أ- (السادسة قوله اللهم لا مانع لما أعطيت ولا معطى لما منعت ولا ينفع ذا الجد منك الجد).','845','1111','0');
INSERT INTO [t38171] VALUES (1113,'١١٠٢ - (السابعة قوله لا إله إلا الله الملك الحق المبين).','845','874','0');
INSERT INTO [t38171] VALUES (1114,'١١٠٣ - (الثامنة قوله بسم الله الذي لا يضر مع اسمه شيء في الأرض ولا في السماء وهو السميع العليم).','846','874','0');
INSERT INTO [t38171] VALUES (1115,'١١٠٤ - (التاسعة قوله اللهم صل على محمد عبدك ونبيك ورسولك النبي الأمي وعلى آل محمد)','846','874','0');
INSERT INTO [t38171] VALUES (1116,'١١٠٥ - (العاشرة قوله أعوذ بالله السميع العليم من الشيطان الرجيم اللهم إني أعوذ بك من همزات الشياطين وأعوذ بك رب أن يحضرون).','847','874','0');
INSERT INTO [t38171] VALUES (1117,'١١٠٦ - (وآية الكرسي)','847','874','0');
INSERT INTO [t38171] VALUES (1118,'١١٠٧ - (وخواتيم البقرة من قوله آمن الرسول)','848','874','0');
INSERT INTO [t38171] VALUES (1119,'١١٠٨ - (وشهد الله)','848','874','0');
INSERT INTO [t38171] VALUES (1120,'١١٠٩ - (وقل اللهم مالك الملك الآيتين)','848','874','0');
INSERT INTO [t38171] VALUES (1121,'١١١٠ - (وقوله تعالى لقد جاءكم رسول إلى آخرها)','849','874','0');
INSERT INTO [t38171] VALUES (1122,'١١١١ - (وقوله تعالى لقد صدق الله رسوله الرؤيا بالحق إلى آخرها)','849','874','0');
INSERT INTO [t38171] VALUES (1123,'١١١٢ - (وقوله تعالى الحمد لله الذي لم يتخذ ولدا الآية)','849','874','0');
INSERT INTO [t38171] VALUES (1124,'١١١٣ - (وخمس آيات من أول الحديد وثلاث آيات من آخر سورة الحشر)','850','874','0');
INSERT INTO [t38171] VALUES (1125,'١١١٤ - (روي عن)','850','874','0');
INSERT INTO [t38171] VALUES (1126,'١١١٥ - (قال النبي - صلى الله عليه وسلم - إن لله سبعين حجابا من نور لو كشفها لأحرقت سبحات وجهه كل من أدرك بصره)','853','874','0');
INSERT INTO [t38171] VALUES (1127,'١١١٦ - (كان رسول الله - صلى الله عليه وسلم - وأصحابه رضي الله عنهم يشتغلون في هذا الوقت بالأذكار)','853','874','0');
INSERT INTO [t38171] VALUES (1128,'١١١٧ - (وخرج رسول الله - صلى الله عليه وسلم - على أصحابه وهم يصلون عند الإشراق فنادى بأعلى صوته ألا إن صلاة الأوابين إذا رمضت الفصال) هكذا هو في القوت','853','874','0');
INSERT INTO [t38171] VALUES (1129,'١١١٨ - وعلم من سياق المصنف أن القيلولة من غير قيام الليل كالسحور من غير صيام النهار وقد روى في فصل القيلولة','854','874','0');
INSERT INTO [t38171] VALUES (1130,'١١١٩ - (وقيل هي المراد بقوله تتجافى جنوبهم عن المضاجع روي ذلك عن الحسن)','854','874','0');
INSERT INTO [t38171] VALUES (1131,'١١٢٠ - (أكثر ما روي عن رسول الله - صلى الله عليه وسلم - صلى به من الليل)','855','874','0');
INSERT INTO [t38171] VALUES (1132,'١١٢١ - لم يكن ينام حتى يقرأ سورة (السجدة وتبارك الملك)','856','874','0');
INSERT INTO [t38171] VALUES (1133,'١١٢٢ - (كان - صلى الله عليه وسلم - يقرأ المسبحات)','856','874','0');
INSERT INTO [t38171] VALUES (1134,'١١٢٣ - (في الخبر أن النبي - صلى الله عليه وسلم - كان يحب سبح اسم ربك الأعلى)','856','874','0');
INSERT INTO [t38171] VALUES (1135,'١١٢٣/ أ- (وكان النبي - صلى الله عليه وسلم - يقرأ في ثلاث ركعات الوتر ثلاث سور سبح اسم ربك الأعلى وقل يا أيها الكافرون وسورة الإخلاص).','857','1134','0');
INSERT INTO [t38171] VALUES (1136,'١١٢٤ - (نقض الوتر فقد صح فيه نهى فلا ينبغي أن ينقض).','858','874','0');
INSERT INTO [t38171] VALUES (1137,'١١٢٥ - (قال لا وتران في ليلة)','858','874','0');
INSERT INTO [t38171] VALUES (1138,'١١٢٦ - (روي أنه - صلى الله عليه وسلم - ما مات حتى كان أكثر صلاته جالسا إلا المكتوبة).','859','874','0');
INSERT INTO [t38171] VALUES (1139,'١١٢٧ - (وقد قال - صلى الله عليه وسلم - للقاعد نصف أجر القائم وللنائم نصف أجر القاعد).','859','874','0');
INSERT INTO [t38171] VALUES (1140,'١١٢٨ - (نقل) وفي نسخة فقد قيل (إنه إذا نام العبد على طهارة ذاكرا لله عز وجل)','859','874','0');
INSERT INTO [t38171] VALUES (1141,'١١٢٩ - (وفي الخبر أنه إذا نام العبد على طهارة رفعت روحه إلى العرش).','859','874','0');
INSERT INTO [t38171] VALUES (1142,'١١٣٠ - (قال - صلى الله عليه وسلم - نوم العالم عبادة ونفسه تسبيح).','860','874','0');
INSERT INTO [t38171] VALUES (1143,'١١٣١ - قال معاذ لكني أنام ثم أقوم وأحتسب في نومتي ما أحتسبه في قومتي فذكرا ذلك لرسول الله - صلى الله عليه وسلم - فقال معاذ أفقه منك).','860','874','0');
INSERT INTO [t38171] VALUES (1144,'١١٣٢ - (قال النبي - صلى الله عليه وسلم - إذا نام العبد على طهارة عرج بروحه إلى العرش فكانت رؤياه صادقة وإن لم ينم على طهارة قصرت روحه عن البلوغ فتلك المنامات أضغاث أحلام لا تصدق).','860','874','0');
INSERT INTO [t38171] VALUES (1145,'١١٣٣ - (قال - صلى الله عليه وسلم - من أتى فراشه وهو ينوي أن يقوم يصلي من الليل فغلبته عيناه حتى يصبح كتب له ما نوى وكان نومه صدقة عليه من الله تعالى).','861','874','0');
INSERT INTO [t38171] VALUES (1146,'١١٣٤ - (قال النبي - صلى الله عليه وسلم - من آوى إلى فراشه لا ينوي ظلم أحد ولا يحقد على أحد غفر له ما اجترم).','861','874','0');
INSERT INTO [t38171] VALUES (1147,'١١٣٥ - (في الخبر لا تكابدوا الليل) هكذا هو في القوت.','862','874','0');
INSERT INTO [t38171] VALUES (1148,'١١٣٦ - (وقيل للنبي - صلى الله عليه وسلم - إن فلانة تصلي بالليل فإذا غلبها النوم تعلقت بحبل فنهي عن ذلك وقال ليصل أحدكم من الليل ما تيسر له فإذا غلبه النوم فليرقد).','862','874','0');
INSERT INTO [t38171] VALUES (1149,'١١٣٧ - (قال - صلى الله عليه وسلم - خير هذا الدين أيسره).','862','874','0');
INSERT INTO [t38171] VALUES (1150,'١١٣٨ - (قيل إن فلانا يصلي فلا ينام ويصوم فلا يفطر فقال - صلى الله عليه وسلم - لكني أصلي وأنام وأصوم وأفطر هذه سنتي فمن رغب عنها فليس مني).','863','874','0');
INSERT INTO [t38171] VALUES (1151,'١١٣٩ - (قال - صلى الله عليه وسلم - لا تشادوا هذا الدين فإنه متين من يشاده يغلبه ولا تبغض إليك عبادة الله عز وجل).','863','874','0');
INSERT INTO [t38171] VALUES (1152,'١١٤٠ - (فليقل عند تيقظاته وتقلباته مهما تنبه ما كان يقوله رسول الله - صلى الله عليه وسلم - لا إله إلا الله الواحد القهار رب السماوات والأرض وما بينهما العزيز الغفار).','864','874','0');
INSERT INTO [t38171] VALUES (1153,'١١٤١ - (وردت الأخبار باهتزاز العرش وانتشار الرياح من جنات عدن ومن نزول الجبار إلى سماء الدنيا) هكذا هو لفظ القوت (وغير ذلك من الأخبار).','864','874','0');
INSERT INTO [t38171] VALUES (1154,'١١٤٢ - (الله أكبر ذي الملك الملكوت والجبروت والكبرياء والعظمة والجلال والقدرة وليقل هذه الكلمات فإنها مأثورة عن النبي - صلى الله عليه وسلم - في قيامه للتهجد اللهم لك الحمد أنت نور السماوات والأرض ولك الحمد أنت بهاء السماوات والأرض ولك الحمد أنت زين السماوات والأرض ولك الحمد أنت قيام السماوات والأرض ومن فيهن ومن عليهن أنت الحق ومنك الحق ولقاؤك حق والجنة حق والنار حق)','865','874','0');
INSERT INTO [t38171] VALUES (1155,'١١٤٣ - (اللهم آت نفسي تقواها وزكها أنت خير من زكاها أئت وليها ومولاها)','865','874','0');
INSERT INTO [t38171] VALUES (1156,'١١٤٤ - (اللهم اهدني لأحسن الأعمال لا يهدي لأحسنها إلا أنت واصرف عني سيئها ولا يصرف عني سيئها إلا أنت)','866','874','0');
INSERT INTO [t38171] VALUES (1157,'١١٤٥ - (صلى أولا ركعتين حفيفتين ثم ركع ركعتين طويلتين ثم صلى ركعتين دون اللتين قبلهما ثم لم يزل يقصر بالتدريج إلى ثلاث عشرة ركعة).','866','874','0');
INSERT INTO [t38171] VALUES (1158,'١١٤٦ - (سئلت عائشة رضي الله عنها أكان يجهر النبي - صلى الله عليه وسلم - في قيام الليل أم يسر فقالت ربما أسر وربما جهر)','866','874','0');
INSERT INTO [t38171] VALUES (1159,'١١٤٧ - (قال - صلى الله عليه وسلم -) صلاة المغرب أوترت صلاة النهار فأوتروا صلاة الليل).','866','874','0');
INSERT INTO [t38171] VALUES (1160,'١١٤٨ - (إن لنفسك عليك حقا وإن لضيفك عليك حقا وإن لأهلك عليك حقا فاعط كل ذي حق حقه وذلك أن امرأة أبي الدرداء أخبرت سلمان بأن أبا الدرداء لا ينام الليل فأتيا النبي - صلى الله عليه وسلم - فذكرا ذلك له فقال - صلى الله عليه وسلم - صدق سلمان)','867','874','0');
INSERT INTO [t38171] VALUES (1161,'١١٤٩ - (في الخبر من جمع بين هذه الأربعة غفر الله له)','867','874','0');
INSERT INTO [t38171] VALUES (1162,'١١٥٠ - (ما سأله أحد شيئا فقال لا)','868','874','0');
INSERT INTO [t38171] VALUES (1163,'١١٥١ - (في الخبر يصبح ابن آدم وعلى كل سلامي من جسده صدقة يعني كل مفصل وفي جسده ثلاثمائة وستون مفصلا فأمرك بالمعروف صدقة ونهيك عن المنكر صدقة وحملك عن الضعيف صدقة وهدايتك إلى الطريق صدقة وإماطتك الأذى صدقة حتى ذكر التسبيح والتهليل ثم قال وركعتا الضحى تأتي على ذلك كله ويجمعن لك ذلك كله)','868','874','0');
INSERT INTO [t38171] VALUES (1164,'١١٥٢ - (وفي الخبر الإيمان ثلاث وثلاثون وثلاثمائة طريقة من لقي الله عز وجل بالشهادة على طريق منها دخل الجنة)','869','874','0');
INSERT INTO [t38171] VALUES (1165,'١١٥٣ - (وقال بعض العلماء الإيمان ثلاثمائة وثلاثة عشر خلقا بعدد الرسل كل مؤمن هو على خلق منها فهو سالك للطريق إلى الله تعالى)','870','874','0');
INSERT INTO [t38171] VALUES (1166,'١١٥٤ - (قال - صلى الله عليه وسلم - من عوده الله عز وجل عبادة فتركها ملالة مقتة الله تعالى)','870','874','0');
INSERT INTO [t38171] VALUES (1167,'١١٥٥ - (في صلاته - صلى الله عليه وسلم - بعد العصر تداركا لما فاته من ركعتين شغله عنها الوفد ثم لم يزل بعد ذلك يصليهما بعد العصر ولكن في منزله لا في المسجد كي لا يقتدي به وروت ذلك عائشة وأم سلمة رضي الله عنهما)','871','874','0');
INSERT INTO [t38171] VALUES (1168,'١١٥٦ - (قال رسول الله - صلى الله عليه وسلم - فيما روت عائشة رضي الله عنها إن أفضل الصلوات عند الله عز وجل صلاة المغرب لم يحطها عن مسافر ولا مقيم)','871','874','0');
INSERT INTO [t38171] VALUES (1169,'١١٥٧ - (عن أبي هريرة رضي الله عنهما)','872','874','0');
INSERT INTO [t38171] VALUES (1170,'١١٥٨ - (وروى سعيد بن جبير عن ثوبان)','872','874','0');
INSERT INTO [t38171] VALUES (1171,'١١٥٩ - (قال - صلى الله عليه وسلم - من ركع عشر ركعات ما بين المغرب والعشاء بنى الله له قصرا في الجنة فقال عمر رضي الله عنه إذا تكثر قصورنا يا رسول الله فقال - صلى الله عليه وسلم - أكثروا أفضل أو قال أطيب)','873','874','0');
INSERT INTO [t38171] VALUES (1172,'١١٦٠ - وعن أنس بن مالك رضي الله عنه قال قال رسول الله - صلى الله عليه وسلم - من صلى المغرب في جماعة ثم صلى بعدها ركعتين ولا يتكلم فيما بين ذلك بشيء من أمر الدنيا يقرأ في الركعة الأولى بفاتحة الكتاب وعشر آيات من أول البقرة وآيتين من وسطها وإلهكم إله واحد لا إله إلا هو الرحمن الرحيم إن في خلق السماء والأرض إلى آخر الآية وقل هو الله أحد خمس عشرة مرة ثم يركع ويسجد ويقرأ في الثانية فاتحة الكتاب وآية الكرسي وآيتين بعدها إلى قوله أولئك أصحاب النار هم فيها خالدون وثلاث آيات من آخر البقرة من قوله عز وجل لله ما في السماوات وما في الأرض إلى آخرها وقل هو الله أحد خمس عشرة مرة وصف من ثوابها في الحديث ما يخرج عن الحصر)','873','874','0');
INSERT INTO [t38171] VALUES (1173,'١١٦١ - (وقال) صاحب القوت روينا عن عبد الرحمن بن منصور عن سعد بن سعيد عن (كرز بن وبرة) الحارثي نزيل جرجان (وهو من الأبدال قلت للخضر عليه السلام علمني شيئا أعمله في ليلتي فقال إذا صليت المغرب فقم إلى) وقت (صلاة العشاء مصليا)','874','874','0');
INSERT INTO [t38171] VALUES (1174,'١١٦٢ - (قيل لعبيد)','875','874','0');
INSERT INTO [t38171] VALUES (1175,'١١٦٣ - فصل إحياء بين العشاءين','875','874','0');
INSERT INTO [t38171] VALUES (1176,'١١٦٤ - (قال النبي - صلى الله عليه وسلم - يعقد الشيطان على قافية أحدكم إذا هو نام ثلاث عقد ويضرب مكان كل عقدة عليك ليل طويل فارقد فإن استيقظ وذكر)','877','874','0');
INSERT INTO [t38171] VALUES (1177,'١١٦٥ - وفي خبر آخر أنه ذكر عنده - صلى الله عليه وسلم - رجل نام الليل) كله (حتى أصبح فقال ذاك) رجل (بال الشيطان في أذنه)','877','874','0');
INSERT INTO [t38171] VALUES (1178,'١١٦٦ - (وفي الخبر إن للشيطان سعوطا) بالفتح وهو ما يسعطه الإنسان في أنفه (ولعوقا)','877','874','0');
INSERT INTO [t38171] VALUES (1179,'١١٦٧ - (قال - صلى الله عليه وسلم - ركعتان يركعهما العبد في جوف الليل الأخير) وهو ثلثه (خير له من الدنيا وما فيها).','878','874','0');
INSERT INTO [t38171] VALUES (1180,'١١٦٨ - (وقال - صلى الله عليه وسلم - أتريد أن تكون رحمة الله عليك حيا ومقبورا ومبعوثا)','878','874','0');
INSERT INTO [t38171] VALUES (1181,'١١٦٩ - (وقال - صلى الله عليه وسلم - عليكم بقيام الليل فإنه دأب الصالحين قبلكم وإن قيام الليل قربة إلى الله تعالى ومكفر للذنوب ومطردة للداء عن الجسد ومنهاة عن الإثم).','879','874','0');
INSERT INTO [t38171] VALUES (1182,'١١٧٠ - (وقال - صلى الله عليه وسلم - ما من امرئ تكون له صلاة بالليل يغلبه عليها نوم إلا كتب له أجر صلاته وكان نومه صدقة عليه).','879','874','0');
INSERT INTO [t38171] VALUES (1183,'١١٧١ - (وقال - صلى الله عليه وسلم - لأبي ذر رضي الله عنه لو أردت سفرا أعددت)','880','874','0');
INSERT INTO [t38171] VALUES (1184,'١١٧٢ - (وروى أنه كان على عهد النبي - صلى الله عليه وسلم - رجل إذا أخذ الناس مضاجعهم وهدأت العيون)','880','874','0');
INSERT INTO [t38171] VALUES (1185,'١١٧٣ - (يروى أن جبريل قال للنبي - صلى الله عليه وسلم - نعم الرجل ابن عمر لو كان يصلي بالليل فأخبره النبي - صلى الله عليه وسلم - بذلك فكان يداوم بعده على قيام الليل)','880','874','0');
INSERT INTO [t38171] VALUES (1186,'١١٧٤ - (وقيل لرسول الله - صلى الله عليه وسلم - إن فلانا يصلي بالليل فإذا أصبح سرق فقال النبي - صلى الله عليه وسلم - سينهاه ما يعمل)','881','874','0');
INSERT INTO [t38171] VALUES (1187,'١١٧٥ - (وقال - صلى الله عليه وسلم - رحم الله رجلا قام من الليل يصلي ثم أيقظ امرأته فصلت فإن أبت نضح)','881','874','0');
INSERT INTO [t38171] VALUES (1188,'١١٧٦ - (قال - صلى الله عليه وسلم - من استيقظ من الليل وأيقظ امرأته فصليا ركعتين كتبا من الذاكرين الله كثيرا والذاكرات)','881','874','0');
INSERT INTO [t38171] VALUES (1189,'١١٧٧ - (وقال عمر رضي الله عنه قال النبي - صلى الله عليه وسلم - من نام عن حزبه أو عن شيء منه بالليل فقرأه ما بين صلاة الفجر والظهر كتب له كما لو قرأه من الليل)','881','874','0');
INSERT INTO [t38171] VALUES (1190,'١١٧٨ - (وقال يوسف بن مهران)','882','874','0');
INSERT INTO [t38171] VALUES (1191,'١١٧٩ - (قالت عائشة رضي الله عنها كان رسول الله - صلى الله عليه وسلم - إذا أوتر من آخر الليل فإن كانت له حاجة إلى أهله دنا منهن)','883','874','0');
INSERT INTO [t38171] VALUES (1192,'١١٨٠ - (وقالت عائشة رضي الله عنها ما ألفيته بعد السحر الأعلى إلا نائما)','884','874','0');
INSERT INTO [t38171] VALUES (1193,'١١٨١ - (كان هذا من أخلاق رسول الله - صلى الله عليه وسلم -) ففي الخبر ما كنت تريد أن ترى رسول الله قائما إلا رأيته ولا كنت تريد أن تراه نائما إلا رأيته','884','874','0');
INSERT INTO [t38171] VALUES (1194,'١١٨٢ - (قيام رسول لله - صلى الله عليه وسلم - من حيث المقدار فلم يكن على ترتيب واحد بل ربما كان يقوم نصف لليل أو ثلثه أو سدسه) وفي بعض النسخ أو ثلثيه بعد قوله أو ثلثه (مختلف ذلك في الليالي)','884','874','0');
INSERT INTO [t38171] VALUES (1195,'١١٨٣ - (وقالت عائشة رضي الله عنها كان رسول الله - صلى الله عليه وسلم - يقوم) من الليل (إذ سمع الصارخ)','885','874','0');
INSERT INTO [t38171] VALUES (1196,'١١٨٤ - (روي عن بعض الصحابة)','885','874','0');
INSERT INTO [t38171] VALUES (1197,'١١٨٥ - وقد جاء في الأثر (صل من الليل ولو قدر حلب شاة)','885','874','0');
INSERT INTO [t38171] VALUES (1198,'١١٨٦ - (قال النبي - صلى الله عليه وسلم - للعامل في هذه الليلة حسنات مائة سنة فمن صلى فيها اثنتي عشرة ركعة يقرأ في كل ركعة فاتحة الكتاب وسورة من القرآن يتشهد في كل ركعتين ويسلم في آخرهن ثم يقول سبحان الله والحمد لله ولا إله إلا الله والله أكبر مائة مرة ويستغفر الله مائة مرة ويصلي على النبي - صلى الله عليه وسلم - مائة مرة ويدعو لنفسه بما شاء من أمر دنياه وآخرته يصبح صائما فإن الله سبحانه يستجيب دعاءه كله إلا أن يدعو في معصية)','886','874','0');
INSERT INTO [t38171] VALUES (1199,'١١٨٧ - (قال - صلى الله عليه وسلم - من أحيا ليلة العيدين لم يمت قلبه تموت القلوب)','886','874','0');
INSERT INTO [t38171] VALUES (1200,'١١٨٨ - (روى أبو هريرة) رضي الله عنه (أن رسول - صلى الله عليه وسلم - قال من صام يوم سبع وعشرين من رجب كتب الله عز وجل له صيام ستين شهرا وهو اليوم الذي هبط فيه جبريل على محمد - صلى الله عليه وسلم - بالرسالة).','887','874','0');
INSERT INTO [t38171] VALUES (1201,'١١٨٩ - (قد روي عن أنس) بن مالك رضي الله عنه (عن رسول الله - صلى الله عليه وسلم - أنه قال إذا سلم يوم الجمعة سلمت الأيام وإذا سلم شهر رمضان سلمت السنة)','887','874','0');
INSERT INTO [t38171] VALUES (1202,'ربع العادات','889','0','0');
INSERT INTO [t38171] VALUES (1203,'كتاب آداب الأكل','889','1202','0');
INSERT INTO [t38171] VALUES (1204,'١١٩٠ - (قال - صلى الله عليه وسلم - إن الرجل ليؤجر) أي يثاب (حتى في اللقمة يرفعها إلى فيه)','890','1203','0');
INSERT INTO [t38171] VALUES (1205,'١١٩١ - (قال - صلى الله عليه وسلم - الوضوء قبل الطعام ينفي الفقر وبعده ينفي اللمم)','890','1203','0');
INSERT INTO [t38171] VALUES (1206,'١١٩٢ - (كان رسول الله - صلى الله عليه وسلم - إذا أتي بطعام وضعه على الأرض).','891','1203','0');
INSERT INTO [t38171] VALUES (1207,'١١٩٣ - (وقال أنس بن مالك رحمه الله ما أكل رسول الله - صلى الله عليه وسلم - على خوان ولا في سكرجة قيل فعلى ماذا كنتم تأكلون قال على السفرة).','891','1203','0');
INSERT INTO [t38171] VALUES (1208,'١١٩٥ - (وكان يقول لا آكل متكئا إنما أنا عبد آكل كما يأكل العبد وأجلس كما يجلس).','891','1203','0');
INSERT INTO [t38171] VALUES (1209,'١١٩٦ - (قال - صلى الله عليه وسلم - ما ملأ آدمي وعاء شرا من بطنه حسب ابن آدم لقيمات يقمن صلبه فإن لم يفعل فثلث طعام وثلث شراب وثلث للنفس).','892','1203','0');
INSERT INTO [t38171] VALUES (1210,'١١٩٧ - أكرموا الخبز','892','1203','0');
INSERT INTO [t38171] VALUES (1211,'١١٩٨ - (قال - صلى الله عليه وسلم - إذا حضر العشاء).','893','1203','0');
INSERT INTO [t38171] VALUES (1212,'١١٩٩ - (قال - صلى الله عليه وسلم - اجتمعوا على طعامكم يبارك لكم فيه).','893','1203','0');
INSERT INTO [t38171] VALUES (1213,'١٢٠٠ - (وقال أنس رضي الله عنه كان رسول الله - صلى الله عليه وسلم - لا يأكل وحده).','893','1203','0');
INSERT INTO [t38171] VALUES (1214,'١٢٠١ - (كان - صلى الله عليه وسلم - لا يعيب مأكولا كان إذا أعجبه أكله وإلا تركه).','893','1203','0');
INSERT INTO [t38171] VALUES (1215,'١٢٠٢ - (قال - صلى الله عليه وسلم - كل مما يليك).','893','1203','0');
INSERT INTO [t38171] VALUES (1216,'١٢٠٣ - (كان) - صلى الله عليه وسلم - (يدور على الفاكهة فقيل له في ذلك ليس هو نوعا واحدا).','894','1203','0');
INSERT INTO [t38171] VALUES (1217,'١٢٠٤ - (قال - صلى الله عليه وسلم - أكرموا الخبز فإن الله أنزله من بركات السماء).','894','1203','0');
INSERT INTO [t38171] VALUES (1218,'١٢٠٥ - (قال - صلى الله عليه وسلم - إذا وقعت لقمة أحدكم فليأخذها وليمط ما كان بها من أذى ولا يدعها للشيطان ولا يمسح يده بالمنديل حتى يلعق أصابعه فإنه لا يدري في أي طعامه البركة).','895','1203','0');
INSERT INTO [t38171] VALUES (1219,'١٢٠٦ - (ولا ينفخ في الطعام الحار) ليبرد (فهو منهي عنه)','895','1203','0');
INSERT INTO [t38171] VALUES (1220,'١٢٠٧ - (قال - صلى الله عليه وسلم - مصوا الماء مصا)','896','1203','0');
INSERT INTO [t38171] VALUES (1221,'١٢٠٨ - (نهى عن الشرب قائما).','897','1203','0');
INSERT INTO [t38171] VALUES (1222,'١٢٠٩ - (وروي أنه - صلى الله عليه وسلم - شرب قائما).','897','1203','0');
INSERT INTO [t38171] VALUES (1223,'١٢١٠ - (قال - صلى الله عليه وسلم - من أكل ما يسقط من المائدة عاش في سعة وعوفى في ولده).','897','1203','0');
INSERT INTO [t38171] VALUES (1224,'١٢١١ - (يقال من لعق القصعة وشرب ماءها كان له عتق رقبة).','898','1203','0');
INSERT INTO [t38171] VALUES (1225,'١٢١٢ - (وإن أفطر عند قوم فليقل)','899','1203','0');
INSERT INTO [t38171] VALUES (1226,'١٢١٣ - (قوله - صلى الله عليه وسلم - كل لحم)','899','1203','0');
INSERT INTO [t38171] VALUES (1227,'١٢١٤ - (كان - صلى الله عليه وسلم - إذا خوطب في شيء ثلاثا لم يراجع بعد ثلاث).','900','1203','0');
INSERT INTO [t38171] VALUES (1228,'١٢١٥ - (وكان صلى الله عليه يكرر الكلام ثلاثا)','900','1203','0');
INSERT INTO [t38171] VALUES (1229,'١٢١٦ - (قال أنس إذا أكرمك أخوك فاقبل كرامته ولا تردها فإنما تكرم الله عز وجل).','900','1203','0');
INSERT INTO [t38171] VALUES (1230,'١٢١٧ - الاجتماع على الغسل أقرب (إلى التواضع وأبعد عن طول الانتطار)','901','1203','0');
INSERT INTO [t38171] VALUES (1231,'١٢١٨ - (قال - صلى الله عليه وسلم - لا تزال الملائكة تصلي على أحدكم) أي تستغفر له (ما دامت مائدته موضوعة)','901','1203','0');
INSERT INTO [t38171] VALUES (1232,'١٢١٩ - (قال رسول الله - صلى الله عليه وسلم - إنه قال إن الإخوان إذا رفعوا أيديهم عن الطعام لم يحاسب من أكل فضل ذلك فأنا أحب أن أستكثر مما أقدمه إليكم لنأكل فضل ذلك).','902','1203','0');
INSERT INTO [t38171] VALUES (1233,'١٢٢٠ - (وفي الخبر لا يحاسب العبد على ما يأكله مع إخوانه)','902','1203','0');
INSERT INTO [t38171] VALUES (1234,'١٢٢١ - (وكان بعضهم يكثر) من (الأكل) مع الجماعة (لذلك ويقلل) منه (إذا أكل وحده)','902','1203','0');
INSERT INTO [t38171] VALUES (1235,'١٢٢٢ - (وفي الخبر ثلاثة لا يحاسب عليها العبد أكلة السحر وما أفطر عليه والأكل مع الإخوان)','902','1203','0');
INSERT INTO [t38171] VALUES (1236,'١٢٢٣ - (في الخبر يقول الله تعالى للعبد يوم القيامة يا ابن آدم جعت فلم تطعمني فيقول كيف أطعمك وأنت رب العالمين فيقول جاع أخوك المسلم فلم تطعمه ولو أطعمته كنت أطعمتني)','903','1203','0');
INSERT INTO [t38171] VALUES (1237,'١٢٢٤ - (قال - صلى الله عليه وسلم - إذا جاءكم الزائر فأكرموه).','903','1203','0');
INSERT INTO [t38171] VALUES (1238,'١٢٢٥ - (وقال - صلى الله عليه وسلم - إن في الجنة غرفا يرى ظاهرها من باطنها وباطنها من ظاهرها)','903','1203','0');
INSERT INTO [t38171] VALUES (1239,'١٢٢٦ - (وقال - صلى الله عليه وسلم - خيركم من أطعم الطعام)','904','1203','0');
INSERT INTO [t38171] VALUES (1240,'١٢٢٧ - (قال - صلى الله عليه وسلم - من أطعم أخاه حتى يشبعه وسقاه حتى يرويه بعده الله من النار سبع خنادق ما بين كل خندقين مسيرة خمسمائة عام)','904','1203','0');
INSERT INTO [t38171] VALUES (1241,'١٢٢٨ - (وفي الخبر من مشى إلى طعام لم يدع إليه مشى فاسقا وأكل حراما)','904','1203','0');
INSERT INTO [t38171] VALUES (1242,'١٢٢٩ - (قصد رسول الله - صلى الله عليه وسلم - وأبو بكر وعمر رضي الله عنهما منزل أبي الهيثم بن التيهان)','905','1203','0');
INSERT INTO [t38171] VALUES (1243,'١٢٣٠ - (قال - صلى الله عليه وسلم - بلغت (الصدقة محلها)','905','1203','0');
INSERT INTO [t38171] VALUES (1244,'١٢٣١ - (وفي الخبر دخلنا على جابر بن عبد الله) الأنصاري رضي الله عنهما (فقدم إلينا خبزا وخلا وقال لولا أنا نهينا عن التكلف لتكلفت لكم).','905','1203','0');
INSERT INTO [t38171] VALUES (1245,'١٢٣٢ - (قال سلمان) الفارسي رضي الله عنه (أمرنا رسول الله - صلى الله عليه وسلم - أن لا نتكلف للضيف ما ليس عندنا وأن نقدم ما حضرنا).','906','1203','0');
INSERT INTO [t38171] VALUES (1246,'١٢٣٣ - (قال - صلى الله عليه وسلم - من صادف من أخيه شهوة غفر له)','906','1203','0');
INSERT INTO [t38171] VALUES (1247,'١٢٣٤ - (ومن سر أخاه المؤمن فقد سر الله تعالى)','906','1203','0');
INSERT INTO [t38171] VALUES (1248,'١٢٣٥ - (وقال - صلى الله عليه وسلم - فيما رواه) أبو الز بير عن (جابر) رضي الله عنه (من لذذ أخاه بما يشتهي كتب الله له ألف ألف حسنة ومحا عنه ألف ألف سيئة ورفع له ألف ألف درجة وأطعمه الله من ثلاث جنان جنة الفردوس وجنة عدن وجنة الخلد).','907','1203','0');
INSERT INTO [t38171] VALUES (1249,'١٢٣٦ - (قال - صلى الله عليه وسلم - لا تتكلفوا)','907','1203','0');
INSERT INTO [t38171] VALUES (1250,'١٢٣٧ - (وقال - صلى الله عليه وسلم - لا خير فيمن لا يضيف)','908','1203','0');
INSERT INTO [t38171] VALUES (1251,'١٢٣٨ - (مر رسول الله - صلى الله عليه وسلم - برجل له إبل وبقر كثيرة فلم يضيفه ومر بامرأة لها شويهات) جمع قلة شويهة وهي مصغر شارة فإضافته (فذبحت له) من تلك الشويهات (فقال - صلى الله عليه وسلم - انظروا إليها إنما هذه الأخلاق بيد الله فمن شاء أن يمنحه خلقا حسنا فعل)','908','1203','0');
INSERT INTO [t38171] VALUES (1252,'١٢٣٩ - (وقال أبو رافع مولى رسول الله - صلى الله عليه وسلم -)','908','1203','0');
INSERT INTO [t38171] VALUES (1253,'١٢٤٠ - (وكان إبراهيم الخليل صلوات الله عليه وسلامه إذا أراد أن يأكل خرج ميلا أو ميلين يطلب من يتغدى معه)','909','1203','0');
INSERT INTO [t38171] VALUES (1254,'١٢٤١ - (كان يكنى أبا الضيفان)','909','1203','0');
INSERT INTO [t38171] VALUES (1255,'١٢٤٢ - (وسئل رسول الله - صلى الله عليه وسلم - ما الإيمان فقال إطعام الطعام وبذل السلام).','910','1203','0');
INSERT INTO [t38171] VALUES (1256,'١٢٤٣ - (وقال - صلى الله عليه وسلم - في الكفارات والدرجات إطعام الطعام والصلاة بالليل والناس نيام).','910','1203','0');
INSERT INTO [t38171] VALUES (1257,'١٢٤٤ - (قال - صلى الله عليه وسلم - شر الطعام طعام الوليمة يدعى إليها الأغنياء دون الفقراء).','910','1203','0');
INSERT INTO [t38171] VALUES (1258,'١٢٤٥ - (كان - صلى الله عليه وسلم - يجيب دعوة العبد ودعوة المسكين).','910','1203','0');
INSERT INTO [t38171] VALUES (1259,'١٢٤٦ - (قال - صلى الله عليه وسلم - لو دعيت إلى كراع لأجبت ولو أهدي إلي ذراع لقبلت).','911','1203','0');
INSERT INTO [t38171] VALUES (1260,'١٢٤٧ - (ليس من السنة إجابته)','911','1203','0');
INSERT INTO [t38171] VALUES (1261,'١٢٤٨ - (وقال - صلى الله عليه وسلم - لو دعيت إلى كراع الغميم لأجبت)','911','1203','0');
INSERT INTO [t38171] VALUES (1262,'١٢٤٩ - (وهو) أي كراع الغميم (موضع على أميال من المدينة) كذا في القوت وسيأتي الكلام عليه قريبا (أفطر رسول الله - صلى الله عليه وسلم -) في رمضان (لما بلغه) كذا في القوت.','911','1203','0');
INSERT INTO [t38171] VALUES (1263,'١٢٥٠ - (قال - صلى الله عليه وسلم - لمن امتنع بعذر الصوم تكلف لك أخوك وتقول إني صائم).','912','1203','0');
INSERT INTO [t38171] VALUES (1264,'١٢٥١ - (قوله - صلى الله عليه وسلم - من أكرم أخاه المؤمن فكأنما أكرم الله).','912','1203','0');
INSERT INTO [t38171] VALUES (1265,'١٢٥٢ - (ينوي إدخال السرور عليه)','913','1203','0');
INSERT INTO [t38171] VALUES (1266,'١٢٥٣ - (قال - صلى الله عليه وسلم - إنما الأعمال بالنيات ولكل امرئ ما نوى فمن كانت هجرته إلى الله ورسوله فهجرته إلى الله ورسوله ومن كانت هجرته إلى دنيا يصيبها أو امرأة ينكحها فهجرته إلى ما هاجر إليه).','913','1203','0');
INSERT INTO [t38171] VALUES (1267,'١٢٥٤ - (قال - صلى الله عليه وسلم - إن من التواضع لله الرضا بالدون في المجلس).','915','1203','0');
INSERT INTO [t38171] VALUES (1268,'١٢٥٥ - (قال رسول الله - صلى الله عليه وسلم - هذان حرام على ذكور أمتي).','916','1203','0');
INSERT INTO [t38171] VALUES (1269,'١٢٥٦ - (قال - صلى الله عليه وسلم - من كان يؤمن بالله واليوم الآخر فليكرم ضيفه).','916','1203','0');
INSERT INTO [t38171] VALUES (1270,'١٢٥٧ - (وقال حاتم الأصم): (العبجلة من الشيطان إلا في خمسة فإنها من سنة رسول الله - صلى الله عليه وسلم - إطعام الطعام وتجهيز الميت وتزويج البكر وقضاء الدين والتوبة من الذنب).','917','1203','0');
INSERT INTO [t38171] VALUES (1271,'١٢٥٨ - (قال - صلى الله عليه وسلم - فضل عائشة على النساءكفضل الثريد على سائر الطعام).','919','1203','0');
INSERT INTO [t38171] VALUES (1272,'١٢٥٩ - (قال - صلى الله عليه وسلم - سيد الإدام اللحم).','919','1203','0');
INSERT INTO [t38171] VALUES (1273,'١٢٦٠ - (قال ابن مسعود رضي الله عنه نهينا أن نجيب دعوة من يباهي بطعامه).','920','1203','0');
INSERT INTO [t38171] VALUES (1274,'١٢٦١ - (كان لا يرفع من بين يدي رسول الله - صلى الله عليه وسلم - فضلة طعام قط)','920','1203','0');
INSERT INTO [t38171] VALUES (1275,'١٢٦٢ - (وقال - صلى الله عليه وسلم - إن من سنة الضيف أن يشيع إلى باب الدار).','920','1203','0');
INSERT INTO [t38171] VALUES (1276,'١٢٦٣ - (قال - صلى الله عليه وسلم - إن الرجل ليدرك بحسن خلقه درجة الصائم القائم).','920','1203','0');
INSERT INTO [t38171] VALUES (1277,'١٢٦٤ - (قال - صلى الله عليه وسلم - الضيافة ثلاثة أيام فما زاد فصدقة).','920','1203','0');
INSERT INTO [t38171] VALUES (1278,'١٢٦٥ - (قال رسول الله - صلى الله عليه وسلم - فراش للرجل وفراش للمرأة وفراش للضيف والرابع للشيطان).','921','1203','0');
INSERT INTO [t38171] VALUES (1279,'١٢٦٦ - (حكي عن إبراهيم)','921','1203','0');
INSERT INTO [t38171] VALUES (1280,'١٢٦٧ - (وقد نقل ضده عن ابن عمر رضي الله عنهما قال كنا نأكل على عهد رسول الله - صلى الله عليه وسلم - ونحن نمشي ونشرب ونحن قيام)','923','1203','0');
INSERT INTO [t38171] VALUES (1281,'١٢٦٨ - (و) بالسند في القوت إلى أمير المؤمنين (قال السمك يذيب الجسد)','923','1203','0');
INSERT INTO [t38171] VALUES (1282,'١٢٦٩ - (في الخبر قطع العروق مسقمة)','924','1203','0');
INSERT INTO [t38171] VALUES (1283,'١٢٧٠ - وفي الخبر (رأى رسول الله - صلى الله عليه وسلم - صهيبا) هو ابن سنان المعروف بالرومي رضي الله عنه من نجباء الصحابة (وإحدى عينيه رمدة وهو يأكل التمر فقال تأكل التمر وأنت رمد فقال يا رسول الله إنما أمضغ بالشق الآخر يعني جانب) (العين) (السليمة فضحك رسول الله - صلى الله عليه وسلم -)','925','1203','0');
INSERT INTO [t38171] VALUES (1284,'١٢٧١ - (قال رسول الله - صلى الله عليه وسلم - إن آل جعفر شغلوا بميتهم عن صنيع طعامهم فاحملوا إليهم ما يأكلون).','925','1203','0');
INSERT INTO [t38171] VALUES (1285,'١٢٧٢ - (قال الشافعي رضي الله عنه الأكل على أربعة أنحاء)','925','1203','0');
INSERT INTO [t38171] VALUES (1286,'كتاب آداب النكاح','926','1202','0');
INSERT INTO [t38171] VALUES (1287,'١٢٧٣ - (قوله - صلى الله عليه وسلم - النكاح سنتي فمن أحب فطرتي فليستن بسنتي)','927','1286','0');
INSERT INTO [t38171] VALUES (1288,'١٢٧٤ - (قال - صلى الله عليه وسلم - تناكحوا) لكي (تكثروا فإني أباهي بكم) أي أفاخر بسبب كثرتكم (الأمم) السالفة (يوم القيامة)','927','1286','0');
INSERT INTO [t38171] VALUES (1289,'١٢٧٥ - (وقال - صلى الله عليه وسلم - من رغب عن سنتي فليس مني وإن من','927','1286','0');
INSERT INTO [t38171] VALUES (1290,'١٢٧٦ - (قال - صلى الله عليه وسلم - من ترك التزويج مخافة العيلة)','928','1286','0');
INSERT INTO [t38171] VALUES (1291,'١٢٧٧ - (قال - صلى الله عليه وسلم - من كان ذا طول فليتزوج)','928','1286','0');
INSERT INTO [t38171] VALUES (1292,'١٢٧٨ - (قال - صلى الله عليه وسلم - من استطاع منكم الباءة فليتزوج فإنه أغض للبصر وأحصن للفرج ومن لا فليصم فإن الصوم له وجاء)','928','1286','0');
INSERT INTO [t38171] VALUES (1293,'١٢٧٩ - قال - صلى الله عليه وسلم - إذا أتاكم من ترضون دينه وأمانته فزوجوه إلا تفعلوه تكن فتنة في الأرض وفساد كبير.','929','1286','0');
INSERT INTO [t38171] VALUES (1294,'١٢٨١ - (قال - صلى الله عليه وسلم - من تزوج فقد أحرز شطر دينه فليتق الله في الشطر الثاني)','930','1286','0');
INSERT INTO [t38171] VALUES (1295,'١٢٨٢ - (وكان بعض الصحابة قد انقطع إلى رسول الله - صلى الله عليه وسلم - يخدمه ويبيت عنده لحاجة إن طرقته)','930','1286','0');
INSERT INTO [t38171] VALUES (1296,'١٢٨٣ - (قال - صلى الله عليه وسلم - خير الناس بعد المائتين)','931','1286','0');
INSERT INTO [t38171] VALUES (1297,'١٢٨٤ - (وقال - صلى الله عليه وسلم - يأتي على الناس زمان يكون هلاك الرجل على يد زوجته وأبويه وولده يعيرونه بالفقر يكلفونه ما لا يطيق فيدخل المداخل التي يذهب فيها دينه فيهلك).','932','1286','0');
INSERT INTO [t38171] VALUES (1298,'١٢٨٥ - (في الخبر قلة العيال أحد اليسارين وكثرتهم أحد الفقرين)','933','1286','0');
INSERT INTO [t38171] VALUES (1299,'١٢٨٦ - (وإنه تعالى يقول ما ترددت في شيء كترددي في قبض روح عبدي المسلم هو يكره الموت وأنا أكره مساءته ولا بد من الموت).','933','1286','0');
INSERT INTO [t38171] VALUES (1300,'١٢٨٧ - (وقال - صلى الله عليه وسلم - لحصير في ناحية البيت خير من امرأة لا تلد)','934','1286','0');
INSERT INTO [t38171] VALUES (1301,'١٢٨٨ - (وقال - صلى الله عليه وسلم - خير نسائكم الولود الودود)','934','1286','0');
INSERT INTO [t38171] VALUES (1302,'١٢٨٩ - (وقال - صلى الله عليه وسلم - سوداء ولود خير من حسناء لا تلد)','935','1286','0');
INSERT INTO [t38171] VALUES (1303,'١٢٩٠ - (في الخبر أن الأدعية تعرض على الموتى على أطباق من نور)','935','1286','0');
INSERT INTO [t38171] VALUES (1304,'١٢٩١ - (روي عن رسول الله - صلى الله عليه وسلم - أنه قال إن الطفل يجر بأبويه إلى الجنة).','935','1286','0');
INSERT INTO [t38171] VALUES (1305,'١٢٩٢ - (قال - صلى الله عليه وسلم - أيضا إن المولود يقال له ادخل الجنة فيقف على باب الجنة فيظل محبنطئا).','936','1286','0');
INSERT INTO [t38171] VALUES (1306,'١٢٩٣ - (في خبر آخر أن الأطفال يجمعون في موقف)','936','1286','0');
INSERT INTO [t38171] VALUES (1307,'(١٢٩٤) - (قال - صلى الله عليه وسلم - من مات له اثنان من الولد فقد احتظر بحظار من النار).','937','1286','0');
INSERT INTO [t38171] VALUES (1308,'١٢٩٥ - (وقال - صلى الله عليه وسلم - من مات له ثلاثة ولم يبلغوا الحنث أدخل الجنة بفضل رحمته إياهم قيل يا رسول الله واثنان قال واثنان).','937','1286','0');
INSERT INTO [t38171] VALUES (1309,'١٢٩٦ - (بقوله - صلى الله عليه وسلم - ما رأيت ناقصات عقل ودين أغلب لذوي الألباب منكن).','938','1286','0');
INSERT INTO [t38171] VALUES (1310,'١٢٩٧ - (وقال - صلى الله عليه وسلم - في دعائه اللهم إني أعوذ بك من شر سمعي وبصري وقلبي وشر منيى).','938','1286','0');
INSERT INTO [t38171] VALUES (1311,'١٢٩٨ - (قال - صلى الله عليه وسلم - أسألك أن تطهر قلبي وتحفظ فرجي)','938','1286','0');
INSERT INTO [t38171] VALUES (1312,'١٢٩٩ - (أمر رسول الله - صلى الله عليه وسلم - كل من وقع بصره على امرأة فتاقت إليها نفسه أن يجامع أهله لأن ذلك يرفع الوسواس عن النفس).','939','1286','0');
INSERT INTO [t38171] VALUES (1313,'١٣٠٠ - (قال إن المرأة إذا أقبلت أقبلت في صورة شيطان فإذا رأى أحدكم امرأة فأعجبته فليأت أهله فإن معها مثل الذي معها).','939','1286','0');
INSERT INTO [t38171] VALUES (1314,'١٣٠١ - (قال عليه السلام لا تدخلوا على المغيبات وهي التي غاب زوجها عنها فإن الشيطان يجري من أحدكم مجرى الدم قلنا ومنك قال ومني ولكن الله أعانني عليه فأسلم).','939','1286','0');
INSERT INTO [t38171] VALUES (1315,'١٣٠٢ - (قال ابن عباس) رضي الله عنه (خير هذه الأمة أكثرها نساء)','940','1286','0');
INSERT INTO [t38171] VALUES (1316,'١٣٠٣ - (قال - صلى الله عليه وسلم - أشبهت خلقي وخلقي).','940','1286','0');
INSERT INTO [t38171] VALUES (1317,'١٣٠٤ - (وقال - صلى الله عليه وسلم - حسن مني وحسين من علي) كذا في القوت.','940','1286','0');
INSERT INTO [t38171] VALUES (1318,'١٣٠٥ - (وفي الخبر على العاقل أن تكون له ثلاث ساعات','940','1286','0');
INSERT INTO [t38171] VALUES (1319,'١٣٠٦ - (وقال - صلى الله عليه وسلم - لكل عامل شرة ولكل شرة فترة فمن كانت فترته إلى سنتي فقد اهتدى).','941','1286','0');
INSERT INTO [t38171] VALUES (1320,'١٣٠٧ - (في بعض الأخبار عن رسول الله - صلى الله عليه وسلم - إنه قال شكوت إلى جبريل عليه السلام ضعفي عن الوقاع فدلني على الهريسة).','942','1286','0');
INSERT INTO [t38171] VALUES (1321,'١٣٠٨ - (وقال عليه الصلاة والسلام حبب إلي من دنياكم ثلاث الطيب والنساء وقرة عيني في الصلاة).','944','1286','0');
INSERT INTO [t38171] VALUES (1322,'١٣٠٩ - (قال عليه الصلاة والسلام ليتخذ أحدكم قلبا شاكرا ولسانا ذاكرا وزوجة مؤمنة صالحة).','946','1286','0');
INSERT INTO [t38171] VALUES (1323,'١٣١٠ - (قال - صلى الله عليه وسلم - فضلت على آدم عليه السلام بخصلتين كانت زوجته عونا له على المعصية وأزواجي عونا لي على الطاعات وكان شيطانه كافرا وشيطاني مسلم لا يأمر إلا بخير).','947','1286','0');
INSERT INTO [t38171] VALUES (1324,'١٣١١ - (قال - صلى الله عليه وسلم - يوم من وال عادل أفضل من عبادة سبعين سنة).','948','1286','0');
INSERT INTO [t38171] VALUES (1325,'١٣١٢ - (قال - صلى الله عليه وسلم - ما أنفق الرجل علي أهله فهو صدقة وإن الرجل ليؤجر في رفعه اللقمة إلى في امرأته)','948','1286','0');
INSERT INTO [t38171] VALUES (1326,'١٣١٣ - (قال - صلى الله عليه وسلم - من حسنت صلاته وكثرت عياله وقل ماله ولم يغتب المسلمين كان معي في الجنة كهاتين)','948','1286','0');
INSERT INTO [t38171] VALUES (1327,'١٣١٤ - (وفي حديث آخر إن الله تعالى يحب الفقير المتعفف أبا العيال)','948','1286','0');
INSERT INTO [t38171] VALUES (1328,'١٣١٥ - (وفي الحديث إذا كثرت ذنوب العبد ابتلاه الله بالهم ليكفرها)','949','1286','0');
INSERT INTO [t38171] VALUES (1329,'١٣١٦ - (عن رسول الله - صلى الله عليه وسلم - قال من الذنوب ذنوب لا يكفرها إلا الهم بطلب المعيشة)','949','1286','0');
INSERT INTO [t38171] VALUES (1330,'١٣١٧ - (وقال - صلى الله عليه وسلم - من كان له ثلاث بنات فأنفق عليهن وأحسن إليهن حتى يغنيهن الله عنه أوجب الله له الجنة ألبتة البتة إلا أن يعمل عملا لا يغفر له)','950','1286','0');
INSERT INTO [t38171] VALUES (1331,'١٣١٨ - (وفي الخبر إن العبد ليوقف عند الميزان وله من الحسنات أمثال الجبال) في الكثرة (فيسأل عن رعاية عياله والقيام بهن و) يسأل أيضا (عن ماله من أين اكتسبه وفيما أنفقه حتى يستغرق بتلك المطالبات كل أعماله فلا تبقى حسنة فتنادي الملائكة) على رؤس الخلائق (هذا الذي أكل عياله حسناته في الدنيا وارتهن اليوم بأعماله)','951','1286','0');
INSERT INTO [t38171] VALUES (1332,'١٣١٩ - (وقال - صلى الله عليه وسلم - لا يلقى الله تعالى أحد بذنب أعظم من جهالة أهله)','951','1286','0');
INSERT INTO [t38171] VALUES (1333,'١٣٢٠ - (قال - صلى الله عليه وسلم - كفى بالمرء إثما أن يضيع من يعول)','951','1286','0');
INSERT INTO [t38171] VALUES (1334,'١٣٢١ - (كان ينزل عليه الوحي وهو في فراش امرأته)','952','1286','0');
INSERT INTO [t38171] VALUES (1335,'١٣٢٢ - (قالت عائشة رضي الله عنها تزوجني رسول الله - صلى الله عليه وسلم - في شوال وبنى بي في شوال)','952','1286','0');
INSERT INTO [t38171] VALUES (1336,'١٣٢٣ - (قال يا رسول الله لي امرأة لا ترد يد لامس)','952','1286','0');
INSERT INTO [t38171] VALUES (1337,'١٣٢٤ - (قال تنكح المرأة لمالها وجمالها وحسبها ودينها والعبرة بذات الدين).','952','1286','0');
INSERT INTO [t38171] VALUES (1338,'١١٢٥ - (في حديث آخر من نكح المرأة لمالها وجمالها حرم مالها وجمالها ومن نكحها لدينها رزقه الله مالها وجمالها)','953','1286','0');
INSERT INTO [t38171] VALUES (1339,'١٣٢٦ - (قال - صلى الله عليه وسلم - لا تنكح المرأة لجمالها فلعل جمالها يرديها)','953','1286','0');
INSERT INTO [t38171] VALUES (1340,'١٣٢٧ - (قوله - صلى الله عليه وسلم - إن الله يبغض الثرثارين المتشدقين)','953','1286','0');
INSERT INTO [t38171] VALUES (1341,'١٣٢٨ - (قال إذا أوقع الله في نفس أحدكم من امرأة فلينظر إليها فإنه أحرى أن يؤدم بينهما).','954','1286','0');
INSERT INTO [t38171] VALUES (1342,'١٣٢٩ - (وقال - صلى الله عليه وسلم - إن في أعين الأنصار شيئا فإذا أراد أحدكم أن يتزوج منهن فلينظر إليهن)','954','1286','0');
INSERT INTO [t38171] VALUES (1343,'١٣٣٠ - (وقال - صلى الله عليه وسلم - خير النساء أحسنهن وجوها وأرخصهن مهورا)','954','1286','0');
INSERT INTO [t38171] VALUES (1344,'١٣٣١ - (تزوج رسول الله - صلى الله عليه وسلم - بعض نسائه على عشرة دراهم وأثاث البيت وكان)','955','1286','0');
INSERT INTO [t38171] VALUES (1345,'١٣٣٢ - (وأولم - صلى الله عليه وسلم - على بعض نسائه بمدين من شعير) رواه البخاري من حديث عائشة.','955','1286','0');
INSERT INTO [t38171] VALUES (1346,'١٣٣٣ - (وأولم على امرأة أخرى بمدي تمر ومدي سويق)','955','1286','0');
INSERT INTO [t38171] VALUES (1347,'١٣٣٤ - (وكان عمر بن) الخطاب (رضي الله عنه ينهى عن المغالاة)','955','1286','0');
INSERT INTO [t38171] VALUES (1348,'١٣٣٥ - (وتزوج بعض أصحاب رسول الله - صلى الله عليه وسلم - على نواة من ذهب يقال قيمتها خمسة دراهم)','956','1286','0');
INSERT INTO [t38171] VALUES (1349,'١٣٣٦ - (في الخبر من بركة المرأة سرعة تزويجها وسرعة رحمها أي الولادة ويسر مهرها)','956','1286','0');
INSERT INTO [t38171] VALUES (1350,'١٣٣٧ - (وقال - صلى الله عليه وسلم - أبركهن أقلهن مهرا)','957','1286','0');
INSERT INTO [t38171] VALUES (1351,'١٣٣٨ - (قال - صلى الله عليه وسلم - تهادوا تحابوا)','957','1286','0');
INSERT INTO [t38171] VALUES (1352,'١٣٣٩ - (قال - صلى الله عليه وسلم - عليكم بالولود الودود)','958','1286','0');
INSERT INTO [t38171] VALUES (1353,'١٣٤٠ - (قال - صلى الله عليه وسلم - لجابر وقد نكح ثيبا هلا بكرا تلاعبها وتلاعبك)','958','1286','0');
INSERT INTO [t38171] VALUES (1354,'١٣٤١ - وقال عليه السلام تخيروا لنطفكم فإن العرق نزاع','959','1286','0');
INSERT INTO [t38171] VALUES (1355,'١٣٤٢ - قال عليه السلام إياكم وخضراء الدمن فقيل ما خضراء الدمن قال المرأة الحسناء في المنبت السوء.','959','1286','0');
INSERT INTO [t38171] VALUES (1356,'١٣٤٣ - (قال رسول الله - صلى الله عليه وسلم - لا تنكحوا القرابة القريبة فإن الولد يخلق ضاويا).','959','1286','0');
INSERT INTO [t38171] VALUES (1357,'١٣٤٤ - (قال - صلى الله عليه وسلم - النكاح رق)','960','1286','0');
INSERT INTO [t38171] VALUES (1358,'١٣٤٥ - (قال - صلى الله عليه وسلم - من زوج كريمته من فاسق فقد قطع رحمها).','960','1286','0');
INSERT INTO [t38171] VALUES (1359,'١٣٤٦ - (فقال بارك الله لك أولم ولو بشاة).','960','1286','0');
INSERT INTO [t38171] VALUES (1360,'١٣٤٧ - (قال - صلى الله عليه وسلم - فصل ما بين الحرام والحلال الدف والصوت).','961','1286','0');
INSERT INTO [t38171] VALUES (1361,'١٣٤٨ - (وقال رسول الله - صلى الله عليه وسلم - أعلنوا هذا النكاح واجعلوه في المساجد واضربوا عليه بالدفوف).','961','1286','0');
INSERT INTO [t38171] VALUES (1362,'١٣٤٩ - (وعن الربيع بنت معوذ قالت جاء رسول الله - صلى الله عليه وسلم - فدخل على غداة بني بي فجلس على فراشي وجويريات لنا يضربن بدفهن ويندبن من قتل من آبائي إلى أن قالت إحداهن وفينا نبي','961','1286','0');
INSERT INTO [t38171] VALUES (1363,'١٣٥٠ - (وآخر ما وصى به رسول الله - صلى الله عليه وسلم - ثلاث كان يتكلم بهن حتى تلجلج لسانه وخفي كلامه جعل يقول الصلاة الصلاة وما ملكت أيمانكم لا تكلفوهم ما لا يطيقون الله الله في النساء فإنهن عوان في أيديكم يعني أسراء أخذتموهن بأمانة الله واستحللتم فروجهن بكلمة الله).','962','1286','0');
INSERT INTO [t38171] VALUES (1364,'١٣٥١ - (قال - صلى الله عليه وسلم - من صبر على سوء خلق امرأته أعطاه الله من الأجر مثل ما أعطى أيوب عليه السلام على بلائه ومن صبرت على سوء خلق زوجها أعطاها الله مثل ما أعطى آسية امرأة فرعون).','963','1286','0');
INSERT INTO [t38171] VALUES (1365,'١٣٥٢ - (كان أزواجه يراجعنه الكلام وتهجره الواحدة منهن يوما إلى الليل).','963','1286','0');
INSERT INTO [t38171] VALUES (1366,'١٣٥٣ - (ودفعت إحداهن) أي من الزوجات (في صدر رسول الله - صلى الله عليه وسلم - فزبرتها) أي زجرتها ونهتها (أمها فقال - صلى الله عليه وسلم - دعيها) أي اتركيها (فإنهن يصنعن أكثر من ذلك)','963','1286','0');
INSERT INTO [t38171] VALUES (1367,'١٣٥٤ - (وجرى بينه) - صلى الله عليه وسلم - (وبين عائشة)','963','1286','0');
INSERT INTO [t38171] VALUES (1368,'١٣٥٥ - (وقالت)','964','1286','0');
INSERT INTO [t38171] VALUES (1369,'١٣٥٦ - (وكان يقول لها إني لأعرف غضبك علي من رضاك قالت وكيف تعرفه قال إذا رضيت قلت لا وإله محمد وإذا غضبت قلت لا وإله إبراهيم قالت صدقت إنما أهجر اسمك)','964','1286','0');
INSERT INTO [t38171] VALUES (1370,'١٣٥٧ - (يقال إن أول حب وقع في الإسلام حب النبي - صلى الله عليه وسلم - عائشة)','965','1286','0');
INSERT INTO [t38171] VALUES (1371,'١٣٥٨ - حديث كنت لك كأبي زرع لأم زرع.','965','1286','0');
INSERT INTO [t38171] VALUES (1372,'١٣٥٩ - (وكان - صلى الله عليه وسلم - يقول لنسائه لا تؤذوني في عائشة فإنه والله ما أنزل علي الوحي وأنا في لحاف امرأة منكن غيرها)','965','1286','0');
INSERT INTO [t38171] VALUES (1373,'١٣٦٠ - (وقال أنس) بن مالك (رضي الله عنه كان رسول الله - صلى الله عليه وسلم - أرحم الناس بالنساء والصبيان).','966','1286','0');
INSERT INTO [t38171] VALUES (1374,'١٣٦١ - (روى أنه - صلى الله عليه وسلم - كان يسابق عائشة) رضي الله عنها (في العدو) وهو الجري الشديد (فسبقته يوما وسبقها في بعض الأيام فقال هذه بتلك).','966','1286','0');
INSERT INTO [t38171] VALUES (1375,'١٣٦٢ - (وفي الخبر أنه - صلى الله عليه وسلم - كان من أفكه الناس) إذا خلا (مع نسائه) كذا في القوت','966','1286','0');
INSERT INTO [t38171] VALUES (1376,'١٣٦٣ - (وقالت عائشة رضي الله عنها سمعت أصوات أناس من الحبشة وغيرهم)','967','1286','0');
INSERT INTO [t38171] VALUES (1377,'١٣٦٤ - (وقال - صلى الله عليه وسلم - أكمل المؤمنين إيمانا أحسنهم خلقا وألطفهم بأهله)','967','1286','0');
INSERT INTO [t38171] VALUES (1378,'١٣٦٥ - (وقال - صلى الله عليه وسلم - خياركم خياركم لنسائه وأنا خياركم لنسائي)','968','1286','0');
INSERT INTO [t38171] VALUES (1379,'١١٦٦ - (إن الله يبغض الجعظري الجواظ)','968','1286','0');
INSERT INTO [t38171] VALUES (1380,'١٣٦٧ - (قيل شاوروهن وخالفوهن)','968','1286','0');
INSERT INTO [t38171] VALUES (1381,'١٣٦٨ - (قال - صلى الله عليه وسلم - تعس عبد الزوجة)','969','1286','0');
INSERT INTO [t38171] VALUES (1382,'١٣٦٩ - (قال - صلى الله عليه وسلم - مثل المرأة الصالحة)','969','1286','0');
INSERT INTO [t38171] VALUES (1383,'١٣٧٠ - (قال - صلى الله عليه وسلم - استعيذوا) بالله (من الفواقر الثلاث)','969','1286','0');
INSERT INTO [t38171] VALUES (1384,'١٣٧١ - (وقال - صلى الله عليه وسلم - في خيرات النساء) أي خيارهن (إنهن صواحبات يوسف).','970','1286','0');
INSERT INTO [t38171] VALUES (1385,'١٣٧٢ - (وقال - صلى الله عليه وسلم - لا يفلح قوم تملكهم امرأة)','970','1286','0');
INSERT INTO [t38171] VALUES (1386,'١٣٧٣ - (نهى رسول الله - صلى الله عليه وسلم - أن تتبع عورات النساء وفي لفظ آخر أن يتعنت النساء)','971','1286','0');
INSERT INTO [t38171] VALUES (1387,'١٣٧٤ - (ولما قدم رسول الله - صلى الله عليه وسلم - من سفر)','971','1286','0');
INSERT INTO [t38171] VALUES (1388,'١٣٧٥ - (وفي الخبر المشهور المرأة كالضلع)','971','1286','0');
INSERT INTO [t38171] VALUES (1389,'١٣٧٦ - (قال - صلى الله عليه وسلم - غيرة يبغضها الله وهي غيرة الرجل على أهله من غير ريبة)','972','1286','0');
INSERT INTO [t38171] VALUES (1390,'١٣٧٧ - (قال - صلى الله عليه وسلم - إن الله تعالى يغار والمؤمن يغار وغيرة الله أن يأتي المؤمن ما حرم الله عليه).','972','1286','0');
INSERT INTO [t38171] VALUES (1391,'١٣٧٨ - (وقال رسول الله - صلى الله عليه وسلم - رأيت قصرا في الجنة).','972','1286','0');
INSERT INTO [t38171] VALUES (1392,'١٣٧٩ - (قال - صلى الله عليه وسلم - إن من الغيرة ما يحبه الله ومنها ما يبغضه الله ومن الخيلاء ما يحبه الله ومنها ما يبغضه الله فأما الغيرة التي يحبها الله فالغيرة في الريبة والغيرة التي يبغضها الله فالغيرة في غير الريبة والاختيال الذي يحبه الله اختيال الرجل بنفسه عند القتال وعند الصدمة الأولى والإختيال الذي يبغضه الله الاختيال في الباطل).','974','1286','0');
INSERT INTO [t38171] VALUES (1393,'١٣٨٠ - (قال - صلى الله عليه وسلم - إني لغيور وما من امرئ لا يغار إلا منكوس القلب).','974','1286','0');
INSERT INTO [t38171] VALUES (1394,'١٣٨١ - (قال رسول الله - صلى الله عليه وسلم - لابنته فاطمة رضي الله عنها أي شيء خير للمرأة قالت أن لا ترى رجلا ولا يراها رجل فضمها إليه','974','1286','0');
INSERT INTO [t38171] VALUES (1395,'١٣٨٢ - (قال عمر رضي الله عنه أعروا النساء).','975','1286','0');
INSERT INTO [t38171] VALUES (1396,'١٣٨٣ - (أذن رسول الله - صلى الله عليه وسلم - للنساء في حضور المساجد).','975','1286','0');
INSERT INTO [t38171] VALUES (1397,'١٣٨٤ - (قالت عائشة رضي الله عنها لو علم النبي - صلى الله عليه وسلم - ما أحدث النساء لمنعهن من الخروج).','975','1286','0');
INSERT INTO [t38171] VALUES (1398,'١٣٨٥ - (وقال عمر رضي الله عنه قال رسول الله - صلى الله عليه وسلم - لا تمنعوا إماء الله مساجد الله فقال بعض ولده) أي ولد عمر (بل نمنعهن فضربه وغضب عليه وقال تسمعني أقول قال رسول الله - صلى الله عليه وسلم - لا تمنعوا فتقول بلى).','976','1286','0');
INSERT INTO [t38171] VALUES (1399,'١٣٨٦ - (كان رسول الله - صلى الله عليه وسلم - قد أذن لهن في الأعياد خاصة إن يخرجن).','976','1286','0');
INSERT INTO [t38171] VALUES (1400,'١٣٨٧ - (قال - صلى الله عليه وسلم - خيركم خيركم لأهله).','976','1286','0');
INSERT INTO [t38171] VALUES (1401,'١٣٨٨ - (قال - صلى الله عليه وسلم - دينار أنفقته في سبيل الله ودينار أنفقته في رقبة) أي في فكها (ودينار تصدقت به على مسكين ودينار أنفقته على أهلك أعظمها أجر الذي تنفقه على أهلك).','977','1286','0');
INSERT INTO [t38171] VALUES (1402,'١٣٨٩ - (قال رسول الله - صلى الله عليه وسلم - من كان له امرأتان فمال إلى إحداهن دون الأخرى وفي لفظ لم يعدل بينهما جاء يوم القيامة وأحد شقيه مائل)','977','1286','0');
INSERT INTO [t38171] VALUES (1403,'١٣٩٠ - (يقول اللهم هذا جهدي فيما أملك ولا طاقة لي فيما تملك ولا أملك)','977','1286','0');
INSERT INTO [t38171] VALUES (1404,'١٣٩١ - (كان يطاف به محمولا في مرضه في كل يوم وكل ليلة','977','1286','0');
INSERT INTO [t38171] VALUES (1405,'١٣٩٢ - (وكان لا يقسم لها ويقسم لعائشة ليلتين ولسائر أزواجه ليلة ليلة)','978','1286','0');
INSERT INTO [t38171] VALUES (1406,'١٣٩٣ - (روى عن عائشة رضي الله عنها أن رسول الله - صلى الله عليه وسلم - طاف على نسائه في ليلة واحدة)','979','1286','0');
INSERT INTO [t38171] VALUES (1407,'١٣٩٤ - (وعن أنس رضي الله عنه أنه - صلى الله عليه وسلم - طاف على تسع نسوة ضحوة نهار)','979','1286','0');
INSERT INTO [t38171] VALUES (1408,'١٣٩٥ - (وقد قيل لرسول الله - صلى الله عليه وسلم -)','979','1286','0');
INSERT INTO [t38171] VALUES (1409,'١٣٩٦ - (إذا أرسل بهدية إلى بيت زينب)','980','1286','0');
INSERT INTO [t38171] VALUES (1410,'١٣٩٧ - وقال عليه السلام لو أن أحدكم إذا أتى أهله وقال اللهم جنبني الشيطان وجنب الشيطان ما رزقتنا فإن كان بينهما ولد لم يضره الشيطان.','980','1286','0');
INSERT INTO [t38171] VALUES (1411,'١٣٩٨ - (كان رسول الله - صلى الله عليه وسلم -) إذا أراد الجماع (يغطي رأسه ويغض صوته) أي يخفضه (ويقول للمرأة عليك السكينة)','980','1286','0');
INSERT INTO [t38171] VALUES (1412,'١٣٩٩ - (في الخبر إذا جامع أحدكم أهله) أي حليلته (فلا يتجردا)','980','1286','0');
INSERT INTO [t38171] VALUES (1413,'١٤٠٠ - (قال - صلى الله عليه وسلم - لا يقعن أحدكم على امرأته كما تقع البهيمة)','980','1286','0');
INSERT INTO [t38171] VALUES (1414,'١٤٠١ - (وقال - صلى الله عليه وسلم - ثلاث خصال من العجز في الرجل أن يلقى من يحب معرفته فيفارقه قبل أن يعرف اسمه ونسبه والثاني أن يكرمه أخوه فيرد عليه كرامته والثالث أن يقارب الرجل جاريته فيصيبها قبل أن يحدثها ويؤانسها ويضاجعها فيقضي حاجته منها قبل أن تقضي حاجتها منه)','981','1286','0');
INSERT INTO [t38171] VALUES (1415,'١٤٠٢ - (قال عبد الله بن عمر رضي الله عنهما قلت للنبي - صلى الله عليه وسلم - أينام أحدنا وهو جنب قال نعم إذا توضأ)','981','1286','0');
INSERT INTO [t38171] VALUES (1416,'١٤٠٣ - قالت عائشة رضي الله عنها كان النبي - صلى الله عليه وسلم - وسلم ينام جنبا لم يمس ماء)','981','1286','0');
INSERT INTO [t38171] VALUES (1417,'١٤٠٤ - (فما من نسمة كائنة قدر الله كونها إلا وهي كائنة هكذا قال رسول الله - صلى الله عليه وسلم -)','982','1286','0');
INSERT INTO [t38171] VALUES (1418,'١٤٠٥ - (يروى عن النبي - صلى الله عليه وسلم - إن الرجل ليجامع أهله) أي حليلته (فيكتب له من جماعة) ذلك (أجر ولد ذكر قاتل في سبيل الله فقتل)','982','1286','0');
INSERT INTO [t38171] VALUES (1419,'١٤٠٦ - (قال - صلى الله عليه وسلم - في العزل) لما سئل عنه (ذلك الوأد الخفي وقرأ وإذا الموؤدة سئلت وهو في الصحيح)','983','1286','0');
INSERT INTO [t38171] VALUES (1420,'١٤٠٧ - (قال - صلى الله عليه وسلم - من كانت له ابنة فأدبها فأحسن تأديبها وغذاها فأحسن غذاءها وأسبغ عليها النعمة التي أسبغ الله عليه كانت له مأمنة وميسرة من النار إلى الجنة)','983','1286','0');
INSERT INTO [t38171] VALUES (1421,'١٤٠٨ - (وقال ابن عباس رضي الله عنه ما من أحد يدرك ابنتين فيحسن إليهما ما صحبتاه إلا أدخلتاه الجنة)','983','1286','0');
INSERT INTO [t38171] VALUES (1422,'١٤٠٩ - (قال أنس)','983','1286','0');
INSERT INTO [t38171] VALUES (1423,'١٤١٠ - (قال أنس)','984','1286','0');
INSERT INTO [t38171] VALUES (1424,'١٤١١ - (قال أنس) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - من حمل طرفة من السوق إلى عياله فكأنما حمل إليهم صدقة حتى يضعها فيهم وليبدأ بالإناث دون الذكور فإنه من فرح أنثى فكأنما بكى من خشية الله ومن بكى من خشية الله حرم الله بدنه على النار).','984','1286','0');
INSERT INTO [t38171] VALUES (1425,'١٤١٢ - (قال أبو هريرة رضي الله عنه قال رسول الله - صلى الله عليه وسلم - من كان له ثلاث بنات أو أخوات فصبر على لأوائهن وضرائهن)','984','1286','0');
INSERT INTO [t38171] VALUES (1426,'١٤١٣ - (روى رافع عن أبيه)','985','1286','0');
INSERT INTO [t38171] VALUES (1427,'١٤١٤ - (روى عنه - صلى الله عليه وسلم - أنه قال من ولد له مولود)','985','1286','0');
INSERT INTO [t38171] VALUES (1428,'١٤١٥ - (والختان في اليوم السابع ورد به خبر)','986','1286','0');
INSERT INTO [t38171] VALUES (1429,'١٤١٦ - (وقد قال - صلى الله عليه وسلم - إذا سميتم فعبدوا)','986','1286','0');
INSERT INTO [t38171] VALUES (1430,'١٤١٧ - (وقال عليه الصلاة والسلام أحب الأسماء إلى الله عبد الله وعبد الرحمن).','987','1286','0');
INSERT INTO [t38171] VALUES (1431,'١٤١٨ - (قال - صلى الله عليه وسلم - سموا باسمي ولا تكنوا بكنيتي).','987','1286','0');
INSERT INTO [t38171] VALUES (1432,'١٤١٩ - (سمي رجل) ولده (أبا عيسى فقال - صلى الله عليه وسلم -) لما سمعه رادا عليه (إن عيسى لا أب له) إنما هو كلمته ألقاها إلى مريم (فكره ذلك).','988','1286','0');
INSERT INTO [t38171] VALUES (1433,'١٤٢٠ - (قال عبد الرحمن بن يزيد بن معاوية)','988','1286','0');
INSERT INTO [t38171] VALUES (1434,'١٤٢١ - (وقال - صلى الله عليه وسلم - إنكم تدعون يوم القيامة بأسمائكم وأسماء','988','1286','0');
INSERT INTO [t38171] VALUES (1435,'١٤٢٢ - (بدل رسول الله - صلى الله عليه وسلم - اسم العاص بعبد الله).','989','1286','0');
INSERT INTO [t38171] VALUES (1436,'١٤٢٣ - (وقال رسول الله - صلى الله عليه وسلم - لا تجمعوا بين اسمي وكنيتي).','990','1286','0');
INSERT INTO [t38171] VALUES (1437,'١٤٢٤ - (ونهى في) تسمية الرجل (أسلم وأفلح ونافع وبركة لأنه قد يقال بركة ثم فيقال لا)','990','1286','0');
INSERT INTO [t38171] VALUES (1438,'١٤٢٥ - (روت عائشة رضي الله عنها أن النبي - صلى الله عليه وسلم - أمر في الغلام أن يعق بشاتين مكافئتين)','991','1286','0');
INSERT INTO [t38171] VALUES (1439,'١٤٢٦ - (وروي أنه) - صلى الله عليه وسلم - (عق عن الحسن بشاة).','991','1286','0');
INSERT INTO [t38171] VALUES (1440,'١٤٢٧ - (وقال - صلى الله عليه وسلم - مع الغلام عقيقته فأهريقوا عنه دما وأميطوا عنه الأذى).','992','1286','0');
INSERT INTO [t38171] VALUES (1441,'١٤٢٨ - (روي أنه - صلى الله عليه وسلم - أمر فاطمة رضي الله عنها يوم سابع الحسين)','992','1286','0');
INSERT INTO [t38171] VALUES (1442,'١٤٢٩ - (حديث أبغض الحلال إلى الله الطلاق).','992','1286','0');
INSERT INTO [t38171] VALUES (1443,'١٤٣٠ - (قال) عبد الله (بن عمر رضي الله عنهما كان تحتي امرأة أحبها وكان أبي يكرهها فيأمرني بطلاقها فراجعت رسول الله - صلى الله عليه وسلم -) في شأنها (فقال يا ابن عمر طلق امرأتك)','993','1286','0');
INSERT INTO [t38171] VALUES (1444,'١٤٣١ - (قال رسول الله - صلى الله عليه وسلم - أيما امرأة سألت زوجها طلاقها)','993','1286','0');
INSERT INTO [t38171] VALUES (1445,'١٤٣٢ - (وقال - صلى الله عليه وسلم - المختلعات)','993','1286','0');
INSERT INTO [t38171] VALUES (1446,'١٤٣٣ - حديث (مره فليراجعها)','994','1286','0');
INSERT INTO [t38171] VALUES (1447,'١٤٣٤ - (ورد في إفشاء سر النكاح في الخبر الصحيح وعيد عظيم).','994','1286','0');
INSERT INTO [t38171] VALUES (1448,'١٤٣٥ - (قال - صلى الله عليه وسلم - أيما امرأة) ذات زوج (ماتت وزوجها عنها راض دخلت الجنة)','995','1286','0');
INSERT INTO [t38171] VALUES (1449,'١٤٣٦ - (وكان رجل خرج في سفر وعهد إلى امرأته أن لا تنزل من العلو إلى السفل)','995','1286','0');
INSERT INTO [t38171] VALUES (1450,'١٤٣٧ - (وقال - صلى الله عليه وسلم - إذا صلت المرأة خمسها)','995','1286','0');
INSERT INTO [t38171] VALUES (1451,'١٤٣٨ - (فقال) أي في حقهن لما ذكرن عنده (حاملات والدات مرضعات رحيمات بأولادهن)','996','1286','0');
INSERT INTO [t38171] VALUES (1452,'١٤٣٩ - (وقال - صلى الله عليه وسلم - اطلعت)','996','1286','0');
INSERT INTO [t38171] VALUES (1453,'١٤٤٠ - (وفي خبر آخر) قال - صلى الله عليه وسلم - (اطلعت في الجنة) أي عليها (فإذا أقل أهلها النساء فقلت)','997','1286','0');
INSERT INTO [t38171] VALUES (1454,'١٤٤١ - (قالت عائشة رضي الله عنها أتت فتاة)','997','1286','0');
INSERT INTO [t38171] VALUES (1455,'١٤٤٣ - وقال - صلى الله عليه وسلم - لو أمرت أحد أن يسجد لأحد لأمرت المرأة أن تسجد لزوجها من عظم حقه عليها.','999','1286','0');
INSERT INTO [t38171] VALUES (1456,'١٤٤٤ - (قال - صلى الله عليه وسلم - أقرب ما تكون المرأة من وجه ربها)','1000','1286','0');
INSERT INTO [t38171] VALUES (1457,'١٤٤٥ - قال عليه السلام المرة عورة فإذا خرجت استشرفها الشيطان.','1000','1286','0');
INSERT INTO [t38171] VALUES (1458,'١٤٤٦ - (قال للمرأة عشر عورات فإذا تزوجت ستر الزوج عورة واحدة فإذا ماتت ستر القبر العشرة)','1000','1286','0');
INSERT INTO [t38171] VALUES (1459,'١٤٤٧ - قال رسول الله - صلى الله عليه وسلم - لا يحل لها أن تطعم من بيته إلا بإذنه إلا الرطب','1001','1286','0');
INSERT INTO [t38171] VALUES (1460,'١٤٤٨ - (وقد قال - صلى الله عليه وسلم - أنا وامرأة سفعاء الخدين)','1001','1286','0');
INSERT INTO [t38171] VALUES (1461,'١٤٤٩ - (وقال - صلى الله عليه وسلم - حرم الله على كل آدمي الجنة يدخلها قبلي غير أني أنظر عن يميني فإذا امرأة تبادرني)','1002','1286','0');
INSERT INTO [t38171] VALUES (1462,'١٤٥٠ - (روي عن معاذ بن جبل)','1002','1286','0');
INSERT INTO [t38171] VALUES (1463,'١٤٥١ - (رسول الله - صلى الله عليه وسلم - يقول لا يحل لامرأة تؤمن بالله اليوم الآخر أن تحد على ميت أكثر من ثلاثة أيام إلا على زوج)','1002','1286','0');
INSERT INTO [t38171] VALUES (1464,'كتاب آداب الكسب والمعاش','1003','1202','0');
INSERT INTO [t38171] VALUES (1465,'١٤٥٢ - (قال - صلى الله عليه وسلم - من الذنوب ذنوب لا يكفرها إلا الهم في طلب المعيشة).','1004','1464','0');
INSERT INTO [t38171] VALUES (1466,'١٤٥٣ - (وقال - صلى الله عليه وسلم - التاجر الصدوق يحشر يوم القيامة مع الصديقين والشهداء).','1004','1464','0');
INSERT INTO [t38171] VALUES (1467,'١٤٥٤ - (قال - صلى الله عليه وسلم - من طلب الدنيا حلالا).','1004','1464','0');
INSERT INTO [t38171] VALUES (1468,'١٤٥٥ - (قال - صلى الله عليه وسلم - لا تقولوا هذا فإنه إن كان يسعى لنفسه)','1005','1464','0');
INSERT INTO [t38171] VALUES (1469,'١٤٥٦ - (وقال - صلى الله عليه وسلم - إن الله يحب العبد يتخذ المهنة ليستغني بها عن الناس).','1006','1464','0');
INSERT INTO [t38171] VALUES (1470,'١٤٥٧ - (وفي الخبر إن الله يحب المؤمن المحترف)','1006','1464','0');
INSERT INTO [t38171] VALUES (1471,'١٤٥٨ - (قال - صلى الله عليه وسلم - أحل ما أكل الرجل من كسبه وكل بيع مبرور).','1007','1464','0');
INSERT INTO [t38171] VALUES (1472,'١٤٥٩ - (في خبر آخر) ولفظ القوت وفي لفظ آخر (أحل ما أكل العبد كسب يد الصانع إذا نصح).','1007','1464','0');
INSERT INTO [t38171] VALUES (1473,'١٤٦٠ - (وقال - صلى الله عليه وسلم - عليكم بالتجارة فإن فيها تسعة أعشار الرزق).','1007','1464','0');
INSERT INTO [t38171] VALUES (1474,'١٤٦١ - (وقال نبينا - صلى الله عليه وسلم - إني لا أعلم شيئا يقربكم من الجنة ويبعدكم من النار إلا أمرتكم به وإني لا أعلم شيئا يبعدكم من الجنة ويقربكم من النار إلا نهيتكم عنه وإن الروح الأمين نفث في روعي أن نفسا لن تموت حتى تستوفي رزقها وإن أبطأ عنها فاتقوا الله وأجملوا في الطلب أمر بالإجمال في الطلب ولم يقل اتركوا الطلب ثم قال في آخره ولا يحملنكم استبطاء شيء من الرزق على أن تطلبوه بمعصية الله تعالى فإن الله لا ينال ما عنده بمعصيته).','1008','1464','0');
INSERT INTO [t38171] VALUES (1475,'١٤٦٢ - (وقال الأسواق موائد الله تعالى فمن أتاها أصاب منها).','1009','1464','0');
INSERT INTO [t38171] VALUES (1476,'١٤٦٣ - (قال - صلى الله عليه وسلم - لأن يأخذ أحدكم حبله)','1009','1464','0');
INSERT INTO [t38171] VALUES (1477,'١٤٦٤ - (قال - صلى الله عليه وسلم - من فتح على نفسه بابا من السؤال فتح الله عليه سبعين بابا من الفقر).','1009','1464','0');
INSERT INTO [t38171] VALUES (1478,'١٤٦٥ - (قول النبي - صلى الله عليه وسلم - إن الله جعل رزقي تحت ظل رمحي).','1010','1464','0');
INSERT INTO [t38171] VALUES (1479,'١٤٦٦ - (وقوله - صلى الله عليه وسلم - حين ذكر الطير فقال تغدو)','1010','1464','0');
INSERT INTO [t38171] VALUES (1480,'١٤٦٧ - (قال - صلى الله عليه وسلم - ما أوحى إلي)','1010','1464','0');
INSERT INTO [t38171] VALUES (1481,'١٤٦٨ - (الدنيا حبها رأس كل خطيئة)','1011','1464','0');
INSERT INTO [t38171] VALUES (1482,'١٤٦٩ - (وإنما الكلب هو الذي لا يجوز أن يقتنى إعجابا بصورته)','1011','1464','0');
INSERT INTO [t38171] VALUES (1483,'١٤٧٠ - (قال رسول الله - صلى الله عليه وسلم - من احتكر الطعام أربعين يوما ثم تصدق به لم تكن صدقته كفارة لاحتكاره)','1012','1464','0');
INSERT INTO [t38171] VALUES (1484,'١٤٧١ - (روى ابن عمر) عبد الله (رضي الله عنهما عنه - صلى الله عليه وسلم - أنه قال من احتكر الطعام أربعين يوما فقد برئ من الله وبرىء الله منه)','1012','1464','0');
INSERT INTO [t38171] VALUES (1485,'١٤٧٢ - (روى في فضل ترك الاحتكار)','1013','1464','0');
INSERT INTO [t38171] VALUES (1486,'١٤٧٣ - (قال - صلى الله عليه وسلم - من سن سنة سيئة فعمل بها من بعده كان عليه وزرها ووزر من عمل بها ولا ينقص من أوزارهم شيئا)','1014','1464','0');
INSERT INTO [t38171] VALUES (1487,'١٤٧٤ - (قوله - صلى الله عليه وسلم - رحم الله أمرءا سهل البيع سهل الشراء سهل القضاء سهل الاقتضاء)','1014','1464','0');
INSERT INTO [t38171] VALUES (1488,'١٤٧٥ - (وفي الخبر ويل للتاجر من بلى والله ولا والله وويل للصانع من عدو بعد غد)','1014','1464','0');
INSERT INTO [t38171] VALUES (1489,'١٤٧٦ - (في الخبر اليمين الكاذبة منفقة للسلعة)','1014','1464','0');
INSERT INTO [t38171] VALUES (1490,'١٤٧٧ - وروى أبو هريرة رضي الله عنه عن النبي - صلى الله عليه وسلم - أنه قال ثلاثة لا ينظر الله إليهم يوم القيامة عتل مستكبر ومنان بعطيته ومنفق سلعته بيمينه.','1015','1464','0');
INSERT INTO [t38171] VALUES (1491,'١٤٧٨ - (من غشنا فليس منا)','1015','1464','0');
INSERT INTO [t38171] VALUES (1492,'١٤٧٩ - (قال) واثلة (سمعت رسول الله - صلى الله عليه وسلم - يقول لا يحل لأحد يبيع بيعا إلا يبين ما فيه)','1016','1464','0');
INSERT INTO [t38171] VALUES (1493,'١٤٨٠ - قال - صلى الله عليه وسلم - البيعان إذا صدقا ونصحا بورك لهما في بيعهما وإذا كتما وكذبا نزعت بركة معهما.','1016','1464','0');
INSERT INTO [t38171] VALUES (1494,'١٤٨١ - في الحديث يد الله على الشريكين ما لم يتخاونا فإذا تخاونا رفع يده عنهما.','1017','1464','0');
INSERT INTO [t38171] VALUES (1495,'١٤٨٢ - (في لفظ آخر (من قال لا إله إلا الله مخلصا دخل الجنة)','1017','1464','0');
INSERT INTO [t38171] VALUES (1496,'١٤٨٣ - (قيل وما إخلاصها قال أن تحجزه)','1017','1464','0');
INSERT INTO [t38171] VALUES (1497,'١٤٨٤ - (وقال - صلى الله عليه وسلم - ما آمن بالقرآن من استحل محارمه)','1018','1464','0');
INSERT INTO [t38171] VALUES (1498,'١٤٨٥ - لا تزال لا إله إلا الله تدفع عن الخلق سخط الله ما لم يؤثروا صفقة دنياهم على آخرتهم (وفي لفظ آخر)','1018','1464','0');
INSERT INTO [t38171] VALUES (1499,'١٤٨٦ - ما اشترى رسول الله - صلى الله عليه وسلم - شيئا قال للوزان لما كان يزن ثمنه زن وارجح.','1018','1464','0');
INSERT INTO [t38171] VALUES (1500,'١٤٨٧ - (نهى النبي - صلى الله عليه وسلم - عن تلقي الركبان)','1019','1464','0');
INSERT INTO [t38171] VALUES (1501,'١٤٨٨ - (ونهى) - صلى الله عليه وسلم - (عن النجش)','1019','1464','0');
INSERT INTO [t38171] VALUES (1502,'١٤٨٩ - (نهى) - صلى الله عليه وسلم - (أيضا أن يبيع حاضر لباد)','1019','1464','0');
INSERT INTO [t38171] VALUES (1503,'١٤٩٠ - (ونهى - صلى الله عليه وسلم - عن النجش)','1020','1464','0');
INSERT INTO [t38171] VALUES (1504,'١٤٩١ - (في الحديث غبن المترسل حرام)','1020','1464','0');
INSERT INTO [t38171] VALUES (1505,'١٤٩٢ - (ورد في حديث من طريق أهل البيت المغبون لا محمود ولا مأجور)','1021','1464','0');
INSERT INTO [t38171] VALUES (1506,'١٤٩٣ - (حديث اسمح يسمح لك).','1021','1464','0');
INSERT INTO [t38171] VALUES (1507,'١٤٩٤ - (وقال - صلى الله عليه وسلم - من أنظر معسرا أو ترك له حاسبه الله حسابا يسيرا).','1023','1464','0');
INSERT INTO [t38171] VALUES (1508,'١٤٩٥ - (وذكر - صلى الله عليه وسلم - رجلا كلان مسرفا على نفسه)','1024','1464','0');
INSERT INTO [t38171] VALUES (1509,'١٤٩٦ - (وقال - صلى الله عليه وسلم - من أقرض دينارا إلى أجل).','1025','1464','0');
INSERT INTO [t38171] VALUES (1510,'١٤٩٧ - (وقال - صلى الله عليه وسلم - رأيت على باب الجنة مكتوبا الصدقة بعشر أمثالها والقرض بثمان عشر).','1025','1464','0');
INSERT INTO [t38171] VALUES (1511,'١٤٩٨ - (ونظر رسول الله - صلى الله عليه وسلم - إلى رجل يلازم رجلا بدين فأومأ) أي أشار (إلى صاحب الدين بيده أن ضع الشطر ففعل)','1026','1464','0');
INSERT INTO [t38171] VALUES (1512,'١٤٩٩ - وفي الخبر خذ حقك في كفاف وعفاف واف أو غير واف يحاسبك الله حسابا يسيرا.','1026','1464','0');
INSERT INTO [t38171] VALUES (1513,'١٥٠٠ - (قال - صلى الله عليه وسلم - خيركم أحسنكم قضاء)','1027','1464','0');
INSERT INTO [t38171] VALUES (1514,'١٥٠١ - (قال - صلى الله عليه وسلم - من ادان دينا) أصله أدنان أي أخذ دينا (وهو ينوي قضاءه وكل الله به ملائكة يحفظونه ويدعون له حتى يقضيه)','1027','1464','0');
INSERT INTO [t38171] VALUES (1515,'١٥٠٢ - (فقال دعوه فإن لصاحب الحق مقالا)','1028','1464','0');
INSERT INTO [t38171] VALUES (1516,'١٥٠٣ - (قال - صلى الله عليه وسلم - أنصر أخاك ظالما أو مظلوما).','1028','1464','0');
INSERT INTO [t38171] VALUES (1517,'١٥٠٤ - حديث من أقال نادما صفقته أقال الله عثرته يوم القيامة.','1029','1464','0');
INSERT INTO [t38171] VALUES (1518,'١٥٠٥ - (قال) أبو عبد الله (أحمد بن حنبل) رحمه الله تعالى (ورد نهي عن رسول الله - صلى الله عليه وسلم - وعن أصحابه في الصياغة من الصحاح وأنا اكره الكسر).','1030','1464','0');
INSERT INTO [t38171] VALUES (1519,'١٥٠٦ - (وقد روى خير تجارتكم البز وخير صنائعكم الخرز)','1030','1464','0');
INSERT INTO [t38171] VALUES (1520,'١٥٠٧ - (في حديث آخر لو اتجر أهل الجنة لاتجروا في البز ولو اتجر أهل النمار لاتجروا في الصرف)','1031','1464','0');
INSERT INTO [t38171] VALUES (1521,'١٥٠٨ - (وفي الخبر تلتقي ملائكة الليل والنهار عند طلوع الفجر وعند صلاة العصر)','1031','1464','0');
INSERT INTO [t38171] VALUES (1522,'١٥٠٩ - (قال - صلى الله عليه وسلم - ذاكر الله في الغافلين كالمقاتل بين الفارين).','1031','1464','0');
INSERT INTO [t38171] VALUES (1523,'١٥١١ - قال - صلى الله عليه وسلم - اتق الله حيث كنت واتبع السيئة الحسنة تمحها وخالق الناس بخلق حسن.','1032','1464','0');
INSERT INTO [t38171] VALUES (1524,'١٥١١ - (في الخبر لا يركب البحر) أي على متنه (إلا لحج أو عمرة أو غزو)','1033','1464','0');
INSERT INTO [t38171] VALUES (1525,'١٥١٢ - (في الخبر شر البقاع الأسواق وشر أهلها أولهم دخولا وآخرهم خروجا منها)','1033','1464','0');
INSERT INTO [t38171] VALUES (1526,'١٥١٣ - (وقال إن الله تعالى أمر المؤمنين بما أمر به المرسلين فقال عز من قائل (يا أيها الذين آمنوا كلوا من طيبات من رزقناكم)','1034','1464','0');
INSERT INTO [t38171] VALUES (1527,'١٥١٤ - (كان لا يسأل عن كل ما يحمل إليه)','1034','1464','0');
INSERT INTO [t38171] VALUES (1528,'١٥١٥ - في الخبر من دعا الله تعالى لظالم بالبقاء فقد أحب أن يعصي الله في أرضه.','1034','1464','0');
INSERT INTO [t38171] VALUES (1529,'١٥١٦ - (وفي الحديث أن الله تعالى يغضب)','1034','1464','0');
INSERT INTO [t38171] VALUES (1530,'١٥١٧ - (وفي خبر آخر من أكرم فاسقا فقد أعان على هدم الإسلام)','1035','1464','0');
INSERT INTO [t38171] VALUES (1531,'كتاب الحلال والحرام','1036','1202','0');
INSERT INTO [t38171] VALUES (1532,'١٥١٨ - قال - صلى الله عليه وسلم - طلب الحلال فريضة على كل مسلم رواه ابن مسعود) ولفظ القوت ورويته عن ابن مسعود عن رسول الله - صلى الله عليه وسلم - فساقه.','1037','1531','0');
INSERT INTO [t38171] VALUES (1533,'١٥١٩ - (قال - صلى الله عليه وسلم - من سعى على عياله)','1037','1531','0');
INSERT INTO [t38171] VALUES (1534,'٢٥١٠ - وقال - صلى الله عليه وسلم - من أكل الحلال أربعين يوما نور الله قلبه وأجرى ينابيع الحكمة من قلبه على لسانه وفي رواية زهده الله في الدنيا.','1038','1531','0');
INSERT INTO [t38171] VALUES (1535,'١٥٢١ - (وروى أن سعدا) هو ابن وقاص القرشي الزهري أحد العشرة رضي الله عنه (سأل رسول الله - صلى الله عليه وسلم - أن يسأل الله تعالى أن يجعله مجاب','1038','1531','0');
INSERT INTO [t38171] VALUES (1536,'١٥٢١/ أ- رب أشعب أغبر مطعمه حرام يدعو فأنى يستجاب له -الحديث.','1039','1531','0');
INSERT INTO [t38171] VALUES (1537,'١٥٢٢ - (وفي حديث ابن عباس) رضي الله عنهما (عن النبي - صلى الله عليه وسلم - قال إن لله تعالى ملكا على بيت المقدس ينادي في كل ليلة من أكل حراما لم يقبل منه صرف ولا عدل فقيل) في تفسيره (الصرف النافلة والعدل الفريضة)','1039','1531','0');
INSERT INTO [t38171] VALUES (1538,'١٥٢٣ - (قال - صلى الله عليه وسلم - من اشترى ثوبا بعشرة دراهم وفي ثمنه درهم حرام لم يقبل الله صلاته ما دام عليه منه شيء)','1040','1531','0');
INSERT INTO [t38171] VALUES (1539,'١٥٢٤ - (قال عليه) الصلاة (والسلام من لم يبال من أين اكتسب المال لم يبال الله من أين أدخله النار).','1040','1531','0');
INSERT INTO [t38171] VALUES (1540,'١٥٢٥ - (وقال عليه) الصلاة و (السلام كل لحم نبت من حرام فالنار أولى به)','1040','1531','0');
INSERT INTO [t38171] VALUES (1541,'١٥٢٦ - (وقال عليه) الصلاة و (السلام العبادة عشرة أجزاء فتسعة فيها في طلب الحلال روي هذا مرفوعا وموقوفا على بعض الصحابة).','1041','1531','0');
INSERT INTO [t38171] VALUES (1542,'١٥٢٧ - (وقال - صلى الله عليه وسلم - من أمسى وانيا) أي تعبا (من طلب الحلال بات مغفورا له).','1041','1531','0');
INSERT INTO [t38171] VALUES (1543,'١٥٢٨ - (وقال عليه) الصلاة و (السلام من أصاب مالا من مأثم)','1041','1531','0');
INSERT INTO [t38171] VALUES (1544,'١٥٢٩ - (يروى أن الله تعالى قال وأما الورعون فأنا أستحي أن أحاسبهم)','1042','1531','0');
INSERT INTO [t38171] VALUES (1545,'١٥٣٠ - (وقال عليه الصلاة و (السلام درهم من ربا)','1042','1531','0');
INSERT INTO [t38171] VALUES (1546,'١٥٣١ - (وفي حديث أبي هريرة) رضي الله عنه رفعه (المعدة) بفتح الميم وكسر العين من الإنسان مقر الطعام والشراب وتخفف بكسر الميم وسكون العين (حوض البدن والعروق إليها واردة فإذا صحت المعدة صدرت العروق بالصحة وإذا سقمت صدرت بالسقم) هكذا هو في القوت.','1043','1531','0');
INSERT INTO [t38171] VALUES (1547,'١٥٣٢ - (وفي الحديث من اكتسب مالا من حرام فإن تصدق به لم يتقبل منه وإن تركه وراءه كان زاده إلى النار).','1044','1531','0');
INSERT INTO [t38171] VALUES (1548,'١٥٣٣ - (روي أن) أبا بكر (الصديق رضي الله عنه شرب لبنا من كسب عبده ثم سأل عنه) أي عن اللبن (العبد من أين اكتسبه فقال تكهنت لقوم) أخبرتهم عن بعض الأمور المغيبة (فأعطوني) إياه (فأدخل) الصديق (أصبعه في فيه وجعل يقيء حتى ظننت أن نفسه ستخرج وقال اللهم إني أعتذر إليك مما حملت العروق وخالط الأمعاء) هكذا هو في القوت.','1044','1531','0');
INSERT INTO [t38171] VALUES (1549,'١٥٣٤ - قال صاحب القوت (في بعض الأخبار أنه عليه السلام أخبر بذلك فقال أو ما علمتم أن الصديق لا يدخل جوفه إلا طيبا)','1045','1531','0');
INSERT INTO [t38171] VALUES (1550,'١٥٣٥ - (أمر رسول الله - صلى الله عليه وسلم - بأن يمقل الذباب في الطعام إذا وقع فيه)','1045','1531','0');
INSERT INTO [t38171] VALUES (1551,'١٥٣٦ - (قال - صلى الله عليه وسلم - لا يبلغ العبد درجة المتقين حتى يدع) أي يترك (ما لا بأس به مخافة مما به بأس).','1046','1531','0');
INSERT INTO [t38171] VALUES (1552,'١٥٣٧ - (قول النبي - صلى الله عليه وسلم -) للحسن بن علي رضي الله عنهما (دع ما يريبك)','1046','1531','0');
INSERT INTO [t38171] VALUES (1553,'١٥٣٨ - (كل ما أصميت ودع ما أنميت)','1047','1531','0');
INSERT INTO [t38171] VALUES (1554,'١٥٣٩ - (في الكلب المعلم وإن أكل فلا تأكل فإني أخاف أن يكون إنما أمسك على نفسه)','1047','1531','0');
INSERT INTO [t38171] VALUES (1555,'١٥٤٠ - (كل منه فقال وإن أكل قال كل)','1048','1531','0');
INSERT INTO [t38171] VALUES (1556,'١٥٤١ - (أخذ الحسين بن علي) بن أبي طالب رضي الله عنهما (تمرة من الصدقة وكان صغيرا فقال) له (رسول الله - صلى الله عليه وسلم - كخ أي ألقها).','1048','1531','0');
INSERT INTO [t38171] VALUES (1557,'١٥٤٢ - (روي أن النبي - صلى الله عليه وسلم - سئل عن أن يكحل) المسجد (فقال عريش مثل عريش موسى وإنما هو شيء مثل الكحل يطلى به فلم يرخص فيه رسول الله - صلى الله عليه وسلم -).','1049','1531','0');
INSERT INTO [t38171] VALUES (1558,'١٥٤٣ - (قال رسول الله - صلى الله عليه وسلم - الحلال بين والحرام بين).','1049','1531','0');
INSERT INTO [t38171] VALUES (1559,'١٥٤٤ - (كان - صلى الله عليه وسلم - إذا أتي بشيء اشتبه عليه أنه صدقة أو هدية سأل عنه حتى يعلم أيهما هو).','1050','1531','0');
INSERT INTO [t38171] VALUES (1560,'١٥٤٥ - (وروي أنه - صلى الله عليه وسلم - أرق ليلة) أي قلق في نومه (فقال بعض نسائه يا رسول الله أرقت قال أجل) أي نعم (وجدت تمرة فخشيت أن تكون من الصدقة وفي رواية فأكلتها فخشيت أن تكون من الصدقة).','1050','1531','0');
INSERT INTO [t38171] VALUES (1561,'١٥٤٧ - (ثم أعلمه الله تعالى بعد ذلك أنه لم يمسخ الله خلقا فجعل له نسلا).','1051','1531','0');
INSERT INTO [t38171] VALUES (1562,'١٥٤٨ - (روت عائشة رضي الله عنها أن رجلا أتى النبي - صلى الله عليه وسلم - بأرنب فقال رميتي عرفت فيها سهمي فقال أصميت أو أنميت فقال بل أنميت قال إن الليل خلق من خلق الله لا يقدر قدره إلا الذي خلقه فلعله أعان على شيء قتله).','1052','1531','0');
INSERT INTO [t38171] VALUES (1563,'١٥٤٩ - (قال - صلى الله عليه وسلم - كل منه وإن غاب عنك ما لم تجد فيه أثرا غير سهمك).','1052','1531','0');
INSERT INTO [t38171] VALUES (1564,'١٥٥٠ - (سرق في زمانه - صلى الله عليه وسلم - مجن) بكسر الميم وهو الترس لأن صاحبه يتستر به (فقطع السارق)','1053','1531','0');
INSERT INTO [t38171] VALUES (1565,'١٥٥١ - (وغل واحد من) جملة (الغنيمة عباءة) وهي كساء من صوف أخرجه البخاري من حديث عبد الله بن عمر واسم الغال كركرة قاله العراقي.','1053','1531','0');
INSERT INTO [t38171] VALUES (1566,'١٥٥٢ - النهي الوارد (في كسب الحجام وكراهته).','1053','1531','0');
INSERT INTO [t38171] VALUES (1567,'١٥٥٣ - (أمر بأن يعلف الناضح) وهو في الأصل البعير الذي يحمل الماء من النهر أو البشر يستقي به ثم استعمل في كل بعير وإن لم يحمل الماء','1053','1531','0');
INSERT INTO [t38171] VALUES (1568,'١٥٥٤ - قال النبي - صلى الله عليه وسلم - لعن اليهود إذ حرمت عليهم الخمور فباعوها).','1054','1531','0');
INSERT INTO [t38171] VALUES (1569,'١٥٥٥ - روى ابن خسرو في مسنده','1054','1531','0');
INSERT INTO [t38171] VALUES (1570,'١٥٥٦ - (- صلى الله عليه وسلم - قال لكل من سأله عن الصيد إذا أرسلت كلبك المعلم وذكرت عليه اسم الله فكل).','1055','1531','0');
INSERT INTO [t38171] VALUES (1571,'١٥٥٧ - (وقد شهر الذبح بالتسمية).','1055','1531','0');
INSERT INTO [t38171] VALUES (1572,'١٥٥٨ - (قوله - صلى الله عليه وسلم - المؤمن يذبح على اسم الله تعالى سمي أو لم يسم).','1055','1531','0');
INSERT INTO [t38171] VALUES (1573,'١٥٥٩ - (قد صح في الصحاح من الأخبار) الواردة (حديث الجنين بأن ذكاته ذكاة أمه صحة لا يتطرق احتمال إلى متنه ولا ضعف إلى سنده)','1057','1531','0');
INSERT INTO [t38171] VALUES (1574,'١٦٥٠ - (قال لا ولكنه لم يكن بأرض قومي فأجدني أعافه وأكله خالد ورسول الله - صلى الله عليه وسلم - ينظر) إليه (وقد نقل ذلك في الصحيحين)','1058','1531','0');
INSERT INTO [t38171] VALUES (1575,'١٥٦١ - (قال ذلك) وهو قوله استفت قلبك (لوابصة) رضي الله عنه (لما كان قد عرف من حاله)','1058','1531','0');
INSERT INTO [t38171] VALUES (1576,'١٥٦٢ - (كان - صلى الله عليه وسلم - لا يسأل عن كل ما يحمل إليه) في كل أحيانه (بل سأله في أول قدومه إلى المدينة) مهاجرا (عما يحمل إليه أصدقة أم هدية)','1059','1531','0');
INSERT INTO [t38171] VALUES (1577,'١٥٦٣ - (كان - صلى الله عليه وسلم - يدعي إلى الضيافات فيجيب) إليها (ولا يسأل أصدقة أم لا)','1060','1531','0');
INSERT INTO [t38171] VALUES (1578,'١٥٦٤ - جاء إلى رسول الله - صلى الله عليه وسلم - فقال إني تزوجت امرأة فجاءتنا أمة سوداء فزعمت أنها قد أرضعتنا وهي كاذبة فقال دعها أي فارقها واتركها (فقال إنها سوداء يصغر من شأنها فقال وكيف وقد زعمت أنها قد أرضعتكما)','1060','1531','0');
INSERT INTO [t38171] VALUES (1579,'١٥٦٥ - (أمر رسول الله - صلى الله عليه وسلم - بالتصدق بالشاة المصلية) أي المشوبة على النار (التي قدمت إليه فكلمته بأنها حرام قال أطعموها الأسارى)','1060','1531','0');
INSERT INTO [t38171] VALUES (1580,'١٥١٦ - وقال للصحابة ألا ترون ما يقول صاحبكم يزعم أن الروم ستغلب فخاطرهم أبو بكر رضي الله عنه بإذن رسول الله - صلى الله عليه وسلم - فلما حقق الله صدقة وجاء أبو بكر رضي الله عنه بما قامرهم به قال عليه السلام هذا سحت فتصدق به وفرح المؤمنون بنصر الله وكان قد نزل تحريم القمار بعد إذن رسول الله - صلى الله عليه وسلم - له في المخاطرة مع الكفار.','1062','1531','0');
INSERT INTO [t38171] VALUES (1581,'١٥٦٧ - حديث الإنسان يأكل من غرس غيره.','1064','1531','0');
INSERT INTO [t38171] VALUES (1582,'١٥٦٨ - (يقال اعلفوه الناضح).','1065','1531','0');
INSERT INTO [t38171] VALUES (1583,'١٥٦٩ - (قال حين بعث) أبا الوليد (عبادة بن الصامت) بن قيس الأنصاري الخزرجي المدني أحد النقباء بدري مشهور وكان طوله عشرة أشبار مات بالرملة سنة أربع وثلاثين عن اثنين وسبعين سنة (إلى الصدقة) أي واليا يتولى قبضها من أربابها (اتق الله يا أبا الوليد) ودعاه بالكنية ترحما (لا تجيء) وفي رواية لا تأتي قال الزمخشري لا مزيدة أو أصلها لئلا تأتي بحذف اللام (يوم القيامة ببعير تحمله على رقبتك) هو ظرف وقع حالا من الضمير في تأتي مستعليا رقبتك بعير (له رغاء) بالضم أي تصويت (وبقرة لها خوار) بالضم كذلك (وشاة تيعر) وفي نسخة لها ثؤاج بالضم صوت الغنم (قال يا رسول الله أهكذا يكون قال نعم والذي نفسي بيده) أي في قبضة قدرته (إلا من رحم الله) وتجاوز عنه (قال) عبادة (فوالذي بعثك بالحق لا أعمل على شيء أبدا).','1066','1531','0');
INSERT INTO [t38171] VALUES (1584,'١٥٧٠ - (وقال - صلى الله عليه وسلم - إني لا أخاف عليكم أن تشركوا بعدي ولكن أخاف عليكم أن تنافسوا).','1067','1531','0');
INSERT INTO [t38171] VALUES (1585,'١٥٧١ - (قال) ابن عمر (إن الخبيث لا يكفر الخبيث وإنك قد وليت البصرة ولا أحسبك إلا وقد أصبت منها شرا فقال عامر ألا تدعو لي فقال ابن عمر سمعت رسول الله - صلى الله عليه وسلم - يقول لا يقبل الله صلاة بغير طهور ولا صدقة من غلول).','1067','1531','0');
INSERT INTO [t38171] VALUES (1586,'١٥٧٢ - (قال فمن نابذهم) أي جانبهم (نجا) من النفاق والمداهنة (ومن اعتزلهم) منكرا عليهم (سلم) من العقوبة على ترك المنكر (أو كاد يسلم من وقع معهم في دنياهم فهو منهم).','1068','1531','0');
INSERT INTO [t38171] VALUES (1587,'١٤٧٣ - (قال - صلى الله عليه وسلم - سيكون بعدي أمراء يظلمون) الناس (ويكذبون) في قولهم (فمن صدقهم بكذبهم وأعانهم على ظلمهم فليس) هو (مني ولست) أنا (منه ولم يرد على الحوض) يوم القيامة.','1068','1531','0');
INSERT INTO [t38171] VALUES (1588,'١٥٧٤ - (وروى أبو هريرة) رضي الله عنه (أنه - صلى الله عليه وسلم - قال أبغض القراء إلى الله تعالى الذين يزورون الأمراء) أي يغشون أبوابهم ومجالسهم والمراد بالقراء العلماء.','1069','1531','0');
INSERT INTO [t38171] VALUES (1589,'١٥٧٥ - وفي الخبر العلماء أمناء الرسل على عباد الله ما لم يخالطوا السلطان فإذا فعلوا ذلك فقد خانوا الرسل فاحذروهم واعتزلوهم رواه أنس رضي الله عنه.','1069','1531','0');
INSERT INTO [t38171] VALUES (1590,'١٥٧٦ - (نهى رسول الله - صلى الله عليه وسلم - حين قال يا معشر المهاجرين والأنصار لا تدخلوا على أهل الدنيا فإنها مسخطة للرزق).','1070','1531','0');
INSERT INTO [t38171] VALUES (1591,'١٥٧٧ - (دعي سعيد بن المسيب) رحمه الله (إلى البيعة للوليد وسليمان بن عبد الملك بن مروان) بن الحكم بن أبي العاص الأموي','1070','1531','0');
INSERT INTO [t38171] VALUES (1592,'١٥٧٨ - (روي عن محمد بن صالح) بن عبد الرحمن البغدادي أبي بكر الأنماطي ثقة حافظ مات سنة إحدى وسبعين على الصحيح (قال كنت عند حماد بن سلمة) بن دينار البصري العابد يكنى أبا سلمة مات سنة سبع وستين روى له البخاري في الأدب ومسلم والأربعة (فإذا ليس في البيت إلا حصير وهو جالس عليه ومصحف يقرأ فيه وجراب فيه علمه) أي الأحاديث التي كتبها عن شيوخه (ومطهرة يتوضأ منها فبينا أنا عنده إذ دق الباب فإذا هو) وقد أخرجه الخطيب وابن عسكر وابن النجار في تواريخهم عن مقاتل ابن صالح الخراساني قال دخلت على حماد بن سلمة فبينا أنا عنده جالس إذ دق داق الباب فقال يا صبية أخرجي فانظري من هذا فقالت هذا رسول محمد بن سليمان الهاشمي وهو أمير البصرة','1071','1531','0');
INSERT INTO [t38171] VALUES (1593,'١٥٧٩ - (لما استعمل) أمير المؤمنين (عثمان بن عفان) رضي الله عنه (ابن عامر) واليا على البصرة (أتاه أصحاب رسول الله - صلى الله عليه وسلم -) يسلمون عليه (وأبطأ عنه أبو ذر) رضي الله عنه (وكان له صديقا فعاتبه) على ترك المجيء (فقال أبو ذر سمعت رسول الله - صلى الله عليه وسلم - يقول إن الرجل إذا ولى ولاية تباعد الله عنه).','1073','1531','0');
INSERT INTO [t38171] VALUES (1594,'١٥٨٠ - (قالت عائشة رضي الله عنها جبلت النفوس على حب من أحسن إليها).','1073','1531','0');
INSERT INTO [t38171] VALUES (1595,'١٥٨١ - (قال رسول الله - صلى الله عليه وسلم - اللهم لا تجعل لفاجر عندي يدا فيحبه قلبي).','1075','1531','0');
INSERT INTO [t38171] VALUES (1596,'١٥٨٢ - (قال - صلى الله عليه وسلم - لا تزال هذه الأمة تحت يد الله وكنفه ما لم تمالئ قراؤها أمراءها).','1076','1531','0');
INSERT INTO [t38171] VALUES (1597,'١٥٨٣ - (إن النبي - صلى الله عليه وسلم - لعن في الخمر عشرة حتى لعن العاصر والمعتصر).','1076','1531','0');
INSERT INTO [t38171] VALUES (1598,'١٥٨٤ - (وقال ابن مسعود) رضي الله عنه (آكل الربا وموكله وشاهداه وكاتبه ملعونون على لسان محمد - صلى الله عليه وسلم -).','1076','1531','0');
INSERT INTO [t38171] VALUES (1599,'١٥٨٥ - (يقال للشرطي دع سوطك وادخل النار).','1077','1531','0');
INSERT INTO [t38171] VALUES (1600,'١٥٨٦ - (قال - صلى الله عليه وسلم - من أشراط الساعة رجال معهم سياط كأذناب البقر)','1078','1531','0');
INSERT INTO [t38171] VALUES (1601,'١٥٨٧ - (روى ابن مسعود) رضي الله عنه (عن النبي - صلى الله عليه وسلم - أن الله تعالى لعن علماء بني إسرائيل إذا خالطوا الظالمين في معايشهم)','1078','1531','0');
INSERT INTO [t38171] VALUES (1602,'١٥٨٨ - (قال - صلى الله عليه وسلم - تهادوا تحابوا)','1079','1531','0');
INSERT INTO [t38171] VALUES (1603,'١٥٨٩ - (قال رسول الله - صلى الله عليه وسلم - يأتي على الناس زمان يستحل فيه والسحت بالهدية والقتل بالموعظة يقتل البريء ليوعظ به العامة)','1080','1531','0');
INSERT INTO [t38171] VALUES (1604,'١٥٩٠ - (قال جابر) بن عبد الله (وأبو هريرة) رضي الله عنهما (هدايا الملوك غلول)','1080','1531','0');
INSERT INTO [t38171] VALUES (1605,'١٥٩١ - (كان رسول الله - صلى الله عليه وسلم - يقبل الهدية)','1081','1531','0');
INSERT INTO [t38171] VALUES (1606,'١٥٩٢ - ذكر الرشوة وقد وردت في ذمها أخبار','1082','1531','0');
INSERT INTO [t38171] VALUES (1607,'كتاب آداب الأخوة والصحبة','1085','1202','0');
INSERT INTO [t38171] VALUES (1608,'١٥٩٣ - (وقال النبي - صلى الله عليه وسلم - أكثر ما يدخل الجنة تقوى الله وحسن الخلق)','1086','1607','0');
INSERT INTO [t38171] VALUES (1609,'١٥٩٤ - وقال أسامة بن شريك الثعلبي بالمثلثة والمهملة صحابي تفرد بالرواية عنه زياد بن علاقة على الصحيح روى له الأربعة (قلنا يا رسول الله ما خير ما أعطى الإنسان فقال حسن الخلق) وفس نسخة خلق حسن.','1086','1607','0');
INSERT INTO [t38171] VALUES (1610,'١٥٩٥ - وقال - صلى الله عليه وسلم - بعثت لأتمم محاسن الأخلاق','1086','1607','0');
INSERT INTO [t38171] VALUES (1611,'١٥٩٦ - (قال - صلى الله عليه وسلم - أثقل ما يوضع في الميزان خلق حسن)','1087','1607','0');
INSERT INTO [t38171] VALUES (1612,'١٥٩٧ - (قال - صلى الله عليه وسلم - ما حسن الله خلق امرئ وخلقه فيطعمه النار)','1087','1607','0');
INSERT INTO [t38171] VALUES (1613,'١٥٩٨ - (قال - صلى الله عليه وسلم - يا أبا هريرة عليك بحسن الخلق قال أبو هريرة) رضي الله عنه (وما حسن الخلق يا رسول الله قال تصل من قطعك وتعفو عمن ظلمك وتعطي من حرمك)','1088','1607','0');
INSERT INTO [t38171] VALUES (1614,'١٥٩٩ - قال - صلى الله عليه وسلم - إن أقربكم مني مجلسا أحاسنكم أخلاقا الموطؤن أكنافا الذين يألفون ويؤلفون','1088','1607','0');
INSERT INTO [t38171] VALUES (1615,'١٦٠٠ - قال - صلى الله عليه وسلم - المؤمن إلف مألوف ولا خير فيمن لا يألف ولا يؤلف.','1089','1607','0');
INSERT INTO [t38171] VALUES (1616,'١٦٠١ - (وقال - صلى الله عليه وسلم - في الثناء على الإخوة في الدين من أراد الله به خيرا رزقه خليلا صالحا إن نسي ذكره وإن ذكر أعانه)','1089','1607','0');
INSERT INTO [t38171] VALUES (1617,'١٦٠٢ - (وقال - صلى الله عليه وسلم - مثل الأخوين إذا التقيا مثل اليدين تغسل إحداهما الأخرى وما التقي مؤمنان قط إلا أفاد الله أحدهما من صاحبه خيرا)','1090','1607','0');
INSERT INTO [t38171] VALUES (1618,'١٦٠٣ - (قال - صلى الله عليه وسلم - في الترغيب في الأخوة في الله من آخى أخا في الله رفعه الله درجة في الجنة لا ينالها بشىء من عمله)','1090','1607','0');
INSERT INTO [t38171] VALUES (1619,'١٦٠٤ - (رسول الله - صلى الله عليه وسلم - يقول ينصب لطائفة) أي لجماعة من الناس (كراسي) جمع كرسي (حول العرش يوم القيامة وجوههم كالقمر ليلة البدر).','1091','1607','0');
INSERT INTO [t38171] VALUES (1620,'١٦٠٥ - (رواه أبو هريرة) رضي الله عنه عن النبي - صلى الله عليه وسلم - (فقال فيه إن حول العرش منابر من نور عليها قوم لباسهم نور ووجوهم نور ليسوا أنبياء ولا شهداء يغبطهم النبيون والشهداء قالوا يا رسول الله صفهم لنا قال هم المتحابون في الله والمتجالسون فيه','1091','1607','0');
INSERT INTO [t38171] VALUES (1621,'١٦٠٦ - (وقال - صلى الله عليه وسلم - ما تحاب اثنان في ألفة إلا كان أحبهما إلى الله أشدهما حبا لصاحبه).','1092','1607','0');
INSERT INTO [t38171] VALUES (1622,'١٦٠٧ - (قال - صلى الله عليه وسلم - إن الله تعالى يقول حقت محبتي) أي وجبت (للذين يتزاورون من أجلي وحقت محبتي للذين يتحابون من أجلي وحقت محبتي للذين يتناصرون من أجلي).','1093','1607','0');
INSERT INTO [t38171] VALUES (1623,'١٦٠٨ - (وقال - صلى الله عليه وسلم - إن الله تعالى يقول يوم القيامة أين المتحابون لجلالي اليوم أظلهم في ظلي يوم لا ظل إلا ظلي).','1094','1607','0');
INSERT INTO [t38171] VALUES (1624,'١٦٠٩ - سبعة يظلهم الله- الحديث.','1094','1607','0');
INSERT INTO [t38171] VALUES (1625,'١٦١٠ - (وقال - صلى الله عليه وسلم - ما زار رجل رجلا في الله شوقا إليه ورغبة في لقائه إلا ناداه ملك من خلفه طبت وطاب ممشاك وطابت لك الجنة).','1094','1607','0');
INSERT INTO [t38171] VALUES (1626,'١٦١١ - (وقال - صلى الله عليه وسلم - إن رجلا زار أخا) له (في الله فأرصد الله له ملكا فقال أين تريد فقال أريد أن أزور أخي فلانا في الله فقال) تزوره (لحاجة لك عنده) دنيوية (فقال لا قال لقرابة بينك وبينه قال لا قال بنعمة له عندك تربها قال لا قال فمه) أي فما الذي حملكم أن تزوره (قال أحبه في الله تعالى قال إن الله أرسلني إليك يخبرك إنه يحبك بحبك إياه وقد أوجب لك الجنة).','1095','1607','0');
INSERT INTO [t38171] VALUES (1627,'١٦١٢ - (وقال - صلى الله عليه وسلم - أوثق عرى الإيمان)','1095','1607','0');
INSERT INTO [t38171] VALUES (1628,'١٦١٣ - (قال - صلى الله عليه وسلم - إن أحبكم إلى الله الذين يألفون) الناس (ويؤلفون) أي تألفهم الناس (وإن أبغضكم إلى الله المشاءون بالنميمة) أي إفساد ذات البين (المفرقون بين الأخوان). كذا في القوت.','1096','1607','0');
INSERT INTO [t38171] VALUES (1629,'١٦١٤ - (قال - صلى الله عليه وسلم - إن لله ملكا نصفه من النار ونصفه من الثلج يقول) في دعائه أبدا (اللهم كما ألفت بين الثلج والنار). كذلك (ألف بين) قلوب (عبادك الصالحين) كذا في القوت.','1096','1607','0');
INSERT INTO [t38171] VALUES (1630,'١٦١٥ - (وقال) - صلى الله عليه وسلم - (أيضا ما أحدث أحد إخاء) بالمد (في الله) تعالى إلا أحدث الله له درجة في الجنة).','1097','1607','0');
INSERT INTO [t38171] VALUES (1631,'١٦١٦ - (قال) - صلى الله عليه وسلم - (المتحابون في الله على عمود من ياقوتة حمراء في رأس العمود سبعون ألف غرفة) وهي بالضم العلية جمعه غرف وغرفات (يشرفون) أي يطلعون (على أهل الجنة حتى يضيء حسنهم لأهل الجنة كما تضيء الشمس لأهل الدنيا فيقول أهل الجنة انطلقوا بنا ننظر المتحابين في الله فيضيء حسنهم لأهل الجنة)','1097','1607','0');
INSERT INTO [t38171] VALUES (1632,'١٦١٧ - قال الحسن البصري (يا ابن آدم لا يغرنك قول من يقول (المرء مع من أحب).','1098','1607','0');
INSERT INTO [t38171] VALUES (1633,'١٦١٨ - (قال الأرواح جنود مجندة فما تعارف منها ائتلف وما تناكر منها اختلف).','1098','1607','0');
INSERT INTO [t38171] VALUES (1634,'١٦١٩ - إن الأرواح جنود مجندة تلتقي فتشام في الهواء).','1099','1607','0');
INSERT INTO [t38171] VALUES (1635,'١٦٢٠ - (قال - صلى الله عليه وسلم - إن أرواح المؤمنين ليلتقيان على مسيرة يوم وما رأى أحدهما صاحبه قط).','1099','1607','0');
INSERT INTO [t38171] VALUES (1636,'١٦٢١ - (روي أن امرأة بمكة كانت تضحك النساء وكانت بالمدينة أخرى) مثلها (فنزلت المكية على المدنية فدخلت على عائشة) رضي الله عنها فأضحكتها فقالت أين نزلت فذكرت فقالت صدق الله ورسوله سمعت رسول الله - صلى الله عليه وسلم - يقول الأرواح جنود مجندة الحديث).','1100','1607','0');
INSERT INTO [t38171] VALUES (1637,'١٦٢٣ - (وقال نبينا - صلى الله عليه وسلم - في دعائه اللهم إني أسألك رحمة من عندك تهدي بها قلبي وتجمع بها أمري وتلم بها شعثي وتصلح بها غائبي وترفع بها شاهدي وتزكي بها عملي وتلهمني بها رشدى وترد بها ألفتي وتعصمنى بها من كل سوء اللهم أعطني إيمانا ويقينا ليس بعده كفر ورحمة (أنال بها شرف كرامتك في الدنيا والآخرة)','1101','1607','0');
INSERT INTO [t38171] VALUES (1638,'١٦٢٤ - (قال - صلى الله عليه وسلم - (اللهم عافني من بلاء الدنيا وعذاب القبر).','1101','1607','0');
INSERT INTO [t38171] VALUES (1639,'١٦٢٥ - (كان - صلى الله عليه وسلم - إذا حمل إليه باكورة من الفواكه) وهو من أول كل فاكهة ما عجل الأخراج والجمع البواكير والبكورات (مسح بها عينيه وأكرمها وقال إنه قريب عهد بربنا).','1102','1607','0');
INSERT INTO [t38171] VALUES (1640,'١٦٢٦ - قال (ابن عمر) رضي الله عنهما (بينما النبي - صلى الله عليه وسلم - جالس وعنده أبو بكر رضي الله عنه عليه عباءة).','1102','1607','0');
INSERT INTO [t38171] VALUES (1641,'١٦٢٧ - (قال - صلى الله عليه وسلم - المؤمن والمشرك لا تتراءى ناراهما).','1103','1607','0');
INSERT INTO [t38171] VALUES (1642,'١٦٢٨ - (قال - صلى الله عليه وسلم - المرء على دين خليله فلينظر أحدكم من يخالل).','1103','1607','0');
INSERT INTO [t38171] VALUES (1643,'١٦٢٩ - (قال أبو ذر) رضي الله عنه (الوحدة خير من الجليس السوء والجليس الصالح خير من الوحدة).','1104','1607','0');
INSERT INTO [t38171] VALUES (1644,'١٦٣٠ - (خرج - صلى الله عليه وسلم - إلى بئر يغتسل عندها فأمسك حذيفة بن اليمان) رضي الله عنه (الثوب على النبي) - صلى الله عليه وسلم - (ونشره) أي سترة له (حتى اغتسل ثم جلس حذيفة ليغتسل فتناول النبي) - صلى الله عليه وسلم - (الثوب وقام يستر حذيفة من الناس فأبى حذيفة وقال بأبي أنت وأمي يا رسول الله لا تفعل فأبي - صلى الله عليه وسلم - إلا أن يستره بالثوب حتى اغتسل) هكذا أورده صاحب القوت.','1104','1607','0');
INSERT INTO [t38171] VALUES (1645,'١٦٣١ - روي أن النبي - صلى الله عليه وسلم - صحبه رجل في طريق فدخل غيضة (فاجتنى منها سواكين) من أراك (أحدهما معوج والآخر مستقيم فدفع المستقيم إلى صاحبه) وحبس المعوج لنفسه (فقال يا رسول الله كنت أحق بالمستقيم مني فقال ما من صاحب يصحب صاحبا ولو ساعة من نهار إلا سئل عن صحبته هل أقام فيها حق الله أو أضاعه) كذا أورده صاحب القوت.','1104','1607','0');
INSERT INTO [t38171] VALUES (1646,'١٦٣٢ - (قال - صلى الله عليه وسلم - إن لله أواني) جمع آنية (في أرضه وهي القلوب وأحب القلوب إلى الله) أي أكثرها حبا عنده (أصفاها وأصلبها وأرقها) قال المصنف (أصفاها من الذنوب وأصلبها في الدين وأرقها على الأخوان).','1105','1607','0');
INSERT INTO [t38171] VALUES (1647,'١٦٣٣ - وذكر في بعض الأخبار (أن ابن عمر) رضي الله عنهما (كان يلتفت يمينا وشمالا بين يدي النبي - صلى الله عليه وسلم -) ولفظ القوت وقد روينا عن النبي - صلى الله عليه وسلم - أنه رأى ابن عمر يلتفت يمينا وشمالا (فسأله فقال) يا رسول الله (أحببت رجلا فأنا أطلبه ولا أراه فقال) يا عبد الله (إذا أحببت أحدا فسله عن اسمه واسم أبيه وعن منزله فإن كان مريضا عدته وإن كان مشغولا أعنته) كذا في القوت (وفي رواية عن اسم جده وعشيرته).','1105','1607','0');
INSERT INTO [t38171] VALUES (1648,'١٦٣٤ - (قال أنس) بن مالك رضي الله عنه (كان النبي - صلى الله عليه وسلم - لا يواجه أحدا بما يكرهه).','1106','1607','0');
INSERT INTO [t38171] VALUES (1649,'١٦٣٥ - (قال - صلى الله عليه وسلم - استعيذوا بالله من جار السوء الذي إذا رأى خيرا ستره وإذا رأى شرا أظهره).','1106','1607','0');
INSERT INTO [t38171] VALUES (1650,'١٦٣٦ - (قال - صلى الله عليه وسلم -) (إن من البيان سحرا).','1107','1607','0');
INSERT INTO [t38171] VALUES (1651,'١٦٣٧ - (وقال - صلى الله عليه وسلم - في خبر آخر البذاء والبيان شعبتان من النفاق).','1108','1607','0');
INSERT INTO [t38171] VALUES (1652,'١٦٣٨ - (وفي حديث آخر) قال - صلى الله عليه وسلم - (إن الله كره لكم البيان كل البيان).','1108','1607','0');
INSERT INTO [t38171] VALUES (1653,'١٦٣٩ - قول النبي - صلى الله عليه وسلم - (إن الله قد حرم من المؤمن دمه وماله وعرضه وأن يظن به ظن السوء)','1109','1607','0');
INSERT INTO [t38171] VALUES (1654,'١٦٤٠ - وقال - صلى الله عليه وسلم - إياكم والظن فإن الظن أكذب الحديث.','1109','1607','0');
INSERT INTO [t38171] VALUES (1655,'١٦٤١ - (قال - صلى الله عليه وسلم - من ستر عورة أخيه ستره الله في الدنيا والآخرة)','1109','1607','0');
INSERT INTO [t38171] VALUES (1656,'١٦٤٢ - (في خبر آخر فكأنما أحيا موؤدة)','1110','1607','0');
INSERT INTO [t38171] VALUES (1657,'١٦٤٣ - (قال - صلى الله عليه وسلم - إذا حدث الرجل بحديث)','1111','1607','0');
INSERT INTO [t38171] VALUES (1658,'١٦٤٤ - (قال - صلى الله عليه وسلم - المجالس بالأمانة)','1111','1607','0');
INSERT INTO [t38171] VALUES (1659,'١٦٤٥ - (وقال - صلى الله عليه وسلم - إنما يتجالس المتجالسان بالأمانة لا يحل لأحدهما أن يفشي على صاحبه ما يكره)','1112','1607','0');
INSERT INTO [t38171] VALUES (1660,'١٦٤٦ - (وقال - صلى الله عليه وسلم - لا تدابروا ولا تباغضوا ولا تقاطعوا ولا تحاسدوا وكونوا عباد الله إخوانا)','1112','1607','0');
INSERT INTO [t38171] VALUES (1661,'١٦٤٧ - (في حديث أبي أمامة) صدي بن عجلان (الباهلي) رضي الله عنه سكن الشام ومات بها سنة ست وثماثين (قال خرج علينا رسول الله - صلى الله عليه وسلم - ونحن نتمارى فغضب وقال ذروا المراء)','1113','1607','0');
INSERT INTO [t38171] VALUES (1662,'١٦٤٨ - (روى ابن عباس) رضي الله عنهما (عن النبي - صلى الله عليه وسلم - إنه قال لأتماره) أي لا تخاصمه (ولا تماذحه) بما يتأذى به (ولا تعده موعدا فتخلفه).','1113','1607','0');
INSERT INTO [t38171] VALUES (1663,'١٦٤٩ - (قال عليه السلام إنكم لا تسعون الناس بأموالكم ولكن ليسعهم منكم بسط وجه وحسن خلق)','1114','1607','0');
INSERT INTO [t38171] VALUES (1664,'١٦٥٠ - (قال - صلى الله عليه وسلم - إذا أحب أحدكم أخاه) أي لما فيه من الصفات المرضية (فليخبره) ندبا مؤكدا أي أنه يحبه.','1114','1607','0');
INSERT INTO [t38171] VALUES (1665,'١٦٥١ - قال - صلى الله عليه وسلم - أحسن مجاورة من جاورك تكن مسلما وأحسن مصاحبة من صاحبك تكن مؤمنا)','1115','1607','0');
INSERT INTO [t38171] VALUES (1666,'١٦٥٢ - (قال - صلى الله عليه وسلم - المؤمن مرآة المؤمن).','1115','1607','0');
INSERT INTO [t38171] VALUES (1667,'١٦٥٣ - عن رسول الله - صلى الله عليه وسلم - (اتقوا زلة العالم ولا تقطعوه وانتظروا فيئته).','1115','1607','0');
INSERT INTO [t38171] VALUES (1668,'١٦٥٤ - (قال - صلى الله عليه وسلم - شرار عباد الله المشاؤن بالنميمة المفرقون بين الأحبة).','1116','1607','0');
INSERT INTO [t38171] VALUES (1669,'١٦٥٥ - (قال عليه السلام من اعتذر إليه أخوه فلم يقبل عذره فعليه مثل إثم صاحب المكس).','1116','1607','0');
INSERT INTO [t38171] VALUES (1670,'١٦٥٦ - (وقال - صلى الله عليه وسلم - المؤمن سريع الغضب سريع الرضا).','1117','1607','0');
INSERT INTO [t38171] VALUES (1671,'١٦٥٧ - (قال عليه السلام أحبب حبيبك هونا ما عسى أن يكون بغيضك يوما ما وأبغض بغيضك هونا ما عسى أن يكون حبيبك يوما ما).','1117','1607','0');
INSERT INTO [t38171] VALUES (1672,'١٦٥٨ - (قال - صلى الله عليه وسلم - إذا دعا الرجل لأخيه بظهر الغيب قال الملك ولك مثل ذلك).','1118','1607','0');
INSERT INTO [t38171] VALUES (1673,'١٦٥٩ - (وفي لفظ آخر) من هذا الحديث (يقول الله عز وجل بك أبدأ)','1119','1607','0');
INSERT INTO [t38171] VALUES (1674,'١٦٦٠ - (وفي الحديث دعوة الرجل لأخيه في ظهر الغيب لا ترد).','1119','1607','0');
INSERT INTO [t38171] VALUES (1675,'١٦٦١ - (جاء في الخبر) عن النبي - صلى الله عليه وسلم - أنه قال (إذا مات العبد قال الناس ما خلف وقالت الملائكة ما قدم).','1119','1607','0');
INSERT INTO [t38171] VALUES (1676,'١٦٦٢ - (روي عن رسول الله - صلى الله عليه وسلم - أنه قال مثل الميت في قبره مثل الغريق)','1120','1607','0');
INSERT INTO [t38171] VALUES (1677,'١٦٦٣ - (روي أنه - صلى الله عليه وسلم - أكرم عجوزا)','1120','1607','0');
INSERT INTO [t38171] VALUES (1678,'١٦٦٤ - ولفظ القوت حدثنا محمد بن القاسم عن الربيع بن سليمان (أن الشافعي) رضي الله عنه (آخى رجلا ببغداد ثم إن أخاه هذا ولى السيبين) بكسر السين المهملة وسكون التحية وفتح الموحدة مثنى السيب وهما الأعلى والأسفل كورة بالعراق (فتغير) للشافعي (عما كان عليه) مما كان يعهده منه (فكتب إليه الشافعي) رحمة الله تعالى (هذه الأبيات) وهي من نظمه','1121','1607','0');
INSERT INTO [t38171] VALUES (1679,'١٦٦٥ - (قال - صلى الله عليه وسلم - أنا والأتقياء من أمتي براء من التكلف)','1122','1607','0');
INSERT INTO [t38171] VALUES (1680,'١٦٦٦ - (قال بعضهم إذا عمل الرجل في بيت أخيه أربع خصال فقد تم أنسه به إذا أكل عنده ودخل الخلاء ونام وصلى)','1123','1607','0');
INSERT INTO [t38171] VALUES (1681,'١٦٦٧ - (قال - صلى الله عليه وسلم - المرء على دين خليله ولا خير في صحبة من لا يرى لك مثل ما ترى له)','1123','1607','0');
INSERT INTO [t38171] VALUES (1682,'١٦٦٨ - (قال - صلى الله عليه وسلم - بحسب المؤمن من الشر أن يحتقر أخاه المسلم)','1124','1607','0');
INSERT INTO [t38171] VALUES (1683,'١٦٦٩ - (آخى رسول الله - صلى الله عليه وسلم - عليا) رضي الله عنه (فشاركه في العلم)','1124','1607','0');
INSERT INTO [t38171] VALUES (1684,'١٦٧٠ - (روى في الخبر كان - صلى الله عليه وسلم - يعطي كل من جلس إليه نصيبه من وجهه وما أتاه أحد إلا أظن أنه أكرم الناس عليه حتى كان مجلسه في سمعه وحديثه ولطيف مسألته وتوجهه للمجالس إليه وكان مجلسه مجلس حياء وتواضع وأمانة)','1125','1607','0');
INSERT INTO [t38171] VALUES (1685,'١٦٧١ - (قال - صلى الله عليه وسلم - من جلس في مجلس فكثر فيه لغطه فقال قبل أن يقوم من مجلسه ذلك سبحانك اللهم وبحمدك أشهد أن لا إله إلا أنت استغفرك وأتوب إليك إلا غفر له ما كان في مجلسه ذلك).','1125','1607','0');
INSERT INTO [t38171] VALUES (1686,'١٦٧٢ - قوله - صلى الله عليه وسلم - لو كنت متخذا خليلا لاتخذت أبا بكر خليلا ولكن صاحبكم خليل الله.','1126','1607','0');
INSERT INTO [t38171] VALUES (1687,'١٦٧٣ - (فقال علي مني بمنزلة هارون من موسى إلا النبوة).','1127','1607','0');
INSERT INTO [t38171] VALUES (1688,'١٦٧٤ - (وكان - صلى الله عليه وسلم - حبيب الله وخليله فقد روي أنه) - صلى الله عليه وسلم - (صعد المنبر يوما مستبشرا فرحا فقال) ألا (إن الله) تبارك وتعالى (قد اتخذني خليلا كما اتخذ إبراهيم خليلا فأنا حبيب الله وأنا خليل الله)','1128','1607','0');
INSERT INTO [t38171] VALUES (1689,'١٦٧٥ - (وقد روى أنس) رضي الله عنه (عن رسول الله - صلى الله عليه وسلم - أنه قال أربع من حق المسلمين عليك أن تعين محسنهم وأن تستغفر لمذنبهم وأن تدعو لمدبرهم وأن تحب تائبهم).','1128','1607','0');
INSERT INTO [t38171] VALUES (1690,'١٦٧٦ - (حقوق المسلم) هي أن تسلم عليه إذا لقيته وتحبيبه إذا دعاك وتشمته إذا عطس وتعوده إذا مرض وتشهد جنازته إذا مات وتبر قسمه إذا أقسم عليك وتنصح له إذا استنصحك وتحفظه بظهر الغيب إذا غاب عنك وتحب له ما تحب لنفسك وتكره له ما تكره لنفسك ورد جميع ذلك في أخبار وأثار.','1128','1607','0');
INSERT INTO [t38171] VALUES (1691,'١٦٧٧ - (مثل المؤمنين في توادهم وتراحمهم كمثل الجسد إذا اشتكى عضو منه تداعى سائره بالسهر بالحمى).','1130','1607','0');
INSERT INTO [t38171] VALUES (1692,'١٦٧٨ - (روى أبو موسى) الأشعري رضي الله عنه (عنه - صلى الله عليه وسلم - أنه قال المؤمن للمؤمن كالبنيان) المراد بعض المؤمنين لبعض أي لا يتقوى في أمر دينه ودنياه إلا بمعونة أخيه كما أن بعض البنيان يقوي بعضه بعضا (يشد بعضه بعضا) بيان لوجه التشبيه وبعضا منصوب بنزع الخافض أو مفعول يشد.','1130','1607','0');
INSERT INTO [t38171] VALUES (1693,'١٦٧٩ - (قال - صلى الله عليه وسلم - المسلم من سلم المسلمون من لسانه ويده).','1130','1607','0');
INSERT INTO [t38171] VALUES (1694,'١٦٨٠ - (وقال - صلى الله عليه وسلم - في حديث طويل يأمر فيه بالفصائل فإن لا تقدر فدع الناس من الشر فإنها).','1131','1607','0');
INSERT INTO [t38171] VALUES (1695,'١٦٨١ - قال - صلى الله عليه وسلم - (أيضا أفضل المسلمين من سلم المسلمون من لسانه ويده).','1131','1607','0');
INSERT INTO [t38171] VALUES (1696,'١٦٨٢ - (قال - صلى الله عليه وسلم - أتدرون من المسلم فقالوا الله ورسوله أعلم فقال المسلم من سلم المسلمون من لسانه ويده قالوا فمن المؤمن قال','1131','1607','0');
INSERT INTO [t38171] VALUES (1697,'١٦٨٣ - (قال - صلى الله عليه وسلم - لقد رأيت رجلا يتقلب في الجنة في شجرة قطعها عن ظهر الطريق كانت تؤذي المسلمين).','1132','1607','0');
INSERT INTO [t38171] VALUES (1698,'١٦٨٤ - (وقال أبو هريرة) هكذا في سائر نسخ الكتاب ووجدت بخط الحافظ العراقي ما نصه ولعله أبو برزة وهكذا رأيت في نسخة من نسخ الكتاب مصلحا بخط بعض من يوثق به وكذا في نسخ الجامع الصغير كتب بعض المقيدين أبو برزة بإزاء أبي هريرة (يا رسول الله علمني شيئا أنتفع به فقال عليه السلام أعزل الأذى عن طريق المسلمين) أي أزل عن طريقهم ما يؤذيهم من حجر أو حجر أو غصن أو شوك أو جيفة أو قذر وإن كان يسيرا حقيرا ويظهر أن المراد بالطريق المسلوك لا المهجور وإن مر فيه على ندور وخرج بطريق المسلمين طريق أهل الحرب وغيرهم فلا يندب عزل الأذى عنها.','1133','1607','0');
INSERT INTO [t38171] VALUES (1699,'١٦٨٥ - (وقال - صلى الله عليه وسلم - من زحزح عن طريق المسلمين شيئا يؤذيهم كتب له بها حسنة ومن كتب له حسنة أوجب الله له بها الجنة).','1133','1607','0');
INSERT INTO [t38171] VALUES (1700,'١٦٨٦ - (وقال - صلى الله عليه وسلم - لا يحل لمسلم أن يشير إلى أخيه بنظرة تؤذيه).','1133','1607','0');
INSERT INTO [t38171] VALUES (1701,'١٦٨٧ - (وقال - صلى الله عليه وسلم - لا يحل لمسلم أن يروع مسلما)','1134','1607','0');
INSERT INTO [t38171] VALUES (1702,'١٦٨٨ - (وقال - صلى الله عليه وسلم - إن الله تعالى يكره أذى المؤمن)','1134','1607','0');
INSERT INTO [t38171] VALUES (1703,'١٦٨٩ - (وقال - صلى الله عليه وسلم - إن الله عز وجل أوحى إلي أن تواضعوا حتى لا يفخر أحد على أحد).','1134','1607','0');
INSERT INTO [t38171] VALUES (1704,'١٦٩٠ - (عن ابن أبي أوفي كان رسول الله - صلى الله عليه وسلم - يتواضع لكل مسلم ولا يأنف ولا يتكبر أن يمشي مع الأرملة والمسكين فيقضي','1134','1607','0');
INSERT INTO [t38171] VALUES (1705,'١٦٩١ - (قال - صلى الله عليه وسلم - لا يدخل الجنة قتات)','1135','1607','0');
INSERT INTO [t38171] VALUES (1706,'١٦٩٢ - قال رسول الله - صلى الله عليه وسلم - لا يحل لمسلم أن يهجر أخاه فوق ثلاث)','1135','1607','0');
INSERT INTO [t38171] VALUES (1707,'١٦٩٣ - (وقال - صلى الله عليه وسلم - من أقال مسلما عثرته أقاله الله يوم القيامة).','1136','1607','0');
INSERT INTO [t38171] VALUES (1708,'١٦٩٤ - (قال - صلى الله عليه وسلم - ما نقص مال من صدقة وما زاد الله رجلا بعفو إلا عزا وما من أحد تواضع لله)','1136','1607','0');
INSERT INTO [t38171] VALUES (1709,'١٦٩٥ - (روى علي بن الحسين عن أبيه عن جده رضي الله','1136','1607','0');
INSERT INTO [t38171] VALUES (1710,'١٦٩٦ - (وعنه بإسناده قال قال رسول الله - صلى الله عليه وسلم - رأس العقل بعد الدين التودد إلى الناس واصطناع المعروف إلى كل بر وفاجر)','1137','1607','0');
INSERT INTO [t38171] VALUES (1711,'١٦٩٧ - (وقال أبو هريرة الله عنه كان رسول الله - صلى الله عليه وسلم - لا يأخذ أحد بيده فينزع يده حتى يكون هو الذي يرسله ولم يكن ترى ركبته خارجة عن ركبة جليسه ولم يكن أحد يكلمه إلا أقبل عليه بوجهه ثم لم ينصرف)','1138','1607','0');
INSERT INTO [t38171] VALUES (1712,'١٦٩٨ - قال أبو هريرة رضي الله عنه قال رسول الله - صلى الله عليه وسلم -','1138','1607','0');
INSERT INTO [t38171] VALUES (1713,'١٦٩٩ - قال جابر رضي الله عنه قال رسول الله - صلى الله عليه وسلم - ليس منا من لم يوقر كبيرنا ولم يرحم صغيرنا.','1139','1607','0');
INSERT INTO [t38171] VALUES (1714,'١٧٠٠ - وقال - صلى الله عليه وسلم - من إجلال الله إكرام ذي الشيبة المسلم','1140','1607','0');
INSERT INTO [t38171] VALUES (1715,'١٧٠١ - (قال جابر) بن عبد الله رضي الله عنه (قدم وفد جهينة) وهي قبيلة من قضاعة (على رسول الله - صلى الله عليه وسلم - فقام غلام)','1140','1607','0');
INSERT INTO [t38171] VALUES (1716,'١٧٠٢ - (وفي الخبر) عن النبي - صلى الله عليه وسلم - (ما وقر) أي عظم (شاب شيخا) لأجل سنه (إلا قيض الله له) أي سبب وقدر في سنته مجازاة له على فعله (من يوقره) بأن يقدر له عمرا يبلغ به إلى الشيخوخة','1140','1607','0');
INSERT INTO [t38171] VALUES (1717,'١٧٠٣ - وقال - صلى الله عليه وسلم - لا تقوم الساعة حتى يكون الولد غيظا والمطر قيظا وتفيض اللئام فيضا وتغيض الكرام غيضا ويجترئ الصغير على الكبير واللئيم على الكريم','1141','1607','0');
INSERT INTO [t38171] VALUES (1718,'١٧٠٤ - كان - صلى الله عليه وسلم - يقدم من السفر فيتلقاه الصبيان فيقف عليهم ثم يأمر بهم فيرفعون إليه فيرفع منهم بين يديه ومن خلفه ويأمر أصحابه أن يحملوا بعضهم فربما تفاخر الصبيان بعد ذلك فيقول بعضهم لبعض حملني رسول الله - صلى الله عليه وسلم - بين يديه وحملك أنت وراءه ويقول بعضهم أمر أصحابه أن يحملوك وراءهم.','1141','1607','0');
INSERT INTO [t38171] VALUES (1719,'١٧٠٥ - (وكان - صلى الله عليه وسلم - يؤتى بالصبي الصغير ليدعو له بالبركة وليسميه فيأخذه فيضعه في حجره فربما بال الصبي)','1142','1607','0');
INSERT INTO [t38171] VALUES (1720,'١٧٠٦ - (قال رسول الله - صلى الله عليه وسلم - أتدرون على من حرمت النار قالوا الله ورسوله أعلم قال)','1142','1607','0');
INSERT INTO [t38171] VALUES (1721,'١٧٠٧ - وقال أبو هريرة) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - إن الله يحب السهل).','1143','1607','0');
INSERT INTO [t38171] VALUES (1722,'١٧٠٨ - (وقال بعضهم يا رسول الله دلني على عمل يدخلني الجنة فقال إن من موجبات المغفرة).','1143','1607','0');
INSERT INTO [t38171] VALUES (1723,'١٧٠٩ - (وقال عبد الله بن عمر رضي الله عنهما) فيما يروي عنه (البر شىء هين وجه طليق وكلام لين).','1144','1607','0');
INSERT INTO [t38171] VALUES (1724,'١٧١٠ - (وقال - صلى الله عليه وسلم - إن في الجنة لغرفا يرى ظهورها من بطونها وبطونها من ظهورها).','1144','1607','0');
INSERT INTO [t38171] VALUES (1725,'١٧١١ - (وقال معاذ بن جبل) رضي الله عنه (قال لي رسول الله - صلى الله عليه وسلم - أوصيك) يا معاذ (بتقوى الله تعالى وصدق الحديث ووفاء العهد وأداء الأمانة وترك الخيانة وحفظ الجار ورحمة اليتيم ولين الكلام وبذل السلام وخفض الجناح).','1145','1607','0');
INSERT INTO [t38171] VALUES (1726,'١٧١٢ - (قال أنس) رضي الله عنه (عرضت لرسول الله - صلى الله عليه وسلم - امرأة) كان في عقلها شيء (وقالت لي معك حاجة وكان معه ناس من أصحابه فقال) لها (اجلسي في أي نواحي السكك) أي سكك المدينة (شئت أجلس إليك ففعلت فجلس إليها حتى قضى حاجتها).','1146','1607','0');
INSERT INTO [t38171] VALUES (1727,'١٧١٣ - (قال - صلى الله عليه وسلم - العدة عطية)','1146','1607','0');
INSERT INTO [t38171] VALUES (1728,'١٧١٤ - (وقال - صلى الله عليه وسلم - العدة دين) أي كالدين في تأكد الوفاء بها فإذا أحسنت القول فأحسن الفعل ليجتمع لك مزية اللسان وثمرة الإحسان ولا تقل ما لا تفعل.','1147','1607','0');
INSERT INTO [t38171] VALUES (1729,'١٧١٥ - (حديث آية المنافق ثلاث).','1147','1607','0');
INSERT INTO [t38171] VALUES (1730,'١٧١٦ - (وقال - صلى الله عليه وسلم - ثلاث من كن فيه فهو منافق).','1148','1607','0');
INSERT INTO [t38171] VALUES (1731,'١٧١٧ - (ومنها أن ينصف الناس من نفسه ولا يأتي إليهم إلا بما يحب أن يؤتى إليه قال - صلى الله عليه وسلم - لا يستكمل العبد الإيمان حتى تكون فيه ثلاث خصال الإنفاق من الإقتار) أي الافتقار أقتر الرجل إذا افتقر فيكون المعنى الإنفاق من العدم وهو مشكل إذ العدم لا ينفق منه ويخرج على وجوه إما أن يكون من بمعنى في والمعنى الإنفاق في حالة الفقر وهو من غاية الكرم أو بمعنى عند أي عند الفقر (والإنصاف من نفسه) أي العدل منها يقال أنصف من نفسه وانتصفت أنا منه (وبذل السلام) أي إعطاؤه وإفشاؤه.','1148','1607','0');
INSERT INTO [t38171] VALUES (1732,'١٧١٨ - (وقال - صلى الله عليه وسلم - من سره أن يزحزح) أي يخرج (عن النار و) أن (يدخل الجنة فلتأته منيته) أي موته المقدر (وهو يشهد أن لا إله إلا الله وأن محمدا رسول الله وليأت إلى الناس ما يحب أن يؤتى إليه).','1149','1607','0');
INSERT INTO [t38171] VALUES (1733,'١٧١٩ - (وقال - صلى الله عليه وسلم - يا أبا الدرداء أحسن مجاورة من جاورك تكن مؤمنا وأحب للناس ما تحب لنفسك تكن مسلما)','1149','1607','0');
INSERT INTO [t38171] VALUES (1734,'١٧٢٠ - (روي أن عائشة رضي الله عنها كانت في سفر فنزلت منزلا فوضعت طعامها) لتأكل (فجاء سائل) فسأل (فقالت عائشة رضي الله عنها)','1150','1607','0');
INSERT INTO [t38171] VALUES (1735,'١٧٢١ - قال - صلى الله عليه وسلم - إذا أتاكم كريم قوم فأكرموه','1151','1607','0');
INSERT INTO [t38171] VALUES (1736,'١٧٢٢ - حديث الشيماء ومقدمها عليه - صلى الله عليه وسلم -.','1152','1607','0');
INSERT INTO [t38171] VALUES (1737,'١٧٢٣ - (ولربما أتاه) - صلى الله عليه وسلم - (من يأتيه وهو على وسادة جالس فلا يكون فيها سعة يجلس معهم فينزعها) من تحته (ويضعها تحت الذي يجلس إليه فإن أبى) من جلسوه عليها (عزم عليه حتى يفعل).','1153','1607','0');
INSERT INTO [t38171] VALUES (1738,'١٧٢٤ - (قال - صلى الله عليه وسلم - ألا أخبركم بأفضل) أي بدرجة هي أفضل (من درجة الصيام والصلاة والصدقة).','1153','1607','0');
INSERT INTO [t38171] VALUES (1739,'١٧٢٥ - (وقال - صلى الله عليه وسلم - أفضل الصدقة إصلاح ذات البين).','1154','1607','0');
INSERT INTO [t38171] VALUES (1740,'١٧٢٦ - (و) روى (عن أنس) رضي الله عنه (قال بينما رسول الله - صلى الله عليه وسلم - جالس إذ ضحك حتى بدت ثناياه فقال عمر) بن الخطاب رضي الله عنه (بأبي أنت وأمي ما الذي أضحكك يا رسول الله قال رجلان من أمتي جثيا) على ركبهما (بين يدي رب العزة) جل شأنه (فقال أحدهما يا رب خذ مظلمتي من هذا فقال الله عز وجل رد على أخيك المسلم مظلمته فقال يا رب لم يبق لي من حسناتي شيء فقال الله تعالى للطالب كيف تصنع بأخيك لم يبق من حسناته شيء فقال يا رب فليحمل عني من أوزاري) شيئا (ثم فاضت عينا رسول لله بالبكاء) لما تذكر ذلك الموقف العظيم (فقال إن ذلك ليوم عظيم يوم يحتاج الناس) فيه (إلى أن يحمل عنهم من أوزارهم فيقول الله عز وجل للمظلوم) وفي نسخة للمتظلم (ارفع بصرك فانظر في الجنان)','1154','1607','0');
INSERT INTO [t38171] VALUES (1741,'١٧٢٨ - (وقال - صلى الله عليه وسلم - كل الكذب مكتوب) على ابن آدم وفي رواية يكتب (إلا) ثلاثا (أن يكذب الرجل في الحرب) فلا يكتب عليه ذلك (فإن الحرب خدعة) بل قد يجب إذا دعت إليه ضرورة أهل الإسلام (أو يكذب بين اثنين) بينهما نحو إحن وفتن (ليصلح بينهما) بقوله ذلك (أو يكذب لامرأته ليرضيها).','1155','1607','0');
INSERT INTO [t38171] VALUES (1742,'١٧٢٩ - (قال - صلى الله عليه وسلم - من ستر على مسلم ستره الله في الدنيا والآخرة).','1156','1607','0');
INSERT INTO [t38171] VALUES (1743,'١٧٣٠ - (وقال - صلى الله عليه وسلم - لا يستر عبد عبدا إلا ستره الله يوم القيامة)','1156','1607','0');
INSERT INTO [t38171] VALUES (1744,'١٧٣١ - (وقال أبو سعيد الخدري) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - لا يرى امرؤ في أخيه عورة فسترها عليه إلا دخل الجنة).','1156','1607','0');
INSERT INTO [t38171] VALUES (1745,'١٧٣٢ - (وقال - صلى الله عليه وسلم - لماعز) هو ابن مالك الأسلمي (لما أخبره) عن قصته (لو سترته بثوبك كان خيرا لك).','1157','1607','0');
INSERT INTO [t38171] VALUES (1746,'١٧٣٣ - (عن النبي - صلى الله عليه وسلم - (قال إن الله تعالى إذا ستر على عبده عورة في الدنيا فهو أكرم من أن يكشفها) عليه (في الآخرة فإن كشفها في الدنيا فهو أكرم من أن يكشفها مرة أخرى).','1157','1607','0');
INSERT INTO [t38171] VALUES (1747,'١٧٣٤ - (وقد قال - صلى الله عليه وسلم - لمعاوية) بن أبي سفيان رضي الله عنه (إنك إن اتبعت عورات الناس أفسدتهم أو كدت تفسدهم).','1158','1607','0');
INSERT INTO [t38171] VALUES (1748,'١٧٣٥ - (وقال - صلى الله عليه وسلم - يا معشر من آمن بلسانه ولم يدخل الإيمان في قلبه لا تغتابوا الناس ولا تتبعوا عوراتهم فإنه من يتبع عورة أخيه المسلم يتبع الله عورته ومن يتبع الله عورته يفضحه ولو كان في جوف بيته).','1158','1607','0');
INSERT INTO [t38171] VALUES (1749,'١٧٣٦ - (إن الله عفو يحب العفو)','1159','1607','0');
INSERT INTO [t38171] VALUES (1750,'١٧٣٧ - (يقول إن الله تعالى ليدني) أي ليقرب (منه المؤمن فيضع عليه كنفه ويستره من الناس فيقول له أتعرف ذنب كذا أتعرف ذنب كذا)','1161','1607','0');
INSERT INTO [t38171] VALUES (1751,'١٧٣٨ - (قال - صلى الله عليه وسلم - كل أمتي معافى إلا المجاهرين وإن من المجاهرة أن يعمل الرجل السوء سرا ثم يخبره)','1161','1607','0');
INSERT INTO [t38171] VALUES (1752,'١٧٣٩ - (قال - صلى الله عليه وسلم - من استمع خبر قوم وهم له كارهون صب في إذنه الأنك يوم القيامة)','1162','1607','0');
INSERT INTO [t38171] VALUES (1753,'١٧٤٠ - (قال - صلى الله عليه وسلم - إني أوتى وأسأل وتطلب إلي الحاجة وأنتم عندي فاشفعوا لتؤجروا ويقضي الله على يدي نبيه ما أحب)','1162','1607','0');
INSERT INTO [t38171] VALUES (1754,'١٧٤١ - (وقال - صلى الله عليه وسلم - ما من صدقة أفضل من صدقة اللسان قيل وكيف ذلك)','1163','1607','0');
INSERT INTO [t38171] VALUES (1755,'١٧٤٢ - (فقال لا إنما أنا شافع)','1163','1607','0');
INSERT INTO [t38171] VALUES (1756,'١٧٤٣ - (قال - صلى الله عليه وسلم - من بدأ بالكلام قبل السلام فلا تجيبوه حتى يبدأ بالسلام)','1163','1607','0');
INSERT INTO [t38171] VALUES (1757,'١٧٤٤ - (وقال بعضهم دخلت على رسول الله - صلى الله عليه وسلم - ولم أسلم ولم أستأذن فقال - صلى الله عليه وسلم - ارجع فقل السلام عليكم أدخل)','1164','1607','0');
INSERT INTO [t38171] VALUES (1758,'١٧٤٥ - (وروى جابر) بن عبد الله رضي الله عنه (قال قال رسول الله - صلى الله عليه وسلم - إذا دخلتم بيوتكم فسلموا على أهلها فإن الشيطان إذا سلم أحدكم لم يدخل بيته)','1164','1607','0');
INSERT INTO [t38171] VALUES (1759,'١٧٤٦ - (قال يا أنس أسبغ الوضوء يزد في عمرك وسلم على من لقيته من أمتي تكثر حسناتك وإذا دخلت منزلك فسلم على أهل بيتك يكثر خير بيتك)','1164','1607','0');
INSERT INTO [t38171] VALUES (1760,'١٧٤٧ - (قال أفشوا السلام بينكم)','1165','1607','0');
INSERT INTO [t38171] VALUES (1761,'١٧٤٨ - (وقال - صلى الله عليه وسلم - أيضا إذا سلم المسلم على المسلم فرد عليه) بأن قال وعليكم السلام (صلت عليه الملائكة سبعين مرة)','1165','1607','0');
INSERT INTO [t38171] VALUES (1762,'١٧٤٩ - (وقال - صلى الله عليه وسلم - يسلم الراكب على الماشي وإذا سلم واحد من القوم أجزأ عنهم).','1165','1607','0');
INSERT INTO [t38171] VALUES (1763,'١٧٥٠ - (جاء رجل إلى رسول الله - صلى الله عليه وسلم - فقال سلام عليك) وفي نسخة عليكم وفي أخرى السلام عليكم (فقال له رسول الله - صلى الله عليه وسلم - عشر حسنات فجاء آخر فقال سلام عليكم ورحمة الله فقال عشرون حسنة فجاء آخر فقال سلام عليكم ورحمة الله وبركاته فقال ثلاثون حسنة.','1167','1607','0');
INSERT INTO [t38171] VALUES (1764,'١٧٥١ - (وكان أنس) رضي الله عنه (يمر على الصبيان فيسلم عليهم وروي) هو (عن رسول الله - صلى الله عليه وسلم - أنه فعل ذلك).','1168','1607','0');
INSERT INTO [t38171] VALUES (1765,'١٧٥٢ - (وروى عبد الحميد بن بهرام) الفزاري المدائني صدوق روى له البخاري في الأدب المفرد والترمذي وابن ماجه (أنه - صلى الله عليه وسلم - مر في المسجد يوما وعصبة من النساء قعود فاومأ بيده بالتسليم وأشار عبد الحميد بيده إلى الحكاية).','1169','1607','0');
INSERT INTO [t38171] VALUES (1766,'١٧٥٣ - (قال عليه السلام لا تبدؤا اليهود ولا النصارى بالسلام وإذا لقيتم أحدهم في الطريق فاضطروه إلى أضيقه).','1170','1607','0');
INSERT INTO [t38171] VALUES (1767,'١٧٥٤ - (وعن أبي هريرة) رضي الله عنه (قال قال رسول الله - صلى الله عليه وسلم - لا تصافحوا أهل الذمة ولا تبدؤهم بالسلام).','1170','1607','0');
INSERT INTO [t38171] VALUES (1768,'١٧٥٥ - (قالت عائشة رضي الله عنها إن رهطا من اليهود دخلوا على رسول الله - صلى الله عليه وسلم - فقالوا السام عليك فقالت عائشة).','1170','1607','0');
INSERT INTO [t38171] VALUES (1769,'١٧٥٦ - (وقال - صلى الله عليه وسلم - يسلم الراكب على الماشي والماشي على القاعد والقليل على الكثير والصغير على الكبير)','1172','1607','0');
INSERT INTO [t38171] VALUES (1770,'١٧٥٧ - (وقال - صلى الله عليه وسلم - لا تشبهوا باليهود و) لا (النصارى فإن تسليم اليهود الإشارة بالأصابع وتسليم النصارى الإشارة بالكف)','1173','1607','0');
INSERT INTO [t38171] VALUES (1771,'١٧٥٨ - (وقال - صلى الله عليه وسلم - إذا انتهى أحدكم إلى مجلس فليسلم فإن بدا له أن يجلس فليجلس ثم إذا قام فليسلم فليست الأولى بأحق من الآخرة)','1174','1607','0');
INSERT INTO [t38171] VALUES (1772,'١٧٥٩ - (وقال أنس) رضي الله عنه (إذا التقى المؤمنان فتصافحا)','1175','1607','0');
INSERT INTO [t38171] VALUES (1773,'١٧٦٠ - (قال عمر) بن الخطاب (رضي الله عنه سمعت رسول الله - صلى الله عليه وسلم - يقول إذا التقى المسلمان فسلم كل منهما على صاحبه وتصافحا نزلت بينهما مائة رحمة للبادئ)','1175','1607','0');
INSERT INTO [t38171] VALUES (1774,'١٧٦١ - (قال أبو هريرة) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - تمام تحياتكم بينكم المصافحة)','1176','1607','0');
INSERT INTO [t38171] VALUES (1775,'١٧٦٢ - (قال - صلى الله عليه وسلم - قبلة المسلم)','1176','1607','0');
INSERT INTO [t38171] VALUES (1776,'١٧٦٣ - (روي عن ابن عمر) رضي الله عنهما (قال قبلنا يد النبي - صلى الله عليه وسلم -)','1176','1607','0');
INSERT INTO [t38171] VALUES (1777,'١٧٦٤ - وعن كعب بن مالك بن أبي كعب الأنصاري السلمي بالفتح المدني صحابي مشهور وهو أحد الثلاثة الذين خلفوا عن غزوة تبوك مات في خلافة علي روى له الجاعة (قال لما نزلت توبتي)','1176','1607','0');
INSERT INTO [t38171] VALUES (1778,'١٧٦٥ - (روي أن أعرابيا) أي من سكان البادية (قال يا رسول الله ائذن لي فأقبل رأسك ويدك فإذن له ففعل)','1177','1607','0');
INSERT INTO [t38171] VALUES (1779,'١٧٦٦ - (إن المسلمين إذا التقيا فتصافحا تحاتت)','1177','1607','0');
INSERT INTO [t38171] VALUES (1780,'١٧٦٧ - (وعنه - صلى الله عليه وسلم - أنه قال إذا مر الرجل بالقوم فسلم عليهم فردوا عليه)','1177','1607','0');
INSERT INTO [t38171] VALUES (1781,'١٧٦٨ - (والانحناء عند السلام منهى عنه) وهو من فعل الأعاجم (قال أنس) رضي الله عنه (قلنا يا رسول الله أينحني بعضنا لبعض)','1177','1607','0');
INSERT INTO [t38171] VALUES (1782,'١٧٦٩ - (قال أبو ذر رضي الله عنه ما لقيته - صلى الله عليه وسلم - إلا صافحني وطلبني يوما فلم) يجدني لأني لم (أكن في البيت فلما أخبرت جئت وهو) جالس (على سرير) فقام (فالتزمني فكانت أجود وأجود).','1178','1607','0');
INSERT INTO [t38171] VALUES (1783,'١٧٧٠ - (قال أنس) رضي الله عنه (ما كان شخص أحب إلينا) وفي نسخة إليهم (من رسول الله - صلى الله عليه وسلم - وكانوا إذا رأوه لم يقوموا) له (لما) كانوا (يعلمون من كراهيته لذلك).','1178','1607','0');
INSERT INTO [t38171] VALUES (1784,'١٧٧١ - (وروي أنه - صلى الله عليه وسلم - قال مرة إذا رأيتموني فلا تقوموا كما تصنع الأعاجم).','1178','1607','0');
INSERT INTO [t38171] VALUES (1785,'١٧٧٢ - (وقال - صلى الله عليه وسلم - من سره أن يمثل له الرجال قياما فليتبوأ مقعده من النار).','1178','1607','0');
INSERT INTO [t38171] VALUES (1786,'١٧٧٣ - (قال - صلى الله عليه وسلم - لا يقم الرجل الرجل من مجلسه ثم يجلس فيه ولكن توسعوا وتفسحوا).','1179','1607','0');
INSERT INTO [t38171] VALUES (1787,'١٧٧٤ - (وقال - صلى الله عليه وسلم - إذا أخذ القوم مجالسهم فإن دعا أحد أخاه فأوسع له فليأته فإنما هي كرامة أكرمه بها أخوه فإن لم يوسع له فلينظر إلى أوسع مكان يجده فيجلس).','1179','1607','0');
INSERT INTO [t38171] VALUES (1788,'١٧٧٥ - (وروى أنه سلم رجل على رسول الله - صلى الله عليه وسلم - وهو يبول فلم يجبه).','1180','1607','0');
INSERT INTO [t38171] VALUES (1789,'١٧٧٦ - (قال - صلى الله عليه وسلم - إن عليك السلام تحية الميت قاله ثلاثا ثم قال إذا لقي أحدكم أخاه فليقل السلام عليكم ورحمة الله وبركاته).','1180','1607','0');
INSERT INTO [t38171] VALUES (1790,'١٧٧٧ - قال ألا أخبركم عن النفر الثلاثة أما أحدهم فآوي إلى الله فآواه الله) أي رجع وانعطف ومال إليه فأدخله تحت كنفه وأقبل إليه (وأما الثاني فاستحيا) أي غلب عليه الحياء فلم يدخل في الصف (فاستحيا الله منه وأما الثالث فأعرض فأعرض الله عنه).','1181','1607','0');
INSERT INTO [t38171] VALUES (1791,'١٧٧٨ - (وقال رسول الله - صلى الله عليه وسلم - ما من مسلمين يلتقيان فيتصافحان إلا غفر لهما قبل أن يتفرقا).','1181','1607','0');
INSERT INTO [t38171] VALUES (1792,'١٧٧٩ - (وسلمت أم هانئ) فاختة ابنة أبي طالب أخت علي رضي الله عنهما (عليه) - صلى الله عليه وسلم - (فقال من هذه فقيل له أم هانئ فقال - صلى الله عليه وسلم - مرحبا بأم هانئ).','1182','1607','0');
INSERT INTO [t38171] VALUES (1793,'١٧٨٠ - (روى أبو الدرداء أن رجلا نال من رجل عند رسول الله - صلى الله عليه وسلم - فرد عنه رجل فقال النبي - صلى الله عليه وسلم - من رد عن عرض أخيه كان له حجابا من النار)','1182','1607','0');
INSERT INTO [t38171] VALUES (1794,'١٧٨١ - (وقال - صلى الله عليه وسلم - ما من امرئ مسلم يرد عن عرض أخيه) في الدين بأن يرد عنه من أذاه وعابه (إلا كان حقا على الله أن يرد عنه نار جهنم يوم القيامة) جزاء بما فعل.','1183','1607','0');
INSERT INTO [t38171] VALUES (1795,'١٧٨٢ - (عن أنس) رضي الله عنه (أن النبي - صلى الله عليه وسلم - قال من ذكر عنده أخوه المسلم وهو يستطيع نصره). على من ذكره بسوء (فلم ينصره ولو بكلمة أذله الله عز وجل). كذا في نسخة العراقي وفي لفظ أدركه الله بها (في الدنيا والآخرة ومن ذكر عنده أخوه المسلم فنصره نصره الله تعالى بها في الدنيا والآخرة).','1183','1607','0');
INSERT INTO [t38171] VALUES (1796,'١٧٨٣ - (وقال - صلى الله عليه وسلم - من حمى عرض أخيه المسلم في الدنيا) بالرد عنه (بعث الله له ملكا يحميه يوم القيامة من النار). جزاء بما فعل.','1184','1607','0');
INSERT INTO [t38171] VALUES (1797,'١٧٨٤ - (وقال جابر) بن عبد الله (وأبو طلحة) زيد بن سهل الأنصاريان رضي الله عنهما (سمعنا رسول الله - صلى الله عليه وسلم - يقول ما من امرئ مسلم ينصر مسلما في موضع يهتك فيه من عرضه ويستحل من حرمته إلا نصره الله عز وجل في موضع) وفي نسخة في موطن (يحب فيه نصره وما من امرئ خذل مسلما في موطن ينتهك فيه حرمته إلا خذله الله في موطن يحب فيه نصرته).','1184','1607','0');
INSERT INTO [t38171] VALUES (1798,'١٧٨٥ - (عن ابن مسعود رضي الله عنه قال كان رسول الله - صلى الله عليه وسلم - يعلمنا يقول إذا عطس أحدكم فليقل الحمد لله رب العالمين فإذا قال ذلك فليقل من عنده يرحمك الله فإذا قالوا ذلك فليقل يغفر الله لي ولكم).','1185','1607','0');
INSERT INTO [t38171] VALUES (1799,'١٧٨٦ - حديث إذا عطس فحمد قال الحمد لله على كل حال.','1185','1607','0');
INSERT INTO [t38171] VALUES (1800,'١٧٨٧ - (و) يروي (أنه شمت رسول الله - صلى الله عليه وسلم - عاطسا ولم يشمت آخر فسأله فقال إنه حمد الله تعالى وأنت سكت).','1186','1607','0');
INSERT INTO [t38171] VALUES (1801,'١٧٨٨ - (وقال - صلى الله عليه وسلم - يشمت المسلم إذا عطس ثلاثا) أي ثلاث مرات (فإن زاد فهو زكام).','1186','1607','0');
INSERT INTO [t38171] VALUES (1802,'١٧٨٩ - (وقال أبو هريرة) رضي الله عنه (كان رسول الله - صلى الله عليه وسلم - إذا عطس غض صوته) أي خففه (واستتر بثوبه أو يده وروي خمر وجهه).','1187','1607','0');
INSERT INTO [t38171] VALUES (1803,'١٧٩٠ - (وقال أبو موسى الأشعري) رضي الله عنه (كان اليهود يتعاطسون عند رسول الله - صلى الله عليه وسلم -) عمدا رجاء أن يقول يرحمكم الله فكان يقول يهديكم الله).','1187','1607','0');
INSERT INTO [t38171] VALUES (1804,'١٧٩١ - (وروى عبد الله بن عامر بن ربيعة عن أبيه أن رجلا عطس خلف النبي - صلى الله عليه وسلم - في الصلاة فقال الحمد لله حمدا كثيرا طيبا مباركا فيه كما يرضى ربنا وبعد ما يرضى والحمد لله على كل حال فلما سلم النبي - صلى الله عليه وسلم - قال من صاحب الكلمات فقال أنا يا رسول الله ما أردت بهن إلا خيرا فقال لقد رأيت اثنى عشر ملكا كلهم يبتدرونها أيهم يكتبها).','1187','1607','0');
INSERT INTO [t38171] VALUES (1805,'١٧٩٢ - وقال - صلى الله عليه وسلم - من عطس عنده فسبق إلى الحمد لم يشتك خاصرته).','1188','1607','0');
INSERT INTO [t38171] VALUES (1806,'١٧٩٣ - (وقال عليه السلام العطاس من الله والتثاؤب من الشيطان فإذا تثاءب أحدكم فليضع يديه علي فيه فإذا قال هاها فإن الشيطان يضحك من جوفه).','1188','1607','0');
INSERT INTO [t38171] VALUES (1807,'١٧٩٤ - (وقال موسى عليه السلام يا رب أقريب أنت أناجيك أم بعيد فأناديك فقال أنا جليس من ذكرني فقال يا رب فأنا نكون على حال نجلك) أي ننزهك (أن نذكرك عليها) أي معها (كالجنابة والغائط فقال) يا موسى (اذكرني على كل حال).','1189','1607','0');
INSERT INTO [t38171] VALUES (1808,'١٧٩٥ - (قال - صلى الله عليه وسلم - يا عائشة إن شر الناس منزلة عند الله يوم القيامة من تركه الناس اتقاء فحشه).','1190','1607','0');
INSERT INTO [t38171] VALUES (1809,'١٧٩٦ - (في الخبر ما وقى به المرء عرضه فهو له صدقة).','1191','1607','0');
INSERT INTO [t38171] VALUES (1810,'١٧٩٧ - (كان النبي - صلى الله عليه وسلم - يقول اللهم أحيني مسكينا وأمتني مسكينا واحشرني في زمرة المساكين).','1191','1607','0');
INSERT INTO [t38171] VALUES (1811,'١٧٩٨ - (وقال - صلى الله عليه وسلم - إياكم ومجالسة الموتى قيل ومن الموتى يا رسول الله قال الأغنياء)','1192','1607','0');
INSERT INTO [t38171] VALUES (1812,'١٧٩٩ - (قال - صلى الله عليه وسلم - لا تغبطن فاجرا بنعمة)','1193','1607','0');
INSERT INTO [t38171] VALUES (1813,'١٨٠٠ - (قال - صلى الله عليه وسلم - من ضم يتيما من أبوين مسلمين حتى يستغني فقد وجبت له الجنة البتة)','1193','1607','0');
INSERT INTO [t38171] VALUES (1814,'١٨٠١ - (وقال على أنا وكافل اليتيم)','1194','1607','0');
INSERT INTO [t38171] VALUES (1815,'١٨٠٢ - (وقال - صلى الله عليه وسلم - من وضع يده على رأس يتيم ترحما كانت له بكل شعرة تمر يده عليها حسنة)','1194','1607','0');
INSERT INTO [t38171] VALUES (1816,'١٨٠٣ - (وقال - صلى الله عليه وسلم - خير بيت من)','1194','1607','0');
INSERT INTO [t38171] VALUES (1817,'١٨٠٤ - (قال - صلى الله عليه وسلم - المؤمن يحب للمؤمن ما يحب لنفسه)','1195','1607','0');
INSERT INTO [t38171] VALUES (1818,'١٨٠٥ - (وقال - صلى الله عليه وسلم - لا يؤمن أحدكم حتى يحب لأخيه ما يحب لنفسه)','1195','1607','0');
INSERT INTO [t38171] VALUES (1819,'١٨٠٦ - (وقال - صلى الله عليه وسلم - إن أحدكم مرآة أخيه)','1195','1607','0');
INSERT INTO [t38171] VALUES (1820,'١٨٠٧ - (وقال - صلى الله عليه وسلم - من قضى حاجة لأخيه فكأنما خدم الله تعالى عمره)','1196','1607','0');
INSERT INTO [t38171] VALUES (1821,'١٨٠٨ - (قال - صلى الله عليه وسلم - من أقر عين مؤمن)','1196','1607','0');
INSERT INTO [t38171] VALUES (1822,'١٨٠٩ - (قال - صلى الله عليه وسلم - من مشى في حاجة أخيه ساعة من ليل أو نهار قضاها أو لم يقضها كان خيرا له من اعتكاف شهرين متتابعين)','1197','1607','0');
INSERT INTO [t38171] VALUES (1823,'١٨١٠ - (وقال - صلى الله عليه وسلم - من فرج عن مغموم) الذي أصابه الغم (وأغاث ملهوفا) أي مكروبا (غفر الله له ثلاثا وسبعين مغفرة)','1197','1607','0');
INSERT INTO [t38171] VALUES (1824,'١٨١١ - (وقال - صلى الله عليه وسلم - انصر أخاك ظالما أو مظلوما فقيل كيف ينصره ظالما قال يمنعه من الظلم)','1198','1607','0');
INSERT INTO [t38171] VALUES (1825,'١٨١٢ - (قال - صلى الله عليه وسلم - إن من أحب الأعمال إلى الله) تعالى (إدخال السرور على) أخيه (المؤمن وأن يفرج عنه غما)','1199','1607','0');
INSERT INTO [t38171] VALUES (1826,'١٨١٣ - (وقال - صلى الله عليه وسلم - من حمى مؤمنا من منافق يعنته)','1199','1607','0');
INSERT INTO [t38171] VALUES (1827,'١٨١٤ - (وقال - صلى الله عليه وسلم - خصلتان ليس فوقهما شيء من الشر الشرك بالله والضر لعباد الله وخصلتان ليس فوقهما شيء من البر الإيمان بالله والنفع لعباد الله).','1199','1607','0');
INSERT INTO [t38171] VALUES (1828,'١٨١٥ - (وقال - صلى الله عليه وسلم - من لم يهتم للمسلمين فليس منهم).','1200','1607','0');
INSERT INTO [t38171] VALUES (1829,'١٨١٦ - (قال - صلى الله عليه وسلم - من عاد مريضا قعد في مخارف الجنة) جمع مخرف موضع الاختراف وخرف الثمار واخترفها قطعها وجناها والمراد بمخارف الجنة مجاني ثمارها (حتى إذا قام وكل الله به سبعين ألف ملك يصلون عليه) أي يستغفرون له (حتى الليل).','1200','1607','0');
INSERT INTO [t38171] VALUES (1830,'١٨١٧ - (وقال - صلى الله عليه وسلم - إذا عاد الرجل المريض خاض في الرحمة فإذا قعد عنده قرت فيه).','1201','1607','0');
INSERT INTO [t38171] VALUES (1831,'١٨١٨ - (قال - صلى الله عليه وسلم - إذا عاد المسلم أخاه) في الدين (أو زاره) احتسابا لله (قال الله تعالى طبت وطاب ممشاك) أي مشيك (وتبوأت منزلا في الجنة) أي اتخذته.','1202','1607','0');
INSERT INTO [t38171] VALUES (1832,'١٨١٩ - (قال - صلى الله عليه وسلم - إذا مرض العبد بعث الله تعالى له ملكين فيقول) لهما وفي نسخة فقال (انظرا ماذا يقول لعواده) جمع عائد (فإن هو) أي المريض (إذا جاؤه) وسألوه عن حاله (حمد الله تعالى وأثنى عليه رفعا ذلك إلى الله تعالى وهو أعلم فيقول لعبدي على أن توفيته) أي من هذا المرض (أن أدخله الجنة وإن أنا شفيته أن أبدل له لحما خيرا من لحمه ودما خيرا من دمه وأن أكفر عنه سيأته).','1203','1607','0');
INSERT INTO [t38171] VALUES (1833,'١٨٢٠ - (قال رسول الله - صلى الله عليه وسلم - من يرد الله به خيرا يصب منه)','1203','1607','0');
INSERT INTO [t38171] VALUES (1834,'١٨٢١ - (يقال بسم الله الرحمن الرحيم أعيذك بالله الأحد الصمد الذي لم يلد ولم يولد ولم يكن له كفوا أحد من شر ما تجد قال ذلك مرارا). وفي نسخة ثلاثا.','1204','1607','0');
INSERT INTO [t38171] VALUES (1835,'١٨٢٢ - (دخل - صلى الله عليه وسلم - على علي رضي الله عنه وهو مريض فقال قل اللهم إني أسألك تعجيل عافيتك أو صبرا على بليتك أو خروجا من الدنيا إلى رحمتك فإنك ستعطى إحداهن).','1204','1607','0');
INSERT INTO [t38171] VALUES (1836,'١٨٢٣ - (قال - صلى الله عليه وسلم - يا أبا هريرة ألا أخبرك بأمر هو حق) أي لا يستراب فيه (من تكلم به في أول مضجعه) أي رقوده (من مرضه نجاه الله من النار) ببركة ما تكلم به فقلت بلى يا رسول الله (قال تقول لا إله إلا الله) وفي بعض النسخ هنا زيادة وحده لا شريك له (يحيى ويميت','1204','1607','0');
INSERT INTO [t38171] VALUES (1837,'١٨٢٤ - (روي أنه - صلى الله عليه وسلم - قال عيادة المريض فواق ناقة).','1205','1607','0');
INSERT INTO [t38171] VALUES (1838,'١٨٢٥ - (قال طاوس) اليماني رحمه الله تعالى (أفضل العيادة أخفها)','1205','1607','0');
INSERT INTO [t38171] VALUES (1839,'١٨٢٦ - (قال ابن عباس) رضي الله عنه (عيادة المريض مرة سنة فما زاد فنافلة)','1206','1607','0');
INSERT INTO [t38171] VALUES (1840,'١٨٢٧ - (وقال بعضهم عيادة المريض بعد ثلاث)','1206','1607','0');
INSERT INTO [t38171] VALUES (1841,'١٨٢٨ - (وقال - صلى الله عليه وسلم - أغبوا في العيادة)','1206','1607','0');
INSERT INTO [t38171] VALUES (1842,'١٨٢٩ - (قال رسول الله - صلى الله عليه وسلم - من شيع) وفي نسخة من تبع (جنازة فله قيراط من الأجر فإن وقف حتى يدفن فله قيراطان)','1207','1607','0');
INSERT INTO [t38171] VALUES (1843,'١٨٣٠ - (قال - صلى الله عليه وسلم - يتبع الميت ثلاثة فيرجع اثنان ويبقى واحد يتبعه أهله وماله وعمله فيرجع أهله وماله ويبقى) معه (عمله)','1208','1607','0');
INSERT INTO [t38171] VALUES (1844,'١٨٣١ - (قال - صلى الله عليه وسلم - ما رأيت منظرا) أي منظورا (إلا والقبر أفظع)','1209','1607','0');
INSERT INTO [t38171] VALUES (1845,'١٨٣٢ - (وقال عمر) بن الخطاب (رضي الله عنه خرجنا مع رسول الله - صلى الله عليه وسلم -)','1209','1607','0');
INSERT INTO [t38171] VALUES (1846,'١٨٣٣ - (وكان عثمان) بن عفان (رضي الله عنه إذا وقف على قبر بكى حتى يبل لحيته)','1209','1607','0');
INSERT INTO [t38171] VALUES (1847,'١٨٣٤ - (أول ما يكلم ابن آدم حفرته) أي قبره (فيقول أنا بيت الدود وبيت الوحدة وبيت الغربة وبيت الظلمة فهذا ما أعددت لك فما أعددت لي)','1210','1607','0');
INSERT INTO [t38171] VALUES (1848,'١٨٣٥ - (وقال - صلى الله عليه وسلم - ما من ليلة إلا وينادي مناديا يا أهل القبور من تغبطون قالوا نغبط أهل المساجد لأنهم يصومون ولا نصوم ويصلون ولا نصلي ويذكرون الله ولا نذكر)','1211','1607','0');
INSERT INTO [t38171] VALUES (1849,'١٨٣٦ - (والإسراع بالجنازة سنة)','1211','1607','0');
INSERT INTO [t38171] VALUES (1850,'١٨٣٧ - (قال النبي - صلى الله عليه وسلم - الجيران) جمع جار كنار ونيران (جار) وفي رواية فجار (له حق واحد) على جاره وهو أدق الجيران حقا (وجار له حقان وجار له ثلاثة حقوق فالجار الذي له ثلاثة حقوق هو الجار المسلم ذو الرحم فله حق الجوار وحق الإسلام وحق الرحم وأما الجار الذي له حقان فالجار المسلم له حق الجوار وحق الإسلام','1211','1607','0');
INSERT INTO [t38171] VALUES (1851,'١٨٣٨ - (قال النبي - صلى الله عليه وسلم - ما زال جبريل يوصيني بالجار حتى ظننت أنه سيورثه).','1212','1607','0');
INSERT INTO [t38171] VALUES (1852,'١٨٣٩ - (وقال - صلى الله عليه وسلم - من كان يؤمن بالله واليوم الآخر فليكرم جاره).','1213','1607','0');
INSERT INTO [t38171] VALUES (1853,'١٨٤٠ - (قال - صلى الله عليه وسلم - لا يؤمن عبد حتى يأمن جاره بوائقه).','1213','1607','0');
INSERT INTO [t38171] VALUES (1854,'١٨٤١ - (قال - صلى الله عليه وسلم - إذا أنت رميت كلب جارك فقد آذيته).','1214','1607','0');
INSERT INTO [t38171] VALUES (1855,'١٨٤٢ - (وقيل لرسول الله - صلى الله عليه وسلم - إن فلانة تصوم النهار وتقوم الليل وتؤذي جيرانها فقال - صلى الله عليه وسلم - هي في النار).','1214','1607','0');
INSERT INTO [t38171] VALUES (1856,'١٨٤٣ - (وجاء رجل إلى النبي - صلى الله عليه وسلم - يشكو جاره) أنه يؤذيه (فقال له - صلى الله عليه وسلم - اصبر) على أذاه (ثم قال له في الثالثة أو الرابعة اطرح متاعك في الطريق) فذهب فطرح متاعه في الطريق (قال فجعل الناس يمرون به فيقولون مالك فيقال آذاه جاره فجعلوا يقولون لعنه الله فجاءه جاره فقال رد متاعك والله لا أعود).','1214','1607','0');
INSERT INTO [t38171] VALUES (1857,'١٨٤٤ - روى الزهري) بن عبيد الله بن شهاب رحمه الله تعالى (أن رجلا أتى النبي - صلى الله عليه وسلم - يشكو جاره فأمر - صلى الله عليه وسلم - أن ينادي على باب','1214','1607','0');
INSERT INTO [t38171] VALUES (1858,'١٨٤٥ - (قال - صلى الله عليه وسلم - اليمن والشؤم في المرأة والمسكن والفرس فيمن المرأة خفة مهرها ويسر نكاحها وحسن خلقها وشؤمها غلاء مهرها وعسر نكاحها وسوء خلقها ويمن المسكن سعته وحسن جوار أهله وشؤمه ضيقه وسوء جوار أهله ويمن الفرس ذله وحسن خلقه وشؤمه صعوبته).','1215','1607','0');
INSERT INTO [t38171] VALUES (1859,'١٨٤٦ - (قال - صلى الله عليه وسلم - أتدرون ما حق الجار) على الجار (إن استعان بك أعنته وإن استقرضك) أي طلب منك أن تقرضه شيئا (أقرضته) إن تيسر معك (وإن افتقر عدت عليه) وفي نسخة جدت (وإن مرض عدته وإن مات اتبعت جنازته) إلى المصلى ثم إلى القبر (وإن أصابه خير هنأته) به (وإن أصابه مصيبة) في نفس أو مال أو أهل (عزيته) بما ورد في السنة من المأثور (ولا تستطيل عليه بالبناء) رفعا يضره أشار به لقوله (فتحجب عنه) ونسخة فتحجر أي تمنع عنه (الريح) أو الضوء فإن خلا عن الضرر جاز إلا لذمي على مسلم (إلا بإذنه وإن اشتريت فاكهة فاهد له فإن لم تفعل فأدخلها سرا ولا يخرج بها ولدك ليغيظ بها ولده ولا تؤذه بقتار) بالضم أي ريح (قدرك) أي طعامك الذي تطبخه في القدر فأطلق الظرف وأراد المظروف (إلا أن تغرف له منها).','1217','1607','0');
INSERT INTO [t38171] VALUES (1860,'١٨٤٧ - (قال مجاهد) التابعي رحمه الله تعالى (وكنت عند عبد الله بن عمر رضي الله عنهما وغلام له يسلخ شاة فقال يا غلام إذا سلخت فابدأ بجارنا اليهودي حتى قال ذلك مرارا فقال له كم تقول هذا فقال إن رسول الله - صلى الله عليه وسلم - لم يزل يوصينا بالجار حتى حسبنا أنه سيورثه).','1218','1607','0');
INSERT INTO [t38171] VALUES (1861,'١٨٤٨ - (وقال أبو ذر) الغفاري رضي الله عنه (أوصاني خليلي رسول الله - صلى الله عليه وسلم - وقال إذا طبخت قدرا فأكثر ماءها ثم انظر بعض أهل البيت من جيرانك فاغرف لهم منها).','1218','1607','0');
INSERT INTO [t38171] VALUES (1862,'١٨٤٩ - (وقالت عائشة رضي الله عنها لرسول الله - صلى الله عليه وسلم - إن لي جارين أحدهما مقبل ببابه والآخر ناء) أي بعيد (ببابه عني وربما كان الذي عندي لا يسعهما) أي لا يكفيهما (فأيهما أعظم حقا فقال المقبل عليك ببابه).','1218','1607','0');
INSERT INTO [t38171] VALUES (1863,'١٨٥٠ - (وقالت عائشة رضي الله عنها خلال المكارم عشرة) والحصر إضافي باعتبار الذكر هنا (تكون في الرجل ولا تكون في ابنه وتكون في العبد ولا تكون في سيده يقسمها الله تعالى لمن أحب صدق الحديث).','1219','1607','0');
INSERT INTO [t38171] VALUES (1864,'١٨٥١ - (قال - صلى الله عليه وسلم - إن من سعادة المرء المسلم المسكن الواسع والجار الصالح والمركب الهنيء).','1219','1607','0');
INSERT INTO [t38171] VALUES (1865,'١٨٥٢ - (وقال عبد الله) بن مسعود رضي الله عنه (قال رجل يا رسول الله كيف لي أن أعلم إذا أحسنت أو أسأت قال إذا سمعت جيرانك يقولون قد أحسنت فقد أحسنت وإذا سمعتهم يقولون قد أسأت فقد أسأت).','1220','1607','0');
INSERT INTO [t38171] VALUES (1866,'١٨٥٣ - (قال جابر) رضي الله عنه (من كان له جار في حائط) أي مزرعة أو بستان (أو شريك فلا يبعه حتى يعرضه عليه)','1220','1607','0');
INSERT INTO [t38171] VALUES (1867,'١٨٥٤ - (قال أبو هريرة) رضي الله عنه (قضى رسول الله - صلى الله عليه وسلم - إن الجار يضع جذوعه) وفي نسخة جذعه (في حائط جاره) إن احتاج لذلك (شاء الجار) ذلك (أم أبى) أي امتنع.','1221','1607','0');
INSERT INTO [t38171] VALUES (1868,'١٨٥٥ - (قال ابن عباس) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - لا يمنعن أحدكم جاره أن يضع خشبه في حائطه).','1221','1607','0');
INSERT INTO [t38171] VALUES (1869,'١٨٥٦ - (قال - صلى الله عليه وسلم - من أراد الله به خيرا عسله قيل وما عسله قال يحببه إلى جيرانه)','1221','1607','0');
INSERT INTO [t38171] VALUES (1870,'١٨٥٧ - (قال رسول الله - صلى الله عليه وسلم - يقول الله تعالى أنا الرحمن وهذه الرحم شققت لها اسما من اسمي فمن وصلها وصلته ومن قطعها بتته) أي قطعته.','1221','1607','0');
INSERT INTO [t38171] VALUES (1871,'١٨٥٨ - (وقال - صلى الله عليه وسلم - من سره أن ينسأ له) أي يؤخر (في أثره يوسع عليه في رزقه فليتق الله وليصل رحمه).','1222','1607','0');
INSERT INTO [t38171] VALUES (1872,'١٨٥٩ - (قيل لرسول الله - صلى الله عليه وسلم - أي الناس أفضل قال أتقاهم لله وأوصلهم لرحمه وآمرهم بالمعروف وأنهاهم عن المنكر).','1223','1607','0');
INSERT INTO [t38171] VALUES (1873,'١٨٦٠ - (وقال أبو ذر) رضي الله عنه (أوصاني خليلي رسول الله - صلى الله عليه وسلم - بصلة الرحم وإن أدبرت وأمرني أن أقول الحق وإن كان مرا).','1223','1607','0');
INSERT INTO [t38171] VALUES (1874,'١٨٦١ - (وقال - صلى الله عليه وسلم - الرحم معلقة بالعرش وليس الواصل بالمكافئ ولكن الواصل الذي إذا انقطعت رحمه وصلها).','1223','1607','0');
INSERT INTO [t38171] VALUES (1875,'١٨٦٢ - (قال - صلى الله عليه وسلم - إن أعجل الطاعة ثوابا صلة الرحم حتى إن أهل البيت ليكونون فجارا فتنمى) أي تزداد (أموالهم ويكثر عددهم إذا وصلوا أرحامهم).','1224','1607','0');
INSERT INTO [t38171] VALUES (1876,'١٨٦٣ - (قال زيد بن أسلم) أبو عبد الله العدوي مولى عمر ثقة عالم وكان يرسل مات سنة ست وثلاثين (لما خرج رسول الله - صلى الله عليه وسلم - إلى مكة عرض له رجل فقال إن كنت تريد النساء البيض والنوق الأدم فعليك ببني مدلج) وهي قبيلة من العرب (فقال - صلى الله عليه وسلم - إن الله قد منعني من بني مدلج بصلتهم الرحم)','1224','1607','0');
INSERT INTO [t38171] VALUES (1877,'١٨٦٤ - (وقال عليه السلام الصدقة على المساكين صدقة وعلى ذي الرحم ثنتان)','1224','1607','0');
INSERT INTO [t38171] VALUES (1878,'١٨٦٥ - (قال عليه السلام أفضل الصدقة علي ذي الرحم الكاشح)','1225','1607','0');
INSERT INTO [t38171] VALUES (1879,'١٨٦٦ - (قوله أفضل الفضائل أن تصل من قطعك وتعطي من حرمك وتصفح عمن ظلمك)','1225','1607','0');
INSERT INTO [t38171] VALUES (1880,'١٨٦٧ - (قال - صلى الله عليه وسلم - لن يجزى ولد والده حتى يجده مملوكا فيشتريه فيعتقه)','1225','1607','0');
INSERT INTO [t38171] VALUES (1881,'١٨٦٨ - (قال - صلى الله عليه وسلم - بر الوالدين أفضل من الصلاة والصوم والحج والعمرة والجهاد في سبيل الله تعالى)','1226','1607','0');
INSERT INTO [t38171] VALUES (1882,'١٨٦٩ - (وقال - صلى الله عليه وسلم - من أصبح مرضيا لأبويه أصبح له بابان مفتوحان إلى الجنة)','1226','1607','0');
INSERT INTO [t38171] VALUES (1883,'١٨٧٠ - (وقال - صلى الله عليه وسلم - إن الجنة يوجد ريحها من مسيرة خمسمائة عام ولا يجد ريحها عاق) أي لوالديه (ولا قاطع رحم)','1227','1607','0');
INSERT INTO [t38171] VALUES (1884,'١٨٧١ - (وقال - صلى الله عليه وسلم - بر أمك وأباك وأختك وأخاك ثم أدناك فأدناك)','1227','1607','0');
INSERT INTO [t38171] VALUES (1885,'١٨٧٢ - (ما على أحد إذا أراد أن يتصدق بصدقة أن يجعلها لوالديه إذا كانا مسلمين فيكون لوالديه أجرها ويكون له مثل أجورهما من غير أن ينقص من أجورهما شيء)','1228','1607','0');
INSERT INTO [t38171] VALUES (1886,'١٨٧٣ - (قال مالك بن ربيعة)','1228','1607','0');
INSERT INTO [t38171] VALUES (1887,'١٨٧٤ - (قال - صلى الله عليه وسلم - إن أبر البر)','1229','1607','0');
INSERT INTO [t38171] VALUES (1888,'١٨٧٥ - (وقال - صلى الله عليه وسلم - إن من أبر البر أن يصل الرجل أهل ودأبيه بعد أن يولي الأب)','1229','1607','0');
INSERT INTO [t38171] VALUES (1889,'١٨٧٦ - (قال - صلى الله عليه وسلم - بر الوالدة على الولد ضعفان)','1230','1607','0');
INSERT INTO [t38171] VALUES (1890,'١٨٧٧ - (قال - صلى الله عليه وسلم - الوالدة أسرع إجابة قيل يا رسول الله ولم ذاك قال هي أرحم من الأب ودعوة الرحيم لا تسقط)','1230','1607','0');
INSERT INTO [t38171] VALUES (1891,'١٨٧٨ - (سأله) - صلى الله عليه وسلم - (رجل فقال يا رسول الله من أبر قال والديك فقال ليس لي والدان قال بر ولدك فكما إن لوالديك حقا كذلك لولدك عليك حق)','1230','1607','0');
INSERT INTO [t38171] VALUES (1892,'١٨٧٩ - (قال - صلى الله عليه وسلم - رحم الله والدا أعان ولده على بره).','1230','1607','0');
INSERT INTO [t38171] VALUES (1893,'١٨٨٠ - (وقال - صلى الله عليه وسلم - ساووا بين أولادكم في العطية).','1231','1607','0');
INSERT INTO [t38171] VALUES (1894,'١٨٨١ - حديث أنس الغلام يعق عنه يوم السابع ويماط عنه- الحديث.','1231','1607','0');
INSERT INTO [t38171] VALUES (1895,'١٨٨٢ - (وقال - صلى الله عليه وسلم - من حق الولد على الوالد أن يحسن أدبه ويحسن اسمه).','1232','1607','0');
INSERT INTO [t38171] VALUES (1896,'١٨٨٣ - (وقال عليه السلام كل غلام رهين أو رهينة بعقيقته تذبح عنه يوم السابع ويحلق رأسه).','1232','1607','0');
INSERT INTO [t38171] VALUES (1897,'١٨٨٤ - (رأى الأقرع بن حابس) التميمي من المؤلفة قلوبهم (النبي - صلى الله عليه وسلم - وهو يقبل ولده الحسن فقال) الأقرع (إن لي عشرة من الولد ما قبلت واحدا منهم) فنظر إليه (فقال إن من لا يرحم لا يرحم).','1234','1607','0');
INSERT INTO [t38171] VALUES (1898,'١٨٨٥ - (قالت عائشة رضي الله عنها قال لي رسول الله - صلى الله عليه وسلم - يوما اغسلي وجه أسامة) هو ابن زيد بن حارثة بن شراحيل القضاعي حب رسول الله وابن حب رسول الله (فجعلت أكسله وأنا أنفة) يقال أنف من كذا إذا استكبر أو استحى وفي نسخة وأنا أتقيه أي أتحذره (فضرب بيدي ثم أخذه فغسل وجهه ثم قبله ثم قال قد أحسن بنا إذ لم يكن جارية).','1234','1607','0');
INSERT INTO [t38171] VALUES (1899,'١٨٨٦ - (وأقبل الحسن) بن علي رضي الله عنهما وفي نسخة دخل الحسن وفي أخرى الحسين يتعثر) وفي أخرى تعثر الحسن (وهو على منبره - صلى الله عليه وسلم - وفي نسخة والنبي - صلى الله عليه وسلم - على منبره (فنزل) عن المنبر (فحمله وقرأ قول الله تعالى إنما أموالكم وأولادكم فتنة).','1235','1607','0');
INSERT INTO [t38171] VALUES (1900,'١٨٨٧ - (قال عبد الله بن شداد) بن الهاد بن عمرو بن جابر بن بشر بن عتوارة الليثي أبو الوليد المدني وأمه سلمى بنت عميس الخثعمية أخت أسماء وهو وعبد الله بن عباس وخالد بن الوليد وعبد الله بن جعفر أولاد الخالة، من كبار التابعين وثقاتهم فقد يوم دجيل روى له الجماعة (بينما رسول الله - صلى الله عليه وسلم - يصلي بالناس إذ جاءه الحسين) بن علي رضي الله عنهما (فركب عنقه وهو ساجد فأطال السجود بالناس حتى ظنوا أنه قد حدث أمر فلما قضى) - صلى الله عليه وسلم - (صلاته قالوا قد أطلت السجود حتى ظننا أنه قد حدث أمر فقال) كل ذلك لم يكن (إن ابني) كان (قد ارتحلني) أي ركبني كما تركب الراحلة (فكرهت أن أعجله حتى يقضي حاجته).','1235','1607','0');
INSERT INTO [t38171] VALUES (1901,'١٨٨٨ - (قال - صلى الله عليه وسلم - ريح الولد من ريح الجنة).','1236','1607','0');
INSERT INTO [t38171] VALUES (1902,'١٨٨٩ - (قال أبو سعيد الخدري) رضي الله عنه (هاجر رجل إلى رسول الله - صلى الله عليه وسلم - من اليمن وأراد الجهاد) في سبيل الله (فقال) له (- صلى الله عليه وسلم - هل باليمن أبواك قال نعم قال فهل أذنا لك) في الخروج (فقال لا فقال - صلى الله عليه وسلم - فارجع إلى أبويك فاستأذنهما فإن فعلا فجاهد وإلا فبرهما ما استطعت فإن ذلك خير ما تلقي الله به بعد التوحيد).','1236','1607','0');
INSERT INTO [t38171] VALUES (1903,'١٨٩٠ - جاء رجل (آخر إلى النبي - صلى الله عليه وسلم - يستشيره في الغزو فقال ألك والدة فقال نعم قال فالزمها فإن الجنة عند رجليها).','1237','1607','0');
INSERT INTO [t38171] VALUES (1904,'١٨٩١ - جاء رجل (آخر) إلى النبي - صلى الله عليه وسلم - (يطلب البيعة على الهجرة وقال ما جئتك حتى أبكيت والدي قال ارجع إليهما (فأضحكهما كما أبكيتهما).','1237','1607','0');
INSERT INTO [t38171] VALUES (1905,'١٨٩٢ - (قال - صلى الله عليه وسلم - وسلم حق كبير الأخوة على صغيرهم كحق الوالد على ولده).','1237','1607','0');
INSERT INTO [t38171] VALUES (1906,'١٨٩٣ - (قال - صلى الله عليه وسلم - إذا استصعب على أحدكم دابته أو ساء خلق زوجته أو أحد من أهل بيته فليؤذن في أذنه).','1238','1607','0');
INSERT INTO [t38171] VALUES (1907,'١٨٩٤ - (كان آخر ما أوصى به رسول الله - صلى الله عليه وسلم - أن قال اتقوا الله فيما ملكت أيمانكم أطعموهم مما تأكلون واكسوهم مما تلبسون ولا تكلفوهم من العمل ما لا يطيقون فما أحببتم فأمسكوا وماكرهتم فبيعوا ولا تعذبوا خلق الله فإن الله تعالى ملككم إياهم ولو شاء لملكهم إياكم).','1238','1607','0');
INSERT INTO [t38171] VALUES (1908,'١٨٩٥ - (قال - صلى الله عليه وسلم - للمملوك طعامه وكسوته بالمعروف ولا يكلف من العمل ما لا يطيق)','1239','1607','0');
INSERT INTO [t38171] VALUES (1909,'١٨٩٦ - (وقال - صلى الله عليه وسلم - لا يدخل الجنة خب) الخب بالكسر الخداع ورجل خب بالفتح تسمية بالمصدر (ولا مكر) ككتف أي صاحب مكر ويحتمل أن يكون بفتح فسكون تسمية بالمصدر كما في خب (ولا خائن) أي صاحب خيانة (ولا سيء الملكة) الذي يسيء السيرة مع من يملكه.','1240','1607','0');
INSERT INTO [t38171] VALUES (1910,'١٨٩٧ - (قال عبد الله بن عمر) رضي الله عنه (جاء رجل إلى رسول الله - صلى الله عليه وسلم - فقال يا رسول الله كم نعفو عن الخادم فصمت) أي سكت (عنه رسول الله - صلى الله عليه وسلم - ثم قال اعف عنه كل يوم سبعين مرة).','1240','1607','0');
INSERT INTO [t38171] VALUES (1911,'١٨٩٨ - (وقال) محمد (بن المنكدر) بن عبد الله بن الهدير التيمي أبو عبد الله ويقال أبو بكر القرشي المدني تابعي ثقة روى له الجماعة مات سنة ثلاثين ومائة. (إن رجلا من أصحاب النبي - صلى الله عليه وسلم - ضرب عبدا له فجعل العبد يقول أسألك بالله أسألك بالله) مرتين (أسألك بوجه الله) قال (فسمع رسول الله - صلى الله عليه وسلم - صياح العبد فانطلق إليه فلما رأى رسول الله - صلى الله عليه وسلم - أمسك يده). عن ضربه (فقال - صلى الله عليه وسلم - سألك بوجه الله تعالى فلم تعفه فلما رأيتني أمسكت يدك قال فإنه حر لوجه الله تعالى يا رسول الله فقال لو لم تفعل لسفعت وجهك النار).','1241','1607','0');
INSERT INTO [t38171] VALUES (1912,'١٨٩٩ - (قال - صلى الله عليه وسلم -) إن (العبد إذا نصح لسيده وأحسن عبادة الله تعالى فله أجره مرتين).','1241','1607','0');
INSERT INTO [t38171] VALUES (1913,'١٩٠٠ - (عرض علي أول ثلاثة يدخلون الجنة وأول ثلاثة يدخلون النار فأما أول ثلاثة يدخلون الجنة فالشهيد وعبد مملوك أحسن عبادة ربه ونصح لسيده وعفيف متعفف ذو عيال وأول ثلاثة يدخلون النار أمير مسلط وذو ثروة لا يعطي حق الله وفقير فخور)','1242','1607','0');
INSERT INTO [t38171] VALUES (1914,'١٩٠١ - وعن أبي مسعود) عقبة بن عامر (الأنصاري) ويقال له البدري أيضا لنزوله بدرا لا لشهوده إياها وهو عقبي سنا رضي الله عنه (قال بينما أنا أضرب غلاما لي فسمعت صوتا من خلفي اعلم) بصيغة الأمر من علم (أبا مسعود) هكذا رواية مسلم وأبي داود وفي رواية يا أبا مسعود (مرتين) أي قالها مرتين (فالتفت فإذا رسول الله - صلى الله عليه وسلم - فألقيت السوط فقال والله لله) وفي رواية والله إن الله ورواية مسلم فقال إن الله (أقدر عليك منك على هذا الغلام) (فقلت هو حر لوجه الله تعالى فقال أما لو لم تفعل للفحتك النار) والمعنى أقدر عليك بالعقوبة من قدرتك على ضربه لكنه يحلم إذا','1242','1607','0');
INSERT INTO [t38171] VALUES (1915,'١٩٠٢ - (قال - صلى الله عليه وسلم - إذا ابتاع) أي اشترى (أحدكم الخادم) عبدا أو أمة (فليكن أول شيء يطعمه الحلواء) أي ما فيه حلاوة خلقية أو مصنوعة (فإنه أطيب لنفسه) مع ما فيه من التفاؤل الحسن والأمر للندب','1243','1607','0');
INSERT INTO [t38171] VALUES (1916,'١٩٠٣ - (وقال أبو هريرة رضي الله عنه قال رسول الله - صلى الله عليه وسلم - إذا أتى أحدكم خادمه بطعامه فليجلسه وليأكل معه فإن لم يفعل فليناوله لقمة وفي رواية إذا كفى أحدكم مملوكه صنعة طعامه فكفاه حره ومؤنته وقربه إليه فليجلسه وليأكل معه فإن لم يفعل فليناوله أو ليأخذ أكلة فليروغها وأشار بيده وليضعها في يده وليقل كل هذه).','1243','1607','0');
INSERT INTO [t38171] VALUES (1917,'١٩٠٤ - (قال - صلى الله عليه وسلم - من كانت عنده جارية فعلمها) وفي نسخة فعالها (وأحسن إليها ثم أعتقها وتزوجها فذلك له أجران)','1244','1607','0');
INSERT INTO [t38171] VALUES (1918,'١٩٠٥ - (وقال - صلى الله عليه وسلم - كلكم راع وكلكم مسؤل عن رعيته)','1244','1607','0');
INSERT INTO [t38171] VALUES (1919,'١٩٠٦ - ثلاثة لا تسأل عنهم- الحديث.','1245','1607','0');
INSERT INTO [t38171] VALUES (1920,'كتاب آداب العزلة','1246','1202','0');
INSERT INTO [t38171] VALUES (1921,'١٩٠٧ - يروى عن فضالة بن عبيد رضي الله عنه عن النبي - صلى الله عليه وسلم - قال (ثلاثة لا يسئل عنهم رجل ينازع الله في ردائه ورداؤه الكبرياء وإزاره العظمة)','1247','1920','0');
INSERT INTO [t38171] VALUES (1922,'١٩٠٨ - (قوله - صلى الله عليه وسلم - من فارق الجماعة) أي جماعة المسلمين (شبرا خلع ربقة الإسلام من عنقه)','1247','1920','0');
INSERT INTO [t38171] VALUES (1923,'١٩٠٩ - (قوله - صلى الله عليه وسلم - من شق عصا المسلمين والمسلمون في إسلام دامج) أي مجتمع (فقد خلع ربقة الإسلام من عنقه)','1248','1920','0');
INSERT INTO [t38171] VALUES (1924,'١٩١٠ - (قال) - صلى الله عليه وسلم - (من هجر أخاه فوق ثلاث) ليال (فمات دخل النار)','1248','1920','0');
INSERT INTO [t38171] VALUES (1925,'١٩١١ - (قال - صلى الله عليه وسلم - لا يحل لمسلم أن يهجر أخاه فوق ثلاث والسابق بالصلح يدخل الجنة)','1248','1920','0');
INSERT INTO [t38171] VALUES (1926,'١٩١٢ - (وقال - صلى الله عليه وسلم - من هجر أخاه في الإسلام سنة) أي بغير عذر شرعي (فهو كسافك دمه)','1249','1920','0');
INSERT INTO [t38171] VALUES (1927,'١٩١٣ - (ما روي عن عائشة رضي الله عنها وعن أبيها أن النبي - صلى الله عليه وسلم - هجرها ذا الحجه والمحرم وبعض صفر)','1250','1920','0');
INSERT INTO [t38171] VALUES (1928,'١٩١٤ - (روى عمر) بن الخطاب (رضي الله عنه أنه - صلى الله عليه وسلم - اعتزل نساءه وآلى منهن شهرا وصعد إلى غرفة له وهي خزانته فلبث فيها تسعا وعشرين) يوما (فلما نزل قيل له إنك كنت فيها تسعا وعشرين فقال الشهر قد يكون تسعا وعشرين)','1250','1920','0');
INSERT INTO [t38171] VALUES (1929,'١٩١٥ - (روت عائشة رضي الله عنها أن النبي - صلى الله عليه وسلم - قال لا يحل لمسلم أن يهجر أخاه فوق ثلاثة أيام إلا أن يكون ممن لا يؤمن بوائقه).','1251','1920','0');
INSERT INTO [t38171] VALUES (1930,'١٩١٦ - (روي أن رجلا أتى الجبل ليتعبد فيه فجيء به إلى النبي - صلى الله عليه وسلم - فقال لا تفعل أنت ولا أحد منكم لصبر أحدكم في بعض مواطن الإسلام خير له من عبادة أحدكم أربعين عاما)','1251','1920','0');
INSERT INTO [t38171] VALUES (1931,'١٩١٧ - (عن أبي هريرة رضي الله عنه أنه قال غزونا على عهد رسول الله - صلى الله عليه وسلم - فمررنا بشعب) أي طريق في الجبل (فيه عيينة)','1251','1920','0');
INSERT INTO [t38171] VALUES (1932,'١٩١٨ - (روى معاذ بن جبل أنه - صلى الله عليه وسلم - قال إن الشيطان ذئب الإنسان كذئب الغنم يأخذ القاصية والناحية والشاردة وإياكم والشعاب وعليكم بالعامة والجماعة والمساجد)','1252','1920','0');
INSERT INTO [t38171] VALUES (1933,'١٩١٩ - (روي أنه - صلى الله عليه وسلم - قيل له الوضوء من جر مخمر) أي مغطى (أحب إليك أم من هذه المطاهر التي يتطهر منها الناس) قال في المصباح كل إناء يتطهر به مطهرة والجمع المطاهر (فقال بل من هذه المطاهر التماسا لبركة أيدي المسلمين)','1253','1920','0');
INSERT INTO [t38171] VALUES (1934,'١٩٢٠ - (روي أنه - صلى الله عليه وسلم - لما طاف بالبيت) أي فرغ من طوافه (عدل إلى زمزم ليشرب منها) أنث الضمير على إرادة العين (فإذا التمر المنتقع في حياض الأدم قد مغثه الناس) أي مرسوه ودلكوه (بأيديهم وهم يتناولون منه ويشربون) والمعنى أنهم قد وسخوه لما خالطته أيديهم (فاستسقى منه وقال اسقوني فقال العباس) بن عبد المطلب رضي الله عنه (إن هذا النبيذ شراب قد مغث) أي مرس ودلك (وخيض بالأيدي أفلا آتيك بشراب أنظف من هذا في جر مخمر) أي مغطى (في البيت فقال اسقوني من هذا الذي يشرب الناس منه التمس بركة يد المسلمين فشرب منه).','1253','1920','0');
INSERT INTO [t38171] VALUES (1935,'١٩٢١ - حديث الهجرة إلى الحبشة.','1254','1920','0');
INSERT INTO [t38171] VALUES (1936,'١٩٢٢ - (قوله - صلى الله عليه وسلم - لعبد الله بن عامر الجهني) هكذا في سائر نسخ الكتاب وليس في الصحابة من اسمه عبد الله بن عامر إلا رجلان أحدهما بلدي حليف بني ساعدة وهو بدري عند ابن إسحاق وآخر عامري له وفادة وفي نسخة العراقي عقبة بن عامر الجهني وهكذا هو في سنن الترمذي (لما قال له يا رسول الله ما النجاة قال ليسعك بيتك وأمسك عليك لسانك وابك على خطيئتك).','1255','1920','0');
INSERT INTO [t38171] VALUES (1937,'١٩٢٣ - (قال - صلى الله عليه وسلم - الذي يخالط الناس ويصبر على أذاهم خير) وفي رواية أفضل (من الذي لا يخالطه الناس ولا يصبر على أذاهم)','1255','1920','0');
INSERT INTO [t38171] VALUES (1938,'١٩٢٤ - (روي أنه - صلى الله عليه وسلم - قال لأصحابه ألا أنبئكم بخير الناس قالوا بلى يا رسول الله فأشار بيده نحو المغرب وقال رجل آخذ بعنان فرسه في سبيل الله ينتظر أن يغير أو يغار عليه إلا أنبئكم بخير الناس بعده وأشار بيده نحو الحجاز وقال رجل في غنمه يقيم','1255','1920','0');
INSERT INTO [t38171] VALUES (1939,'١٩٢٥ - وعن قيس بن أبي حازم قال لما ولي أبو بكر صعد المنبر فحمد الله ثم قال (يا أيها الناس إنكم تقرؤن هذه الآية) وهي في سورة المائدة {يا أيها الذين آمنوا عليكم أنفسكم لا يضركم من ضل إذا اهتديتم} وإنكم تضعونها في غير موضعها) وفي نسخة على غير مواضعها (وإني سمعت رسول الله - صلى الله عليه وسلم - يقول إذا رأى الناس المنكر) وفي لفظ أن الناس إذا رأوا المنكر (فلم يغيروه) وفي لفظ ولا يغيرونه (أوشك أن يعمهم الله بعقاب).','1256','1920','0');
INSERT INTO [t38171] VALUES (1940,'١٩٢٦ - (وقد قال - صلى الله عليه وسلم - إن الله يسأل العبد) أي يوم وقوفه بين يديه (حتى يقول ما منعك إذ رأيت المنكر في الدنيا أن تغيره) بيدك أو بلسانك (فإذا لقن الله العبد حجته فيقول يا رب رجوتك وخفت الناس).','1257','1920','0');
INSERT INTO [t38171] VALUES (1941,'١٩٢٧ - (قال - صلى الله عليه وسلم - تجدون من شرار الناس ذا الوجهين يأتي هؤلاء بوجه وهؤلاء بوجه).','1258','1920','0');
INSERT INTO [t38171] VALUES (1942,'١٩٢٨ - (وقال - صلى الله عليه وسلم - إن من شرار الناس ذا الوجهين يأتي هؤلاء بوجه وهؤلاء بوجه).','1258','1920','0');
INSERT INTO [t38171] VALUES (1943,'١٩٢٩ - (قوله - صلى الله عليه وسلم - عند ذكر الصالحين تنزل الرحمة).','1258','1920','0');
INSERT INTO [t38171] VALUES (1944,'١٩٣٠ - (قال مثل الجليس السوء كمثل الكير إن لم يحرقك بشرره علق بك من ريحه).','1259','1920','0');
INSERT INTO [t38171] VALUES (1945,'١٩٣١ - (وقال مثل الذي يجلس يستمع الحكمة ثم لا يعمل إلا بشر ما يستمع كمثل رجل أتى راعيا فقال له يا راعي اجزر لي شاة من غنمك فقال اذهب فخذ خير شاة فيها فذهب فأخذ بأذن كلب الغنم).','1259','1920','0');
INSERT INTO [t38171] VALUES (1946,'١٩٣٢ - (إذا رأيت الناس مرجت عهودهم) أي اضطربت (وخفت أماناتهم) أي قلت (وكانوا هكذا وشبك بين أصابعه) إشارة إلى شدة الاختلاط (فقلت ما تأمرني يا رسول الله فقال الزم بيتك وأملك عليك لسانك) أي لا تتكلم في شيء من أمورهم (وخذ ما تعرف ودع ما تنكر وعليك بأمر الخاصة ودع عنك أمر العامة).','1260','1920','0');
INSERT INTO [t38171] VALUES (1947,'١٩٣٣ - (وقال ابن مسعود) رضي الله عنه (ذكر رسول الله - صلى الله عليه وسلم - أيام الفتنة وأيام الهرج) بفتح فسكون (قلت متى الهرج) يا رسول الله (قال حين لا يأمن الرجل جليسه) أي من بوائقه (قلت فبم تأمرني إن أدركت ذلك الزمان قال كف نفسك ويديك) أي عن المباشرة (وادخل دارك) وأغلق عليك الباب (قال قلت أرأيت يا رسول الله إن دخل علي داري قال فادخل بيتك) أي داخل الدار (قال إن دخل علي بيتي قال فادخل مسجدك) أي المخدع الذي تصلي فيه داخل البيت (واصنع هكذا وقبض على الكوع) هو طرف الزند الذي يلي الإبهام (وقل ربي الله حتى تموت).','1260','1920','0');
INSERT INTO [t38171] VALUES (1948,'١٩٣٤ - (روي عن ابن عمر رضي الله عنه أنه لما بلغه أن الحسين) بن علي (رضي الله عنه توجه إلى العراق) حين وردت عليه كتب من الكوفة بنصرته والقيام معه وكان قد شاور جملة من الصحابة فما رضوا خروجه من المدينة فأبي فلما خرج بأهله وعياله (اتبعه) ابن عمر (فلحقه على مسيرة ثلاثة أيام) من المدينة بعد خروجه (فقال أين تريد فقال) أريد (العراق فإذا معه طوامير وكتب) التي وصلت إليه منهم (فقال هذه كتبهم وبيعتهم فقال لا تنظر إلى كتبهم ولا تأتهم)','1261','1920','0');
INSERT INTO [t38171] VALUES (1949,'١٩٣٥ - (قال أبو الدرداء) رضى الله عنه (أخبر تقله).','1262','1920','0');
INSERT INTO [t38171] VALUES (1950,'١٩٣٦ - (وقال - صلى الله عليه وسلم - انظروا إلى من هو دونكم)','1262','1920','0');
INSERT INTO [t38171] VALUES (1951,'١٩٣٧ - (في الخبر أن من سلب الله كريمتيه) أي عينيه ويقال للعين كريمة لكرامتها على صاحبها (عوضه الله عنهما هو خير منهما)','1263','1920','0');
INSERT INTO [t38171] VALUES (1952,'١٩٣٨ - (ليس الخبر كالمعاينة)','1264','1920','0');
INSERT INTO [t38171] VALUES (1953,'١٩٣٩ - (إن القلوب إذا أكرهت عميت)','1264','1920','0');
INSERT INTO [t38171] VALUES (1954,'١٩٤٠ - (قال - صلى الله عليه وسلم - إن الله لا يمل حتى تملوا)','1265','1920','0');
INSERT INTO [t38171] VALUES (1955,'١٩٤١ - (كان سيد المرسلين - صلى الله عليه وسلم - يشتري الشيء فيحمله إلى بيته بنفسه فيقول له صاحبه أعطني أحمله فيقول صاحب الشيء أحق بحمله).','1265','1920','0');
INSERT INTO [t38171] VALUES (1956,'١٩٤٢ - (فالمجاهد) ليس هو من جاهد الكفار بسيفه وسنانه فقط بل هو أيضا (من جاهد نفسه وهواه) بأن أماته بسيف تأديبه (كما صرح به رسول الله - صلى الله عليه وسلم -).','1266','1920','0');
INSERT INTO [t38171] VALUES (1957,'كتاب آداب السفر','1267','1202','0');
INSERT INTO [t38171] VALUES (1958,'١٩٤٣ - (قال - صلى الله عليه وسلم - من خرج من بيته في طلب العلم) الشرعي النافع الذي أريد به وجه الله (فهو في سبيل الله) أي حكمه حكم من هو في الجهاد (حتى يرجع).','1268','1957','0');
INSERT INTO [t38171] VALUES (1959,'١٩٤٤ - (رحل جابر بن عبد الله) الأنصاري رضي الله عنه (من المدينة إلى مصر مع غيره من الصحابة فسافروا شهرا في حديث بلغهم عن عبد الله بن أنيس) بن أسد الجهني ثم (الأنصاري) حليفهم يكني أبا يحيى روى عنه أولاده وعمر وحمزة وعبد الله وبسر بن سعيد روى له الجماعة إلا البخاري مات بالشام سنة ثمانين (يحدث عن رسول الله - صلى الله عليه وسلم - حتى سمعوه).','1268','1957','0');
INSERT INTO [t38171] VALUES (1960,'١٩٤٥ - (قال أسامة بن زيد قال رسول الله - صلى الله عليه وسلم - إن هذا الوجع أو السقم رجز عذب به بعض الأمم قبلكم ثم بقي بعد في الأرض فيذهب المرة ويأتي الأخرى فمن سمع به في أرض فلا يقدمن عليه ومن وقع بأرض وهو بها فلا يخرجنه الفرار منه).','1270','1957','0');
INSERT INTO [t38171] VALUES (1961,'١٩٤٦ - (قالت عائشة رضي الله عنها قال رسول الله - صلى الله عليه وسلم - إن فناء أمتي بالطعن والطاعون فقلت هذا الطعن قد عرفناه فما الطاعون قال غدة كغدة البعير تأخذهم في مراقهم المسلم الميت منه شهيد والمقيم عليه المحتسب كالمرابط في سبيل الله والفار منه كالفار من الزحف.','1270','1957','0');
INSERT INTO [t38171] VALUES (1962,'١٩٤٧ - (وعن مكحول) أبي عبد الله الدمشقي الفقيه مات سنة بضع عشرة ومائة روى له مسلم والأربعة (عن أم أيمن) بركة حاضنة رسول الله - صلى الله عليه وسلم - وهي والدة أسامة بن زيد ماتت في خلافة عثمان رضي الله عنهما (قالت أوصى رسول الله - صلى الله عليه وسلم - بعض أصحابه).','1271','1957','0');
INSERT INTO [t38171] VALUES (1963,'١٩٤٨ - (نهى - صلى الله عليه وسلم - أن يسافر الرجل وحده).','1273','1957','0');
INSERT INTO [t38171] VALUES (1964,'١٩٤٩ - (وقال الثلاثة نفر)','1273','1957','0');
INSERT INTO [t38171] VALUES (1965,'١٩٥٠ - (قال) أيضا (إذا كنتم ثلاثة في سفر فأمروا أحدكم). هكذ هو في القوت.','1273','1957','0');
INSERT INTO [t38171] VALUES (1966,'١٩٥١ - (وكانوا يفعلون ذلك ويقولون هو أمير أمره رسول الله - صلى الله عليه وسلم -) هكذا هو في القوت.','1273','1957','0');
INSERT INTO [t38171] VALUES (1967,'١٩٥٢ - (قال - صلى الله عليه وسلم - خير الأصحاب أربعة)','1274','1957','0');
INSERT INTO [t38171] VALUES (1968,'١٩٥٣ - (قال بعضهم صحبت عبد الله بن عمر رضي الله عنهما من مكة إلى المدينة فلما أردت أن أفارقه شيعني وقال سمعت رسول الله - صلى الله عليه وسلم - يقول قال لقمان الحكيم إن الله تعالى إذا استودع شيئا حفظه وإني أستودع الله دينك وأمانتك وخواتيم عملك).','1274','1957','0');
INSERT INTO [t38171] VALUES (1969,'١٩٥٤ - (وروى زيد بن أرقم) بن زيد بن قيس الأنصاري الخزرجي صحابي مشهور رضي الله عنه أول مشاهده الخندق مات سنة ست وسبعين من الهجرة روى له الجماعة (عن رسول الله - صلى الله عليه وسلم - أنه قال إذا أراد أحدكم سفرا فليودع إخوانه فإن الله تعالى جاعل في دعائهم البركة).','1276','1957','0');
INSERT INTO [t38171] VALUES (1970,'١٩٥٥ - (وعن عمرو بن شعيب عن أبيه عن جده) عبد الله بن عمرو بن العاص رضي الله عنهم (أن رسول الله - صلى الله عليه وسلم - كان إذا ودع رجلا قال زودك الله التقوى وكفر ذنبك ووجهك للخير حيث توجهت).','1277','1957','0');
INSERT INTO [t38171] VALUES (1971,'١٩٥٦ - (وقال موسى بن وردان) العامري مولاهم المصري مدني الأصل صدوق مات سنة سبع عشرة ومائة عن أربع وسبعين وروى له البخاري في الأدب والأربعة (أتيت أبا هريرة) رضي الله عنه (أودعه لسفر أردته فقال ألا أعلمك يا ابن أخي شيئا علمنيه رسول الله - صلى الله عليه وسلم - عند الوداع فقلت بلى فقال أستودعك الله الذي لا تضيع ودائعه).','1277','1957','0');
INSERT INTO [t38171] VALUES (1972,'١٩٥٧ - (عن أنس بن مالك) رضي الله عنه (أن رجلا أتى النبي - صلى الله عليه وسلم - فقال إني أريد سفرا فأوصني فقال له في حفظ الله وفي كنفه زودك الله التقوى وكفر ذنبك ووجهك للخير حيث كنت أو أينما كنت شك فيه الراوي).','1278','1957','0');
INSERT INTO [t38171] VALUES (1973,'١٩٥٩ - (روى أنس بن مالك) رضي الله عنه (إن رجلا أتى النبي - صلى الله عليه وسلم - فقال إني أردت سفرا).','1280','1957','0');
INSERT INTO [t38171] VALUES (1974,'١٩٦٠ - رواه الطبراني في الكبير من حديث بريدة رضي الله عنه أنه - صلى الله عليه وسلم - كان إذا خرج من بيته قال بسم الله رب أعوذ بك من أن أذل أو أضل أو أظلم أو أظلم أو أجهل أو يجهل علي','1282','1957','0');
INSERT INTO [t38171] VALUES (1975,'١٩٦١ - (فإذا) نهض من جلوسه و (مشى قال اللهم بك انتشرت وعليك توكلت وبك اعتصمت وإليك توجهت اللهم أنت ثقتي ورجائي فاكفني ما أهمني وما لا أهتم به وما أنت أعلم به مني عز جارك وجل ثناؤك ولا إله غيرك اللهم زودني التقوى واغفر لي ذنبي ووجهني للخير أينما توجهت).','1282','1957','0');
INSERT INTO [t38171] VALUES (1976,'١٩٦٢ - (وليدع بهذا الدعاء في كل منزل يرحل عنه فإذا ركب الدابة فليقل بسم الله وبالله والله أكبر توكلت على الله ولا حول ولا قوة إلا بالله العلي العظيم ما شاء الله كان وما لم يشأ لم يكن سبحان الذي سخر لنا هذا وما كنا له مقرنين وإنا إلى ربنا لمنقلبون)','1283','1957','0');
INSERT INTO [t38171] VALUES (1977,'١٩٦٣ - (روى جابر) بن عبد الله الأنصاري رضي الله عنه (أن النبي - صلى الله عليه وسلم - رحل يوم الخميس يريد تبوك) وهو موضع بالشام (وبكر) أي سافر في أول النهار (وقال اللهم بارك لأمتي في بكورها)','1285','1957','0');
INSERT INTO [t38171] VALUES (1978,'١٩٦٤ - (وروى أنس) رضي الله عنه (إنه قال - صلى الله عليه وسلم - اللهم بارك لأمتي في بكورها يوم الخميس والسبت)','1285','1957','0');
INSERT INTO [t38171] VALUES (1979,'١٩٦٥ - (وكان - صلى الله عليه وسلم - إذا بعث سرية) أي طائفة من العسكر (بعثها أول النهار)','1285','1957','0');
INSERT INTO [t38171] VALUES (1980,'١٩٦٦ - (وروى أبو هريرة) رضي الله عنه (أنه - صلى الله عليه وسلم - قال اللهم بارك لأمتي) في بكورها (يوم خميسها)','1286','1957','0');
INSERT INTO [t38171] VALUES (1981,'١٩٦٧ - (وقال عبد الله بن عباس) رضي الله عنه (إذا كانت لك إلى رجل حاجة فاطلبها إليه نهارا ولا تطلبها ليلا واطلبها بكرة فإني سمعت رسول الله - صلى الله عليه وسلم - يقول اللهم بارك لأمتي في بكورها)','1286','1957','0');
INSERT INTO [t38171] VALUES (1982,'١٩٦٨ - قال النبي - صلى الله عليه وسلم - (لأن أشيع مجاهدا في سبيل الله فأكففه) وفي نسخة فاكتنفه (على رحله غدوة أو روحة أحب إلي من الدنيا وما فيها).','1286','1957','0');
INSERT INTO [t38171] VALUES (1983,'١٩٦٩ - (مهما أشرف على المنزل) يريد نزوله (فليقل) هذه الكلمات (اللهم رب السماوات السبع وما أظللن ورب الأرضين السبع وما أقللن) أي حلمن (ورب الشياطين وما أضللن) أي أغوين (ورب الرياح وما ذرين ورب البحار وما جرين أسألك خير هذا المنزل وخير أهله وأعوذ بك من شر هذا المنزل وشر ما فيه اصرف عني شر شرارهم).','1287','1957','0');
INSERT INTO [t38171] VALUES (1984,'١٩٧٠ - (فإذا نزل المنزل فليصل فيه ركعتين) فقد روى البيهقي من حديث أنس كان إذا نزل منزلا لم يرتحل حتى يصلي فيه ركعتين وعند الطبراني من حديث فضالة بن عبيد كان إذا نزل منزلا في سفر ودخل بيته لم يجلس حتى يركع ركعتين (ثم ليقل أعوذ','1288','1957','0');
INSERT INTO [t38171] VALUES (1985,'١٩٧١ - (ومهما علا نشزا) محركة وهو ما ارتفع (من الأرض في وقت السير فينبغي أن يقول اللهم لك الشرف على كل شرف ولك الحمد على كل حال).','1289','1957','0');
INSERT INTO [t38171] VALUES (1986,'١٩٧٢ - (مهما هبط سبح).','1290','1957','0');
INSERT INTO [t38171] VALUES (1987,'١٩٧٣ - (مهما خاف الوحشة في سفره قال سبحان الملك القدوس رب الملائكة والروح جللت السماوات والأرض بالعزة والجبروت).','1290','1957','0');
INSERT INTO [t38171] VALUES (1988,'١٩٧٤ - (وقالت أم سعد الأنصارية) هي كبشة بنت رافع بن عبيد الخدرية أم سعد بن معاذ رضي الله عنه (كان رسول الله لا يفارقه في السفر المرآة والمكحلة).','1290','1957','0');
INSERT INTO [t38171] VALUES (1989,'١٩٧٥ - (في رواية أخرى عنها ستة أشياء المرآة والقارورة) أي وعاء الطيب (والمقراض) وهو المقص (والسواك والمكحلة والمشط).','1291','1957','0');
INSERT INTO [t38171] VALUES (1990,'١٩٧٦ - (وقال صهيب قال رسول الله - صلى الله عليه وسلم - عليكم بالإثمد عند مضجعكم فإنه مما يزيد في البصر وينبت الشعر).','1291','1957','0');
INSERT INTO [t38171] VALUES (1991,'١٩٧٧ - (وروى أنه - صلى الله عليه وسلم - كان يكتحل ثلاثا ثلاثا) رواه أنس بلفظ كان يكتحل وترا ذكره المحب الطبري في الأحكام وأخرج أحمد والطبراني من حديث عقبة بن عامر كان إذا اكتحل اكتحل وترا وإذا استجمر استجمر وترا (وفي رواية أنه اكتحل لليمنى ثلاثا ولليسرى ثنتين).','1292','1957','0');
INSERT INTO [t38171] VALUES (1992,'١٩٧٨ - (فإذا دخل) البيت (قال توبا توبا لربنا أوبا أو لا يغادر علينا حوبا).','1292','1957','0');
INSERT INTO [t38171] VALUES (1993,'١٩٧٩ - وينبغي أن يحمل لأهل بيته ولأقاربه تحفة) وفي نسخة هدية (مطعوما أو غيره على قدر إمكانه فهو سنة فقد روى أنه إن لم يجد شيئا ليضع في مخلاته حجرا).','1293','1957','0');
INSERT INTO [t38171] VALUES (1994,'١٩٨٠ - (فلا يزيد على ثلاثة أيام فهو حد الضيافة).','1293','1957','0');
INSERT INTO [t38171] VALUES (1995,'١٩٨١ - (قال صفوان بن عسال) المرادي صحابي مشهور نزل الكوفة له اثنتا عشرة غزوة وروى عنه ابن مسعود مع جلالته وزر بن حبيش وعبد الله بن سلمة وطائفة وروى له الترمذي والنسائي وابن ماجه (أمرنا رسول الله - صلى الله عليه وسلم - إذا كنا مسافرين أو) قال (سفرا) شك من الراوي وهو بفتح فسكون جمع سافر كركب وراكب (أن','1293','1957','0');
INSERT INTO [t38171] VALUES (1996,'١٩٨٢ - (كذلك فعل رسول الله - صلى الله عليه وسلم -: مسح أعلى الخف وأسفله).','1294','1957','0');
INSERT INTO [t38171] VALUES (1997,'١٩٨٣ - (روى أبو أمامة) الباهلي صدى بن عجلان رضي الله عنه (أنه قال دعا رسول الله - صلى الله عليه وسلم - بخفيه فلبس أحدهما فجاء غراب فاحتمل الآخر ثم رمى به فخرجت منه حية) وفي لفظ فوقعت بدل فخرجت (فقال النبي - صلى الله عليه وسلم - من كان يؤمن بالله واليوم الآخر فلا يلبس خفيه حتى ينفضهما).','1296','1957','0');
INSERT INTO [t38171] VALUES (1998,'١٩٨٤ - (ترخص رسول الله - صلى الله عليه وسلم - فقصر في بعض الغزوات ثمانية عشر يوما على موضع واحد).','1296','1957','0');
INSERT INTO [t38171] VALUES (1999,'١٩٨٥ - (كان رسول الله - صلى الله عليه وسلم - يصلي على راحلته أينما توجهت به دابته وأوتر رسول الله - صلى الله عليه وسلم - على الراحلة).','1297','1957','0');
INSERT INTO [t38171] VALUES (2000,'١٩٨٦ - (روي عن رسول الله - صلى الله عليه وسلم - أنه قال لأهل المدينة (ما بين المغرب والمشرق قبلة) والمغرب يقع على يمين أهل المدينة والمشرق على يسارهم فجعل رسول الله - صلى الله عليه وسلم - جميع ما يقع بينهما قبلة ومساحة الكعبة لا تفي بما بين المشرق والمغرب وإنما يفي بذلك جهتها).','1297','1957','0');
INSERT INTO [t38171] VALUES (2001,'١٩٨٧ - (روي أن أهل مسجد قباء كانوا في صلاة الصبح مستقبلين لبيت المقدس مستدبرين الكعبة لأن المدينة بينهما فقيل لهم ألا قد حولت القبلة إلى الكعبة فاستداروا في أثناء الصلاة من غير طلب دلالة ولم ينكر عليهم وسمى مسجدهم القبلتين).','1299','1957','0');
INSERT INTO [t38171] VALUES (2002,'١٩٨٨ - (قوله - صلى الله عليه وسلم - في آداب قضاء الحاجة لا تستقبلوا بها القبلة ولا تستدبروها ولكن شرقوا أو غربوا)','1300','1957','0');
INSERT INTO [t38171] VALUES (2003,'١٩٨٩ - (قال - صلى الله عليه وسلم - ليس الصبح هكذا وجمع بين كفيه وإنما الصبح هكذا ووضع إحدى سبابتيه على الأخرى وفتحهما وأشار به إلى أنه معترض)','1300','1957','0');
INSERT INTO [t38171] VALUES (2004,'كتاب السماع','1301','1202','0');
INSERT INTO [t38171] VALUES (2005,'١٩٩٠ - (آفة العلم الخيلاء كما قاله - صلى الله عليه وسلم -)','1302','2004','0');
INSERT INTO [t38171] VALUES (2006,'١٩٩١ - (قال عائشة رضي الله عنها فأخبرت بذلك رسول الله - صلى الله عليه وسلم - فقال اللهم حبب لنا المدينة كحبنا مكة)','1302','2004','0');
INSERT INTO [t38171] VALUES (2007,'١٩٩٢ - (كان رسول الله - صلى الله عليه وسلم - ينقل اللبن) ككتف الطوب النيء (مع القوم في بناء المسجد) النبوي (وهو يقول).','1302','2004','0');
INSERT INTO [t38171] VALUES (2008,'١٩٩٣ - (وكان النبي - صلى الله عليه وسلم - يضع لحسان) بن ثابت رضي الله عنه (منبرا في المسجد يقوم عليه قائما فيفاخر عن رسول الله - صلى الله عليه وسلم - أو ينافح) أي يدافع وهو شك من الراوي (ويقول رسول الله - صلى الله عليه وسلم - إن الله يؤيد حسان بروح القدس ما نافح أو) قال (فاخر عن رسول الله - صلى الله عليه وسلم -)','1303','2004','0');
INSERT INTO [t38171] VALUES (2009,'١٩٩٤ - لما أنشده النابغة الجعدي رضي الله عنه واسمه قيس بن عبد الله بن عدس بن ربيعة بن جعدة بن كعب بن عامر بن صعصعة','1303','2004','0');
INSERT INTO [t38171] VALUES (2010,'١٩٩٦ - (عن أنس) بن مالك رضي الله عنه (أن النبي - صلى الله عليه وسلم - كان يحدي له في السفر وأن أنجشة) بفتح الهمزة وسكون النون وضم الجيم وفتح الشين المعجمة (كان يحدو بالنساء والبراء بن مالك) يعني أخاه (كان يحدو بالرجال فقال الني - صلى الله عليه وسلم - يا أنجشة رويدك سوقا بالقوارير).','1307','2004','0');
INSERT INTO [t38171] VALUES (2011,'١٩٩٧ - (إنشاد النساء بالدف والألحان عند قدوم رسول الله - صلى الله عليه وسلم -) المدينة.','1307','2004','0');
INSERT INTO [t38171] VALUES (2012,'١٩٩٨ - (عن عائشة رضي الله عنها أنها قالت رأيت رسول الله - صلى الله عليه وسلم - يسترني بردائه وأنا أنظر إلى الحبشة يلعبون) أي بالحراب والدرق (في المسجد حتى أكون أنا التي أسأمه فاقدروا) بضم الدال وكسرها لغتان حكاهما الجوهري وغيره وهو من التقدير أي قدروا في أنفسكم (قدر) رغبة من تكون بهذ الصفة وهي (الجارية الحديثة السن الحريصة على اللهو) أي حداثة السن والحرص على اللهو ولا مانع لها من ذلك متى تشتهي (إشارة إلى طول مدة وقوفها).','1308','2004','0');
INSERT INTO [t38171] VALUES (2013,'١٩٩٩ - (عن عائشة رضي الله عنها أن أبا بكر رضي الله عنه دخل عليها وعندها جاريتان في أيام منى تدففان وتضربان والنبي - صلى الله عليه وسلم - متغش بثوبه) أي مخمر وجهه (فانتهرهما) أي زجرهما (أبو بكر فكشف النبي - صلى الله عليه وسلم - عن وجهه فقال دعهما يا أبا بكر فإنها أيام عيد).','1309','2004','0');
INSERT INTO [t38171] VALUES (2014,'٢٠٠٠ - (قالت عائشة رضي الله عنها رأيت رسول الله - صلى الله عليه وسلم - يسترني وأنا أنظر إلى الحبشة وهم يلعبون في المسجد فزجرهم عمر رضي الله عنه فقال النبي - صلى الله عليه وسلم - أمنا يا بني أرفدة يعني من الأمن).','1309','2004','0');
INSERT INTO [t38171] VALUES (2015,'٢٠٠١ - (روي عن عائشة رضي الله عنها أنا قالت كنت ألعب عند رسول الله - صلى الله عليه وسلم - وكان تأتيني صواحبات لي فكن يستحيين) وفي نسخة ينقمعن (من رسول الله - صلى الله عليه وسلم - وكان النبي - صلى الله عليه وسلم - يسر بهن إلي','1309','2004','0');
INSERT INTO [t38171] VALUES (2016,'٢٠٠٢ - (في رواية) أخرى (أن النبي - صلى الله عليه وسلم - قال لها) يوما (ما هذا) يا عائشة (قالت بنياتي) بالتصغير وفى نسخة بناتي (قال فما هذا الذي أرى في وسطهن قالت فرس قال ما هذا الذي عليه قالت جناحان فقال فرس له جناحان قالت أوما سمعت أنه كان لسليمان بن داود) عليهما السلام (خيل لها أجنحة قالت فضحك النبي - صلى الله عليه وسلم - حتى بدت نواجذه).','1310','2004','0');
INSERT INTO [t38171] VALUES (2017,'٢٠٠٣ - (قالت عائشة رضي الله عنها دخل على رسول الله - صلى الله عليه وسلم - وعندي جاريتان تغنيان بغناء بعاث).','1310','2004','0');
INSERT INTO [t38171] VALUES (2018,'٢٠٠٤ - (وقال الحافظ أبو الفضل محمد بن طاهر المقدسي أخبرنا أبو إسحاق وإبراهيم بن محمد الأصفهاني بها حدثنا إبراهيم بن عبد الله التاجر حدثنا الحسين بن إسماعيل المحاملي','1311','2004','0');
INSERT INTO [t38171] VALUES (2019,'٢٠٠٥ - (روى أبو هريرة) رضي الله عنه (عن رسول الله - صلى الله عليه وسلم - أنه ذكر غلاما في بني إسرائيل كان على جبل) وفي نسخة كان في بني إسرائيل على جبل (فقال لأمه من خلق السماء قالت الله عز وجل فقال من خلق الأرض قالت الله عز وجل فقال من خلق الجبال قالت الله عز وجل فقال من خلق هذا الغيم قالت الله عز وجل فقال إني لأسمع لله تعالى شأنا ثم رمى بنفسه من الجبل فتقطع).','1311','2004','0');
INSERT INTO [t38171] VALUES (2020,'٢٠٠٦ - (روت عائشة رضي الله عنها) أن النبي - صلى الله عليه وسلم - قال (إن الله تعالى حرم القينة وبيعها وثمنها وتعليمها).','1312','2004','0');
INSERT INTO [t38171] VALUES (2021,'٢٠٠٧ - (عن جابر) بن عبد الله رضي الله عنه (عن النبي - صلى الله عليه وسلم - أنه قال كان إبليس أول من ناح وأول من تغنى فقد جمع بين النياحة والغناء).','1312','2004','0');
INSERT INTO [t38171] VALUES (2022,'٢٠٠٨ - (روى أبو أمامة) صدي بن عجلان الباهلي رضي الله عنه (عنه - صلى الله عليه وسلم - إنه قال ما رفع رجل صوته بغناء إلا بعث الله له شيطانين على منكبيه يضربان باعقابهما على صدره حتى يمسك)','1312','2004','0');
INSERT INTO [t38171] VALUES (2023,'٢٠٠٩ - (روى عقبة بن عامر) الجهني رضي الله عنه (إن النبي - صلى الله عليه وسلم - قال كل شيء يلهو به الرجل فهو باطل إلا تأديبه فرسه ورميه بالقوس وملاعبته امرأته)','1313','2004','0');
INSERT INTO [t38171] VALUES (2024,'٢٠١٠ - (قوله - صلى الله عليه وسلم - لا يحل دم امرئ مسلم)','1314','2004','0');
INSERT INTO [t38171] VALUES (2025,'٢٠١١ - (قول عبد الله بن مسعود رضي الله عنه الغناء ينبت في القلب النفاق)','1314','2004','0');
INSERT INTO [t38171] VALUES (2026,'٢٠١٢ - (عن نافع) موسى ابن عمر (أنه قال كنت مع ابن عمر) رضي الله عنهما (في طريق فسمع زمارة راع فوضع إصبعيه في أذنيه ثم عدل عن الطريق فلم يزل يقول يا نافع أتسمع ذلك حتى قلت لا فأخرج أصبعيه) من أذنيه (وقال هكذا رأيت رسول الله - صلى الله عليه وسلم - صنع)','1316','2004','0');
INSERT INTO [t38171] VALUES (2027,'٢٠١٣ - (قول الرسول - صلى الله عليه وسلم - إن أهل الجنة يزورون ربهم في كل يوم جمعة مرة)','1316','2004','0');
INSERT INTO [t38171] VALUES (2028,'٢٠١٤ - (قال رسول الله - صلى الله عليه وسلم - اتقوا فراسة المؤمن فإنه ينظر بنور الله تعالى)','1316','2004','0');
INSERT INTO [t38171] VALUES (2029,'٢٠١٥ - (قوله - صلى الله عليه وسلم - شيبتني هود وأخواتها)','1317','2004','0');
INSERT INTO [t38171] VALUES (2030,'٢٠١٦ - (وروي أن ابن مسعود) رضي الله عنه قرأ على رسول الله - صلى الله عليه وسلم - سورة النساء حتى انتهى إلى قوله تعالى فكيف إذا جئنا من كل أمة بشهيد وجئنابك على هؤلاء شهيدا قال حسبك فكانت عيناه تذرفان) أي تسيلان (بالدموع).','1319','2004','0');
INSERT INTO [t38171] VALUES (2031,'٢٠١٧ - (وفي رواية) أخرى (أنه - صلى الله عليه وسلم - قرئ عنده) قوله تعالى (إن لدينا أنكالا وجحيما وطعاما ذا غصة وعذابا أليما فصعق).','1320','2004','0');
INSERT INTO [t38171] VALUES (2032,'٢٠١٨ - (وروي أنه - صلى الله عليه وسلم - قرأ إن تعذبهم فإنهم عبادك فبكى).','1320','2004','0');
INSERT INTO [t38171] VALUES (2033,'٢٠١٩ - (ما روي أن زرارة بن أوفى) العامري الحريشي البصري يكنى أبا حاجب وكان قاضيها ثقة عابد أخرج له الجماعة (كان من) ثقات (التابعين كان يؤم الناس بالرقة فقرأ) يوما في صلاته (فإذا نقر في الناقور فصعق ومات في محرابه).','1321','2004','0');
INSERT INTO [t38171] VALUES (2034,'٢٠٢٠ - (فصل).','1321','2004','0');
INSERT INTO [t38171] VALUES (2035,'٢٠٢١ - (لابد من مخالقة الناس بأخلاقهم كما ورد في الخبر).','1324','2004','0');
INSERT INTO [t38171] VALUES (2036,'كتاب الأمر بالمعروف والنهي عن المنكر','1325','1202','0');
INSERT INTO [t38171] VALUES (2037,'٢٠٢٢ - (روى عن أبي بكر) الصديق (رضي الله عنه أنه قال في خطبة خطبها) بعد أن استخلف (يا أيها الناس إنكم تقرؤون هذه الآية وتتأولونها على خلاف تأويلها يا أيها الذين آمنوا عليكم أنفسكم لا يضركم من ضل إذا اهتديتم وإني سمعت رسول الله - صلى الله عليه وسلم - يقول ما من قوم عملوا بالمعاصي وفيهم من يقدر أن ينكر عليهم فلم يفعل إلا يوشك أن يعمهم الله بعذاب من عنده).','1326','2036','0');
INSERT INTO [t38171] VALUES (2038,'٢٠٢٣ - (روي عن أبي ثعلبة الخشني رضي الله عنه) في اسمه أقوال وممن بايع تحت الشجرة منسوب إلى جده خشن بن لأي (أنه سأل رسول الله - صلى الله عليه وسلم - عن تفسير قوله تعالى لا يضركم من ضل إذا اهتديتم فقال يا أبا ثعلبة مر بالمعروف وانه عن المنكر فإذا رأيت شحا مطاعا وهوى متبعا ودنيا مؤثرة وإعجاب كل ذي رأي برأيه فعليك بنفسك ودع العوام إن من ورائكم فتنا كقطع الليل المظلم للمتمسك فيها بمثل الذي أنتم عليه أجر خمسين منكم قيل بل منهم يا رسول الله قال بل منكم لأنكم تجدون على الخير أعوانا ولا يجدون عليه أعوانا).','1327','2036','0');
INSERT INTO [t38171] VALUES (2039,'٢٠٢٤ - (قال - صلى الله عليه وسلم - لتأمرن بالمعروف وتنهون عن المنكر أو ليسلطن الله عليكم شراركم ثم يدعو خياركم فلا يستجاب لهم).','1328','2036','0');
INSERT INTO [t38171] VALUES (2040,'٢٠٢٥ - (قال - صلى الله عليه وسلم - يا أيها الناس إن الله تعالى يقول لتأمرن بالمعروف ولتنهون عن المنكر قبل أن تدعوا فلا يستجيب لكم)','1329','2036','0');
INSERT INTO [t38171] VALUES (2041,'٢٠٢٦ - (قال - صلى الله عليه وسلم - ما أعمال البر عند الجهاد في سبيل الله إلا كنفثة في بحر لجي وما جميع أعمال البر والجهاد في سبيل الله عند الأمر بالمعروف والنهي عن المنكر إلا كنفثة في بحر لجي)','1329','2036','0');
INSERT INTO [t38171] VALUES (2042,'٢٠٢٧ - (قال - صلى الله عليه وسلم - إن الله تعالى ليسأل العبد ما منعك إذ رأيت المنكر أن تنكره فإذا لقن الله العبد حجته قال رب وثقت بك وفرقت من الناس). أي خفت منهم.','1329','2036','0');
INSERT INTO [t38171] VALUES (2043,'٢٠٢٨ - (قال - صلى الله عليه وسلم - إياكم والجلوس على الطرقات قالوا) يا رسول الله (ما لنا بد إنما هي مجالسنا نتحدث فيها قال فإذا أبيتم إلا ذاك فاعطوا الطريق حقها قالوا وما حق الطريق قال غض البصر) أي عن المحارم (وكف الأذى ورد السلام وأمر بالمعروف ونهى عن المنكر).','1330','2036','0');
INSERT INTO [t38171] VALUES (2044,'٢٠٢٩ - (قال - صلى الله عليه وسلم - كلام ابن آدم كله عليه لا له إلا أمر بمعروف أو نهى عن منكر أو ذكر الله تعالى).','1330','2036','0');
INSERT INTO [t38171] VALUES (2045,'٢٠٣٠ - (وقال - صلى الله عليه وسلم - إن الله تعالى لا يعذب الخاصة بذنوب العامة حتى يرى المنكر بين أظهرهم وهم قادرون على أن ينكروه فلا ينكروه).','1330','2036','0');
INSERT INTO [t38171] VALUES (2046,'٢٠٣١ - (وروى أبو أمامة) عدي بن عجلان (الباهلي) رضي الله عنه (عن النبي - صلى الله عليه وسلم - أنه قال كيف أنتم إذا طغى نساؤكم وفسق شبابكم وتركتم جهادكم','1331','2036','0');
INSERT INTO [t38171] VALUES (2047,'٢٠٣٢ - (وعن عكرمة بن ابن عباس) رضي الله عنه قال (قال رسول الله - صلى الله عليه وسلم - لا تقفن عند رجل يقتل مظلوما) أي من غير وجه شرعي (فإن اللعنة تنزل على من حضر حين لم يدفعوا ولا تقفن عند','1331','2036','0');
INSERT INTO [t38171] VALUES (2048,'٢٠٣٣ - (قال) ابن عباس (وقال رسول الله - صلى الله عليه وسلم - لا ينبغي لامرئ شهد مقاما فيه حق إلا تكلم به فإنه لم يقدم أجله ولم يحرمه رزقا هو له).','1332','2036','0');
INSERT INTO [t38171] VALUES (2049,'٢٠٣٤ - (قال أبو هريرة) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - من حضر معصية فكرهها فكأنه غاب عنها ومن غاب عنها فأحبها فكأنه حضرها).','1332','2036','0');
INSERT INTO [t38171] VALUES (2050,'٢٠٣٥ - (قال ابن مسعود رضي الله عنه قال رسول الله - صلى الله عليه وسلم - ما بعث الله عز وجل نبيا إلا وله حواري) أي أنصار (فيمكث النبي بين أظهرهم ما شاء الله يعمل فيهم بكتاب الله وبأمره حتى إذا قبض','1332','2036','0');
INSERT INTO [t38171] VALUES (2051,'٢٠٣٦ - (قال ابن عباس) رضي الله عنه (قيل يا رسول الله أتهلك القرية وفيها الصالحون قال نعم قيل بم يا رسول الله قال بتهاونهم وسكوتهم على معاصي الله تعالى).','1333','2036','0');
INSERT INTO [t38171] VALUES (2052,'٢٠٣٧ - (وقال جابر بن عبد الله) الأنصاري رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - أوحى الله تبارك وتعالى إلى ملك أن اقلب مدينة كذا وكذا على أهلها قال) الراوي (فقال) الملك (يا رب إن فيهم عبدك فلانا لم يعصك طرفة عين قال اقلبها عليه وعليهم فإن وجهه لم يتغير في ساعة قط) وفي نسخة لم يتمعر.','1333','2036','0');
INSERT INTO [t38171] VALUES (2053,'٢٠٣٨ - (قالت عائشة رضي الله عنها قال رسول الله - صلى الله عليه وسلم - عذب أهل قرية فيها ثمانية عشر ألفا عملهم عمل الأنبياء قالوا يا رسول الله كيف قال لم يكونوا يغضبون لله عز وجل ولا يأمرون بالمعروف ولا ينهون عن المنكر)','1334','2036','0');
INSERT INTO [t38171] VALUES (2054,'٢٠٣٩ - (وقال أبو ذر) جندب بن جنادة (الغفاري) رضي الله عنه','1334','2036','0');
INSERT INTO [t38171] VALUES (2055,'٢٠٤٠ - (عن أبي عبيدة بن الجراح) رضي الله عنه وهو أحد العشرة المبشرة (قلت يا رسول الله أي الشهداء أكرم على الله تعالى قال رجل قام إلى وال جائر فأمره بالمعروف ونهاه عن المنكر فقتله فإن لم يقتله فإن القلم لا يجري عليه بعد ذلك وإن عاش ما عاش)','1335','2036','0');
INSERT INTO [t38171] VALUES (2056,'٢٠٤١ - (قال الحسن البصري) رحمه الله تعالى مرسلا (قال رسول الله - صلى الله عليه وسلم - أفضل شهداء أمتي رجل قام إلى إمام جائر فأمره بالمعروف ونهاه عن المنكر فقتله على ذلك فهو الشهيد منزلته في الجنة بين حمزة وجعفر)','1335','2036','0');
INSERT INTO [t38171] VALUES (2057,'٤٠٤٢ - (قال عمر بن الخطاب رضي الله عنه سمعت رسول الله - صلى الله عليه وسلم - يقول بئس القوم قوم لا يأمرون بالقسط وبئس القوم قوم لا يأمرون بالمعروف ولا ينهون عن المنكر)','1336','2036','0');
INSERT INTO [t38171] VALUES (2058,'٢٠٤٣ - (أفضل الدرجات كلمة حق عند إمام جائر كما ورد في الحديث).','1336','2036','0');
INSERT INTO [t38171] VALUES (2059,'٢٠٤٤ - (عن قدامة بن عبد الله) بن عمار بن معاوية العامري (الكلابي) يكني أبا عبد الله صحابي شهد حجة الوداع وله رواية قليلة وكان بنجد روى له الترمذي والنسائي وابن ماجه (قال رأيت','1336','2036','0');
INSERT INTO [t38171] VALUES (2060,'٢٠٤٥ - (قال رسول الله - صلى الله عليه وسلم - المؤمن للمؤمن كالبنيان يشد بعضه بعضا).','1337','2036','0');
INSERT INTO [t38171] VALUES (2061,'٢٠٤٦ - (ورد في حق الأب على الخصوص ما يوجب الاستثناء في العموم إذ لا خلاف) بين العلماء (في أن الجلاد ليس له أن يقتل أباه حدا) وفي نسخة بالزنا (ولا أن يباشر إقامة الحد عليه بل لا يباشر قتل أبيه الكافر بل لو قطع يده لم يلزمه قصاص ولم يكن له أن يؤذيه في مقابلة) كل ذلك لهيبة الأب (وقد ورد في ذلك أخبار وثبت بعضها بالإجماع).','1337','2036','0');
INSERT INTO [t38171] VALUES (2062,'٢٠٤٧ - (أما الرتبة الثالثة ففيه نظر من حيث أن الهجوم على أخذ الأموال) المغصوبة (من خزائنه وردها إلى الملاك وعلى تحليل الخيوط من ثيابه الحرير وكسر الخمور في بيته يكاد يفضي إلى خرق)','1337','2036','0');
INSERT INTO [t38171] VALUES (2063,'٢٠٤٨ - (قال الكيس من دان نفسه) أي أذلها واستعبدها يعني جعل نفسه مطيعة منقادة لأوامر ربها (وعمل لما بعد الموت) قبل نزوله ليصير على نور من ربه (والأحمق).','1338','2036','0');
INSERT INTO [t38171] VALUES (2064,'٢٠٤٩ - (قوله - صلى الله عليه وسلم - لا يأمر بالمعروف ولا ينهى عن المنكر إلا رفيق فيما يأمر به رفيق فيما ينهى عنه حليم فيما يأمر به حليم فيما ينهي عنه فقيه فيما يأمر به فقيه فيما ينهي عنه)','1339','2036','0');
INSERT INTO [t38171] VALUES (2065,'٢٠٥٠ - وهذا يدل على أنه لا يشترط أن يكون فقيها مطلقا بل فيما يأمر به وينهى عنه وكذا الحلم)','1340','2036','0');
INSERT INTO [t38171] VALUES (2066,'٢٠٥١ - (قد روى عن أنس) بن مالك رضي الله عنه (قال قلنا يا رسول الله لا نأمر بالمعروف حتى نعمل به كله ولا ننهي عن المنكر حتى نجتنبه كله فقال - صلى الله عليه وسلم - بل مروا بالمعروف وإن لم تعملوا به كله وانهوا عن المنكر وإن لم تجتنبوه كله).','1340','2036','0');
INSERT INTO [t38171] VALUES (2067,'٢٠٥٣ - (قال رسول الله - صلى الله عليه وسلم - خير الشهداء) أي من هذه الأمة (حمزة بن عبد المطلب ثم رجل قام إلى إمام) جائر (فأمر ونهاه في ذات الله تعالى فقتله على ذلك)','1341','2036','0');
INSERT INTO [t38171] VALUES (2068,'٢٠٥٤ - (وقال - صلى الله عليه وسلم - أفضل الجهاد كلمة حق عند سلطان جائر)','1342','2036','0');
INSERT INTO [t38171] VALUES (2069,'٢٠٥٥ - (ووصف النبي - صلى الله عليه وسلم - عمر بن الخطاب رضي الله عنه فقال قرن من حديد لا تأخذه في الله لومة لائم وتركه قوله الحق ماله','1342','2036','0');
INSERT INTO [t38171] VALUES (2070,'٢٠٥٦ - (قام أبو بكر الصديق رضي الله عنه دونه يقول وهو يبكي ويلكم أتقتلون رجلا أن يقول ربي الله ثم انصرفوا عنه فإن ذلك لأشد ما رأيت قريشا بلغت منه قط)','1343','2036','0');
INSERT INTO [t38171] VALUES (2071,'٢٠٥٧ - (روي أن معاوية) بن أبي سفيان (رضي الله عنه حبس العطاء) عن أهله مرة وكان على المنبر (فقام إليه أبو مسلم الخولاني) عبد الله بن ثوب بن خيار تابعي من أهله الشام نزلها في أيام معاوية وكان صاحب كرامات (فقال له يا معاوية إنه) أي المال (ليس من','1343','2036','0');
INSERT INTO [t38171] VALUES (2072,'٢٠٥٨ - (روى عن ضبة بن محصن العنزي) بسكون النون البصري ذكره ابن حبان في كتاب الثقات روى له مسلم وأبو داود والترمذي حديثا واحدا (قال كان علينا أبو موسى) عبد الله بن قيس (الأشعري) رضي الله عنه (أمير بالبصرة) ولاه عمر بن الخطاب رضي الله عنه (وكان إذا خطبنا حمد الله وأثنى عليه وصلى على النبي - صلى الله عليه وسلم - وأنشأ) بعد ذلك (يدعو لعمر) بن الخطاب رضي الله عنه (قال فغاظني) أو أغضبني (ذلك فقمت إليه فقلت له أين أنت عن صاحبه) يعني أبا بكر رضي الله عنه (تفضله عليه فصنع ذلك جمعا ثم كتب إلى عمر يشكوني يقول) في شكواه (إن ضبة بن محصن العنزى يتعرض لي في خطبتي فكتب إليه عمر) رضي الله عنه (إن أشخص به إلي) أي وجهه إلي (فأشخصني إليه فقدمت فدققت عليه','1344','2036','0');
INSERT INTO [t38171] VALUES (2073,'٢٠٥٩ - (قال رسول الله - صلى الله عليه وسلم - من استرعى رعية فلم يحطها بالنصيحة حرم الله عليه الجنة)','1347','2036','0');
INSERT INTO [t38171] VALUES (2074,'٢٠٦٠ - عن عطية بن بشر قال قال رسول الله - صلى الله عليه وسلم - أيما عبد جاءته موعظة من الله في دينه فإنها نعمة من الله سيقت إليه فإن قبلها بشكر وإلا كانت حجة من الله عليه ليزداد بها إنما ويزداد الله بها سخطا عليه','1347','2036','0');
INSERT INTO [t38171] VALUES (2075,'٢٠٦١ - (قال رسول الله - صلى الله عليه وسلم - أيما وال بات غاشا لرعيته حرم الله عليه الجنة)','1347','2036','0');
INSERT INTO [t38171] VALUES (2076,'٢٠٦٢ - (عن عروة بن رويم) اللخمي الأزدي أبو القاسم روى عن أبي إدريس الخولاني وعدة وله مقاطيع ويرسل كثيرا وعنه الأوزاعي وسعيد بن عبد العزيز وخلق وثق وفي موته أقوال الصحيح أنه سنة ١٣٥ روى له أبو داود والنسائي وابن ماجه (قال كانت بيد رسول الله - صلى الله عليه وسلم - جريدة يستاك بها ويروع بها)','1348','2036','0');
INSERT INTO [t38171] VALUES (2077,'٢٠٦٤ - (عن حبيب بن مسلمة) بن مالك بن وهب القرشي الفهري المكي مختلف في صحبته نزل الشام والراجح ثبوت صحبته لكنه كان صغيرا وله ذكر في الصحيح في حديث ابن عمرو مع معاوية روي عن النبي - صلى الله عليه وسلم - وأبي ذر وعنه زياد بن جارية وابن أبي مليكة قيل شهد اليرموك أميرا روى له أبو داود وابن ماجه مات بأرمينية أميرا عليها لمعاوية سنة ١٤٢ (إن رسول الله - صلى الله عليه وسلم - دعا إلى القصاص في خدشة خدش) وفي نسخة في خدشة خدشه (أعرابيا لم يتعمده) أي لم يقصد خدشه عمدا (فأتاه جبريل عليه السلام فقال يا محمد إن الله لم يبعثك جبارا ولا متكبرا فدعا النبي - صلى الله عليه وسلم - الأعرابي فقال اقتص مني فقال','1348','2036','0');
INSERT INTO [t38171] VALUES (2078,'٢٠٦٥ - (قال فيها رسول الله - صلى الله عليه وسلم - لقيد قوس أحدكم من الجنة خير من الدنيا وما فيها).','1349','2036','0');
INSERT INTO [t38171] VALUES (2079,'٢٠٦٦ - (عن عبد الرحمن بن عمرة الأنصاري).','1349','2036','0');
INSERT INTO [t38171] VALUES (2080,'٢٠٦٧ - (سأل العباس) بن عبد المطلب رضي الله عنه (النبي - صلى الله عليه وسلم - إمارته على مكة والطائف أو اليمن فقال له النبي - صلى الله عليه وسلم - يا عباس يا عم النبي نفس تنجيها خير من إمارة لا تحصيها)','1350','2036','0');
INSERT INTO [t38171] VALUES (2081,'٢٠٦٨ - (إذ أوحى الله إليه وأنذر عشيرتك الأقربين فقال) - صلى الله عليه وسلم - (يا عباس ويا صفية عمة النبي ويا فاطمة ابنة محمد إني لست أغنى عنكم من الله شيئا لي عملي ولكم عملكم).','1351','2036','0');
INSERT INTO [t38171] VALUES (2082,'٢٠٦٩ - (قال رسول الله - صلى الله عليه وسلم - شر الرعاء رعاء الحطمة فهو الهالك وحده وأمير ارتع نفسه وعماله فهلكوا جميعا)','1352','2036','0');
INSERT INTO [t38171] VALUES (2083,'٢٠٧٠ - (قال جبريل عليه السلام أتى النبي - صلى الله عليه وسلم - فقال أتيتك حين أمر الله بمنافخ النار).','1352','2036','0');
INSERT INTO [t38171] VALUES (2084,'٢٠٧١ - قال العراقي: قصة الأوزاعي هذه مع المنصور وموعظته له وفيه عشرة أحاديث مرفوعة وهي بجملتها رواها ابن أبي الدنيا في مواعظ الخلفاء ورويناها في مشيخة الخفاف ومشيخة ابن طبر زد وفي إسنادها أحمد بن عبيد بن ناصح قال ابن عدي يحدث بمناكير وهو عندي من أهل الصدق اهـ.','1353','2036','0');
INSERT INTO [t38171] VALUES (2085,'٢٠٧٢ - وقد أورد الحافظ ابن حجر في الإصابة هذه القصة في ترجمة الخضر عليه السلام مختصرة جدا','1353','2036','0');
INSERT INTO [t38171] VALUES (2086,'كتاب آداب المعيشة وأخلاق النبوة','1355','1202','0');
INSERT INTO [t38171] VALUES (2087,'٢٠٧٣ - (أدب نبيه - صلى الله عليه وسلم -)','1356','2086','0');
INSERT INTO [t38171] VALUES (2088,'٢٠٧٤ - كان - صلى الله عليه وسلم - (يقول اللهم جنبني منكرات الأخلاق).','1356','2086','0');
INSERT INTO [t38171] VALUES (2089,'٢٠٧٥ - (كان خلق رسول الله - صلى الله عليه وسلم - القرآن)','1357','2086','0');
INSERT INTO [t38171] VALUES (2090,'٢٠٧٦ - (يقول كيف يفلح قوم خضبوا وجه نبيهم بالدم وهو يدعوهم إلى ربهم فأنزل الله تعالى ليس لك من الأمر شيء أو يتوب عليهم أو يعذبهم فإنهم ظالمون).','1357','2086','0');
INSERT INTO [t38171] VALUES (2091,'٢٠٧٧ - (قال) - صلى الله عليه وسلم - (بعثت لأتمم مكارم الأخلاق).','1358','2086','0');
INSERT INTO [t38171] VALUES (2092,'٢٠٧٨ - (أن الله يحب مكارم الأخلاق)','1358','2086','0');
INSERT INTO [t38171] VALUES (2093,'٢٠٧٩ - (قال والذي نفسي بيده لا يدخل الجنة إلا حسن الأخلاق).','1359','2086','0');
INSERT INTO [t38171] VALUES (2094,'٢٠٨٠ - (عن معاذ بن جبل) رضي الله عنه (عن النبي - صلى الله عليه وسلم - قال إن الله حف الإسلام بمكارم الأخلاق ومحاسن الأعمال ومن ذلك) أي من محاسن الأعمال (حسن المعاشرة)','1359','2086','0');
INSERT INTO [t38171] VALUES (2095,'٢٠٨١ - (قال أنس) بن مالك (رضي الله عنه فلم يدع) - صلى الله عليه وسلم - (نصيحة جميلة إلا وقد دعانا إليها وأمرنا بها ولم يدع غما أو قال عيبا ولا شيئا إلا حذرناه ونهانا عنه ويكفي من ذلك كله هذه الآية إن الله يأمر بالعدل والإحسان الآية).','1360','2086','0');
INSERT INTO [t38171] VALUES (2096,'٢٠٨٣ - (كان - صلى الله عليه وسلم - أحلم الناس).','1362','2086','0');
INSERT INTO [t38171] VALUES (2097,'٢٠٨٤ - كان - صلى الله عليه وسلم - (أشجع الناس).','1363','2086','0');
INSERT INTO [t38171] VALUES (2098,'٢٠٨٥ - كان - صلى الله عليه وسلم - (أعدل الناس)','1364','2086','0');
INSERT INTO [t38171] VALUES (2099,'٢٠٨٦ - كان - صلى الله عليه وسلم - (أعف الناس) أي أكثرهم عفة وهي بالكسر حصول حالة للنفس يمتنع بها عن غلبة الشمهوة ولذلك قال (لم تمس يده قط يد امرأة لا يملك رقها أو عصمة نكاحها أو تكون ذات محرم منه).','1364','2086','0');
INSERT INTO [t38171] VALUES (2100,'٢٠٨٧ - كان - صلى الله عليه وسلم - (أسخى الناس) أي أكثرهم سخاء.','1364','2086','0');
INSERT INTO [t38171] VALUES (2101,'٢٠٨٨ - (لا يبيت عنده دينار ولا درهم قط فإن فضل) أي بقي شيء (ولم يجد من يعطيه وفجأه الليل) أي أتاه فجأة (لم يأو إلى منزله حتى يتبرأ منه إلى من يحتاج إليه).','1365','2086','0');
INSERT INTO [t38171] VALUES (2102,'٢٠٨٩ - (لم يأخذ مما آتاه الله إلا قوت عامه فقط من أيسر ما يجد من التمر والشعير ويضع باقي ذلك في سبيل الله).','1365','2086','0');
INSERT INTO [t38171] VALUES (2103,'٢٠٩٠ - (لا يسئل شيئا إلا أعطاه).','1366','2086','0');
INSERT INTO [t38171] VALUES (2104,'٢٠٩١ - (يعود على قوت عامه) الذي ادخره لعياله (فيؤثر منه) على نفسه وعياله (حتى لربما احتاج قبل انقضاء العام إن لم يأته شيء).','1366','2086','0');
INSERT INTO [t38171] VALUES (2105,'٢٠٩٢ - (كان) - صلى الله عليه وسلم - (يخصف النعل) أي يصلحها بترقيع وخرز (ويرقع الثوب) أي يضع لما وهي منه رقعة أخرى يخيطها به (ويخدم في مهنة أهله)','1367','2086','0');
INSERT INTO [t38171] VALUES (2106,'٢٠٩٣ - (ويقطع اللحم معهن).','1367','2086','0');
INSERT INTO [t38171] VALUES (2107,'٢٠٩٤ - (وكان) - صلى الله عليه وسلم - من أشد الناس حياء لا يثبت بصره في وجه أحد).','1368','2086','0');
INSERT INTO [t38171] VALUES (2108,'٢٠٩٥ - كان - صلى الله عليه وسلم - (يجيب دعوة العبد والحر).','1368','2086','0');
INSERT INTO [t38171] VALUES (2109,'٢٠٩٦ - (كان - صلى الله عليه وسلم - يقبل الهدية ولو أنها جرعة لبن أو فخذ أرنب ويكافئ عليها).','1368','2086','0');
INSERT INTO [t38171] VALUES (2110,'٢٠٩٧ - كان - صلى الله عليه وسلم - (يأكلها) أي الهدية (ولا يأكل الصدقة)','1369','2086','0');
INSERT INTO [t38171] VALUES (2111,'٢٠٩٨ - كان - صلى الله عليه وسلم - (لا يستكبر عن إجابة الأمة والمسكين)','1369','2086','0');
INSERT INTO [t38171] VALUES (2112,'٢٠٩٩ - كان - صلى الله عليه وسلم - (يغضب لربه عز وجل ولا يغضب لنفسه).','1369','2086','0');
INSERT INTO [t38171] VALUES (2113,'٢١٠٠ - (ينفذ الحق وإن عاد ذلك بالضرر عليه وعلى أصحابه)','1370','2086','0');
INSERT INTO [t38171] VALUES (2114,'٢١٠١ - (عرض عليه) - صلى الله عليه وسلم - (الانتصار بالمشركين على المشركين وهو في قلة وحاجة إلى إنسان واحد يزيده في عدد من معه فأبى وقال إنا لا ننتصر بمشرك)','1370','2086','0');
INSERT INTO [t38171] VALUES (2115,'٢١٠٢ - (وجد من فضلاء أصحابه وخيارهم قتيلا بين اليهود فلم يحف) أي لم يجر (عليهم ولا زاد على مر الحق) أي لم يتجاوز عن الحق الذي هو مر (بل وداه) أي القتيل من عنده (بمائة ناقة وإن بأصحابه لحاجة إلى بعير واحد يتقوون به).','1370','2086','0');
INSERT INTO [t38171] VALUES (2116,'٢١٠٣ - (كان) - صلى الله عليه وسلم - (يعصب الحجر على بطنه من الجوع)','1371','2086','0');
INSERT INTO [t38171] VALUES (2117,'٢١٠٤ - (يأكل ما حضر) لديه (ولا يرد ما وجد)','1372','2086','0');
INSERT INTO [t38171] VALUES (2118,'٢١٠٥ - (ولا يتورع من مطعم حلال)','1372','2086','0');
INSERT INTO [t38171] VALUES (2119,'٢١٠٦ - (وإن وجد تمرا دون خبز أكله)','1372','2086','0');
INSERT INTO [t38171] VALUES (2120,'٢١٠٧ - (وإن وجد شواء أكله)','1372','2086','0');
INSERT INTO [t38171] VALUES (2121,'٢١٠٨ - (وإن وجد خبز بر أو شعير أكله)','1372','2086','0');
INSERT INTO [t38171] VALUES (2122,'٢١٠٩ - (وإن وجد حلوا أو عسلا أكله)','1372','2086','0');
INSERT INTO [t38171] VALUES (2123,'٢١١٠ - (وإن وجد لبنا دون خبز اكتفى به)','1373','2086','0');
INSERT INTO [t38171] VALUES (2124,'٢١١١ - (وإن وجد بطيخا أو رطبا أكله)','1373','2086','0');
INSERT INTO [t38171] VALUES (2125,'٢١١٢ - كان (منديله باطن قدمه).','1373','2086','0');
INSERT INTO [t38171] VALUES (2126,'٢١١٣ - كان (يجيب الوليمة)','1373','2086','0');
INSERT INTO [t38171] VALUES (2127,'٢١١٤ - (يعود المرضى)','1374','2086','0');
INSERT INTO [t38171] VALUES (2128,'٢١١٥ - (يشهد الجنائز)','1374','2086','0');
INSERT INTO [t38171] VALUES (2129,'٢١١٦ - (يمشي وحده بين أعدائه بلا حارس).','1374','2086','0');
INSERT INTO [t38171] VALUES (2130,'٢١١٧ - كان أشد الناس تواضعا','1374','2086','0');
INSERT INTO [t38171] VALUES (2131,'٢١١٨ - (أسكنهم) أي أكثرهم سكونا (في غير كبر).','1375','2086','0');
INSERT INTO [t38171] VALUES (2132,'٢١١٩ - (وأبلغهم) أي أكثرهم بلاغة في الكلام (من غير تطويل).','1376','2086','0');
INSERT INTO [t38171] VALUES (2133,'٢١٢٠ - (وأحسنهم بشرا).','1376','2086','0');
INSERT INTO [t38171] VALUES (2134,'٢١٢١ - (لا يهوله شيء من أمور الدنيا) يقال هاله الشيء إذا راعه وأعجبه.','1376','2086','0');
INSERT INTO [t38171] VALUES (2135,'٢١٢٢ - (يلبس ما وجد) من غير قيد (فمرة) يلبس (شملة ومرة برد حبرة يمانية ومرة جبة صوف ما وجد من المباح لبس).','1377','2086','0');
INSERT INTO [t38171] VALUES (2136,'٢١٢٣ - (وخاتمه فضة)','1377','2086','0');
INSERT INTO [t38171] VALUES (2137,'٢١٢٤ - (يردف خلفه عبده)','1377','2086','0');
INSERT INTO [t38171] VALUES (2138,'٢١٢٥ - (يركب ما أمكنه مرة فرسا)','1378','2086','0');
INSERT INTO [t38171] VALUES (2139,'٢١٢٦ - (يحب الطيب)','1378','2086','0');
INSERT INTO [t38171] VALUES (2140,'٢١٢٧ - (يجالس الفقراء)','1379','2086','0');
INSERT INTO [t38171] VALUES (2141,'٢١٢٨ - (يؤاكل المساكين)','1379','2086','0');
INSERT INTO [t38171] VALUES (2142,'٢١٢٩ - (يكرم أهل الفضل في أخلاقهم ويتألف أهل الشرف بالبر لهم)','1379','2086','0');
INSERT INTO [t38171] VALUES (2143,'٢١٣٠ - (يصل ذوي رحمه من غير أن يؤثرهم على من هو أفضل منهم)','1380','2086','0');
INSERT INTO [t38171] VALUES (2144,'٢١٣١ - كان (لا يجفو على أحد)','1380','2086','0');
INSERT INTO [t38171] VALUES (2145,'٢١٣٢ - (ويقبل معذرة المعتذر إليه)','1380','2086','0');
INSERT INTO [t38171] VALUES (2146,'٢١٣٣ - (يمزح) أحيانا (ولا يقول إلا حقا)','1381','2086','0');
INSERT INTO [t38171] VALUES (2147,'٢١٣٤ - (يضحك من غير قهقهة)','1381','2086','0');
INSERT INTO [t38171] VALUES (2148,'٢١٣٥ - (يرى اللعب المباح فلا ينكره)','1381','2086','0');
INSERT INTO [t38171] VALUES (2149,'٢١٣٦ - (يسابق أهله)','1381','2086','0');
INSERT INTO [t38171] VALUES (2150,'٢١٣٧ - (ترفع الأصوات عليه)','1382','2086','0');
INSERT INTO [t38171] VALUES (2151,'٢١٣٨ - (كان له لقاح وغنم يتقوت هو وأهله من ألبانها)','1382','2086','0');
INSERT INTO [t38171] VALUES (2152,'٢١٣٩ - (كان له عبيد وإماء لا يرتفع عليهم في مأكل ولا ملبس)','1383','2086','0');
INSERT INTO [t38171] VALUES (2153,'٢١٤٠ - (كان لا يمضي له وقت في غير عمل لله تعالى أو فيما لا بد له منه لصلاح نفسه)','1383','2086','0');
INSERT INTO [t38171] VALUES (2154,'٢١٤١ - (لا يحقر مسكينا لفقره وزمانته ولا يهاب ملكا لملكه يدعو هذا وهذا إلى الله دعاء واحدا)','1383','2086','0');
INSERT INTO [t38171] VALUES (2155,'٢١٤٢ - (وما شتم رسول الله - صلى الله عليه وسلم - أحدا من المؤمنين بشتيمة إلا جعل لها كفارة ورحمة)','1383','2086','0');
INSERT INTO [t38171] VALUES (2156,'٢١٤٣ - (وما لعن امرأة قط ولا خادما بلعنة).','1384','2086','0');
INSERT INTO [t38171] VALUES (2157,'٢١٤٤ - (وقيل له وهو في القتال لو لعنتهم يا رسول الله فقال إنما بعثت رحمة ولم أبعث لعانا)','1384','2086','0');
INSERT INTO [t38171] VALUES (2158,'٢١٤٥ - (وكان إذا سئل أن يدعو على أحد مسلم أو كافر عام أو خاص عدل عن الدعاء عليه ودعا له)','1384','2086','0');
INSERT INTO [t38171] VALUES (2159,'٢١٤٦ - (ما ضرب بيده أحدا قط إلا أن يضرب بها في سبيل الله وما انتقم من شيء صنع إليه قط إلا أن تنتهك حرمة الله)','1384','2086','0');
INSERT INTO [t38171] VALUES (2160,'٢١٤٧ - (ما خير بين أمرين قط إلا اختار أيسرهما إلا أن يكون فيه إثم أو قطيعة رحم فيكون أبعد الناس من ذلك)','1385','2086','0');
INSERT INTO [t38171] VALUES (2161,'٢١٤٨ - (ما كان يأتيه أحد حر أو عبد أو أمة إلا قام معه في حاجته)','1385','2086','0');
INSERT INTO [t38171] VALUES (2162,'٢١٤٩ - (وقال أنس) خادمه رضي الله عنه (والذي بعثه بالحق ما قال لي في شيء قط كرهه لم فعلته ولا لامني أحد من أهله إلا قال دعوه إنما كان هذا بكتاب وقدر)','1385','2086','0');
INSERT INTO [t38171] VALUES (2163,'٢١٥٠ - (وما عاب رسول الله - صلى الله عليه وسلم - مضجعا إن فرشوا له اضطجع وإن لم يفرش له اضطجع على الأرض).','1386','2086','0');
INSERT INTO [t38171] VALUES (2164,'٢١٥١ - أخرج البيهقي في الدلائل من حديث فليح عن هلال بن علي عن عطاء بن يسار قال','1386','2086','0');
INSERT INTO [t38171] VALUES (2165,'٢١٥٢ - (وكان من خلقه) - صلى الله عليه وسلم - (أن يبدأ من لقيه بالسلام)','1387','2086','0');
INSERT INTO [t38171] VALUES (2166,'٢١٥٣ - (ومن قاومه) وفي بعض النسخ فاوضه (لحاجة صابره حتى يكون هو المنصرف)','1387','2086','0');
INSERT INTO [t38171] VALUES (2167,'٢١٥٤ - (وما أخذ أحد بيده فيرسل يده حتى يرسلها الآخذ)','1388','2086','0');
INSERT INTO [t38171] VALUES (2168,'٢١٥٥ - (وكان) - صلى الله عليه وسلم - (إذا لقى أحد من أصحابه بدأه بالمصافحة ثم أخذ يده فشابكه ثم شد قبضته)','1388','2086','0');
INSERT INTO [t38171] VALUES (2169,'٢١٥٦ - (كان) - صلى الله عليه وسلم - لا يقوم ولا يجلس إلا على ذكر الله تعالى)','1389','2086','0');
INSERT INTO [t38171] VALUES (2170,'٢١٥٧ - (كان لا يجلس إليه أحد وهو يصلي إلا خفف صلاته وأقبل عليه فقال ألك حاجة فإذا فرغ من حاجته عاد إلى صلاته).','1389','2086','0');
INSERT INTO [t38171] VALUES (2171,'٢١٥٨ - (وكان) - صلى الله عليه وسلم - (أكثر جلوسه أن ينصب ساقيه جميعا ويمسك بيديه عليهما شبه الحبوة)','1389','2086','0');
INSERT INTO [t38171] VALUES (2172,'٢١٥٩ - (لم يكن يعرف مجلسه من مجالس أصحابه)','1390','2086','0');
INSERT INTO [t38171] VALUES (2173,'٢١٦٠ - (كان حيث انتهى به المجلس جلس)','1390','2086','0');
INSERT INTO [t38171] VALUES (2174,'٢١٦١ - (ما رؤي) - صلى الله عليه وسلم - (قط مادا رجليه بين أصحابه حتى يضيق بهما على أحد إلا أن يكون المكان واسعا لا ضيق فيه).','1390','2086','0');
INSERT INTO [t38171] VALUES (2175,'٢١٦٢ - (وكان) - صلى الله عليه وسلم - (أكثر ما يجلس مستقبل القبلة)','1390','2086','0');
INSERT INTO [t38171] VALUES (2176,'٢١٦٣ - (كان) - صلى الله عليه وسلم - (يكرم من يدخل عليه حتى ربما بسط ثوبه لمن ليست بينه وبينه قرابة ولا رضاع يجلسه عليه)','1391','2086','0');
INSERT INTO [t38171] VALUES (2177,'٢١٦٤ - (كان) - صلى الله عليه وسلم - (أرأف الناس بالناس وخير الناس للناس وأنفع الناس للناس)','1391','2086','0');
INSERT INTO [t38171] VALUES (2178,'٢١٦٥ - (وكان) - صلى الله عليه وسلم - (إذا قام من مجلسه قال سبحانك اللهم وبحمدك أشهد أن لا إله إلا أنت أستغفرك وأتوب إليك ثم يقول علمنيهن جبريل عليه السلام)','1391','2086','0');
INSERT INTO [t38171] VALUES (2179,'٢١٦٦ - (كان - صلى الله عليه وسلم - أفصح الناس منطقا وأحلاهم كلاما ويقول أئا أفصح العرب)','1392','2086','0');
INSERT INTO [t38171] VALUES (2180,'٢١٦٧ - (إن أهل الجنة يتكلمون فيها بلغة محمد - صلى الله عليه وسلم -)','1392','2086','0');
INSERT INTO [t38171] VALUES (2181,'٢١٦٨ - (كان يتكلم بجوامع الكلم لا فضول ولا تقصير يتبع بعضه بعضا بين كلامه توقف يحفظه سامعه ويعيه).','1393','2086','0');
INSERT INTO [t38171] VALUES (2182,'٢١٦٩ - (كان) - صلى الله عليه وسلم - (جهير الصوت).','1394','2086','0');
INSERT INTO [t38171] VALUES (2183,'٢١٧٠ - (جاء أعرابي) أي من سكان البادية (يوما وهو - صلى الله عليه وسلم - متغير) لونه (ينكره أصحابه فأراد أن يسأله) في شيء (فقالوا لا تفعل يا أعرابي فإنا ننكر لونه فقال دعوني فوالذي بعثه بالحق نبيا لا أدعه حتى يتبسم فقال يا رسول الله بلغنا أن المسيح يعني الدجال يأتي الناس بالثريد وقد هلكوا جوعا أفترى لي بأبي وأمي أن أكف عن ثريده تعففا وتنزها حتى أهلك هزالا أم أضرب) اليد (في ثريده حتى إذا تضلعت شبعا) أي امتلأت (آمنت بالله) وحده (وكفرت به) يعني الدجال (قالوا فضحك رسول الله - صلى الله عليه وسلم - حتى','1394','2086','0');
INSERT INTO [t38171] VALUES (2184,'٢١٧١ - (يقول اللهم أرني الحق حقا فأتبعه وأرني المنكر منكرا وارزقني اجتنابه وأعذني من أن يشتبه علي فاتبع هواي بغير هدى منك واجعل هواي تبعا لطاعتك وخذ رضا نفسك من نفسي في عافية واهدني لما اختلف فيه من الحق بإذنك إنك تهدي من تشاء إلى صراط مستقيم).','1395','2086','0');
INSERT INTO [t38171] VALUES (2185,'٢١٧٢ - (كان أحب الطعام إليه ما كان على ضفف والضفف) محركة (ما كثرت عليه الأيدي).','1395','2086','0');
INSERT INTO [t38171] VALUES (2186,'٢١٧٣ - (وكان) - صلى الله عليه وسلم - (إذا وضعت المائدة قال بسم الله اللهم اجعلها نعمة مشكورة تصل بها نعمة الجنة).','1396','2086','0');
INSERT INTO [t38171] VALUES (2187,'٢١٧٤ - (كان) - صلى الله عليه وسلم - (كثيرا إذا جلس يأكل يجمع بين ركبتيه وبين قدميه كما يجمع المصلى) في حال صلاته (إلا أن الركبة تكون فوق الركبة والقدم فوق القدم ويقول إنما أنا عبد آكل كما يأكل العبد وأجلس كما يجلس العبد).','1396','2086','0');
INSERT INTO [t38171] VALUES (2188,'٢١٧٥ - (كان) - صلى الله عليه وسلم - (لا يأكل) الطعام (الحار ويقول إنه غير ذي بركة وإن الله تعالى لم يطعمنا نارا فأبردوه).','1397','2086','0');
INSERT INTO [t38171] VALUES (2189,'٢١٧٦ - (كان) - صلى الله عليه وسلم - (يأكل مما يليه).','1398','2086','0');
INSERT INTO [t38171] VALUES (2190,'٢١٧٧ - (يأكل بأصابعه الثلاث)','1399','2086','0');
INSERT INTO [t38171] VALUES (2191,'٢١٧٨ - يروى أنه - صلى الله عليه وسلم - (جاءه عثمان بن عفان) رضي الله عنه (بفالوذج) وهو اسم أعجمي لنوع من الحلواء (فأكل منه وقال ما هذا يا أبا عبد الله) قال ابن عبد البر يكنى أبا عبد اللهو أبا عمرو كنيتان مشهورتان وأبو عمرو أشهرهما قيل أنه ولدت له رقية بنت رسول الله - صلى الله عليه وسلم - ابنا','1399','2086','0');
INSERT INTO [t38171] VALUES (2192,'٢١٧٩ - (كان) - صلى الله عليه وسلم - (يأكل خبز الشعير غير منخول)','1401','2086','0');
INSERT INTO [t38171] VALUES (2193,'٢١٨٠ - (كان) - صلى الله عليه وسلم - (يأكل القثاء بالرطب).','1401','2086','0');
INSERT INTO [t38171] VALUES (2194,'٢١٨١ - كان - صلى الله عليه وسلم - يأكل القثاء (بالملح)','1401','2086','0');
INSERT INTO [t38171] VALUES (2195,'٢١٨٢ - (كان) - صلى الله عليه وسلم - (أحب الفواكه الرطبة إليه البطيخ والعنب)','1401','2086','0');
INSERT INTO [t38171] VALUES (2196,'٢١٨٣ - (كان) - صلى الله عليه وسلم - (يأكل البطيخ بالخبز).','1402','2086','0');
INSERT INTO [t38171] VALUES (2197,'٢١٨٤ - يأكل تارة (بالسكر).','1402','2086','0');
INSERT INTO [t38171] VALUES (2198,'٢١٨٥ - (ربما أكله بالرطب).','1402','2086','0');
INSERT INTO [t38171] VALUES (2199,'٢١٨٦ - (ويستعين باليدين جميعا).','1403','2086','0');
INSERT INTO [t38171] VALUES (2200,'٢١٨٧ - (وأكل) - صلى الله عليه وسلم - (يوما رطبا كان في يمينه وكان يحفظ النوى في يساره فمرت به شاة فأشار إليها بالنوى فجعلت تأكل من كفه اليسرى وهو يأكل بيمينه حتى فرغ وانصرفت الشاة).','1403','2086','0');
INSERT INTO [t38171] VALUES (2201,'٢١٨٨ - (وربما أكل العنب خرطا)','1404','2086','0');
INSERT INTO [t38171] VALUES (2202,'٢١٨٩ - (كان أكثر طعامه) - صلى الله عليه وسلم - (التمر والماء).','1404','2086','0');
INSERT INTO [t38171] VALUES (2203,'٢١٩٠ - (كان) - صلى الله عليه وسلم - (يتمجع اللبن بالتمر ويسميهما الأطيبين).','1404','2086','0');
INSERT INTO [t38171] VALUES (2204,'٢١٩١ - (كان أحب الطعام إليه) - صلى الله عليه وسلم - (اللحم ويقول يزيد في السمع وهو سيد الطعام في الدنيا والآخرة ولو سألت ربي أن يطعمنيه كل يوم لفعل).','1405','2086','0');
INSERT INTO [t38171] VALUES (2205,'٢١٩٢ - (كان) - صلى الله عليه وسلم - (يأكل الثريد باللحم والقرع)','1405','2086','0');
INSERT INTO [t38171] VALUES (2206,'٢١٩٣ - (كان) - صلى الله عليه وسلم - (يحب القرع) وهو الدبا٠ء (ويقول إنها شجرة أخي يونس عليه السلام).','1405','2086','0');
INSERT INTO [t38171] VALUES (2207,'٢١٩٤ - (قالت عائشة رضي الله عنها كان) - صلى الله عليه وسلم - (يقول يا عائشة إذا طبختم قدرا فأكثروا فيها من الدباء فإنه يشد قلب الحزين).','1406','2086','0');
INSERT INTO [t38171] VALUES (2208,'٢١٩٥ - (كان) - صلى الله عليه وسلم - (يأكل لحم الطير الذي يصاد).','1406','2086','0');
INSERT INTO [t38171] VALUES (2209,'٢١٩٦ - (كان لا يتبعه ولا يصيده ويحب أن يصاد له فيؤتي به فيأكله).','1406','2086','0');
INSERT INTO [t38171] VALUES (2210,'٢١٩٧ - (كان) - صلى الله عليه وسلم - (إذا أكل اللحم لم يطأطئ رأسه إليه ورفعه إلى فيه رفعا ثم ينتهسه انتهاسا)','1407','2086','0');
INSERT INTO [t38171] VALUES (2211,'٢١٩٨ - (كان) - صلى الله عليه وسلم - (يأكل الخبز والسمن)','1407','2086','0');
INSERT INTO [t38171] VALUES (2212,'٢١٩٩ - (كان) - صلى الله عليه وسلم - (يحب من الشاة الذراع والكتف)','1407','2086','0');
INSERT INTO [t38171] VALUES (2213,'٢٢٠٠ - (من القدير) أي المطبوخ في القدر (الدباء)','1408','2086','0');
INSERT INTO [t38171] VALUES (2214,'٢٢٠١ - (من الصباغ الخل)','1408','2086','0');
INSERT INTO [t38171] VALUES (2215,'٢٢٠٢ - (ومن التمر العجوة)','1408','2086','0');
INSERT INTO [t38171] VALUES (2216,'٢٢٠٣ - (ودعا) - صلى الله عليه وسلم - (في العجوة بالبركة وقال هي من الجنة) (وشفاء من السم والسحر).','1408','2086','0');
INSERT INTO [t38171] VALUES (2217,'٢٢٠٤ - (كان) - صلى الله عليه وسلم - (يحب من البقول الهندباء والباذروج) هو الريحان القرنفلي وهو الضميرات (والبقلة الحمقاء التي يقال لها الرجلة).','1409','2086','0');
INSERT INTO [t38171] VALUES (2218,'٢٢٠٥ - (كان) - صلى الله عليه وسلم - (يكره الكليتين)','1409','2086','0');
INSERT INTO [t38171] VALUES (2219,'٢٢٠٦ - (ولا يأكل من الشاء)','1410','2086','0');
INSERT INTO [t38171] VALUES (2220,'٢٢٠٧ - (كان) - صلى الله عليه وسلم - (لا يأكل الثوم ولا البصل ولا الكراث).','1411','2086','0');
INSERT INTO [t38171] VALUES (2221,'٢٢٠٨ - (ما ذم) - صلى الله عليه وسلم - (طعاما قط لكن إن أعجبه أكله وإن كرهه تركه)','1412','2086','0');
INSERT INTO [t38171] VALUES (2222,'٢٢٠٩ - (كان) - صلى الله عليه وسلم - (يعاف الضب والطحال ولا يحرمهما)','1412','2086','0');
INSERT INTO [t38171] VALUES (2223,'٢٢١٠ - (كان) - صلى الله عليه وسلم - (يلعق الصحيفة) التي فيها الطعام (ويقول آخر الطعام أكثر بركة).','1412','2086','0');
INSERT INTO [t38171] VALUES (2224,'٢٢١١ - (كان) - صلى الله عليه وسلم - (يلعق أصابعه من الطعام حتى تحمر).','1413','2086','0');
INSERT INTO [t38171] VALUES (2225,'٢٢١٢ - (وكان) - صلى الله عليه وسلم - (لا يمسح يده بالمنديل حتى يلعق أصابعه واحدة واحدة ويقول لا يدرى في أي الأصابع البركة).','1413','2086','0');
INSERT INTO [t38171] VALUES (2226,'٢٢١٤ - (كان) - صلى الله عليه وسلم - (إذا أكل الخبز واللحم خاصة غسل يديه غسلا جيدا).','1416','2086','0');
INSERT INTO [t38171] VALUES (2227,'٢٢١٥ - (يمسح بفضل الماء على وجهه وكان) - صلى الله عليه وسلم - (يشرب في ثلاث دفعات له فيها ثلاث تسميات وفي أواخرها ثلاث تحميدات).','1416','2086','0');
INSERT INTO [t38171] VALUES (2228,'٢٢١٦ - (كان) - صلى الله عليه وسلم - (يمص) الماء (مصا).','1417','2086','0');
INSERT INTO [t38171] VALUES (2229,'٢٢١٧ - (ولا يعب عبا).','1417','2086','0');
INSERT INTO [t38171] VALUES (2230,'٢٢١٨ - (وربما كان) - صلى الله عليه وسلم - (يشرب بنفس واحد حتى يفرغ).','1418','2086','0');
INSERT INTO [t38171] VALUES (2231,'٢٢١٩ - (وكان) - صلى الله عليه وسلم - (لا يتنفس في الإناء) أي في جوفه (بل ينحرف عنه) لأنه يغير الماء إما لتغير الفم بالمأكول وإما لترك السواك وإما لأن النفس يصعد ببخار المعدة.','1418','2086','0');
INSERT INTO [t38171] VALUES (2232,'٢٢٢٠ - (وكان) - صلى الله عليه وسلم - (يدفع فضل سوره) أي ما بقي من الشراب (إلى من على يمينه).','1418','2086','0');
INSERT INTO [t38171] VALUES (2233,'٢٢٢١ - (فإن كان من على يساره أجل رتبة قال للذي على يمينه السنة أن تعطى فإن أحببت آثرتهم).','1419','2086','0');
INSERT INTO [t38171] VALUES (2234,'٢٢٢٢ - (وأتي) - صلى الله عليه وسلم - (بإناء فيه عسل ولبن فأبي أن يشربه وقال شربتان في شربة وإدامان في إناء واحد ثم قال - صلى الله عليه وسلم - لا أحرمه ولكني أكره الفخر والحساب بفضول الدنيا غدا وأحب التواضع فإن من تواضع لله رفعه).','1419','2086','0');
INSERT INTO [t38171] VALUES (2235,'٢٢٢٣ - (كان في بيته أشد حياء من العاتق لا يسألهم طعاما) يعتنيه (ولا يتشهاه عليهم أن أطعموه أكل وما أعطوه) وفي بعض النسخ وما أطعموه (قبل وما سقوه شرب)','1420','2086','0');
INSERT INTO [t38171] VALUES (2236,'٢٢٢٤ - (كان) - صلى الله عليه وسلم - (ربما قام فأخذ ما يأكل أو يشرب بنفسه).','1420','2086','0');
INSERT INTO [t38171] VALUES (2237,'٢٢٢٥ - (كان - صلى الله عليه وسلم - يلبس من الثياب ما وجد من إزار أو رداء أو قميص أو جبة أو غير ذلك).','1420','2086','0');
INSERT INTO [t38171] VALUES (2238,'٢٢٢٦ - (وكان) - صلى الله عليه وسلم - (يعجبه الثياب الخضر)','1421','2086','0');
INSERT INTO [t38171] VALUES (2239,'٢٢٢٧ - (كان) - صلى الله عليه وسلم - (أكثر لباسه البياض ويقول البسوها وكفنوا بها موتاكم).','1421','2086','0');
INSERT INTO [t38171] VALUES (2240,'٢٢٢٨ - (وكان - صلى الله عليه وسلم - يلبس القباء المحشو)','1422','2086','0');
INSERT INTO [t38171] VALUES (2241,'٢٢٢٩ - (وكان) - صلى الله عليه وسلم - (له قباء سندس فيلبسه فتحس خضرته على بياض لونه).','1422','2086','0');
INSERT INTO [t38171] VALUES (2242,'٢٢٣٠ - (وكانت ثيابه) - صلى الله عليه وسلم - (كلها مشمرة وفوق الكعبين ويكون الإزار فوق ذلك إلى نصف الساق).','1422','2086','0');
INSERT INTO [t38171] VALUES (2243,'٢٢٣١ - (وكان) - صلى الله عليه وسلم - (قميصه مشدود الأزرار وربما حل الأزرار في الصلاة وغيرها).','1423','2086','0');
INSERT INTO [t38171] VALUES (2244,'٢٢٣٢ - (وكانت له) - صلى الله عليه وسلم - (ملحفة) بكسر الميم الملاءة تلتحف بها المرأة (مصبوغة بالزعفران وربما صلى بالناس فيها وحدها).','1424','2086','0');
INSERT INTO [t38171] VALUES (2245,'٢٢٣٣ - (ربما لبس) - صلى الله عليه وسلم - (الكساء وحده ما عليه غيره).','1424','2086','0');
INSERT INTO [t38171] VALUES (2246,'٢٢٣٤ - (كان له) - صلى الله عليه وسلم - (كساء ملبد بلبسه).','1424','2086','0');
INSERT INTO [t38171] VALUES (2247,'٢٢٣٥ - (كان له) - صلى الله عليه وسلم - (ثوبان لجمعته خاصة سوى ثيابه في غير الجمعة).','1425','2086','0');
INSERT INTO [t38171] VALUES (2248,'٢٢٣٦ - (ربما لبس) - صلى الله عليه وسلم - (الإزار الواحد ليس عليه غيره يعقد طرفيه بين كتفيه).','1425','2086','0');
INSERT INTO [t38171] VALUES (2249,'٢٢٣٧ - (ربما أم به الناس على الجنائز).','1425','2086','0');
INSERT INTO [t38171] VALUES (2250,'٢٢٣٨ - (يكون ذلك الإزار الذي نجامع فيه يومئذ).','1425','2086','0');
INSERT INTO [t38171] VALUES (2251,'٢٢٣٩ - (كان) - صلى الله عليه وسلم - (ربما صلى بالليل في الإزار ويرتدي ببعض الثوب مما يلي هدبه ويلقي البقية على بعض نسائه فيصلي كذلك).','1426','2086','0');
INSERT INTO [t38171] VALUES (2252,'٢٢٤٠ - (كان له) - صلى الله عليه وسلم - (كساء أسود فوهبه) لآخر (فقالت له أم سلمة) رضي الله عنها (بأبي أنت وأمي) يا رسول الله (ما فعل ذلك الكساء الأسود قال كسوته فقال ما رأيت شيئا قط كان أحن من بياضك على سواده).','1426','2086','0');
INSERT INTO [t38171] VALUES (2253,'٢٢٤١ - (وقال أنس) رضي الله عنه (ربما رأيته) - صلى الله عليه وسلم - (يصلي بنا الظهر في شمله عاقدا بين طرفيها).','1427','2086','0');
INSERT INTO [t38171] VALUES (2254,'٢٢٤٢ - (وكان) - صلى الله عليه وسلم - (يتختم)','1427','2086','0');
INSERT INTO [t38171] VALUES (2255,'٢٢٤٣ - (وربما خرج) - صلى الله عليه وسلم - (وفي خاتمة خيط مربوط يتذكر به الشيء).','1427','2086','0');
INSERT INTO [t38171] VALUES (2256,'٢٢٤٤ - (وكان) - صلى الله عليه وسلم - (يختم به على الكتب)','1428','2086','0');
INSERT INTO [t38171] VALUES (2257,'٢٢٤٥ - (ويقول الخاتم على الكتاب خير من التهمة)','1428','2086','0');
INSERT INTO [t38171] VALUES (2258,'٢٢٤٦ - (كان) - صلى الله عليه وسلم - (يلبس القلانس)','1428','2086','0');
INSERT INTO [t38171] VALUES (2259,'٢٢٤٧ - (ربما لم تكن العمامة فيشد العصابة على رأسه وعلى جبهته).','1429','2086','0');
INSERT INTO [t38171] VALUES (2260,'٢٢٤٨ - (كانت له) - صلى الله عليه وسلم - (عمامة تسمى السحاب فوهبها من علي) رضي الله عنه (فربما طلع علي فيها فيقول - صلى الله عليه وسلم - أتاكم علي في السحاب).','1429','2086','0');
INSERT INTO [t38171] VALUES (2261,'٢٢٤٩ - (كان) - صلى الله عليه وسلم - إذا لبس ثوبا) أي إذا أراد لبسه (يلبسه من قبل ميامنه).','1430','2086','0');
INSERT INTO [t38171] VALUES (2262,'٢٢٥٠ - (يقول الحمد الله الذي كساني ما أواري به عوتي وأتجمل به في الناس).','1430','2086','0');
INSERT INTO [t38171] VALUES (2263,'٢٢٥١ - (إذا نزع ثوبه خرج من مياسره)','1430','2086','0');
INSERT INTO [t38171] VALUES (2264,'٢٢٥٢ - (كان له) - صلى الله عليه وسلم - (ثوب لجمعته خاصة سوى ثيابه لغير الجمعة)','1431','2086','0');
INSERT INTO [t38171] VALUES (2265,'٢٢٥٣ - (كان) - صلى الله عليه وسلم - (إذا لبس) ثوبا (جديدا أعطى خلق ثيابه مسكينا ثم يقول ما من مسلم يكسو مسلما من سمل ثيابه لا يكسوه إلا الله كان في ضمان الله وحرزه وخيره ما واراه حيا وميتا).','1431','2086','0');
INSERT INTO [t38171] VALUES (2266,'٢٢٥٤ - (وكان له) - صلى الله عليه وسلم - (فراش من أدم)','1432','2086','0');
INSERT INTO [t38171] VALUES (2267,'٢٢٥٥ - (وكانت له) - صلى الله عليه وسلم - (عباءة تفرش له حيثما تنقل تثنى طاقتين تحته).','1432','2086','0');
INSERT INTO [t38171] VALUES (2268,'٢٢٥٦ - (وكان) - صلى الله عليه وسلم - (ينام على الحصير ليس تحته شيء غيره)','1433','2086','0');
INSERT INTO [t38171] VALUES (2269,'٢٢٥٧ - (وكان من خلقه) - صلى الله عليه وسلم - (تسميته دوابه وسلاحه ومتاعه)','1433','2086','0');
INSERT INTO [t38171] VALUES (2270,'٢٢٥٨ - (وكان اسم رايته العقاب)','1433','2086','0');
INSERT INTO [t38171] VALUES (2271,'٢٢٥٩ - (واسم سيفه الذي) كان (يشهد به الحروب ذو الفقار)','1434','2086','0');
INSERT INTO [t38171] VALUES (2272,'٢٢٦٠ - (كان له) - صلى الله عليه وسلم - (سيف يقال له المخذم) (وآخر يقال له رسوب وآخر يقال له القضيب).','1435','2086','0');
INSERT INTO [t38171] VALUES (2273,'٢٢٦١ - (كان قبيعة سيفه) - صلى الله عليه وسلم - (من الفضة)','1435','2086','0');
INSERT INTO [t38171] VALUES (2274,'٢٢٦٢ - (كان) - صلى الله عليه وسلم - (يلبس المنطقة) بكسر الميم من الأدم) محركة الجلد المدبوغ أو الأحمر أو مطلقا أقوال (فيها ثلاث حلق من الفضة)','1436','2086','0');
INSERT INTO [t38171] VALUES (2275,'٢٢٦٣ - (وكان اسم قوسه - صلى الله عليه وسلم -) اسم (جعبته الكافور).','1436','2086','0');
INSERT INTO [t38171] VALUES (2276,'٢٢٦٤ - (وكان اسم ناقته) - صلى الله عليه وسلم - (القصوى وهي التي يقال لها العضباء واسم بغلته الدلدل وكان اسم حماره يعفور واسم شاته التي يشرب لبنها عينة).','1436','2086','0');
INSERT INTO [t38171] VALUES (2277,'٢٢٦٥ - (كانت له) - صلى الله عليه وسلم - (مطهرة من فخار يتوضأ فيها يشرب منها فيرسل الناس أولادهم الصغار الذين قد عقلوا فيدخلون على رسول الله - صلى الله عليه وسلم - فلا يدفعون عنه فإذا وجدوا في المطهرة ماء شربوا منه ومسحوا على وجوههم وأجسادهم يبتغون بذلك البركة).','1437','2086','0');
INSERT INTO [t38171] VALUES (2278,'٢٢٦٦ - (أتي بقلائد من ذهب وفضة)','1439','2086','0');
INSERT INTO [t38171] VALUES (2279,'٢٢٦٧ - (وروى جابر)','1440','2086','0');
INSERT INTO [t38171] VALUES (2280,'٢٢٦٨ - (وكان - صلى الله عليه وسلم - في حرب فرأوا من المسلمين غرة)','1440','2086','0');
INSERT INTO [t38171] VALUES (2281,'٢٢٦٩ - (وروى أنس) رضي الله عنه (أن يهودية أتت إلى النبي - صلى الله عليه وسلم - بشاة مسمومة ليأكل منها فجيء بها إلى النبي - صلى الله عليه وسلم - فسألها عن ذلك فقالت أردت قتلك فقال ما كان الله ليسلطك على ذلك قالوا أفلا نقتلها فقال لا).','1441','2086','0');
INSERT INTO [t38171] VALUES (2282,'٢٢٧٠ - (وسحره) - صلى الله عليه وسلم - (رجل من اليهود فأخبره جبريل) عليه السلام (بذلك حتى استخرجه) من بئر ذروان (وحل عقده فوجد لذلك خفة ولا ذكر ذلك لليهودي ولا أظهره عليه قط).','1442','2086','0');
INSERT INTO [t38171] VALUES (2283,'٢٢٧١ - (قال علي كرم الله وجهه بعثني رسول الله - صلى الله عليه وسلم - أنا والزبير والمقداد) بن الأسود (فقال انطلقوا حتى تأتوا روضة خاخ)','1443','2086','0');
INSERT INTO [t38171] VALUES (2284,'٢٢٧٢ - (وقسم - صلى الله عليه وسلم - قسمة فقال رجل من الأنصار هذه قسمة ما أريد بها وجه الله فذكر ذلك للنبي - صلى الله عليه وسلم - فاحمر وجهه وقال رحم الله أخي موسى قد أوذي بأكثر من هذا فصبر).','1444','2086','0');
INSERT INTO [t38171] VALUES (2285,'٢٢٧٣ - (كان - صلى الله عليه وسلم - يقول لا يبلغني أحد منكم عن أحد من أصحابي شيئا فأني أحب أن أخرج إليكم وأنا سليم الصدر).','1445','2086','0');
INSERT INTO [t38171] VALUES (2286,'٢٢٧٤ - (كان - صلى الله عليه وسلم - رقيق البشرة) محركة ظاهر الجلد وهو علامة اعتدال المزاج ويكنى به عن الحياء أيضا (لطيف الظاهر والباطن يعرف في وجهه) الشريف (غضبته ورضاه):','1445','2086','0');
INSERT INTO [t38171] VALUES (2287,'٢٢٧٥ - (كان) لله (إذا اشتد وجده) أي غضبه يقال وجد عليه وجدا وموجودة إذا غضب عليه (أكثر من مس لحيته).','1445','2086','0');
INSERT INTO [t38171] VALUES (2288,'٢٢٧٦ - (وكان) - صلى الله عليه وسلم - (لا يشافه أحدا بما يكرهه)','1445','2086','0');
INSERT INTO [t38171] VALUES (2289,'٢٢٧٧ - (بال أعرابي في المسجد بحضرته فهم به الأصحاب)','1446','2086','0');
INSERT INTO [t38171] VALUES (2290,'٢٢٧٨ - (وجاء أعرابي يطلب منه شيئا فأعطاه رسول الله - صلى الله عليه وسلم - ثم قال له أحسنت إليك) يخبر بذلك باطنه (فقال الأعرابي لا ولا أجملت قال فغضب المسلمون لذلك وقاموا إليه فأشار إليهم أن','1446','2086','0');
INSERT INTO [t38171] VALUES (2291,'٢٢٧٩ - (كان - صلى الله عليه وسلم - أجود الناس وأسخاهم)','1447','2086','0');
INSERT INTO [t38171] VALUES (2292,'٢٢٨٠ - (كان) - صلى الله عليه وسلم - (في شهر رمضان كالريح المرسلة) بفتح السين أي المطلقة (لا يمسك شيئا)','1448','2086','0');
INSERT INTO [t38171] VALUES (2293,'٢٢٨١ - (من رآه بديهة هابه ومن خالطه معرفة أحبه يقول ناعته لم أر قبله ولا بعده مثله).','1448','2086','0');
INSERT INTO [t38171] VALUES (2294,'٢٢٨٢ - (وما سئل) - صلى الله عليه وسلم - (قط على الإسلام) شيئا من متاع الدنيا (إلا أعطاه) وجاد به أو وعده أو سكت (فإن رجلا أتاه فسأله فأعطاه غنما بين جبلين فرجع إلى قومه وقال يا قوم أسلموا فإن محمدا يعطي عطاء من لا يخشى الفاقة)','1448','2086','0');
INSERT INTO [t38171] VALUES (2295,'٢٢٨٣ - (وما سئل) - صلى الله عليه وسلم - (شيئا فقال لا).','1449','2086','0');
INSERT INTO [t38171] VALUES (2296,'٢٢٨٤ - (وحمل إليه تسعون ألف درهم فوضعها على حصير ثم قام إليها يقسمها فما رد سائلا حتى فرغ منها)','1449','2086','0');
INSERT INTO [t38171] VALUES (2297,'٢٢٨٥ - (جاءه رجل فسأله)','1450','2086','0');
INSERT INTO [t38171] VALUES (2298,'٢٢٨٦ - حديث أنفق بلال.','1451','2086','0');
INSERT INTO [t38171] VALUES (2299,'٢٢٨٧ - (ولما قفل) - صلى الله عليه وسلم - (من حنين جاءت الأعراب يسألونه حتى اضطروه إلى شجرة فخطفت رداءه فوقف رسول الله وقال اعطوني ردائي لو كان لي عدد هذه العضاه) هي من أشجار البادية (نعما) أي ابلا (لقسمته بينكم ثم لا تجدوني بخيلا ولا كذابا ولا جبانا).','1451','2086','0');
INSERT INTO [t38171] VALUES (2300,'٢٢٨٨ - (كان - صلى الله عليه وسلم - أنجد الناس وأشجعهم).','1451','2086','0');
INSERT INTO [t38171] VALUES (2301,'٢٢٨٩ - (قال علي رضي الله عنه لقد رأيتني يوم بدر ونحن نلوذ بالنبي - صلى الله عليه وسلم - وهو أقرب إلى العدو وكان أشد الناس بأسا يومئذ).','1451','2086','0');
INSERT INTO [t38171] VALUES (2302,'٢٢٩٠ - (قال) رضي الله عنه (أيضا كنا إذا احمر البأس) أي','1451','2086','0');
INSERT INTO [t38171] VALUES (2303,'٢٢٩١ - (وقيل كان رسول الله - صلى الله عليه وسلم - قليل الكلام قليل الحديث فإذا أمر الناس بالقتال تشمر).','1452','2086','0');
INSERT INTO [t38171] VALUES (2304,'٢٢٩٢ - (وكان) - صلى الله عليه وسلم - (من أشد الناس بأسا)','1452','2086','0');
INSERT INTO [t38171] VALUES (2305,'٢٢٩٣ - (قالوا وكان) - صلى الله عليه وسلم - (قوي البطش).','1452','2086','0');
INSERT INTO [t38171] VALUES (2306,'٢٢٩٤ - (وقال عمران بن حصين) رضى الله عنه (ما لقي رسول الله - صلى الله عليه وسلم - كتيبة) طائفة من الجيش مجتمعة (إلا كان أول من يضرب).','1453','2086','0');
INSERT INTO [t38171] VALUES (2307,'٢٢٩٥ - (ولما غشيته المشركون) يوم حنين (نزل) عن بغلته (فجعل يقول).','1453','2086','0');
INSERT INTO [t38171] VALUES (2308,'٢٢٩٦ - (كان - صلى الله عليه وسلم - أشد الناس تواضعا على علو منصبه).','1453','2086','0');
INSERT INTO [t38171] VALUES (2309,'٢٢٩٧ - (قال ابن عامر)','1453','2086','0');
INSERT INTO [t38171] VALUES (2310,'٢٢٩٨ - (كان أصحابه) - صلى الله عليه وسلم - (لا يقومون له)','1454','2086','0');
INSERT INTO [t38171] VALUES (2311,'٢٢٩٩ - (أتي النبي - صلى الله عليه وسلم - برجل فأرعد من هيبته)','1454','2086','0');
INSERT INTO [t38171] VALUES (2312,'٢٣٠٠ - (كان) - صلى الله عليه وسلم - (يجلس بين أصحابه) حالة كونه (مختلطا بهم كأنه أحدهم فيأتي الغريب)','1454','2086','0');
INSERT INTO [t38171] VALUES (2313,'٢٣٠١ - (وقالت عائشة رضي الله عنها) لرسول الله (كل جعلني الله فداءك متكئا فإنه أهون عليك قال فأصغى برأسه حتى كاد أن تصيب جبهته الأرض ثم قال بل آكل كما يأكل العبد وأجلس كما يجلس العبد).','1455','2086','0');
INSERT INTO [t38171] VALUES (2314,'٢٣٠٢ - (كان لا يأكل على خوان ولا في سكرجة حتى لحق بالله تعالى).','1455','2086','0');
INSERT INTO [t38171] VALUES (2315,'٢٣٠٣ - (وكان) - صلى الله عليه وسلم - (لا يدعوه أحد من أصحابه وغيرهم إلا قال لبيك).','1455','2086','0');
INSERT INTO [t38171] VALUES (2316,'٢٣٠٤ - (وكان) - صلى الله عليه وسلم - (إذا جلس مع الناس إن تكلموا في معنى الآخرة أخذ معهم) أي في الحديث (وإن تحدثوا في طعام أو شراب تحدث معهم وإن تكلموا في الدنيا تحدث معهم رفقا بهم وتواضعا لهم).','1456','2086','0');
INSERT INTO [t38171] VALUES (2317,'٢٣٠٥ - (وكانوا يتناشدون الشعر بين يديه أحيانا) فيسمعهم (ويذكرون أشياء من أمر الجاهلية ويضحكون فيتبسم هو إذا ضحكوا)','1456','2086','0');
INSERT INTO [t38171] VALUES (2318,'٢٣٠٦ - (كان من صفة رسول الله - صلى الله عليه وسلم - في قامته) الشريفة (أنه لم يكن الطويل البائن)','1456','2086','0');
INSERT INTO [t38171] VALUES (2319,'٢٣٠٧ - (يقول - صلى الله عليه وسلم - جعل الخير كله في الربعة)','1457','2086','0');
INSERT INTO [t38171] VALUES (2320,'٢٣٠٨ - (ربما جعله غدائر أربعا يخرج كل أذن بين غديرتين)','1457','2086','0');
INSERT INTO [t38171] VALUES (2321,'٢٣٠٩ - قال العراقي:','1458','2086','0');
INSERT INTO [t38171] VALUES (2322,'٢٣١٠ - (كان - صلى الله عليه وسلم - يقول إن لي عند ربي عشرة أسماء أنا محمد وأنا أحمد وأنا الماحي الذي يمحو الله بي الكفر وأنا العاقب الذي ليس بعده أحد وأنا الحاشر يحشر العباد على قدمي وأنا رسول','1458','2086','0');
INSERT INTO [t38171] VALUES (2323,'٢٣١١ - (انشقاق القمر)','1461','2086','0');
INSERT INTO [t38171] VALUES (2324,'٢٣١٢ - من معجزاته - صلى الله عليه وسلم - أنه (أطعم النفر الكثير في منزل جابر)','1463','2086','0');
INSERT INTO [t38171] VALUES (2325,'٢٣١٣ - من معجزاته - صلى الله عليه وسلم -','1464','2086','0');
INSERT INTO [t38171] VALUES (2326,'٢٣١٤ - (و) من معجزاته - صلى الله عليه وسلم - أن أطعم (يوم الخندق مرة ثمانين)','1465','2086','0');
INSERT INTO [t38171] VALUES (2327,'٢٣١٥ - من معجزاته - صلى الله عليه وسلم - أنه أطعم (مرة أكثر من ثمانين رجلا من أقراص شعير حملها أنس)','1467','2086','0');
INSERT INTO [t38171] VALUES (2328,'٢٣١٦ - من معجزاته - صلى الله عليه وسلم - أنه أطعم (مرة أهل الجيش من تمر يسير ساقته بنت بشر)','1469','2086','0');
INSERT INTO [t38171] VALUES (2329,'٢٣١٧ - (و) من معجزاته - صلى الله عليه وسلم - أن (نبع الماء) الطهور (من بين اصابعه)','1471','2086','0');
INSERT INTO [t38171] VALUES (2330,'٢٣١٨ - (وتوضأ من قدح صغير ضاق أن يبسط - صلى الله عليه وسلم - يده فيه).','1472','2086','0');
INSERT INTO [t38171] VALUES (2331,'٢٣١٩ - من معجزاته - صلى الله عليه وسلم - أنه (أهراق) بفتح الهمزة والهاء أصله أراق (وضوأه) بالفتح هو الماء الذي يتوضأ به (في عين تبوك) وهو موضع بالشام (ومرة أخرى في بئر الحديبية فجاشتا بالماء فشرب من عين تبوك أهل الجيش وهم ألوف حتى رووا وشرب من بئر الحديبية ألف وخمسمائة ولم يكن فيها قبل ذلك ماء).','1474','2086','0');
INSERT INTO [t38171] VALUES (2332,'٢٣٢٠ - من معجزاته - صلى الله عليه وسلم - أنه (أمر عمر بن الخطاب) رضي الله عنه (أن يزود أربعمائة راكب من تمر كان في اجتماعه) وهيئته (كربضة البعير وهو) بفتح الراء وسكون الموحدة والضاد المعجمة (موضع بروكه فزودهم كلهم منه وبقي يحسبه).','1476','2086','0');
INSERT INTO [t38171] VALUES (2333,'٢٣٢١ - من معجزاته - صلى الله عليه وسلم - أنه (رمى الجيش بقبضة من تراب) الأرض وقالت شاهت الوجوه أي قبحت (فعميت عيونهم)','1476','2086','0');
INSERT INTO [t38171] VALUES (2334,'٢٣٢٢ - من معجزاته - صلى الله عليه وسلم - أنه (أبطل الله الكهانة بمبعثه - صلى الله عليه وسلم - فعدمت وكانت) قبل (ظاهرة موجودة).','1477','2086','0');
INSERT INTO [t38171] VALUES (2335,'٢٣٢٣ - من معجزاته - صلى الله عليه وسلم - أن (حن الجذع)','1478','2086','0');
INSERT INTO [t38171] VALUES (2336,'٢٣٢٤ - من معجزاته - صلى الله عليه وسلم - أن (دعا) طائفة (اليهود إلى تمني الموت وأخبرهم بأنهم لا يتمنونه فحيل بينهم وبين النطق بذلك وعجزوا عنه).','1484','2086','0');
INSERT INTO [t38171] VALUES (2337,'٢٣٢٥ - من معجزاته أنه (أخبر - صلى الله عليه وسلم - بالغيوب)','1484','2086','0');
INSERT INTO [t38171] VALUES (2338,'٢٣٢٦ - (تقتله الفئة الباغية).','1485','2086','0');
INSERT INTO [t38171] VALUES (2339,'٢٣٢٧ - (الحسن يصلح الله به بين فئتين من المسلمين عظيمتين).','1486','2086','0');
INSERT INTO [t38171] VALUES (2340,'٢٣٢٨ - - صلى الله عليه وسلم - (أخبر عن رجل قاتل في سبيل الله أنه من أهل النار فظهر ذلك بأن قتل ذلك الرجل نفسه).','1486','2086','0');
INSERT INTO [t38171] VALUES (2341,'٢٣٢٩ - (اتبعه) حال مهاجرته إلى المدينة (سراقة)','1487','2086','0');
INSERT INTO [t38171] VALUES (2342,'٢٣٣٠ - (أخبر بمقتل الأسود العنسي)','1488','2086','0');
INSERT INTO [t38171] VALUES (2343,'٢٣٣١ - (خرج على مائة من قريش ينتظرونه فوضع التراب على رؤسهم ولم يروه).','1489','2086','0');
INSERT INTO [t38171] VALUES (2344,'٢٣٣٢ - (شكا إليه البعير بحضرة أصحابه وتذلل له).','1489','2086','0');
INSERT INTO [t38171] VALUES (2345,'٢٣٣٣ - (قال لنفر من أصحابه) كانوا (مجتمعين أحدكم ضرسه في النار مثل) جبل (أحد فماتوا كلهم على استقامة وارتد منهم واحد فقتل مرتدا).','1490','2086','0');
INSERT INTO [t38171] VALUES (2346,'٢٣٣٤ - (قال لآخرين منهم) أي من الصحابة (آخركم موتا في النار فسقط آخرهم موتا في نار فأحترق فيها فمات).','1491','2086','0');
INSERT INTO [t38171] VALUES (2347,'٢٣٣٥ - (دعا شجرتين فأتتاه فاجتمعتا ثم أمرهما فافترقتا)','1491','2086','0');
INSERT INTO [t38171] VALUES (2348,'٢٣٣٦ - (دعا) طائفة (النصارى إلى المباهلة) أي الملاعنة (فامتنعوا) عن ذلك (وأخبر) - صلى الله عليه وسلم - (أنهم إن فعلوا) ذلك (هلكوا فعلموا صحة قوله فامتنعوا).','1492','2086','0');
INSERT INTO [t38171] VALUES (2349,'٢٣٣٧ - (أتاه عامر بن الطفيل) بن مالك بن جعفر الكلابي (وأربد بن قيس وهما فارسا العرب وفاتكاهم) والفتك هو الأخذ بقوة وبطش (عازمين) أي قاصدين (على قتله - صلى الله عليه وسلم - فحيل بينهما وبين ذلك فدعا - صلى الله عليه وسلم - عليهما فهلك عامر بغدة وهلك أربد بصاعقة أحرقته).','1492','2086','0');
INSERT INTO [t38171] VALUES (2350,'٢٣٣٨ - (أخبر أنه يقتل أبي بن خلف)','1493','2086','0');
INSERT INTO [t38171] VALUES (2351,'٢٣٣٩ - (أطعم السم فمات الذي أكله معه وعاش هو - صلى الله عليه وسلم - بعده أربع سنين وكلمه الذراع المسموم).','1494','2086','0');
INSERT INTO [t38171] VALUES (2352,'٢٣٤٠ - (أخبر يوم بدر بمضارع صناديد قريش ووقفهم على مصارعهم رجلا رجلا فلم يتعد واحد منهم ذلك الموضع).','1495','2086','0');
INSERT INTO [t38171] VALUES (2353,'٢٣٤١ - (أخبر أن طوائف من أمته يغزون في البحر فكان كذلك).','1496','2086','0');
INSERT INTO [t38171] VALUES (2354,'٢٣٤٢ - (زويت له الأرض فأري مشارقها ومغاربها وأخبر بأن ملك أمته سيبلغ ما زوى منها فكان ذلك كما أخبر فقد بلغ ملكهم من أول المشرق من بلاد الترك إلى آخر المغرب من بلاد الأندلس)','1497','2086','0');
INSERT INTO [t38171] VALUES (2355,'٢٣٤٢/أ- (أخبر فاطمة ابنته رضوان الله عليها) وهي الزهراء تكنى بأم أبيها ولدت سنة إحدى وأربعين من مولد أبيها - صلى الله عليه وسلم - وهي أصغر البنات (بأنها أول أهله لحاقا به فكان كذلك)','1497','2354','0');
INSERT INTO [t38171] VALUES (2356,'٢٣٤٣ - (أخبر نساءه بأن أطولهن يدا أسرعهم لحاقا به فكانت زينب بنت جحش)','1498','2086','0');
INSERT INTO [t38171] VALUES (2357,'٢٣٤٤ - (مسح ضرع شاة حائل) يقال حالت الشاة وكذا الناقة والمرأة وكل أنثى حيالا بالكسر لم تحمل فهي حائل (لا لبن لها فدرت) اللبن (فكان ذلك سبب إسلام ابن مسعود).','1499','2086','0');
INSERT INTO [t38171] VALUES (2358,'٢٣٤٥ - (فعل ذلك) - صلى الله عليه وسلم - (مرة أخرى في خيمة أم معبد) عاتكة بنت خلف (الخزاعية)','1499','2086','0');
INSERT INTO [t38171] VALUES (2359,'٢٣٤٦ - (ندرت عين بعض أصحابه فسقطت فردها فكانت أصح عينيه وأحسنهما).','1501','2086','0');
INSERT INTO [t38171] VALUES (2360,'٢٣٤٧ - (تفل في عين علي كرم الله وجهه وهو أرمد يوم خيبر فصح من وقته وبعثه بالراية).','1503','2086','0');
INSERT INTO [t38171] VALUES (2361,'٢٣٤٨ - (كانوا يسمعون تسبيح الطعام بين يديه - صلى الله عليه وسلم -).','1505','2086','0');
INSERT INTO [t38171] VALUES (2362,'٢٣٤٩ - (أصيبت رجل بعض أصحابه فمسحها بيده فبرئت من حينها).','1506','2086','0');
INSERT INTO [t38171] VALUES (2363,'٢٣٥٠ - (قل زاد جيش كان معه - صلى الله عليه وسلم - فدعا بجميع ما بقي واجتمع شيء يسير جدا فدعا بالبركة ثم أمرهم فأخذوا فلم يبق وعاء في العسكر إلا ملئ من ذلك).','1508','2086','0');
INSERT INTO [t38171] VALUES (2364,'٢٣٥١ - (حكى الحكم بن العاص)','1508','2086','0');
INSERT INTO [t38171] VALUES (2365,'٢٣٥٢ - (خطب امرأة) من أبيها (فقال أبوها إن بها برصا امتناعا من خطبته واعتذارا ولم يكن بها برص فقال - صلى الله عليه وسلم - فلتكن كذلك فبرصت وهي أم شبيب بن البرصاء الشاعر).','1509','2086','0');
INSERT INTO [t38171] VALUES (2366,'٢٣٥٣ - ويد طلحة لما زاد ما كان بها من شلل أصابها يوم أحد حتى مسحها بيده.','1510','2086','0');
INSERT INTO [t38171] VALUES (2367,'ربع المهلكات','1511','0','0');
INSERT INTO [t38171] VALUES (2368,'كتاب عجائب القلب','1511','2367','0');
INSERT INTO [t38171] VALUES (2369,'٢٣٥٤ - (إذا عرف نفسه عرف ربه)','1512','2368','0');
INSERT INTO [t38171] VALUES (2370,'٢٣٥٥ - (الروح ولم يتكلم فيه رسول الله - صلى الله عليه وسلم -).','1512','2368','0');
INSERT INTO [t38171] VALUES (2371,'٢٣٥٦ - (قوله - صلى الله عليه وسلم - أعدى عدوك) أي أكثرهم عداوة لك (نفسك التي بين جنبيك).','1512','2368','0');
INSERT INTO [t38171] VALUES (2372,'٢٣٥٧ - (قوله - صلى الله عليه وسلم - أول ما خلق الله العقل)','1512','2368','0');
INSERT INTO [t38171] VALUES (2373,'٢٣٥٨ - في الخبر وقد سئل أي الجهاد أفضل فقال جهادك هواك (وإن ضيع ثغره وأهمل رعيته ذم أثره) إذا عاد إليه كما ورد في الخبر كلكم راع وكلكم مسؤل عن رعيته (وانتقم منع عند لقاء الله تعالى فيقال له يوم القيامة يا راعي السوء أكلت اللحم وشربت اللبن ولم ترد الضالة ولم تجبر الكسير اليوم أنتقم منك كما ورد في الخبر).','1513','2368','0');
INSERT INTO [t38171] VALUES (2374,'٢٣٥٩ - (قوله - صلى الله عليه وسلم - رجعنا من الجهاد الأصغر إلى الجهاد الأكبر).','1514','2368','0');
INSERT INTO [t38171] VALUES (2375,'٢٣٦٠ - (قوله) - صلى الله عليه وسلم - (حكاية عن ربه عز وجل لقد طال شوق الأبرار إلى لقائي وأنا إلى لقائهم أشد شوقا).','1514','2368','0');
INSERT INTO [t38171] VALUES (2376,'٢٣٦١ - (الإنسان عيناه هاد) وفي لفظ هاديتان (وأذناه قمع) وفي لفظ قمعان (ولسانه ترجمان ويداه جناحان ورجلاه بريد والقلب ملك فإذا طالب الملك طابت جنوده قالت) عائشة رضي الله عنها (هكذا سمعت رسول الله - صلى الله عليه وسلم -)','1514','2368','0');
INSERT INTO [t38171] VALUES (2377,'٢٣٦٢ - (قال علي رضي الله عنه في تمثيل القلوب إن لله تعالى في أرضه آنية)','1515','2368','0');
INSERT INTO [t38171] VALUES (2378,'٢٣٦٣ - (قوله - صلى الله عليه وسلم - إذا أراد الله بعبد خيرا جعل له واعظا) أي ناصحا ومذكرا للعواقب (من قلبه).','1515','2368','0');
INSERT INTO [t38171] VALUES (2379,'٢٣٦٤ - (قوله) - صلى الله عليه وسلم - (من كان له من قلبه واعظ كان عليه من الله حافظ)','1516','2368','0');
INSERT INTO [t38171] VALUES (2380,'٢٣٦٥ - (قال - صلى الله عليه وسلم - القلوب أربعة قلب أجرد فيه مراج يزهر) أي يلمع (فذلك قلب المؤمن وقلب أسود منكوس) أي مقلوب أعلاه أسفله وأسفله أعلاه (فذلك قلب الكافر وقلب أغلف مربوط على غلافه فذلك قلب المنافق وقلب مصفح فيه إيمان ونفاق فمثل الإيمان فيه كمثل البقلة يمدها الماء الطيب ومثل النفاق فيه كمثل القرحة يمدها القيح والصديد فأي المادتين غلبت عليه حكم له بها وفي رواية ذهبت به)','1516','2368','0');
INSERT INTO [t38171] VALUES (2381,'٢٣٦٦ - (قوله - صلى الله عليه وسلم - من قارف ذنبا) أي أصاب وارتكب (فارقه عقل لا يعود إليه أبدا).','1517','2368','0');
INSERT INTO [t38171] VALUES (2382,'٢٣٦٧ - (كل مولود يولد على الفطرة).','1517','2368','0');
INSERT INTO [t38171] VALUES (2383,'٢٣٦٨ - (روي عن ابن عمر) رضي الله عنهما (قال قيل يا رسول الله أين الله في الأرض قال في قلوب عباده المؤمنين)','1518','2368','0');
INSERT INTO [t38171] VALUES (2384,'٢٣٦٩ - (في الخبر قال الله تعالى لم يسعني أرضي ولا سمائي ووسعني قلب عبدي المؤمن)','1518','2368','0');
INSERT INTO [t38171] VALUES (2385,'٢٣٧٠ - (وفي الخبر أنه قيل لرسول الله - صلى الله عليه وسلم - من خير الناس فقال كل مؤمن مخموم القلب فقيل وما مخموم القلب فقال هو التقي النقي الذي لا غش فيه ولا بغي ولا غل ولا حسد)','1519','2368','0');
INSERT INTO [t38171] VALUES (2386,'٢٣٧١ - (قال - صلى الله عليه وسلم - أكثر أهل الجنه البله)','1520','2368','0');
INSERT INTO [t38171] VALUES (2387,'٢٣٧٢ - (قال رسول الله - صلى الله عليه وسلم - قلب المؤمن أشد تقلبا من القدر في غليانها).','1520','2368','0');
INSERT INTO [t38171] VALUES (2388,'٢٣٧٣ - (قال) - صلى الله عليه وسلم - (قلب المؤمن بين أصبعين من أصابع الرحمن).','1520','2368','0');
INSERT INTO [t38171] VALUES (2389,'٢٣٧٤ - (وقال - صلى الله عليه وسلم - سبق المفردون قيل ومن هم المفردون يا رسول الله قال المتنزهون بذكر الله تعالى وضع الذكر عنهم أوزارهم فوردوا القيامة خفافا ثم قال في وصفهم إخبارا عن الله فقال ثم أقبل بوجهي عليهم أترى من واجهته بوجهي يعلم أحد أي شيء أريد أن أعطيه ثم قال تعالى أول ما أعطيهم أن أقذف النور في قلوبهم فيخبرون عني كما أخبر عنهم).','1521','2368','0');
INSERT INTO [t38171] VALUES (2390,'٢٣٧٥ - (ورد في الخبر أن بعضهم) أي المؤمنين (يعطى نورا مثل الجبل وبعضهم يعطى أصفر) منه (حتى يكون رجل يعطى ئوره على إبهام قدمه فيضيء مرة وينطفئ أخرى فإذا أضاء قدم قدمه فمشى وإذا طفئ قام ومرورهم على الصراط على قدر نورهم فمنهم من يمر كطرف العين ومنهم من يمر كالبرق) الخاطف (ومنهم) من يمر (كالسحاب ومنهم) من يمر (كانقضاض الكوكب) وهو سقوطه يشير إلى السرعة (ومنهم من يمر كشد الفرس) أي عدوه (والذي أعطي نوره على إبهام قدمه يحبو على وجهه ويديه ورجليه تخر منه يد) أي تسقط (وتعلق أخرى وتخر رجل وتعلق أخرى وتصيب جوانبه النار قال ولا يزال كذلك حتى يخلص الحديث).','1522','2368','0');
INSERT INTO [t38171] VALUES (2391,'٢٣٧٦ - (جاء في الخبر أنه يقال يوم القيامة أخرجوا من النار من في قلبه مثقال ذرة من إيمان ونصف مثقال من إيمان وربع مثقال) من إيمان (وذرة)','1523','2368','0');
INSERT INTO [t38171] VALUES (2392,'٢٣٧٧ - (قوله - صلى الله عليه وسلم - ليس شيء خيرا من ألف مثله إلا الإنسان أو المؤمن)','1523','2368','0');
INSERT INTO [t38171] VALUES (2393,'٢٣٧٨ - قال - صلى الله عليه وسلم - أكثر أهل الجنة البله وعليون لذوي الألباب)','1524','2368','0');
INSERT INTO [t38171] VALUES (2394,'٢٣٧٩ - (قال اللهم أعطني نورا) (وزدني نورا واجعل في قلبي نورا وفي سمعي نورا حتى قال وفي شعري وبشري ولحمي ودمي وعظامي).','1524','2368','0');
INSERT INTO [t38171] VALUES (2395,'٢٣٨٠ - (سئل - صلى الله عليه وسلم - عن قوله تعالى أفمن شرح الله صدره للإسلام فهو على نور من ربه)','1524','2368','0');
INSERT INTO [t38171] VALUES (2396,'٢٣٨٠/ أ- (قال - صلى الله عليه وسلم - لابن عباس) رضي الله عنه (اللهم فقهه في الدين وعلمه التأويل).','1525','2395','0');
INSERT INTO [t38171] VALUES (2397,'٢٣٨١ - (بلوغ صوته إليه من جملة الكرامات العظيمة)','1525','2368','0');
INSERT INTO [t38171] VALUES (2398,'٢٣٨٢ - (قال - صلى الله عليه وسلم - في القلب لمتان لمة من الملك إيعاد بالخير وتصديق بالحق ولمة من العدو إيعاد بالشر وتكذيب بالحق ونهي عن الخير)','1526','2368','0');
INSERT INTO [t38171] VALUES (2399,'٢٣٨٣ - (قال - صلى الله عليه وسلم - ما منكم من أحد إلا وله) وفي رواية معه (شيطان قالوا وأنت يا رسول الله قال وأنا إلا أن الله تعالى أعانني عليه فأسلم)','1526','2368','0');
INSERT INTO [t38171] VALUES (2400,'٢٣٨٤ - (قال عمرو بن العاصي)','1527','2368','0');
INSERT INTO [t38171] VALUES (2401,'٢٣٨٥ - (في الخبر إن للوضوء شيطانا يقال له الولهان فاستعيذوا بالله منه).','1527','2368','0');
INSERT INTO [t38171] VALUES (2402,'٢٣٨٦ - (قال أنس) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - إن الشيطان واضع خرطومه)','1527','2368','0');
INSERT INTO [t38171] VALUES (2403,'٢٣٨٧ - (قال ابن وضاح في حديث ذكره إذا بلغ الرجل أربعين سنة ولم يتب مسح الشيطان وجهه بيده وقال بأبي وجه من لا يفلح)','1528','2368','0');
INSERT INTO [t38171] VALUES (2404,'٢٣٨٨ - (قال - صلى الله عليه وسلم - إن الشيطان يجري من ابن آدم مجرى الدم فضيقوا مجاريه بالجوع)','1528','2368','0');
INSERT INTO [t38171] VALUES (2405,'٢٣٨٩ - (قال - صلى الله عليه وسلم - إن الشيطان قعد لابن آدم بطرقه فقعد له بطريق الإسلام)','1528','2368','0');
INSERT INTO [t38171] VALUES (2406,'٢٣٩٠ - (قال - صلى الله عليه وسلم - إن المؤمن ينضى شيطانه كما ينضى أحدكم بعيره في سفره).','1529','2368','0');
INSERT INTO [t38171] VALUES (2407,'٢٣٩١ - (وقال ابن مسعود) رضي الله عنه (خط لنا رسول الله - صلى الله عليه وسلم - يوما خطا وقال هذا سبيل الله)','1529','2368','0');
INSERT INTO [t38171] VALUES (2408,'٢٣٩٢ - (روي عن النبي - صلى الله عليه وسلم - أنه قال كان راهب في بني إسرائيل) أي عابد في صومعته (فعمد الشيطان إلى جارية فخنقها)','1530','2368','0');
INSERT INTO [t38171] VALUES (2409,'٢٣٩٣ - (قال - صلى الله عليه وسلم - حبك الشيء يعمي ويصم)','1532','2368','0');
INSERT INTO [t38171] VALUES (2410,'٢٣٩٤ - (العجلة من الشيطان والتأني من الله تعالى).','1532','2368','0');
INSERT INTO [t38171] VALUES (2411,'٢٣٩٥ - (قال ثابث) بن أسلم (البناني)','1533','2368','0');
INSERT INTO [t38171] VALUES (2412,'٢٣٩٦ - (روى أبو أمامة)','1534','2368','0');
INSERT INTO [t38171] VALUES (2413,'٢٣٩٧ - (رسول الله - صلى الله عليه وسلم - يقول لفاطمة رضي الله عنها وهي بضعة منه)','1534','2368','0');
INSERT INTO [t38171] VALUES (2414,'٢٣٩٨ - (قال رسول الله - صلى الله عليه وسلم - إن الشيطان يأتي أحدكم فيقول من خلقك فيقول الله تبارك وتعالى فيقول فمن خلق الله فإذا وجد أحدكم ذلك فليقل أمنت بالله ورسوله) (فإن ذلك يذهب عنه).','1535','2368','0');
INSERT INTO [t38171] VALUES (2415,'٢٣٩٩ - (فقال - صلى الله عليه وسلم - اتقوا مواضع التهم).','1536','2368','0');
INSERT INTO [t38171] VALUES (2416,'٢٤٠٠ - (عن عبد الرحمن بن أبي ليلى)','1536','2368','0');
INSERT INTO [t38171] VALUES (2417,'٢٤٠١ - (قال الحسن) البصري رحمه الله تعالى (نبئت أن جبريل عليه السلام أتى النبي - صلى الله عليه وسلم - فقال إن عفريتا من الجن يكيدك فإذا أويت إلى فراشك فاقرأ آية الكرسي).','1536','2368','0');
INSERT INTO [t38171] VALUES (2418,'٢٤٠٢ - (قال - صلى الله عليه وسلم - لقد أتاني الشيطان فنازعني) أي في الصلاة (ثم نازعني فأخذت بحلقه فوالذي بعثني بالحق ما أرسلته حتى وجدت برد ماء لسانه على يدي ولولا دعوة أخي سليمان عليه السلام لأصبح طريحا).','1537','2368','0');
INSERT INTO [t38171] VALUES (2419,'٢٤٠٣ - (قال - صلى الله عليه وسلم - ما سلك الشيطان فجا) أي طريقا (سلكه عمر)','1537','2368','0');
INSERT INTO [t38171] VALUES (2420,'٢٤٠٤ - (قد قال أبو أمامة الباهلي)','1538','2368','0');
INSERT INTO [t38171] VALUES (2421,'٢٤٠٥ - (عن أبي الدرداء) رضي الله عنه (قال قال رسول الله - صلى الله عليه وسلم - خلق الله الجن ثلاثة أصناف صنف حيات وعقارب وخشاش الأرض)','1538','2368','0');
INSERT INTO [t38171] VALUES (2422,'٢٤٠٦ - (ما رأى النبي - صلى الله عليه وسلم - جبريل عليه السلام في صورته) الحقيقة (إلا مرتين وذلك أنه سأله أن يريه نفسده على صورته فواعده بالبقيع وظهر له فسد الأفق من المشرق إلى المغرب ورآه أخرى على صورته ليلة المعراج عند سدرة المنتهي).','1539','2368','0');
INSERT INTO [t38171] VALUES (2423,'٢٤٠٧ - (إنما كان يراه في صورة الأدمي غالبا)','1541','2368','0');
INSERT INTO [t38171] VALUES (2424,'٢٤٠٨ - (كان يراه في صورة دحية الكلبي وكان) دحية (رجلا حسن الوجه)','1541','2368','0');
INSERT INTO [t38171] VALUES (2425,'٢٤٠٩ - (روى عن رسول الله - صلى الله عليه وسلم - أنه قال عفي لأمتي)','1541','2368','0');
INSERT INTO [t38171] VALUES (2426,'٢٤١٠ - (روي عن عثمان بن مظعون)','1542','2368','0');
INSERT INTO [t38171] VALUES (2427,'٢٤١١ - (روي عن رسول الله - صلى الله عليه وسلم - أنه قال إذا التقى المسلمان بسيفيهما) فقتل أحدهما صاحبه (فالقاتل والمقتول في النار فقيل يا رسول الله هذا القاتل) يستحق النار (فما بال المقتول) أي فما ذنبه (قال) - صلى الله عليه وسلم - (لأنه أراد قتل صاحبه).','1543','2368','0');
INSERT INTO [t38171] VALUES (2428,'٢٤١٢ - (لما نزل قوله تعالى)','1543','2368','0');
INSERT INTO [t38171] VALUES (2429,'٢٤١٣ - (قال رسول الله - صلى الله عليه وسلم - ما من عبد إلا وله أربعة أعين عينان في رأسه يبصر بهما أمر دنياه وعينان في قلبه يبصر بهما أمر دينه).','1545','2368','0');
INSERT INTO [t38171] VALUES (2430,'٢٤١٤ - (يقول لا ومقلب القلوب)','1545','2368','0');
INSERT INTO [t38171] VALUES (2431,'٢٤١٥ - (قال مثل القلب مثل العصفور يتقلب في كل ساعة).','1546','2368','0');
INSERT INTO [t38171] VALUES (2432,'٢٤١٦ - (قال) - صلى الله عليه وسلم - (مثل القلب في تقلبه كالقدر إذا استجمعت غليانا)','1546','2368','0');
INSERT INTO [t38171] VALUES (2433,'٢٤١٧ - (قال) - صلى الله عليه وسلم - (مثل القلب كمثل ريشة بأرض فلاة تقلبها الرياح ظهر البطن).','1547','2368','0');
INSERT INTO [t38171] VALUES (2434,'٢٤١٨ - (قال تعالى فيما يروي عنه نبينا - صلى الله عليه وسلم - هؤلاء في الجنة ولا أبالي في النار ولا أبالي).','1547','2368','0');
INSERT INTO [t38171] VALUES (2435,'كتاب رياضة النفس وتهذيب الخلق','1548','2367','0');
INSERT INTO [t38171] VALUES (2436,'٢٤١٩ - (قال - صلى الله عليه وسلم - تصل من قطعك وتعطي من حرمك) أي منعك (وتعفو عمن ظلمك).','1549','2435','0');
INSERT INTO [t38171] VALUES (2437,'٢٤٢٠ - (قال - صلى الله عليه وسلم - أثقل ما يوضع في الميزان خلق حسن).','1549','2435','0');
INSERT INTO [t38171] VALUES (2438,'٢٤٢١ - (جاء رجل إلى رسول الله - صلى الله عليه وسلم - من بين يديه فقال يا رسول الله ما الدين فقال حسن الخلق ثم أتاه من قبل يمينه فقال ما الدين قال حسن الخلق ثم أتاه من قبل شماله فقال ما الدين قال حسن الخلق ثم أتاه من ورائه فقال ما الدين فالتفت إليه وقال أما تفقه هو أن لا تغضب).','1550','2435','0');
INSERT INTO [t38171] VALUES (2439,'٢٤٢٢ - (قيل يا رسول الله ما الشؤم)','1550','2435','0');
INSERT INTO [t38171] VALUES (2440,'٢٤٢٣ - (قال رجل لرسول الله - صلى الله عليه وسلم - أوصني فقال اتق الله حيث كنت قال زدني قال اتبع السيئة الحسنة تمحها قال زدني قال خالق الناس بخلق حسن).','1551','2435','0');
INSERT INTO [t38171] VALUES (2441,'٢٤٢٤ - (وقال أبو الدرداء) رضي الله عنه (سمعت رسول الله - صلى الله عليه وسلم - يقول أول ما يوضع في الميزان حسن الخلق والسخاء ولما خلق الله الإيمان قال اللهم قوني فقواه بحسن الخلق والسخاء ولما خلق الله الكفر قال اللهم قوني فقواه بالبخل وسوء الخلق).','1551','2435','0');
INSERT INTO [t38171] VALUES (2442,'٢٤٢٥ - (وقال - صلى الله عليه وسلم - إن الله استخلص هذا الدين لنفسه ولا يصلح لدينكم إلا السخاء وحسن الخلق ألا فزينوا دينكم بهما)','1552','2435','0');
INSERT INTO [t38171] VALUES (2443,'٢٤٢٦ - (قال - صلى الله عليه وسلم - حسن الخلق خلق الله الأعظم) قال الحكيم','1552','2435','0');
INSERT INTO [t38171] VALUES (2444,'٢٤٢٧ - (وقال - صلى الله عليه وسلم - إنكم لن تسعوا الناس بأموالكم فسعوهم ببسط الوجه وحسن الخلق)','1553','2435','0');
INSERT INTO [t38171] VALUES (2445,'٢٤٢٨ - (قال) - صلى الله عليه وسلم - (أيضا سوء الخلق يفسد العمل كما يفسد الخل العسل)','1554','2435','0');
INSERT INTO [t38171] VALUES (2446,'٢٤٢٩ - (عن جرير بن عبد الله) البجلي رضي الله عنه (قال قال - صلى الله عليه وسلم - إنك امرؤ قد حسن الله خلقك فحسن خلقك)','1554','2435','0');
INSERT INTO [t38171] VALUES (2447,'٢٤٣٠ - (وعن البراء بن عازب) رضي الله عنهما (قال كان رسول الله - صلى الله عليه وسلم - أحسن الناس وجها وأحسنهم خلقا).','1554','2435','0');
INSERT INTO [t38171] VALUES (2448,'٢٤٣١ - (عن أبي مسعود) عقبة بن عامر الأنصاري (البدري) لنزوله بدرا لا لشهوده وقعتها (كان رسول الله - صلى الله عليه وسلم - يقول في دعائه اللهم حسنت خلقي) بفتح فسكون (فحسن خلقي)','1554','2435','0');
INSERT INTO [t38171] VALUES (2449,'٢٤٣٢ - (عن عبد الله بن عمرو) رضي الله عنهما (قال كان رسول الله - صلى الله عليه وسلم - يكثر الدعاء فيقول اللهم إني أسألك الصحة والعافية وحسن الخلق).','1555','2435','0');
INSERT INTO [t38171] VALUES (2450,'٢٤٣٣ - (عن أبي هريرة رضي الله عنه عن النبي - صلى الله عليه وسلم - قال كرم المؤمن دينه وحسبه حسن خلقه ومروءته عقله)','1555','2435','0');
INSERT INTO [t38171] VALUES (2451,'٢٤٣٤ - (عن ابن عباس) رضي الله عنهما (قال قال رسول الله - صلى الله عليه وسلم - ثلاث)','1556','2435','0');
INSERT INTO [t38171] VALUES (2452,'٢٤٣٥ - (وكان من دعائه - صلى الله عليه وسلم - في افتتاح الصلاة اللهم اهدني لأحسن الأخلاق لا يهدي لأحسنها إلا أنت واصرف عني سيئها لا يصرف عني سيئها إلا أنت)','1556','2435','0');
INSERT INTO [t38171] VALUES (2453,'٢٤٣٥/ أ- (قال أنس) رضي الله عنه (بينما نحن مع رسول الله - صلى الله عليه وسلم - يوما إذ قال إن حسن الخلق ليذيب الخطيئة)','1556','2452','0');
INSERT INTO [t38171] VALUES (2454,'٢٤٣٥/ أ- (قال - صلى الله عليه وسلم - من سعادة المرء حسن الخلق)','1557','2452','0');
INSERT INTO [t38171] VALUES (2455,'٢٤٣٦ - (وقال - صلى الله عليه وسلم - اليمن حسن الخلق).','1557','2435','0');
INSERT INTO [t38171] VALUES (2456,'٢٤٣٧ - (قال - صلى الله عليه وسلم - لأبي ذر) الغفاري رضي الله عنه (يا أبا ذر لا عقل كالتدبير) أي النظر في عواقب الأمور (ولا حسب كحسن الخلق).','1557','2435','0');
INSERT INTO [t38171] VALUES (2457,'٢٤٣٨ - (عن أنس) رضي الله عنه (قال قالت أم حبيبة) رملة بنت أبي سفيان إحدى أمهات المؤمنين رضي الله عنها (يا رسول الله أرأيت المرأة يكون لها زوجان في الدنيا) يتزوجها واحد بعد واحد (فتموت) هي (ويموتان ويدخلون الجنة لأيها تكون هى قال لأحسنهما خلقا كان عندها في الدنيا يا أم حبيبة ذهب حسن الخلق بخير الدنيا والآخرة).','1558','2435','0');
INSERT INTO [t38171] VALUES (2458,'٢٤٣٩ - (وقال - صلى الله عليه وسلم - إن المسلم المسدد)','1558','2435','0');
INSERT INTO [t38171] VALUES (2459,'٢٤٤٠ - (قال عبد الرحمن بن سمرة)','1558','2435','0');
INSERT INTO [t38171] VALUES (2460,'٢٤٤١ - (قال أنس) رضي الله عنه (قال النبي - صلى الله عليه وسلم - إن العبد ليبلغ بحسن خلقه عظيم درجات الآخرة وشرف المنازل وأنه ضعيف العبادة).','1559','2435','0');
INSERT INTO [t38171] VALUES (2461,'٢٤٤٢ - (قال - صلى الله عليه وسلم - سوء الخلق ذنب لا يغفر وسوء الظن خطيئة نتوج)','1559','2435','0');
INSERT INTO [t38171] VALUES (2462,'٢٤٤٣ - (قال - صلى الله عليه وسلم - إن العبد ليبلغ من سوء خلقه أسفل درك جهنم).','1559','2435','0');
INSERT INTO [t38171] VALUES (2463,'٢٤٤٤ - (قال - صلى الله عليه وسلم - حسنوا أخلاقكم).','1559','2435','0');
INSERT INTO [t38171] VALUES (2464,'٢٤٤٥ - (قال رسول الله - صلى الله عليه وسلم - إنما أنا بشر أغضب كما يغضب البشر).','1560','2435','0');
INSERT INTO [t38171] VALUES (2465,'٢٤٤٦ - (كان - صلى الله عليه وسلم - يتكلم بين يديه بما يكرهه فيغضب حتى تحمر وجنتاه ولكن لا يقول إلا حقا فكان الغضب لا يخرجه عن الحق).','1560','2435','0');
INSERT INTO [t38171] VALUES (2466,'٢٤٤٧ - (قال رسول الله - صلى الله عليه وسلم -: خير الأمور أوسطها).','1560','2435','0');
INSERT INTO [t38171] VALUES (2467,'٢٤٤٨ - (لما سئل رسول الله - صلى الله عليه وسلم - عن السعادة) ما هي (فقال طول العمر في طاعة الله).','1561','2435','0');
INSERT INTO [t38171] VALUES (2468,'٢٤٤٩ - (يرى أحدهم القذى)','1561','2435','0');
INSERT INTO [t38171] VALUES (2469,'٢٤٥٠ - (قال رسول الله - صلى الله عليه وسلم - المؤمن بين خمس شدائد مؤمن يحسده ومنافق يبغضه وكافر يقتله وشيطان يضله ونفيس تنازعه).','1562','2435','0');
INSERT INTO [t38171] VALUES (2470,'٢٤٥١ - (قال - صلى الله عليه وسلم - لقوم قدموا من الجهاد مرحبا بكم قدمتم من الجهاد الأصغر إلى الجهاد الأكبر فقالوا وما الجهاد الأكبر قال جهاد النفس).','1562','2435','0');
INSERT INTO [t38171] VALUES (2471,'٢٤٥٢ - (قال - صلى الله عليه وسلم - المجاهد من جاهد نفسه في الله عز وجل).','1562','2435','0');
INSERT INTO [t38171] VALUES (2472,'٢٤٥٣ - (قال - صلى الله عليه وسلم - كف أذاك عن نفسك ولا تتابع هواها في معصية الله إذا تخاصمك يوم القيامة فيلعن بعضك بعضا إلا أن يغفر الله تعالى كذلك ويستر).','1562','2435','0');
INSERT INTO [t38171] VALUES (2473,'٢٤٥٤ - (حب الدنيا رأس كل خطيئة)','1563','2435','0');
INSERT INTO [t38171] VALUES (2474,'٢٤٥٥ - (المؤمن يحب لأخيه ما يحب لنفسه)','1563','2435','0');
INSERT INTO [t38171] VALUES (2475,'٢٤٥٦ - (قال) - صلى الله عليه وسلم - (من كان يؤمن بالله واليوم الآخر فليكرم ضيفه)','1563','2435','0');
INSERT INTO [t38171] VALUES (2476,'٢٤٥٧ - (قال) - صلى الله عليه وسلم - (من كان يؤمن بالله واليوم الآخر فليكرم جاره)','1563','2435','0');
INSERT INTO [t38171] VALUES (2477,'٢٤٥٨ - (قال) - صلى الله عليه وسلم - (من كان يؤمن بالله واليوم الآخر فليقل خيرا أو ليصمت)','1563','2435','0');
INSERT INTO [t38171] VALUES (2478,'٢٤٥٩ - (قال - صلى الله عليه وسلم - إذا رأيتم المؤمن صموتا وقورا فادنوا منه فإنه يلقي الحكمة).','1564','2435','0');
INSERT INTO [t38171] VALUES (2479,'٢٤٦٠ - (قال - صلى الله عليه وسلم - من سرته حسنته أو ساءته سيئته فهو مؤمن).','1564','2435','0');
INSERT INTO [t38171] VALUES (2480,'٢٤٦١ - (قال - صلى الله عليه وسلم - لا يحل لمؤمن أن يشير إلى أخيه بنظرة يؤذيه).','1564','2435','0');
INSERT INTO [t38171] VALUES (2481,'٢٤٦٢ - (قال - صلى الله عليه وسلم - لا يحل لمسلم أن يروع مسلما).','1564','2435','0');
INSERT INTO [t38171] VALUES (2482,'٢٤٦٣ - (قال - صلى الله عليه وسلم - إنما يتجالس المتجالسان بأمانة الله) تعالى (فلا يحل لأحدهما أن يفشي على أخيه ما يكره)','1565','2435','0');
INSERT INTO [t38171] VALUES (2483,'٢٤٦٤ - (سئل رسول الله - صلى الله عليه وسلم - عن علامة المؤمن والمنافق فقال إن المؤمن همته في الصلاة والصيام والعبادة وإن المنافق همته في الطعام والشراب كالبهيمة).','1565','2435','0');
INSERT INTO [t38171] VALUES (2484,'٢٤٦٥ - (أما ترى أن نداء رسول الله - صلى الله عليه وسلم - بلغه وهو على هذه الصفة فقيل له يا أيها المزمل يا أيها المدثر).','1565','2435','0');
INSERT INTO [t38171] VALUES (2485,'٢٤٦٦ - (قال - صلى الله عليه وسلم - عليكم بدين العجائز).','1566','2435','0');
INSERT INTO [t38171] VALUES (2486,'كتاب كسر الشهوتين: شهوة البطن شهوة الفرج','1569','2367','0');
INSERT INTO [t38171] VALUES (2487,'٢٤٦٧ - (قال رسول الله - صلى الله عليه وسلم - جاهدوا أنفسكم بالجوع والعطش فإن الأجر في ذلك كأجر المجاهد في سبيل الله وإنه ليس من عمل أحب إلى الله من جوع وعطش).','1570','2486','0');
INSERT INTO [t38171] VALUES (2488,'٢٤٦٨ - (قال ابن عباس) رضي الله عنهما (قال رسول الله - صلى الله عليه وسلم - لا يدخل ملكوت السماء من ملأ بطنه).','1570','2486','0');
INSERT INTO [t38171] VALUES (2489,'٢٤٦٩ - (قيل يا رسول الله أي الناس أفضل قال من قل مطعمه وضحكه ورضي) من اللباس (بما يستر عورته).','1570','2486','0');
INSERT INTO [t38171] VALUES (2490,'٢٤٧٠ - (وقال رسول الله - صلى الله عليه وسلم - سيد الأعمال الجوع وذل النفس لباس الصوف).','1570','2486','0');
INSERT INTO [t38171] VALUES (2491,'٢٤٧١ - (وقال أبو سعيد الخدري) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - البسوا واشربوا وكلوا في أنصاف البطون فإنه جزء من النبوة).','1571','2486','0');
INSERT INTO [t38171] VALUES (2492,'٢٤٧٢ - (وقال الحسن البصري) رحمه الله تعالى مرسلا (قال النبي - صلى الله عليه وسلم - التفكر نصف العبادة وقلة الطعام هي العبادة).','1571','2486','0');
INSERT INTO [t38171] VALUES (2493,'٢٤٧٣ - (وقال النبي - صلى الله عليه وسلم - أفضلكم عند الله منزلة يوم القيامة أطولكم جوعا وتفكر وأبغضكم عند الله عز وجل يوم القيامة كل نؤم أكول شروب)','1571','2486','0');
INSERT INTO [t38171] VALUES (2494,'٢٤٧٤ - (وفي الخبران النبي - صلى الله عليه وسلم - كان يجوع من غير عوز أى مختار له)','1571','2486','0');
INSERT INTO [t38171] VALUES (2495,'٢٤٧٥ - (وقال - صلى الله عليه وسلم - إن الله تعالى يباهي الملائكة بمن قل مطعمه','1571','2486','0');
INSERT INTO [t38171] VALUES (2496,'٢٤٧٦ - (وقال - صلى الله عليه وسلم - لا تميتوا القلب بكثرة الطعام والشراب فإن القلب كالزرع يموت إذا كثر عليه الماء).','1572','2486','0');
INSERT INTO [t38171] VALUES (2497,'٢٤٧٧ - (وفي حديث أسامة بن زيد وأبي هريرة) رضي الله عنهما الطويل (ذكر فضيلة الجوع إذ قال فيه إن أقرب الناس من الله عز وجل من طال جوعه وعطشه وحزنه في الدنيا الأحفياء)','1572','2486','0');
INSERT INTO [t38171] VALUES (2498,'٢٤٧٨ - (روى الحسن) البصري رحمه الله تعالى (عن أبي هريرة) رضي الله عنه (أن رسول الله - صلى الله عليه وسلم - قال البسوا الصوف وشمروا وكلوا في أنصاف البطون تدخلوا في ملكوت السماء).','1573','2486','0');
INSERT INTO [t38171] VALUES (2499,'٢٤٧٩ - (قال عيسى عليه السلام يا معشر الحواريين أجيعوا أكبادكم)','1574','2486','0');
INSERT INTO [t38171] VALUES (2500,'٢٤٨٠ - (في خبر مرسل إن الشيطان ليجري من ابن آدم مجرى الدم فضيقوا مجاريه بالجوع والعطش).','1574','2486','0');
INSERT INTO [t38171] VALUES (2501,'٢٤٨١ - (وفي الخبر إن الأكل على الشبع يورث البرص)','1574','2486','0');
INSERT INTO [t38171] VALUES (2502,'٢٤٨٢ - (وقال عليه الصلاة والسلام المؤمن يأكل في معي','1574','2486','0');
INSERT INTO [t38171] VALUES (2503,'٢٤٨٣ - (روى) الحسن البصري (عن عائشة رضي الله عنها قالت سمعت رسول الله يقول أديموا قرع باب الجنة يفتح لكم قلت وكيف نديم قرع باب الجنة قال بالجوع والظمأ)','1576','2486','0');
INSERT INTO [t38171] VALUES (2504,'٢٤٨٤ - (روي أن أبا جحيفة) وهب بن عبد الله السوائي رضي الله عنه توفي رسول الله - صلى الله عليه وسلم - وهو مراهق (تجشأ في مجلس رسول الله - صلى الله عليه وسلم - فقال له اقتصر من جشائك فإن أطول الناس جوعا يوم القيامة أكثرهم شبعا في الدنيا)','1576','2486','0');
INSERT INTO [t38171] VALUES (2505,'٢٤٨٢ - (كانت عائشة رضي الله عنها تقول إن رسول الله - صلى الله عليه وسلم - لم يمتلئ قط شبعا وربما بكيت رحمة له مما أرى به من الجوع فامسح بطنه بيدي وأقول نفسي لك الفداء لو تبلغت من الدنيا بقدر ما يقوتك ويمنعك من الجوع فيقول يا عائشة إخواني من أولي العزم من الرسل قد صبروا على ما هو أشد من هذا فمضوا على حالهم فقدموا على ربهم فأكرم مآبهم وأجزل ثوابهم فأجدني أستحي إن ترفهت في معيشتي أن يقصر بي غدا دونهم فالصبر أياما يسيرة أحب إلي من أن ينقص حظي غدا في الآخرة وما من شيء أحب إلي من اللحوق بأصحابي وإخواني قالت فوالله ما استكمل بعد ذلك جمعة حتى قبضه الله إليه).','1577','2486','0');
INSERT INTO [t38171] VALUES (2506,'٢٤٨٦ - (وعن أنس) رضي الله عنه (قال جاءت فاطمة رضي الله عنها بكسرة خبز إلى رسول الله - صلى الله عليه وسلم - فقال ما هذه الكسرة قالت قرص خبزته لم تطب نفسي حتى أتيك بهذه الكسرة فقال أما إنه أول طعام دخل فم أبيك منذ ثلاثة أيام).','1577','2486','0');
INSERT INTO [t38171] VALUES (2507,'٢٤٨٧ - (قال - صلى الله عليه وسلم - إن أهل الجوع في الدنيا هم أهل الشبع في الآخرة وإن أبغض الناس إلى الله تعالى المتخمون الملآى) أي الذين يملؤن بطونهم من الطعام حتى يتخمون والتخمة فساد الطعام في المعدة (وما ترك عبد أكلة يشتهيها إلا كانت له درجة في الجنة).','1578','2486','0');
INSERT INTO [t38171] VALUES (2508,'٢٤٨٨ - (وأما الآثار فقد قال عمر رضي الله عنه إياكم والبطنة فإنها ثقل في الحياة نتن في الممات)','1578','2486','0');
INSERT INTO [t38171] VALUES (2509,'٢٤٨٩ - (قال رسول الله - صلى الله عليه وسلم - جاهدوا أنفسكم بالجوع والعطش','1578','2486','0');
INSERT INTO [t38171] VALUES (2510,'٢٤٩٠ - قال - صلى الله عليه وسلم - أحيوا قلوبكم بقلة الضحك وطهروها بالجوع تصفو وترق).','1579','2486','0');
INSERT INTO [t38171] VALUES (2511,'٢٤٩١ - (قال مثل الجوع مثل الرعد ومثل القناعة مثل السحاب والحكمة كالمطر)','1579','2486','0');
INSERT INTO [t38171] VALUES (2512,'٢٤٩٢ - (قال النبي - صلى الله عليه وسلم - من أجاع بطنه عظمت فكرته وفطن قلبه).','1579','2486','0');
INSERT INTO [t38171] VALUES (2513,'٢٤٩٣ - (وقال ابن عباس رضي الله عنهما قال النبي - صلى الله عليه وسلم - من شبع ونام قسا قلبه) أي غلظ واشتد ثم (قال) - صلى الله عليه وسلم - (لكل شيء زكاة وزكاة البدن الجوع).','1579','2486','0');
INSERT INTO [t38171] VALUES (2514,'٢٤٩٤ - (قال النبي - صلى الله عليه وسلم - نور الحكمة الجوع والتباعد من الله تعالى الشبع والقربة إلى الله عز وجل حب المساكين والدنو منهم ولا تشبعوا فينطفئ نور الحكمة من قلوبكم ومن بات يصلي في خفة من الطعام بات الحور حوله حتى يصبح).','1580','2486','0');
INSERT INTO [t38171] VALUES (2515,'٢٤٩٥ - (لما عرضت الدنيا وخزائنها على النبي - صلى الله عليه وسلم - قال لا بل أجوع يوما وأشبع يوما فإذا جعت صبرت وإذا شبعت شكرت أو كما قال)','1580','2486','0');
INSERT INTO [t38171] VALUES (2516,'٢٤٩٦ - (قال - صلى الله عليه وسلم - البطنة أصل الداء والحمية أصل الدواء وعودوا كل جسد ما اعتاد).','1580','2486','0');
INSERT INTO [t38171] VALUES (2517,'٢٤٩٧ - (في الحديث صوموا تصحوا).','1581','2486','0');
INSERT INTO [t38171] VALUES (2518,'٢٤٩٨ - (نظر رسول الله - صلى الله عليه وسلم - إلى سمين البطن فأومأ)','1581','2486','0');
INSERT INTO [t38171] VALUES (2519,'٢٤٩٩ - (كان أبو ذر) الغفاري (رضي الله عنه يقول طعامي في كل جمعة صاع من شعير على عهد رسول الله - صلى الله عليه وسلم - والله لا أزيد عليه شيئا حتى ألقاه فإني سمعته يقول أقربكم من منزلا يوم القيامة وأحبكم إلي من مات على ما هو عليه اليوم)','1582','2486','0');
INSERT INTO [t38171] VALUES (2520,'٢٥٠٢ - (روى أبو سعيد) مالك بن سنان (الخدري) الأنصاري رضي الله عنه (أن النبي - صلى الله عليه وسلم - كان إذا تغدى لم يتعش وإذا تعشى لم يتغد)','1583','2486','0');
INSERT INTO [t38171] VALUES (2521,'٢٥٠٣ - (وقال النبي - صلى الله عليه وسلم - لعائشة رضي الله عنها إياك والسرف فإن أكلتين في كل يوم من السرف)','1583','2486','0');
INSERT INTO [t38171] VALUES (2522,'٢٥٠٠ - (كان قوت أهل الصفة)','1584','2486','0');
INSERT INTO [t38171] VALUES (2523,'٢٥٠٤ - (عن أبي هريرة) رضي الله عنه (قال ما قام رسول الله - صلى الله عليه وسلم - قيامكم هذا قط وإن كان ليقوم حتى تزلع قدماه)','1584','2486','0');
INSERT INTO [t38171] VALUES (2524,'٢٥٠٥ - (قال - صلى الله عليه وسلم - شرار أمتي الذين يأكلون مخ الحنطة).','1584','2486','0');
INSERT INTO [t38171] VALUES (2525,'٢٥٠٦ - (قال - صلى الله عليه وسلم - شرار أمتي الذين غذوا بالنعيم وبنيت عليه أجسامهم وإنما همتهم أنواع الطعام وأنواع اللباس ويتشدقون في الكلام)','1585','2486','0');
INSERT INTO [t38171] VALUES (2526,'٢٥٠٧ - (روى نافع عن ابن عمر) رضي الله عنه (أنه كان مريضا فاشتهى سمكة طرية فالتمست له بالمدينة فلم توجد)','1585','2486','0');
INSERT INTO [t38171] VALUES (2527,'٢٥٠٨ - (قال - صلى الله عليه وسلم - إذا استد) بالسين المهملة وفي نسخة العراقي إذا سددت (كلب الجوع)','1586','2486','0');
INSERT INTO [t38171] VALUES (2528,'٢٥٠٩ - (في الخبر لا يستدير الرغيف ويوضع بين يديك حتى يعمل فيه ثلاثمائة وستون صانعا)','1587','2486','0');
INSERT INTO [t38171] VALUES (2529,'٢٥١٠ - (كفى بالمرء إسرافا أن يأكل من كل ما يشتهيه ويفعل','1587','2486','0');
INSERT INTO [t38171] VALUES (2530,'٢٥١١ - (وفي الحديث أذيبوا طعامكم) أي اهضموه (بالصلاة والذكر)','1588','2486','0');
INSERT INTO [t38171] VALUES (2531,'٢٥١٢ - (قوله - صلى الله عليه وسلم - خير الأمور أوسطها).','1588','2486','0');
INSERT INTO [t38171] VALUES (2532,'٢٥١٣ - (كان) - صلى الله عليه وسلم - (يدخل على أهله فيقول هل عندكم من شيء فإن قالوا نعم أكل وإن قالوا لا قال إني إذا لصائم).','1589','2486','0');
INSERT INTO [t38171] VALUES (2533,'٢٥١٤ - (كان) - صلى الله عليه وسلم - (يقدم إليه الشيء فيقول أما إني قد أردت الصوم ثم يأكل).','1589','2486','0');
INSERT INTO [t38171] VALUES (2534,'٢٥١٥ - (خرج - صلى الله عليه وسلم - يوما وقال إني صائم فقالت عائشة رضي الله عنها قد أهدي لنا حيس)','1589','2486','0');
INSERT INTO [t38171] VALUES (2535,'٢٥١٦ - (كان رسول الله - صلى الله عليه وسلم - يحب العسل ويأكله).','1589','2486','0');
INSERT INTO [t38171] VALUES (2536,'٢٥١٧ - (عن ابن عباس) رضي الله عنهما (في قوله تعالى ومن شر غاسق إذا وقب قال هو قيام الذكر)','1590','2486','0');
INSERT INTO [t38171] VALUES (2537,'٢٥١٨ - (قال - صلى الله عليه وسلم - النساء حبائل الشيطان).','1590','2486','0');
INSERT INTO [t38171] VALUES (2538,'٢٥١٩ - (روي في غريب الحديث أن رسول الله - صلى الله عليه وسلم - قال شكوت إلى جبريل ضعف الوقاع فأمرني بأكل الهريسة).','1591','2486','0');
INSERT INTO [t38171] VALUES (2539,'٢٥٢٠ - (قال - صلى الله عليه وسلم - ما تركت بعدي فتنة أضر على الرجال من النساء).','1591','2486','0');
INSERT INTO [t38171] VALUES (2540,'٢٥٢١ - (قال - صلى الله عليه وسلم - اتقوا فتنة الدنيا وفتنة النساء فإن أول فتنة بني إسرائيل كانت من النساء).','1591','2486','0');
INSERT INTO [t38171] VALUES (2541,'٢٥٢٢ - (قال - صلى الله عليه وسلم - لكل ابن آدم حظه من الزنا فالعينان تزنيان وزناهما النظر واليدان تزنيان وزناهما البطش والرجلان تزنيان وزناهما المشي والفم يزني وزناه القبل والقلب يهم ويتمنى ويصدق ذلك الفرج أو يكذبه).','1591','2486','0');
INSERT INTO [t38171] VALUES (2542,'٢٥٢٣ - (قالت أم سلمة)','1592','2486','0');
INSERT INTO [t38171] VALUES (2543,'٢٥٢٤ - (قال - صلى الله عليه وسلم - من عشق فعف فكتم فمات فهو شهيد).','1592','2486','0');
INSERT INTO [t38171] VALUES (2544,'٢٥٢٥ - (قال - صلى الله عليه وسلم - لك الأولى وعليك الثانية أي النظرة).','1595','2486','0');
INSERT INTO [t38171] VALUES (2545,'كتاب اللسان','1596','2367','0');
INSERT INTO [t38171] VALUES (2546,'٢٥٢٦ - (قال - صلى الله عليه وسلم - من صمت نجا)','1597','2545','0');
INSERT INTO [t38171] VALUES (2547,'٢٥٢٧ - (قال عليه السلام الصمت حكم وقليل فاعله).','1597','2545','0');
INSERT INTO [t38171] VALUES (2548,'٢٥٢٨ - (روي عن عبد الله بن سفيان)','1597','2545','0');
INSERT INTO [t38171] VALUES (2549,'٢٥٢٩ - (قال عقبة بن عامر)','1598','2545','0');
INSERT INTO [t38171] VALUES (2550,'٢٥٣٠ - (قال سهل بن سعد)','1598','2545','0');
INSERT INTO [t38171] VALUES (2551,'٢٥٣١ - (قال - صلى الله عليه وسلم - من وقي شر قبقبه وذبذبه ولقلقه فقد وقي الشر كله).','1599','2545','0');
INSERT INTO [t38171] VALUES (2552,'٢٥٣٢ - (سئل رسول الله - صلى الله عليه وسلم - عن أكثر ما يدخل) الناس (الجنة فقال تقوى الله وحسن الخلق وسئل عن أكثر ما يدخل) الناس (النار فقال الأجوفان الفم والفرج).','1600','2545','0');
INSERT INTO [t38171] VALUES (2553,'٢٥٣٣ - (قال معاذ بن جبل) رضي الله عنه (قلت يا رسول الله أنؤاخذ بما نقول فقال ثكلتك أمك وهل يكب الناس في النار على مناخرهم إلا حصائد ألسنتهم).','1600','2545','0');
INSERT INTO [t38171] VALUES (2554,'٢٥٣٤ - (وقال عبد الله الثقفي) هو عبد الله بن سفيان بن عبد الله بن الحارث بن ربيعة الثقفي الطائفي الذي تقدم ذكره قريبا (قلت يا رسول الله حدثني بأمر أعتصم به فقال قل ربي ثم استقم فقال قلت يا رسول الله ما أخوف ما تخاف علي فأخذ بلسانه وقال هذا).','1600','2545','0');
INSERT INTO [t38171] VALUES (2555,'٢٥٣٥ - (وقال أنس بن مالك) رضي الله عنه قال (- صلى الله عليه وسلم - لا يستقيم إيمان العبد حتى يستقيم قلبه ولا يستقيم قلبه حتى يستقيم لسانه ولا يدخل الجنة رجل لا يأمن جاره يوثقه).','1601','2545','0');
INSERT INTO [t38171] VALUES (2556,'٢٥٣٦ - (وقال - صلى الله عليه وسلم - من سره أن يسلم)','1601','2545','0');
INSERT INTO [t38171] VALUES (2557,'٢٥٣٧ - (وعن سعيد بن جبير مرفوعا إلى رسول الله - صلى الله عليه وسلم - أنه قال إذا أصبح ابن آدم أصبحت الأعضاء كلها تذكر اللسان أي تقول اتق الله فينا فإنك إن استقمت استقمنا وإن اعوججت اعوججنا).','1602','2545','0');
INSERT INTO [t38171] VALUES (2558,'٢٥٣٨ - (روي أن عمر بن الخطاب رضي الله عنه رأى أبا بكر الصديق رضي الله عنه وهو يمد لسانه بيده فقال له ما تصنع يا خليفة رسول الله قال هذا أوردني الموارد)','1602','2545','0');
INSERT INTO [t38171] VALUES (2559,'٢٥٣٩ - (عن عبد الله بن مسعود)','1603','2545','0');
INSERT INTO [t38171] VALUES (2560,'٢٥٤٠ - (قال ابن عمر) رضي الله عنهما (قال رسول الله - صلى الله عليه وسلم - من كف لسانه)','1604','2545','0');
INSERT INTO [t38171] VALUES (2561,'٢٥٤١ - (روي أن معاذ بن جبل) رضي الله عنه (قال يا رسول الله أوصني قال اعبد الله كأنك تراه وعد نفسك في الموتى وإن شئت أنبأتك بما هو أملك لك من هذا كله وأشار بيده إلى لسانه).','1604','2545','0');
INSERT INTO [t38171] VALUES (2562,'٢٥٤٢ - (وعن صفوان بن سليم)','1605','2545','0');
INSERT INTO [t38171] VALUES (2563,'٢٥٤٣ - (وقال أبو هريرة) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - من كان يؤمن بالله واليوم الآخر فليقل خيرا أو ليسكت)','1605','2545','0');
INSERT INTO [t38171] VALUES (2564,'٢٥٤٤ - (وقال الحسن) البصري رحمه الله تعالى (ذكر لنا أن النبي - صلى الله عليه وسلم - قال رحم الله عبدا قال فغنم أو سكت فسلم)','1606','2545','0');
INSERT INTO [t38171] VALUES (2565,'٢٥٤٥ - (عن البراء) بن عازب رضي الله عنه (قال جاء أعرابي إلى رسول الله - صلى الله عليه وسلم - فقال دلني على عمل يدخلني الجنة قال أطعم الجائع واسق الظمآن وأمر بالمعروف وانه عن المنكر فإن لم تطق','1606','2545','0');
INSERT INTO [t38171] VALUES (2566,'٢٥٤٦ - (قال - صلى الله عليه وسلم - اخزن لسانك إلا من خير فإنك بذلك تغلب الشيطان).','1607','2545','0');
INSERT INTO [t38171] VALUES (2567,'٢٥٤٧ - (قال - صلى الله عليه وسلم - إن الله عند لسان كل قائل)','1607','2545','0');
INSERT INTO [t38171] VALUES (2568,'٢٥٤٨ - (قال - صلى الله عليه وسلم - إذا رأيتم المؤمن صموتا) أي كثير الصمت (فاقتربوا منه فإنه يلقن الحكمة).','1608','2545','0');
INSERT INTO [t38171] VALUES (2569,'٢٥٤٩ - (قال ابن مسعود)','1608','2545','0');
INSERT INTO [t38171] VALUES (2570,'٢٥٥٠ - (قال - صلى الله عليه وسلم - إن لسان المؤمن وراء قلبه فإذا أراد أن يتكلم بشيء تدبره بقلبه ثم أمضاه بلسانه وإن لسان المنافق أمام قلبه فإذا هم بشيء أمضاه بلسانه ولم يتدبره بقلبه).','1608','2545','0');
INSERT INTO [t38171] VALUES (2571,'٢٥٥١ - (قال نبينا - صلى الله عليه وسلم - من كثر كلامه كثر سقطه ومن كثر سقطه كثرت ذنوبه ومن كثرت ذنوبه كانت النار أولى به).','1609','2545','0');
INSERT INTO [t38171] VALUES (2572,'٢٥٥٢ - (المؤمن لا يكون صمته إلا فكرا و)','1610','2545','0');
INSERT INTO [t38171] VALUES (2573,'٢٥٥٤ - (قال أنس) بن مالك رضي الله عنه (استشهد غلام منا) أي من الأنصار (يوم أحد فوجدنا على بطنه حجرا مربوطا) أي من الجوع (فمسحت أمه عن وجهه التراب وقالت هنيئا لك يا بني فقال - صلى الله عليه وسلم - ما يدريك لعله كان يتكلم فيما لا يعنيه ويمنع ما لا يضره).','1612','2545','0');
INSERT INTO [t38171] VALUES (2574,'٢٥٥٥ - (في حديث أخر أن النبي - صلى الله عليه وسلم - فقد كعبا) أي ابن عجرة (فسأل عنه فقالوا) هو (مريض فخرج يمشى حتى أتاه) عائدا له (فلما دخل عليه قال أبشر يا كعب فقالت أمه هنيئا لك الجنة فقال - صلى الله عليه وسلم - من هذه المتألية على الله قال) كعب (هي أمي يا رسول الله قال وما يدريك يا أم كعب لعل كعبا قال ما لا يعنيه أو منع ما لا يغنيه).','1612','2545','0');
INSERT INTO [t38171] VALUES (2575,'٢٥٥٦ - (عن محمد بن كعب)','1613','2545','0');
INSERT INTO [t38171] VALUES (2576,'٢٥٥٧ - (قال أبو ذر)','1613','2545','0');
INSERT INTO [t38171] VALUES (2577,'٢٥٥٨ - (قال - صلى الله عليه وسلم - طوبى لمن أمسك الفضل من لسانه وأنفق الفضل من ماله).','1614','2545','0');
INSERT INTO [t38171] VALUES (2578,'٢٥٥٩ - (عن مطرف بن عبد الله)','1615','2545','0');
INSERT INTO [t38171] VALUES (2579,'٢٥٦٠ - (قال عمرو بن دينار) المكي التابعي ثقة (تكلم رجل عند رسول الله - صلى الله عليه وسلم - فأكثر فقال له - صلى الله عليه وسلم - كم دون لسانك من باب فقال شفتاي وأسناني قال أفما كان لك في ذلك ما يرد كلامك)','1616','2545','0');
INSERT INTO [t38171] VALUES (2580,'٢٥٦١ - (قال بلا بن الحارث قال رسول الله - صلى الله عليه وسلم - إن الرجل ليتكلم بالكلمة من رضوان الله ما يظن أن تبلغ به ما بلغت فيكتب الله بها رضوانه إلى يوم القيامة وإن الرجل ليتكلم بالكلمة من سخط الله ما يظن أن تبلغ به ما بلغت فيكتب الله عليه بها سخطه إلى يوم القيامة).','1616','2545','0');
INSERT INTO [t38171] VALUES (2581,'٢٥٦٢ - (قال - صلى الله عليه وسلم - إن الرجل ليتكلم بالكلمة) الواحدة لأجل أن (يضحك بها جلساءه يهوى)','1617','2545','0');
INSERT INTO [t38171] VALUES (2582,'٢٥٦٣ - (قال - صلى الله عليه وسلم - أعظم الناس خطايا يوم القيامة أكثرهم خوضا في الباطل).','1618','2545','0');
INSERT INTO [t38171] VALUES (2583,'٢٥٦٤ - (قال - صلى الله عليه وسلم - لا تمار أخاك ولا تمازحه ولا تعده موعدا فتخلفه).','1618','2545','0');
INSERT INTO [t38171] VALUES (2584,'٢٥٦٥ - (وقال - صلى الله عليه وسلم - ذروا المراء) أي اتركوه (فإنه لا تفهم حكمته ولا تؤمن فتنته).','1618','2545','0');
INSERT INTO [t38171] VALUES (2585,'٢٥٦٦ - (وقال - صلى الله عليه وسلم - من ترك المراء وهو محق بني له بيت في أعلى الجنة ومن ترك المراء وهو مبطل بني له بيت في ربض الجنة)','1619','2545','0');
INSERT INTO [t38171] VALUES (2586,'٢٥٦٧ - (أم سلمة) أم المؤمنين (رضي الله عنها قالت قال رسول الله - صلى الله عليه وسلم - إن أول ما عهد إلي ربي ونهاني عنه بعد عبادة الأوثان وشرب الخمر ملاحاة الرجال).','1619','2545','0');
INSERT INTO [t38171] VALUES (2587,'٢٥٦٨ - (قال) - صلى الله عليه وسلم - (أيضا ما ضل قوم إلا أوتوا الجدل).','1619','2545','0');
INSERT INTO [t38171] VALUES (2588,'٢٥٦٩ - (قال) - صلى الله عليه وسلم - (أيضا ست) خصال (من كن فيه بلغ حقيقة الإيمان الصيام في الصيف)','1620','2545','0');
INSERT INTO [t38171] VALUES (2589,'٢٥٧٠ - (قال) - صلى الله عليه وسلم - (أيضا لا يستكمل عبد حقيقة الإيمان حتى يذر المراء وإن كان محقا).','1620','2545','0');
INSERT INTO [t38171] VALUES (2590,'٢٥٧١ - (قال - صلى الله عليه وسلم - يكفر كل لحاء ركعتان)','1621','2545','0');
INSERT INTO [t38171] VALUES (2591,'٢٥٧٢ - (قال - صلى الله عليه وسلم - رحم الله من كف لسانه عن أهل القبلة إلا بأحسن ما يقدر عليه).','1621','2545','0');
INSERT INTO [t38171] VALUES (2592,'٢٥٧٣ - (قالت عائشة رضي الله عنها قال رسول الله - صلى الله عليه وسلم - إن أبغض الرجال إلى الله الألد الخصم).','1621','2545','0');
INSERT INTO [t38171] VALUES (2593,'٢٥٧٤ - (قال أبو هريرة) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - من جادل في خصومة من غير علم لم يزل في سخط الله حتى ينزع).','1622','2545','0');
INSERT INTO [t38171] VALUES (2594,'٢٥٧٥ - (قال - صلى الله عليه وسلم - يمكنكم من الجنة طيب الكلام وإطعام الطعام).','1622','2545','0');
INSERT INTO [t38171] VALUES (2595,'٢٥٧٦ - (قال أنس) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - إن في الجنة غرفا يرى ظاهرها من باطنها وباطنها من ظاهرها أعدها الله نعالى لمن أطعم الطعام وألان الكلام).','1623','2545','0');
INSERT INTO [t38171] VALUES (2596,'٢٥٧٧ - (قال نبينا - صلى الله عليه وسلم - الكلمة الطيبة صدقة).','1623','2545','0');
INSERT INTO [t38171] VALUES (2597,'٢٥٧٨ - (قال) - صلى الله عليه وسلم - (اتقوا النار ولو بشق تمرة فإن لم تجدوا فبكلمة طيبة).','1623','2545','0');
INSERT INTO [t38171] VALUES (2598,'٢٥٧٩ - (قال فيه النبي - صلى الله عليه وسلم - أنا وأتقياء أمتي برآء من التكلف).','1624','2545','0');
INSERT INTO [t38171] VALUES (2599,'٢٥٨٠ - (قال - صلى الله عليه وسلم - إن أبغضكم إلى الله وأبعدكم مني مجلسا الثرثارون المتفيقهون المشدقون في الكلام).','1624','2545','0');
INSERT INTO [t38171] VALUES (2600,'٢٥٨١ - (وقالت فاطمة رضي الله عنها)','1624','2545','0');
INSERT INTO [t38171] VALUES (2601,'٢٥٨٢ - (وقال - صلى الله عليه وسلم - ألا هلك المتنطعون ثلاث مرات)','1625','2545','0');
INSERT INTO [t38171] VALUES (2602,'٢٥٨٣ - (جاء عمر بن سعد بن أبي وقاص)','1625','2545','0');
INSERT INTO [t38171] VALUES (2603,'٢٥٨٤ - (قال - صلى الله عليه وسلم - إياكم والفحش فإن الله تعالى لا يحب الفحش ولا التفحش).','1625','2545','0');
INSERT INTO [t38171] VALUES (2604,'٢٥٨٥ - (نهى رسول الله - صلى الله عليه وسلم - عن أن تسب قتلى بدر من المشركين فقال لا تسبوا هؤلاء فإنهم لا يخلص إليهم شيء مما تقولون وتؤذون الأحياء ألا إن البذاء لؤم).','1626','2545','0');
INSERT INTO [t38171] VALUES (2605,'٢٥٨٦ - (وقال - صلى الله عليه وسلم - أربعة يؤذون أهل النار في النار على ما بهم من الأذى يسعون بين الحميم والجحيم يدعون بالويل والثبور)','1626','2545','0');
INSERT INTO [t38171] VALUES (2606,'٢٥٨٧ - (قال - صلى الله عليه وسلم - ليس المؤمن بالطعان ولا اللعان ولا الفاحش ولا البذيء).','1627','2545','0');
INSERT INTO [t38171] VALUES (2607,'٢٥٨٨ - (قال - صلى الله عليه وسلم - الجنة حرام على كل فاحش أن يدخلها)','1628','2545','0');
INSERT INTO [t38171] VALUES (2608,'٢٥٨٩ - (وقال - صلى الله عليه وسلم - لعائشة) رضي الله عنها (يا عائشة لو كان الفحش رجلا كان رجل سوء).','1628','2545','0');
INSERT INTO [t38171] VALUES (2609,'٢٥٩٠ - (قال - صلى الله عليه وسلم - البذاء) يروى بكسر الموحدة وبفتحها ممدودا (والبيان شعبتان من شعب النفاق).','1629','2545','0');
INSERT INTO [t38171] VALUES (2610,'٢٥٩١ - (قال - صلى الله عليه وسلم - إن الله لا يحب الفاحش المتفحش الصياح في الأسواق).','1629','2545','0');
INSERT INTO [t38171] VALUES (2611,'٢٥٩٢ - (قال جابر بن سمرة)','1630','2545','0');
INSERT INTO [t38171] VALUES (2612,'٢٥٩٣ - (قال أعرابي لرسول الله - صلى الله عليه وسلم - أوصني فقال عليك بتقوى الله وإن امرؤ عيرك) أي عابك (بشيء يعلمه فيك فلا تعيره بشيء تعلمه) أنت (فيه يكن وباله عليه وأجره لك ولا تسبن شيئا قال) الأعرابي (فما سببت شيئا بعده).','1631','2545','0');
INSERT INTO [t38171] VALUES (2613,'٢٥٩٤ - (المتسابان شيطانان).','1631','2545','0');
INSERT INTO [t38171] VALUES (2614,'٢٥٩٥ - (وقال - صلى الله عليه وسلم - المستبان ما قالا)','1631','2545','0');
INSERT INTO [t38171] VALUES (2615,'٢٥٩٦ - (وقال - صلى الله عليه وسلم - سباب)','1632','2545','0');
INSERT INTO [t38171] VALUES (2616,'٢٥٩٧ - (وفي رواية من أكبر الكبائر أن يسب الرجل والديه قالوا يا رسول الله كيف يسب الرجل والديه قال يسب أبا الرجل فيسب الآخر أباه).','1633','2545','0');
INSERT INTO [t38171] VALUES (2617,'٢٥٩٨ - قال العراقي:','1633','2545','0');
INSERT INTO [t38171] VALUES (2618,'٢٥٩٩ - (قال - صلى الله عليه وسلم - المؤمن ليس بلعان).','1633','2545','0');
INSERT INTO [t38171] VALUES (2619,'٢٦٠٠ - (قال - صلى الله عليه وسلم - لا تلاعنوا)','1634','2545','0');
INSERT INTO [t38171] VALUES (2620,'٢٦٠١ - (قال حذيفة) بن اليمان رضي الله عنه (ما تلاعن قوم قط إلا حق عليهم القول)','1634','2545','0');
INSERT INTO [t38171] VALUES (2621,'٢٦٠٢ - (قال عمران بن حصين) رضي الله عنه (بينما رسول الله - صلى الله عليه وسلم - في بعض أسفاره إذ امرأة من الأنصار على ناقة لها فضجرت منها) أي لسوء سيرها (فلعنتها فقال رسول الله - صلى الله عليه وسلم - خذوا ما عليها) من الأثقال (وأعروها) بقطع الهمزة (فإنها ملعونة قال) عمران رضي الله عنه (فكأني أنظر إلى تلك الناقة تمشي بين الناس ولا يتعرض لها أحد).','1634','2545','0');
INSERT INTO [t38171] VALUES (2622,'٢٦٠٣ - (قالت عائشة رضي الله عنها سمع رسول الله - صلى الله عليه وسلم - أبا بكر) رضي الله عنه (وهو يلعن بعض رقيقه فالتفت إليه فقال يا أبا بكر ألعانين وصديقين كلا ورب الكعبة) قال ذلك (مرتين أو ثلاثا فأعتق أبو بكر يومئذ بعض رقيقه وأتى النبي - صلى الله عليه وسلم - وقال لا أعود).','1635','2545','0');
INSERT INTO [t38171] VALUES (2623,'٢٦٠٤ - (قال - صلى الله عليه وسلم - إن اللعانين لا يكونون شفعاء ولا شهداء يوم القيامة).','1635','2545','0');
INSERT INTO [t38171] VALUES (2624,'٢٦٠٤/ أ- (وقال أنس) رضي الله عنه (كان رجل يسير مع النبي - صلى الله عليه وسلم - على بعير فلعن بعيره فقال له النبي - صلى الله عليه وسلم - يا عبد الله لا تسر معنا على بعير ملعون).','1636','2623','0');
INSERT INTO [t38171] VALUES (2625,'٢٦٠٥ - (روي أنه) - صلى الله عليه وسلم - (كان يلعن الذين قتلوا أصحاب بئر معونة في قنوته شهرا فنزل قوله تعالى ليس لك من الأمر شيء أو يتوب عليهم أو يعذبهم فإنهم ظالمون يعني أنهم ربما يسلمون فمن أين تعلم أنهم ملعونون).','1636','2545','0');
INSERT INTO [t38171] VALUES (2626,'٢٦٠٦ - (روي أن رسول لله - صلى الله عليه وسلم - سأل أبا بكر رضي الله عنه عن قبر مر به وهو يريد الطائف فقال)','1637','2545','0');
INSERT INTO [t38171] VALUES (2627,'٢٦٠٧ - (قال - صلى الله عليه وسلم - لا يرمى رجل رجلا بالكفر ولا يرميه بالفسق إلا ارتدت عليه إن لم يكن صاحبه كذلك).','1638','2545','0');
INSERT INTO [t38171] VALUES (2628,'٢٦٠٨ - (قال - صلى الله عليه وسلم - ما شهد رجل على رجل بالكفر إلا باء به أحدهما إن كان كافرا فهو كما قال وإن لم يكن كافرا فقد كفر بتكفيره).','1638','2545','0');
INSERT INTO [t38171] VALUES (2629,'٢٦٠٩ - (قال معاذ) بن جبل رضي الله عنه (قال لي رسول الله - صلى الله عليه وسلم - أنهاك أن تشتم مسلما أو تعصي أماما عادلا).','1638','2545','0');
INSERT INTO [t38171] VALUES (2630,'٢٦١٠ - (قال مسروق) بن الأجدع بن مالك الهمداني الوداعي أبو عائشة الكوفي ثقة فقيه عابد مخضرم مات سنة اثنين وستين (دخلت على عائشة رضي الله عنها فقالت ما فعل فلان لعنه الله قلت توفي قالت رحمه الله قلت وكيف هذا قالت قال رسول الله - صلى الله عليه وسلم - لا تسبوا الأموات فإنهم قد أفضوا إلى ما قدموا).','1639','2545','0');
INSERT INTO [t38171] VALUES (2631,'٢٦١١ - (قال - صلى الله عليه وسلم - لا تسبوا الأموات فتؤذوا الأحياء)','1639','2545','0');
INSERT INTO [t38171] VALUES (2632,'٢٦١٢ - (وقال - صلى الله عليه وسلم - أيها الناس احفظوني في أصحابي وإخواني وأصهاري ولا تسبوهم أيها الناس إذا مات الميت فاذكروا منه خيرا).','1640','2545','0');
INSERT INTO [t38171] VALUES (2633,'٢٦١٣ - (قال رجل لرسول الله أوصني فقال أوصيك أن لا تكون لعانا).','1641','2545','0');
INSERT INTO [t38171] VALUES (2634,'٢٦١٤ - (قال بعضهم لعن المؤمن كعدل قتله وقال حماد بن زيد)','1641','2545','0');
INSERT INTO [t38171] VALUES (2635,'٢٦١٥ - (عن أبي قتادة) الحارث بن ربعي بن بلدمة السلمي بفتحتين المدني شهد أحدا وما بعدها ومات سنة أربع وخمسين (قال كان يقال من لعن مؤمنا فهو مثل أن يقتله وقد نقل ذلك مرفوعا إلى رسول الله - صلى الله عليه وسلم -).','1642','2545','0');
INSERT INTO [t38171] VALUES (2636,'٢٦١٦ - (قال رسول الله - صلى الله عليه وسلم - لأن يمتلئ بطن)','1642','2545','0');
INSERT INTO [t38171] VALUES (2637,'٢٦١٧ - (وعن مسروق) بن الأجدع الهمداني التابعي الثقة (أنه سئل عن بيت من الشعر فكرهه)','1643','2545','0');
INSERT INTO [t38171] VALUES (2638,'٢٦١٨ - (قالت عائشة رضي الله عنها كان رسول الله - صلى الله عليه وسلم - يخصف نعله وكنت جالسة أغزل فنظرت)','1645','2545','0');
INSERT INTO [t38171] VALUES (2639,'٢٦١٩ - (ولما قسم رسول الله - صلى الله عليه وسلم - الغنائم يوم حنين)','1646','2545','0');
INSERT INTO [t38171] VALUES (2640,'٢٦٢٠ - (قال - صلى الله عليه وسلم - لا تمار أخاك ولا تمازحه).','1647','2545','0');
INSERT INTO [t38171] VALUES (2641,'٢٦٢١ - (روي عن النبي - صلى الله عليه وسلم - أنه قال إني آمزح ولا أقول إلا حقا)','1648','2545','0');
INSERT INTO [t38171] VALUES (2642,'٢٦٢٢ - (وقال عمر) رضى الله عنه (من كثر ضحكه قلت هيبته)','1648','2545','0');
INSERT INTO [t38171] VALUES (2643,'٢٦٢٣ - (قال - صلى الله عليه وسلم - لو علمتم ما أعلم لبكيتم كثيرا)','1649','2545','0');
INSERT INTO [t38171] VALUES (2644,'٢٦٢٤ - (قال القاسم موسى معاوية)','1652','2545','0');
INSERT INTO [t38171] VALUES (2645,'٢٦٢٥ - (قال عمر رضي الله عنه من مزح استخف به)','1652','2545','0');
INSERT INTO [t38171] VALUES (2646,'٢٦٢٦ - (قال عمر بن عبد العزيز) رحمه الله تعالى (اتقوا الله وإياكم والمزاح فإنه يورث الضغينة ويجر إلى القبيح تحدثوا بالقرآن وتجالسوا به فإن ثقل عليكم فحديث حسن من حديث الرجال)','1653','2545','0');
INSERT INTO [t38171] VALUES (2647,'٢٦٢٧ - (وقال عطاء) بن أيى رباح (إن رجلا سأل ابن عباس)','1653','2545','0');
INSERT INTO [t38171] VALUES (2648,'٢٦٢٨ - (وعن الحسن) البصري رحمه الله تعالى (قال أتت عجوز)','1654','2545','0');
INSERT INTO [t38171] VALUES (2649,'٢٦٢٩ - (روى زيد بن أسلم)','1654','2545','0');
INSERT INTO [t38171] VALUES (2650,'٢٦٣٠ - (وجاءت امرأة أخرى فقالت يا رسول الله - صلى الله عليه وسلم - احملني على بعير فقال بل نحملك على ابن البعير فقالت ما أصنع به إنه لا يحملني فقال - صلى الله عليه وسلم - وهل بعير إلا وهو ابن بعير فكان يمزح به).','1654','2545','0');
INSERT INTO [t38171] VALUES (2651,'٢٦٣١ - (وقال أنس) رضي الله عنه (كان لأبي طلحة)','1655','2545','0');
INSERT INTO [t38171] VALUES (2652,'٢٦٣٢ - (وقالت عائشة رضي الله عنها خرجت مع رسول الله - صلى الله عليه وسلم - في غزوة بدر فقال تعالى حتى أسابقك فشددت علي درعي) وفي نسخة فشددت درعي على بطني (ثم خططنا خطا فقمنا عليه واستبقنا فسبقني وقال هذه مكان ذي المجاز)','1655','2545','0');
INSERT INTO [t38171] VALUES (2653,'٢٦٣٣ - (وقالت) عائشة رضي الله عنها (أيضا كان عندي رسول الله - صلى الله عليه وسلم - وسودة بنت زمعة)','1655','2545','0');
INSERT INTO [t38171] VALUES (2654,'٢٦٣٤ - (وروى أن الضحاك بن سفيان)','1656','2545','0');
INSERT INTO [t38171] VALUES (2655,'٢٦٣٥ - (وروى)','1657','2545','0');
INSERT INTO [t38171] VALUES (2656,'٢٦٣٦ - (وقال - صلى الله عليه وسلم - مرة لصهيب)','1658','2545','0');
INSERT INTO [t38171] VALUES (2657,'٢٦٣٧ - (وروي أن خوات بن جبير)','1658','2545','0');
INSERT INTO [t38171] VALUES (2658,'٢٦٣٨ - (وكان نعيمان)','1659','2545','0');
INSERT INTO [t38171] VALUES (2659,'٢٦٣٩ - (قال وكان (نعيمان المذكور (لا يدخل المدينة رسل ولا طرفة إلا اشترى منها ثم جاء به إلى النبي - صلى الله عليه وسلم - ويقول هذا أهديته لك فإذا جاء به إلى النبي - صلى الله عليه وسلم - ويقول هذا أهديته لك فإذا جاء صاحبه يطلب نعيمان بثمنه)','1660','2545','0');
INSERT INTO [t38171] VALUES (2660,'٢٦٤٠ - (قالت عائشة) رضي الله عنها (حكيت إنسانا فقال النبي - صلى الله عليه وسلم - ما أحب أني حكيت إنسانا وأن لي كذا وكذا).','1660','2545','0');
INSERT INTO [t38171] VALUES (2661,'٢٦٤١ - (وقال ابن عباس) رضي الله عنه (في قوله) تعالى (يا ويلتنا ما لهذا الكتاب لا يغادر صغيرة ولا كبيرة إلا أحصاها الصغيرة ابتسم بالاستهزاء بالمؤمن والكبيرة القهقهة بذلك)','1661','2545','0');
INSERT INTO [t38171] VALUES (2662,'٢٦٤٢ - (وعن عبد الله بن زمعة)','1661','2545','0');
INSERT INTO [t38171] VALUES (2663,'٢٦٤٤ - (وقال معاذ بن جبل) رضي الله عنه (من عير أخاه بذنب قد تاب منه لم يمت حتى يعمله).','1662','2545','0');
INSERT INTO [t38171] VALUES (2664,'٢٦٤٥ - (قال رسول الله - صلى الله عليه وسلم - إذا حدث الرجل بحديث ثم التفت فهي أمانة).','1663','2545','0');
INSERT INTO [t38171] VALUES (2665,'٢٦٤٦ - (قال) - صلى الله عليه وسلم - (طلق الحديث بينكم أمانة)','1663','2545','0');
INSERT INTO [t38171] VALUES (2666,'٢٦٤٧ - (وقال الحسن) البصري رحمه الله تعالى (إن من الخيانة أن تحدث بسر أخيك)','1663','2545','0');
INSERT INTO [t38171] VALUES (2667,'٢٦٤٨ - (ويروى أن معاوية) بن أبي سفيان رضي الله عنه (أسر إلى الوليد بن عتبة)','1663','2545','0');
INSERT INTO [t38171] VALUES (2668,'٢٦٤٩ - (وقال - صلى الله عليه وسلم - العدة عطية)','1664','2545','0');
INSERT INTO [t38171] VALUES (2669,'٢٦٥٠ - (قال) - صلى الله عليه وسلم - (الوأي مثل الدين أو أفضل والوأي الوعد).','1665','2545','0');
INSERT INTO [t38171] VALUES (2670,'٢٦٥١ - (وقد أثنى الله تعالى على نبيه إسماعيل عليه السلام فقال إنه كان صادق الوعد وكان رسولا نبيا فيقال إنه واعد إنسانا في موضع فلم يرجع إليه فبقى اثنين وعشرين يوما في انتظاره)','1665','2545','0');
INSERT INTO [t38171] VALUES (2671,'٢١٥٢ - (ولما حضرت عبد الله بن عمرو) بن العاص رضي الله عنهما (الوفاة قال إنه كان خطب إلي ابنتي رجل من قريش وقد كان مني إليه شبه الوعد فوالله لا ألقى الله بثلث النفاق)','1665','2545','0');
INSERT INTO [t38171] VALUES (2672,'٢٦٥٣ - (وعن عبد الله بن أبي الحمساء)','1666','2545','0');
INSERT INTO [t38171] VALUES (2673,'٢٦٥٤ - (وقيل لإبراهيم) النخعي (الرجل يواعد الرجل الميعاد فلا يجيء قال ينتظره ما بينه وبين أن يدخل وقت الصلاة التي تجيء)','1667','2545','0');
INSERT INTO [t38171] VALUES (2674,'٢٦٥٥ - (وكان رسول الله - صلى الله عليه وسلم - إذا وعد وعدا قال عسى).','1667','2545','0');
INSERT INTO [t38171] VALUES (2675,'٢٦٥٦ - (وكان) ابن مسعود رضي الله عنه (لا يعد وعدا إلا ويقول إن شاء الله).','1667','2545','0');
INSERT INTO [t38171] VALUES (2676,'٢٦٥٧ - (قال أبو هريرة) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - ثلاث من كن فيه) أبي ثلاث خصال من وجدت فيه (فهو منافق وإن صام وصلى وزعم أنه مسلم إذا حدث كذب وإذا وعد أخلف وإذا ائتمن خان).','1667','2545','0');
INSERT INTO [t38171] VALUES (2677,'٢٦٥٨ - (وقال عبد الله بن عمرو) بن العاص رضي الله عنهما (قال رسول الله - صلى الله عليه وسلم - أربع من كن فيه كان منافقا ومن كان فيه خلة منهن كانت فيه خلة من النفاق حتى يدعها)','1668','2545','0');
INSERT INTO [t38171] VALUES (2678,'٢٦٥٩ - (فقد روي أنه - صلى الله عليه وسلم - كان وعد أبا الهيثم)','1670','2545','0');
INSERT INTO [t38171] VALUES (2679,'٢٦٦٠ - (كان رسول الله - صلى الله عليه وسلم - جالسا يقسم غنائم هوازن بحنين)','1671','2545','0');
INSERT INTO [t38171] VALUES (2680,'٢٦٦١ - (قال النبي - صلى الله عليه وسلم - ليس الخلف أن يعد الرجل الرجل ومن نيته أن يفي)','1673','2545','0');
INSERT INTO [t38171] VALUES (2681,'٢٦٦٢ - (قال إسماعيل بن أوسط)','1673','2545','0');
INSERT INTO [t38171] VALUES (2682,'٢٦٦٣ - (قال أبو أمامة) صدي بن عجلان الباهلي رضي الله عنه','1674','2545','0');
INSERT INTO [t38171] VALUES (2683,'٢٦٦٣/ أ - (وقال الحسن) البصري رحمه الله تعالى (كان يقال إن من النفاق اختلاف السر والعلانية و) اختلاف (القول والعمل و) اختلاف (المدخل والمخرج وإن الأصل الذي بني عليه النفاق الكذب)','1675','2682','0');
INSERT INTO [t38171] VALUES (2684,'٢٦٦٤ - (قال - صلى الله عليه وسلم - كبرت خيانة) ثانيه باعتبار الضمير وهو فاعل معنى (أن تحدث أخاك) في الدين وإن لم يكن أخاك في النسب (حديثا هو لك به مصدق وأنت له به كاذب).','1675','2545','0');
INSERT INTO [t38171] VALUES (2685,'٢٦٦٥ - (قال ابن مسعود) رضي الله عنه (قال النبي - صلى الله عليه وسلم - لا يزال العبد يكذب ويتحرى الكذب حتى يكتب عند الله كذابا).','1676','2545','0');
INSERT INTO [t38171] VALUES (2686,'٢٦٦٦ - (ومر رسول الله - صلى الله عليه وسلم - برجلين يتبايعان شاة ويتحالفان يقول أحدهما والله لا انقصك من كذا وكذا ويقول الآخر والله لا أزيدك على كذا وكذا فمر بالشاة وقد اشتراها أحدهما فقال أوجب أحدهما بالإثم والكفارة).','1676','2545','0');
INSERT INTO [t38171] VALUES (2687,'٢٦٦٧ - (قال - صلى الله عليه وسلم - الكذب ينقص الرزق).','1676','2545','0');
INSERT INTO [t38171] VALUES (2688,'٢٦٦٨ - (وقال - صلى الله عليه وسلم - إن التجار هم الفجار فقيل يا رسول الله قد أحل البيع قال نعم ولكنهم يحلفون فيأثمون ويحدثون فيكذبون).','1677','2545','0');
INSERT INTO [t38171] VALUES (2689,'٢٦٦٩ - (قال - صلى الله عليه وسلم - ثلاثة نفر لا يكلمهم الله) (يوم القيامة)','1677','2545','0');
INSERT INTO [t38171] VALUES (2690,'٢٦٧٠ - (قال - صلى الله عليه وسلم - ما حلف حالف بالله فأدخل فيها مثل جناح بعوضة إلا كانت نكتة في قلبه إلى يوم القيامة).','1678','2545','0');
INSERT INTO [t38171] VALUES (2691,'٢٦٧١ - (قال أبو ذر) الغفاري رضي الله عنه (ثلاثة) من الناس (يحبهم الله رجل كان في فئة)','1678','2545','0');
INSERT INTO [t38171] VALUES (2692,'٢٦٧٢ - (قال - صلى الله عليه وسلم - ويل للذي يحدث) الناس (فيكذب)','1680','2545','0');
INSERT INTO [t38171] VALUES (2693,'٢٦٧٣ - (وقال - صلى الله عليه وسلم - رأيت كأن رجلا جاءني فقال لي قم فقمت معه وإذا أنا برجلين أحدهما نائم والآخر جالس بيد القائم كلوب من حديد)','1680','2545','0');
INSERT INTO [t38171] VALUES (2694,'٢٦٧٤ - (عن عبد الله بن جراد)','1680','2545','0');
INSERT INTO [t38171] VALUES (2695,'٢٦٧٥ - (قال أبو سعيد) الخدري رضي الله عنه (سمعت رسول الله - صلى الله عليه وسلم - يدعو ويقول)','1681','2545','0');
INSERT INTO [t38171] VALUES (2696,'٢٦٧٦ - (قال - صلى الله عليه وسلم - ثلاثة) من الناس (لا يكلمهم الله) (ولا ينظر إليهم)','1682','2545','0');
INSERT INTO [t38171] VALUES (2697,'٢٦٧٧ - (قال) أبو محمد (عبد الله بن عامر)','1682','2545','0');
INSERT INTO [t38171] VALUES (2698,'٢٦٧٨ - (قال - صلى الله عليه وسلم - لو أفاء الله علي نعما) أي إبلا (عدد هذا الحصى) وفي لفظ عدد هذه العضاء (لقسمتها بينكم ثم لا تجدوني بخيلا ولا كذابا ولا جبانا)','1683','2545','0');
INSERT INTO [t38171] VALUES (2699,'٢٦٧٩ - (قال - صلى الله عليه وسلم - وكان متكئا) على وسادة (ألا أنبئكم بأكبر','1683','2545','0');
INSERT INTO [t38171] VALUES (2700,'٢٦٨٠ - (قال ابن عمر) رضي الله عنه (قال النبي - صلى الله عليه وسلم - إن العبد ليكذب الكذبة فيتباعد الملك عنه مسيرة ميل من نتن ما جاء به).','1684','2545','0');
INSERT INTO [t38171] VALUES (2701,'٢٦٨١ - (قال أنس) بن مالك رضي الله عنه (قال النبي - صلى الله عليه وسلم - تقبلوا لي بست)','1685','2545','0');
INSERT INTO [t38171] VALUES (2702,'٢٦٨٢ - (قال - صلى الله عليه وسلم - إن للشيطان كحلا)','1686','2545','0');
INSERT INTO [t38171] VALUES (2703,'٢٦٨٣ - (خطب عمر بن الخطاب) رضي الله عنه (بالجابية)','1687','2545','0');
INSERT INTO [t38171] VALUES (2704,'٢٥٨٤ - (قال - صلى الله عليه وسلم - من حدث)','1687','2545','0');
INSERT INTO [t38171] VALUES (2705,'٢٦٨٥ - (قال - صلى الله عليه وسلم - من حلف على يمين)','1688','2545','0');
INSERT INTO [t38171] VALUES (2706,'٢٦٨٦ - (روي أن النبي - صلى الله عليه وسلم - رد شهادة رجل كذبة كذبها).','1689','2545','0');
INSERT INTO [t38171] VALUES (2707,'٢٦٨٧ - (قال - صلى الله عليه وسلم - على كل خصلة يطبع)','1690','2545','0');
INSERT INTO [t38171] VALUES (2708,'٢٦٨٨ - (قالت عائشة) رضي الله عنها (ما كان من خلق أشد عند أصحاب رسول الله - صلى الله عليه وسلم - من الكذب ولقد كان رسول الله - صلى الله عليه وسلم - يطلع على الرجل من أصحابه على الكذبة فما تنحل من صدره حتى يعلم أنه قد أحدث لله عز وجل منها توبة).','1691','2545','0');
INSERT INTO [t38171] VALUES (2709,'٢٦٨٩ - (قال موسى عليه السلام يا رب أي عبادك خير عملا قال من لا يكذب لسانه ولا يفجر قلبه ولا يزني فرجه).','1691','2545','0');
INSERT INTO [t38171] VALUES (2710,'٢٦٩٠ - (قال لقمان) لابنه (يا بني إياك والكذب فإنه شهي كلحم العصفور عما قليل يقلاه صاحبه)','1692','2545','0');
INSERT INTO [t38171] VALUES (2711,'٢٦٩١ - (قال - صلى الله عليه وسلم - في مدح الصدق أربع) خصال (إذا كن فيك فلا يضرك ما فاتك من الدنيا)','1692','2545','0');
INSERT INTO [t38171] VALUES (2712,'٢٦٩٢ - (قال أبو بكر رضي الله عنه في خطبته بعد وفاة رسول الله - صلى الله عليه وسلم - قام فينا رسول الله - صلى الله عليه وسلم - مقامي هذا عام أول ثم بكى)','1693','2545','0');
INSERT INTO [t38171] VALUES (2713,'٢٦٩٣ - (قال معاذ) بن جبل رضي الله عنه (قال رسول الله - صلى الله عليه وسلم -) لي أوصيك بتقوى الله وصدق الحديث والوفاء بالعهد وبذل السلام وخفض الجناح).','1693','2545','0');
INSERT INTO [t38171] VALUES (2714,'٢٦٩٤ - (قال علي رضي الله عنه أعظم الخطايا)','1694','2545','0');
INSERT INTO [t38171] VALUES (2715,'٢٦٩٥ - (قال عمر بن عبد العزيز) رحمه الله تعالى (ما كذبت كذبة منذ شددت علي إزاري)','1695','2545','0');
INSERT INTO [t38171] VALUES (2716,'٢٦٩٦ - (عن عمر) بن الخطاب (رضي الله عنه) قال (أحبكم إلينا ما لم نركم أحسنكم أسماء فإذا رأيناكم فأحبكم إلينا أحسنكم خلقا فإذا اختبرناكم فأحبكم إلينا أصدقكم حديثا وأعظمكم أمانة)','1695','2545','0');
INSERT INTO [t38171] VALUES (2717,'٢٦٩٧ - (عن ميمون بن أبي شبيب)','1695','2545','0');
INSERT INTO [t38171] VALUES (2718,'٢٦٩٨ - (قال) عامر بن شراحيل (الشعبي) رحمه الله تعالى (ما أدري أيهما أبعد غورا في النار الكذب أو البخل)','1696','2545','0');
INSERT INTO [t38171] VALUES (2719,'٢٦٩٩ - (قال) محمد بن صبيح (بن السماك) البغدادي الواعظ (ما أراني أؤجر) أي أثاب (على ترك الكذب لأني إنما أدعه) أي أتركه (أنفة).','1696','2545','0');
INSERT INTO [t38171] VALUES (2720,'٢٧٠٠ - (وقيل لخالد بن صبيح)','1696','2545','0');
INSERT INTO [t38171] VALUES (2721,'٢٧٠١ - (قال) أبو يحيى (مالك بن دينار)','1696','2545','0');
INSERT INTO [t38171] VALUES (2722,'٢٧٠٢ - وقال مالك بن دينار) رحمه الله تعالى (الصدق والكذب يعتركان في القلب حتى يخرج أحدهما صاحبه)','1697','2545','0');
INSERT INTO [t38171] VALUES (2723,'٢٧٠٣ - (كلم عمر بن عبد العزيز) رحمه الله تعالى (الوليد) بن عبد الملك بن مروان (في شيء فقال له الوليد كذبت فقال عمر ما كذبت منذ علمت أن الكذب يشين صاحبه)','1698','2545','0');
INSERT INTO [t38171] VALUES (2724,'٢٧٠٤ - (قال ميمون بن مهران) الجزري الثقة كاتب عمر بن عبد العزيز (إن الكذب في بعض المواطن خير أرأيت لو أن رجلا سعى وآخر وراءه بالسيف فدخل دارا فانتهى إليك فقال أرأيت فلانا ما كنت قائلا ألست تقول لم أره وما تصدق فهذا الكذب واجب)','1698','2545','0');
INSERT INTO [t38171] VALUES (2725,'٢٧٠٥ - (روي عن أم كلثوم)','1698','2545','0');
INSERT INTO [t38171] VALUES (2726,'٢٧٠٦ - (قالت أم كلثوم) أيضا (قال رسول الله - صلى الله عليه وسلم - ليس بكذاب من أصلح بين اثنين فقال خيرا أو نمى خيرا)','1699','2545','0');
INSERT INTO [t38171] VALUES (2727,'٢٧٠٧ - (قالت أسماء بنت يزيد) بن السكن الأنصارية بنت عمة معاذ روى لها الأربعة (أن رسول الله - صلى الله عليه وسلم - قال كل الكذب يكتب على ابن آدم إلا رجل كذب بين رجلين)','1699','2545','0');
INSERT INTO [t38171] VALUES (2728,'٢٧٠٨ - (روي عن أبي كاهل)','1700','2545','0');
INSERT INTO [t38171] VALUES (2729,'٢٧٠٩ - (وقال عطاء بن يسار) أبو محمد الهلالي المدني ثقة روى له الجماعة (قال رجل للنبي - صلى الله عليه وسلم - أكذب أهلي قال لا خير في الكذب قال أعدها) وعدا (وأقول لها) كذا وكذا أمنيها (قال لا جناح عليك)','1700','2545','0');
INSERT INTO [t38171] VALUES (2730,'٢٧١٠ - (يروى أن ابن أبي عذرة الدؤلي وكان في خلافة عمر)','1701','2545','0');
INSERT INTO [t38171] VALUES (2731,'٢٧١١ - (عن النواس بن سمعان) بن خالد العامري (الكلابي) رضي الله عنه (قال ما لي أراكم تتهافتون في الكذب','1701','2545','0');
INSERT INTO [t38171] VALUES (2732,'٢٧١٢ - (وقال ثوبان) رضي الله عنه (الكذب كله إثم إلا ما نفع به مسلم أو دفع عنه)','1702','2545','0');
INSERT INTO [t38171] VALUES (2733,'٢٧١٣ - (قال علي رضي الله عنه إذا حدثتكم عن رسول الله - صلى الله عليه وسلم - فلأن أخر)','1702','2545','0');
INSERT INTO [t38171] VALUES (2734,'٢٧١٤ - (قال رسول الله - صلى الله عليه وسلم - من ارتكب شيئا من هذه القاذورات)','1703','2545','0');
INSERT INTO [t38171] VALUES (2735,'٢٧١٥ - (قالت أسماء)','1703','2545','0');
INSERT INTO [t38171] VALUES (2736,'٢٧١٦ - (وقال - صلى الله عليه وسلم - من تطعم بما لم يطعم وقال) هذا (لي وليس له وأعطيت ولم يعط كان كلابس ثوبب زور يوم القيامة).','1704','2545','0');
INSERT INTO [t38171] VALUES (2737,'٢٧١٧ - (وقد نقل عن السلف) قولهم (إن في المعاريض','1704','2545','0');
INSERT INTO [t38171] VALUES (2738,'٢٧١٨ - (قال عمر رضي الله عنه) في معنى ذلك (في المعاريض ما يكفي الرجل عن الكذب)','1705','2545','0');
INSERT INTO [t38171] VALUES (2739,'٢٧٢٠ - (قال رسول الله - صلى الله عليه وسلم - لا يستكمل المؤمن إيمانه حتى يحب لأخيه ما يحب لنفسه وحتى يجتنب الكذب في مزاحه).','1706','2545','0');
INSERT INTO [t38171] VALUES (2740,'٢٧٢١ - (قال مجاهد) بن جبر المكي التابعي الثقة (قالت أسماء بنت عميس)','1706','2545','0');
INSERT INTO [t38171] VALUES (2741,'٢٧٢٢ - (عن عبد الرحمن بن سلمة (وقال) أبو الحارث (الليث بن سعد)','1707','2545','0');
INSERT INTO [t38171] VALUES (2742,'٢٧٢٣ - (جاءت أخت الربيع بن خثيم) الثوري الكوفي العابد (عائدة)','1708','2545','0');
INSERT INTO [t38171] VALUES (2743,'٢٧٢٤ - (قال عيسى عليه السلام إن من أعظم الذنوب عند الله تعالى أن يقول العبد إن الله يعلم لما لا يعلم).','1708','2545','0');
INSERT INTO [t38171] VALUES (2744,'٢٧٢٥ - (قال رسول الله - صلى الله عليه وسلم - إن من أعظم الفرى)','1708','2545','0');
INSERT INTO [t38171] VALUES (2745,'٢٧٢٦ - (وقال - صلى الله عليه وسلم - من كذب في حلمه) بضم فسكون أي في منامه (كلف يوم القيامة أن يعقد شعيرة)','1709','2545','0');
INSERT INTO [t38171] VALUES (2746,'٢٧٢٧ - (وقال - صلى الله عليه وسلم - كل) مبتدأ (المسلم) فيه رد على من زعم أن كلا لا تضاف إلا إلى نكرة (على المسلم حرام)','1709','2545','0');
INSERT INTO [t38171] VALUES (2747,'٢٧٢٨ - (قال أبو هريرة) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - لا تحاسدوا ولا تباغضوا ولا يغتب بعضكم بعضا وكونوا عباد الله إخوانا)','1710','2545','0');
INSERT INTO [t38171] VALUES (2748,'٢٧٢٩ - (وعن جابر) بن عبد الله (وأبي سعيد) الخدري رضي الله عنهما (قالا قال رسول الله - صلى الله عليه وسلم - إياكم والغيبة فإن الغيبة أشد من الزنا)','1710','2545','0');
INSERT INTO [t38171] VALUES (2749,'٢٧٣٠ - (وقال أنس) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - مررت ليلة أسري بي على قوم يخمشون)','1711','2545','0');
INSERT INTO [t38171] VALUES (2750,'٢٧٣١ - (وقال سليم بن جابر) أبو جري الهجيمي وقيل سليم بن جابر صحابي مشهور كان ينزل البدو وتقدم ذكره قريبا (أتيت رسول الله - صلى الله عليه وسلم - فقلت علمني خبرا ينفعني الله به قال لا تحقرن من المعروف شيئا ولو أن تصب من دلوك في إناء المستسقى وأن تلقى أخاك ببشر حسن)','1712','2545','0');
INSERT INTO [t38171] VALUES (2751,'٢٧٣٢ - (وقال البراء) بن عازب رضي الله عنه (خطبنا رسول الله - صلى الله عليه وسلم - حتى أسمع العواتق)','1712','2545','0');
INSERT INTO [t38171] VALUES (2752,'٢٧٣٣ - (قال أنس) بن مالك رضي الله عنه (أمر رسول الله - صلى الله عليه وسلم - الناس بصوم يوم)','1713','2545','0');
INSERT INTO [t38171] VALUES (2753,'٢٧٣٤ - (وفي رواية) أخرى (أنه) - صلى الله عليه وسلم - (لما أعرض عنه جاءه بعد ذلك وقال يا رسول الله إنهما والله قد ماتتا أو كادتا أن تموتا فقال النبي - صلى الله عليه وسلم - ائتوني بهما فجاءتاه فدعا رسول الله - صلى الله عليه وسلم - بعس أو)','1714','2545','0');
INSERT INTO [t38171] VALUES (2754,'٢٧٣٥ - (وقال أنس) بن مالك رضي الله عنه (خطبتا رسول الله - صلى الله عليه وسلم - فذكر الربا وعظم شأنه فقال إن الدرهم يصيبه الرجل من الربا أعظم عند الله في الخطيئة من ست وثلاثين زنية يزنيها الرجل)','1715','2545','0');
INSERT INTO [t38171] VALUES (2755,'٢٧٣٦ - (قال جابر) بن عبد الله رضي الله عنه (كنا مع رسول الله - صلى الله عليه وسلم - في مسير) أي سفر نسير معه فيه (فأتى على قبرين يعذب صاحبهما فقال ألا إنهما لا يعذبان في كبيرة)','1716','2545','0');
INSERT INTO [t38171] VALUES (2756,'٢٧٣٧ - (لما رجم رسول الله - صلى الله عليه وسلم - الرجل في الزنا)','1717','2545','0');
INSERT INTO [t38171] VALUES (2757,'٢٧٣٨ - (وقال أبو هريرة) رضي الله عنه (من أكل لحم أخيه في الدنيا قرب إليه لحمه في الآخرة فقيل كله ميتا كما أكلته حيا فيأكله ويضج)','1717','2545','0');
INSERT INTO [t38171] VALUES (2758,'٢٧٣٩ - (إن رجلين كانا قاعدين عند باب من أبواب المسجد)','1718','2545','0');
INSERT INTO [t38171] VALUES (2759,'٢٧٤٠ - (عن مجاهد) بن جبر المكي التابعي الثقة (قال) في قوله تعالى (ويل لكل همزة لمزة الهمزة الطعان في الناس) أي في أعراضهم (واللمزة الذي يأكل لحوم الناس)','1718','2545','0');
INSERT INTO [t38171] VALUES (2760,'٢٧٤١ - (قال قتادة) بن دعامة السدوسي أبو الخطاب البصري (ذكر لنا أن عذاب القبر ثلاثة أثلاث ثلث من الغيبة وثلث من البول وثلث من النميمة)','1718','2545','0');
INSERT INTO [t38171] VALUES (2761,'٢٧٤٢ - (وقال حسن البصري) رحمه الله تعالى (للغيبة أسرع في دين المؤمن من الأكلة في الجسد)','1718','2545','0');
INSERT INTO [t38171] VALUES (2762,'٢٧٤٣ - (قال بعضهم أدركنا السلف وهم لا يرون العبادة في الصوم ولا في الصلاة ولكن في الكف عن أعراض الناس)','1719','2545','0');
INSERT INTO [t38171] VALUES (2763,'٢٧٤٤ - (وقال ابن عباس) رضي الله عنه (إذا أردت أن تذكر عيوب صاحبك فاذكر عيوبك)','1719','2545','0');
INSERT INTO [t38171] VALUES (2764,'٢٧٤٥ - (وقال أبو هريرة) رضي الله عنه (يبصر أحدهم القذي في عين أخيه ولا يبصر الجذل في عينه).','1719','2545','0');
INSERT INTO [t38171] VALUES (2765,'٢٧٤٦ - (كان الحسن) البصري رحمه الله تعالى (يقول ابن آدم إنك لن تصيب حقيقة الإيمان حتى لا تعيب الناس بعيب هو فيك وحتى تبدأ بصلاح ذلك العيب فتصلحه من نفسك فإذا فعلت ذلك كان شغلك في خاصة نفسك وأحب العباد إلى الله من كان هكذا)','1719','2545','0');
INSERT INTO [t38171] VALUES (2766,'٢٧٤٧ - (وقال مالك بن دينار) رحمه الله تعالى (مر عيسى بن مريم) عليه السلام (والحواريون)','1720','2545','0');
INSERT INTO [t38171] VALUES (2767,'٢٧٤٨ - (سمع علي بن الحسين) بن علي بن أبي طالب رحمه الله تعالى (رجلا يغتاب آخر فقال إياك والغيبة فإنها إدام كلاب الناس)','1720','2545','0');
INSERT INTO [t38171] VALUES (2768,'٢٧٤٩ - (قال عمر رضي الله عنه عليكم بذكر الله فإنه شفاء وإياكم وذكر الناس فإنه داء)','1720','2545','0');
INSERT INTO [t38171] VALUES (2769,'٢٧٥٠ - (روي أنه ذكر لرسول الله - صلى الله عليه وسلم - امرأة وكثرة صومها وصلاتها لكنها تؤذي جيرانها) وتلبسهم وتسبهم (فقال هي في النار).','1723','2545','0');
INSERT INTO [t38171] VALUES (2770,'٢٧٥١ - (روي أن النبي - صلى الله عليه وسلم - قال هل تدرون ما الغيبة قالوا الله ورسوله أعلم قال ذكرك أخاك)','1723','2545','0');
INSERT INTO [t38171] VALUES (2771,'٢٧٥٢ - (وقال معاذ بن جبل) رضي الله عنه (ذكر رجل عند رسول الله - صلى الله عليه وسلم - فقالوا ما أعجزه فقال رسول الله - صلى الله عليه وسلم - اغتبتم صاحبكم قالوا يا رسول الله - صلى الله عليه وسلم - قلنا ما فيه قال إن قلتم ما ليس فيه فقد بهتموه).','1724','2545','0');
INSERT INTO [t38171] VALUES (2772,'٢٧٥٣ - (عن حذيفة عن عائشة) رضي الله عنها (أنها ذكرت امرأة فقالت إنها لقصيرة فقال النبي - صلى الله عليه وسلم - اغتبتيها).','1725','2545','0');
INSERT INTO [t38171] VALUES (2773,'٢٧٥٤ - (قال الحسن) البصري رحمه الله تعالى (ذكر غيرك ثلاثة الغيبة والبهتان والإفك والكل) مذكور (في كتاب الله الغيبة أن تقول ما فيه والإفك أن تقول ما بلغك والبهتان أن تقول ما ليس فيه)','1725','2545','0');
INSERT INTO [t38171] VALUES (2774,'٢٧٥٥ - (قالت عائشة) رضي الله عنها (لا يغتابن منكم أحد أحدا فإني قلت لامرأة مرة وأنا عند النبي - صلى الله عليه وسلم - إن هذه لطويلة الذيل فقال النبي - صلى الله عليه وسلم - الفظي الفظي فلفظت بضعة من لحم).','1726','2545','0');
INSERT INTO [t38171] VALUES (2775,'٢٧٥٦ - (وذكر) ابن سيرين (إبراهيم النخعي) وكان أعور (فوضع يده على عينه ولم يقل الأعور).','1726','2545','0');
INSERT INTO [t38171] VALUES (2776,'٢٧٥٧ - (وذكر) محمد (بن سيرين) رحمه الله تعالى (رجلا فقال ذلك الرجل الأسود ثم قال استغفر الله إني أراني قد اغتبته).','1726','2545','0');
INSERT INTO [t38171] VALUES (2777,'٢٧٥٨ - (قول عائشة رضي الله عنها دخلت علينا امرأة) وعندنا النبي - صلى الله عليه وسلم - (فلما ولت) أي انصرفت مولية ظهرها (أومأت) أي أشرت (بيدي) وفي رواية بإبهامي (أنها قصيرة) قصر الإبهام (فقال - صلى الله عليه وسلم - قد اغتبتيها).','1727','2545','0');
INSERT INTO [t38171] VALUES (2778,'٢٧٥٩ - (كان رسول الله - صلى الله عليه وسلم - إذا كره من إنسان شيئا قال ما بال أقوام يفعلون كذا وكذا)','1727','2545','0');
INSERT INTO [t38171] VALUES (2779,'٢٧٦٠ - (قال رسول الله - صلى الله عليه وسلم - المستمع أحد المغتابين)','1727','2545','0');
INSERT INTO [t38171] VALUES (2780,'٢٧٦١ - (روي عن أبي بكر وعمر رضي الله عنهما أن أحدهما قال لصاحبه إن فلانا لنؤم)','1728','2545','0');
INSERT INTO [t38171] VALUES (2781,'٢٧٦٢ - (قال رسول الله - صلى الله عليه وسلم - من أذل)','1729','2545','0');
INSERT INTO [t38171] VALUES (2782,'٢٧٦٣ - (وقال أبو الدرداء) رضي الله عنه (قال النبي - صلى الله عليه وسلم - من رد عن عرض أخيه بالغيب)','1729','2545','0');
INSERT INTO [t38171] VALUES (2783,'٢٧٦٤ - (وقال) - صلى الله عليه وسلم - (أيضا من ذب عن عرض أخيه بالغيب كان حقا على الله أن يعتقه من النار).','1730','2545','0');
INSERT INTO [t38171] VALUES (2784,'٢٧٦٥ - (ووى عامر بن واثلة)','1730','2545','0');
INSERT INTO [t38171] VALUES (2785,'٢٧٦٦ - (قال - صلى الله عليه وسلم -) والله (ما النار في اليبس بأسرع من الغيبة في حسنات العبد).','1731','2545','0');
INSERT INTO [t38171] VALUES (2786,'٢٧٦٧ - (قوله - صلى الله عليه وسلم - طوبى لمن شغله عيبه عن عيوب الناس).','1731','2545','0');
INSERT INTO [t38171] VALUES (2787,'٢٧٦٨ - (قال - صلى الله عليه وسلم - إن لجهنم بابا) أي عظيم المشقة (لا يدخل منه (وفي رواية لا يدخله (إلا من شفى غيظه بمعصية الله تعالى)','1732','2545','0');
INSERT INTO [t38171] VALUES (2788,'٢٧٦٩ - (وقال - صلى الله عليه وسلم - من اتقى ربه كل لسانه ولم يشف غيظه).','1732','2545','0');
INSERT INTO [t38171] VALUES (2789,'٢٧٧٠ - (وقال - صلى الله عليه وسلم - من كظم غيظا وهو يقدر على أن يمضيه دعاه الله يوم القيامة على رؤس الأشهاد حتى يخيره في أي الحور شاء).','1732','2545','0');
INSERT INTO [t38171] VALUES (2790,'٢٧٧١ - (قال - صلى الله عليه وسلم - إن الله حرم من المسلم دمه وماله وإن يظن به ظن السوء).','1733','2545','0');
INSERT INTO [t38171] VALUES (2791,'٢٧٧٢ - (قال - صلى الله عليه وسلم - ثلاث في المؤمن وله منهن مخرج فمخرجه من سوء الظن أن لا يحققه).','1733','2545','0');
INSERT INTO [t38171] VALUES (2792,'٢٧٧٣ - وقال (- صلى الله عليه وسلم - إن لصاحب الحق مقالا)','1734','2545','0');
INSERT INTO [t38171] VALUES (2793,'٢٧٧٤ - (قال - صلى الله عليه وسلم - مطل الغني ظلم)','1734','2545','0');
INSERT INTO [t38171] VALUES (2794,'٢٧٧٥ - (وقال - صلى الله عليه وسلم - لي الواجد)','1734','2545','0');
INSERT INTO [t38171] VALUES (2795,'٢٧٧٦ - (روي عن هند بنت عتبة)','1735','2545','0');
INSERT INTO [t38171] VALUES (2796,'٢٧٧٧ - (قال - صلى الله عليه وسلم - أترعون)','1736','2545','0');
INSERT INTO [t38171] VALUES (2797,'٢٧٧٨ - (وكانوا يقولون ثلاثة لا غيبة لهم الإمام الجائر والمبتاع والمجاهر بفسقه)','1737','2545','0');
INSERT INTO [t38171] VALUES (2798,'٢٧٧٩ - (قال رسول الله - صلى الله عليه وسلم - من ألقى جلباب الحياء عن وجهه فلا غيبة له)','1737','2545','0');
INSERT INTO [t38171] VALUES (2799,'٢٧٨٠ - (قال عمر رضي الله عنه ليس لفاجر حرمة).','1738','2545','0');
INSERT INTO [t38171] VALUES (2800,'٢٧٨١ - (قال الصلت بن طريف قلت للحسن) البصري (الرجل الفاسق المعلن بفجوره ذكري له بما فيه غيبة قال لا ولا كرامة).','1738','2545','0');
INSERT INTO [t38171] VALUES (2801,'٢٧٨٢ - (قال الحسن) البصري رحمه الله (ثلاثة لا غيبة لهم صاحب الهوى والفاسق المعلن بفسقه والإمام الجائر)','1738','2545','0');
INSERT INTO [t38171] VALUES (2802,'٢٧٨٣ - (قال عوف) بن أبي جميلة الأعرابي البصري العبدي (دخلت على)','1739','2545','0');
INSERT INTO [t38171] VALUES (2803,'٢٧٨٤ - (قال رسول الله - صلى الله عليه وسلم - كفارة من اغتبت أن تستغفر له).','1740','2545','0');
INSERT INTO [t38171] VALUES (2804,'٢٧٨٥ - (وقال مجاهد كفارة أكلك اللحم أخيك أن تثني عليه وتدعو له بخير)','1740','2545','0');
INSERT INTO [t38171] VALUES (2805,'٢٧٨٦ - (سئل عطاء) ابن أبي رباح (عن التوبة من الغيبة)','1741','2545','0');
INSERT INTO [t38171] VALUES (2806,'٢٧٨٧ - (روي أنه - صلى الله عليه وسلم - قال من كانت لأخيه عنده مظلمة في عرض أو مال فليستحلها منه قبل إن يأتي يوم ليس هناك دينار ولا درهم يؤخذ من حسناته فإن لم تكن له حسنات أخذت من سيآت صاحبه فزيدت على سيآته)','1741','2545','0');
INSERT INTO [t38171] VALUES (2807,'٢٧٨٨ - (وقال سعيد) بن المسيب المسيب (لا أحلل من ظلمني)','1741','2545','0');
INSERT INTO [t38171] VALUES (2808,'٢٧٨٩ - (قول النبي - صلى الله عليه وسلم - أيعجز أحدكم أن يكون كأبي ضمضم كان إذا اخرج من بيته قال اللهم إني قد تصدقت بعرضي على الناس).','1742','2545','0');
INSERT INTO [t38171] VALUES (2809,'٢٧٩٠ - (قال عبد الله بن المبارك) رحمه الله تعالى (الزنيم ولد','1743','2545','0');
INSERT INTO [t38171] VALUES (2810,'٢٧٩١ - (وقال عز وجل حمالة الحطب وقيل إنها كانت نمامة حمالة للحديث).','1744','2545','0');
INSERT INTO [t38171] VALUES (2811,'٢٧٩٢ - (وقال تعالى فخانتاهما فلم يغنيا عنهما من الله شيئا قيل كانت امرأة لوط) عليه السلام (تخبر بالضيفان وامرأة نوح) عليه السلام (كانت تخبر أنه مجنون).','1745','2545','0');
INSERT INTO [t38171] VALUES (2812,'٢٧٩٣ - (وقد قال - صلى الله عليه وسلم - لا يدخل الجنة نمام)','1745','2545','0');
INSERT INTO [t38171] VALUES (2813,'٢٧٩٤ - (قال أبو هريرة) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - أحبكم إلى الله تعالى أحاسنكم أخلاقا الموطؤن أكنافا الذين يألفون','1745','2545','0');
INSERT INTO [t38171] VALUES (2814,'٢٧٩٥ - (قال - صلى الله عليه وسلم - ألا أخبركم بشراركم قالوا بلى يا رسول الله قال المشاؤن بالنميمة المفسدون بين الأحبة الباغون للبرآء العنت)','1746','2545','0');
INSERT INTO [t38171] VALUES (2815,'٢٧٩٦ - (قال أبو ذر) الغفاري رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - من أشاد)','1746','2545','0');
INSERT INTO [t38171] VALUES (2816,'٢٧٩٧ - (قال أبو الدرداء) رضي الله عنه (قال - صلى الله عليه وسلم - أيما رجل أشاع عن رجل كلمة وهو منها بريء ليشينه بها في الدنيا كان حقا على الله أن يذيبه بها يوم القيامة في النار)','1747','2545','0');
INSERT INTO [t38171] VALUES (2817,'٢٧٩٨ - (وقال أبو هريرة) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - من شهد على مسلم بشهادة ليس لها بأهل فليتبوأ مقعده من النار).','1747','2545','0');
INSERT INTO [t38171] VALUES (2818,'٢٧٩٩ - (ويقال إن ثلث عذاب القبر من النميمة)','1747','2545','0');
INSERT INTO [t38171] VALUES (2819,'٢٨٠٠ - (عن ابن عمر) رضي الله عنه (عن النبي - صلى الله عليه وسلم -) قال (إن الله تعالى لما خلق الجنة قال لها تكلمي فقالت سعد من دخلني فقال لها الجبار جل جلاله وعزتي وجلالي لا يسكن فيك ثمانية من الناس مدمن خمر ولا مصر على زنا ولا قتات وهو النمام ولا ديوث)','1748','2545','0');
INSERT INTO [t38171] VALUES (2820,'٢٨٠٢ - (وقال - صلى الله عليه وسلم - لا يدخل الجنة قاطع)','1749','2545','0');
INSERT INTO [t38171] VALUES (2821,'٢٨٠٣ - (قال النبي - صلى الله عليه وسلم - الساعي بالناس إلى الناس لغير رشدة يعني ليس بولد حلال)','1749','2545','0');
INSERT INTO [t38171] VALUES (2822,'٢٨٠٤ - (ودخل رجل على سليمان بن عبد الملك) بن مروان (فاستأذن في الكلام وقال إني مكلمك يا أمير المؤمنين بكلام','1749','2545','0');
INSERT INTO [t38171] VALUES (2823,'٢٨٠٥ - (قال حماد بن سلمة) بن دينار البصري أبو سلمة توفي','1750','2545','0');
INSERT INTO [t38171] VALUES (2824,'٢٨٠٦ - (قال) أبو اليقظان (عمار بن ياسر)','1751','2545','0');
INSERT INTO [t38171] VALUES (2825,'٢٨٠٧ - (قال أبو هريرة) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - تجدون من شر عباد الله يوم القيامة ذا الوجهين الذي يأتي هؤلاء بحديث هؤلاء وهؤلاء بحديث هؤلاء)','1752','2545','0');
INSERT INTO [t38171] VALUES (2826,'٢٨٠٨ - (قال أبو هريرة) رضي الله عنه (لا ينبغي لذى الوجهين أن يكون أمينا عند الله تعالى)','1752','2545','0');
INSERT INTO [t38171] VALUES (2827,'٢٨٠٩ - (قال رسول الله - صلى الله عليه وسلم - أبغض خليقة الله يوم القيامة الكذابون والمستكبرون والذين يكنزون)','1752','2545','0');
INSERT INTO [t38171] VALUES (2828,'٢٨١٠ - (قال ابن مسعود) رضي الله عنه (لا يكن أحدكم','1752','2545','0');
INSERT INTO [t38171] VALUES (2829,'٢٨١١ - (قيل لابن عمر) رضي الله عنه (إنا ندخل على أمرائنا فنقول القول فإذا خرجنا)','1753','2545','0');
INSERT INTO [t38171] VALUES (2830,'٢٨١٢ - (قال خالد بن معدان) الكلاعي الحمصي أبو عبد الله ثقة عابد مات سنة ثلاث ومائة روى له الجماعة (من مدح إماما)','1753','2545','0');
INSERT INTO [t38171] VALUES (2831,'٢٨١٣ - (روي أن رجلا مدح رجلا عند النبي - صلى الله عليه وسلم - ويحك قطعت عنق صاحبك لو سمعها ما أفلح ثم قال إن كان أحدكم لا','1753','2545','0');
INSERT INTO [t38171] VALUES (2832,'٢٨١٤ - (قال رسول الله - صلى الله عليه وسلم - إن الله تعالى يغضب إذا مدح الفاسق)','1754','2545','0');
INSERT INTO [t38171] VALUES (2833,'٢٨١٥ - (سمع عمر رضي الله عنه رجلا يثني على رجل فقال أسافرت معه قال لا قال أخالطته)','1754','2545','0');
INSERT INTO [t38171] VALUES (2834,'٢٨١٦ - (وقال الحسن) البصري رحمه الله تعالى (من دعا لظالم بالبقاء فقد أحب أن يعصى الله في الأرض)','1754','2545','0');
INSERT INTO [t38171] VALUES (2835,'٢٨١٧ - (قال الحسن) البصري رحمه الله تعالى كان عمر رضي الله','1754','2545','0');
INSERT INTO [t38171] VALUES (2836,'٢٨١٨ - (وقال - صلى الله عليه وسلم - إذا مدحت أخاك في وجهه فكأنما أمررت على حلقه موسى رميضا)','1755','2545','0');
INSERT INTO [t38171] VALUES (2837,'٢٨١٩ - (وقال) - صلى الله عليه وسلم - (أيضا لمن مدح رجلا عقرت الرجل عقرك الله).','1755','2545','0');
INSERT INTO [t38171] VALUES (2838,'٢٨٢٠ - (وقال - صلى الله عليه وسلم - لو مشى رجل إلى رجل بسكين مرهف) أي حديد (كان خيرا له من أن يثني عليه في وجهه).','1755','2545','0');
INSERT INTO [t38171] VALUES (2839,'٢٨٢١ - (وقال عمر رضي الله عنه المدح هو الذبح)','1756','2545','0');
INSERT INTO [t38171] VALUES (2840,'٢٨٢٢ - (قال لعمر) رضي الله عنه (لو لم أبعث لبعثت يا عمر).','1756','2545','0');
INSERT INTO [t38171] VALUES (2841,'٢٨٢٣ - (قال رسول الله - صلى الله عليه وسلم - أنا سيد ولد آدم ولا فخر)','1756','2545','0');
INSERT INTO [t38171] VALUES (2842,'٢٨٢٤ - (قال - صلى الله عليه وسلم - وجبت لما أثنوا على بعض الموتى)','1756','2545','0');
INSERT INTO [t38171] VALUES (2843,'٢٨٢٥ - (قوله - صلى الله عليه وسلم - احثوا) أي ارموا (في وجوه المداحين)','1757','2545','0');
INSERT INTO [t38171] VALUES (2844,'٢٨٢٦ - (قال) أبو محمد (سفيان بن عيينة) بن أبي عمران الهلالي الكوفي ثم المكي ثقة حافظ فقيه إمام حجة مات في رجب سنه ١٩٨ وله إحدى وتسعون سنة (لا يضر المدح من عرف نفسه)','1757','2545','0');
INSERT INTO [t38171] VALUES (2845,'٢٨٢٧ - (قال حذيفة) بن اليمان رضي الله عنه (قال النبي - صلى الله عليه وسلم - لا يقل أحدكم ما شاء الله وشئت ولكن ليقل ما شاء الله ثم شئت)','1758','2545','0');
INSERT INTO [t38171] VALUES (2846,'٢٨٢٨ - (وقال ابن عباس) رضي الله عنهما (جاء رجل إلى رسول الله - صلى الله عليه وسلم - فكلمه في بعض الأمر فقال ما شاء الله وشئت فقال - صلى الله عليه وسلم - أجعلتني لله عدلا قل ما شاء الله وحده)','1758','2545','0');
INSERT INTO [t38171] VALUES (2847,'٢٨٢٩ - (خطب رجل عند رسول الله - صلى الله عليه وسلم - فقال من يطع الله ورسوله فقد رشد ومن يعصهما فقد غوى فقال)','1759','2545','0');
INSERT INTO [t38171] VALUES (2848,'٢٨٣٠ - (وكان إبراهيم) النخعي (يكره أن يقول الرجل أعوذ بالله وبك ويجوز)','1759','2545','0');
INSERT INTO [t38171] VALUES (2849,'٢٨٣١ - (وكره بعضهم أن يقول) الرجل في دعائه (اللهم اعتقنا من النار وقالوا) وفي توجيه ذلك إن (العتق) إنما (يكون بعد الورود وكانوا يستجيرون من النار ويتعوذون من النار)','1760','2545','0');
INSERT INTO [t38171] VALUES (2850,'٢٨٣٢ - (وقال رجل اللهم اجعلني ممن تصيبه شفاعة محمد) - صلى الله عليه وسلم - (فقال حذيفة)','1760','2545','0');
INSERT INTO [t38171] VALUES (2851,'٢٨٣٣ - (قال إبراهيم) النخعي (إذا قال الرجل للرجل يا حمار يا خنزير قيل له يوم القيامة حمارا رأيتني خلقته خنزيرا رأيتني خلقته)','1760','2545','0');
INSERT INTO [t38171] VALUES (2852,'٢٨٣٤ - (عن ابن عباس) رضي الله عنه قال (إن أحدكم يشرك بالله حتى يشركه بكلبه يقول لولاه لسرقنا الليلة)','1761','2545','0');
INSERT INTO [t38171] VALUES (2853,'٢٨٣٥ - (قال عمر) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - إن الله ينهاكم أن تحلفوا بآبائكم قال عمر)','1761','2545','0');
INSERT INTO [t38171] VALUES (2854,'٢٨٣٦ - (وقال - صلى الله عليه وسلم - لا تسموا العنب الكرم إنما الكرم الرجل المسلم)','1762','2545','0');
INSERT INTO [t38171] VALUES (2855,'٢٨٣٧ - (وقال أبو هريرة) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - لا يقولن أحدكم عبدي وأمتي كلكم عبيد الله وكل نسائكم إماء الله ولكن ليقل غلامي وجاريتي وفتاي ولا يقولن المملوك ربي وربتي ولكن سيدي وسيدتي فكلكم عبيد والرب الله سبحانه وتعالى)','1762','2545','0');
INSERT INTO [t38171] VALUES (2856,'٢٨٣٨ - (قال - صلى الله عليه وسلم - لا تقولوا للمنافق سيدنا فإنه إن يكن سيدكم فقد اسخطتم ربكم)','1763','2545','0');
INSERT INTO [t38171] VALUES (2857,'٢٨٣٩ - (قال رسول الله - صلى الله عليه وسلم - من قال أنا بريء من الإسلام فإن كان صادقا فهو كما قال وإن كان كاذبا فلن يرجع إلى الإسلام سالما).','1763','2545','0');
INSERT INTO [t38171] VALUES (2858,'(فصل)','1765','2857','0');
INSERT INTO [t38171] VALUES (2859,'٢٨٤٠ - (قال رسول الله - صلى الله عليه وسلم - ذروني) أي اتركوني من السؤال (ما تركتكم)','1767','2545','0');
INSERT INTO [t38171] VALUES (2860,'٢٨٤١ - (قال أنس) رضي الله عنه (سأل الناس رسول الله - صلى الله عليه وسلم - يوما حتى أكثروا عليه وأغضبوه فصعد المنبر فقال سلوني فلا تسألوني عن شىء إلا أنبأتكم به فقام إليه رجل)','1768','2545','0');
INSERT INTO [t38171] VALUES (2861,'٢٨٤٢ - (في الحديث نهى رسول الله - صلى الله عليه وسلم - عن قيل وقال وإضاعة المال وكثرة السؤال)','1769','2545','0');
INSERT INTO [t38171] VALUES (2862,'٢٨٤٣ - (وقال - صلى الله عليه وسلم - يوشك الناس يتساءلون بينهم حتى يقولوا قد خلق الله فإذا قالوا ذلك فقولوا الله أحد حتى تختموا السورة ثم ليتفل أحدكم عن يساره ثلاثا وليستعذ بالله من الشيطان الرجيم)','1769','2545','0');
INSERT INTO [t38171] VALUES (2863,'٢٨٤٤ - (قال جابر) رضي الله عنه (ما نزلت آية التلاعن إلا لكثرة السؤال).','1769','2545','0');
INSERT INTO [t38171] VALUES (2864,'كتاب ذم الغضب والحقد والحسد','1770','2367','0');
INSERT INTO [t38171] VALUES (2865,'٢٨٤٥ - (روى أبو هريرة) رضي الله عنه (أن رجلا قال يا رسول الله مرني بعمل وأقلل قال لا تغضب ثم أعاد عليه فقال لا تغضب)','1771','2864','0');
INSERT INTO [t38171] VALUES (2866,'٢٨٤٦ - (وقال ابن عمر) رضي الله عنه (قلت لرسول الله - صلى الله عليه وسلم - قل لي قولا وأقلل لعلي أعقله قال لا تغضب فأعدت ذلك عليه مرتين كل ذلك يرجع إلي) ويقول (لا تغضب).','1772','2864','0');
INSERT INTO [t38171] VALUES (2867,'٢٨٤٧ - (وعن عبد الله بن عمرو) بن العاص رضي الله عنهما (أنه سأل رسول الله - صلى الله عليه وسلم -)','1772','2864','0');
INSERT INTO [t38171] VALUES (2868,'٢٨٤٨ - (قال أبو الدرداء) رضي الله عنه (قلت يا رسول الله دلني على عمل يدخلني الجنة قال لا تغضب).','1773','2864','0');
INSERT INTO [t38171] VALUES (2869,'٢٨٤٩ - (قال - صلى الله عليه وسلم - الغضب يفسد الإيمان كما يفسد الصبر)','1773','2864','0');
INSERT INTO [t38171] VALUES (2870,'٢٨٥٠ - (وقال - صلى الله عليه وسلم - ما غضب أحد إلا أشفى على جهنم).','1773','2864','0');
INSERT INTO [t38171] VALUES (2871,'٢٨٥١ - (وقال له) - صلى الله عليه وسلم - (رجل أي شيء أشد قال غضب الله قال فما يبعدني من غضب الله قال لا تغضب).','1773','2864','0');
INSERT INTO [t38171] VALUES (2872,'٢٨٥٢ - (الغضب من النار كما قال - صلى الله عليه وسلم -).','1774','2864','0');
INSERT INTO [t38171] VALUES (2873,'٢٨٥٣ - (قال - صلى الله عليه وسلم - خير أمتي أحداؤها) جمع حديد والمعنى أنشطها وأسرعها إلى الخير (يعني في الدين)','1774','2864','0');
INSERT INTO [t38171] VALUES (2874,'٢٨٥٤ - (قال خير الأمور أوساطها)','1775','2864','0');
INSERT INTO [t38171] VALUES (2875,'٢٨٥٥ - (قوله من أصبح آمنا في سربه)','1775','2864','0');
INSERT INTO [t38171] VALUES (2876,'٢٨٥٦ - (قال) - صلى الله عليه وسلم - (اللهم أنا بشر أغضب كما يغضب البشر فأيما مسلم سببته أو لعنته أو ضربته فاجعلها منى صلاة عليه وزكاة وقربه تقربه بها إليك يوم القيامة).','1776','2864','0');
INSERT INTO [t38171] VALUES (2877,'٢٨٥٧ - (قال عبد الله بن عمرو بن العاص) بن وائل السهمي القرشي رضي الله عنهما (اكتب عنك كل ما قلت في الغضب والرضا فقال اكتب فوالذي بعثني بالحق ما يخرج منه إلا حق وأشار إلى لسانه)','1776','2864','0');
INSERT INTO [t38171] VALUES (2878,'٢٨٥٨ - (بعث رسول الله - صلى الله عليه وسلم - وصيفا)','1776','2864','0');
INSERT INTO [t38171] VALUES (2879,'٢٨٥٩ - (تقول بلسانك أعوذ بالله من الشيطان الرجيم هكذا أمر رسول الله - صلى الله عليه وسلم - أن يقال عند الغيظ).','1777','2864','0');
INSERT INTO [t38171] VALUES (2880,'٢٨٦٠ - (كان - صلى الله عليه وسلم - إذا غضبت عائشة) رضي الله عنها (أخذ بأنفها وقال يا عويش)','1777','2864','0');
INSERT INTO [t38171] VALUES (2881,'٢٨٦١ - (قال - صلى الله عليه وسلم - إن الغضب جمرة توقد في القلب ألم تروا إلى انتفاخ أوداجه) أي عروق رقبته (وحمرة عينيه فإذا وجد أحدكم من ذلك شيئا فإن كان قائما فليجلس وإن كان جالسا فليقم).','1777','2864','0');
INSERT INTO [t38171] VALUES (2882,'٢٨٦١ - (وقال ابن عباس) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - إذا غضبت فاسكت)','1778','2864','0');
INSERT INTO [t38171] VALUES (2883,'٢٨٦٢ - (وقال أبو هريرة) رضي الله عنه (كان النبي - صلى الله عليه وسلم - إذا غضب وهو قائم جلس وإذا وهو جالس اضطجع فيذهب غضبه).','1778','2864','0');
INSERT INTO [t38171] VALUES (2884,'٢٨٦٣ - (وقال أبو سعيد الخدري) رضي الله عنه (قال النبي - صلى الله عليه وسلم -) في خطبته (إلا أن الغضب جمرة في قلب ابن آدم ألا ترون إلى حمرة عينيه وانتفاخ أوداجه فمن وجد من ذلك شيئا فليلصق خده بالأرض).','1779','2864','0');
INSERT INTO [t38171] VALUES (2885,'٢٨٦٤ - (قال رسول الله - صلى الله عليه وسلم - إذا غضب أحدكم فليتوضأ بالماء فإن الغضب من النار وفي رواية إن الغضب من الشيطان وإن الشيطان خلق من النار وإنما تطفأ النار بالماء فإذا غضب أحدكم فليتوضأ).','1779','2864','0');
INSERT INTO [t38171] VALUES (2886,'٢٨٦٥ - (فقال يا أبا ذر بلغني أنك اليوم عيرت رجلا بأمه فقال نعم فانطلق أبو ذر يرضي صاحبه فسبقه الرجل فسلم عليه فذكر ذلك لرسول الله - صلى الله عليه وسلم - فقال يا أبا ذر ارفع رأسك فانظر ثم أعلم أنك لست بأفضل من أحمر فيها ولا أسود إلا أن تفضله بعمل) أي صالح (ثم قال إذا غضبت فإن كنت قائما فاقعد وإن كنت قاعدا فاتكئ وإن كنت متكئا فاضطجع)','1780','2864','0');
INSERT INTO [t38171] VALUES (2887,'٢٨٦٦ - (قال رسول الله - صلى الله عليه وسلم - من كف غضبه كف الله عن عذابه ومن اعتذر إلى ربه قبل الله عذره ومن خزن لسانه ستر الله عورته)','1781','2864','0');
INSERT INTO [t38171] VALUES (2888,'٢٨٦٧ - (قال - صلى الله عليه وسلم - أشدكم من غلب نفسه)','1781','2864','0');
INSERT INTO [t38171] VALUES (2889,'٢٨٦٨ - (وقال رسول الله - صلى الله عليه وسلم - من كظم غيظا) أي رده ومنعه (ولو شاء أن يمضيه)','1781','2864','0');
INSERT INTO [t38171] VALUES (2890,'٢٨٦٩ - (وقال ابن عمر) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - ما جرع عبد جرعة أعظم أجرا من جرعة غيظ كظمها)','1782','2864','0');
INSERT INTO [t38171] VALUES (2891,'٢٨٧٠ - (قال - صلى الله عليه وسلم - ما من جرعة أحب إلى الله تعالى من جرعة غيظ يكظمها عبد وما كظمها عبد إلا ملأ الله قلبه)','1782','2864','0');
INSERT INTO [t38171] VALUES (2892,'٢٨٧١ - (قال - صلى الله عليه وسلم - من كظم غيظا وهو يقدر على أن ينفذه دعاه الله على رؤس الخلائق ويخيره من أي الحور شاء)','1783','2864','0');
INSERT INTO [t38171] VALUES (2893,'٢٨٧٢ - (قال - صلى الله عليه وسلم - إنما العلم بالتعلم)','1783','2864','0');
INSERT INTO [t38171] VALUES (2894,'٢٨٧٣ - (قال رسول الله - صلى الله عليه وسلم - اطلبو العلم واطلبوا مع العلم السكينة والحلم لينوا)','1784','2864','0');
INSERT INTO [t38171] VALUES (2895,'٢٨٧٤ - (دعاء رسول الله - صلى الله عليه وسلم - أغنني بالعلم)','1785','2864','0');
INSERT INTO [t38171] VALUES (2896,'٢٨٧٥ - (قال أبو هريرة رضي الله عنه قال النبي - صلى الله عليه وسلم - وابتغوا)','1785','2864','0');
INSERT INTO [t38171] VALUES (2897,'٢٨٧٦ - (قال - صلى الله عليه وسلم - خمس من سنن المرسلين)','1786','2864','0');
INSERT INTO [t38171] VALUES (2898,'٢٨٧٧ - (قال علي) رضي الله عنه (قال النبي - صلى الله عليه وسلم - إن الرجل المسلم يدرك بالحلم درجه الصائم القائم)','1787','2864','0');
INSERT INTO [t38171] VALUES (2899,'٢٨٧٨ - (قال رجل من المسلمين اللهم ليس عندي صدقة أتصدق بها فأيما رجل أصاب من عرضي شيئا فهو عليه صدقة فأوحى الله إلى النبي - صلى الله عليه وسلم - أني قد غفرت لها).','1788','2864','0');
INSERT INTO [t38171] VALUES (2900,'٢٨٧٩ - (روي أن ابن مسعود) رضي الله عنه (مر بلغو معرضا)','1789','2864','0');
INSERT INTO [t38171] VALUES (2901,'٢٨٨٠ - (وقال النبي - صلى الله عليه وسلم - اللهم لا يدركني ولا أدركه زمان لا يتبعون فيه العليم ولا يستحيون فيه من الحليم قلوبهم قلوب العجم وألسنتهم ألسنة العرب).','1789','2864','0');
INSERT INTO [t38171] VALUES (2902,'٢٨٨١ - (قال - صلى الله عليه وسلم - ليلني)','1790','2864','0');
INSERT INTO [t38171] VALUES (2903,'٢٨٨٢ - (قال عليه السلام إن فيك يا أشج خلقين يحبهما الله ورسوله قال ما هما بأبي أنت وأمي يا رسول الله قال الحلم والأناة فقال خلتان تخلقتها أو خلقان جبلت عليهما فقال بل خلقان جبلك الله عليهما فقال الحمد لله الذي جبلني على خلقين يحبهما الله ورسوله).','1791','2864','0');
INSERT INTO [t38171] VALUES (2904,'٢٨٨٣ - (وقال - صلى الله عليه وسلم - إن الله يحب الحليم الحيي)','1791','2864','0');
INSERT INTO [t38171] VALUES (2905,'٢٨٨٥ - (قال - صلى الله عليه وسلم - إذا اجتمع الخلائق يوم القيامة)','1792','2864','0');
INSERT INTO [t38171] VALUES (2906,'٢٨٨٦ - (شتم رجل أبا بكر) رضي الله عنه في مجلس النبي - صلى الله عليه وسلم - (وهو سكت)','1793','2864','0');
INSERT INTO [t38171] VALUES (2907,'٢٨٨٧ - (قال رسول الله - صلى الله عليه وسلم - ثلاث) خصال (والذي نفسي بيده إن كنت حالفا لحلفت عليهن) أي على حقيقتهن (ما نقصت صدقة من مال)','1793','2864','0');
INSERT INTO [t38171] VALUES (2908,'٢٨٨٨ - (وقال - صلى الله عليه وسلم - التواضع لا يزيد العبد إلا رفعة فتواضعوا يرفعكم الله والعفو لا يزيد العبد إلا عزا فاعفوا يعزكم الله والصدقة لا تزيد المال إلا كثرة فتصدقوا يرحمكم الله)','1794','2864','0');
INSERT INTO [t38171] VALUES (2909,'٢٨٨٩ - (قال عقبة بن عامر) الجهني رضي الله عنه (لقيت رسول الله - صلى الله عليه وسلم - يوما فبدرته فأخذت بيده أو بدرني فأخذ بيدي فقال يا عقبة ألا أخبرك بأفضل أخلاق أهل الدنيا والآخرة)','1795','2864','0');
INSERT INTO [t38171] VALUES (2910,'٢٨٩٠ - (قال رسول الله - صلى الله عليه وسلم - قال موسى) عليه السلام (يا رب أي عبادك أعز عليك قال الذي إذا قدر عفا).','1795','2864','0');
INSERT INTO [t38171] VALUES (2911,'٢٨٩١ - (جاء رجل إلى رسول الله - صلى الله عليه وسلم - يشكو مظلمة) ظلمها (فأمره النبي - صلى الله عليه وسلم - أن يجلس وأراد أن يأخذ له بمظلمته فقال رسول الله - صلى الله عليه وسلم - إن المظلومين)','1795','2864','0');
INSERT INTO [t38171] VALUES (2912,'٢٨٩٢ - (وقالت عائشة) رضي الله عنها (قال رسول الله - صلى الله عليه وسلم - من دعا على من ظلمه فقد انتصر)','1796','2864','0');
INSERT INTO [t38171] VALUES (2913,'٢٨٩٣ - (عن أنس رضي الله عنه قال قال رسول الله - صلى الله عليه وسلم - إذا بعث الله الخلائق يوم القيامة نادى مناد من تحت العرش ثلاثة أصوات يا معشر الموحدين إن الله قد عفا عنكم فليعف بعضكم عن بعض).','1796','2864','0');
INSERT INTO [t38171] VALUES (2914,'٢٨٩٤ - (وعن أبي هريرة) رضي الله عنه (أن رسول الله - صلى الله عليه وسلم - لما فتح مكة طاف بالبيت وصلى ركعتين ثم أتى الكعبة فأخذ بعضادتي الباب فقال ما تقولون وما تظنون فقالوا نقول أخ وابن عم حليم رحيم قالوا ذلك ثلاثا فقال رسول الله - صلى الله عليه وسلم - أقول كما قال يوسف لا تثريب عليكم اليوم يغفر الله لكم وهو أرحم الراحمين قال فخرجوا كأنما نشروا من القبور فدخلوا في الإسلام)','1797','2864','0');
INSERT INTO [t38171] VALUES (2915,'٢٨٩٥ - (وعن سهيل بن عمرو)','1797','2864','0');
INSERT INTO [t38171] VALUES (2916,'٢٨٩٦ - (وعن أنس) رضي الله عنه (قال قال رسول الله - صلى الله عليه وسلم - إذا وقف العباد نادى مناد ليقم من أجره على الله فليدخل الجنة قيل من ذا الذي أجره على الله قال العافون عن الناس فقام كذا وكذا ألفا فدخلوها بغير حساب).','1798','2864','0');
INSERT INTO [t38171] VALUES (2917,'٢٨٩٧ - (وقال ابن مسعود) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - لا ينبغي لوالي أمر أن يؤتى بحد)','1798','2864','0');
INSERT INTO [t38171] VALUES (2918,'٢٨٩٨ - (وقال جابر) بن عبد الله الأنصاري رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - ثلاث)','1798','2864','0');
INSERT INTO [t38171] VALUES (2919,'٢٨٩٩ - (قال يا عائشة إنه من أعطي حظه من الرفق أعطي حظه من خير الدنيا والآخرة ومن حرم حظه من الرفق حرم حظه من خير الدنيا والآخرة)','1799','2864','0');
INSERT INTO [t38171] VALUES (2920,'٢٩٠٠ - (قال - صلى الله عليه وسلم - إذا أحب الله أهل بيت أدخل عليهم الرفق)','1800','2864','0');
INSERT INTO [t38171] VALUES (2921,'٢٩٠١ - (قال - صلى الله عليه وسلم - إن الله ليعطي على الرفق ما لا يعطي على الخرق)','1800','2864','0');
INSERT INTO [t38171] VALUES (2922,'٢٩٠٢ - (وقالت عائشة رضي الله عنها قال النبي - صلى الله عليه وسلم - إن الله رفيق يحب الرفق ويعطي عليه ما لا يعطي على العنف).','1801','2864','0');
INSERT INTO [t38171] VALUES (2923,'٢٩٠٣ - (وقال - صلى الله عليه وسلم - يا عائشة ارفقي فإن الله إذا أراد بأهل بيت كرامه دلهم على باب الرفق)','1801','2864','0');
INSERT INTO [t38171] VALUES (2924,'٢٩٠٤ - (وقال - صلى الله عليه وسلم - من يحرم)','1801','2864','0');
INSERT INTO [t38171] VALUES (2925,'٢٩٠٥ - (وقال - صلى الله عليه وسلم - أيما وال ولي) على قوم (فلان)','1802','2864','0');
INSERT INTO [t38171] VALUES (2926,'٢٩٠٦ - (وقال - صلى الله عليه وسلم - تدرون من يحرم على النار كل هين لين سهل قريب).','1802','2864','0');
INSERT INTO [t38171] VALUES (2927,'٢٩٠٧ - (وقال - صلى الله عليه وسلم - الرفق يمن) أي بركة (والخرق) بالضم (شؤم).','1803','2864','0');
INSERT INTO [t38171] VALUES (2928,'٢٩٠٨ - (وقال - صلى الله عليه وسلم - التآني من الله والعجلة من الشيطان).','1803','2864','0');
INSERT INTO [t38171] VALUES (2929,'٢٩٠٩ - (وروي أن رسول الله - صلى الله عليه وسلم - أتاه رجل فقال يا رسول الله إن الله قد بارك لجميع المسلمين فيك فاخصصني منك بخير فقال الحمد لله مرتين أو ثلاثا ثم أقبل عليه فقال هل أنت مستوص مرتين أو ثلاثا فقال نعم قال إذا أردت أمرا فتدبر عاقبته)','1803','2864','0');
INSERT INTO [t38171] VALUES (2930,'٢٩١٠ - (عن عائشة) رضي الله عنها (أنها كانت مع رسول الله - صلى الله عليه وسلم - في سفر على بعير صعب فجعلت تصرفه يمينا وشمالا فقال رسول الله - صلى الله عليه وسلم - يا عائشة عليك بالرفق)','1804','2864','0');
INSERT INTO [t38171] VALUES (2931,'٢٩١١ - (تتمة)','1804','2864','0');
INSERT INTO [t38171] VALUES (2932,'٢٩١٢ - (وفي الخبر موقوفا ومرفوعا العلم) أي الشرعي النافع (خليل المؤمن)','1805','2864','0');
INSERT INTO [t38171] VALUES (2933,'٢٩١٣ - (قال - صلى الله عليه وسلم - الحسد)','1806','2864','0');
INSERT INTO [t38171] VALUES (2934,'٢٩١٤ - (وقال أنس) رضي الله عنه (كنا يوما جلوسا عند رسول الله - صلى الله عليه وسلم - فقال يطبع عليكم الآن من هذا الفج)','1807','2864','0');
INSERT INTO [t38171] VALUES (2935,'٢٩١٥ - (وقال - صلى الله عليه وسلم - ثلاثة لا ينجو منهن أحد الظن)','1808','2864','0');
INSERT INTO [t38171] VALUES (2936,'٢٩١٦ - (قال - صلى الله عليه وسلم - دب إليكم داء الأمم قبلكم الحسد والبغضاء)','1809','2864','0');
INSERT INTO [t38171] VALUES (2937,'٢٩١٧ - (قال - صلى الله عليه وسلم - كاد الفقر أن يكون كفرا وكاد الحسد أن يغلب القدر).','1809','2864','0');
INSERT INTO [t38171] VALUES (2938,'٢٩١٨ - (وقال - صلى الله عليه وسلم - إنه سيصيب أمتي داء الأمم قالوا)','1810','2864','0');
INSERT INTO [t38171] VALUES (2939,'٢٩١٩ - (قال - صلى الله عليه وسلم - أخوف ما أخاف على أمتي أن يكثر فيهم المال فيتحاسدون ويقتتلون)','1811','2864','0');
INSERT INTO [t38171] VALUES (2940,'٢٩٢٠ - (وقال - صلى الله عليه وسلم - لا تظهر الشماتة لأخيك)','1811','2864','0');
INSERT INTO [t38171] VALUES (2941,'٢٩٢١ - (وقال - صلى الله عليه وسلم - استعينوا على قضاء الحوائج)','1812','2864','0');
INSERT INTO [t38171] VALUES (2942,'٢٩٢٢ - (قال - صلى الله عليه وسلم - إن لنعم الله آعداء قيل ومن أولئك قال الذين يحسدون الناس على ما أتاهم الله من فضله).','1813','2864','0');
INSERT INTO [t38171] VALUES (2943,'٢٩٢٣ - (قال - صلى الله عليه وسلم - ستة يدخلون النار قبل الحساب يا رسول الله من هم قال الأمراء بالجور)','1813','2864','0');
INSERT INTO [t38171] VALUES (2944,'٢٩٢٤ - (قد قال - صلى الله عليه وسلم - المؤمن يغبط والمنافق يحسد).','1814','2864','0');
INSERT INTO [t38171] VALUES (2945,'٢٩٢٥ - (قال ابن عباس) رضي الله عنه (كانت اليهود الذين بالمدينة قبل أن يبعث النبي - صلى الله عليه وسلم - إذا قاتلوا قوما قالوا نسألك بالنبي الذي وعدتنا أن ترسله وبالكتاب) الذي وعدتنا (أن تنزله إلا ما نصرتنا على هذا القوم فكانوا)','1815','2864','0');
INSERT INTO [t38171] VALUES (2946,'٢٩٢٦ - (وقالت صفية بن حيي)','1815','2864','0');
INSERT INTO [t38171] VALUES (2947,'٢٩٢٧ - (قال لا حسد إلا في اثنتين رجل آتاه الله ما لا فسلطه على هلكته في الحق ورجل أتاه الله علما فهو يعمل به ويعلمه','1815','2864','0');
INSERT INTO [t38171] VALUES (2948,'٢٩٢٨ - (قال مثل هذه الأمة مثل أربعة رجل أتاه الله مالا وعلما فهو يعمل بعلمه في ماله)','1816','2864','0');
INSERT INTO [t38171] VALUES (2949,'٢٩٢٩ - (قال ابن إسحاق في السيرة أن قائل ذلك الوليد بن المغيرة أينزل على محمد وأترك وأنا كبير قريش ويترك أبو مسعود','1816','2864','0');
INSERT INTO [t38171] VALUES (2950,'٢٩٣٠ - (أنت مع من أحببت).','1817','2864','0');
INSERT INTO [t38171] VALUES (2951,'٢٩٣١ - (وقال أبو موسى) الأشعري رضي الله عنه (قلت يا رسول الله الرجل يحب المصلين ولا يصلي ويحب الصوم ولا يصوم حتى عد أشياء قال النبي - صلى الله عليه وسلم - هو مع من أحب).','1817','2864','0');
INSERT INTO [t38171] VALUES (2952,'٢٩٣٢ - (وقال رجل لعمر بن عبد العزيز)','1818','2864','0');
INSERT INTO [t38171] VALUES (2953,'٢٩٣٣ - (في الحديث أهل الجنة ثلاثة المحسن) أي في عمله (والمحب له والكاف عنه).','1818','2864','0');
INSERT INTO [t38171] VALUES (2954,'٢٩٣٤ - (وروي عنه موقوفا) عليه (ومرفوعا إلى رسول الله - صلى الله عليه وسلم - أنه قال ثلاث لا يخلو منهن مؤمن وله منهن مخرج فمخرجه من الحسد أن لا يبغي)','1818','2864','0');
INSERT INTO [t38171] VALUES (2955,'كتاب ذم الدنيا','1820','2367','0');
INSERT INTO [t38171] VALUES (2956,'٢٩٣٥ - (قال والذي نفسي بيده للدنيا أهون على الله من هذه الشاة على أهلها ولو كانت الدنيا تعدل عند الله جناح بعوضة ما سقى كافرا منها شربة ماء).','1821','2955','0');
INSERT INTO [t38171] VALUES (2957,'٢٩٣٦ - (وقال - صلى الله عليه وسلم - الدنيا سجن المؤمن وجنة الكافر).','1822','2955','0');
INSERT INTO [t38171] VALUES (2958,'٢٩٣٧ - (قال رسول الله - صلى الله عليه وسلم - الدنيا ملعونة ملعون ما فيها إلا ما كان لله منها).','1823','2955','0');
INSERT INTO [t38171] VALUES (2959,'٢٩٣٨ - (من أحب دنياه أضر بآخرته)','1823','2955','0');
INSERT INTO [t38171] VALUES (2960,'٢٩٣٩ - (وقال - صلى الله عليه وسلم - حب الدنيا رأس كل خطيئة)','1823','2955','0');
INSERT INTO [t38171] VALUES (2961,'٢٩٤٠ - (قال أبو بكر كنت مع رسول الله - صلى الله عليه وسلم - فرأيته يدفع عن نفسه شيئا ولم أر معه أحدا فقلت يا رسول الله ما الذي تدفع عن نفسك قال هذه الدنيا مثلت لي فقلت لها إليك عني ثم رجعت فقالت إنك إن أفلت مني لم يفلت مني من بعدك).','1824','2955','0');
INSERT INTO [t38171] VALUES (2962,'٢٩٤١ - (قال - صلى الله عليه وسلم - يا عجبا كل العجب للمصدق بدار الخلود وهو يسعى لدار الغرور).','1825','2955','0');
INSERT INTO [t38171] VALUES (2963,'٢٩٤٢ - (روي أن رسول الله - صلى الله عليه وسلم - وقف على مزبلة)','1825','2955','0');
INSERT INTO [t38171] VALUES (2964,'٢٩٤٣ - (قال - صلى الله عليه وسلم - إن الدنيا حلوة خضرة)','1826','2955','0');
INSERT INTO [t38171] VALUES (2965,'٢٩٤٤ - (وقال موسى بن يسار)','1826','2955','0');
INSERT INTO [t38171] VALUES (2966,'٢٩٤٥ - (وقال - صلى الله عليه وسلم - ألهاكم التكاثر يقول ابن آدم مالي مالي وهل لك من مالك إلا ما أكلت فأفنيت أو لبست فأبليت أو تصدقت فأمضيت).','1827','2955','0');
INSERT INTO [t38171] VALUES (2967,'٢٩٤٦ - (وقال - صلى الله عليه وسلم - الدنيا دار من لا دار له)','1828','2955','0');
INSERT INTO [t38171] VALUES (2968,'٢٩٤٧ - (قال - صلى الله عليه وسلم - من أصبح والدنيا أكبر همه فليس من الله في شيء)','1828','2955','0');
INSERT INTO [t38171] VALUES (2969,'٢٩٤٨ - (وقال - صلى الله عليه وسلم - من أصبح والدنيا أكبر همه ألزم الله قلبه أربع خصال)','1829','2955','0');
INSERT INTO [t38171] VALUES (2970,'٢٩٤٩ - (قال أبو هريرة رضى الله عنه قال قال رسول الله - صلى الله عليه وسلم - يا أبا هريرة ألا أريك الدنيا جميعا بما فيها قلت بلى يا رسول الله فأخذ بيدي وأتى بي واديا من أودية المدينة فإذا مزبلة فيها رؤس ناس وعذرات)','1829','2955','0');
INSERT INTO [t38171] VALUES (2971,'٢٩٥٠ - (روي أن الله عز وجل لما أهبط آدم عليه السلام إلى الأرض قال)','1830','2955','0');
INSERT INTO [t38171] VALUES (2972,'٢٩٥١ - (وقال - صلى الله عليه وسلم - الدنيا موقوفة بين السماء والأرض منذ خلقها الله تعالى لا ينظر إليها وتقول يوم القيامة يا رب اجعلني لأدني أوليائك نصيبا اليوم فيقول اسكتي يا لا شيء إني لم أرضك لهم في الدنيا أرضاك لهم اليوم)','1831','2955','0');
INSERT INTO [t38171] VALUES (2973,'١٩٥٢ - (وقال - صلى الله عليه وسلم - ليجيئن أقوام يوم القيامة وأعمالهم كجبال تهامة)','1831','2955','0');
INSERT INTO [t38171] VALUES (2974,'٢٩٥٣ - (وقال - صلى الله عليه وسلم - في بعض خطبه المؤمن بين مخافتين بين أجل قد مضي لا يدري الله صانع فيه وبين أجل قد بقي لا يدري ما الله قاض فيه فليتزود العبد لنفسه من نفسه ومن دنياه لآخرته ومن حياته لموته ومن شبابه لهرمه فإن الدنيا خلقت لكم وأنتم خلقتم للآخرة والذي نفسي بيده ما بعد الموت من مستعتب ولا بعد الموت من دار إلا الجنة أو النار).','1833','2955','0');
INSERT INTO [t38171] VALUES (2975,'٢٩٥٤ - (وقال نبينا - صلى الله عليه وسلم - احذروا الدنيا فإنها أسحر من هاروت وماروت).','1833','2955','0');
INSERT INTO [t38171] VALUES (2976,'٢٩٥٥ - (وعن الحسن) البصري (قال خرج رسول الله - صلى الله عليه وسلم - ذات يوم على أصحابه فقال هل منكم من يريد أن يذهب الله عنه العمى ويجعله بصيرا ألا إنه من رغب في الدنيا وطال أمله فيها أعمى الله قلبه على قدر ذلك ومن زهد في الدنيا وقصر أمله فيها أعطاه الله علما بغير تعلم وهدى بغير هداية إلا إنه سيكون بعدكم قوم لا يستقيم لهم الملك إلا بالقتل والتجبر ولا الغنى إلا بالفخر والبخل ولا المحبة إلا باتباع الهوى ألا فمن أدرك ذلك الزمان','1833','2955','0');
INSERT INTO [t38171] VALUES (2977,'٢٩٥٦ - (وقال أبو سعيد الخدري) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - إن أكثر ما أخاف عليكم ما يخرج الله لكم من بركات الأرض فقيل ما بركات الأرض فقال زهرة الدنيا)','1834','2955','0');
INSERT INTO [t38171] VALUES (2978,'٢٩٥٧ - (قال أنس) رضي الله عنه (كانت ناقة رسول الله - صلى الله عليه وسلم - العضباء لا تسبق)','1834','2955','0');
INSERT INTO [t38171] VALUES (2979,'٢٩٥٨ - (قال أبو الدرداء) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - لو تعلمون ما أعلم لضحكتم قليلا ولبكيتم كثيرا ولهانت عليكم الدنيا ولآثرتم الآخرة).','1835','2955','0');
INSERT INTO [t38171] VALUES (2980,'٢٩٥٩ - (قال نبينا - صلى الله عليه وسلم - لتأتينكم بعدي دنيا تأكل كل إيمانكم كما تأكل النار الحطب).','1836','2955','0');
INSERT INTO [t38171] VALUES (2981,'٢٩٦٠ - (قال عمرو بن العاص) رضي الله عنه (على المنبر والله ما رأيت قوما قط أرغب فيما كان رسول الله يزهد فيه منكم والله ما مر برسول الله - صلى الله عليه وسلم - ثلاث إلا والذي عليه أكثر من الذي له).','1836','2955','0');
INSERT INTO [t38171] VALUES (2982,'٢٩٦١ - (عرضت على نبيك - صلى الله عليه وسلم - بمفاتحها وخزائنها لا ينقص ذلك عند الله جناح بعوضة فأبى أن يقبلها).','1836','2955','0');
INSERT INTO [t38171] VALUES (2983,'٢٩٦٢ - (لقد جاءت الرواية عنه عن ربه تبارك وتعالى أنه قال لموسى عليه السلام إذا رأيت الغني مقبلا فقل ذنب عجلت عقوبته وإذا رأيت الفقر مقبلا فقل مرحبا بشعار الصالحين)','1836','2955','0');
INSERT INTO [t38171] VALUES (2984,'٢٩٦٣ - (واعلم) يا موسى (إنه من أخاف لي وليا فقد بارزني بالمحاربة ثم أنا الثائر له يوم القيامة)','1837','2955','0');
INSERT INTO [t38171] VALUES (2985,'٢٩٦٤ - (قال - صلى الله عليه وسلم - مالي وللدنيا وإنما مثلي ومثل الدنيا كمثل راكب صار في يوم صائف فرفعت له شجرة فقال تحت ظلها ساعة ثم راح وتركها).','1837','2955','0');
INSERT INTO [t38171] VALUES (2986,'٢٩٦٥ - (توفي رسول الله - صلى الله عليه وسلم - وما وضع لبنة على لبنة ولا قصبة على قصبة).','1838','2955','0');
INSERT INTO [t38171] VALUES (2987,'٢٩٦٦ - (ورأى بعض أصحابه يبني بيتا من خص)','1838','2955','0');
INSERT INTO [t38171] VALUES (2988,'٢٩٦٧ - (قال النبي - صلى الله عليه وسلم - إنما مثل صاحب الدنيا كمثل الماشي في الماء هل يستطيع الذي يمشي في الماء أن لا تبتل قدماه).','1838','2955','0');
INSERT INTO [t38171] VALUES (2989,'٢٩٦٨ - (قال نبينا - صلى الله عليه وسلم - إن ما يغني من الدنيا بلاء وفتنة وإنما مثل عمل أحدكم كمثل الوعاء إذا طاب أعلاه طاب أسفله وإذا خبث أعلاه خبث أسفله).','1839','2955','0');
INSERT INTO [t38171] VALUES (2990,'٢٩٦٩ - (قال أنس) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - مثل هذه الدنيا مثل ثوب شق من أوله إلى آخره فبقي معلقا)','1839','2955','0');
INSERT INTO [t38171] VALUES (2991,'٢٩٧٠ - (وقد روي أن النبي - صلى الله عليه وسلم - قال للضحاك بن سفيان)','1840','2955','0');
INSERT INTO [t38171] VALUES (2992,'٢٩٧١ - (قال أبي بن كعب) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - إن الدنيا ضربت مثلا لابن آدم فانظر إلى ما يخرج من ابن آدم وإن قزحه وملحه)','1840','2955','0');
INSERT INTO [t38171] VALUES (2993,'٢٩٧٢ - (قال - صلى الله عليه وسلم - إن الله ضرب الدنيا لمطعم ابن آدم مثلا وضرب مطعم ابن آدم للدنيا مثلا وإن قزحه وملحه).','1841','2955','0');
INSERT INTO [t38171] VALUES (2994,'٢٩٧٣ - (قال رسول الله - صلى الله عليه وسلم - ما الدنيا في الآخرة)','1841','2955','0');
INSERT INTO [t38171] VALUES (2995,'٢٩٧٤ - (قال الحسن) البصري رحمه الله تعالى (بلغني أن رسول الله - صلى الله عليه وسلم - قال لأصحابه إنما مثلي ومثلكم ومثل الدنيا كمثل قوم سلكوا مفازة غبراء)','1842','2955','0');
INSERT INTO [t38171] VALUES (2996,'٢٩٧٥ - (قال - صلى الله عليه وسلم - حبب إلي من دنياكم ثلاث الطيب والنساء وقرة عيني في الصلاة)','1843','2955','0');
INSERT INTO [t38171] VALUES (2997,'٢٩٧٦ - (ورد في الأخبار أن أعمال العبد تناضل) أي تدافع (عنه فإذا جاء العذاب من جهة رجليه جاء قيام الليل يدفع عنه وإذا جاء من جهة يديه جاءت الصدقة تدفع عند الحديث)','1844','2955','0');
INSERT INTO [t38171] VALUES (2998,'٢٩٧٧ - (قال رسول الله - صلى الله عليه وسلم - حلالها حساب وحرامها عذاب).','1845','2955','0');
INSERT INTO [t38171] VALUES (2999,'٢٩٧٨ - (قوله - صلى الله عليه وسلم - لعمر رضي الله عنه هذا من النعيم الذي نسأل عنه أشار به إلى الماء البارد)','1845','2955','0');
INSERT INTO [t38171] VALUES (3000,'٢٩٧٩ - (روى الله تعالى الدنيا عن نبينا - صلى الله عليه وسلم -).','1846','2955','0');
INSERT INTO [t38171] VALUES (3001,'٢٩٨٠ - (فكان يطوي أياما).','1847','2955','0');
INSERT INTO [t38171] VALUES (3002,'٢٩٨١ - (بكى عمر ثم قال ما قلت ما قلت إلا إني سمعت رسول الله - صلى الله عليه وسلم - يقول يدخل الجنة شفاعة مثل ربيعة ومضر).','1847','2955','0');
INSERT INTO [t38171] VALUES (3003,'٢٩٨٢ - (أنه - صلى الله عليه وسلم - لما قال الناجي منها واحدة قالوا يا رسول الله ومن هم قال أهل السنة والجماعة فقيل ومن أهل السنة والجماعة فقال ما أنا عليه وأصحابي).','1848','2955','0');
INSERT INTO [t38171] VALUES (3004,'كتاب ذم البخل وحب المال','1853','2367','0');
INSERT INTO [t38171] VALUES (3005,'٢٩٨٣ - (قال رسول الله - صلى الله عليه وسلم - حب المال والشرف ينبتان النفاق في القلب كما ينبت الماء البقل).','1854','3004','0');
INSERT INTO [t38171] VALUES (3006,'٢٩٨٤ - (وقال - صلى الله عليه وسلم - ما ذئبان)','1854','3004','0');
INSERT INTO [t38171] VALUES (3007,'٢٩٨٥ - (وقال - صلى الله عليه وسلم - هلك الأكثرون إلا من قال به) أي بالمال أطلق القول وأراد به العمل (في عباد الله) أي المستحقين من الفقراء (هكذا وهكذا) وأشار (بيده وقليل ما هم).','1855','3004','0');
INSERT INTO [t38171] VALUES (3008,'٢٩٨٦ - (قيل يا رسول الله أي أمتك أشر قال الأغنياء).','1856','3004','0');
INSERT INTO [t38171] VALUES (3009,'٢٩٨٧ - (وقال - صلى الله عليه وسلم - سيأتي بعدكم قوم يأكلون أطايب الدنيا وألوانها وينكحون أجمل النساء وألوانها ويلبسون ألين الثياب وألوانها ويركبون فره الخيل وألوانها لهم بطون من القليل لا تشبع وأنفس بالقليل لا تقنع عاكفين على الدنيا يغدون ويرحون إليها اتخذوها آلهة من دون إلههم وربا دون ربهم إلى أمرها ينتهون وهواهم يتبعون فعزيمة من محمد بن عبد الله لمن أدرك ذلك الزمان من عقب عقبكم وخلف خلفكم أن لا يسلم عليهم ولا يعود','1856','3004','0');
INSERT INTO [t38171] VALUES (3010,'٢٩٨٨ - (قال - صلى الله عليه وسلم - دعوا الدنيا لأهلها)','1857','3004','0');
INSERT INTO [t38171] VALUES (3011,'٢٩٨٩ - (قال رجل يا رسول الله ما لي لا أحب الموت فقال هل معك من مال قال نعم يا رسول الله قال قدم مالك) بين يديك (فإن قلب المؤمن مع ماله إن قدمه أحب أن يلحقه وإن خلفه أحب أن يتخلف معه).','1857','3004','0');
INSERT INTO [t38171] VALUES (3012,'٢٩٩٠ - (قال - صلى الله عليه وسلم - أخلاء ابن آدم) جمع خليل أي أصحابه (ثلاثة واحد يتبعه إلى قبض روحه والثاني إلى قبره والثالث إلى محشره فالذي يتبعه إلى قبض روحه فهو ماله والذي يتبعه إلى قبره هو أهله والذي يتبعه إلى محشره هو عمله).','1858','3004','0');
INSERT INTO [t38171] VALUES (3013,'٢٩٩١ - (كتب سلمان الفارسي إلى أبي الدرداء) رضي الله عنهما (يا أخي إياك أن تجمع من الدنيا مالا تؤدي شكره فإني سمعت رسول الله - صلى الله عليه وسلم - يقول يجاء بصاحب الدنيا الذي أطاع الله فيها وماله','1858','3004','0');
INSERT INTO [t38171] VALUES (3014,'٢٩٩٢ - (قال - صلى الله عليه وسلم - إذا مات العبد قالت الملائكة ما قدم وقال الناس ما خلف)','1859','3004','0');
INSERT INTO [t38171] VALUES (3015,'٢٩٩٣ - (قال - صلى الله عليه وسلم - لا تتخذوا الضيعة) أي العقار وهي الأرض','1859','3004','0');
INSERT INTO [t38171] VALUES (3016,'٢٩٩٤ - (قال - صلى الله عليه وسلم - نعم المال الصالح للرجل الصالح).','1860','3004','0');
INSERT INTO [t38171] VALUES (3017,'٢٩٩٥ - (قال أكثرهم للموت ذكرا وأشدهم له استعدادا).','1860','3004','0');
INSERT INTO [t38171] VALUES (3018,'٢٩٩٦ - (قال نبينا - صلى الله عليه وسلم - اللهم اجعل قوت آل محمد كفافا).','1860','3004','0');
INSERT INTO [t38171] VALUES (3019,'٢٩٩٧ - (وقال) - صلى الله عليه وسلم - (اللهم أحيني مسكينا وأمتني مسكينا واحشرني في زمرة المساكين يوم القيامة)','1861','3004','0');
INSERT INTO [t38171] VALUES (3020,'٢٩٩٨ - (قال نبينا - صلى الله عليه وسلم - تعس عبد الدينار وتعس عبد الدرهم تعس ولا انتعش وإذا شيك فلا انتقش).','1861','3004','0');
INSERT INTO [t38171] VALUES (3021,'٢٩٩٩ - (شرك خفي لا يوجب الخلود في النار وقلما ينفك عنه المؤمنون فإنه أخفى من دبيب النمل)','1862','3004','0');
INSERT INTO [t38171] VALUES (3022,'٣٠٠٠ - (قال رسول الله - صلى الله عليه وسلم - ما وقى به المرء عرضه كتب له صدقة)','1862','3004','0');
INSERT INTO [t38171] VALUES (3023,'٣٠٠١ - (قال رسول الله - صلى الله عليه وسلم - لو كان لابن آدم واديان من ذهب لابتغى لهما ثالثا ولا يملأ جوف ابن آدم إلا التراب ويتوب الله على من تاب)','1862','3004','0');
INSERT INTO [t38171] VALUES (3024,'٣٠٠٢ - (وعن أبي واقد)','1863','3004','0');
INSERT INTO [t38171] VALUES (3025,'٣٠٠٣ - (قال أبو موسى الأشعري)','1863','3004','0');
INSERT INTO [t38171] VALUES (3026,'٣٠٠٤ - (قال - صلى الله عليه وسلم - منهومان لا يشبعان منهوم العلم ومنهوم المال).','1864','3004','0');
INSERT INTO [t38171] VALUES (3027,'٣٠٠٥ - (قال - صلى الله عليه وسلم - يهرم ابن آدم ويشب معه اثنتان الأمل وحب المال أو كما قال).','1865','3004','0');
INSERT INTO [t38171] VALUES (3028,'٣٠٠٦ - (قال - صلى الله عليه وسلم - طوبى لمن هدي إلى الإسلام وكان عيشه كفافا وقنع به).','1865','3004','0');
INSERT INTO [t38171] VALUES (3029,'٣٠٠٧ - (وقال - صلى الله عليه وسلم - ما من أحد غني ولا فقير إلا ود يوم القيامة أنه كان أوتي قوتا في الدنيا).','1865','3004','0');
INSERT INTO [t38171] VALUES (3030,'٣٠٠٨ - (فقال ألا أيها الناس أجملوا في الطلب فإنه ليس لعبد إلا ما كتب له ولن يذهب عبد من الدنيا حتى يأتيه ما كتب له من الدنيا وهي راغمة)','1866','3004','0');
INSERT INTO [t38171] VALUES (3031,'٣٠٠٩ - (قال رسول الله - صلى الله عليه وسلم - إن روح القدس نفث في روعي أن نفسا لن تموت حتى تستكمل رزقها فاتقوا الله واجملوا في الطلب)','1866','3004','0');
INSERT INTO [t38171] VALUES (3032,'٣٠١٠ - (وقال أبو هريرة) رضي الله عنه (قال لي رسول الله - صلى الله عليه وسلم - إذا اشتد بك الجوع فعليك برغيف وكوز من ماء وعلى الدنيا الدمار)','1866','3004','0');
INSERT INTO [t38171] VALUES (3033,'٣٠١١ - (قال أبو هريرة) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - كن ورعا تكن أعبد الناس وكن قنعا تكن أشكر الناس وأحب لأخيك ما تحب لنفسك تكن مؤمنا)','1867','3004','0');
INSERT INTO [t38171] VALUES (3034,'٣٠١٢ - (ونهى - صلى الله عليه وسلم - عن الطمع فيما رواه أبو أيوب الأنصاري) رضي الله عنه (أن أعرابيا أتى النبي - صلى الله عليه وسلم - فقال يا رسول الله عظني وأوجز فقال إذا صليت فصل صلاة مودع ولا تحدثن بحديث تعتذر منه غدا واجمع اليأس مما في أيدي الناس)','1867','3004','0');
INSERT INTO [t38171] VALUES (3035,'١٠١٣ - (قال عوف بن مالك) بن أبي عوف (الأشجعي)','1868','3004','0');
INSERT INTO [t38171] VALUES (3036,'٣٠١٤ - (قال - صلى الله عليه وسلم - ليس الغنى عن كثرة العرض إنما الغنى غنى النفس).','1869','3004','0');
INSERT INTO [t38171] VALUES (3037,'٣٠١٥ - (قال - صلى الله عليه وسلم - ما عال) أي ما افتقر (من اقتصد)','1870','3004','0');
INSERT INTO [t38171] VALUES (3038,'٣٠١٦ - (قال - صلى الله عليه وسلم - ثلاث) خصال (منجيات) من عذاب الله تعالى خشية الله) أي خوفه (في السر والعلانية)','1871','3004','0');
INSERT INTO [t38171] VALUES (3039,'٣٠١٧ - (روي أن رجلا أبصر بالدرداء) رضي الله عنه (يلتقط حبا من الأرض ويقول أن من فقهك رفقك في معيشتك)','1872','3004','0');
INSERT INTO [t38171] VALUES (3040,'٣٠١٨ - (قال ابن عباس رضي الله عنهما قال النبي - صلى الله عليه وسلم - الاقتصاد وحسن السمت والهدي الصالح جزء من بضع وعشرين جزءا من النبوة).','1872','3004','0');
INSERT INTO [t38171] VALUES (3041,'٣٠١٩ - (وفي الخبر التدبير نصف العيش)','1872','3004','0');
INSERT INTO [t38171] VALUES (3042,'٣٠٢٠ - (قال - صلى الله عليه وسلم - من اقتصد) في أموره كلها (أغناه الله تعالى ومن بذر) أي أسرف وتجاوز عن الحدود (أفقره الله ومن ذكر الله عز وجل أحبه الله).','1873','3004','0');
INSERT INTO [t38171] VALUES (3043,'٣٠٢١ - (قال - صلى الله عليه وسلم - إذا أردت أمرا فعليك بالتؤدة حتى يجعل الله لك فرجا ومخرجا).','1874','3004','0');
INSERT INTO [t38171] VALUES (3044,'٣٠٢٢ - (دخل) حبة وسواء (ابنا خالد)','1874','3004','0');
INSERT INTO [t38171] VALUES (3045,'٣٠٢٣ - (ومر رسول الله - صلى الله عليه وسلم - بابن مسعود) عبد الله رضي الله عنه (وهو حزين فقال لا يكثر همك) وفي لفظ لا تكثر همك (ما يقدر يكن وما ترزق يأتك).','1874','3004','0');
INSERT INTO [t38171] VALUES (3046,'٣٠٢٤ - (قال - صلى الله عليه وسلم - أبى الله أن يرزق عبده المؤمن إلا من حيث لا يحتسب).','1876','3004','0');
INSERT INTO [t38171] VALUES (3047,'٣٠٢٥ - (قال - صلى الله عليه وسلم - عز المؤمن استغناؤه عن الناس).','1877','3004','0');
INSERT INTO [t38171] VALUES (3048,'٣٠٢٦ - (قال السخاء شجرة من شجر الجنة)','1878','3004','0');
INSERT INTO [t38171] VALUES (3049,'٣٠٢٧ - (وقال جابر) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - قال جبريل عليه السلام قال الله تعالى إن هذا دين أرتضيه لنفسي ولن يصلحه إلا السخاء وحسن الخلق فأكرموه بهما ما صحبتموه).','1878','3004','0');
INSERT INTO [t38171] VALUES (3050,'٣٠٢٨ - (وعن عائشة رضي الله عنها قالت قال رسول الله ما جبل الله تعالى أولياء إلا على السخاء وحسن الخلق)','1879','3004','0');
INSERT INTO [t38171] VALUES (3051,'٣٠٢٩ - (وعن جابر) رضي الله عنه (قال قيل يا رسول الله أي الأعمال أفضل قال الصبر والسماحة).','1879','3004','0');
INSERT INTO [t38171] VALUES (3052,'٣٠٣٠ - (قال عبد الله بن عمرو) بن العاص رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - خلقان يحبهما الله تعالى وخلقان يبغضهما الله تعالى فأما اللذان يحبهما الله فحسن الخلق والسخاء وأما اللذان يبغضهما الله فسوء الخلق والبخل وإذا أرد الله بعبد خيرا استعمله على قضاء حوائج الناس)','1879','3004','0');
INSERT INTO [t38171] VALUES (3053,'٣٠٣١ - (روى المقدام بن شريح بن هانئ)','1880','3004','0');
INSERT INTO [t38171] VALUES (3054,'٣٠٣٢ - (قال أبو هريرة) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - السخاء شجرة في الجنة فمن كان سخيا أخذ بغصن منها فلم يتركه ذلك الغصن حتى يدخله الجنة والشح شجرة في النار فمن كان شحيحا أخذ بغصن من أغصانها فلم يتركه ذلك الغصن حتى يدخله النار).','1881','3004','0');
INSERT INTO [t38171] VALUES (3055,'٣٠٣٣ - (وقال أبو سعيد الخدري) رضي الله عنه (قال النبي - صلى الله عليه وسلم - يقول الله تعالى اطلبوا الفضل)','1882','3004','0');
INSERT INTO [t38171] VALUES (3056,'٣٠٣٤ - (وعن ابن عباس) رضي الله عنه (قال قال رسول الله - صلى الله عليه وسلم - تجافوا)','1884','3004','0');
INSERT INTO [t38171] VALUES (3057,'٣٠٣٥ - (قال ابن مسعود) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - الرزق إلى مطعم الطعام أسرع من المسكين إلى ذروة البعير وإن الله تعالى ليباهي بمطعم الطعام الملائكة).','1885','3004','0');
INSERT INTO [t38171] VALUES (3058,'٣٠٣٦ - (قال - صلى الله عليه وسلم - إن الله جواد يحب الجود ويحب معالي الأخلاق ويكره سفسافها).','1886','3004','0');
INSERT INTO [t38171] VALUES (3059,'٣٠٣٧ - (قال أنس) رضي الله عنه (إن رسول الله - صلى الله عليه وسلم - لم يسئل على الإسلام شيئا إلا أعطاه فأتاه رجل فسأله فأمر له بشاء كثير بين جبلين من شاء الصدقة فرجع إلى قومه فقال يا قوم أسلموا فإن محمدا يعطي عطاء من لا يخشى الفاقة)','1887','3004','0');
INSERT INTO [t38171] VALUES (3060,'٣٠٣٧/ أ - (وقال ابن عمر) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - إن لله عبادا يخصهم بالنعم لمنافع العباد)','1887','3059','0');
INSERT INTO [t38171] VALUES (3061,'٣٠٣٨ - (وعن الهلالي)','1887','3004','0');
INSERT INTO [t38171] VALUES (3062,'٣٠٣٩ - (وقال - صلى الله عليه وسلم - إن لكل شيء ثمرة وثمرة المعروف تعجيل السراح).','1888','3004','0');
INSERT INTO [t38171] VALUES (3063,'٣٠٤٠ - (وعن نافع) مولى ابن عمر (عن ابن عمر) رضي الله عنه (قال قال رسول الله - صلى الله عليه وسلم - طعام الجواد دواء) لكونه يطعم الضيف مع سماحة نفس وطيب خاطر وانشراح صدر (وطعام البخيل داء)','1888','3004','0');
INSERT INTO [t38171] VALUES (3064,'٣٠٤١ - (وقال - صلى الله عليه وسلم - من عظمت نعمة الله عنده عظمت مؤنة الناس عليه)','1889','3004','0');
INSERT INTO [t38171] VALUES (3065,'٣٠٤٢ - (قالت عائشة رضي الله عنها قال رسول الله - صلى الله عليه وسلم - الجنة دار الأسخياء)','1890','3004','0');
INSERT INTO [t38171] VALUES (3066,'٣٠٤٤ - (وقال - صلى الله عليه وسلم - إن بدلاء أمتي لم يدخلوا الجنة بصلاة ولا صيام ولكن دخلوها بسخاء الأنفس وسلامة الصدور والنصح للمسلمين).','1893','3004','0');
INSERT INTO [t38171] VALUES (3067,'٣٠٤٥ - (وقال النبي - صلى الله عليه وسلم - اصنع المعروف إلى من هو أهله وإلى من ليس بأهله فإن أصبت أهله فقد أصبت أهله وإن لم تصب أهله فأنت أهله).','1893','3004','0');
INSERT INTO [t38171] VALUES (3068,'٣٠٤٦ - (وقال أبو سعيد الخدري) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - إن الله عز وجل جعل للمعروف)','1893','3004','0');
INSERT INTO [t38171] VALUES (3069,'٣٠٤٧ - (قال رسول الله - صلى الله عليه وسلم - كل معروف)','1894','3004','0');
INSERT INTO [t38171] VALUES (3070,'٣٠٤٨ - (ما أنفق الرجل من نفقة فعلى الله خلفها).','1895','3004','0');
INSERT INTO [t38171] VALUES (3071,'٣٠٤٩ - (قال - صلى الله عليه وسلم - كل معروف صدقة والدال على الخير كفاعله والله يحب إغاثة اللهفان)','1896','3004','0');
INSERT INTO [t38171] VALUES (3072,'٣٠٥٠ - (وقال - صلى الله عليه وسلم - كل معروف فعلته إلى غني أو فقير صدقة).','1896','3004','0');
INSERT INTO [t38171] VALUES (3073,'٣٠٥١ - (قال جابر) بن عبد الله الأنصاري رضي الله عنه (بعث رسول الله - صلى الله عليه وسلم - بعثا)','1896','3004','0');
INSERT INTO [t38171] VALUES (3074,'٣٠٥٢ - (النبي - صلى الله عليه وسلم - قال للزبير بن العوام)','1897','3004','0');
INSERT INTO [t38171] VALUES (3075,'٣٠٥٣ - (قال - صلى الله عليه وسلم - إياكم والشح فإنه أهلك من كان قبلكم) من الأمم (حملهم على أن سفكوا دماءهم واستحلوا محارمهم).','1898','3004','0');
INSERT INTO [t38171] VALUES (3076,'٣٠٥٤ - (قال - صلى الله عليه وسلم - إياكم والشح فإنه دعا من كان قبلكم فسفكوا دماءهم ودعاهم فاستحلوا محارمهم ودعاهم فقطعوا أرحامهم).','1898','3004','0');
INSERT INTO [t38171] VALUES (3077,'٣٠٥٥ - (قال - صلى الله عليه وسلم - لا يدخل الجنة)','1899','3004','0');
INSERT INTO [t38171] VALUES (3078,'٣٠٥٦ - (قال - صلى الله عليه وسلم - ثلاث) خصال (مهلكات شح مطاع وهوى متبع وإعجاب المرء بنفسه)','1900','3004','0');
INSERT INTO [t38171] VALUES (3079,'٣٠٥٧ - (وقال - صلى الله عليه وسلم - إن الله يبغض ثلاثة الشيخ الزاني والبخيل المنان)','1900','3004','0');
INSERT INTO [t38171] VALUES (3080,'٣٠٥٨ - (قال - صلى الله عليه وسلم - خصلتان لا تجتمعان في مؤمن البخل وسوء الخلق).','1901','3004','0');
INSERT INTO [t38171] VALUES (3081,'٣٠٥٩ - (قال - صلى الله عليه وسلم - إياكم والظلم فإن الظلم ظلمات يوم القيامة وإياكم والفحش إن الله لا يحب الفاحش ولا المتفحش وإياكم الشح فإنما أهلك من كان قبلكم الشح أمرهم بالكذب فكذبوا وأمرهم بالظلم وأمرهم بالقطيعة فقطعوا).','1901','3004','0');
INSERT INTO [t38171] VALUES (3082,'٣٠٦٠ - (وقال - صلى الله عليه وسلم - شر ما في الرجل شح هالع وجبن خالع).','1902','3004','0');
INSERT INTO [t38171] VALUES (3083,'٣٠٦١ - (قتل شهيد) أي استشهد رجل (على عهد رسول الله - صلى الله عليه وسلم - فبكته باكية فقالت واشهيداه فقال النبي - صلى الله عليه وسلم - وما يدريك أنه شهيد فلعله قد كان يتكلم بما لا يعنيه أو يبخل بما لا ينقصه).','1902','3004','0');
INSERT INTO [t38171] VALUES (3084,'٣٠٦٢ - (قال أبو سعيد الخدري) رضي الله عنه (دخل رجلان على رسول الله - صلى الله عليه وسلم - فسألاه ثمن بعير فأعطاهما دينارين فخرجا من عنده فلقيهما عمر بن الخطاب)','1902','3004','0');
INSERT INTO [t38171] VALUES (3085,'٣٠٦٣ - (وعن ابن عباس) رضي الله عنه (قال قال رسول الله - صلى الله عليه وسلم - الجود من جود الله تعالى فجودوا)','1903','3004','0');
INSERT INTO [t38171] VALUES (3086,'٣٠٦٤ - (وقال - صلى الله عليه وسلم - السخاء شجرة تنبت في الجنة فلا يلج الجنة إلا سخي والبخل شجرة تنبت في النار ولا يلج في النار إلا بخيل).','1903','3004','0');
INSERT INTO [t38171] VALUES (3087,'٣٠٦٦ - (قال علي) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - إن الله يبغض البخيل)','1907','3004','0');
INSERT INTO [t38171] VALUES (3088,'٣٠٦٧ - (قال أبو هريرة أيضا) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - لا يجتمع الإيمان والشح في قلب عبد).','1907','3004','0');
INSERT INTO [t38171] VALUES (3089,'٣٠٦٨ - (قال - صلى الله عليه وسلم - لا ينبغي للمؤمن أن يكون بخيلا ولا جبانا).','1907','3004','0');
INSERT INTO [t38171] VALUES (3090,'٣٠٦٩ - (وقال - صلى الله عليه وسلم - يقول قائلكم الشحيح أغدر من الظالم وأي ظلم أعظم عند الله من الشح حلف الله تعالى بعزته وعظمته وجلاله لا يدخل الجنة شحيح ولا بخيل).','1908','3004','0');
INSERT INTO [t38171] VALUES (3091,'٣٠٧٠ - (وروي أن رسول الله - صلى الله عليه وسلم - كان يطوف بالبيت فإذا رجل متعلق بأستار الكعبة وهو يقول بحرمة هذا البيت إلا غفرت)','1908','3004','0');
INSERT INTO [t38171] VALUES (3092,'٣٠٨١ - (الآثار قال ابن عباس رضي الله عنه لما خلق الله تعالى جنة عدن) وهي أوسط الجنات (قال لها تزيني فتزينت ثم قال لها أظهري أنهارك فأظهرت عين السلسبيل وعين الكافور وعين التسنيم فتفجر منها في الجنان أنهار الخمر وأنهار العسل واللبن ثم قال لها أظهري سررك وحجالك) محركة جمع حجلة وهي الكلة (وكراسيك وحللك وحليك وحور عينك فأظهرت فنظر إليها فقال تكلمي فقالت طوبى لمن دخلني فقال الله تعالى وعزتي لا أسكنتك بخيلا)','1909','3004','0');
INSERT INTO [t38171] VALUES (3093,'٣٠٧٢ - (كان يقال إذا أراد الله بقوم شرا أمر عليهم شرارهم وجعل أرزاقهم بأيدي بخلائهم)','1909','3004','0');
INSERT INTO [t38171] VALUES (3094,'٣٠٧٣ - (قال النبي - صلى الله عليه وسلم - أيما رجل) وفي رواية أيما امرئ (اشتهى شهوة فرد شهوته وآثر على نفسه غفر له)','1910','3004','0');
INSERT INTO [t38171] VALUES (3095,'٣٠٧٤ - (وقالت عائشة رضي الله عنها ما شبع رسول الله - صلى الله عليه وسلم - ثلاثة أيام متوالية حتى فارق الدنيا ولو شئنا لشبعنا ولكنا كنا نؤثر على أنفسنا).','1910','3004','0');
INSERT INTO [t38171] VALUES (3096,'٣٠٧٥ - (بات علي بن أبي طالب كرم الله وجهه على فراش رسول الله - صلى الله عليه وسلم -)','1910','3004','0');
INSERT INTO [t38171] VALUES (3097,'٣٠٧٦ - (قال - صلى الله عليه وسلم - الولد مبخلة)','1911','3004','0');
INSERT INTO [t38171] VALUES (3098,'٣٠٧٧ - (نهاهم عن جمع المال).','1912','3004','0');
INSERT INTO [t38171] VALUES (3099,'٣٠٧٨ - قال العراقي: حديث أبي ذر الأكثرون هم الأقلون يوم القيامة','1913','3004','0');
INSERT INTO [t38171] VALUES (3100,'٣٠٧٩ - (بلغنا أن عبد الرحمن بن عوف) رضي الله عنه (قدمت عليه عير) أي قافلة (من اليمن فضجت المدينة)','1914','3004','0');
INSERT INTO [t38171] VALUES (3101,'٣٠٨٠ - (بلغنا أن النبي - صلى الله عليه وسلم - قال عبد الرحمن بن عوف) رضي الله عنه (أما إنك أول من يدخل الجنة من أغنياء أمتي وما كدت تدخلها إلا حبوا).','1915','3004','0');
INSERT INTO [t38171] VALUES (3102,'٣٠٨١ - (تأنف من المسكنة وذلك فخر المرسلين وأنت تأنف من فخرهم)','1916','3004','0');
INSERT INTO [t38171] VALUES (3103,'٣٠٨٢ - (قال شرار أمتي الذين غذوا بالنعيم ونبتت عليه أجسامهم)','1916','3004','0');
INSERT INTO [t38171] VALUES (3104,'٣٠٨٣ - (قال رسول الله - صلى الله عليه وسلم - قال من أسف على دنيا فاتته اقترب من النار مسافة سنة).','1916','3004','0');
INSERT INTO [t38171] VALUES (3105,'٣٠٨٣ - (قال رسول الله - صلى الله عليه وسلم - قال من أحب الدنيا وسر بها أذهب خوف الآخرة من قلبه).','1917','3004','0');
INSERT INTO [t38171] VALUES (3106,'٣٠٨٥ - (قال - صلى الله عليه وسلم - يؤتى برجل يوم القيامة وقد جمع مالا من حرام وأنفقه في حرام فيقال اذهبوا به إلى النار ويؤتي برجل)','1917','3004','0');
INSERT INTO [t38171] VALUES (3107,'٣٠٨٥ - (قال رسول الله - صلى الله عليه وسلم - قال يدخل صعاليك المهاجرين) أي فقراؤهم (قبل أغنيائهم الجنة بخمسمائة عام).','1918','3004','0');
INSERT INTO [t38171] VALUES (3108,'٣٠٨٦ - (وقال - صلى الله عليه وسلم - يدخل فقراء المؤمنين الجنة قبل أغنيائهم فيتمتعون ويأكلون والآخرون جثاة على ركبهم فيقول قبلكم طلبتي أنتم حكام الناس وملوكهم فأروني ماذا صنعتم فيما أعطيتكم).','1918','3004','0');
INSERT INTO [t38171] VALUES (3109,'٣٠٨٧ - (بعض الصحابة عطش فاستسقى) أي طلب (فأتي بشربة من ماء وعسل)','1919','3004','0');
INSERT INTO [t38171] VALUES (3110,'٣٠٨٨ - (قال رسول الله - صلى الله عليه وسلم - قال سادات المؤمنين في الجنة) أي رؤساؤهم فيها من إذا تغدى لم يجد عشاء وإذا استقرض لم يجد قرضا وليس له فضل كسوة إلا ما يواريه ولا يقدر على أن يكتسب ما يغنيه يمسي مع ذلك ويصبح راضيا عن ربه فأولئك مع الذين أنعم الله عليهم من النبيين والصديقين والشهداء والصالحين أولئك رفيقا).','1920','3004','0');
INSERT INTO [t38171] VALUES (3111,'٣٠٨٩ - (روى عن أبي أمامة) صدي بن عجلان (الباهلي) رضي الله عنه (إن ثعلبة بن حاطب)','1920','3004','0');
INSERT INTO [t38171] VALUES (3112,'٣٠٩٠ - (روي عن عمران بن الحصين)','1923','3004','0');
INSERT INTO [t38171] VALUES (3113,'كتاب ذم الجاه والرياء','1927','2367','0');
INSERT INTO [t38171] VALUES (3114,'٣٠٩١ - (قال رسول الله - صلى الله عليه وسلم - إن أخوف ما أخاف على أمتي الرياء والشهوة الخفية).','1928','3113','0');
INSERT INTO [t38171] VALUES (3115,'٣٠٩٢ - (قال أنس) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - حسب امرئ من الشر)','1929','3113','0');
INSERT INTO [t38171] VALUES (3116,'٣٠٩٣ - (قال جابر بن عبد الله) رضي الله عنه (قال رسول الله حسب المرء من الشر إلا من عصمه الله من السوء أن يشير الناس إليه بالأصابع في دينه ودنياه أن الله لا ينظر إلى صوركم ولكن ينظر إلى قلوبكم وإلى أعمالكم).','1929','3113','0');
INSERT INTO [t38171] VALUES (3117,'٣٠٩٤ - (قال رسول الله - صلى الله عليه وسلم - رب أشعث أغبر ذي طمرين لا يؤبه له لو أقسم على الله لأبره منهم البراء بن مالك).','1930','3113','0');
INSERT INTO [t38171] VALUES (3118,'٣٠٩٥ - (وقال ابن مسعود) رضي الله عنه (قال النبى - صلى الله عليه وسلم - رب ذي طمرين لا يؤبه له لو أقسم على الله لأبره لو قال اللهم إني أسألك الجنة لأعطاه ولم يعطه من الدنيا شيئا).','1931','3113','0');
INSERT INTO [t38171] VALUES (3119,'٣٠٩٦ - (قال - صلى الله عليه وسلم - ألا أدلكم على أهل الجنة كل ضعيف مستضعف لو أقسم على الله لأبره وأهل النار كل متكبر مستكبر جواظ.','1931','3113','0');
INSERT INTO [t38171] VALUES (3120,'٣٠٩٧ - (قال أبو هريرة) رضي الله عنه (قال - صلى الله عليه وسلم - إن أهل الجنة كل أشعث أغبر ذي طمرين لا يؤبه له الذين إذا استأذنوا على الأمراء لم يؤذن لهم وإذا خطبوا النساء لم ينكحوا وإذا قالوا لم ينصت لهم حوائج أحدهم تتلجلج في صدره لو قسم نوره يوم القيامة على الناس لوسعهم)','1932','3113','0');
INSERT INTO [t38171] VALUES (3121,'٣٠٩٨ - (وقال - صلى الله عليه وسلم - إن من أمتي من لو أتى أحدكم يسأله دينارا لم يعطه إياه ولو سأله درهما لم يعطه إياه ولو سأله فلسا لم يعطه إياه ولو سأل الله تعالى الجنة أعطاه إياها ولو سأله الدنيا لم يعطه إياها','1932','3113','0');
INSERT INTO [t38171] VALUES (3122,'٣٠٩٩ - (وروي أن عمر رضي الله عنه دخل المسجد فإذا هو بمعاذ بن جبل يبكي عند قبر رسول الله - صلى الله عليه وسلم - فقال) له عمر (ما يبكيك) يا معاذ (فقال) معاذ (سمعت رسول الله - صلى الله عليه وسلم - يقول إن اليسير من الرياء شرك وإن الله يحب الأتقياء الأخفياء الذين إذا غابوا لم يفقدوا وإذا حضروا لم يعرفوا قلوبهم مصابيح الهدى ينجون من كل غبراء مظلمة).','1933','3113','0');
INSERT INTO [t38171] VALUES (3123,'٣١٠٠ - (قال أبو أمامة) الباهلي رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - يقول الله تعالى إن أغبط أوليائي رجل مؤمن خفيف الحاذ)','1934','3113','0');
INSERT INTO [t38171] VALUES (3124,'٣١٠١ - (قال - صلى الله عليه وسلم - حب المال والجاه ينبتان النفاق في القلب كما ينبت الماء البقل).','1935','3113','0');
INSERT INTO [t38171] VALUES (3125,'٣١٠٢ - (قال - صلى الله عليه وسلم - ما ذئبان ضاريان أرسلا في زريبة غنم بأكثر فسادا من حب الشرف والمال في دين المرء المسلم)','1935','3113','0');
INSERT INTO [t38171] VALUES (3126,'٣١٠٣ - (قال - صلى الله عليه وسلم - إنما هلاك الناس باتباع الهوى وحب الثناء).','1936','3113','0');
INSERT INTO [t38171] VALUES (3127,'٣١٠٤ - (قال رسول الله - صلى الله عليه وسلم - إذ قال اللهم اغفر لقومي اللهم اهد قومي فإنهم لا يعلمون إذ ضربوه)','1936','3113','0');
INSERT INTO [t38171] VALUES (3128,'٣١٠٥ - (قال - صلى الله عليه وسلم - رأس التواضع أن يكره أن يذكر بالبر والتقوى).','1936','3113','0');
INSERT INTO [t38171] VALUES (3129,'٣١٠٦ - (قد روي في بعض الأخبار ما هو قاصم لظهور أمثالنا إن صح) وروده (إذ روي أنه - صلى الله عليه وسلم - قال ويل للصائم ويل للقائم وويل لصاحب الصوف إلا من فقيل يا رسول الله إلامن فقال: إلا من تنزهت نفسه عن الدنيا وأبغض المدحة واستحب المذمة).','1937','3113','0');
INSERT INTO [t38171] VALUES (3130,'٣١٠٧ - (قال تعالى فمن كان يرجوا لقاء ربه) أي يأمل حسن لقائه وثوابه (فليعمل عملا صالحا) يرتضيه الله (ولا يشرك بعبادة ربه أحدا) بأن يرائيه أو يطلب منه أجرا (أنزلت فيمن يطلب الأجر والحمد بعبادته وأعماله).','1937','3113','0');
INSERT INTO [t38171] VALUES (3131,'٣١٠٨ - (قال - صلى الله عليه وسلم - حين سأله رجل يا رسول الله فيم النجاة فقال أن لا يعمل العبد بطاعة الله يريد بها الناس)','1938','3113','0');
INSERT INTO [t38171] VALUES (3132,'٣١٠٩ - (قال ابن عمر) رضي الله عنه (قال - صلى الله عليه وسلم - من راءى راءى الله به ومن سمع سمع الله به).','1939','3113','0');
INSERT INTO [t38171] VALUES (3133,'٣١١٠ - (في حديث آخر طويل إن الله عز وجل يقول لملائكته أن هذا لم يردني بعمله فاجعلوه في سجين)','1939','3113','0');
INSERT INTO [t38171] VALUES (3134,'٣١١١ - (قال - صلى الله عليه وسلم - إن أخوف ما أخاف عليكم الشرك الأصغر قالوا وما الشرك الأصغر يا رسول الله قال الرياء يقول الله عز وجل يوم القيامة إذا جازى العباد بأعمالهم اذهبوا إلى الذين كنتم تراؤن في الدنيا فانظروا هل تجدون عندهم جزاء).','1940','3113','0');
INSERT INTO [t38171] VALUES (3135,'٣١١٢ - (قال - صلى الله عليه وسلم - استعيذوا بالله من جب الحزن قيل وما هو يا رسول الله قال واد في جهنم أعد للقراء المرائين).','1941','3113','0');
INSERT INTO [t38171] VALUES (3136,'٣١١٢ - (قال - صلى الله عليه وسلم - يقول الله عز وجل من عمل عملا أشرك فيه غيري فهو له كله وأنا منه بريء فهو له كله وأنا أغنى الأغنياء عن الشرك).','1941','3113','0');
INSERT INTO [t38171] VALUES (3137,'٣١١٤ - (قال نبينا - صلى الله عليه وسلم - لا يقبل الله عملا فيه مثقال ذرة من رياء).','1942','3113','0');
INSERT INTO [t38171] VALUES (3138,'٣١١٥ - (قال عمر لمعاذ بن جبل) رضي الله عنهما (حين رآه يبكي) عند القبر (ما يبكيك قال حديث سمعته من صاحب هذا القبر يعني النبي - صلى الله عليه وسلم - يقول إن أدنى الرياء شرك).','1942','3113','0');
INSERT INTO [t38171] VALUES (3139,'٣١١٦ - (قال - صلى الله عليه وسلم - إن في ظل العرش يوم لا ظل إلا ظله رجلا تصدق بيمينه فكاد أن يخفيها عن شماله)','1943','3113','0');
INSERT INTO [t38171] VALUES (3140,'٣١١٧ - (قال - صلى الله عليه وسلم - إن المرائي ينادي يوم القيامة يا فاجر يا غادر يا مرائي ضل عملك وحبط أجرك اذهب فخذ أجرك ممن كنت تعلم له).','1943','3113','0');
INSERT INTO [t38171] VALUES (3141,'٣١١٨ - (قال - صلى الله عليه وسلم - لما خلق الله الأرض مادت)','1944','3113','0');
INSERT INTO [t38171] VALUES (3142,'٣١١٩ - (وروي عبد الله بن المبارك)','1945','3113','0');
INSERT INTO [t38171] VALUES (3143,'٣١٢٠ - (قال رجل لعبادة بن الصامت)','1948','3113','0');
INSERT INTO [t38171] VALUES (3144,'٣١٢١ - (وسأل رجل سعيد بن المسيب) رحمه الله تعالى (فقال إن أحدنا يصطنع المعروف يحب أن يحمد ويؤجر فقال له أتحب أن تمقت قال لا قال فإذا عملت عملا لله فأخلصه وقال الضحاك)','1948','3113','0');
INSERT INTO [t38171] VALUES (3145,'٣١٢٢ - (ضرب عمر) رضي الله عنه (رجلا بالدرة ثم قال له) عمر (اقتصها مني قال لا بل أدعها لله ولك فقال له عمر ما صنعت شيئا إما أن تدعها إلي فأعرف ذلك لك أو تدعها لله وحده قال ودعتها لله وحده قال فنعم إذا)','1949','3113','0');
INSERT INTO [t38171] VALUES (3146,'٣١٢٣ - (وقال أبو سليمان) الداراني رحمه الله تعالى (التوقي على العمل أشد من العمل)','1949','3113','0');
INSERT INTO [t38171] VALUES (3147,'٣١٢٤ - (سماه رسول الله - صلى الله عليه وسلم - الشرك الأصغر).','1950','3113','0');
INSERT INTO [t38171] VALUES (3148,'٣١٢٥ - (أخفى من دبيب النمل كما ورد به الخبر).','1950','3113','0');
INSERT INTO [t38171] VALUES (3149,'٣١٢٦ - (قد روي عن علي رضي الله عنه أنه قال إن الله عز وجل يقول للقراء) أي العلماء (يوم القيامة ألم يكن يرخص عليكم السعر ألم تكونوا تبتدؤن بالسلام ألم تكونوا تقضى لكم الحوائج وفي الحديث الآخر لا أجر لكم قد استوفيتم أجوركم)','1951','3113','0');
INSERT INTO [t38171] VALUES (3150,'٣١٢٧ - (قال رسول الله - صلى الله عليه وسلم - ما ستر الله على عبد ذنبا) من ذنوبه (في الدنيا) بأن لم يفضحه به (إلا ستره عليه في الآخرة) تفلا يفضحه به على رؤس الأشهاد.','1951','3113','0');
INSERT INTO [t38171] VALUES (3151,'٣١٢٨ - (روي عن ابن مسعود) رضي الله عنه (أنه سمع رجلا يقول قرأت البارحة سورة البقرة قال ذلك حظك منها وروي','1951','3113','0');
INSERT INTO [t38171] VALUES (3152,'٣١٢٩ - (قال - صلى الله عليه وسلم - العمل كالوعاء إذا طاب آخره طاب أوله).','1952','3113','0');
INSERT INTO [t38171] VALUES (3153,'٣١٣١ - (وروى) أيضا (من راءى بعمله ساعة حبط عمله الذي كان قبله).','1953','3113','0');
INSERT INTO [t38171] VALUES (3154,'٣١٣٢ - (روي أن رجلا قال لرسول الله - صلى الله عليه وسلم - يا رسول الله إني أسر العمل) أي أخفيه (لا أحب أن يطلع عليه فيطلع عليه فيسرني قال لك أجران أجر السر وأجر العلانية).','1953','3113','0');
INSERT INTO [t38171] VALUES (3155,'٣١٣٣ - (قال - صلى الله عليه وسلم - من غزا) وهو (لا يبغي) في غزوانه (إلا عقالا)','1953','3113','0');
INSERT INTO [t38171] VALUES (3156,'٣١٣٤ - (ومن طلب رضاهم في سخط الله سخط الله عليه وأسخطهم أيضا عليه)','1954','3113','0');
INSERT INTO [t38171] VALUES (3157,'٣١٣٥ - (قال - صلى الله عليه وسلم - شاعر بني تميم) هو الأقرع بن حابس (أن مدحي زين وإن ذمي شين فقال له - صلى الله عليه وسلم - كذبت ذلك الله رب العالمين الذي لا إله إلا هو).','1954','3113','0');
INSERT INTO [t38171] VALUES (3158,'٣١٣٦ - (جابر) بن عبد الله الأنصاري رضي الله عنه (بقوله بايعنا رسول الله - صلى الله عليه وسلم - تحت الشجرة)','1955','3113','0');
INSERT INTO [t38171] VALUES (3159,'٣١٣٧ - (حب الدنيا وغلبة الشهوات)','1957','3113','0');
INSERT INTO [t38171] VALUES (3160,'٣١٣٨ - (روي أن أصحاب رسول الله - صلى الله عليه وسلم - شكوا إليه وقالوا تعرض لقلوبنا أشياء لأن نخر من السماء) أي نسقط (فتخطفنا الطير أو تهوي بنا الريح في مكان سحيق) أي بعيد الغور (أحب إلينا من أن نتكلم بها فقال) - صلى الله عليه وسلم - (أوقد وجدتموه قالوا نعم) وجدناه (قال ذلك صريح الإيمان).','1957','3113','0');
INSERT INTO [t38171] VALUES (3161,'٣١٣٩ - (يروى عن النبي - صلى الله عليه وسلم - في حديث ابن عباس) رضي الله عنهما (أنه قال الحمد لله الذي رد كيد الشيطان إلى الوسوسة).','1958','3113','0');
INSERT INTO [t38171] VALUES (3162,'٣١٤٠ - (قال النبي - صلى الله عليه وسلم - من سن سنة حسنة فعمل بها كان له أجرها وأجر من أتبعه).','1958','3113','0');
INSERT INTO [t38171] VALUES (3163,'٣١٤١ - (إن الله يؤيد هذا الدين بالرجل الفاجر وبأقوام لا خلاق لهم كما ورد) ذلك (في الأخبار وبعض المرائين ممن يقتدي به منهم).','1959','3113','0');
INSERT INTO [t38171] VALUES (3164,'٣١٤٢ - (روي في بعض الحديث أن عمل السر يضاعف على عمل العلانية بسبعين ضعفا ويضاعف عمل العلانية إذا استن بعامله على عمل السر بسبعين ضعفا).','1960','3113','0');
INSERT INTO [t38171] VALUES (3165,'٣١٤٣ - (قال عثمان) رضي الله عنه (ما تغنيت ولا تمنيت ولا مسست ذكري بيميني منذ بايعت رسول الله - صلى الله عليه وسلم -).','1961','3113','0');
INSERT INTO [t38171] VALUES (3166,'٣١٤٤ - (قال شداد بن أوس) رضي الله عنه (ما تكلمت بكلمة منذ أسلمت حتى أزمها وأخطمها)','1961','3113','0');
INSERT INTO [t38171] VALUES (3167,'٣١٤٥ - (قال - صلى الله عليه وسلم - الحياء خير كله).','1962','3113','0');
INSERT INTO [t38171] VALUES (3168,'٣١٤٦ - (قال - صلى الله عليه وسلم - الحياء شعبة من الإيمان).','1962','3113','0');
INSERT INTO [t38171] VALUES (3169,'٣١٤٧ - (قال - صلى الله عليه وسلم - الحياء لا يأتي إلا بخير).','1962','3113','0');
INSERT INTO [t38171] VALUES (3170,'٣١٤٨ - (قال - صلى الله عليه وسلم - إن الله يحب الحيي الحليم).','1963','3113','0');
INSERT INTO [t38171] VALUES (3171,'٣١٤٩ - (قال النبي - صلى الله عليه وسلم - ليوم من إمام عادل خير من عبادة الرجل وحده ستين عاما).','1964','3113','0');
INSERT INTO [t38171] VALUES (3172,'٣١٥٠ - (قال - صلى الله عليه وسلم - أول من يدخل الجنة ثلاثة الإمام المقسط أحدهم).','1964','3113','0');
INSERT INTO [t38171] VALUES (3173,'٣١٥١ - (قال - صلى الله عليه وسلم - أقرب الناس منزلا يوم القيامة إمام عادل رواه أبو سعيد الخدري)','1964','3113','0');
INSERT INTO [t38171] VALUES (3174,'٣١٥٢ - (قال - صلى الله عليه وسلم - ما من والي عشيرة إلا جاء يوم القيامة مغلولة يده إلى عنقه أطلقه عدله أو أوبقه جوره رواه معقل بن يسار)','1965','3113','0');
INSERT INTO [t38171] VALUES (3175,'٣١٥٣ - (روى الحسن) البصري رحمه الله تعالى (أن رجلا ولاه النبي - صلى الله عليه وسلم - فقال) الرجل (للنبي - صلى الله عليه وسلم - خر لي فقال اجلس).','1966','3113','0');
INSERT INTO [t38171] VALUES (3176,'٣١٥٤ - (حديث عبد الرحمن بن سمرة) العبشمي القرشي رضي الله عنه (إذ قال له النبي - صلى الله عليه وسلم - يا عبد الرحمن) بن سمرة (لا تسأل الإمارة فإنك إن أوتيتها من غير مسألة أعنت عليها وإن أوتيتها عن مسألة وكلت إليها)','1966','3113','0');
INSERT INTO [t38171] VALUES (3177,'٣١٥٥ - (قال - صلى الله عليه وسلم - القضاة ثلاثة واحد في الجنة واثنان في النار).','1967','3113','0');
INSERT INTO [t38171] VALUES (3178,'٣١٥٦ - (قال) - صلى الله عليه وسلم - (من استقضى فقد ذبح بغير سكين).','1967','3113','0');
INSERT INTO [t38171] VALUES (3179,'٣١٥٧ - (قال إنكم تحرصون على الإمارة وإنها حسرة يوم القيامة وندامة إلا من أخذها بحقها).','1968','3113','0');
INSERT INTO [t38171] VALUES (3180,'٣١٥٨ - (نعمت المرضعة وبئست الفاطمة).','1968','3113','0');
INSERT INTO [t38171] VALUES (3181,'٣١٥٩ - (نهى رسول الله - صلى الله عليه وسلم - عن القضاء).','1969','3113','0');
INSERT INTO [t38171] VALUES (3182,'٣١٦٠ - (قال - صلى الله عليه وسلم - لأن يهدي الله بك رجلا خير لك من الدنيا وما فيها).','1969','3113','0');
INSERT INTO [t38171] VALUES (3183,'٣١٦١ - (قال - صلى الله عليه وسلم - أيما داع دعا إلى هدى واتبع عليه كان له أجره وأجر من اتبعه).','1969','3113','0');
INSERT INTO [t38171] VALUES (3184,'٣١٦٢ - (قال إنما تجالسون بالأمانة)','1970','3113','0');
INSERT INTO [t38171] VALUES (3185,'٣١٦٣ - (جاء في الخبر نعوذ بالله من خشوع النفاق).','1970','3113','0');
INSERT INTO [t38171] VALUES (3186,'٣١٦٤ - (في الخبر إن للرياء سبعين بابا).','1971','3113','0');
INSERT INTO [t38171] VALUES (3187,'٣١٦٥ - (روى) أبو رقية (تميم)','1972','3113','0');
INSERT INTO [t38171] VALUES (3188,'٣١٦٦ - (يقول) فيما أخبرنا عنه نبينا - صلى الله عليه وسلم - (من تقرب إلي)','1973','3113','0');
INSERT INTO [t38171] VALUES (3189,'كتاب ذم العجب والكبر','1975','2367','0');
INSERT INTO [t38171] VALUES (3190,'٣١٦٧ - (قال رسول الله - صلى الله عليه وسلم - قال الله تعالى الكبرياء ردائي والعظمة إزاري).','1976','3189','0');
INSERT INTO [t38171] VALUES (3191,'٣١٦٨ - (قال - صلى الله عليه وسلم - لا يدخل الجنة من كان في قلبه مثقال حبة من خردل من كبر ولا يدخل النار من كان في قلبه مثقال حبة من خردل من إيمان).','1976','3189','0');
INSERT INTO [t38171] VALUES (3192,'٣١٦٩ - (قال أبو هريرة) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - الكبرياء ردائي والعظمة إزاري فمن نازعني واحدا منهما ألقيته في جهنم ولا أبالي).','1977','3189','0');
INSERT INTO [t38171] VALUES (3193,'٣١٧٠ - (قام ابن عمر يبكي فقالوا وما يبكيك يا أبا عبد الرحمن فقال هذا يعني عبد الله بن عمرو) بن العاص (زعم أنه سمع رسول الله - صلى الله عليه وسلم - يقول من كان في قلبه مثقال حبة من خردل من كبر أكبه الله في النار على وجهه).','1978','3189','0');
INSERT INTO [t38171] VALUES (3194,'٣١٧١ - (قال - صلى الله عليه وسلم - لا يزال الرجل يذهب بنفسه حتى يكتب في الجبارين فيصيبه ما أصابهم من العذاب).','1978','3189','0');
INSERT INTO [t38171] VALUES (3195,'٣١٧٢ - (قال - صلى الله عليه وسلم - يخرج من النار عنق له أذنان تسمعان وعينان تبصران ولسان ينطق يقول وكلت بثلاثة بكل جبار عنيد وبكل من دعا مع الله إلها أخر وبالمصورين).','1979','3189','0');
INSERT INTO [t38171] VALUES (3196,'٣١٧٣ - (قال - صلى الله عليه وسلم - لا يدخل الجنة جبار ولا بخيل ولا سيء الملكة).','1979','3189','0');
INSERT INTO [t38171] VALUES (3197,'٣١٧٤ - (وقال - صلى الله عليه وسلم - بئس العبد عبد تجبر واعتدى ونسي الجبار الأعلى بئس الكبير المتعال بئس العبد عبد غفل وسها ونسي المقابر والبلى بئس العبد عبد عتا وبغى ونسي المبدأ والمنتهى).','1979','3189','0');
INSERT INTO [t38171] VALUES (3198,'٣١٧٥ - (قيل يا رسول الله ما أعظم كبر فلان فقال أليس بعده الموت).','1980','3189','0');
INSERT INTO [t38171] VALUES (3199,'٣١٧٧ - (قال النبي - صلى الله عليه وسلم - أهل النار كل جعظري) وهو الفظ الغليظ المنتقم بما ليس عنده (جواظ) وهو الكثير اللحم المختال في مشيته (مستكبر) على إخوانه (جماع) للمال (مناع) للحق (وأهل الجنة الضعفاء المقلون)','1982','3189','0');
INSERT INTO [t38171] VALUES (3200,'٣١٧٨ - (قال - صلى الله عليه وسلم - إن أحبكم إلينا وأقربكم منا في الآخرة أحاسنكم أخلاقا وإن أبغضكم إلينا وأبعدكم منا الثرثارون المتشدقون والمتفيهقون قالوا يا رسول الله قد علمنا الثرثارون والمتشدقون فما المتفيهقون قال المتكبرون).','1982','3189','0');
INSERT INTO [t38171] VALUES (3201,'٣١٧٩ - (قال - صلى الله عليه وسلم - يحشر المتكبرون يوم القيامة ذرا في مثل صور الرجال يعلوهم كل شيء من الصغار)','1983','3189','0');
INSERT INTO [t38171] VALUES (3202,'٣١٨٠ - (وقال أبو هريرة) رضي الله عنه (قال - صلى الله عليه وسلم - يحشر الجبارون المتكبرون يوم القيامة في صور الذر تطؤهم الناس لهوانهم','1983','3189','0');
INSERT INTO [t38171] VALUES (3203,'٣١٨١ - (قال - صلى الله عليه وسلم -) في دعائه (اللهم إني أعوذ بك من نفخة الكبرياء).','1984','3189','0');
INSERT INTO [t38171] VALUES (3204,'٣١٨٢ - (قال أبو بكر الصديق) رضي الله عنه (لا يحقرن أحد أحدا من المسلمين)','1984','3189','0');
INSERT INTO [t38171] VALUES (3205,'٣١٨٣ - (قال - صلى الله عليه وسلم - من فارق روحه جسده وهو بريء من ثلاثة دخل الجنة الكبر والدين والغلول).','1984','3189','0');
INSERT INTO [t38171] VALUES (3206,'٣١٨٤ - (عن النبي - صلى الله عليه وسلم - قال إن في جهنم واديا يقال له هبهب حق على الله أن يسكنه كل جبار فإياك يا بلال أن تسكنه).','1985','3189','0');
INSERT INTO [t38171] VALUES (3207,'٣١٨٥ - (قال - صلى الله عليه وسلم - إن في النار قصرا يجعل فيه المتكبرون ويطبق عليهم).','1985','3189','0');
INSERT INTO [t38171] VALUES (3208,'٣١٨٦ - (قال النعمان بن بشير)','1985','3189','0');
INSERT INTO [t38171] VALUES (3209,'٣١٨٨ - (قال - صلى الله عليه وسلم - بينما رجل يتبختر في برديه)','1986','3189','0');
INSERT INTO [t38171] VALUES (3210,'٣١٨٩ - (قال - صلى الله عليه وسلم - من جر ثوبه خيلاء لم ينظر الله إليه يوم القيامة)','1988','3189','0');
INSERT INTO [t38171] VALUES (3211,'٣١٩٠ - (قال زيد بن أسلم)','1988','3189','0');
INSERT INTO [t38171] VALUES (3212,'٣١٩١ - (وروي أن رسول الله - صلى الله عليه وسلم - بزق يوما على كفه ووضع أصبعه عليه وقال يقول الله تعالى ابن آدم أتعجزني وقد خلقتك من مثل هذه)','1989','3189','0');
INSERT INTO [t38171] VALUES (3213,'٣١٩٢ - (قال - صلى الله عليه وسلم - إذا مشت أمتي المطيطاء)','1990','3189','0');
INSERT INTO [t38171] VALUES (3214,'٣١٩٣ - (قال - صلى الله عليه وسلم - من تعظم في نفسه)','1991','3189','0');
INSERT INTO [t38171] VALUES (3215,'٣١٩٤ - (قال رسول الله - صلى الله عليه وسلم - ما زاد الله عبدا بعفو إلا عزا وما تواضع أحد لله إلا رفعه الله).','1991','3189','0');
INSERT INTO [t38171] VALUES (3216,'٣١٩٥ - (قال - صلى الله عليه وسلم - ما من أحد)','1991','3189','0');
INSERT INTO [t38171] VALUES (3217,'٣١٩٦ - (وقال - صلى الله عليه وسلم - طوبى لمن تواضع في غير مسكنة)','1992','3189','0');
INSERT INTO [t38171] VALUES (3218,'٣١٩٧ - (وعن أبي سلمة المديني عن أبيه عن جده قال كان رسول الله - صلى الله عليه وسلم - عندنا بقباء)','1993','3189','0');
INSERT INTO [t38171] VALUES (3219,'٣١٩٨ - (روي أن النبي - صلى الله عليه وسلم - كان في نفر من أصحابه في بيته يأكلون فقام سائل على الباب وبه زمانة)','1994','3189','0');
INSERT INTO [t38171] VALUES (3220,'٣١٩٩ - (قال - صلى الله عليه وسلم - خيرني ربي بين أمرين أن أكون عبدا رسولا أو ملكا نبيا فلم أدر أيهما اختار وكان صفيي من الملائكة جبريل)','1994','3189','0');
INSERT INTO [t38171] VALUES (3221,'٣٢٠٠ - (وأوحى الله تعالى إلى موسى عليه السلام) يا موسى (إنما أقبل صلاة من تواضع لعظمتي ولم يتعاظم على خلقي وألزم قلبه خوفي وقطع نهاره بذكرى وكلف نفسه عن الشهوات من أجلي)','1995','3189','0');
INSERT INTO [t38171] VALUES (3222,'٣٢٠١ - (قال - صلى الله عليه وسلم - الكرم التقوى والشرف التواضع).','1995','3189','0');
INSERT INTO [t38171] VALUES (3223,'٣٢٠٢ - (النبي - صلى الله عليه وسلم - قال إذا هدى الله عبدا للإسلام وحسن صورته)','1996','3189','0');
INSERT INTO [t38171] VALUES (3224,'٣٢٠٣ - (وقال - صلى الله عليه وسلم - أربع) خصال (لا يعطيهن الله إلا من يحب)','1996','3189','0');
INSERT INTO [t38171] VALUES (3225,'٣٢٠٤ - (قال ابن عباس) رضي الله عنه (قال - صلى الله عليه وسلم - إذا تواضع العبد رفعه الله إلى السماء السابعة).','1997','3189','0');
INSERT INTO [t38171] VALUES (3226,'٣٢٠٥ - (قال - صلى الله عليه وسلم - التواضع لا يزيد العبد إلا رفعة فتواضعوا يرحمكم الله).','1997','3189','0');
INSERT INTO [t38171] VALUES (3227,'٣٢٠٦ - (وروي أن رسول الله - صلى الله عليه وسلم - كان يطعم فجاء رجل أسود)','1998','3189','0');
INSERT INTO [t38171] VALUES (3228,'٣٢٠٧ - (وقال - صلى الله عليه وسلم - إنه ليعجبني أن يحمل الرجل شيئا في يده يكون مهناة)','1998','3189','0');
INSERT INTO [t38171] VALUES (3229,'٣٢٠٨ - (وقال - صلى الله عليه وسلم - ما لي لا أرى عليكم حلاوة العبادة قالوا وما حلاوة العبادة قال التواضع).','1998','3189','0');
INSERT INTO [t38171] VALUES (3230,'٣٢٠٩ - (قال - صلى الله عليه وسلم - إذا رأيتم المتواضعين فتواضعوا لهم وإذا رأيتم المتكبرين فتكبروا عليهم فإن ذلك مذلة لهم وصغار).','1999','3189','0');
INSERT INTO [t38171] VALUES (3231,'٣٢١٠ - (قال عمر رضي الله عنه إذا تواضع العبد لله رفع الله حكمته وقال انتعش)','1999','3189','0');
INSERT INTO [t38171] VALUES (3232,'٣٢١١ - (روي عن النبي - صلى الله عليه وسلم - أنه قال يكون في آخر الزمان زعيم القوم) أي رئيسهم (أرذلهم ما تكلمت عليكم).','2000','3189','0');
INSERT INTO [t38171] VALUES (3233,'٣٢١٢ - (قال النبي - صلى الله عليه وسلم - اللهم إني أعوذ بك من نفخة الكبرياء)','2000','3189','0');
INSERT INTO [t38171] VALUES (3234,'٣٢١٣ - (قال - صلى الله عليه وسلم - لا يدخل الجنة من في قلبه مثقال ذرة من كبر)','2001','3189','0');
INSERT INTO [t38171] VALUES (3235,'٣٢١٤ - (قال) الكبر (من سفه الحق) أي جحده (وغمص الناس)','2001','3189','0');
INSERT INTO [t38171] VALUES (3236,'٣٢١٥ - (قالت قريش لرسول الله - صلى الله عليه وسلم - كيف نجلس إليك وعندك هؤلاء إشارة إلى فقراء المسلمين فازدروهم بأعينهم وتكبروا عن مجالستهم فأنزل الله تعالى ولا تطرد الذين يدعون ربهم بالغداة والعشي إلى قوله)','2002','3189','0');
INSERT INTO [t38171] VALUES (3237,'٣٢١٦ - (سأله ثابت بن قيس بن شماس)','2004','3189','0');
INSERT INTO [t38171] VALUES (3238,'٣٢١٧ - (قال - صلى الله عليه وسلم - آفة العلم الخيلاء).','2005','3189','0');
INSERT INTO [t38171] VALUES (3239,'٣٢١٨ - (قال رسول الله - صلى الله عليه وسلم - فيما رواه العباس)','2005','3189','0');
INSERT INTO [t38171] VALUES (3240,'٣٢١٩ - (قوله سيأتي على الناس زمان من تمسك بعشر ما أنتم عليه نجا).','2006','3189','0');
INSERT INTO [t38171] VALUES (3241,'٣٢٢٠ - (قال - صلى الله عليه وسلم - إذا سمعتم الرجل يقول هلك الناس فهو أهلكهم)','2006','3189','0');
INSERT INTO [t38171] VALUES (3242,'٣٢٢١ - (قال - صلى الله عليه وسلم - كفى بالمرء شرا أن يحقر أخاه المسلم).','2006','3189','0');
INSERT INTO [t38171] VALUES (3243,'٣٢٢٢ - (روي أن رجلا في بني إسرائيل أتى عابدا)','2006','3189','0');
INSERT INTO [t38171] VALUES (3244,'٣٢٢٣ - (روي أن رجلا ذكر بخير للنبي - صلى الله عليه وسلم - فأقبل)','2007','3189','0');
INSERT INTO [t38171] VALUES (3245,'٣٢٢٤ - (وقد أورد ابن يونس في تاريخ الصحابة الذين دخلوا مصر في ترجمة عبد الله بن الحارث أنه قال ما رأيت أحدا أكثر تبسما من رسول الله - صلى الله عليه وسلم - رواه من طريق ابن لهيعة حدثنا عبيد الله بن المغيرة قال سمعت عبد الله بن الحارث يقول فساقه).','2007','3189','0');
INSERT INTO [t38171] VALUES (3246,'٣٢٢٥ - (قول رسول الله - صلى الله عليه وسلم - لا يدخل الجنة من في قلبه مثقال حبة من خردل من كبر)','2008','3189','0');
INSERT INTO [t38171] VALUES (3247,'٣٢٢٦ - (روي عن أبي ذر)','2008','3189','0');
INSERT INTO [t38171] VALUES (3248,'٣٢٢٧ - (روي أن رجلين تفاخرا عند النبي - صلى الله عليه وسلم - فقال أحدهما للآخر أنا فلان بن فلان فمن أنت لا أم لك فقال النبي - صلى الله عليه وسلم - افتخر رجلان عند موسى عليه السلام فقال أحدهما أنا فلان بن فلان حتى عد تسعة فأوحى الله تعالى إلى موسى عليه السلام قل للذي افتخر بل التسعة من أهل النار وأنت عاشرهم)','2008','3189','0');
INSERT INTO [t38171] VALUES (3249,'٣٢٢٨ - (قال - صلى الله عليه وسلم - ليدعن) أي ليتركن (أقوام الفخر بآبائهم وقد صاروا فحما في جهنم أو ليكونن أهون على الله من الجعلان)','2009','3189','0');
INSERT INTO [t38171] VALUES (3250,'٣٢٢٩ - (روي عن عائشة رضي الله عنها أنها قالت دخلت امرأة) قيل أنها من الأنصار (على النبي - صلى الله عليه وسلم - فقلت بيدي هكذا أي أنها قصيرة فقال - صلى الله عليه وسلم - قد اغتبتيها)','2009','3189','0');
INSERT INTO [t38171] VALUES (3251,'٣٢٣٠ - (كان رسول الله - صلى الله عليه وسلم - في بعض الأوقات يمشي مع الأصحاب فيأمرهم بالتقدم)','2010','3189','0');
INSERT INTO [t38171] VALUES (3252,'٣٢٣١ - (كما أخرج الثوب الجديد في الصلاة وأبدله بالخليع لأحد هذين المعنيين).','2010','3189','0');
INSERT INTO [t38171] VALUES (3253,'٣٢٣٢ - (لا يأخذ متاعه ويحمله إلى بيته وهو خلاف عادة المتواضعين كان رسول الله - صلى الله عليه وسلم - يفعل ذلك).','2010','3189','0');
INSERT INTO [t38171] VALUES (3254,'٣٢٣٣ - (قال النبي - صلى الله عليه وسلم - البذاذة من الإيمان).','2011','3189','0');
INSERT INTO [t38171] VALUES (3255,'٣٢٣٤ - (قال - صلى الله عليه وسلم - من ترك زينة لله ووضع ثيابا حسنة تواضعا لله ابتغاء مرضاته كان حقا على الله أن يدخر له عبقري الجنة).','2011','3189','0');
INSERT INTO [t38171] VALUES (3256,'٣٢٣٥ - (قال - صلى الله عليه وسلم - كلوا واشربوا والبسوا وتصدقوا في غير سرف ولا مخيلة إن الله يحب أن يظهر أثر نعمته على عبده).','2012','3189','0');
INSERT INTO [t38171] VALUES (3257,'٣٢٣٦ - (اعلم أن حديث الأبدال','2012','3189','0');
INSERT INTO [t38171] VALUES (3258,'٣٢٣٧ - (كان يأكل على الأرض)','2016','3189','0');
INSERT INTO [t38171] VALUES (3259,'٣٢٣٨ - (وقيل الصلاة عماد الدين)','2016','3189','0');
INSERT INTO [t38171] VALUES (3260,'٣٢٣٩ - (قال النبي - صلى الله عليه وسلم - يؤتى بالعالم يوم القيامة فيلقى في النار فتندلق أقتابه) أي معاؤه (فيدور بها كما يدور الحمار بالرحا فيطيف به أهل النار فيقولون مالك) أي ما شأنك (فيقول كنت آمر بالخير ولا آتيه وأنهى عن الشر وآتيه).','2017','3189','0');
INSERT INTO [t38171] VALUES (3261,'٣٢٤٠ - (قال - صلى الله عليه وسلم - فضل العالم على العباد كفضلي على أدنى رجل من أصحابي)','2017','3189','0');
INSERT INTO [t38171] VALUES (3262,'٣٢٤١ - (فالحكمة ضالة المؤمن فإذا وجدها ينبغي أن يشكر من دله عليها)','2018','3189','0');
INSERT INTO [t38171] VALUES (3263,'٣٢٤٢ - (في الخبر من حمل الفاكهة أوالشيء فقد برئ من الكبر).','2018','3189','0');
INSERT INTO [t38171] VALUES (3264,'٣٢٤٣ - (قال - صلى الله عليه وسلم - من اعتقل البعير ولبس الصوف فقد بريء من الكبر).','2018','3189','0');
INSERT INTO [t38171] VALUES (3265,'٣٢٤٤ - (قال - صلى الله عليه وسلم - إنما أنا عبد آكل بالأرض وألبس الصوف وأعتقل البعير وألعق أصابعي وأجيب دعوة المملوك فمن رغب عن سنتي فليس مني).','2019','3189','0');
INSERT INTO [t38171] VALUES (3266,'٣٢٤٥ - (قال - صلى الله عليه وسلم - ثلاث مهلكات شح مطاع وهوى متبع وإعجاب المرء بنفسه)','2019','3189','0');
INSERT INTO [t38171] VALUES (3267,'٣٢٤٦ - (وقى طلحة) بن عبيد الله التميمي القرشي أحد العشرة رضي الله عنهم (رسول الله - صلى الله عليه وسلم - يوم أحد بنفسه فأكب عليه حتى أصيبت كفه).','2020','3189','0');
INSERT INTO [t38171] VALUES (3268,'٣٢٤٧ - (قال - صلى الله عليه وسلم - لو لم تذنبوا) (لخشيت) وفي رواية لخفت (عليكم ما هو أكبر من ذلك العجب العجب)','2020','3189','0');
INSERT INTO [t38171] VALUES (3269,'٣٢٤٨ - (في الخبر إن صلاة المدل لا ترفع فوق رأسه ولأن تضحك وأنت معترف بذنبك خير من أن تبكي وأنت مدل بعملك).','2020','3189','0');
INSERT INTO [t38171] VALUES (3270,'٢٣٤٩ - حديث يوم حنين قال العراقي:','2021','3189','0');
INSERT INTO [t38171] VALUES (3271,'٣٢٥٠ - (قال النبي - صلى الله عليه وسلم - لأصحابه وهم خير الناس) بنص الخبر خير القرون قرني ثم الذين يلونهم (ما منكم من أحد ينجيه عمله قالوا ولا أنت يا رسول الله - صلى الله عليه وسلم - قال ولا أنا إلا أن يتغمدني الله برحمته).','2022','3189','0');
INSERT INTO [t38171] VALUES (3272,'٣٢٥١ - (وقال - صلى الله عليه وسلم - إن الله قد أذهب عنكم عبية الجاهلية)','2022','3189','0');
INSERT INTO [t38171] VALUES (3273,'٣٢٥٢ - (قال - صلى الله عليه وسلم - يا معشر قريش لا تأتي الناس بالأعمال يوم القيامة وتأتوني بالدنيا تحملونها على رقابكم تقولون يا محمد يا محمد فأقول هكذا أي فأعرض عنكم).','2023','3189','0');
INSERT INTO [t38171] VALUES (3274,'٣٢٥٣ - (قال يا فاطمة بنت محمد يا صفية بنت عبد المطلب عمة رسول الله اعملا لأنفسكما فإني لا أغني عنكما من الله شيئا).','2024','3189','0');
INSERT INTO [t38171] VALUES (3275,'٣٢٥٤ - (قال رسول الله - صلى الله عليه وسلم - بعد قوله لفاطمة وصفية) رضي الله عنهما (إني لا أغني عنكما من الله شيئا إلا أن لكما رحما سأبلها ببلالها).','2025','3189','0');
INSERT INTO [t38171] VALUES (3276,'٣٢٥٥ - (قال - صلى الله عليه وسلم - أترجو سليم) مصغر قبيلة من العرب (شفاعتي ولا يرجوها بنو عبد المطلب).','2025','3189','0');
INSERT INTO [t38171] VALUES (3277,'٣٢٥٦ - (رأى رسول الله - صلى الله عليه وسلم - رجلا غنيا جلس بجنبه فقير فانقبض منه وجمع ثيابه فقال - صلى الله عليه وسلم - خشيت أن يعدو إليك فقره).','2025','3189','0');
INSERT INTO [t38171] VALUES (3278,'٣٢٥٧ - (قال أبو ذر) رضي الله عنه (كنت مع رسول الله - صلى الله عليه وسلم - فدخل المسجد فقال يا أبا ذر ارفع رأسك) قال (فرفعت رأسي فإذا رجل عليه ثياب خلقان)','2025','3189','0');
INSERT INTO [t38171] VALUES (3279,'كتاب ذم الغرور','2027','2367','0');
INSERT INTO [t38171] VALUES (3280,'٣٢٥٨ - (قال - صلى الله عليه وسلم - حبذا نوم الأكياس وفطرهم كيف يعيبون سهر الحمقى واجتهادهم ولمثقال ذرة من صاحب تقوى ويقين أفضل من ملء الأرض من المغترين).','2028','3279','0');
INSERT INTO [t38171] VALUES (3281,'٣٢٥٩ - (وقال - صلى الله عليه وسلم - الكيس من دان نفسه وعمل بعد الموت والأحمق من أتبع نفسه هواها وتمنى على الله).','2028','3279','0');
INSERT INTO [t38171] VALUES (3282,'٣٢٦٠ - (قد أخبر - صلى الله عليه وسلم - بذلك طوائف الكفار) من عبدة الأوثان والكواكب (فقلدوه وصدقوه وآمنوا ولم يطالبوه بالبرهان).','2029','3279','0');
INSERT INTO [t38171] VALUES (3283,'٣٢٦١ - (منهم من قال نشدتك الله) أي حلفتك به (أبعثك الله رسولا فكان يقول نعم فيصدق).','2029','3279','0');
INSERT INTO [t38171] VALUES (3284,'٣٢٦٢ - (قال النبي - صلى الله عليه وسلم - الإحسان أن تعبد الله كأنك تراه)','2030','3279','0');
INSERT INTO [t38171] VALUES (3285,'٣٢٦٣ - (روي عن) أبي عبد الله (خباب بن الأرت)','2030','3279','0');
INSERT INTO [t38171] VALUES (3286,'٣٢٦٤ - (إن الله يحمي عبده من الدنيا وهو يحبه كما يحمي أحدكم مريضه الطعام والشراب وهو يحبه هكذا ورد في الأخبار).','2032','3279','0');
INSERT INTO [t38171] VALUES (3287,'٣٢٦٥ - (استأذن أن يزور قبر أمه) آمنة بنت وهب وذلك بالأبواء (ويستغفر لها فأذن له في الزيارة ولم يؤذن له في الاستغفار فجلس يبكي على قبر أمه لرقته لها بسبب القرابة حتى أبكى من حوله).','2033','3279','0');
INSERT INTO [t38171] VALUES (3288,'٣٢٦٦ - (قد قال - صلى الله عليه وسلم - فيما رواه معقل بن يسار)','2033','3279','0');
INSERT INTO [t38171] VALUES (3289,'٣٢٦٧ - (قول أبي الدرداء) رضي الله عنه (ويل للذي لا يعلم مرة ولو شاء الله لعلمه وويل للذي يعلم ولا يعمل سبع مرات)','2034','3279','0');
INSERT INTO [t38171] VALUES (3290,'٣٢٦٨ - (وفاتحة الزبور رأس الحكمة خشية الله)','2034','3279','0');
INSERT INTO [t38171] VALUES (3291,'٣٢٦٩ - (قوله - صلى الله عليه وسلم - إن الله لا ينظر إلى صوركم ولا إلى أموالكم وإنما ينظر إلى قلوبكم وأعمالكم)','2035','3279','0');
INSERT INTO [t38171] VALUES (3292,'٣٢٧٠ - (النبي - صلى الله عليه وسلم - شهد لهم بأنهم خير الخلق)','2035','3279','0');
INSERT INTO [t38171] VALUES (3293,'٣٢٧١ - (خرج رسول الله - صلى الله عليه وسلم - يوما على أصحابه وهم يتجادلون ويختصمون فغضب عليهم حتى كأنه فقئ في وجهه حب الرمان حمرة من الغضب فقال أبهذا بعثتم أبهذا أمرتم أن تضربوا كتاب الله بعضه ببعض انظروا إلى ما أمرتم به فاعملوا وما نهيتم','2035','3279','0');
INSERT INTO [t38171] VALUES (3294,'٣٢٧٢ - (قول رسول الله - صلى الله عليه وسلم - نضر الله امرءا سمع مقالتي فوعاها فاداها كما سمعها).','2036','3279','0');
INSERT INTO [t38171] VALUES (3295,'٣٢٧٣ - (قوله - صلى الله عليه وسلم - فيما يرويه عن ربه عز وجل ما تقرب المتقربون إلي بمثل أداء ما افترضت عليهم).','2038','3279','0');
INSERT INTO [t38171] VALUES (3296,'٣٢٧٤ - (وقال أبو الدرداء) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - إذا زخرفتم مساجدكم)','2039','3279','0');
INSERT INTO [t38171] VALUES (3297,'٣٢٧٥ - (قال الحسن) البصري رحمه الله تعالى (إن رسول الله - صلى الله عليه وسلم - لما أراد أن يبني مسجد المدينة أتاه جبريل عليه السلام فقال له ابنه سبعة أذرع طولا في السماء لا تزخرفه ولا تنقشه).','2040','3279','0');
INSERT INTO [t38171] VALUES (3298,'٣٢٧٦ - (قال رسول الله - صلى الله عليه وسلم - تبارك الله الذي قسم العقل بين عباده أشتاتا أن الرجلين ليستوي عملهما وبرهما وصومهما وصلاتهما ولكنهما يتفاوتان في العقل كالذرة) وهي تتراءى في ضوء الشمس من الكوة (في جنب أحد) الجبل المشهور (وما قسم الله لخلقه حظا هو أفضل من العقل واليقين).','2040','3279','0');
INSERT INTO [t38171] VALUES (3299,'٣٢٧٧ - (عن أبي الدرداء) رضي الله عنه (أنه قيل يا رسول الله أرأيت الرجل يصوم النهار ويقوم الليل ويحج ويعتمر','2040','3279','0');
INSERT INTO [t38171] VALUES (3300,'٣٢٧٨ - (قال أنس) رضي الله عنه (أثنى على رجل عند رسول الله - صلى الله عليه وسلم - فقالوا خيرا فقال - صلى الله عليه وسلم - كيف عقله قالوا يا رسول الله نقول من عبادته وفضله وخلقه فقال كيف عقله فإن الأحمق يصيب بحمقه أعظم من فجور الفاجر وإنما يقرب الناس يوم القيامة على قدر عقولهم) رواه داود بن المحبر في كتاب العقل وهو ضعيف وقد تقدم في كتاب العلم.','2041','3279','0');
INSERT INTO [t38171] VALUES (3301,'٣٢٧٩ - (قال أبو الدرداء) رضي الله عنه (كان رسول الله - صلى الله عليه وسلم - إذا بلغه عن رجل شدة عبادة سأل عن عقله فإذا قالوا حسن قال أرجوه وإن قالوا غير ذلك قال لن يبلغ قال وذكر له شدة عبادة رجل فقال كيف عقله قالوا ليس بشيء قال لن يبلغ صاحبكم حيث تظنون).','2041','3279','0');
INSERT INTO [t38171] VALUES (3302,'٣٢٨٠ - (قال رسول الله - صلى الله عليه وسلم - حب الدنيا رأس كل خطيئة)','2042','3279','0');
INSERT INTO [t38171] VALUES (3303,'ربع المنجيات','2043','0','0');
INSERT INTO [t38171] VALUES (3304,'كتاب التوبة','2043','3303','0');
INSERT INTO [t38171] VALUES (3305,'٣٢٨١ - (قال عليه السلام الندم توبة).','2044','3304','0');
INSERT INTO [t38171] VALUES (3306,'٣٢٨٢ - (قال رسول الله - صلى الله عليه وسلم - يا أيها الناس توبوا إلى ربكم فإني أتوب إلى الله في اليوم مائة مرة).','2044','3304','0');
INSERT INTO [t38171] VALUES (3307,'٣٢٨٣ - (قال رسول الله - صلى الله عليه وسلم - التائب حبيب الله)','2045','3304','0');
INSERT INTO [t38171] VALUES (3308,'٣٢٨٤ - قال - صلى الله عليه وسلم - (التائب من الذنب كمن لا ذنب له).','2046','3304','0');
INSERT INTO [t38171] VALUES (3309,'٣٢٨٥ - (قال - صلى الله عليه وسلم -) لله اللام لام الابتداء واسم الجلالة مبتدأ وخبره (أشد) أي أكثر (فرحا) (بتوبة عبده المؤمن) (من رجل نزل في أرض ذويه) أي مفازة (مهلكة) وهو مفعلة من الهلاك (معه راحلته) أي ناقته التي يرتحلها (عليها طعامه وشرابه فوضع رأسه) على الأرض فنام نومة فاستيقظ) من نومه (وقد ذهبت راحلته فطلبها حتى) طلع عليه النهار و (اشتد عليه الحر والعطش أو ما شاء الله تعالى قال) في نفسه (ارجع إلى مكاني الذي كنت فيه فأنام حتى أموت فوضع رأسه على ساعده ليموت فاستيقظ فإذا راحلته عليها زاده وطعامه وشرابه فالله أشد فرحا بتوبة العبد المؤمن من هذا براحلته).','2047','3304','0');
INSERT INTO [t38171] VALUES (3310,'٣٢٨٦ - (قوله - صلى الله عليه وسلم - لا يزني الزاني حتى يزني وهو مؤمن).','2048','3304','0');
INSERT INTO [t38171] VALUES (3311,'٣٢٨٧ - (قال - صلى الله عليه وسلم - إنه ليغان على قلبي في اليوم والليلة سبعين مرة فاستغفر الله منه الحديث)','2049','3304','0');
INSERT INTO [t38171] VALUES (3312,'٣٢٨٨ - (قوله - صلى الله عليه وسلم - أتبع السيئة الحسنة تمحها).','2050','3304','0');
INSERT INTO [t38171] VALUES (3313,'٣٢٨٩ - (قال - صلى الله عليه وسلم - لو عملتم الخطايا حتى تبلغ السماء) أي لكثرتها وتراكم بعضها على بعض (ثم ندمتم لتاب الله عليكم).','2051','3304','0');
INSERT INTO [t38171] VALUES (3314,'٣٢٩٠ - (قال) - صلى الله عليه وسلم - (أيضا إن العبد) أي الإنسان (ليذنب)','2051','3304','0');
INSERT INTO [t38171] VALUES (3315,'٣٢٩١ - (قال - صلى الله عليه وسلم - كفارة الذنب الندامة)','2052','3304','0');
INSERT INTO [t38171] VALUES (3316,'٣٢٩٢ - (يروى أن حبشيا قال يا رسول الله إني كنت أعمل','2052','3304','0');
INSERT INTO [t38171] VALUES (3317,'٣٢٩٣ - (يروى) في بعض الأخبار (إن الله لما لعن إبليس سأله النظرة)','2053','3304','0');
INSERT INTO [t38171] VALUES (3318,'٣٢٩٤ - (قال - صلى الله عليه وسلم - إن الحسنات يذهبن السيئات كما يذهب الماء الوسخ).','2053','3304','0');
INSERT INTO [t38171] VALUES (3319,'٣٢٩٥ - (جاء في الخبر الدواوين ثلاثة)','2054','3304','0');
INSERT INTO [t38171] VALUES (3320,'٣٢٩٦ - (قال - صلى الله عليه وسلم - الصلوات الخمس والجمعة إلى الجمعة يكفرن ما بينهم إن اجتنبت الكبائر وفي لفظ أخر كفارات لما بينهن إلا الكبائر).','2055','3304','0');
INSERT INTO [t38171] VALUES (3321,'٣٢٩٧ - (قال - صلى الله عليه وسلم - فيما رواه عبد الله بن عمرو) بن العاص رضي الله عنهما (الكبائر الإشراك بالله) وذلك بأن يتخذ مع الله إلها غيره (وعقوق الوالدين) الأصليين المسلمين وان عليا (وقتل النفس) التي حرمها الله إلا بالحق كالقصاص والقتل بالردة والرجم (واليمين الغموس)','2055','3304','0');
INSERT INTO [t38171] VALUES (3322,'٣٢٩٨ - (من الكبائر استطالة الرجل في عرض أخيه المسلم).','2056','3304','0');
INSERT INTO [t38171] VALUES (3323,'٣٢٩٩ - (قال أبو سعيد الخدري وغيره من الصحابة) رضوان الله عليهم (إنكم لتعملون أعمالا هي أدق في أعينكم من الشعر كنا نعدها على عهد رسول الله من الكبائر)','2056','3304','0');
INSERT INTO [t38171] VALUES (3324,'٣٣٠٠ - (روى أبو هريرة رضي الله عنه أنه قال قال رسول الله - صلى الله عليه وسلم - الصلاة إلى الصلاة كفارة ورمضان إلى رمضان كفارة إلا من ثلاث الشرك بالله وترك السنة ونكث الصفقة قيل ما ترك السنة قيل الخروج عن الجماعة ونكث الصفقة أن يبايع رجلا ثم يخرج عليه بالسيف يقاتله).','2057','3304','0');
INSERT INTO [t38171] VALUES (3325,'٣٣٠١ - (قال - صلى الله عليه وسلم - الناس نيام فإذا ماتوا انتبهوا).','2057','3304','0');
INSERT INTO [t38171] VALUES (3326,'٣٣٠٢ - (كلفوا أن يكلموا الناس على قدر عقولهم)','2057','3304','0');
INSERT INTO [t38171] VALUES (3327,'٣٣٠٣ - (قال - صلى الله عليه وسلم - قلب المؤمن بين أصبعين من أصابع الرحمن)','2058','3304','0');
INSERT INTO [t38171] VALUES (3328,'٣٣٠٤ - (لقوله - صلى الله عليه وسلم - يؤتي بالموت يوم القيامة في صورة كبش أملح)','2058','3304','0');
INSERT INTO [t38171] VALUES (3329,'٣٣٠٥ - (المعذبون ينقسمون إلى من يعذب قليلا وإلى من يعذب ألف سنة إلى سبعة آلاف سنة وذلك آخر من يخرج من النار كما ورد في الخبر).','2059','3304','0');
INSERT INTO [t38171] VALUES (3330,'٣٣٥٦ - (روى الحسن البصري رحمه الله تعالى الخبر الوارد فيمن يخرج من النار بعد ألف عام فإنه)','2060','3304','0');
INSERT INTO [t38171] VALUES (3331,'٣٣٠٧ - (في الخبر آخر من يخرج من النار يعطى مثل الدنيا كلها عشرة أضعاف).','2060','3304','0');
INSERT INTO [t38171] VALUES (3332,'٣٣٠٨ - (يقول الجنة في السماوات كما ورد في الأخبار).','2061','3304','0');
INSERT INTO [t38171] VALUES (3333,'٣٣٠٩ - (قال - صلى الله عليه وسلم - ارحموا ثلاثة عالما بين الجهال وغني قوم افتقر وعزيز قوم ذل).','2062','3304','0');
INSERT INTO [t38171] VALUES (3334,'٣٣١٠ - (قوله - صلى الله عليه وسلم - البلاء موكل بالأنبياء ثم الأولياء ثم الأمثل فالأمثل).','2063','3304','0');
INSERT INTO [t38171] VALUES (3335,'٣٣١١ - (قال رحم الله أخي موسى لقد أوذي بأكثر من هذا فصبر).','2064','3304','0');
INSERT INTO [t38171] VALUES (3336,'٣٣١٢ - (قالت عائشة رضي الله عنها لما مات بعض الصبيان)','2064','3304','0');
INSERT INTO [t38171] VALUES (3337,'٣٣١٣ - (قيل لا صغيرة مع الإصرار ولا كبيرة مع الاستغفار)','2067','3304','0');
INSERT INTO [t38171] VALUES (3338,'٣٣١٤ - (قال رسول الله - صلى الله عليه وسلم - خير الأعمال أدومها وإن قل).','2068','3304','0');
INSERT INTO [t38171] VALUES (3339,'٣٣١٥ - (في الخبر) في كون استصغار الذنب كبيرة (المؤمن يرى ذنبه كالجبل فوقه يخاف أن يقع عليه والمنافق يرى ذنبه كذباب مر على أنفه فأطاره)','2068','3304','0');
INSERT INTO [t38171] VALUES (3340,'٣٣١٦ - (في الخبر كل الناس معافى إلا المجاهرين)','2069','3304','0');
INSERT INTO [t38171] VALUES (3341,'٣٣١٧ - (في الخبر جالسوا التوابين فإنهم أرق أفئدة)','2069','3304','0');
INSERT INTO [t38171] VALUES (3342,'٣٣١٨ - (قال - صلى الله عليه وسلم - من الذنوب ذنوب يكفرها إلا الهموم وفي لفظ آخر إلا الهم بطلب المعيشة).','2070','3304','0');
INSERT INTO [t38171] VALUES (3343,'٣٣١٩ - (في حديث عائشة رضي الله عنها إذا كثرت ذنوب العبد ولم تكن له أعمال تكفرها أدخل الله عليه الهموم فتكون كفارة لذنوبه)','2070','3304','0');
INSERT INTO [t38171] VALUES (3344,'٣٣٢٠ - (قال رسول الله - صلى الله عليه وسلم - لقد تاب توبة لو قسمت بين) وفي نسخة على (أمة لوسعتهم).','2071','3304','0');
INSERT INTO [t38171] VALUES (3345,'٣٣٢١ - (قال مهلا يا خالد فوالذي نفسي بيده لقد تابت توبة لو تابها صاحب مكس لغفر له ثم أمر بها فصلى عليها ودفنت).','2073','3304','0');
INSERT INTO [t38171] VALUES (3346,'١٣٢٢ - (قال - صلى الله عليه وسلم - أما إني لا أنسى ولكن أنسى لأشرع).','2075','3304','0');
INSERT INTO [t38171] VALUES (3347,'٣٣٢٣ - (سبق المفردون المستهترون بذكر الله تعالى وضع الذكر عنهم أثقالهم فوردوا القيامة خفافا).','2075','3304','0');
INSERT INTO [t38171] VALUES (3348,'٣٣٢٤ - (قوله - صلى الله عليه وسلم - فيما رواه عنه علي كرم الله وجهه خياركم كل مفتن تواب)','2075','3304','0');
INSERT INTO [t38171] VALUES (3349,'٣٣٢٥ - (وفي خبر آخر المؤمن كالسنبلة يفيء أحيانا ويميل أحيانا).','2076','3304','0');
INSERT INTO [t38171] VALUES (3350,'٣٣٢٦ - (وفي الخبر لا بد للمؤمن من ذنب يأتيه الفينة بعد الفينة أي الحين بعد الحين).','2076','3304','0');
INSERT INTO [t38171] VALUES (3351,'٣٣٢٧ - (قال النبي - صلى الله عليه وسلم - كل بني آدم خطاؤن وخير الخطائين التوابون المستغفرون).','2077','3304','0');
INSERT INTO [t38171] VALUES (3352,'٣٣٢٨ - (قال) - صلى الله عليه وسلم - (أيضا المؤمن واه راقع فخيرهم من مات على رقعه).','2077','3304','0');
INSERT INTO [t38171] VALUES (3353,'٣٣٢٩ - (قال - صلى الله عليه وسلم - إن العبد ليعمل بعمل أهل الجنة سبعين سنة حتى يقول الناس إنه من أهلها ولا يبقى بينه وبين الجنة إلا شبر)','2078','3304','0');
INSERT INTO [t38171] VALUES (3354,'٣٣٣٠ - (وفي بعض الآثار)','2079','3304','0');
INSERT INTO [t38171] VALUES (3355,'٣٣٣١ - (في بعض الأخبار يصلي أربع ركعات).','2079','3304','0');
INSERT INTO [t38171] VALUES (3356,'٣٣٣٢ - (وفي الخبر إذا عملت سيئة فأتبعها حسنة تكفرها السر بالسر والعلانية بالعلانية).','2080','3304','0');
INSERT INTO [t38171] VALUES (3357,'٣٣٣٣ - (في الخبر الصحيح أن رجلا قال لرسول الله - صلى الله عليه وسلم - إني عالجت امرأة فأصبت منها كل شيء إلا المسيس)','2080','3304','0');
INSERT INTO [t38171] VALUES (3358,'٣٣٣٤ - (في الخبر المستغفر من الذنب وهو مصر عليه كالمستهزئ بآيات الله).','2082','3304','0');
INSERT INTO [t38171] VALUES (3359,'٣٣٣٥ - (كان بعض الصحابة) ولفظ القوت وقد كان بعض السلف (يقول كان لنا أمانان ذهب أحدهما) ولفظ القوت فذهب أحدهما وبقي الآخر (وهو كون الرسول فينا و) الذي (بقي الاستغفار فإن ذهب هلكنا).','2082','3304','0');
INSERT INTO [t38171] VALUES (3360,'٣٣٣٦ - (قال فيه رسول الله - صلى الله عليه وسلم - تعجب ربك من شاب ليست له صبوة).','2083','3304','0');
INSERT INTO [t38171] VALUES (3361,'٣٣٣٧ - (قوله - صلى الله عليه وسلم - ما من يوم طلع فجره ولا ليلة غاب شفقها إلا وملكان يتجاوبان بأربعة أصوات يقول أحدهما يا ليت هذا الخلق) وفي نسخة الخلائق (لم يخلقوا ويقول الآخر يا ليتهم إذ خلقوا علموا لماذا خلقوا فيقول الآخر يا ليتهم إذا لم يعلموا لماذا خلقوا وعملوا بما علموا وفي بعض الروايات ليتهم تجالسوا فتذاكروا ما عملوا ويقول الآخر يا ليتهم إذا لم يعملوا بما علموا تابوا مما عملوا)','2084','3304','0');
INSERT INTO [t38171] VALUES (3362,'٣٣٣٨ - (في حديث عمر بن الخطاب رضي الله عنه)','2085','3304','0');
INSERT INTO [t38171] VALUES (3363,'٣٣٣٩ - (في حديث مجاهد القلب مثل الكف المفتوحة كلما أذنب العبد ذنبا انقبضت أصبع حتى تنقبض الأصابع كلها فيسد على القلب فذلك هو الطبع)','2086','3304','0');
INSERT INTO [t38171] VALUES (3364,'٣٣٤٠ - (ما خلف دينارا ولا درهما).','2086','3304','0');
INSERT INTO [t38171] VALUES (3365,'٣٣٤١ - (في الخبر ما أنكرتم من زمانكم فبما غيرتم من أعمالكم).','2087','3304','0');
INSERT INTO [t38171] VALUES (3366,'٣٣٤٢ - قال جاء (في الخبر يقول الله تعالى إن أدنى ما أصنع بالعبد إذا آثر شهوته على طاعتي أن أحرمه لذيذ مناجاتي)','2087','3304','0');
INSERT INTO [t38171] VALUES (3367,'٣٣٤٣ - (قال له آخر أوصني يا رسول الله فقال عليك باليأس مما في أيدي الناس فإن ذلك هو الغنى وإياك والطمع فإنه الفقر الحاضر وصل صلاة مودع وإياك وما يعتذر منه)','2087','3304','0');
INSERT INTO [t38171] VALUES (3368,'٣٣٤٤ - (كتب معاوية رحمه الله تعالى إلى) أم المؤمنين (عائشة رضي الله عنها أن اكتبي لي كتابا توصينى فيه ولا تكثري)','2088','3304','0');
INSERT INTO [t38171] VALUES (3369,'٣٣٤٥ - (كتبت) رضي الله عنها (إليه أما بعد فاتق الله فإنك إذا اتقيت الله كفاك الله الناس وإذا اتقيت الناس لم يغنوا عنك من الله شيئا والسلام)','2089','3304','0');
INSERT INTO [t38171] VALUES (3370,'٣٣٤٦ - (روى أن رجلا قال لأبي سعيد) رضي الله عنه (أوصني قال عليك بتقوي الله عز وجل فإنها رأس كل خير وعليك بالجهاد فإنه رهبانية الإسلام وعليك بالقرآن فإنه نور لك في أهل الأرض وذكر لك في أهل السماء وعليك بالصمت إلا من خير فإنك بذلك تغلب الشيطان)','2089','3304','0');
INSERT INTO [t38171] VALUES (3371,'٣٣٤٧ - (قول رسول الله - صلى الله عليه وسلم - حفت الجنة بالمكاره وحفت النار بالشهوات)','2090','3304','0');
INSERT INTO [t38171] VALUES (3372,'٣٣٤٨ - (قال له - صلى الله عليه وسلم - إن الله) عز وجل (خلق النار فقال لجبريل عليه السلام اذهب فانظر إليها) فذهب (فنظر إليها فقال وعزتك لا يسمع بها أحد فيدخلها خفها بالشهوات) أي جعلها كالسور المحيط بها (ثم قال) له (اذهب فانظر إليها) فذهب فنظر إليها (فقال لقد خشيت أن لا يبقى أحد إلا دخلها وخلق الجنة فقال لجبريل) عليه السلام (اذهب فنظر إليها) فذهب (فنظر إليها فقال وعزتم لا يسمع بها أحد إلا دخلها فحفها بالمكاره) أي بالشدائد والمكروهات (ثم قال اذهب فانظر إليها) فذهب (فنظر) إليها (فقال وعزتك لقد خشيت أن لا يدخلها أحد).','2090','3304','0');
INSERT INTO [t38171] VALUES (3373,'كتاب الصبر والشكر','2091','3303','0');
INSERT INTO [t38171] VALUES (3374,'٣٣٤٩ - (الإيمان نصفان نصف صبر ونصف شكر كما وردت به الآثار وشهدت له الأخبار).','2092','3373','0');
INSERT INTO [t38171] VALUES (3375,'٣٣٥٠ - (قال - صلى الله عليه وسلم - نصف الإيمان)','2092','3373','0');
INSERT INTO [t38171] VALUES (3376,'٣٣٥١ - (وقال - صلى الله عليه وسلم - من أقل ما أوتيتم)','2092','3373','0');
INSERT INTO [t38171] VALUES (3377,'٣٣٥٢ - (روى جابر أنه سئل - صلى الله عليه وسلم - عن الإيمان فقال الصبر والسماحة).','2093','3373','0');
INSERT INTO [t38171] VALUES (3378,'٣٣٥٣ - (قال) - صلى الله عليه وسلم - (الصبر كنز من كنوز الجنة).','2093','3373','0');
INSERT INTO [t38171] VALUES (3379,'٣٣٥٤ - (سئل) - صلى الله عليه وسلم - (مرة ما الإيمان فقال الصبر)','2093','3373','0');
INSERT INTO [t38171] VALUES (3380,'٣٣٥٥ - (في حديث عطاء) بن أبي رباح التابعي المكي الثقة (عن ابن عباس)','2094','3373','0');
INSERT INTO [t38171] VALUES (3381,'٣٣٥٦ - (قال - صلى الله عليه وسلم - في الصبر على ما تكره خير كثير)','2094','3373','0');
INSERT INTO [t38171] VALUES (3382,'٣٣٥٦/ أ - (وقال رسول الله - صلى الله عليه وسلم - لو كان الصبر رجلا لكان كريما والله يحب الصابرين).','2094','3381','0');
INSERT INTO [t38171] VALUES (3383,'٣٣٥٧ - (قال - صلى الله عليه وسلم - من مات فقد قامت قيامته).','2095','3373','0');
INSERT INTO [t38171] VALUES (3384,'٣٣٥٨ - (قول سيد الأنبياء) - صلى الله عليه وسلم - (كفى بالموت واعظا).','2095','3373','0');
INSERT INTO [t38171] VALUES (3385,'٣٣٥٩ - (قوله - صلى الله عليه وسلم - أنا وكافل اليتيم كهاتين وأشار إلى أصبعيه الكريمتيين - صلى الله عليه وسلم -)','2096','3373','0');
INSERT INTO [t38171] VALUES (3386,'٣٣٦٠ - (وقال - صلى الله عليه وسلم - الولد مبخلة مجبنة محزنة)','2096','3373','0');
INSERT INTO [t38171] VALUES (3387,'٣٣٦١ - (لما نظر - صلى الله عليه وسلم - إلى ولده الحسن) رضي الله عنه (يتعثر في قميصه نزل عن المنبر واحتضنه وقال صدق الله إنما أموالكم وأولادكم فتنة إني لما رأيت ابني) هذا (يتعثر) في قميصه (لم أملك نفسي أن أخذته).','2096','3373','0');
INSERT INTO [t38171] VALUES (3388,'٣٣٦٢ - (وقال - صلى الله عليه وسلم - المهاجر من هجر السوء والمجاهد من جاهد هواه).','2097','3373','0');
INSERT INTO [t38171] VALUES (3389,'٣٣٦٣ - (قال ابن عباس) رضي الله عنهما (الصبر في القرآن على ثلاثة أوجه)','2097','3373','0');
INSERT INTO [t38171] VALUES (3390,'٣٣٦٤ - (وقال - صلى الله عليه وسلم - قال الله عز وجل إذا وجهت إلى عبد من عبيدي مصيبة في بدنه أو ماله أو ولده ثم استقبل ذلك بصبر جميل استحييت منه يوم القيامة أن أنصب له ميزانا أو أنشر له ديوانا).','2098','3373','0');
INSERT INTO [t38171] VALUES (3391,'٣٣٦٥ - (وقال - صلى الله عليه وسلم - انتظار الفرج بالصبر عبادة)','2098','3373','0');
INSERT INTO [t38171] VALUES (3392,'٣٣٦٦ - (وقال - صلى الله عليه وسلم - ما من عبد مؤمن أصيب بمصيبة فقال كما أمره الله تعالى إنا لله وإنا إليه راجعون اللهم أجرني) بالمد (في مصيبتي وأعقبني خيرا منها إلا فعل الله به ذلك).','2099','3373','0');
INSERT INTO [t38171] VALUES (3393,'٣٣٦٧ - (قال أنس) رضي الله عنه (حدثني رسول الله - صلى الله عليه وسلم - أن الله عز وجل قال يا جبريل ما جزاء من سلبت كريمتيه)','2100','3373','0');
INSERT INTO [t38171] VALUES (3394,'٣٣٦٨ - (قال - صلى الله عليه وسلم - يقول الله عز وجل إذا ابتليت عبدي ببلاء فصبر ولم يشكني إلى عواده أبدلته لحما خيرا من لحمه ودما خيرا من دمه فإذا أبرأته أبرأته ولا ذنب له وإن توفيته فإلى رحمتي).','2101','3373','0');
INSERT INTO [t38171] VALUES (3395,'٣٣٦٩ - (قال نبينا - صلى الله عليه وسلم - من إجلال الله ومعرفة حقه أن لا تشكو وجعك ولا تذكر مصيبتك).','2102','3373','0');
INSERT INTO [t38171] VALUES (3396,'٣٣٧٠ - (روي عن الرميصاء أم سليم رضي الله عنها)','2102','3373','0');
INSERT INTO [t38171] VALUES (3397,'٣٣٧١ - (روى جابر) بن عبد الله الأنصاري رضي الله عنه (أنه - صلى الله عليه وسلم - قال رأيتني دخلت الجنة فإذا بالرميصاء امرأة أبي طلحة).','2103','3373','0');
INSERT INTO [t38171] VALUES (3398,'٣٣٧٢ - (وقيد قيل من كنوز البر كتمان المصائب والأوجاع والصدقة)','2103','3373','0');
INSERT INTO [t38171] VALUES (3399,'٣٣٧٣ - (قال - صلى الله عليه وسلم - إن الله يبغض الشاب الفارغ).','2104','3373','0');
INSERT INTO [t38171] VALUES (3400,'٣٣٧٤ - (قال رسول الله - صلى الله عليه وسلم - النظرة سهم مسموم من سهام إبليس)','2104','3373','0');
INSERT INTO [t38171] VALUES (3401,'٣٣٧٥ - (إن المنبت) وهو من انقطع به في السفر وعطبت راحلته (لا أرضا قطع ولا ظهرا بقي)','2105','3373','0');
INSERT INTO [t38171] VALUES (3402,'٣٣٧٦ - (قال - صلى الله عليه وسلم - الطاعم الشاكر بمنزلة الصائم الصابر).','2105','3373','0');
INSERT INTO [t38171] VALUES (3403,'٣٣٧٧ - (روي عن عطاء بن أبي رباح)','2106','3373','0');
INSERT INTO [t38171] VALUES (3404,'٣٣٧٨ - (روى عنه - صلى الله عليه وسلم - أنه قال ينادي يوم القيامة ليقم الحمادون) أي كثيروا الحمد الله تعالى على نعمه (فتقوم زمرة فينصب لهم لواء فيدخلون الجنة قيل) يا رسول الله (ومن الحمادون قال الذين يشكرون الله تعالى على كل حال وفي لفظ آخر الذين يشكرون الله على السراء والضراء).','2107','3373','0');
INSERT INTO [t38171] VALUES (3405,'٣٣٧٩ - (قال - صلى الله عليه وسلم - الحمد رداء الرحمن)','2107','3373','0');
INSERT INTO [t38171] VALUES (3406,'٣٣٨٠ - (قال - صلى الله عليه وسلم - أفضل الذكر لا إله إلا الله وأفضل الدعاء الحمد الله).','2108','3373','0');
INSERT INTO [t38171] VALUES (3407,'٣٣٨١ - (قال) - صلى الله عليه وسلم - (ليس شيء من الأذكار يضاعف ما يضاعف الحمد لله)','2108','3373','0');
INSERT INTO [t38171] VALUES (3408,'٣٣٨٢ - (قال - صلى الله عليه وسلم - الرجل كيف أصبحت فقال بخير فأعاد)','2108','3373','0');
INSERT INTO [t38171] VALUES (3409,'٣٣٨٣ - (قال رسول الله - صلى الله عليه وسلم - اعملوا فكل ميسر لما خلق له لما قيل له يا رسول الله ففيم العمل إذا كانت الأشياء قد فرغ منها من قبل)','2108','3373','0');
INSERT INTO [t38171] VALUES (3410,'٣٣٨٤ - (وقيل إنه من شرب في آنية من ذهب أو فضة فكأنما يجرجر في بطنه نار جهنم)','2109','3373','0');
INSERT INTO [t38171] VALUES (3411,'٣٣٨٥ - (وإذا ذكر القدر فأمسكوا)','2109','3373','0');
INSERT INTO [t38171] VALUES (3412,'٣٣٨٦ - (قيل للنبي - صلى الله عليه وسلم - إن عيسى عليه السلام يقال إنه مشى على الماء فقال لو ازداد يقينا لمشى على الهواء).','2109','3373','0');
INSERT INTO [t38171] VALUES (3413,'٣٣٨٧ - (وقال النبي - صلى الله عليه وسلم - سيكون عليكم أمراء يفسدون وما يصلح الله بهم أكثر فإن أحسنوا فلهم الأجر وعليكم الشكر وإن أساؤا فعليهم الوزر وعليكم الصبر).','2110','3373','0');
INSERT INTO [t38171] VALUES (3414,'٣٣٨٨ - (وقال رجل اللهم إني أسألك تمام النعمة فقال - صلى الله عليه وسلم - وهل تعلم ما تمام النعمة قال لا قال دخول الجنة).','2111','3373','0');
INSERT INTO [t38171] VALUES (3415,'٣٣٨٩ - (وقال) - صلى الله عليه وسلم - (نعم العون على تقوى الله المال).','2111','3373','0');
INSERT INTO [t38171] VALUES (3416,'٣٣٩٠ - (قال - صلى الله عليه وسلم - نعم العون على الدين المرأة الصالحة).','2112','3373','0');
INSERT INTO [t38171] VALUES (3417,'٣٣٩١ - (كان يؤذى ويضرب حتى افتقر إلى الهجرة)','2112','3373','0');
INSERT INTO [t38171] VALUES (3418,'٣٣٩٢ - (قال - صلى الله عليه وسلم - الأئمة من قريش).','2112','3373','0');
INSERT INTO [t38171] VALUES (3419,'٣٣٩٣ - (كان - صلى الله عليه وسلم - من أكرم الناس أرومة في نسب آدم)','2113','3373','0');
INSERT INTO [t38171] VALUES (3420,'٣٣٩٤ - (قال - صلى الله عليه وسلم - تخيروا لنطفكم) وانكحوا (الأكفاء)','2113','3373','0');
INSERT INTO [t38171] VALUES (3421,'٣٣٩٥ - (قال - صلى الله عليه وسلم - أفضل السعادات طول العمر في طاعة الله)','2113','3373','0');
INSERT INTO [t38171] VALUES (3422,'٣٣٩٦ - (قال عمر رضي الله عنه إذا بعثتم رسولا فاطلبوا حسن الوجه حسن الاسم)','2114','3373','0');
INSERT INTO [t38171] VALUES (3423,'٣٣٩٧ - (قال - صلى الله عليه وسلم - اطلبوا الخير عند حسان الوجوه).','2115','3373','0');
INSERT INTO [t38171] VALUES (3424,'٣٣٩٨ - (وقال - صلى الله عليه وسلم - إنكم تتهافتون على النار تهافت الفراش وأنا آخذ بحجزكم).','2116','3373','0');
INSERT INTO [t38171] VALUES (3425,'٣٣٩٩ - (وقوله - صلى الله عليه وسلم - ليكن بلاغ أحدكم من الدنيا كزاد الراكب).','2117','3373','0');
INSERT INTO [t38171] VALUES (3426,'٣٤٠١ - (ولما ذكر رسول الله - صلى الله عليه وسلم - أن الأغنياء يدخلون الجنة بشدة استأذنه عبد الرحمن بن عوف رضي الله عنه)','2119','3373','0');
INSERT INTO [t38171] VALUES (3427,'٣٤٠٢ - (وقال - صلى الله عليه وسلم - ما من أحد يدخل الجنة إلا برحمة الله تعالى أي بهدايته فقيل ولا أنت يا رسول الله قال ولا أنا).','2119','3373','0');
INSERT INTO [t38171] VALUES (3428,'٣٤٠٣ - (ورد فيه من النهي عن تصديق المنجمين)','2120','3373','0');
INSERT INTO [t38171] VALUES (3429,'٣٤٠٤ - (عن علم النجوم)','2120','3373','0');
INSERT INTO [t38171] VALUES (3430,'٣٤٠٥ - (نظر رسول الله - صلى الله عليه وسلم - إلى السماء وقرأ قوله تعالى ربنا ما خلقت هذا باطلا الآية ثم قال ويل لمن قرأ هذه الآية ثم مسح بها','2120','3373','0');
INSERT INTO [t38171] VALUES (3431,'٣٤٠٦ - (والأخبار الواردة في الملائكة الموكلين بالسماوات والأرض وأجزاء النبات والحيوانات حتى كل قطرة من المطر وكل سحاب ينجر من جانت إلى جانب أكثر من أن تحصى فلذلك ترك الاستشهاد به).','2121','3373','0');
INSERT INTO [t38171] VALUES (3432,'٣٤٠٧ - (ورد في الأخبار أن البقعة التي يجتمع فيها الناس إما أن تلعنهم إذا تفرقوا أو تسغفر لهم).','2123','3373','0');
INSERT INTO [t38171] VALUES (3433,'٣٤٠٨ - (ورد أن العالم يستغفر له كل شيء حتى الحوت في البحر) تقدم في كتاب العلم (وأن الملائكة يلعنون العصاة).','2123','3373','0');
INSERT INTO [t38171] VALUES (3434,'٣٤١٠ - (قال - صلى الله عليه وسلم - إن القرآن هو الغنى الذي لا غنى بعده ولا فقر بعده).','2124','3373','0');
INSERT INTO [t38171] VALUES (3435,'٣٤١١ - (قال) - صلى الله عليه وسلم - (من آتاه الله القرآن فظن أن أحدا أغنى منه فقد استهزأ بآيات الله).','2124','3373','0');
INSERT INTO [t38171] VALUES (3436,'٣٤١٢ - (قال - صلى الله عليه وسلم - كفى باليقين غنى).','2125','3373','0');
INSERT INTO [t38171] VALUES (3437,'٣٤١٣ - (وفي الخبر ما عظمت نعمة الله على عبد إلا كثرت حوائج الناس إليه فمن تهاون بهم عرض تلك النعمة للزوال).','2125','3373','0');
INSERT INTO [t38171] VALUES (3438,'٣٤١٤ - (وقال - صلى الله عليه وسلم - إن الله ليحمي عبده المؤمن من الدنيا وهو يحبه كما يحمي أحدكم مريضه)','2125','3373','0');
INSERT INTO [t38171] VALUES (3439,'٣٤١٥ - (قال رسول الله - صلى الله عليه وسلم - إن العبد إذا أذنب ذنبا فأصابته شدة أو بلاء في الدنيا فالله أكرم من أن يعذبه ثانيا).','2126','3373','0');
INSERT INTO [t38171] VALUES (3440,'٣٤١٦ - (روي أن رجلا قال لرسول الله - صلى الله عليه وسلم - أوصني قال لا تتهم الله في شيء قضاه عليك).','2127','3373','0');
INSERT INTO [t38171] VALUES (3441,'٣٤١٧ - (نظر رسول الله - صلى الله عليه وسلم - إلى السماء فضحك فسئل) عن ضحكه (فقال عجبت لقضاء الله تعالى للمؤمن إن قضى له بالسراء رضي وكان خيرا له وإن قضى له بالضراء رضي وكان خيرا له).','2127','3373','0');
INSERT INTO [t38171] VALUES (3442,'٣٤١٨ - (قال رسول الله - صلى الله عليه وسلم - من يرد الله به خيرا يصب منه)','2128','3373','0');
INSERT INTO [t38171] VALUES (3443,'٣٤١٩ - (قال - صلى الله عليه وسلم - قال الله تعالى إذا وجهت إلى عبدي مصيبة في بدنه أو ماله أو ولده ثم استقبل ذلك بصبر جميل استحيي منه يوم القيامة أن أنصب له ميزانا أو أنشر له ديوانا)','2128','3373','0');
INSERT INTO [t38171] VALUES (3444,'٣٤٢٠ - (قال - صلى الله عليه وسلم - ما من عبد أصيب بمصيبة فقال كما أمره الله تعالى إنا لله وإنا إليه راجعون اللهم آجرني في مصيبتي وأعقبني خيرا منها إلا فعل الله لى ذلك)','2128','3373','0');
INSERT INTO [t38171] VALUES (3445,'٣٤٢١ - (قال - صلى الله عليه وسلم - قال الله تعالى من سلبت كريمتيه فجزاؤه الخلود في داري والنظر إلى وجهي)','2128','3373','0');
INSERT INTO [t38171] VALUES (3446,'٣٤٢٢ - (روي أن رجلا قال يا رسول الله ذهب مالي وسقم جسمي فقال - صلى الله عليه وسلم - لا خير في عبد لا يذهب ماله ولا يسقم جسمه إن الله إذا أحب عبدا ابتلاه وإذا ابتلاه صبره).','2129','3373','0');
INSERT INTO [t38171] VALUES (3447,'٣٤٢٣ - (قال - صلى الله عليه وسلم - إن الرجل لتكون له الدرجة عند الله تعالى لا يبلغها بعمل حتى يبتلى ببلاء في جسمه فيبلغها بذلك).','2129','3373','0');
INSERT INTO [t38171] VALUES (3448,'٣٤٢٤ - (وعن خباب بن الأرت)','2131','3373','0');
INSERT INTO [t38171] VALUES (3449,'٣٤٢٥ - (عن أنس) رضي الله عنه (قال قال رسول الله - صلى الله عليه وسلم - إذا أراد الله بعبد خيرا وأراد أن يصافيه صب عليه البلاء صبا وثجه عليه ثجا فإذا دعاه قالت الملائكة صوت معروف فإن دعاه ثانيا فقال يا رب قال الله تعالى لبيك عبدي وسعديك لا تسألني شيئا إلا أعطيتك أو دفعت عنك ما هو خير أو ادخرت لك عندي ما هو أفضل منه فإذا كان يوم القيامة جيء بأهل الأعمال فوفوا أعمالهم بالميزان أهل الصيام والصدقة والحج ثم يؤتى بأهل البلاء فلا','2131','3373','0');
INSERT INTO [t38171] VALUES (3450,'٣٤٢٦ - (روي أنه لما نزل قوله تعالى من يعمل سوءا يجز به قال أبو بكر الصديق رضي الله عنه كيف الفرح بعد هذه الآية فقال رسول الله - صلى الله عليه وسلم - غفر الله لك يا أبا بكر ألست تمرض أليس يصيبك','2132','3373','0');
INSERT INTO [t38171] VALUES (3451,'٣٤٢٧ - (وعن عقبة بن عامر) الجهني رضي الله عنه (عن النبي - صلى الله عليه وسلم - أنه قال إذا رأيتم الرجل يعطيه الله ما يحب وهو مقيم على معصيته فاعلموا أن ذلك استدراج وقرأ قوله تعالى فلما نسوا ما ذكروا به فتحنا عليهم أبواب كل شيء يعني لما تركوا ما أمروا) به (فتحنا عليهم أبواب الخير حتى إذا فرحوا بما أوتوا أي بما أعطوا من الخير أخذناهم بغتة)','2133','3373','0');
INSERT INTO [t38171] VALUES (3452,'٣٤٢٨ - (وعن الحسن) بن يسار (البصري رحمه الله تعالى أن رجلا من الصحابة رضي الله عنهم رأى امرأة كان يعرفها في الجاهلية فكلمها ثم تركها فجعل الرجل يلتفت إليها وهو يمشي فصدمه حائط فأثر في وجهه فأتى النبي - صلى الله عليه وسلم - فأخبره فقال - صلى الله عليه وسلم - إذا أراد الله بعبد خيرا عجل له عقوبة ذنبه في الدنيا).','2133','3373','0');
INSERT INTO [t38171] VALUES (3453,'٣٤٢٩ - (وعن أنس) رضي الله عنه (عن النبي - صلى الله عليه وسلم - قال ما تجرع عبد قط جرعتين أحب إلى الله من جرعة غيظ ردها بحلم و) من (جرعة مصيبة يصبر الرجل لها ولا قطرت قطرة أحب إلى الله من قطرة دم أهريقت في سبيل الله وقطرة دمع في سواد الليل وهو ساجد ولا يراه إلا وما خطا عبد خطوتين أحب إلى الله من خطوة إلى الصلاة الفريضة و) من (خطوة إلى صلة الرحم).','2134','3373','0');
INSERT INTO [t38171] VALUES (3454,'٣٤٣٠ - (وقال لقمان) رحمة الله تعالى (لابنه يا بني إن الذهب يجرب بالنار والعبد الصالح يجرب بالبلاء وإذا أحب الله قوما ابتلاهم فمن رضي فله الرضا ومن سخط فله السخط)','2135','3373','0');
INSERT INTO [t38171] VALUES (3455,'٣٤٣١ - (روي عن رسول الله - صلى الله عليه وسلم - أنه كان يستعيذ في دعائه من بلاء الدنيا والآخرة).','2135','3373','0');
INSERT INTO [t38171] VALUES (3456,'٣٤٣٢ - (كان يقول هو والأنبياء عليهم السلام ربنا آتنا في الدنيا حسنة وفي الآخرة حسنة).','2136','3373','0');
INSERT INTO [t38171] VALUES (3457,'٣٤٣٣ - (وقال علي كرم الله وجهه) في مرضه (اللهم إني أسألك الصبر فقال - صلى الله عليه وسلم - لقد سألت الله البلاء فاسأله العافية).','2136','3373','0');
INSERT INTO [t38171] VALUES (3458,'٣٤٣٤ - (روى) أبو بكر (الصديق رضي الله عنه عن رسول الله - صلى الله عليه وسلم - أنه قال سلوا الله العافية فما أعطي أحد أفضل من','2136','3373','0');
INSERT INTO [t38171] VALUES (3459,'٣٤٣٥ - (قال - صلى الله عليه وسلم - في دعائه وعافيتك أحب إلي)','2137','3373','0');
INSERT INTO [t38171] VALUES (3460,'٣٤٣٦ - (قوله - صلى الله عليه وسلم - الجمعة حج المساكين وجهاد المرأة حسن التبعل).','2137','3373','0');
INSERT INTO [t38171] VALUES (3461,'٣٤٣٧ - (قوله - صلى الله عليه وسلم - شارب الخمر كعابد الوثن).','2138','3373','0');
INSERT INTO [t38171] VALUES (3462,'٣٤٣٨ - (عن النبي - صلى الله عليه وسلم -) أنه قال (آخر الأنبياء دخولا الجنة سليمان بن داود) عليهما السلام (لمكان ملكه وآخر أصحابي دخولا الجنة عبد الرحمن بن عوف لمكان غناه)','2138','3373','0');
INSERT INTO [t38171] VALUES (3463,'٣٤٣٩ - (في خبر آخر) ولفظ القوت وفي لفظ آخر (يدخل سليمان) بن داود الجنة (بعد الأنبياء بأربعين خريفا).','2138','3373','0');
INSERT INTO [t38171] VALUES (3464,'٣٤٤٠ - (وفي الخبر أبواب الجنة كلها مصراعان إلا باب الصبر فإنه مصراع واحد وأول من يدخله أهل البلاء أمامهم أيوب عليه السلام)','2139','3373','0');
INSERT INTO [t38171] VALUES (3465,'٣٤٤١ - (سماها) أي الصدقات (أوساخ الناس وشرف أهل بيته بالصيانة عنها).','2139','3373','0');
INSERT INTO [t38171] VALUES (3466,'كتاب الرجاء والخوف','2140','3303','0');
INSERT INTO [t38171] VALUES (3467,'٣٤٤٢ - (حديث زيد الخيل) بن مهلهل بن زيد بن منهب الطائي رضي الله عنه (إذا قال لرسول الله - صلى الله عليه وسلم - جئت لأسألك عن علامة الله فيمن يريد وعلامته فيمن لا يريد فقال كيف أصبحت قال أصبحت أحب الخير وأهله وإذا قدرت على شيء منه سارعت إليه وأيقنت بثوابه وإذا فاتني منه شيء حزنت عليه وحننت إليه فقال هذه علامة الله فيمن يريد ولو أرادك للأخرى هيأك لها ثم لا يبالي في أي أوديتها هلكت).','2141','3466','0');
INSERT INTO [t38171] VALUES (3468,'٣٤٤٣ - (وقال - صلى الله عليه وسلم - لا يموتن أحدكم إلا وهو يحسن الظن بالله).','2141','3466','0');
INSERT INTO [t38171] VALUES (3469,'٣٤٤٤ - (وقال - صلى الله عليه وسلم - يقول الله عز وجل أنا عند ظن عبدي بي فليظن بي ما شاء).','2142','3466','0');
INSERT INTO [t38171] VALUES (3470,'٣٤٤٥ - (دخل - صلى الله عليه وسلم - على رجل وهو في النزع) أي حالة نزوع الروح منه (فقال كيف يجدك فقال أجدني أخاف ذنوبي وأرجو رحمة ربي فقال - صلى الله عليه وسلم - ما اجتمعا في قلب عبد في هذا الموطن إلا أعطاه الله ما رجا وآمنه مما يخاف).','2142','3466','0');
INSERT INTO [t38171] VALUES (3471,'٣٤٤٦ - (وقال - صلى الله عليه وسلم - إن الله تعالى يقول للعبد يوم القيامة ما منعك إذ رأيت المنكر أن تنكره فإن لقنه الله حجته قال رب رجوتك وخفت الناس قال فيقول الله تعالى قد غفرت لك).','2143','3466','0');
INSERT INTO [t38171] VALUES (3472,'٣٤٤٧ - (في الخبر الصحيح أن رجلا كان يداين الناس) أي يعاملهم بالدين (فيسامح الغني ويتجاوز عن المعسر فلقى الله) تعالى (ولم يعمل خيرا قط فقال الله عز وجل من أحق بذلك منا فعفا عنه لحسن ظنه ورجائه أن يعفو عنه مع إفلاسه عن الطاعات).','2143','3466','0');
INSERT INTO [t38171] VALUES (3473,'٣٤٤٨ - (لما قال) لهم (- صلى الله عليه وسلم -) يعظهم (لو تعلمون ما أعلم لضحكتم قليلا ولبكيتم كثيرا ولخرجتم إلى الصعدات تلدمون) أي تضربون (صدوركم وتجأرون) أي تتضرعون (إلى ربكم فهبط جبريل عليه السلام فقال إن ربك يقول لم تقنط عبادي)','2144','3466','0');
INSERT INTO [t38171] VALUES (3474,'٣٤٤٩ - (وفي الخبر أن الله تعالى أوحى إلى داود عليه السلام) يا داود (أحبني وأحب من يحبني وحببني إلى خلقي فقال يا رب) هذا أحبك وأحب من يحبك و (كيف أحببك إلى خلقك قال اذكرني بالحسن الجميل واذكر آلائي وإحساني وذكرهم ذلك فإنهم لا يعرفون مني إلا الجميل)','2145','3466','0');
INSERT INTO [t38171] VALUES (3475,'٣٤٥٠ - (إن رجلا من بني إسرائيل كان يقنط الناس ويشدد عليهم قال فيقول الله تعالى له يوم القيامة أؤيسك من رحمتي كما كنت تقنط عبادي منها).','2145','3466','0');
INSERT INTO [t38171] VALUES (3476,'٣٤٥١ - (وقال - صلى الله عليه وسلم - إن رجلا يدخل النار فيمكث فيها ألف سنة ينادي يا حنان يا منان فيقول الله تعالى لجبريل)','2145','3466','0');
INSERT INTO [t38171] VALUES (3477,'٣٤٥٢ - (يقال أن النبي - صلى الله عليه وسلم - لم يزل يسأل في أمته حتى قيل له أما ترضى وقد أنزلت عليك هذه الآية) يعني (وإن ربك لذو مغفرة للناس على ظلمهم)','2146','3466','0');
INSERT INTO [t38171] VALUES (3478,'٣٤٥٣ - (روى أبو موسى) عبد الله بن قيس الأشعري رضي الله عنه (عنه - صلى الله عليه وسلم - أنه قال أمتي أمة مرحومة لا عذاب عليها في الآخرة عجل عقابها في الدنيا الزلازل والفتن فإذا كان يوم القيامة دفع إلى كل رجل من أمتي رجل من أهل الكتاب فقيل هذا فداؤك من النار)','2147','3466','0');
INSERT INTO [t38171] VALUES (3479,'٣٤٥٤ - (في لفظ آخر يأتي كل رجل من هذه الأمة بيهودي أو نصراني إلى جهنم فيقول هذا فدائي من النار فيلقي فيها)','2147','3466','0');
INSERT INTO [t38171] VALUES (3480,'٣٤٥٥ - (قال - صلى الله عليه وسلم - الحمى من فيح جهنم وهي حظ المؤمن من النار).','2148','3466','0');
INSERT INTO [t38171] VALUES (3481,'٣٤٥٦ - (روى في تفسير قوله تعالى يوم لا يخزي الله النبي والذين آمنوا)','2149','3466','0');
INSERT INTO [t38171] VALUES (3482,'٣٤٥٧ - (روي عن أنس) بن مالك رضي الله عنه (أن رسول الله - صلى الله عليه وسلم - سأل ربه في ذنوب أمته فقال يا رب اجعل حسابهم إلي لئلا يطلع على مساويهم غيري فأوحى الله تعالى إليه هم أمتك وهم عبادي وأنا أرحم بهم منك لا أجعل حسابهم إلى غيري لئلا تنظر إلى مساويهم أنت ولا غيرك)','2149','3466','0');
INSERT INTO [t38171] VALUES (3483,'٣٤٥٨ - (قال - صلى الله عليه وسلم - حياتي) أي في الدنيا (خير لكم وموتي خير لكم)','2149','3466','0');
INSERT INTO [t38171] VALUES (3484,'٣٤٥٩ - (قال - صلى الله عليه وسلم - يوما يا كريم العفو فقال جبريل عليه السلام أتدري ما تفسير يا كريم العفو هو إن عفا عن السيئات برحمته بدلها حسنات بكرمه)','2150','3466','0');
INSERT INTO [t38171] VALUES (3485,'٣٤٦٠ - (وسمع النبي - صلى الله عليه وسلم - رجلا يقول اللهم إني أسألك تمام النعمة فقال وهل تدري ما تمام النعمة قال لا قال دخول الجنة)','2150','3466','0');
INSERT INTO [t38171] VALUES (3486,'٣٤٦١ - (وفي الخبر إذا أذنب العبد ذنبا فاستغفر يقول الله عز وجل لملائكته انظروا إلى عبدي أذنب ذنبا فعلم أن له ربا يغفر الذنوب ويأخذ بالذنب أشهدكم أني قد غفرت له)','2151','3466','0');
INSERT INTO [t38171] VALUES (3487,'٣٤٦٢ - (في الخبر لو أذنب العبد حتى تبلغ ذنوبه عنان السماء غفرتها له ما استغفرني ورجاني)','2151','3466','0');
INSERT INTO [t38171] VALUES (3488,'٣٤٦٣ - (في الخبر لو لقيني عبدي بقراب الأرض ذنوبا لقيته بقرابها مغفرة)','2152','3466','0');
INSERT INTO [t38171] VALUES (3489,'٣٤٦٤ - (وفي الحديث إن الملك ليرفع القلم عن العبد إذا أذنب ست ساعات فإن تاب واستغفر لم يكتبه عليه وإلا كتبها سيئة وفي لفظ آخر فإذا كتبها عليه وعمل حسنة قال لصاحب الشمال وهو أمير عليه ألق هذه السيئة حتى ألقي من حسناته واحدة من تضعيف العشرة وأرفع له تسع حسنات فيلقى عنه هذه السيئة)','2153','3466','0');
INSERT INTO [t38171] VALUES (3490,'٣٤٦٥ - (روى أنس) رضي الله عنه (في حديث طويل أنه - صلى الله عليه وسلم - قال إذا أذنب العبد ذنبا كتب عليه فقال أعرابي)','2153','3466','0');
INSERT INTO [t38171] VALUES (3491,'٣٤٦٦ - (في الحديث الطويل لأنس) رضي الله عنه (أن الأعرابي قال لرسول الله - صلى الله عليه وسلم -)','2155','3466','0');
INSERT INTO [t38171] VALUES (3492,'٣٤٦٧ - (وفي بعض الأخبار) ولفظ القوت وفي الخبر المنفرد (المؤمن أفضل من الكعبة).','2155','3466','0');
INSERT INTO [t38171] VALUES (3493,'٣٤٦٨ - قال - صلى الله عليه وسلم - (المؤمن طيب طاهر).','2156','3466','0');
INSERT INTO [t38171] VALUES (3494,'٣٤٦٩ - قال - صلى الله عليه وسلم - (المؤمن أكرم على الله من الملائكة).','2156','3466','0');
INSERT INTO [t38171] VALUES (3495,'٣٤٧٠ - (وفي الخبر خلق الله جهنم من فضل رحمته سوطا يسوق الله به عباده إلى الجنة)','2156','3466','0');
INSERT INTO [t38171] VALUES (3496,'٣٤٧١ - (في خبر آخر يقول الله عز وجل إنما خلقت الخلق ليربحوا على ولم أخلقهم لأربح عليهم)','2157','3466','0');
INSERT INTO [t38171] VALUES (3497,'٣٤٧٢ - (في حديث أبي سعيد الخدري) رضي الله عنه (عن رسول الله - صلى الله عليه وسلم -) أنه قال (ما خلق الله شيئا إلا جعل له ما يغلبه وجعل رحمته تغلب غضبه)','2157','3466','0');
INSERT INTO [t38171] VALUES (3498,'٣٤٧٣ - (عن معاذ بن جبل وأنس بن مالك) رضي الله عنهما (أنه - صلى الله عليه وسلم - قال من قال لا إله إلا الله دخل الجنة ومن كان آخر كلامه لا إله إلا الله لم تمسه النار ومن لقي الله لا يشرك به شيئا حرمت عليه النار ولا يدخلها من في قلبه مثقال ذرة من إيمان)','2157','3466','0');
INSERT INTO [t38171] VALUES (3499,'٣٤٧٤ - (في خبر آخر لو علم الكافر سعة رحمة الله ما أيس من جنته أحد)','2159','3466','0');
INSERT INTO [t38171] VALUES (3500,'٣٤٧٧ - (لما تلا رسول الله - صلى الله عليه وسلم - قوله تعالى: إن زلزلة الساعة شيء عظيم قال أتدرون أي يوم هذا هذا يوم يقال)','2159','3466','0');
INSERT INTO [t38171] VALUES (3501,'٣٤٧٦ - (وفي الخبر لو لم تذنبوا لخلق الله خلقا يذنبون فيغفر لهم).','2162','3466','0');
INSERT INTO [t38171] VALUES (3502,'٣٤٧٧ - (وفي لفظ آخر لذهب بكم وجاء بخلق آخر فيذنبون فيغفر لهم انه هو الغفور الرحيم).','2162','3466','0');
INSERT INTO [t38171] VALUES (3503,'٣٤٧٨ - (وفي الخبر لو لم تذنبوا لخشيت عليكم ما هو شر من الذنوب وما هو قال العجب)','2163','3466','0');
INSERT INTO [t38171] VALUES (3504,'٣٤٧٩ - (وقال - صلى الله عليه وسلم - والذي نفسي بيده لله أرحم بعبده المؤمن من الوالدة الشفيقة بولدها).','2163','3466','0');
INSERT INTO [t38171] VALUES (3505,'٣٤٨٠ - (في الخبر ليغفرن الله تعالى يوم القيامة مغفرة ما خطرت قط على قلب أحد حتى إن إبليس ليتطاول لها رجاء أن تصيبه).','2163','3466','0');
INSERT INTO [t38171] VALUES (3506,'٣٤٨١ - (وفي الخبر لله مائة رحمة ادخر منها تسعا وتسعين رحمة وأظهر منها في الدنيا رحمة واحدة فيها يزاحم الخلق فتحن','2163','3466','0');
INSERT INTO [t38171] VALUES (3507,'٣٤٨٢ - (وفي الخبر ما منكم من أحد يدخله عمله الجنة ولا ينجيه من النار قالوا ولا أنت يا رسول الله قال ولا أنا إلا أن يتغمدني الله برحمته)','2164','3466','0');
INSERT INTO [t38171] VALUES (3508,'٣٤٨٣ - (وقال - صلى الله عليه وسلم - إني اختبأت شفاعتي لأهل الكبائر من أمتي).','2165','3466','0');
INSERT INTO [t38171] VALUES (3509,'٣٤٨٤ - (ترونها للمطيعين المتقين بل هي للمتلوثين المخلطين).','2166','3466','0');
INSERT INTO [t38171] VALUES (3510,'٣٤٨٥ - (قال - صلى الله عليه وسلم - بعثت بالحنيفية السمحة السهلة).','2166','3466','0');
INSERT INTO [t38171] VALUES (3511,'٣٤٨٦ - (قال - صلى الله عليه وسلم - أحب أن يعلم أهل الكتابين أن في ديننا سماحة).','2167','3466','0');
INSERT INTO [t38171] VALUES (3512,'٣٤٨٧ - (روى) أبو القاسم (محمد بن) علي بن أبي طالب الهاشمي المدني ابن (الحنفية)','2167','3466','0');
INSERT INTO [t38171] VALUES (3513,'٣٤٨٨ - (وفي الحديث أن رجلين من بني إسرائيل تواخيا في الله تعالى فكان أحدهما يسرف على نفسه)','2168','3466','0');
INSERT INTO [t38171] VALUES (3514,'٣٤٨٩ - (وروي عن) أبي عائشة (مسروق)','2169','3466','0');
INSERT INTO [t38171] VALUES (3515,'٣٤٩٠ - (روى ابن عباس رضى الله عنه أن رسول الله - صلى الله عليه وسلم - كان يقنت على المشركين ويلعنهم في صلاته فنزل عليه قوله تعالى) ليقطع طرفا من الذين كفروا أو يكبتهم إلى قوله تعالى (ليس لك من الأمر شيء الآية فترك الدعاء عليهم وهدى الله تعالى عامة أولئك للإسلام)','2169','3466','0');
INSERT INTO [t38171] VALUES (3516,'٣٤٩١ - (روي في الأثر أن رجلين كانا من العابدين) من عباد بني إسرائيل (متساويين في العبادة قال فإذا أدخلا الجنة رفع أحدهما في الدرجات العلى على صاحبه فيقول يا رب ما كان هذا في الدنيا بأكثر مني عبادة فرفعته علي في) أعلى (عليين فيقول الله سبحانه أنه كان يسألني في الدنيا الدرجات العلى وأنت كنت تسألني النجاة من النار فأعطيت كل عبد سؤله)','2170','3466','0');
INSERT INTO [t38171] VALUES (3517,'٣٤٩٢ - (قال - صلى الله عليه وسلم - سلوا الله الدرجات العلى فإنما تسألون كريما).','2170','3466','0');
INSERT INTO [t38171] VALUES (3518,'٣٤٩٣ - (قال) - صلى الله عليه وسلم - (إذا سألتم الله فأعظموا الرغبة وسلوا الفردوس الأعلى فإن الله لا يتعاظمه شيء).','2171','3466','0');
INSERT INTO [t38171] VALUES (3519,'٣٤٩٤ - (قال - صلى الله عليه وسلم - أنا أخوفكم لله).','2171','3466','0');
INSERT INTO [t38171] VALUES (3520,'٣٤٩٥ - (أشار النبي - صلى الله عليه وسلم - حيث كان) ذات يوم (على المنبر فقبض كفه اليمنى ثم قال هذا كتاب الله كتب فيه أهل الجنة بأسمائهم وأسماء آبائهم وأنسابهم لا يزاد فيهم ولا ينقص ثم قبض كفه اليسرى وقال هذا كتاب الله كتب فيه أهل النار بأسمائهم وأسماء آبائهم وأنسابهم لا يزاد فيهم ولا ينقص وليعملن أهل السعادة بعمل أهل الشقاوة حتى يقال كأنهم منهم بل هم هم ثم يستنقذهم الله قبل الموت ولو بفواق ناقة)','2172','3466','0');
INSERT INTO [t38171] VALUES (3521,'٣٤٩٧ - (لما خير رسول الله - صلى الله عليه وسلم - في مرض موته بين البقاء في الدنيا وبين القدوم على الله تعالى كان يقول أسألك الرفيق','2173','3466','0');
INSERT INTO [t38171] VALUES (3522,'٣٤٩٨ - (قال - صلى الله عليه وسلم - في فضيلة التقوى إذا جمع الله الأولين والآخرين لميقات يوم معلوم ناداهم بصوت يسمع أقصاهم كما يسمع أدناهم يا أيها الناس إني قد أنصت لكم منذ خلقتكم إلى يومكم هذا فأنصتوا إلى اليوم إنما هي أعمالكم ترد عليكم أيها الناس إني جعلت نسبا وجعلتم نسبا فوضعتم نسبي ورفعتم نسبكم قلت إن أكرمكم عند الله أتقاكم وأبيتم إلا أن تقولوا فلان بن فلان وفلان أغنى من فلان فاليوم أضع نسبكم وأرفع نسبي ألا أين المتقون فيرفع للقوم لواء فيتبع القوم لواءهم إلى منازلهم فيدخلون الجنة بغير حساب).','2174','3466','0');
INSERT INTO [t38171] VALUES (3523,'٣٤٩٩ - (قال - صلى الله عليه وسلم - رأس الحكمة) أي أصلها (مخافة الله)','2175','3466','0');
INSERT INTO [t38171] VALUES (3524,'٣٥٠٠ - (وقال - صلى الله عليه وسلم - لابن مسعود إن أردت أن تلقاني فأكثر من الخوف بعدي).','2175','3466','0');
INSERT INTO [t38171] VALUES (3525,'٣٥٠١ - (وفي خبر موسى عليه السلام لا يبقى أحد إلا ناقشته الحساب وفتشت عما في يديه إلا الورعين فإني أستحييهم وأجلهم أن أوقفهم للحساب)','2175','3466','0');
INSERT INTO [t38171] VALUES (3526,'٣٥٠٢ - (قال - صلى الله عليه وسلم - قال الله عز وجل وعزتي لا أجمع على عبدي خوفين ولا أجمع له أمنين فإن أمنني في الدنيا أخفته يوم القيامة وإذا خافني في الدنيا أمنته يوم القيامة).','2176','3466','0');
INSERT INTO [t38171] VALUES (3527,'٣٥٠٣ - (قال - صلى الله عليه وسلم - من خاف الله تعالى خافه كل شيء ومن خاف غير الله خوفه من كل شيء).','2176','3466','0');
INSERT INTO [t38171] VALUES (3528,'٣٥٠٤ - (وقال - صلى الله عليه وسلم - أتمكم عقلا أشدكم خوفا لله تعالى وأحسنكم فيما أمر الله به ونهى عنه نظرا).','2177','3466','0');
INSERT INTO [t38171] VALUES (3529,'٣٥٠٥ - (قال القشيري في الرسالة أخبرنا علي بن أحمد الأهوازي أخبرنا أحمد بن عبيد حدثنا محمد بن عثمان حدثنا القاسم بن محمد حدثنا يحيى بن يمان عن مالك بن مغول عن عبد الرحمن بن سعيد بن وهب (قالت عائشة رضي الله عنها قلت يا رسول الله) قوله تعالى (الذين يؤتون ما أتوا وقلوبهم وجلة أهو الرجل يسرق ويزني)','2177','3466','0');
INSERT INTO [t38171] VALUES (3530,'٣٥٠٦ - (وقال - صلى الله عليه وسلم - ما من عبد مؤمن تخرج من عينيه دمعة وإن كانت مثل رأس الذباب من خشية الله تعالى ثم تصيب شيئا من حر وجهه إلا حرمه الله على النار).','2178','3466','0');
INSERT INTO [t38171] VALUES (3531,'٣٥٠٧ - (وقال - صلى الله عليه وسلم - إذا اقشعر جلد المؤمن من خشية الله فتحاتت عنه خطاياه كما يتحات عن الشجرة ورقها).','2178','3466','0');
INSERT INTO [t38171] VALUES (3532,'٣٥٠٨ - (وقال - صلى الله عليه وسلم - لا يلج النار أحد بكى من خشية الله حتى يعود اللبن في الضرع).','2178','3466','0');
INSERT INTO [t38171] VALUES (3533,'٣٥٠٩ - (قال عقبة بن عامر)','2179','3466','0');
INSERT INTO [t38171] VALUES (3534,'٣٥١٠ - (قالت عائشة رضي الله عنها قلت يا رسول الله أيدخل أحد من أمتك الجنة بغير حساب قال نعم من ذكر ذنوبه فبكى)','2179','3466','0');
INSERT INTO [t38171] VALUES (3535,'٣٥١١ - (قال - صلى الله عليه وسلم - ما من قطرة أحب إلى الله تعالى من قطرة دمع من خشية الله تعالى أو قطرة دم اهريقت في سبيل الله تعالى).','2179','3466','0');
INSERT INTO [t38171] VALUES (3536,'٣٥١٢ - (قال - صلى الله عليه وسلم - اللهم ارزقني عينين هطالتين تشفيان) القلب (بذروف الدمع) وفي لفظ الدموع (قبل أن تصير) وفي لفظ تكون (الدموع دمار الأضراس جمرا).','2179','3466','0');
INSERT INTO [t38171] VALUES (3537,'٣٥١٤ - (كان رسول الله - صلى الله عليه وسلم - قد خصه بعلم المنافقين).','2181','3466','0');
INSERT INTO [t38171] VALUES (3538,'٣٥١٥ - (قال - صلى الله عليه وسلم - إن الرجل ليعمل عمل أهل الجنة خمسين سنة)','2182','3466','0');
INSERT INTO [t38171] VALUES (3539,'٣٥١٦ - (قال - صلى الله عليه وسلم - احتج آدم وموسى عليهما السلام عند ربهما','2182','3466','0');
INSERT INTO [t38171] VALUES (3540,'٣٥١٧ - (روي أنه كان يصلي على طفل) منفوس (ففي رواية أنه سمع في دعائه) له (يقول اللهم قه عذاب القبر وعذاب النار)','2183','3466','0');
INSERT INTO [t38171] VALUES (3541,'٣٥١٨ - (وفي رواية ثانية أنه سمع قائلة تقول هنيئا لك عصفور من عصافير الجنة فغضب وقال ما يدريك أنه كذلك والله أني رسول الله - صلى الله عليه وسلم - وما أدرى ما يصنع بي إن الله خلق الجنة وخلق لها أهلا وخلق النار وخلق لها أهلا لا يزاد فيهم ولا ينقص منهم)','2183','3466','0');
INSERT INTO [t38171] VALUES (3542,'٣٥١٩ - (روي أنه - صلى الله عليه وسلم - قال ذلك أيضا على جنازة عثمان بن مظعون)','2183','3466','0');
INSERT INTO [t38171] VALUES (3543,'٣٥٢٠ - (في حديث آخر أنه - صلى الله عليه وسلم - دخل على بعض أصحابه وهو عليل فسمع امرأة تقول هنيئا لك الجنة فقال - صلى الله عليه وسلم - من هذه المتألية على الله فقال المريض هي أمي يا رسول الله فقال وما يدريك لعل فلانا كان يتكلم بما لا يعنيه ويبخل بما لا يغنيه)','2184','3466','0');
INSERT INTO [t38171] VALUES (3544,'٣٥٢٣ - (روي) في الخبر المشهور (أن النبي - صلى الله عليه وسلم - وجبريل عليه السلام بكيا خوفا من الله عز وجل فأوحى الله إليهما لم تبكيان وقد أمنتكما فقالا ومن يأمن مكرك)','2185','3466','0');
INSERT INTO [t38171] VALUES (3545,'٣٥٢٤ - (ولما ضعفت شوكة المسلمين يوم بدر قال - صلى الله عليه وسلم -) في دعائه (اللهم إن تهلك هذه العصابة لم يبق على وجه الأرض أحد يعبدك فقال أبو بكر رضي الله عنه دع مناشدتك ربك فإنه واف لك بما وعدك).','2185','3466','0');
INSERT INTO [t38171] VALUES (3546,'٣٥٢٥ - (وكان أصحاب رسول الله - صلى الله عليه وسلم - يقولون إنكم لتعملون إعمالا هي أدق في أعينكم من الشعر كنا نعدها على عهد رسول الله - صلى الله عليه وسلم - من الكبائر)','2186','3466','0');
INSERT INTO [t38171] VALUES (3547,'٣٥٢٦ - (وقال رجل لابن عمر) رضي الله عنهما (إنا ندخل على هؤلاء الأمراء فنصدقهم بما يقولون)','2186','3466','0');
INSERT INTO [t38171] VALUES (3548,'٣٥٢٧ - (أشد من ذلك روي أن نفرا قعدوا على باب حذيفة) رضي الله عنه (ينتظرونه فكانوا يتكلمون في شيء من شأنه فلما خرج عليهم سكتوا حياء منه فقال تكلموا فيما كنتم تقولون','2186','3466','0');
INSERT INTO [t38171] VALUES (3549,'٣٥٢٨ - (قال - صلى الله عليه وسلم - العبد المؤمن بين مخافتين بين أجل قد مضي لا يدري ما الله صانع فيه وبين أجل قد بقي لا يدري ما الله قاض فيه فوالذي نفسي بيده ما بعد الموت من مستعتب ولا بعد الدنيا من دار إلا الجنة أو النار).','2187','3466','0');
INSERT INTO [t38171] VALUES (3550,'٣٥٢٩ - (تقول له النار جز يا مؤمن فإن نورك قد أطفأ لهبي)','2188','3466','0');
INSERT INTO [t38171] VALUES (3551,'٣٥٣٠ - (والإفضاح على ملأ من الأشهاد في القيامة).','2188','3466','0');
INSERT INTO [t38171] VALUES (3552,'٣٥٣١ - (وهول الزبانية).','2188','3466','0');
INSERT INTO [t38171] VALUES (3553,'٣٥٣٢ - (في حواصل طيور خضر معلقة تحت العرش إن كانت سعيدة','2189','3466','0');
INSERT INTO [t38171] VALUES (3554,'٣٥٣٣ - (قال - صلى الله عليه وسلم - أكثر أهل الجنة البله)','2189','3466','0');
INSERT INTO [t38171] VALUES (3555,'٣٥٣٤ - (من هذا حاله وإن قتل في المعركة فهو بعيد عن مثل هذه الرتبة) أي رتبة الشهادة (كما دلت عبيه الأخبار).','2189','3466','0');
INSERT INTO [t38171] VALUES (3556,'٣٥٣٥ - (روت عائشة رضي الله عنها أن رسول الله - صلى الله عليه وسلم - كان إذا تغير الهواء وهبت ريح عاصفة يتغير وجهه فيقوم ويتردد في الحجرة ويدخل ويخرج كل ذلك خوفا من عذاب الله).','2190','3466','0');
INSERT INTO [t38171] VALUES (3557,'٣٥٣٦ - (قرأ - صلى الله عليه وسلم -) آية (في سورة الحاقة فصعق)','2190','3466','0');
INSERT INTO [t38171] VALUES (3558,'٣٥٣٧ - (قال الله عز وجل فخر موسى صعقا ورأى رسول الله - صلى الله عليه وسلم - صورة جبريل عليه السلام بالأبطح فصعق).','2190','3466','0');
INSERT INTO [t38171] VALUES (3559,'٣٥٣٨ - (قال - صلى الله عليه وسلم - ما جاءني جبريل قط إلا وهو يرعد فرقا من الجبار)','2190','3466','0');
INSERT INTO [t38171] VALUES (3560,'٣٥٣٩ - (وعن أنس) رضي الله عنه (أنه - صلى الله عليه وسلم - سأل جبريل عليه السلام مالي لا أرى ميكائيل يضحك فقال جبريل) عليه السلام ما ضحك ميكائيل منذ خلقت النار).','2191','3466','0');
INSERT INTO [t38171] VALUES (3561,'٣٥٤٠ - (وقال ابن عمر رضي الله عنهما خرجت مع رسول الله - صلى الله عليه وسلم - حتى دخل بعض حيطان الأنصار)','2191','3466','0');
INSERT INTO [t38171] VALUES (3562,'٣٥٤١ - (روي أن فتى من الأنصار دخلته خشية النار فكان يبكي حتى حبسه ذلك في البيت)','2192','3466','0');
INSERT INTO [t38171] VALUES (3563,'٣٥٤٢ - (وقال ميمون بن مهران) الجزري كاتب عمر بن عبد العزيز (لما نزلت هذه الآية وإن جهنم لموعدهم أجمعين صاح سلمان الفارسي) رضي الله عنه (ووضع يده على رأسه وخرج هاربا ثلاثة أيام لا يقدر عليه).','2192','3466','0');
INSERT INTO [t38171] VALUES (3564,'كتاب الفقر والزهد','2194','3303','0');
INSERT INTO [t38171] VALUES (3565,'٣٥٤٣ - (حملت خزائن الأرض إلى رسول الله - صلى الله عليه وسلم - وإلى أبي بكر وعمر رضي الله عنهما فأخذوها ووضعوها في مواضعها وما هربوا منها إذا كان يستوي عندهم المال والماء والذهب والحجر).','2195','3564','0');
INSERT INTO [t38171] VALUES (3566,'٣٥٤٤ - (قول رسول الله - صلى الله عليه وسلم -) اللهم إني (أعوذ بك من الفقر)','2196','3564','0');
INSERT INTO [t38171] VALUES (3567,'٣٥٤٥ - (قوله - صلى الله عليه وسلم - كاد الفقر أن يكون كفرا)','2196','3564','0');
INSERT INTO [t38171] VALUES (3568,'٣٥٤٦ - (قوله) - صلى الله عليه وسلم - (اللهم أحيني مسكينا وأمتني مسكينا)','2196','3564','0');
INSERT INTO [t38171] VALUES (3569,'٣٥٤٧ - (روى عبد الله بن عمر) بن الخطاب (رضي الله عنهما قال قال رسول الله - صلى الله عليه وسلم - لأصحابه أي الناس خير قالوا)','2196','3564','0');
INSERT INTO [t38171] VALUES (3570,'٣٥٤٨ - (قال - صلى الله عليه وسلم - لبلال) رضي الله عنه (الق الله فقيرا ولا تلقه غنيا).','2197','3564','0');
INSERT INTO [t38171] VALUES (3571,'٣٥٤٩ - (قوله - صلى الله عليه وسلم - الرؤيا الصالحة جزء من ستة وأربعين جزءا من النبوة).','2197','3564','0');
INSERT INTO [t38171] VALUES (3572,'٣٥٥٠ - (قال - صلى الله عليه وسلم - أيضا خير هذه الأمة فقراؤها وأسرعها تضجعا)','2198','3564','0');
INSERT INTO [t38171] VALUES (3573,'٣٥٥١ - (وقال - صلى الله عليه وسلم - إن لي حرفتين اثنتين فمن أحبهما فقد أحبني ومن أبغضها فقد أبغضني الفقر والجهاد).','2198','3564','0');
INSERT INTO [t38171] VALUES (3574,'٣٥٥٢ - (وروي أن جبريل عليه السلام نزل على رسول الله - صلى الله عليه وسلم - فقال يا محمد إن الله يقرأ عليك السلام ويقول أتحب أن أجعل هذه الجبال ذهبا وتكون معك أينما كنت فأطرق رسول الله - صلى الله عليه وسلم - ساعة ثم قال يا جبريل إن الدنيا دار من لا دار له ومال من لا مال','2198','3564','0');
INSERT INTO [t38171] VALUES (3575,'٣٥٥٤ - (وقال - صلى الله عليه وسلم - الفقر أزين بالمؤمن من العذار الحسن على خد الفرس).','2200','3564','0');
INSERT INTO [t38171] VALUES (3576,'٣٥٥٥ - (قال) - صلى الله عليه وسلم - (من أصبح منكم معافى في جسمه أمنا في سربه عنده قوت يومه فكأنما حيزت له الدنيا بحذافيرها)','2200','3564','0');
INSERT INTO [t38171] VALUES (3577,'٣٥٥٦ - (وقال نبينا - صلى الله عليه وسلم - اطلعت في الجنة فرأيت أكثر أهلها الفقراء واطلعت في النار فرأيت أكثر أهلها الأغنياء وفي لفظ قلت: أين الأغنياء فقيل حبسهم الجد).','2200','3564','0');
INSERT INTO [t38171] VALUES (3578,'٣٥٥٧ - (في حديث آخر فرآيت أكثر أهل النار النساء)','2201','3564','0');
INSERT INTO [t38171] VALUES (3579,'٣٥٥٨ - (قال - صلى الله عليه وسلم - تحفة المؤمن في الدنيا الفقر).','2201','3564','0');
INSERT INTO [t38171] VALUES (3580,'٣٥٦٠ - (في حديث آخر رأيته) يعني عبد الرحمن بن عوف (دخل الجنة زحفا)','2202','3564','0');
INSERT INTO [t38171] VALUES (3581,'٣٥٦١ - (وفي خبر عن آل البيت عليهم السلام أنه - صلى الله عليه وسلم - قال إذا أحب الله عبدا ابتلاه فإذا أحبه الحب البالغ اقتناه قيل وما اقتناه قال لم يترك له أهلا ولا مالا).','2202','3564','0');
INSERT INTO [t38171] VALUES (3582,'٣٥٦٢ - (وفي الخبر إذا رأيت الفقر مقبلا فقل مرحبا بشعار الصالحين وإذا رأيت الغنى مقبلا فقل ذنب عجلت عقوبته).','2202','3564','0');
INSERT INTO [t38171] VALUES (3583,'٣٥٦٣ - (لما قال سادات العرب وأغنياؤهم للنبي - صلى الله عليه وسلم - اجعل لنا يوما ولهم يوما يجيئون إليك ولا نجيء ونجيء إليك ولا يجيئون يعنون بذلك الفقراء)','2203','3564','0');
INSERT INTO [t38171] VALUES (3584,'٣٥٦٤ - (واستأذن) عبد الله (ابن أم مكتوم) الأعمى رضي الله عنه (على النبي - صلى الله عليه وسلم -) يوما (وعنده رجل من أشراف قريش فشق ذلك على النبي - صلى الله عليه وسلم - فأنزل الله تعالى عبس وتولى أن جاءه الأعمى وما يدريك لعله يزكى أو يذكر فتنفعه الذكرى يعني ابن أم مكتوم أما من استغنى فأنت له تصدى يعني هذا الشريف).','2205','3564','0');
INSERT INTO [t38171] VALUES (3585,'٣٥٦٥ - (عن النبي - صلى الله عليه وسلم - أنه قال يؤتى بالعبد يوم القيامة فيعتذر الله إليه كما يعتذر الرجل للرجل في الدنيا فيقول وعزتي وجلالي ما زويت الدنيا عنك لهوانك علي ولكن لما أعددت لك من الكرامة والفضيلة اخرج يا عبدي إلى هذه الصفوف فمن أطعمك في أو كساك في يريد بذلك وجهي فخذ بيده فهو لك والناس قد ألجمهم العرق فيتخلل الصفوف وينظر من فعل ذلك به فيأخذه بيده ويدخله الجنة).','2206','3564','0');
INSERT INTO [t38171] VALUES (3586,'٣٥٦٦ - (وقال - صلى الله عليه وسلم - أكثروا معرفة الفقراء واتخذوا عندهم الأيادي فإن لهم دولة قالوا يا رسول الله وما دولتهم قال إذا كان يوم القيامة قيل لهم انظروا من أطعمكم كسرة وسقاكم شربة أو كساكم ثوبا فخذوا بيده ثم أفيضوا به إلى الجنة).','2206','3564','0');
INSERT INTO [t38171] VALUES (3587,'٣٥٦٧ - (وقال - صلى الله عليه وسلم - دخلت الجنة فسمعت حركة أمامي فنظرت فإذا بلال ونظرت في أعلاها فإذا فقراء أمتي وأولادهم ونظرت في أسفلها فإذا فيهم من الأغنياء والنساء قليل قلت يا رب ما شأنهم قال أما النساء فأضرهم الأحمران الذهب والحرير)','2208','3564','0');
INSERT INTO [t38171] VALUES (3588,'٣٥٦٨ - (دخل - صلى الله عليه وسلم - على رجل فقير ولم ير له شيئا فقال لو قسم نور هذا على أهل الأرض لوسعهم).','2209','3564','0');
INSERT INTO [t38171] VALUES (3589,'٣٥٦٩ - (قال - صلى الله عليه وسلم - ألا أخبركم بملوك أهل الجنة قالوا بلى يا رسول الله قال كل ضعيف مستضعف أغبر أشعث ذي طمرين لو أقسم على الله لأبره).','2209','3564','0');
INSERT INTO [t38171] VALUES (3590,'٣٥٧٠ - (قال عمران بن الحصين)','2209','3564','0');
INSERT INTO [t38171] VALUES (3591,'٣٥٧١ - (وروي عن علي كرم الله وجهه أن رسول الله - صلى الله عليه وسلم - قال إذا أبغض الناس فقراءهم وأظهروا عمارة الدنيا وتكالبوا على جمع الدراهم رماهم الله بأربع خصال بالقحط من الزمان والجور من السلطان والخيانة من ولاة الأحكام والشوكة من الأعداء).','2210','3564','0');
INSERT INTO [t38171] VALUES (3592,'٣٥٧٢ - (وأرسل عمر رضي الله عنه إلى سعيد بن عامر) بن خديم الجمحي رضي الله عنه (بألف دينار) وفي رواية بأربعمائة دينار','2210','3564','0');
INSERT INTO [t38171] VALUES (3593,'٣٥٧٣ - (وكان قد أوصاها رسول الله - صلى الله عليه وسلم - وقال إن أردت اللحوق بي فعليك بعيش الفقراء وإياك ومجالسة الأغنياء ولا تنزعي درعك حتى ترقعيه).','2212','3564','0');
INSERT INTO [t38171] VALUES (3594,'٣٥٧٤ - (قال رسول الله - صلى الله عليه وسلم - طوبى لمن هدي للإسلام وكان عيشه كفافا وقنع به)','2212','3564','0');
INSERT INTO [t38171] VALUES (3595,'٣٥٧٥ - (وقال - صلى الله عليه وسلم - يا معشر الفقراء أعطوا لله الرضا من قلوبكم تظفروا بثواب فقركم وإلا فلا).','2213','3564','0');
INSERT INTO [t38171] VALUES (3596,'٣٥٧٦ - (وروي عن عمر بن الخطاب رضي الله عنه عن النبي - صلى الله عليه وسلم - أنه قال إن لكل شيء مفتاحا ومفتاح الجنة حب المساكين والفقراء الصبر هم جلساء الله تعالى يوم القيامة).','2213','3564','0');
INSERT INTO [t38171] VALUES (3597,'٣٥٧٧ - (روى عن علي رضي الله عنه عن النبي - صلى الله عليه وسلم - أنه قال أحب العباد إلى الله تعالى الفقير القانع برزقه الراضي عن الله تعالى).','2213','3564','0');
INSERT INTO [t38171] VALUES (3598,'٣٥٧٨ - (وقال - صلى الله عليه وسلم - اللهم اجعل قوت آل محمد كفافا)','2214','3564','0');
INSERT INTO [t38171] VALUES (3599,'٣٥٧٩ - (قال - صلى الله عليه وسلم - لا أحد أفضل من الفقير إذا كان راضيا).','2214','3564','0');
INSERT INTO [t38171] VALUES (3600,'٣٥٨٠ - (قال - صلى الله عليه وسلم - يقول الله تعالى يوم القيامة أين صفوتي من خلقي فتقول الملائكة ومن هم يا ربنا فيقول فقراء المسلمين القانعون بعطائي الراضون بقدري أدخلوهم الجنة فيدخلونها ويأكلون ويشربون والناس في الحساب يترددون).','2214','3564','0');
INSERT INTO [t38171] VALUES (3601,'٣٥٨١ - (روي في الخبر أن الفقراء شكوا إلى رسول الله - صلى الله عليه وسلم -','2214','3564','0');
INSERT INTO [t38171] VALUES (3602,'٣٥٨٢ - (روى زيد بن أسلم) العدوي التابعي مولى عمر مات سنة ست وثلاثين (عن أنس بن مالك) رضي الله عنه (قال بعث الفقراء رسولا إلى رسول الله - صلى الله عليه وسلم - فقال إني رسول الفقراء إليك فقال مرحبا بك وبمن جئت من عندهم جئت من عند قوم أحبهم فقال قالوا يا رسول الله إن الأغنياء ذهبوا بالجنة) أي بالدرجات فيها (يحجون ولا نقدر عليه ويعتمرون ولا نقدر عليه وإذا مرضوا بعثوا بفضل أموالهم ذخيرة لهم فقال رسول الله - صلى الله عليه وسلم - بلغ عني الفقراء أن لمن صبر واحئسب منكم ثلاث خصال ليست للأغنياء أما خصلة','2215','3564','0');
INSERT INTO [t38171] VALUES (3603,'٣٥٨٣ - (وفي الخبر إن لكل أمة عجلا وعجل هذه الأمة الدينار والدرهم)','2216','3564','0');
INSERT INTO [t38171] VALUES (3604,'٣٥٨٥ - (أهدي إلى رسول الله - صلى الله عليه وسلم -) من رجل أو امرأة (سمن وأقط وكبش فقبل السمن والأقط ورد الكبش).','2217','3564','0');
INSERT INTO [t38171] VALUES (3605,'٣٥٨٦ - (كان - صلى الله عليه وسلم - يقبل من بعض الناس ويرد على بعض).','2217','3564','0');
INSERT INTO [t38171] VALUES (3606,'٣٥٨٧ - (قال) - صلى الله عليه وسلم - (لقد هممت أن لا أتهب) أي لا أقبل الهبة (إلا من قرشي أو ثقفي أو أنصاري أو دوسي).','2218','3564','0');
INSERT INTO [t38171] VALUES (3607,'٣٥٨٨ - (عطاء)','2218','3564','0');
INSERT INTO [t38171] VALUES (3608,'٣٥٨٩ - (وقال - صلى الله عليه وسلم - من أتاه شيء من هذا المال من غير مسألة ولا استشراف فإنما هو رزق ساقه الله إليه وفي لفظ آخر فلا يرده).','2218','3564','0');
INSERT INTO [t38171] VALUES (3609,'٣٥٩٠ - (وقال - صلى الله عليه وسلم - لا حق لابن آدم إلا في ثلاث طعام يقيم صلبه وثوب يواري عورته وبيت يكنه) من الحر والبرد (فما زاد فهو حساب).','2219','3564','0');
INSERT INTO [t38171] VALUES (3610,'٣٥٩١ - (قال - صلى الله عليه وسلم - للسائل حق ولو جاء على فرس).','2219','3564','0');
INSERT INTO [t38171] VALUES (3611,'٣٥٩٢ - (وفي الحديث ردوا السائل ولو بظلف محرق).','2221','3564','0');
INSERT INTO [t38171] VALUES (3612,'٣٥٩٣ - (قوله - صلى الله عليه وسلم - حيث قال مسألة الناس من الفواحش ما أحل) أي ما أبيح (من الفواحش غيرها).','2221','3564','0');
INSERT INTO [t38171] VALUES (3613,'٣٥٩٤ - (وقال - صلى الله عليه وسلم - من سأل عن غني فإنما يستكثر من جمر جهنم ومن سأل وله ما يغنيه جاء يوم القيامة ووجهه عظم يتقعقع ليس له لحم).','2222','3564','0');
INSERT INTO [t38171] VALUES (3614,'٣٥٩٥ - (في لفظ أخر) من سأل وله ما يغنيه (كانت مسألته','2222','3564','0');
INSERT INTO [t38171] VALUES (3615,'٣٥٩٧ - (قال - صلى الله عليه وسلم - استغنوا عن الناس وما قل من السؤال فهو خير قالوا ومنك قال ومني).','2223','3564','0');
INSERT INTO [t38171] VALUES (3616,'٣٥٩٦ - (وكان - صلى الله عليه وسلم - يأمر كثيرا بالتعفف عن السؤال ويقول من سألنا أعطيناه ومن استغنى أغناه الله تعالى ومن لم يسألنا فهو أحب إلينا).','2223','3564','0');
INSERT INTO [t38171] VALUES (3617,'٣٥٩٨ - (قال - صلى الله عليه وسلم - إنما أنا أحكم بالظاهر والله يتولى السرائر).','2224','3564','0');
INSERT INTO [t38171] VALUES (3618,'٣٥٩٩ - (ورد في الحديث) الآخر (استغنوا بغنى الله تعالى عن غيره)','2224','3564','0');
INSERT INTO [t38171] VALUES (3619,'٣٦٠٠ - (في حديث آخر من سأل وله خمسون درهما أو عدلها من الذهب فقد سأل إلحافا)','2224','3564','0');
INSERT INTO [t38171] VALUES (3620,'٣٦٠١ - (قال جميع المسلمين على عهد رسول الله - صلى الله عليه وسلم - إنا نحب ربنا ولو علمنا في أي شيء محبته لفعلناه حتى نزل قوله تعالى ولو أنا كتبنا عليهم أن اقتلوا أنفسكم أو اخرجوا من دياركم ما فعلوه إلا قليل منهم قال ابن مسعود) رضي الله عنه (قال لي رسول الله - صلى الله عليه وسلم - أنت منهم يعني من القليل).','2225','3564','0');
INSERT INTO [t38171] VALUES (3621,'٣٦٠٢ - (قال رسول الله - صلى الله عليه وسلم - من أصبح وهمه الدنيا شتت الله عليه أمره وفرق عليه ضيعته) أي عياله وما يخاف عليه من الضياع (وجعل فقره بين عينيه ولم يأته من الدنيا إلا ما كتب له ومن أصبح وهمه الآخرة جمع الله له همه وحفظ عليه ضيعته وجعل غناه في قلبه وأتته الدنيا وهي راغمة) وإن لم يردها.','2227','3564','0');
INSERT INTO [t38171] VALUES (3622,'٣٦٠٣ - (قال) ابن مسعود أيضا (ما عرفت أن فينا من يحب الدنيا حتى نزل قوله تعالى منكم من يريد الدنيا ومنكم من يريد الآخرة)','2227','3564','0');
INSERT INTO [t38171] VALUES (3623,'٣٦٠٤ - (قال - صلى الله عليه وسلم - إذا رأيتم العبد قد أعطي صمتا وزهدا في الدنيا فاقتربوا منه فإنه يلقي الحكمة).','2227','3564','0');
INSERT INTO [t38171] VALUES (3624,'٣٦٠٥ - (وعن بعض الصحابة أنه قال قلنا يا رسول الله (أي الناس خير قال كل مؤمن مخموم القلب صدوق اللسان قلنا يا رسول الله وما مخموم القلب قال التقي النقي الذي لا غل فيه ولا غش ولا بغي ولا حسد قيل يا رسول الله فمن على أثره قال الذي يشنأ الدنيا) أي يبغضها (ويحب الآخرة).','2228','3564','0');
INSERT INTO [t38171] VALUES (3625,'٣٦٠٦ - (قال - صلى الله عليه وسلم - إن أردت أن يحبك الله فازهد في الدنيا).','2229','3564','0');
INSERT INTO [t38171] VALUES (3626,'٣٦٠٧ - (وفي خبر) مروي (من طريق أهل البيت) أسنده جعفر الصادق عن آبائه الأخيار إلى الرسول المختار قال فيه (الزهد والورع يجولان في القلوب كل ليلة فإن صادفا قلبا فيه الإيمان والحياء أقاما فيه وإلا ارتحلا)','2229','3564','0');
INSERT INTO [t38171] VALUES (3627,'٣٦٠٨ - (ولها قال حارثة) بن مالك الأنصاري ويقال له أيضا الحرث (لرسول الله - صلى الله عليه وسلم - أنا مؤمن حقا قال وما حقيقة إيمانك)','2229','3564','0');
INSERT INTO [t38171] VALUES (3628,'٣٦٠٩ - (ولما سئل رسول الله - صلى الله عليه وسلم - عن معنى الشرح فى قوله تعالى فمن يرد الله أن يهديه يشرح صدره للإسلام وقيل له ما هذا الشرح فقال إن النور إذا دخل في القلب أنشرح له الصدر وانفسح قيل يا رسول الله وهل لذلك من علامة قال نعم التجافي)','2231','3564','0');
INSERT INTO [t38171] VALUES (3629,'٣٦١٠ - (وقال - صلى الله عليه وسلم - استحيوا من الله حق الحياء قالوا إنا نستحي منه فقال) ليس كذلك (تبنون ما لا تسكنون وتجمعون ما لا تأكلون فبين أن ذلك يناقض الحياء من الله تعالى)','2232','3564','0');
INSERT INTO [t38171] VALUES (3630,'٣٦١١ - (لما قدم عليه) - صلى الله عليه وسلم - (بعض الوفود) من العرب قال لهم ما أنتم (قالوا إنا مؤمنون قال وما علامة إيمانكم فذكروا الصبر على البلاء والشكر عند الرخاء والرضا بمواقع القضاء وترك الشماتة بالمصيبة إذا نزلت بالأعداء فقال - صلى الله عليه وسلم - إن كنتم كذلك فلا تجمعوا ما لا تأكلون ولا تبنوا ما لا تسكنون ولا تنافسوا فيما عنه ترحلون فجعل الزهد تكملة لإيمانهم).','2233','3564','0');
INSERT INTO [t38171] VALUES (3631,'٣٦١٢ - (وقال جابر) بن عبد الله الأنصاري رضي الله عنه (خطبنا رسول الله - صلى الله عليه وسلم - فقال من جاء بلا إله إلا الله لا يخلط بها) أي معها (غيرها وجبت له الجنة فقام) إليه (علي) بن أبي طالب (كرم الله وجهه فقال بأبي أنت وأمي يا رسول الله ما لا يخلط بها غيرها صفه لنا فسره لنا فقال حب الدنيا طالبا لها واتباعا لها وقوم يقولون قول الأنبياء ويعملون عمل الجبابرة فمن جاء بلا إله إلا الله ليس فيها شيء من هذا وجبت له الجنة)','2233','3564','0');
INSERT INTO [t38171] VALUES (3632,'٣٦١٣ - (وفي الخبر السخاء من اليقين ولا يدخل النار موقن والبخل من الشك ولا يدخل الجنة من شك)','2234','3564','0');
INSERT INTO [t38171] VALUES (3633,'٣٦١٤ - (روى) سعيد بن (بن المسيب) رحمه الله تعالى (عن أبي ذر رضي الله عنه عن رسول الله - صلى الله عليه وسلم - أنه قال من زهد في الدينا أدخل الله الحكمة قلبه فأنطق بها لسانه وعرفه داء الدنيا ودواءها وأخرجه منها سالما إلى دار السلام)','2234','3564','0');
INSERT INTO [t38171] VALUES (3634,'٣٦١٥ - (روي أنه - صلى الله عليه وسلم - مر في أصحابه بعشار من النوق حفل وهي) النوق (الحوامل)','2235','3564','0');
INSERT INTO [t38171] VALUES (3635,'٣٦١٦ - (روي عن مسروق) بن الأجدع الهمداني التابعي الكوفي (عن عائشة رضي الله عنها قالت قلت يا رسول الله ألا تستطعم الله فيطعمك قالت وبكيت لما رأيت به من الجوع فقال يا عائشة والذي نفسي بيده لو سألت ربي أن يجري معي جبال الدنيا ذهبا لأجراها حيث شئت من الأرض ولكن اخترت جوع الدنيا على شبعها وفقر الدنيا على غناها وحزن الدنيا على فرحها يا عائشة إن الدنيا لا تنبغي لمحمد ولا لآل محمد يا عائشة إن الله لم يرض لأولي العزم من الرسل إلا الصبر على مكروه الدنيا والصبر عن محبوبها ثم لم يرض لي إلا أن يكلفني ما كلفهم فقال فاصبر كما صبر أولوا العزم من الرسل والله ما لي بد من طاعته وإني والله لأصبرن كما صبروا بجهدي ولا قوة إلا بالله).','2237','3564','0');
INSERT INTO [t38171] VALUES (3636,'٣٦١٧ - (روي عن عمر) بن الخطاب (رضي الله عنه أنه حين فتح عليه الفتوحات قالت له ابنته حفصة رضي الله عنها) يا أبت (البس لين الثياب إذا وفدت عليك الوفود من الآفات ومر بصنعة طعام تطعمه) أي تأكله (وتطعم من حضر) منهم (قال عمر يا حفصة ألست تعلمين أن أعلم الناس بحال الرجل أهل بيته فقالت بلى قال ناشدتك الله هل تعلمين أن رسول الله - صلى الله عليه وسلم - لبث في النبوة','2237','3564','0');
INSERT INTO [t38171] VALUES (3637,'٣٦١٨ - (وفي بعض الروايات زيادة من قول عمر) رضي الله عنه (وهو أنه قال كان لي صاحبان سلكا طريقا فإن سلكت غير طريقتهما سلك بي طريق غير طريقهما وإني والله سأصبر على عيشهما الشديد لعلي أدرك معهما العيش الرغيد)','2239','3564','0');
INSERT INTO [t38171] VALUES (3638,'٣٦١٩ - (عن أبي سعيد الخدري) رضي الله عنه (عن النبي - صلى الله عليه وسلم - أنه قال لقد كان الأنبياء قبلي يبتلى أحدهم بالفقر فلا يلبس إلا العباء وإن كان أحدهم ليبتلى بالقمل حتى يقتله القمل وكان أحب إليهم من العطاء إليكم).','2240','3564','0');
INSERT INTO [t38171] VALUES (3639,'٣٦٢٠ - (عن ابن عباس) رضي الله عنه (عن النبي - صلى الله عليه وسلم - قال لما ورد موسى عليه السلام ماء مدين كانت خضرة البقل ترى في بطنه','2240','3564','0');
INSERT INTO [t38171] VALUES (3640,'٣٦٢١ - (في حديث عمر رضي الله عنه أنه لما نزل قوله تعالى والذين يكنزون الذهب والفضة ولا ينفقونها في سبيل الله قال - صلى الله عليه وسلم - تبا للدنيا تبا للدينار والدرهم فقلنا نهانا الله عن كنز الذهب والفضة فأي شيء ندخر فقال - صلى الله عليه وسلم - ليتخذ أحدكم لسانا ذاكر وقلبا شاكرا وزوجة صالحة تعينه على أمر آخرته)','2241','3564','0');
INSERT INTO [t38171] VALUES (3641,'٣٦٢٢ - (في حديث حذيفة) بن اليمان رضي الله عنه (عن رسول الله - صلى الله عليه وسلم -) قال (من آثر الدنيا على الآخرة ابتلاه الله بثلاث هما لا يفارق قلبه أبدا وفقرا لا يستغني به أبدا وحرصا لا يشبع أبدا)','2241','3564','0');
INSERT INTO [t38171] VALUES (3642,'٣٦٢٣ - (قال - صلى الله عليه وسلم - لا يستكمل العبد الإيمان حتى يكون أن لا يعرف أحب إليه من أن يعرف وحتى يكون قلة الشيء أحب إليه من كثرته) قال صاحب القوت رويناه مرسلا عن علي بن معبد عن علي بن أبي طلحة قال قال رسول الله - صلى الله عليه وسلم - فساقه.','2242','3564','0');
INSERT INTO [t38171] VALUES (3643,'٣٦٢٤ - (وعن ابن عباس) رضي الله عنه (قال خرج رسول الله - صلى الله عليه وسلم - ذات يوم يمشي وجبريل معه فصعد على الصفا فقال له النبي - صلى الله عليه وسلم - والذي بعثك بالحق ما أمسى لآل محمد كف سويق ولا سفة دقيق فلم يكن كلامه بأسرع من أن سمع هدة من السماء أفظعته فقال رسول الله - صلى الله عليه وسلم - أمر الله القيامة أن تقوم قال لا ولكن هذا إسرافيل عليه السلام قد نزل إليك حين سمع كلامك فأتاه إسرافيل فقال إن الله عز وجل سمع ما ذكرت فبعثني بمفاتيح الأرض وأمرني أن أعرض عليك إن أحببت أن تسير معك جبال تهامة زمردا وياقوتا وذهبا وفضة وإن شئت نبيا ملكا وإن شئت نبيا عبدا) فرفع رأسه إلى جبريل كأنه يستشيره (فأوحى إليه جبريل أن تواضع لله فقال) بل (نبيا عبدا ثلاثا)','2242','3564','0');
INSERT INTO [t38171] VALUES (3644,'٣٦٢٤/أ - (وقال - صلى الله عليه وسلم - إذا أراد الله بعبد خيرا زهده في الدنيا رغبه','2242','3643','0');
INSERT INTO [t38171] VALUES (3645,'٣٦٢٥ - (وقال - صلى الله عليه وسلم - ازهد في الدنيا يحبك الله وازهد فيما في أيدي الناس يحبك الناس)','2243','3564','0');
INSERT INTO [t38171] VALUES (3646,'٣٦٢٦ - (وقال - صلى الله عليه وسلم - من أراد أن يؤتيه الله علما بغير تعلم وهدى بغير هداية فليزهد في الدنيا).','2243','3564','0');
INSERT INTO [t38171] VALUES (3647,'٣٦٢٧ - (قال - صلى الله عليه وسلم - من اشتاق إلى الجنة سارع إلى الخيرات ومن خاف من النار لها عن الشهوات ومن ترقب الموت ترك اللذات','2243','3564','0');
INSERT INTO [t38171] VALUES (3648,'٣٦٢٨ - (يروى عن نبينا وعن المسيح صلى الله عليهما وسلم أربع لا يدركن إلا بعجب الصمت وهو أول العبادة والتواضع وكثرة الذكر وقلة الشيء).','2244','3564','0');
INSERT INTO [t38171] VALUES (3649,'٣٦٢٩ - (وقال ابن مسعود) رضي الله عنه (ركعتان من زاهد قلبه خير له وأحب إلى الله من عبادة المتعبدين المجتهدين إلى آخر الدهر أبدا سرمدا)','2244','3564','0');
INSERT INTO [t38171] VALUES (3650,'٣٦٣٠ - (وفي الخبر إن الرجل ليوقف في الحساب حتى لو وردت مائة من الإبل عطاشا) من الحمض (على عرقه لصدرت رواء).','2245','3564','0');
INSERT INTO [t38171] VALUES (3651,'٣٦٣١ - (قالت عائشة رضي الله عنها كانت تأتي علينا أربعون ليلة وما يوقد في بيت رسول الله - صلى الله عليه وسلم - مصباح ولا نار قيل لها فبم كنتم تعيشون قالت بالأسودين التمر والماء)','2245','3564','0');
INSERT INTO [t38171] VALUES (3652,'٣٦٣٢ - (قال الحسن) البصري رحمه الله تعالى (كان رسول الله - صلى الله عليه وسلم - يركب الحمار ويلبس الصوف وينتعل المخصوف ويلعق أصابعه ويأكل على الأرض ويقول إنما أنا عبد آكل كما يأكل العبيد وأجلس كما تجلس العبيد).','2246','3564','0');
INSERT INTO [t38171] VALUES (3653,'٣٦٣٣ - (وعن سنان بن سعد) هكذا في سائر النسخ والصواب سهل بن سعد كما نبه عليه العراقي وليس في الصحابة من اسمه سنان بن سعد (قال','2246','3564','0');
INSERT INTO [t38171] VALUES (3654,'٣٦٣٤ - (وعن جابر) رضي الله عنه (قال دخل رسول الله - صلى الله عليه وسلم - على فاطمة رضي الله عنها وهي تطحن بالرحا وعليها كساء من خملة الإبل فلما نظر إليها بكى وقال يا فاطمة تجرعي مرارة الدنيا لنعيم الأبد فأنزل عليه ولسوف يعطيك ربك فترضى).','2247','3564','0');
INSERT INTO [t38171] VALUES (3655,'٣٦٣٥ - (قال - صلى الله عليه وسلم - إن من خيار أمتي فيما أنبأني الملأ الأعلى قوما يضحكون جهرا من سعة رحمة الله ويبكون سرا من خوف عذابه مؤنتهم على الناس خفيفة وعلى أنفسهم ثقيلة يلبسون الخلقان ويتبعون الرهبان أجسامهم في الأرض)','2247','3564','0');
INSERT INTO [t38171] VALUES (3656,'٣٦٣٦ - (وفي الخبر البذاذة من الإيمان)','2248','3564','0');
INSERT INTO [t38171] VALUES (3657,'٣٦٣٧ - (في الخبر من ترك ثوب جمال وهو يقدر عليه تواضعا لله تعالى)','2248','3564','0');
INSERT INTO [t38171] VALUES (3658,'٣٦٣٨ - (قال عمر رضي الله عنه اخشوشنوا وإياكم وزي العجم كسرى وقيصر)','2249','3564','0');
INSERT INTO [t38171] VALUES (3659,'٣٦٣٩ - (قال على رضي الله عنه من تزيا بزي قوم فهو منهم)','2250','3564','0');
INSERT INTO [t38171] VALUES (3660,'٣٦٤٠ - (قال - صلى الله عليه وسلم - إن من شرار أمتي الذين غذوا بالنعيم يطلبون ألوان الطعام وألوان الثياب ويتشدقون بالكلام).','2251','3564','0');
INSERT INTO [t38171] VALUES (3661,'٣٦٤١ - (قال - صلى الله عليه وسلم - إزرة المؤمن إلى أنصاف ساقيه ولا جناح عليه فيما بينه وبين الكعبين وما أسفل من ذلك ففي النار ولا ينظر الله يوم القيامة إلى من جر إزاره بطرا).','2251','3564','0');
INSERT INTO [t38171] VALUES (3662,'٣٦٤٢ - (قال أبو سليمان) الداراني رحمه الله تعالى (قال رسول الله - صلى الله عليه وسلم - لا يلبس الشعر من أمتي إلا مراء أو أحمق).','2252','3564','0');
INSERT INTO [t38171] VALUES (3663,'٣٦٤٣ - (جاء في الخبر يأتي على الناس زمان يوشون) أي يزينون (ثيابهم)','2252','3564','0');
INSERT INTO [t38171] VALUES (3664,'٣٦٤٤ - (أمر رسول الله - صلى الله عليه وسلم -) عمه (العباس) بن عبد المطلب رضي الله عنه (أن يهدم عليته)','2252','3564','0');
INSERT INTO [t38171] VALUES (3665,'٣٦٤٥ - (مر - صلى الله عليه وسلم -) يوما (بجنبذة معلاة) أي قبة مرتفعة (فقال لمن هذه قالوا لفلان) وسموا رجلا من أصحابه (فلما جاءه الرجل أعرض عنه فلم يكن يقبل عليه كما كان)','2253','3564','0');
INSERT INTO [t38171] VALUES (3666,'٣٦٤٦ - (قال الحسن) البصري رحمه الله تعالى (مات رسول الله - صلى الله عليه وسلم - ولم يضع لبنة على لبنة ولا قصبة على قصبة).','2253','3564','0');
INSERT INTO [t38171] VALUES (3667,'٣٦٤٧ - (قال) - صلى الله عليه وسلم - (إذا أراد الله بعبد شرا أهلك ماله فى الماء والطين).','2254','3564','0');
INSERT INTO [t38171] VALUES (3668,'٣٦٤٨ - (قال عبد الله بن عمرو بن العاص) رضي الله عنهما (مر علينا رسول الله - صلى الله عليه وسلم - ونحن نعالج خصا فقال ما هذا قلنا خص لنا قد وهي قال أرى الأمر أعجل من ذلك).','2254','3564','0');
INSERT INTO [t38171] VALUES (3669,'٣٦٤٩ - (وقال - صلى الله عليه وسلم - من بنى فوق ما يكفيه كلف أن يحمله يوم القيامة).','2254','3564','0');
INSERT INTO [t38171] VALUES (3670,'٣٦٥٠ - (في الخبر كل نفقة) ينفقها (العبد يؤجر عليها إلا ما','2254','3564','0');
INSERT INTO [t38171] VALUES (3671,'٣٦٥١ - (قال - صلى الله عليه وسلم - كل بناء وبال على صاحبه يوم القيامة إلا ما أكن من حر وبرد)','2255','3564','0');
INSERT INTO [t38171] VALUES (3672,'٣٦٥٢ - (وقال - صلى الله عليه وسلم - للرجل الذي شكا إليه ضيق منزله اتسع في السماء) قال المصنف (أي في الجنة).','2255','3564','0');
INSERT INTO [t38171] VALUES (3673,'٣٦٥٣ - (قالت عائشة رضي الله عنها كان ضجاع رسول الله - صلى الله عليه وسلم - الذي ينام عليه وسادة من أدم) أي جلد مدبوغ (حشوها ليف) النخل.','2255','3564','0');
INSERT INTO [t38171] VALUES (3674,'٣٦٥٤ - (قال الفضيل) بن عياض رحمة الله تعالى (ما كان فراش رسول الله - صلى الله عليه وسلم - إلا عباءة مثنية ووسادة من أدم حشوها ليف).','2256','3564','0');
INSERT INTO [t38171] VALUES (3675,'٣٦٥٥ - (روي أن عمر بن الخطاب رضي الله عنه دخل على رسول الله - صلى الله عليه وسلم - وهو نائم على سرير) من جريد (مرمول)','2256','3564','0');
INSERT INTO [t38171] VALUES (3676,'٣٦٥٦ - (وقدم رسول الله - صلى الله عليه وسلم - من سفر فدخل على فاطمة رضى الله عنها)','2257','3564','0');
INSERT INTO [t38171] VALUES (3677,'٣٦٥٧ - (رأى رسول الله - صلى الله عليه وسلم - على باب عائشة) رضي الله عنها (سترا فهتكه وقال كلما رأته ذكرت الدنيا أرسل به إلى آل فلان)','2258','3564','0');
INSERT INTO [t38171] VALUES (3678,'٣٦٥٨ - (فرشت له عائشة) رضي الله عنها (ذات ليلة فراشا جديدا وقد كان - صلى الله عليه وسلم - ينام على عباءة مثنية فما زال يتقلب ليلته فلما أصبح قال لها أعيدي العباءة الخلقة ونحى هذا الفراش عني قد أسهرني الليلة)','2258','3564','0');
INSERT INTO [t38171] VALUES (3679,'٣٦٥٩ - (أتته دنانير ستة أو خمسة ليلا فبيتها فسهر ليلته حتى أخرجها من آخر الليل قالت عائشة)','2258','3564','0');
INSERT INTO [t38171] VALUES (3680,'٣٦٦٠ - (قال بعضهم ما من يوم ذر شارقه) أي طلعت شمسه (إلا وأربعة أملاك ينادون في الآفاق بأربعة أصوات ملكان بالمشرق وملكان بالمغرب يقول أحدهم بالمشرق يا باغي الخير)','2259','3564','0');
INSERT INTO [t38171] VALUES (3681,'كتاب التوحيد والتوكل','2261','3303','0');
INSERT INTO [t38171] VALUES (3682,'٣٦٦١ - (قال - صلى الله عليه وسلم - فيما رواه عنه ابن مسعود)','2262','3681','0');
INSERT INTO [t38171] VALUES (3683,'٣٦٦٢ - (وقال - صلى الله عليه وسلم - لو أنكم تتوكلون على الله حق توكله لرزقكم كما يرزق الطير تغدو خماصا وتروح بطانا).','2264','3681','0');
INSERT INTO [t38171] VALUES (3684,'٣٦٦٣ - (وقال - صلى الله عليه وسلم - من انقطع إلى الله عز وجل) بأن كان اعتماده عليه لا على الأسباب (كفاه الله تعالى كل مؤنة) أي مشقة (ورزقه من حيث لا يحتسب ومن انقطع إلى الدنيا وكله الله إليها).','2264','3681','0');
INSERT INTO [t38171] VALUES (3685,'٣٦٦٤ - (وقال - صلى الله عليه وسلم - من سره أن يكون عند الله أغنى الناس فليكن بما عند الله أوثق منه بما في يده).','2264','3681','0');
INSERT INTO [t38171] VALUES (3686,'٣٦٦٥ - (يروى عن رسول الله - صلى الله عليه وسلم - أنه) كان (إذا أصاب أهله خصاصة) أي فقر وحاجة (قال قوموا إلى الصلاة و) كان (يقول بهذا أمرني رب قال عز وجل وأمر أهلك بالصلاة واصطبر عليها).','2265','3681','0');
INSERT INTO [t38171] VALUES (3687,'٣٦٦٦ - (قال - صلى الله عليه وسلم - لم يتوكل على الله من استرقى واكتوى).','2265','3681','0');
INSERT INTO [t38171] VALUES (3688,'٣٦٦٧ - (روي أنه لما قال جبريل لإبراهيم عليهما السلام وقد رمي إلى النار بالمنجنيق ألك حاجة قال أما إليك فلا وفاء بقوله حسبي الله ونعم الوكيل إذ قال ذلك حين أخذ ليرمى)','2266','3681','0');
INSERT INTO [t38171] VALUES (3689,'٣٦٦٨ - (أوحى الله تعالى إلى داود عليه السلام يا داود ما من عبد يعتصم بي دون خلقي فتكيده السماوات والأرض إلا جعلت له مخرجا)','2266','3681','0');
INSERT INTO [t38171] VALUES (3690,'٣٦٦٩ - (نهى عن إفشاء سر القدر).','2266','3681','0');
INSERT INTO [t38171] VALUES (3691,'٣٦٧٠ - (قال) - صلى الله عليه وسلم - (إذا ذكر النجوم فأمسكوا وإذا ذكر القدر فأمسكوا وإذا ذكر أصحابي فأمسكوا)','2267','3681','0');
INSERT INTO [t38171] VALUES (3692,'٣٦٧١ - (قول رسول الله - صلى الله عليه وسلم - في عيسى عليه السلام لو ازداد يقينا لمشى على الهواء لما قيل له أنه كان يمشي على الماء)','2267','3681','0');
INSERT INTO [t38171] VALUES (3693,'٣٦٧٢ - (أصحاب سيد الأنبياء) - صلى الله عليه وسلم - (كالنجوم) المشرقة في السماء (بأيهم) أيها المسافرون في سلوك طريق الحق (اقتديتم اهتديتم)','2267','3681','0');
INSERT INTO [t38171] VALUES (3694,'٣٦٧٣ - (قال رسول الله - صلى الله عليه وسلم -) في جعل الله تعالى بحكمته وعزته عن مباشرة الأشياء بنفسه للخلقة والحياة واسطة وذلك (في وصف ملك الأرحام أنه يدخل الرحم فيأخذ النطفة في يده ثم يصورها جسدا','2267','3681','0');
INSERT INTO [t38171] VALUES (3695,'٣٦٧٤ - (في الخبر أن ملكي الموت والحياة تناظرا فقال ملك الموت أنا أميت الأحياء وقال ملك الحياة أنا أحي الموتى فأوحى الله تعالى إليهما كونا على عملكما وما سخرتكما له من الصنع فأنا المحي و) أنا (المميت لا يميت ولا يحي سواي)','2268','3681','0');
INSERT INTO [t38171] VALUES (3696,'٣٦٧٥ - (قال - صلى الله عليه وسلم - للذي ناوله التمرة خذها لو لم تأتها لأتتك).','2269','3681','0');
INSERT INTO [t38171] VALUES (3697,'٣٦٧٦ - (رسول الله - صلى الله عليه وسلم - فقال أصدق بيت قاله شاعر)','2269','3681','0');
INSERT INTO [t38171] VALUES (3698,'٣٦٧٧ - (قال - صلى الله عليه وسلم - من اعتز بالعبيد أذله الله تعالى).','2269','3681','0');
INSERT INTO [t38171] VALUES (3699,'٣٦٧٨ - (وقال - صلى الله عليه وسلم - لو توكلتم على الله حق توكله لرزقتم كما ترزق الطير تغدو خماصا وتروح بطانا ولزالت بدعائكم الجبال)','2270','3681','0');
INSERT INTO [t38171] VALUES (3700,'٣٦٧٩ - (في الخبر إن العبد ليهم من الليل بأمر من أمور) الدنيا من (التجارة) وغيرها (مما لو فعله لكان فيه هلاكه فينظر الله تعالى إليه من فوق عرشه فيصرفه عنه فيصبح كئيبا حزينا فيظن)','2270','3681','0');
INSERT INTO [t38171] VALUES (3701,'٣٦٨٠ - (قال - صلى الله عليه وسلم - إن الله خمر طينة آدم بيده أربعين صباحا).','2271','3681','0');
INSERT INTO [t38171] VALUES (3702,'٣٦٨١ - (روي في الفقير الذي أمر النبي - صلى الله عليه وسلم - عليا وأسامة) رضي الله عنهما (أن يغسلاه فغسلاه وكفناه ببردته فلما دفنه قال لأصحابه إنه يبعث يوم القيامة وجهه كالقمر ليلة البدر ولولا','2271','3681','0');
INSERT INTO [t38171] VALUES (3703,'٣٦٨٢ - (ونهى بلالا) رضي الله عنه (عن الادخار في كسرة خبز) كان (ادخرها ليفطر عليها).','2272','3681','0');
INSERT INTO [t38171] VALUES (3704,'٣٦٨٣ - (قال له إذا سئلت فلا تمنع وإذا أعطيت فلا تخبأ).','2273','3681','0');
INSERT INTO [t38171] VALUES (3705,'٣٦٨٤ - (كان إذا بال تيمم مع قرب الماء ويقول ما يدريني لعلى لا أبلغه).','2273','3681','0');
INSERT INTO [t38171] VALUES (3706,'٣٦٨٥ - (روى أبو أمامة) صدي بن عجلان (الباهلي) رضي الله عنه (أن بعض أصحاب الصفة توفي فما وجد له كفن فقال - صلى الله عليه وسلم - فتشوا ثوبه) ففتشوه (فوجدوا فيه دينارين في داخل إزاره فقال - صلى الله عليه وسلم - كيتان).','2273','3681','0');
INSERT INTO [t38171] VALUES (3707,'٣٦٨٦ - (اعقلها وتوكل).','2273','3681','0');
INSERT INTO [t38171] VALUES (3708,'٣٦٨٧ - (روي عن رسول الله - صلى الله عليه وسلم - فيمن ترك العزل فأقر النطفة قرارها)','2274','3681','0');
INSERT INTO [t38171] VALUES (3709,'٣٦٨٨ - (في الخبر) قال - صلى الله عليه وسلم - (من دعا على ظالمه فقد انتصر)','2275','3681','0');
INSERT INTO [t38171] VALUES (3710,'٣٦٨٩ - (قال - صلى الله عليه وسلم - ما من داء إلا وله دواء عرفه من عرفه وجهله من جهله إلا السام يعني الموت).','2275','3681','0');
INSERT INTO [t38171] VALUES (3711,'٣٦٩٠ - (وقال) - صلى الله عليه وسلم - (تداووا عباد الله فإن الله خلق الداء والدواء).','2276','3681','0');
INSERT INTO [t38171] VALUES (3712,'٣٦٩١ - (وسئل) - صلى الله عليه وسلم - (عن الدواء والرقى هل ترد من قدر الله شيئا فقال هي من قدر الله تعالى).','2276','3681','0');
INSERT INTO [t38171] VALUES (3713,'٣٦٩٢ - (في الخبر المشهور ما مررت) ليلة أسري بي (بملأ من الملائكة إلا قالوا يا محمد مر أمتك بالحجامة)','2277','3681','0');
INSERT INTO [t38171] VALUES (3714,'٣٦٩٣ - (في الحديث أنه أمر بها) أي بالحجامة قال احتجموا) إرشادا لا إلزاما (لسبع عشرة وتسع عشرة وإحدى وعشرين) من الشهر العربي (لا يتبيغ بكم الدم) أي يثور ويغلب (فيقتلكم) أي فيكون ثورانه سببا لموتكم وهذا فيه كمال شفقته على أمته.','2277','3681','0');
INSERT INTO [t38171] VALUES (3715,'٣٦٩٤ - (في خبر مقطوع من احتجم يوم الثلاثاء لسبع عشرة من الشهر) العربي (كان له دواء من داء سنة).','2278','3681','0');
INSERT INTO [t38171] VALUES (3716,'٣٦٩٥ - (أما أمره) - صلى الله عليه وسلم - (فقد أمر غير واحد من الصحابة بالتداوي وبالحمية)','2279','3681','0');
INSERT INTO [t38171] VALUES (3717,'٣٦٩٦ - (قطع) - صلى الله عليه وسلم - (لسعد بن معاذ) بن النعمان الأنصاري الأشهلي أبي عمرو سيد الأوس شهد بدرا (عرقا أي فصده)','2279','3681','0');
INSERT INTO [t38171] VALUES (3718,'٣٦٩٧ - (كوى) - صلى الله عليه وسلم - (أسعد بن زرارة)','2279','3681','0');
INSERT INTO [t38171] VALUES (3719,'٣٦٩٨ - (وقال) - صلى الله عليه وسلم - (لعلي) رضي الله عنه (وكان رمد العين لا تأكل من هذا يعني الرطب وكل من هذا فإنه أوفق لك يعني سلقا قد طبخ بدقيق شعير).','2280','3681','0');
INSERT INTO [t38171] VALUES (3720,'٣٦٩٩ - (روي في الحديث) المروي (من طريق أهل البيت أنه - صلى الله عليه وسلم - كان يكتحل كل ليلة ويحتجم كل شهر ويشرب الدواء كل سنة)','2281','3681','0');
INSERT INTO [t38171] VALUES (3721,'٣٧٠٠ - (وتداوى - صلى الله عليه وسلم - غير مرة من العقرب وغيرها)','2281','3681','0');
INSERT INTO [t38171] VALUES (3722,'٣٧٠١ - (روى أنه) - صلى الله عليه وسلم - (كان إذا نزل عليه الوحي صدع رأسه)','2282','3681','0');
INSERT INTO [t38171] VALUES (3723,'٣٧٠٢ - (في الخبر أنه) - صلى الله عليه وسلم - (كان إذا خرجت به قرحة جعل عليها حناء).','2282','3681','0');
INSERT INTO [t38171] VALUES (3724,'٣٧٠٣ - (جعل) - صلى الله عليه وسلم - (على قرحة خرجت به ترابا).','2282','3681','0');
INSERT INTO [t38171] VALUES (3725,'٣٧٠٤ - (نهى رسول الله - صلى الله عليه وسلم - عن الكي)','2283','3681','0');
INSERT INTO [t38171] VALUES (3726,'٣٧٠٥ - (دون الرقى)','2283','3681','0');
INSERT INTO [t38171] VALUES (3727,'٣٧٠٦ - (فقد قال - صلى الله عليه وسلم - نحن معاشر الأنبياء أشد الناس بلاء ثم الأمثل فالأمثل يبتلى العبد على قدر إيمانه فإن كان صلب الإيمان شدد عليه البلاء وإن كان في إيمانه ضعف خفف عنه البلاء).','2283','3681','0');
INSERT INTO [t38171] VALUES (3728,'٣٧٠٧ - (وفي الخبر أن الله تعالى يجرب عبده بالبلاء كما يجرب أحدكم ذهبه بالنار فمنهم من يخرج ذهبه كالإبريز ومنهم دون ذلك ومنهم من يخرج أسود محترقا).','2284','3681','0');
INSERT INTO [t38171] VALUES (3729,'٣٧٠٨ - (في حديث من طريق أهل البيت إن الله تعالى إذا أحب عبدا ابتلاه فإن صبر اجتباه فإن رضي اصطفاه)','2284','3681','0');
INSERT INTO [t38171] VALUES (3730,'٣٧٠٩ - (قال - صلى الله عليه وسلم - تحبون أن تكونوا كالحمر الصيالة) كذا في النسخ وهو في معجم البغوي الضالة من الضلال (لا تمرضون ولا تسقمون).','2285','3681','0');
INSERT INTO [t38171] VALUES (3731,'٣٧١٠ - (ففي الخبر أن الله تعالى يقول لملائكته اكتبوا لعبدي صالح ما كان يعمله) في صحته (فإنه في وثاقي) أي حبسي وقيدي (إن أطلقته) منه (أبدلته لحما خيرا من لحمه ودما خيرا من دمه) قيل لأنه قد طهر من المعاصي وكفر به عنه الخطايا (وإن توفيته توفيته إلى رحمتي)','2286','3681','0');
INSERT INTO [t38171] VALUES (3732,'٣٧١١ - (وقال - صلى الله عليه وسلم - أفضل الأعمال ما أكرهت عليه النفوس)','2286','3681','0');
INSERT INTO [t38171] VALUES (3733,'٣٧١٢ - (قال - صلى الله عليه وسلم - لا تزال الحمى والمليلة) قيل هي حرارة الحمى ووهجتها وقيل هي الحمى التي تكون في العظام (في العبد حتى يمشي على الأرض كالبردة وما عليه خطيئة).','2286','3681','0');
INSERT INTO [t38171] VALUES (3734,'٣٧١٣ - (في الخبر حمى يوم كفارة سنة).','2287','3681','0');
INSERT INTO [t38171] VALUES (3735,'٣٧١٤ - (لما ذكر رسول الله - صلى الله عليه وسلم - كفارة الذنوب بالحمى سأل زيد بن ثابت) رضي الله عنه (ربه عز وجل أن لا يزال محموما فلم تكن الحمى تفارقه حتى مات رحمه الله) نقله صاحب القوت قال ويقال أيضا أبي بن كعب (وسأل ذلك طائفة من الأنصار فكانت الحمى لا تزايلهم)','2288','3681','0');
INSERT INTO [t38171] VALUES (3736,'٣٧١٥ - (قال - صلى الله عليه وسلم - من أذهب الله كريمتيه) فصبر (لم يرض له ثوابا دون الجنة)','2289','3681','0');
INSERT INTO [t38171] VALUES (3737,'٣٧١٦ - (قال - صلى الله عليه وسلم - أكثروا من ذكر هادم اللذات)','2289','3681','0');
INSERT INTO [t38171] VALUES (3738,'٣٧٢٠ - (قيل الحمى رائد الموت) أي رسوله الذي يتقدمه وفي نسخة بريد الموت (فهي مذكر له ودافع للتسويف)','2289','3681','0');
INSERT INTO [t38171] VALUES (3739,'٣٧١٨ - (النبي - صلى الله عليه وسلم - عرض) وفي نسخة عرضت (عليه امرأة فحكى)','2290','3681','0');
INSERT INTO [t38171] VALUES (3740,'٣٧١٩ - (ذكر رسول الله - صلى الله عليه وسلم - الأمراض والأوجاع كالصداع وغيره فقال رجل وما الصداع)','2290','3681','0');
INSERT INTO [t38171] VALUES (3741,'٣٧٢٠ - (ذلك أنه ورد في الخبر الحمى حظ كل مؤمن من النار)','2291','3681','0');
INSERT INTO [t38171] VALUES (3742,'٣٧٢١ - (وفي حديث أنس وعائشة رضي الله عنهما قيل يا رسول الله هل يكون مع الشهداء يوم القيامة غيرهم فقال نعم من ذكر الموت في كل يوم عشرين مرة وفي لفظ) حديث (آخر الذي يذكر ذنوبه فتحزنه)','2292','3681','0');
INSERT INTO [t38171] VALUES (3743,'٣٧٢٢ - (روي عن عمر رضي الله عنه وعن الصحابة)','2292','3681','0');
INSERT INTO [t38171] VALUES (3744,'٣٧٢٣ - (والمؤمنون كالجسد الواحد إذا اشتكى منه عضو تداعى إليه سائر أعضائه)','2294','3681','0');
INSERT INTO [t38171] VALUES (3745,'٣٧٢٤ - (شبه الفرار من الطعون في بعض الأخبار بالفرار من الزحف لأن فيه كسرا لقلوب بقية المسلمين وسعيا في إهلاكهم).','2295','3681','0');
INSERT INTO [t38171] VALUES (3746,'٣٧٢٥ - (قال بعضهم من بث) أي أظهر ما بلى به (لم يصبر)','2295','3681','0');
INSERT INTO [t38171] VALUES (3747,'٣٧٢٦ - (وفي الخبر إذا مرض العبد أوحى الله تعالى إلى الملكين انظرا ما يقول لعواده، فإن حمد الله وأثنى عليه بخير دعوا له فإن شكا وذكر شرا قالا كذلك تكون)','2295','3681','0');
INSERT INTO [t38171] VALUES (3748,'كتاب المحبة والشوق والأنس والرضى','2297','3303','0');
INSERT INTO [t38171] VALUES (3749,'٣٧٢٧ - (جعل رسول الله - صلى الله عليه وسلم - الحب لله من شرط الإيمان)','2298','3748','0');
INSERT INTO [t38171] VALUES (3750,'٣٧٢٨ - (في حديث آخر لا يؤمن أحدكم حتى يكون الله ورسوله أحب إليه مما سواهما)','2298','3748','0');
INSERT INTO [t38171] VALUES (3751,'٣٧٢٩ - (كيف وقد قال تعالى قل إن كان آباؤكم وأبناؤكم وإخوانكم الآية)','2299','3748','0');
INSERT INTO [t38171] VALUES (3752,'٣٧٣٠ - (أمر رسول الله - صلى الله عليه وسلم - بالمحبة) فيما شرعه من الأحكام (فقال أحبوا الله لما يغذوكم به من نعمه وأحبوني بحب الله تعالى).','2300','3748','0');
INSERT INTO [t38171] VALUES (3753,'٣٧٣١ - (يروى أن رجلا قال يا رسول الله إني أحبك فقال - صلى الله عليه وسلم - استعد للفقر فقال إني أحب الله فقال استعد للبلاء)','2300','3748','0');
INSERT INTO [t38171] VALUES (3754,'٣٧٣٢ - (عن عمر رضي الله عنه قال نظر النبي - صلى الله عليه وسلم - إلى مصعب بن عمير)','2301','3748','0');
INSERT INTO [t38171] VALUES (3755,'٣٧٣٣ - (في الخبر المشهور أن إبراهيم عليه السلام قال لملك الموت إذ جاءه لقبض روحه هل رأيت خليلا يميت خليله فأوحى الله إليه هل رأيت محبا يكره لقاء حبيبه فقال يا ملك الموت الآن فاقبض)','2302','3748','0');
INSERT INTO [t38171] VALUES (3756,'٣٧٣٤ - (قال نبينا - صلى الله عليه وسلم - في دعائه اللهم ارزقني حبك وحب من أحبك وحب ما يقربني إلى حبك واجعل حبك أحب إلي من الماء البارد)','2302','3748','0');
INSERT INTO [t38171] VALUES (3757,'٣٧٣٥ - (جاء أعرابي إلى النبي - صلى الله عليه وسلم - فقال يا رسول الله متى الساعة قال ما أعددت لها قال ما أعددت لها كبير صلاة ولا صيام إلا إني أحب الله ورسوله فقال له رسول الله - صلى الله عليه وسلم - المرء مع من أحب قال أنس فما رأيت المسلمين فرحوا بشيء بعد الإسلام فرحهم بذلك).','2302','3748','0');
INSERT INTO [t38171] VALUES (3758,'٣٧٣٦ - (وقد) روى عن ابن مسعود موقوفا (جبلت القلوب على حب من أحسن إليها وبغض من أساء إليها)','2303','3748','0');
INSERT INTO [t38171] VALUES (3759,'٣٧٣٧ - (قال رسول الله - صلى الله عليه وسلم - اللهم لا تجعل لفاجر علي يدا فيحبه قلبي)','2304','3748','0');
INSERT INTO [t38171] VALUES (3760,'٣٧٣٨ - (كان رسول الله - صلى الله عليه وسلم - تعجبه الخضرة والماء الجاري).','2304','3748','0');
INSERT INTO [t38171] VALUES (3761,'٣٧٣٩ - (قال رسول الله - صلى الله عليه وسلم - إن الله جميل يحب الجمال).','2304','3748','0');
INSERT INTO [t38171] VALUES (3762,'٣٧٤٠ - سبحانك (لا أحصي ثناء عليك أنت كما أثنيت على نفسك)','2305','3748','0');
INSERT INTO [t38171] VALUES (3763,'٣٧٤١ - (وفي الخبر لا يكونن أحدكم كالأجير السوء إن لم يعط أجرا لم يعمل ولا كالعبد السوء إن لم يخف لم يعمل)','2305','3748','0');
INSERT INTO [t38171] VALUES (3764,'٣٧٤٢ - (قال الله تعالى لا يزال يتقرب العبد إلي بالنوافل حتى أحبه فإذا أحببته كنت سمعه الذي يسمع به وبصره الذي يبصر به ولسانه الذي ينطق به).','2305','3748','0');
INSERT INTO [t38171] VALUES (3765,'٣٧٤٣ - (والصحيح أن رسول الله - صلى الله عليه وسلم - ما رأى الله تعالى ليلة المعراج).','2306','3748','0');
INSERT INTO [t38171] VALUES (3766,'٣٧٤٤ - (وأقصاها في حق المؤمنين كما وردت به الأخبار سبعة آلاف).','2307','3748','0');
INSERT INTO [t38171] VALUES (3767,'٣٧٤٥ - (قال النبي - صلى الله عليه وسلم - إن الله يتجلى للناس عامة ولأبي بكر خاصة).','2308','3748','0');
INSERT INTO [t38171] VALUES (3768,'٣٧٤٦ - (لا يحشر المرء إلا على ما مات عليه)','2308','3748','0');
INSERT INTO [t38171] VALUES (3769,'٣٧٤٧ - (قال رسول الله - صلى الله عليه وسلم - أفضل السعادات طول العمر في طاعة الله).','2309','3748','0');
INSERT INTO [t38171] VALUES (3770,'٣٧٤٨ - (وقال - صلى الله عليه وسلم - أبغض إله عبد في الأرض الهوى)','2309','3748','0');
INSERT INTO [t38171] VALUES (3771,'٣٧٤٩ - قال - صلى الله عليه وسلم - من قال لا إله إلا الله مخلصا دخل الجنة)','2309','3748','0');
INSERT INTO [t38171] VALUES (3772,'(٣٧٥٠ - (قال رسول الله - صلى الله عليه وسلم - الأرض في البحر كالإصطبل في الأرض).','2310','3748','0');
INSERT INTO [t38171] VALUES (3773,'٣٧٥١ - (كان ينادي رسول الله - صلى الله عليه وسلم - ويقول إني ممسك بحجزكم عن النار وأنتم تتهافتون فيها تهافت الفراش).','2310','3748','0');
INSERT INTO [t38171] VALUES (3774,'٣٧٥٢ - (فمما اشتهر من دعاء رسول الله - صلى الله عليه وسلم - أنه كان يقول اللهم إني أسألك الرضا بعد القضاء وبرد العيش بعد الموت ولذة النظر إلى وجهك الكريم والشوق إلى لقائك)','2311','3748','0');
INSERT INTO [t38171] VALUES (3775,'٣٧٥٣ - (قال أبو الدرداء) رضي الله عنه (لكعب) الأحبار رحمه الله تعالى (أخبرني عن أخص آية يعني في التوراة فقال يقول عز وجل طال','2311','3748','0');
INSERT INTO [t38171] VALUES (3776,'٣٧٥٤ - (روى) إسماعيل بن أبي زياد عن أبان عن (أنس) رضي الله عنه (عن النبي - صلى الله عليه وسلم - أنه قال إذا أحب الله عبدا لم يضره ذنب والتائب من الذنب كمن لا ذنب له ثم تلا إن الله يحب التوابين)','2312','3748','0');
INSERT INTO [t38171] VALUES (3777,'٣٧٥٥ - (وقال - صلى الله عليه وسلم - إن الله تعالى يعطي الدنيا من يحب ومن لا يحب ولا يعطي الإيمان إلا من يحب).','2312','3748','0');
INSERT INTO [t38171] VALUES (3778,'٣٧٥٦ - (قال رسول الله - صلى الله عليه وسلم - من تواضع لله رفعه الله ومن تكبر وضعه الله ومن أكثر ذكر الله أحبه الله).','2313','3748','0');
INSERT INTO [t38171] VALUES (3779,'٣٧٥٧ - (قال - صلى الله عليه وسلم - قال الله تعالى لا يزال العبد يتقرب إلي بالنوافل حتى أحبه فإذا أحببته كنت سمعه الذي يسمع به وبصره الذي يبصر به الحديث)','2313','3748','0');
INSERT INTO [t38171] VALUES (3780,'٣٧٥٨ - (وفي الخبر إذا أحب الله عبدا ابتلاه فإن صبر اجتباه فإن رضى اصطفاه)','2314','3748','0');
INSERT INTO [t38171] VALUES (3781,'٣٧٥٩ - (وقد قال رسول الله - صلى الله عليه وسلم - إذا أحب الله عبدا جعل له واعظا من نفسه وزاجرا من قلبه يأمره وينهاه).','2314','3748','0');
INSERT INTO [t38171] VALUES (3782,'٣٧٦٠ - (قد قال) - صلى الله عليه وسلم - (إذا أراد الله بعبد خيرا بصره بعيب نفسه).','2314','3748','0');
INSERT INTO [t38171] VALUES (3783,'٣٧٦١ - (قال - صلى الله عليه وسلم - من أحب لقاء الله أحب الله لقاءه)','2314','3748','0');
INSERT INTO [t38171] VALUES (3784,'٣٧٦٢ - (قال النبي - صلى الله عليه وسلم - لا يتمنين أحدكم الموت فقال إنما قاله لضر نزل به)','2315','3748','0');
INSERT INTO [t38171] VALUES (3785,'٣٧٦٣ - (روي أن أبا حذيفة) هشم وقيل هشيم وقيل هاشم وقيل قيس (بن عتبة بن ربيعة بن عبد شمس)','2315','3748','0');
INSERT INTO [t38171] VALUES (3786,'٣٧٦٤ - (قال رسول الله - صلى الله عليه وسلم - أكثر أهل الجنة البله وعليون لذوي الألباب).','2317','3748','0');
INSERT INTO [t38171] VALUES (3787,'٣٧٦٥ - (قال رسول الله - صلى الله عليه وسلم - من استوى يوماه فهو مغبون ومن كان يومه شرا من أمسه فهو ملعون).','2317','3748','0');
INSERT INTO [t38171] VALUES (3788,'٣٧٦٦ - (قال - صلى الله عليه وسلم - إنه ليغان على قلبي في اليوم والليلة حتى أستغفر الله سبعين مرة)','2318','3748','0');
INSERT INTO [t38171] VALUES (3789,'٣٧٦٧ - (فقال من قرأ سورة الإخلاص فقد قرأ ثلث القرآن).','2318','3748','0');
INSERT INTO [t38171] VALUES (3790,'٣٧٦٨ - (في الحديث إن الله تعالى يتجلى للمؤمنين فيقول سلوني فيقولون رضاك).','2319','3748','0');
INSERT INTO [t38171] VALUES (3791,'٣٧٦٩ - (وقال - صلى الله عليه وسلم - من رضى من الله تعالى بالقليل من الرزق رضي الله تعالى منه بالقليل من العمل).','2320','3748','0');
INSERT INTO [t38171] VALUES (3792,'٣٧٧٠ - (قال) - صلى الله عليه وسلم - (أيضا إذا أحب الله عبدا ابتلاه فإن صبر اجتباه وإن رضي اصطفاه)','2320','3748','0');
INSERT INTO [t38171] VALUES (3793,'٣٧٧١ - (قال) - صلى الله عليه وسلم - (أيضا إذا كان يوم القيامة أنبت الله لطائفة من أمتي أجنحة فيطيرون من قبورهم إلى الجنان يسرحون فيها ويتنعمون كيف شاؤا فتقول لهم الملائكة هل رأيتم الحساب فيقولون ما رأينا حسابا فيقولون هل جزتم الصراط فيقولون: لا. فتقول لهم هل رأيتم جهنم فيقولون ما رأينا شيئا فتقول الملائكة من أمة من أنتم فيقولون: من أمة محمد - صلى الله عليه وسلم - فيقولون نشدناكم الله حدثونا ما كانت أعمالكم في الدنيا فيقولون خصلتان كانت فينا فبلغنا الله تعالى هذه المنزلة بفضل رحمته فيقولون وما هما فيقولون كنا إذا خلونا نستحي أن نعصيه ونرضى باليسير مما قسم لنا فتقول الملائكة يحق لكم هذا) نقله صاحب القوت فقال وروينا حديثا حسنا عن حماد بن سلمة عن ثابت البناني عن أنس عن النبي - صلى الله عليه وسلم - قال إذا كان يوم القيامة فساقه.','2321','3748','0');
INSERT INTO [t38171] VALUES (3794,'٣٧٧٢ - (وقال - صلى الله عليه وسلم - أعطوا الله الرضا من قلوبكم تظفروا بثواب فقركم وإلا فلا)','2322','3748','0');
INSERT INTO [t38171] VALUES (3795,'٣٧٧٣ - (روي عن نبينا - صلى الله عليه وسلم - أنه قال من أحب أن يعلم ما له عن الله عز وجل فلينظر ما لله عز وجل عنده فإن الله تعالى ينزل العبد من حيث أنزله العبد من نفسه)','2322','3748','0');
INSERT INTO [t38171] VALUES (3796,'٣٧٧٤ - (الله تعالى قال أنا الله لا إله إلا أنا من لم يصبر على بلائي ولم يشكر نعمائي ولم يرض بقضائي فليتخذ ربا سوائي).','2322','3748','0');
INSERT INTO [t38171] VALUES (3797,'٣٧٧٥ - (قوله تعالى فيما أخبر عنه نبينا - صلى الله عليه وسلم - أنه قال تعالى قدرت المقادير ودبرت التدبير وأحكمت الصنع فمن رضي فله الرضا مني حتى يلقاني ومن سخط فله السخط مني حتى يلقاني)','2323','3748','0');
INSERT INTO [t38171] VALUES (3798,'٣٧٧٦ - (وفي الخبر المشهور يقول الله تعالى خلقت الخير والشر فطوبى لمن خلقته للخير وأجريت الخير على يديه وويل لمن خلقته للشر وأجريت الشر على يديه وويل ثم ويل لمن قال لم وكيف)','2324','3748','0');
INSERT INTO [t38171] VALUES (3799,'٣٧٧٧ - (وفي الحديث الدال على الخير كفاعله)','2324','3748','0');
INSERT INTO [t38171] VALUES (3800,'٣٧٧٨ - (وفي الخبر لو أن عبدا قتل بالمشرق ورضي بقتله آخر بالمغرب كان شريكا في قتله)','2324','3748','0');
INSERT INTO [t38171] VALUES (3801,'٣٧٧٩ - (في الخبر إن الله تعالى أخذ الميثاق على كل مؤمن أن يبغض كل منافق وعلى كل منافق أن يبغض كل مؤمن)','2325','3748','0');
INSERT INTO [t38171] VALUES (3802,'٣٧٨٠ - (وقال) - صلى الله عليه وسلم - (من أحب قوما ووالاهم حشر معهم يوم القيامة).','2325','3748','0');
INSERT INTO [t38171] VALUES (3803,'٣٧٨١ - (وقد قيل قسم الخير عشرة أجزاء فتسعة أعشاره بالشام وعشرة بالعراق وقسم الشر عشرة أجزاء على العكس من ذلك)','2325','3748','0');
INSERT INTO [t38171] VALUES (3804,'٣٧٨٢ - (قال النبي - صلى الله عليه وسلم - لا يستكمل العبد الإيمان حتى يكون قلة الشيء أحب إليه من كثرته وحتى يكون أن لا يعرف أحب إليه من أن يعرف).','2326','3748','0');
INSERT INTO [t38171] VALUES (3805,'٣٧٨٣ - (وقال - صلى الله عليه وسلم - ثلاث من كن فيه استكمل إيمانه لا يخاف في الله لومة لائم ولا يرائي بشيء من عمله وإذا عرض له أمران أحدهما للدنيا والآخر للآخرة آثر أمر الآخرة على أمر الدنيا).','2326','3748','0');
INSERT INTO [t38171] VALUES (3806,'٣٧٨٤ - (قال - صلى الله عليه وسلم - لا يكمل إيمان العبد حتى تكون فيه ثلاث خصال إذا غضب لم يخرجه غضبه من الحق وإذا رضي لم يدخله رضاه في الباطل وإذا قدر لم يتناول ما ليس له).','2327','3748','0');
INSERT INTO [t38171] VALUES (3807,'٣٧٨٥ - (في حديث آخر) قال - صلى الله عليه وسلم - (ثلاث من أوتيهن فقد أوتي مثل ما أوتي آل داود العدل في الرضا والغضب والقصد في الغنى والفقر وخشية الله في السر والعلانية).','2327','3748','0');
INSERT INTO [t38171] VALUES (3808,'٣٧٨٦ - (قال - صلى الله عليه وسلم - للصديق رضي الله عنه إن الله تعالى قد أعطاك مثل كل من آمن من أمتي وأعطاني مثل إيمان كل من آمن به من ولد آدم).','2327','3748','0');
INSERT INTO [t38171] VALUES (3809,'٣٧٨٧ - (وفي حديث آخر إن لله تعالى ثلاثمائة خلق من لقيه بخلق منها مع التوحيد دخل الجنة فقال أبو بكر يا رسول الله هل في خلق منها فقال كلها فيك يا أبا بكر وأحبها إلى الله السخاء).','2328','3748','0');
INSERT INTO [t38171] VALUES (3810,'٣٧٨٨ - (قال - صلى الله عليه وسلم - رأيت ميزانا دلي من السماء فوضعت في كفة ووضعت أمتي في كفة فرجحت بهم ووضع أبو بكر في كفة وجيء بأمتي فوضعت في كفة فرجح بهم).','2329','3748','0');
INSERT INTO [t38171] VALUES (3811,'٣٧٨٩ - (قال لو كنت متخذا من الناس خليلا لاتخذت أبا بكر خليلا ولكن صاحبكم خليل الله يعني نفسه).','2330','3748','0');
INSERT INTO [t38171] VALUES (3812,'٣٧٩٠ - (وعن علي بن أبي طالب) رضي الله عنه (قال سألت رسول الله - صلى الله عليه وسلم - عن سنته فقال المعرفة رأس مالي والعقل أصل ديني والحب أساسي والشوق مركبي وذكر الله أنيسي والثقة) بالله تعالى (كنزي والحزن رفيقي والعلم سلاحي والصبر دوائي والرضا) بالله تعالى (غنيمتي والعجز فخري والزهد حرفتي واليقين قوتي والصدق شفيعي والطاعة حسبي والجهاد خلقي وقرة عيني في الصلاة).','2331','3748','0');
INSERT INTO [t38171] VALUES (3813,'كتاب النية الإخلاص والصدق','2333','3303','0');
INSERT INTO [t38171] VALUES (3814,'٣٧٩١ - جاء في الحديث القدسي قال روى ابن جرير والبزار من حديث أبي هريرة قال الله عز وجل من عمل لي عملا أشرك فيه غيري فهو له كله وأنا أغنى الشركاء عن الشرك.','2334','3813','0');
INSERT INTO [t38171] VALUES (3815,'٣٧٩٢ - قال الطبراني: حدثنا علي بن عبد العزيز حدثنا أبو حذيفة حدثنا سفيان الثوري عن المقدام بن شريح عن أبيه عن سعد بن أبي وقاص قال نزلت هذه الآية في ستة من أصحاب النبي - صلى الله عليه وسلم - منهم ابن مسعود قال كنا نستبق إلى النبي - صلى الله عليه وسلم','2334','3813','0');
INSERT INTO [t38171] VALUES (3816,'٣٧٩٣ - (وقال - صلى الله عليه وسلم - أكثر شهداء أمتي أصحاب الفرش) (ورب قتيل بين الصفين الله أعلم بنيته).','2337','3813','0');
INSERT INTO [t38171] VALUES (3817,'٣٧٩٤ - (وقال - صلى الله عليه وسلم - إن الله تعالى لا ينظر إلى صوركم وأموالكم وإنما ينظر إلى قلوبكم وأعمالكم)','2337','3813','0');
INSERT INTO [t38171] VALUES (3818,'٣٧٩٥ - (قال - صلى الله عليه وسلم - إن العبد ليعمل أعمالا حسنة فتصعد بها الملائكة في صحف مختمة فتلقى بين يدي الله تعالى فيقول)','2337','3813','0');
INSERT INTO [t38171] VALUES (3819,'٣٧٩٦ - (وقال - صلى الله عليه وسلم - الناس أربعة رجل آتاه الله عز وجل علما ومالا فهو يعمل بعلمه في ماله فيقول رجل لو آتاني الله مثل ما آتاه لعملت كما يعمل فهما في الأجر سواء ورجل آتاه الله مالا ولم يؤته علما فهو يتخبط بجهله في ماله فيقول رجل لو آتاني الله مثل ما آتاه عملت كما يعمل فهما في الوزر سواء)','2338','3813','0');
INSERT INTO [t38171] VALUES (3820,'٣٧٩٦/أ - (وكذلك في حديث أنس بن مالك) رضي الله عنه (لما خرج رسول الله - صلى الله عليه وسلم - في غزوة تبوك قال إن بالمدينة أقواما ما قطعنا واديا','2338','3819','0');
INSERT INTO [t38171] VALUES (3821,'٣٧٩٦/ ب - (وفي حديث ابن مسعود) رضي الله عنه (من هاجر ليبتغي شيئا فهو له فهاجر رجل فتزوج امرأة منا فكان يسمى مهاجر أم قيس)','2339','3819','0');
INSERT INTO [t38171] VALUES (3822,'٣٧٩٦/ ج - (وكذلك جاء في الخبر أن رجلا قتل في سبيل الله وكان يدعى قتيل الحمار لأنه قاتل رجلا ليأخذ سلبه وحماره فقتل على ذلك فأضيف إلى نيته)','2340','3819','0');
INSERT INTO [t38171] VALUES (3823,'٣٧٩٧ - وفي حديث عبادة بن الصامت رضي الله عنه: (عن النبي - صلى الله عليه وسلم - قال من غزا) في سبيل الله (وهو لا ينوي إلا عقالا فله ما نوى)','2340','3813','0');
INSERT INTO [t38171] VALUES (3824,'٣٧٩٧/ أ- وقال أبي بن كعب رضي الله عنه: (استعنت رجلا يغزو معي فقال لا حتى تجعل لي جعلا فجعلت له فذكرت ذلك للنبي - صلى الله عليه وسلم - فقال ليس له من دنياه وآخرته إلا ما جعلت له)','2341','3823','0');
INSERT INTO [t38171] VALUES (3825,'٣٧٩٨ - (وفي الإسرائيليات أن رجلا مر بكثبان من رمل في مجاعة)','2341','3813','0');
INSERT INTO [t38171] VALUES (3826,'٣٧٩٩ - (وقد ورد في أخبار كثيرة من هم بحسنة ولم يعملها كتبت لى حسنة)','2341','3813','0');
INSERT INTO [t38171] VALUES (3827,'٣٨٠٠ - (وفي حديث عبد الله بن عمرو) بن العاص رضي الله عنهما (من كانت الدنيا نيته جعل الله فقره بين عينيه وفارقها أرغب ما يكون فيها ومن تكن الآخرة نيته جعل الله غناه في قلبه وجمع عليه ضيعته وفارقها أزهد ما يكون فيها)','2342','3813','0');
INSERT INTO [t38171] VALUES (3828,'٣٨٠١ - وفي حديث أم سلمة رضي الله عنها: (أن النبي - صلى الله عليه وسلم - ذكر جيشا يخسف بهم بالبيداء)','2343','3813','0');
INSERT INTO [t38171] VALUES (3829,'٣٨٠٢ - (وقال عمر رضي الله عنه سمعت رسول الله - صلى الله عليه وسلم - يقول إنما يقتتل المقتتلون على النيات)','2343','3813','0');
INSERT INTO [t38171] VALUES (3830,'٣٨٠٣ - (وقال - صلى الله عليه وسلم - إذا التقى الصفان نزلت الملائكة تكتب الخلق على مراتبهم فلان يقاتل للدنيا فلان يقاتل حمية فلان يقاتل عصبية ألا فلا تقولوا فلان قتل في سبيل الله فمن قاتل لتكون كلمة الله هي العليا فهو في سبيل الله)','2343','3813','0');
INSERT INTO [t38171] VALUES (3831,'٣٨٠٤ - وعن جابر بن عبد الله الأنصاري رضي الله عنه: (عن رسول الله - صلى الله عليه وسلم - أنه قال يبعث كل عبد على ما مات عليه).','2344','3813','0');
INSERT INTO [t38171] VALUES (3832,'٣٨٠٥ - (وفي حديث الأحنف) بن قيس التميمي له رواية (عن أبي بكرة) نفيع بن الحارث الثقفي رضي الله عنه أن النبي - صلى الله عليه وسلم - قال: (إذا التقى المسلمان بسيفيهما فالقاتل والمقتول في النار قيل يا رسول الله هذا القاتل فما بال المقتول قال لأنه أراد قتل صاحبه)','2344','3813','0');
INSERT INTO [t38171] VALUES (3833,'٣٨٠٦ - (قوله - صلى الله عليه وسلم - إن الله تجاوز لأمتي ما حدثت به أنفسها ما لم يتكلموا أو يعملوا به)','2346','3813','0');
INSERT INTO [t38171] VALUES (3834,'٣٨٠٧ - (وفي الحديث الآخر إذا هم عبدي بسيئة فلا تكتبوها عليه)','2346','3813','0');
INSERT INTO [t38171] VALUES (3835,'٣٨٠٨ - وفي حديث أبي هريرة رضي الله عنه: (من تزوج امرأة على صداق وهو لا ينوي أداءه فهو زان ومن ادان دينا وهو لا ينوي قضاءه فهو سارق)','2346','3813','0');
INSERT INTO [t38171] VALUES (3836,'٣٨٠٩ - (وقال - صلى الله عليه وسلم - من تطيب لله تعالى جاء يوم القيامة وريحه أطيب من المسك ومن تطيب لغيره جاء يوم القيامة وريحه أنتن من الجيفة)','2347','3813','0');
INSERT INTO [t38171] VALUES (3837,'٣٨١٠ - وقال أبو هريرة رضي الله عنه: (يبعثون يوم القيامة على قدر نياتهم)','2347','3813','0');
INSERT INTO [t38171] VALUES (3838,'٣٨١١ - (قوله - صلى الله عليه وسلم - نية المؤمن خير من عمله).','2347','3813','0');
INSERT INTO [t38171] VALUES (3839,'٣٨١٢ - قال النبي - صلى الله عليه وسلم -: (إن في الجسد مضغة إذا صلحت صلح لها سائر الجسد)','2348','3813','0');
INSERT INTO [t38171] VALUES (3840,'٣٨١٣ - (وقال - صلى الله عليه وسلم - اللهم أصلح الراعي والرعية).','2348','3813','0');
INSERT INTO [t38171] VALUES (3841,'٣١٨٤ - وقد ورد في مسح رأس اليتيم','2348','3813','0');
INSERT INTO [t38171] VALUES (3842,'٣٨١٥ - (وبهذا أيضا يعرف معنى قوله - صلى الله عليه وسلم - من هم بحسنة فلم يعملها كتبت له حسنة)','2349','3813','0');
INSERT INTO [t38171] VALUES (3843,'٣٨١٦ - (والتقوى هاهنا أعنى القلب)','2349','3813','0');
INSERT INTO [t38171] VALUES (3844,'٣٨١٧ - (فإن عرفه فهو معاند للشرع وإن جهله فهو عاص بجهله إذ طلب العلم فريضة على كل مسلم)','2349','3813','0');
INSERT INTO [t38171] VALUES (3845,'٣٨١٨ - (وقال النبي - صلى الله عليه وسلم - لا يعذر الجاهل على الجهل ولا يحل للجاهل أن يسكت على جهله ولا للعالم أن يسكت على علمه)','2349','3813','0');
INSERT INTO [t38171] VALUES (3846,'٣٨١٩ - (مع أن السخاء هو أحب الأخلاق إلى الله تعالى حتى قال رسول الله - صلى الله عليه وسلم - إن لله تعالى ثلاثمائة خلق من تقرب إليه بواحد منها دخل الجنة وأحبها إليه السخاء)','2349','3813','0');
INSERT INTO [t38171] VALUES (3847,'٣٨٢٠ - وفي جزء أبي الجهم حدثنا سوارحدثنا مجالد عن أبي الوداك عن أبي سعيد عن ابن عباس قال خطبنا عمر فقال إن أخوف ما أخاف عليكم تغير الزمان وزيغة عالم وجدال منافق بالقرآن وأئمة مضلون يضلون الناس بغير علم.','2350','3813','0');
INSERT INTO [t38171] VALUES (3848,'٣٨٢١ - (قوله - صلى الله عليه وسلم - الأعمال بالنيات)','2350','3813','0');
INSERT INTO [t38171] VALUES (3849,'٣٨٢٢ - (رجاء لما وعده به رسول الله - صلى الله عليه وسلم - حيث قال من قعد في المسجد فقد زار الله تعالى وحق على المزور إكرام زائره)','2350','3813','0');
INSERT INTO [t38171] VALUES (3850,'٣٨٢٣ - فقد روى ابن جرير من حديث أبي هريرة من جلس في المسجد ينتظر الصلاة فهو في صلاة','2350','3813','0');
INSERT INTO [t38171] VALUES (3851,'٣٨٢٤ - (وهو نوع ترهب ولذلك قال رسول الله - صلى الله عليه وسلم - رهبانية أمتي القعود في المساجد)','2351','3813','0');
INSERT INTO [t38171] VALUES (3852,'٣٨٢٥ - (كما روي في الخبر من غدا إلى المسجد يذكر الله تعالى أو يذكر به كان كالمجاهد في سبيل الله تعالى)','2351','3813','0');
INSERT INTO [t38171] VALUES (3853,'٣٨٢٦ - (وقد قال الحسن بن علي رضي الله عنهما من أدمن الاختلاف إلى المسجد رزقه الله إحدى سبع خصال أخا مستفادا في الله أو رحمة مستنزلة أو علما مستظرفا أو كلمة تدله على هدى أو تصرفه عن ردى أو يترك الذنوب خشية أو حياء)','2352','3813','0');
INSERT INTO [t38171] VALUES (3854,'٣٨٢٧ - (ولا ينبغي أن يستحقر العبد شيئا من الخطوات والخطرات واللحظات فكل ذلك يسئل عنه يوم القيامة أنه فعله وما الذي قصد به هذا في مباح محض لا تشوبه كراهة ولذلك','2352','3813','0');
INSERT INTO [t38171] VALUES (3855,'٣٨٢٧/ أ- (وفي حديث معاذ بن جبل) رضي الله عنه (أن النبي - صلى الله عليه وسلم - قال إن العبد ليسئل يوم القيامة عن كل شيء حتى عن كحل عينيه وعن فتات الطينة بأصبعيه وعن لمسه ثوب أخيه)','2353','3854','0');
INSERT INTO [t38171] VALUES (3856,'٣٨٢٧/ ب- (وفي خبر آخر من تطيب لله تعالى جاء يوم القيامة وريحه أطيب من المسك ومن تطيب لغير الله تعالى جاء يوم القيامة وريحه أنتن من الجيفة)','2353','3854','0');
INSERT INTO [t38171] VALUES (3857,'٣٨٢٨ - (ومن نوقش الحساب عذب)','2353','3813','0');
INSERT INTO [t38171] VALUES (3858,'٣٨٢٩ - حديث أبي هريرة من اغتسل يوم الجمعة','2353','3813','0');
INSERT INTO [t38171] VALUES (3859,'٣٨٣٠ - (في الخبر إن العبد ليحاسب فتبطل أعماله لدخول الآفة فيها حتى يستوجب النار ثم ينشر له من الأعمال الصالحة ما يستوجب به الجنة فيتعجب ويقول يا رب هذه أعمال ما عملتها فيقال هذه أعمال الذين اغتابوك وآذوك وظلموك)','2354','3813','0');
INSERT INTO [t38171] VALUES (3860,'٣٨٣٠/ أ- (وفي الخبر إن العبد ليوافي القيامة بحسنات أمثال الجبال لو خلصت له لدخل الجنة فيأتي وقد ظلم هذا وشتم هذا وضرب هذا فيقتص لهذا من حسناته حتى لا يبقى له حسنة فتقول الملائكة قد فنيت حسناته وبقي طالبون فيقول الله تعالى ألقوا عليه من سيآتهم ثم صكوا له صكا إلى النار)','2355','3859','0');
INSERT INTO [t38171] VALUES (3861,'٣٨٣١ - (فصل).','2355','3813','0');
INSERT INTO [t38171] VALUES (3862,'٣٨٣٢ - (وقال النبي - صلى الله عليه وسلم - ثلاث لا يغل) أي لا يحقد (عليهن قلب رجل مسلم إخلاص العمل لله)','2356','3813','0');
INSERT INTO [t38171] VALUES (3863,'٣٨٣٢/ أ- (وعن) أبي زرارة (مصعب بن سعد) المدني ثقة روى له الجماعة مات سنة ثلاث ومائة (عن أبيه)','2357','3862','0');
INSERT INTO [t38171] VALUES (3864,'٣٨٣٢ / ب- (وعن الحسن) البصري رحمه الله تعالى (قال قال رسول الله - صلى الله عليه وسلم - يقول الله تعالى الإخلاص سر من سري استودعته قلب من أحببته من عبادي).','2358','3862','0');
INSERT INTO [t38171] VALUES (3865,'٣٨٣٢/ ج - (وقال علي بن أبي طالب كرم الله وجهه لا تهتموا لقلة العمل واهتموا للقبول فإن النبي - صلى الله عليه وسلم - قال لمعاذ بن جبل)','2361','3862','0');
INSERT INTO [t38171] VALUES (3866,'٣٨٣٢/ د- (وقال - صلى الله عليه وسلم - ما من عبد يخلص لله العمل أربعين يوما إلا ظهرت ينابيع الحكمة من قلبه على لسانه).','2361','3862','0');
INSERT INTO [t38171] VALUES (3867,'٣٨٣٣ - (وقال - صلى الله عليه وسلم - أول من يسئل يوم القيامة ثلاثة رجل آتاه الله العلم فيقول الله تعالى)','2362','3813','0');
INSERT INTO [t38171] VALUES (3868,'٣٨٣٤ - (وكتب عمر بن الخطاب رضي الله عنه إلى أبي موسى) عبد الله بن قيس (الأشعري)','2364','3813','0');
INSERT INTO [t38171] VALUES (3869,'٣٨٣٥ - وقد روى أبو الشيخ وابن عساكر من حديث جابر من صلى ركعتين في خلاء لا يراه إلا الله عز وجل والملائكة كانت له براءة من النار ورواه الضياء بلفظ كتبت له وروى أبو الشيخ من حديث ابن عمر من صلى ركعتين في السر رفع عنه اسم النفاق.','2364','3813','0');
INSERT INTO [t38171] VALUES (3870,'٣٨٣٦ - (وإنما البيان الشافي بيان سيد الأولين والآخرين - صلى الله عليه وسلم - إذ سئل عن الإخلاص فقال أن تقول ربي الله ثم تستقيم كما أمرت).','2364','3813','0');
INSERT INTO [t38171] VALUES (3871,'٣٨٣٧ - (ولهذا قيل ركعتان من عالم أفضل من عبادة سنة من جاهل)','2365','3813','0');
INSERT INTO [t38171] VALUES (3872,'٣٨٣٨ - (حديث ابن عمر من تعلم علما لغير الله وأراد به غير الله فليتبوأ مقعده من النار)','2365','3813','0');
INSERT INTO [t38171] VALUES (3873,'٣٨٣٩ - (حديث: لا أجر له).','2365','3813','0');
INSERT INTO [t38171] VALUES (3874,'٣٨٤٠ - (فقد روى طاوس) بن كيسان اليماني (وعدة من التابعين)','2367','3813','0');
INSERT INTO [t38171] VALUES (3875,'٣٨٤٠/أ - (وروى معاذ) بن جبل رضي الله عنه (عن النبي - صلى الله عليه وسلم - أنه قال أدنى الرياء شرك)','2367','3874','0');
INSERT INTO [t38171] VALUES (3876,'٣٨٤٠/ ب- (وقال أبو هريرة) رضي الله عنه (قال النبي - صلى الله عليه وسلم - يقال لمن أشرك في عمله خذ أجرك ممن عملت له).','2367','3874','0');
INSERT INTO [t38171] VALUES (3877,'٣٨٤٠/ ج - (وروي عن عباده) بن الصامت رضي الله عنه عن النبي - صلى الله عليه وسلم - (إن الله عز وجل يقول أنا أغنى الأغنياء عن الشركة من عمل عملا فأشرك معي غيري ودعت نصيبي لشريكي).','2368','3874','0');
INSERT INTO [t38171] VALUES (3878,'٣٨٤٠/ د- (وقال ابن مسعود رضي الله عنه قال رسول الله - صلى الله عليه وسلم - من هاجر يبتغي شيئا من الدنيا فهو له)','2368','3874','0');
INSERT INTO [t38171] VALUES (3879,'٣٨٤١ - (وقال النبي - صلى الله عليه وسلم - إن الصدق يهدي إلى البر)','2368','3813','0');
INSERT INTO [t38171] VALUES (3880,'٣٨٤٢ - وقال ابن عباس رضي الله عنهما: (أربع من كن فيه فقد ربح الصدق والحياء وحسن الخلق والشكر)','2371','3813','0');
INSERT INTO [t38171] VALUES (3881,'٣٨٤٣ - في كتاب الصمت لابن أبي الدنيا حدثنا أحمد بن منيع حدثنا مروان بن معاوية عن مجمع بن عيسى عن منصور بن المعتمر قال: قال رسول الله - صلى الله عليه وسلم -','2371','3813','0');
INSERT INTO [t38171] VALUES (3882,'٣٨٤٤ - (فقد قيل في المعاريض مندوحة عن الكذب)','2372','3813','0');
INSERT INTO [t38171] VALUES (3883,'٣٨٤٥ - (كان رسول الله - صلى الله عليه وسلم - إذا توجه إلى سفر وري بغيره).','2373','3813','0');
INSERT INTO [t38171] VALUES (3884,'٣٨٤٥/ أ - (قال رسول الله - صلى الله عليه وسلم - ليس بكذاب من أصلح بين اثنين فقال خيرا أو أنمى خيرا)','2373','3883','0');
INSERT INTO [t38171] VALUES (3885,'٣٨٤٦ - (وقال نبينا - صلى الله عليه وسلم - تعس عبد الدنيار وتعس عبد الدرهم وعبد الحلة وعبد الخميصة)','2374','3813','0');
INSERT INTO [t38171] VALUES (3886,'٣٨٤٧ - (فقد روي عن أنس) بن مالك بن النضر بن ضمضم الأنصاري رضي الله عنه (أن عمه أنس بن النضر)','2374','3813','0');
INSERT INTO [t38171] VALUES (3887,'٣٨٤٧/ ب- (وقال عبيد بن عمير)','2377','3886','0');
INSERT INTO [t38171] VALUES (3888,'٣٨٤٧/ ج- وقال مجاهد رحمه الله تعالى: (رجلان خرجا على ملأ من الناس فقالا إن رزقنا الله مالا لنصدقن به فبخلوا به فنزلت)','2378','3886','0');
INSERT INTO [t38171] VALUES (3889,'٣٨٤٨ - (ولذلك قال رسول الله - صلى الله عليه وسلم - اللهم اجعل سريرتي خيرا من علانيتي واجعل علانيتي صالحة)','2379','3813','0');
INSERT INTO [t38171] VALUES (3890,'٣٨٤٩ - وسئل أبو ذر رضي الله عنه: (عن الإيمان فقرأ هذه الآية فقيل له سألناك عن الإيمان فقال سألت رسول الله - صلى الله عليه وسلم - عن الإيمان)','2380','3813','0');
INSERT INTO [t38171] VALUES (3891,'٣٨٥٠/ أ- وقال جابر رضي الله عنه: (قال رسول الله مررت ليلة أسري بي وجبريل بالملأ الأعلى كالحلس البالي)','2380','3890','0');
INSERT INTO [t38171] VALUES (3892,'٣٨٥٠/ ب- (وقال النبي - صلى الله عليه وسلم - لا يبلغ عبد حقيقة الإيمان حتى ينظر إلى الناس كالأباعر في جنب الله ثم يرجع إلى نفسه فيجدها أحقر حقير).','2382','3890','0');
INSERT INTO [t38171] VALUES (3893,'كتاب المحاسبة','2383','3303','0');
INSERT INTO [t38171] VALUES (3894,'٣٨٥١ - وروى عبادة بن الصامت رضي الله عنه: (أنه - صلى الله عليه وسلم - قال لرجل سأله أن يوصيه ويعظه إذا أردت أمرا فتدبر عاقبته فإن كان رشدا فأمضه وإن كان غيا فانته عنه)','2384','3893','0');
INSERT INTO [t38171] VALUES (3895,'٣٨٥١/ أ - وروى شداد بن أوس رضي الله عنه: (عنه - صلى الله عليه وسلم - أنه قال الكيس من دان نفسه وعمل لما بعد الموت والأحمق من أتبع نفسه هواها وتمنى على الله)','2384','3894','0');
INSERT INTO [t38171] VALUES (3896,'٣٨٥١/ ب- وقال عمر رضي الله عنه: (حاسبوا أنفسكم قبل أن تحاسبوا وزنوها قبل أن توزنوا وتهيؤا للعرض الأكبر)','2384','3894','0');
INSERT INTO [t38171] VALUES (3897,'٣٨٥١/ د- (وقال - صلى الله عليه وسلم - اعبد الله كأنك تراه فإن لم تكن تراه فإنه يراك)','2387','3894','0');
INSERT INTO [t38171] VALUES (3898,'٣٨٥٢ - (وحكي أن زليخا) إمرأة العزيز (لما خلت بيوسف عليه السلام قامت فغطت وجه صنم لها) كانت تعبده (فقال)','2387','3893','0');
INSERT INTO [t38171] VALUES (3899,'٣٨٥٣ - (وقال حميد) بن أبي حميد يترويه (الطويل)','2388','3893','0');
INSERT INTO [t38171] VALUES (3900,'٣٨٥٤ - (وقيل لعبد الواحد بن زيد البصري العابد)','2390','3893','0');
INSERT INTO [t38171] VALUES (3901,'٣٨٥٥ - (ففي الخبر أنه ينشر للعبد في كل حركة من حركاته وإن صغرت ثلاثة دواوين الديوان الأول لم) بكسر اللام ونصب الميم وأصله لما وهو للاستفهام (والثاني كيف والثالث','2390','3893','0');
INSERT INTO [t38171] VALUES (3902,'٣٨٥٦ - (قال النبي - صلى الله عليه وسلم - لمعاذ) بن جبل رضي الله عنه يا معاذ (إن الرجل ليسئل عن كحل عينيه وعن فتات الطين بإصبعيه وعن لمسه ثوب أخيه)','2391','3893','0');
INSERT INTO [t38171] VALUES (3903,'٣٨٥٧ - (وقال في حديث سعد) بن أبي وقاص (حين أوصاه سلمان) رضي الله عنهما (اتق الله عند همك إذا هممت).','2391','3893','0');
INSERT INTO [t38171] VALUES (3904,'٣٨٥٨ - (وقد قال رسول الله - صلى الله عليه وسلم - إن الله يحب البصر الناقد) بالقاف أو هو بالفاء والذال (عند ورود الشبهات والعقل الكامل عند هجوم الشهوات).','2391','3893','0');
INSERT INTO [t38171] VALUES (3905,'٣٨٥٩ - وقد روى أبو نعيم في الحلية من طريق أيوب السختياني','2392','3893','0');
INSERT INTO [t38171] VALUES (3906,'٣٨٦٠ - (قال عيسى عليه السلام الأمور ثلاث أمر استبان رشده فاتبعه وأمر استبان غيه فاجتنبه وأمر أشكل عليك فكله إلى عالمه).','2393','3893','0');
INSERT INTO [t38171] VALUES (3907,'٣٨٦٠/أ - (وقد كان من دعاء النبي - صلى الله عليه وسلم - اللهم إني أعوذ بك أن أقول في الدين بغير علم).','2393','3906','0');
INSERT INTO [t38171] VALUES (3908,'٣٨٦١ - (قوله - صلى الله عليه وسلم - خير المجالس ما استقبل به القبلة)','2394','3893','0');
INSERT INTO [t38171] VALUES (3909,'٣٨٦٢ - (قوله - صلى الله عليه وسلم - من حسن إسلام المرء تركه ما لا يعنيه)','2394','3893','0');
INSERT INTO [t38171] VALUES (3910,'٣٨٦٣ - (وقد قال - صلى الله عليه وسلم - ثلاث من كن فيه استكمل إيمانه)','2394','3893','0');
INSERT INTO [t38171] VALUES (3911,'٣٨٦٤ - (رواه أبو ذر) الغفاري (رضي الله عنه من','2394','3893','0');
INSERT INTO [t38171] VALUES (3912,'٣٨٦٥ - (قال النبي - صلى الله عليه وسلم - لا تسبوا الدهر فإن الله هو الدهر).','2396','3893','0');
INSERT INTO [t38171] VALUES (3913,'٣٨٦٦ - (ويحكى أن غزوان وأبا موسى)','2396','3893','0');
INSERT INTO [t38171] VALUES (3914,'٣٨٦٧ - (ويحكى أن حسان بن أبي سنان)','2396','3893','0');
INSERT INTO [t38171] VALUES (3915,'٣٨٦٧/ أ- (وقال مالك بن ضيغم) الجلاب البصري (جاء رياح القيسي)','2397','3914','0');
INSERT INTO [t38171] VALUES (3916,'٣٨٦٧/ ب- (ويحكى أن) أبا رقية (تميم) بن أوس بن خارجة (الداري)','2398','3914','0');
INSERT INTO [t38171] VALUES (3917,'٣٨٦٧/ ج - (وعن طلحة) اختلف فيه فقيل هو الصحابي أحد العشرة وقيل هو طلحة بن مصرف كما سيأتي في بيان الاختلاف فيه عقيب الحديث (قال انطلق رجل ذات يوم فنزع ثيابه وتمرغ في الرمضاء)','2398','3914','0');
INSERT INTO [t38171] VALUES (3918,'٣٨٦٨ - (وقال حذيفة بن قتادة) المرعشي رحمه الله تعالى: (قيل لرجل كيف تصنع بنفسك في شهوتها فقال ما على وجه الأرض نفس أبغض إلي منها فكيف أعطيها شهوتها)','2399','3893','0');
INSERT INTO [t38171] VALUES (3919,'٣٨٦٩ - (وقال عبد الله بن قيس)','2401','3893','0');
INSERT INTO [t38171] VALUES (3920,'٣٨٧٠ - (و) ذكرنا أيضا (أن عمر) رضي الله عنه (كان يضرب قدميه بالدرة كل ليلة ويقول ماذا عملت اليوم)','2401','3893','0');
INSERT INTO [t38171] VALUES (3921,'٣٨٧١ - (ورأى) أبو عبد الله (محمد بن بشر) بن الفرافصة بن المختار بن رويح العبدي الكوفي ثقة حافظ مات سنة ثلاث ومائتين روى له الجماعة (داود)','2402','3893','0');
INSERT INTO [t38171] VALUES (3922,'٣٨٧٢ - (قال - صلى الله عليه وسلم - رحم الله أقواما يحسبهم الناس مرضى وما هم بمرضى).','2403','3893','0');
INSERT INTO [t38171] VALUES (3923,'٣٨٧٣ - (وقال رسول الله - صلى الله عليه وسلم - طوبى لمن طال عمره وحسن عمله).','2404','3893','0');
INSERT INTO [t38171] VALUES (3924,'٣٨٧٤ - (وكانت ابنة الربيع بن خثيم) كزبير بن عائذ بن عبد الله الثوري الكوفي (تقول له يا أبت مالي أرى الناس ينامون وأنت لا تنام فيقول يا ابنتاه إن أباك يخاف البيات)','2404','3893','0');
INSERT INTO [t38171] VALUES (3925,'٣٨٧٥ - (وقيل لعامر بن عبد الله)','2405','3893','0');
INSERT INTO [t38171] VALUES (3926,'٣٨٧٦ - (وقال القاسم بن محمد)','2405','3893','0');
INSERT INTO [t38171] VALUES (3927,'٣٨٧٧ - روى هناد في الزهد وابن أبي الدنيا في المرض والكفارات وأبو نعيم في الطب والبيهقي في الشعب والقضاعي فى المسند عن الحسن مرسلا الحمى رائد الموت وهي سجن الله في الأرض للمؤمن. يحبس بها عبده إذا شاء ويرسله إذا شاء.','2406','3893','0');
INSERT INTO [t38171] VALUES (3928,'٣٨٧٨ - ولابن أبي الدنيا في ذلك جزء (محاسبة النفس)','2406','3893','0');
INSERT INTO [t38171] VALUES (3929,'كتاب التفكر','2407','3303','0');
INSERT INTO [t38171] VALUES (3930,'٣٨٧٩ - (تفكر ساعة خير من عبادة سنة).','2408','3929','0');
INSERT INTO [t38171] VALUES (3931,'٣٨٨٠ - وقد قال ابن عباس رضي الله عنه: (إن قوما تفكروا في الله عز وجل فقال النبي - صلى الله عليه وسلم - تفكروا في خلق الله ولا تتفكروا في الله فإنكم لن تقدروا قدره).','2408','3929','0');
INSERT INTO [t38171] VALUES (3932,'٣٨٨١ - (وعن النبي - صلى الله عليه وسلم - أنه خرج على قوم ذات يوم وهم يتفكرون فقال ما لكم لا تتكلمون فقالوا نتفكر في خلق الله عز وجل قال فكذلك فافعلوا تفكروا في خلقه ولا تتفكروا فيه فإن بهذا المغرب أرضا بيضاء نورها بياضها وبياضها نورها مسيرة الشمس أربعين يوما بها خلق من خلق الله عز وجل لم يعصوا الله عز وجل طرفة عين قالوا يا رسول الله فأين الشيطان منهم قال ما يدرون خلق الشيطان أم لا قالوا من ولد آدم قال لا يدرون خلق آدم أم لا).','2409','3929','0');
INSERT INTO [t38171] VALUES (3933,'٣٨٨٢ - (وعن عطاء) بن أبي رباح المكي الفقيه الثقة روى له الجماعة (قال انطلقت أنا وعبيد بن عمير)','2411','3929','0');
INSERT INTO [t38171] VALUES (3934,'٣٨٨٣ - وأما حديث زرغبا تزدد حبا فرواه البزار والحارث بن','2411','3929','0');
INSERT INTO [t38171] VALUES (3935,'٣٨٨٤ - (وعن الحسن) البصري رحمه الله تعالى (قال تفكر ساعة خير من قيام ليلة)','2412','3929','0');
INSERT INTO [t38171] VALUES (3936,'٣٨٨٥ - وعن أبي سعيد الخدري رضي الله عنه: (قال قال رسول الله - صلى الله عليه وسلم - اعطوا أعينكم حظها من العبادة فقالوا يا رسول الله وما حظها من العبادة قال النظر في المصحف)','2412','3929','0');
INSERT INTO [t38171] VALUES (3937,'٣٨٨٦ - قال - صلى الله عليه وسلم - إن الله عز وجل: يؤيد هذا الدين بأقوام لا خلاف لهم)','2413','3929','0');
INSERT INTO [t38171] VALUES (3938,'٣٨٨٧ - (وفي الأخبار ما يدل على عظمها).','2413','3929','0');
INSERT INTO [t38171] VALUES (3939,'٣٨٨٨ - (وفي الأخبار أن بين كل سماء إلى أخرى مسيرة خمسمائة عام).','2414','3929','0');
INSERT INTO [t38171] VALUES (3940,'٣٨٨٩ - (فصل).','2415','3929','0');
INSERT INTO [t38171] VALUES (3941,'كتاب ذكر الموتى وما بعده','2418','3303','0');
INSERT INTO [t38171] VALUES (3942,'٣٨٩٠ - (قوله - صلى الله عليه وسلم - من كره لقاء الله كره الله لقاءه)','2419','3941','0');
INSERT INTO [t38171] VALUES (3943,'٣٨٩١ - روي عن حذيفة بن اليمان رضي الله عنهما: (أنه لما حضرته الوفاة قال حبيب جاء على فاقة لا أفلح من ندم اللهم أن كنت تعلم أن الفقر أحب إلي من الغنى والسقم أحب إلي من الصحة والموت أحب إلي من العيش فسهل علي الموت حتى ألقاك)','2419','3941','0');
INSERT INTO [t38171] VALUES (3944,'٣٨٩٢ - (فصل).','2420','3941','0');
INSERT INTO [t38171] VALUES (3945,'٣٨٩٣ - (قال رسول الله - صلى الله عليه وسلم - أكثروا من ذكر هادم اللذات)','2421','3941','0');
INSERT INTO [t38171] VALUES (3946,'٣٨٩٤ - (وقال - صلى الله عليه وسلم - لو تعلم البهائم من الموت ما يعلم ابن آدم)','2422','3941','0');
INSERT INTO [t38171] VALUES (3947,'٣٨٩٥ - (وقال - صلى الله عليه وسلم - تحفة المؤمن الموت).','2423','3941','0');
INSERT INTO [t38171] VALUES (3948,'٣٨٩٦ - (وقال - صلى الله عليه وسلم - الموت كفارة لكل مسلم)','2423','3941','0');
INSERT INTO [t38171] VALUES (3949,'٣٨٩٧ - (وقال عطاء الخراساني)','2424','3941','0');
INSERT INTO [t38171] VALUES (3950,'٣٨٩٧/ أ- وقال أنس رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - أكثروا من ذكر الموت فإنه)','2425','3949','0');
INSERT INTO [t38171] VALUES (3951,'٣٨٩٨ - (وقال - صلى الله عليه وسلم - كفى بالموت مفرقا).','2426','3941','0');
INSERT INTO [t38171] VALUES (3952,'٣٨٩٨/ أ- (وقال - صلى الله عليه وسلم - كفى بالموت واعظا).','2426','3951','0');
INSERT INTO [t38171] VALUES (3953,'٣٨٩٩ - (وذكر عند رسول الله - صلى الله عليه وسلم - رجل فأحسنوا الثناء عليه فقال كيف كان ذكر صاحبكم للموت قالوا ما كنا نكاد نسمعه يذكر الموت قال فإن صاحبكم ليس هناك).','2426','3941','0');
INSERT INTO [t38171] VALUES (3954,'٣٩٠٠ - وقال ابن عمر رضي الله عنهما: (أتيت النبي - صلى الله عليه وسلم - عاشر عشرة فقال رجل من الأنصار من أكيس الناس وأكرم الناس يا رسول الله فقال أكثرهم ذكرا للموت وأشدهم استعدادا له أولئك هم الأكياس ذهبوا بشرف الدنيا وكرامة الآخرة).','2427','3941','0');
INSERT INTO [t38171] VALUES (3955,'٣٩٠١ - وقال ابن مسعود رضي الله عنه: (السعيد من وعظ بغيره)','2428','3941','0');
INSERT INTO [t38171] VALUES (3956,'٣٩٠٢ - (قال رسول الله - صلى الله عليه وسلم - لعبد الله بن عمر)','2428','3941','0');
INSERT INTO [t38171] VALUES (3957,'٣٩٠٣ - (وروي عن علي كرم الله وجهه أنه - صلى الله عليه وسلم - قال إن أشد ما أخاف عليكم خصلتين)','2429','3941','0');
INSERT INTO [t38171] VALUES (3958,'٣٩٠٤ - وقالت أم المنذر الأنصارية رضي الله عنها: (اطلع رسول الله - صلى الله عليه وسلم - ذات عشية إلى الناس فقال أيها الناس أما تستحيون من الله قالوا وما ذاك يا رسول الله قال تجمعون ما لا تأكلون وتؤملون ما لا تدركون وتبنون ما لا تسكنون).','2431','3941','0');
INSERT INTO [t38171] VALUES (3959,'٣٩٠٥ - وقال أبو سعيد الخدري رضي الله عنه: (اشترى أسامة بن زيد) الكعبي رضي الله عنهما حب رسول الله وابن حب رسول الله - صلى الله عليه وسلم - (من زيد بن ثابت)','2431','3941','0');
INSERT INTO [t38171] VALUES (3960,'٣٩٠٦ - وعن ابن عباس رضي الله عنه: (أن رسول الله - صلى الله عليه وسلم - كان يخرج) أي إلى الخلاء (يهريق الماء فيتمسح بالتراب) أي يتيمم به (فأقول له يا رسول الله إن الماء منك قريب فيقول ما يدريني لعلي لا أبلغه).','2432','3941','0');
INSERT INTO [t38171] VALUES (3961,'٣٩٠٧ - (وروي أنه - صلى الله عليه وسلم - أخذ ثلاثة أعواد فغرز عودا بين يديه والآخر إلى جنبه وأما الآخر فأبعده فقال هل تدرون ما هذا قالوا الله ورسوله أعلم قال هذا الإنسان وهذا الأجل وذاك الأمل يتعاطاه ابن آدم ويختلجه الأجل دون الأمل).','2432','3941','0');
INSERT INTO [t38171] VALUES (3962,'٣٩٠٨ - (وقال - صلى الله عليه وسلم - مثل ابن آدم وإلى جنبه تسع وتسعون منية إن أخطأته المنايا وقع في الهرم).','2433','3941','0');
INSERT INTO [t38171] VALUES (3963,'٣٩٠٩ - وقال عبد الله بن مسعود رضي الله عنه: (خط رسول الله - صلى الله عليه وسلم - خطا مربعا وخط وسطه خطا وخط خطوطا إلى جنب الخط وخط خطا خارجا وقال أتدرون ما هذا قلنا الله ورسوله أعلم قال هذا الإنسان للخط الذي في الوسط وهذا الأجل محيط به وهذه الأعراض للخطوط التي حوله تنهشه إن أخطأه هذا نهشه هذا وذاك الأمل يعني الخط الخارج).','2433','3941','0');
INSERT INTO [t38171] VALUES (3964,'٣٩١٠ - وقال أنس رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - يهرم) أي يكبر (ابن آدم ويبقى منه) خصلتان (اثنتان)','2434','3941','0');
INSERT INTO [t38171] VALUES (3965,'٣٩١١ - (وقال - صلى الله عليه وسلم - نجا أول هذه الأمة)','2435','3941','0');
INSERT INTO [t38171] VALUES (3966,'٣٩١٢ - (وكان - صلى الله عليه وسلم - يقول في دعائه اللهم إني أعوذ بك من دنيا تمنع خير الآخرة وأعوذ بك من حياة تمنع خير الممات وأعوذ بك من أمل يمنع خير العمل).','2435','3941','0');
INSERT INTO [t38171] VALUES (3967,'٣٩١٣ - (قال رسول الله - صلى الله عليه وسلم - الشيخ شاب في حب طلب الدنيا وإن التقت ترقوتاه من الكبر إلا الذين اتقوا وقليل ما هم).','2436','3941','0');
INSERT INTO [t38171] VALUES (3968,'٣٩١٤ - (وهذا الإنسان هو الذي يصلي صلاة مودع)','2437','3941','0');
INSERT INTO [t38171] VALUES (3969,'٣٩١٥ - (عن معاذ بن جبل رضي الله عنه لما سأله رسول الله - صلى الله عليه وسلم - عن حقيقة إيمانه فقال ما خطوت خطوة إلا ظننت أني لا أتبعها أخرى).','2437','3941','0');
INSERT INTO [t38171] VALUES (3970,'٣٩١٦ - (قال - صلى الله عليه وسلم - ما ينتظر أحدكم من الدنيا إلا غنى مطغيا)','2437','3941','0');
INSERT INTO [t38171] VALUES (3971,'٣٩١٧ - وقال ابن عباس رضي الله عنه: (قال النبي - صلى الله عليه وسلم - لرجل وهو يعظه اغتنم خمسا قبل خمس)','2438','3941','0');
INSERT INTO [t38171] VALUES (3972,'٣٩١٨ - (وقال - صلى الله عليه وسلم - نعمتان)','2439','3941','0');
INSERT INTO [t38171] VALUES (3973,'٣٩١٩ - (وقال - صلى الله عليه وسلم - من خاف أدلج)','2439','3941','0');
INSERT INTO [t38171] VALUES (3974,'٣٩٢٠ - وقال - صلى الله عليه وسلم - جاءت الراجفة تتبعها الرادفة وجاء الموت بما فيه).','2440','3941','0');
INSERT INTO [t38171] VALUES (3975,'٣٩٢١ - (وكان رسول الله - صلى الله عليه وسلم - إذا أنس من الناس غفلة أو غرة نادى فيهم بصوت رفيع أتتكم المنية راتبة لازمة إما بشقاوة وإما بسعادة).','2440','3941','0');
INSERT INTO [t38171] VALUES (3976,'٣٩٢٢ - وقال أبو هريرة رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - أنا النذير والموت المغير والساعة الموعد).','2441','3941','0');
INSERT INTO [t38171] VALUES (3977,'٣٩٢٣ - وقال ابن عمر رضي الله عنه: (خرج رسول الله - صلى الله عليه وسلم - والشمس على أطراف السعف فقال ما بقي من الدنيا إلا كما بقي من يومنا فيما مضى منه).','2441','3941','0');
INSERT INTO [t38171] VALUES (3978,'٣٩٢٤ - (وقال - صلى الله عليه وسلم - مثل الدنيا كثوب شق من أوله إلى آخره فبقي متعلقا بخيط فى آخره فيوشك ذلك الخيط أن ينقطع).','2442','3941','0');
INSERT INTO [t38171] VALUES (3979,'٣٩٢٥ - قال جابر رضي الله عنه: (كان رسول الله - صلى الله عليه وسلم - إذا خطب فذكر الساعة رفع صوته واحمرت وجنتاه كأنه منذر جيش يقول صبحتكم ومستكم بعثت أنا والساعة كهاتين وقرن بين أصبعيه)','2442','3941','0');
INSERT INTO [t38171] VALUES (3980,'٣٩٢٦ - وقال ابن مسعود رضي الله عنه: (تلا رسول الله - صلى الله عليه وسلم -) قوله تعالى: (فمن يرد الله أن يهديه يشرح صدره للإسلام فقال رسول الله - صلى الله عليه وسلم - إن النور إذا دخل الصدر انفسح فقيل يا رسول الله هل لذلك علامة تعرف قال نعم التجافي عن دار الغرور والإنابة إلى دار الخلود والاستعداد للموت قبل نزوله)','2443','3941','0');
INSERT INTO [t38171] VALUES (3981,'٣٩٢٧ - وقال أبو عبيدة بكر بن الأسود:','2444','3941','0');
INSERT INTO [t38171] VALUES (3982,'٣٩٢٨ - (قال رسول الله - صلى الله عليه وسلم - إن الله يقبل توبة العبد ما لم يغرغر).','2444','3941','0');
INSERT INTO [t38171] VALUES (3983,'٣٩٢٩ - (وروي أن نفرا من بني إسرائيل مروا بمقبرة فقال بعضهم لبعض لو دعوتم الله تعالى أن يخرج لكم من هذه المقبرة ميتا تسألونه)','2445','3941','0');
INSERT INTO [t38171] VALUES (3984,'٣٩٣٠ - (وروي أنه - صلى الله عليه وسلم - كان يقول اللهم إنك تأخذ الروح من بين العصب والقصب والأنامل اللهم فأعني على الموت وهونه علي).','2445','3941','0');
INSERT INTO [t38171] VALUES (3985,'٣٩٣١ - (وسئل - صلى الله عليه وسلم - عن الموت وشدته فقال إن أهون الموت بمنزلة حسكة) كانت (في صوف فهل تخرج الحسكة من الصوف إلا ومعها صوف).','2446','3941','0');
INSERT INTO [t38171] VALUES (3986,'٣٩٣٢ - (ودخل - صلى الله عليه وسلم - على مريض ثم قال إني أعلم ما يلقى ما منه عرق إلا ويألم للموت على حدته).','2446','3941','0');
INSERT INTO [t38171] VALUES (3987,'٣٩٣٣ - (وكان علي رضي الله عنه يحض) الناس (على القتال ويقول إن لم تقتلوا تموتوا والذي نفسي بيده لألف ضربة بالسيف أهون من موت على فراش)','2447','3941','0');
INSERT INTO [t38171] VALUES (3988,'٣٩٣٤ - وعن أبي عبد الله (زيد بن أسلم) العدوي مولاهم المدني ثقة عالم كان يرسل مات سنة ست وثلاثين روي له الجماعة (عن أبيه)','2448','3941','0');
INSERT INTO [t38171] VALUES (3989,'٣٩٣٥ - (وعن بعضهم أنه كان يسأل كثيرا من المرضى كيف تجدون الموت فلما مرض قيل له فأنت كيف تجده فقال كأن السماوات مطبقة على الأرض وكأن نفسي تخرج من ثقب إبرة)','2449','3941','0');
INSERT INTO [t38171] VALUES (3990,'٣٩٣٦ - (وقال - صلى الله عليه وسلم - موت الفجأة راحة للمؤمن وأسف على الفاجر).','2450','3941','0');
INSERT INTO [t38171] VALUES (3991,'٣٩٣٧ - وروي عن أبي عبد الله (مكحول)','2451','3941','0');
INSERT INTO [t38171] VALUES (3992,'٣٩٣٨ - (ويروى لو أن قطرة من ألم الموت وضعت على جبال الدنيا كلها لذابت).','2451','3941','0');
INSERT INTO [t38171] VALUES (3993,'٣٩٣٩ - (وروي أن إبراهيم عليه السلام لما مات قال الله تعالى له كيف وجدت الموت يا خليلي قال كسفود جعل في','2451','3941','0');
INSERT INTO [t38171] VALUES (3994,'٣٩٤٠ - (وروي عن موسى عليه السلام أنه لما صارت روحه إلى الله تعالى قال له ربه يا موسى كيف وجدت الموت قال وجدت نفسي كالعصفور)','2452','3941','0');
INSERT INTO [t38171] VALUES (3995,'٣٩٤١ - (وروي عن النبي - صلى الله عليه وسلم - أنه كان عنده قدح من ماء عند الموت فجعل يدخل يده في الماء ثم يمسح بها وجهه ويقول اللهم هون علي سكرات الموت).','2452','3941','0');
INSERT INTO [t38171] VALUES (3996,'٣٩٤٢ - (وفاطمة رضي الله عنها تقول واكرباه لكربك يا أبتاه وهو يقول لا كرب على أبيك بعد اليوم).','2453','3941','0');
INSERT INTO [t38171] VALUES (3997,'٣٩٤٣ - (وقال عمر رضي الله عنه لكعب الأحبار)','2453','3941','0');
INSERT INTO [t38171] VALUES (3998,'٣٩٤٤ - (وقال النبي - صلى الله عليه وسلم - إن العبد ليعالج كرب الموت وسكرات الموت وإن مفاصله ليسلم بعضها على بعض تقول عليك السلام تفارقني وأفارقك إلى يوم القيامة).','2453','3941','0');
INSERT INTO [t38171] VALUES (3999,'٣٩٤٤/ أ- (وروى أبو هريرة عن النبي - صلى الله عليه وسلم - إن داود عليه السلام كان رجلا غيورا وكان إذا خرج أغلق الأبواب فأغلق ذات يوم وخرج فأشرفت امرأته فإذا هي برجل في الدار فقالت من أدخل هذا الرجل لئن جاء داود ليلقين منه عنتا)','2454','3998','0');
INSERT INTO [t38171] VALUES (4000,'٣٩٤٤/ ب- (عن ابن عباس) رضي الله عنه (أن إبراهيم عليه السلام كان رجلا غيورا وكان له بيت يتعبد فيه','2454','3998','0');
INSERT INTO [t38171] VALUES (4001,'٣٩٤٤/ ج- وقالت عائشة رضي الله عنها لا أغبط أحدا يهون عليه الموت بعد الذي رأيت من شدة موت رسول الله - صلى الله عليه وسلم -)','2455','3998','0');
INSERT INTO [t38171] VALUES (4002,'٣٩٤٥ - (وروي أن عيسى عليه السلام مر بجمجمة','2455','3941','0');
INSERT INTO [t38171] VALUES (4003,'٣٩٤٦ - (ومنها مشاهدة الملكين الحافظين قال وهيب)','2456','3941','0');
INSERT INTO [t38171] VALUES (4004,'٣٩٤٧ - (وقد قال - صلى الله عليه وسلم - لن يخرج أحدكم من الدنيا حتى يعلم أين مصيره وحتى يرى مقعده من الجنة أو النار).','2457','3941','0');
INSERT INTO [t38171] VALUES (4005,'٣٩٤٩ - وروى أن حذيفة بن اليمان رضي الله عنهما: (قال لابن مسعود)','2459','3941','0');
INSERT INTO [t38171] VALUES (4006,'٣٩٥٠ - (ودخل مروان)','2460','3941','0');
INSERT INTO [t38171] VALUES (4007,'٣٩٥١ - (وروي في الحديث عن النبي - صلى الله عليه وسلم -','2461','3941','0');
INSERT INTO [t38171] VALUES (4008,'٣٩٥١/ أ- وقال الحسن البصري رحمه الله تعالى: (لا راحة للمؤمن إلا في لقاء الله ومن كانت راحته في لقاء الله تعالى فيوم الموت يوم سروره)','2466','4007','0');
INSERT INTO [t38171] VALUES (4009,'٣٩٥٢ - وقيل لجابر بن زيد أبي الشعثاء الأزدي البصري التابعي الثقة مشهور بكنيته مات سنة ثلاث وتسعين روى له الجماعة (عند الموت ما تشتهي قال نظرة إلى الحسن) وهو البصري (فلما دخل عليه الحسن قيل له هذا الحسن فرفع طرفه إليه ثم قال يا إخوتاه الساعة والله أفارقكم إلى النار أو إلى الجنة).','2467','3941','0');
INSERT INTO [t38171] VALUES (4010,'٣٩٥٣ - وقال أبو عبد الله: (محمد بن واسع) البصري العابد رحمه الله تعالى (عند الموت يا إخوتاه عليكم السلام إلى النار أو يعفو الله)','2467','3941','0');
INSERT INTO [t38171] VALUES (4011,'٣٩٥٤ - وروى ابن أبي الدنيا عن عمار بن نصر عن قتيبة','2468','3941','0');
INSERT INTO [t38171] VALUES (4012,'٣٩٥٥ - (روي عن النبي - صلى الله عليه وسلم - أنه قال ارقبوا الميت عند ثلاث إذا رشح جبينه ودمعت) وفي نسخة ذرفت (عيناه ويبست شفتاه فهي من رحمة الله تعالى قد ننزلت به وإذا غط غطيط المخنوق واحمر لونه وأزبدت شفتاه فهو من عذاب الله قد نزل به).','2469','3941','0');
INSERT INTO [t38171] VALUES (4013,'٣٩٥٦ - (وقال عمر رضي الله عنه احضروا موتكم واذكروهم فإنهم يرون ما لا ترون ولقنوهم لا إله إلا الله)','2469','3941','0');
INSERT INTO [t38171] VALUES (4014,'٣٩٥٧ - وقال أبو هريرة رضي الله عنه: (سمعت رسول الله - صلى الله عليه وسلم - يقول حضر ملك الموت رجلا يموت)','2470','3941','0');
INSERT INTO [t38171] VALUES (4015,'٣٩٥٨ - قال أبو سعيد الخدري رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - لقنوا موتاكم لا إله إلا الله)','2470','3941','0');
INSERT INTO [t38171] VALUES (4016,'(تنبيه)','2472','4015','0');
INSERT INTO [t38171] VALUES (4017,'٣٩٥٩ - ومن أظرف ما وقع في ذلك ما قال البيهقي في الشعب','2474','3941','0');
INSERT INTO [t38171] VALUES (4018,'٣٩٦٠ - (دخل) واثلة بالمثلثة (بن الأسقع)','2475','3941','0');
INSERT INTO [t38171] VALUES (4019,'٣٩٦١ - (ودخل النبي - صلى الله عليه وسلم - على شاب وهو يموت فقال كيف تجدك فقال أرجو الله وأخاف ذنوبي فقال - صلى الله عليه وسلم - ما اجتمعا في قلب عبد في مثل هذا الموطن إلا أعطاه الله الذي يرجو وآمنه من الذي يخاف)','2476','3941','0');
INSERT INTO [t38171] VALUES (4020,'٣٩٦٢ - وروى الطبراني في الكبير وأبو نعيم وابن منده كلاهما','2476','3941','0');
INSERT INTO [t38171] VALUES (4021,'٣٩٦٣ - قال المصنف في الدرة الفاخرة','2477','3941','0');
INSERT INTO [t38171] VALUES (4022,'٣٩٦٤ - (وهو أول من تنشق الأرض عنه)','2479','3941','0');
INSERT INTO [t38171] VALUES (4023,'٣٩٦٩ - روى ابن المبارك وأحمد كلاهما في الزهد وابن عساكر','2479','3941','0');
INSERT INTO [t38171] VALUES (4024,'٣٩٧٠ - قال ابن مسعود رضي الله عنه: (دخلنا على رسول الله - صلى الله عليه وسلم - في بيت أمنا عائشة رضي الله عنها حين دنا الفراق فنظر إلينا فدمعت عيناه - صلى الله عليه وسلم - ثم قال مرحبا بكم حياكم الله آواكم الله نصركم الله أوصيكم بتقوى الله وأوصى بكم الله إني لكم منه نذير مبين أن لا تعلوا على الله في بلاده وعباده وقد دنا الأجل والمنقلب إلى الله وإلى سدرة المنتهى وإلى جنة المأوى والكأس الأوفى فاقرؤا على أنفسكم وعلى من دخل في دينكم بعدي مني السلام ورحمة الله).','2480','3941','0');
INSERT INTO [t38171] VALUES (4025,'٣٩٧١ - (وروي أنه - صلى الله عليه وسلم - قال لجبريل) عليه السلام (عند موته من لأمتي بعدي فأوحى الله تعالى إلى جبريل) عليه السلام (أن بشر حبيبي أني لا أخذله في أمته وبشره بأنه أسرع الناس خروجا من الأرض) أي من قبره (إذا بعثوا وسيدهم إذا جمعوا وإن الجنة محرمة على الأمم حتى تدخلها أمته فقال) - صلى الله عليه وسلم - (الآن قرت عيني).','2481','3941','0');
INSERT INTO [t38171] VALUES (4026,'٣٩٧٢ - (وقالت عائشة رضي الله عنها أمرنا رسول الله - صلى الله عليه وسلم - أن نغسله بسبع قرب من سبعة آبار ففعلنا ذلك فوجد راحة فخرج فصلى بالناس واستغفر لأهل أحد ودعا لهم وأوصى بالأنصار فقال أما بعد يا معشر المهاجرين فإنكم تزيدون وأصبحت الأنصار لا تزيد على هيئتها التي هي عليها اليوم وإن الأنصار عيبتي التي أويت إليها) أي موضع سري (فأكرموا كريمهم يعني محسنهم وتجاوزوا عن مسيئهم ثم قال إن عبدا خير بين الدنيا وبين ما عند الله فاختار ما عند الله فبكى أبو بكر رضي الله عنه وظن أنه يريد نفسه) أي لما فهم الرمز الذي أشار به النبي - صلى الله عليه وسلم - من قرينة ذكره ذلك في مرض موته فاستشعر منه أنه أراد نفسه فلذلك بكى (فقال النبي - صلى الله عليه وسلم - على رسلك يا أبا بكر سدوا هذه الأبواب الشوارع في المسجد إلا باب أبي بكر فإني لا أعلم امرءا أفضل عندي في الصحبة من أبي بكر).','2482','3941','0');
INSERT INTO [t38171] VALUES (4027,'(تنبيه)','2484','4026','0');
INSERT INTO [t38171] VALUES (4028,'٣٩٧٣ - قالت عائشة رضي الله عنها: (فقبض - صلى الله عليه وسلم - في بيتي وفي يومي وبين سحري ونحري وجمع الله بين ريقي وريقه عند الموت فدخل علي أخي عبد الرحمن وبيده سواك فجعل ينظر إليه فعرفت أنه يعجبه ذلك فقلت له آخذه لك فأومأ برأسه أي نعم فناولته إياه فأدخله في فيه فاشتد عليه فقلت ألينه لك فأومأ برأسه أي نعم فلينته وكان بين يديه ركوة ماء فجعل يدخل فيها يده ويقول لا إله إلا الله إن للموت لسكرات ثم نصب يده يقول الرفيق الأعلى الرفيق الأعلى).','2485','3941','0');
INSERT INTO [t38171] VALUES (4029,'٣٩٧٤ - وروى سعيد بن عبد الله عن أبيه عبد الله بن ضرار بن الأزور (قال لما رأت الأنصار أن رسول الله - صلى الله عليه وسلم - يزداد ثقلا أطافوا بالمسجد فدخل العباس رضي الله عنه على النبي - صلى الله عليه وسلم - فأعلمه بمكانهم وإشفاقهم ثم دخل عليه الفضل بن العباس (فأعلمه بمثل ذلك ثم دخل عليه علي رضي الله عنه فأعلمه بمثله فمد يده وقال: ها، فتناولوه فقال ما يقولون قالوا يقولون نخشى أن يموت وتصايح نساؤهم لاجتماع رجالهم إلى النبي - صلى الله عليه وسلم - فثار رسول الله - صلى الله عليه وسلم - فخرج متوكئا على علي والفضل والعباس أمامه ورسول الله - صلى الله عليه وسلم - معصوب الرأس يخط برجليه حتى جلس على أسفل مرقاة من المنبر وثاب الناس إليه)','2487','3941','0');
INSERT INTO [t38171] VALUES (4030,'٣٩٧٥ - وروى ابن مسعود رضي الله عنه: (إن النبي - صلى الله عليه وسلم - قال لأبي بكر رضي الله عنه سل يا أبا بكر فقال يا رسول الله دنا','2489','3941','0');
INSERT INTO [t38171] VALUES (4031,'٣٩٧٦ - (وقال عبد الله بن زمعة)','2491','3941','0');
INSERT INTO [t38171] VALUES (4032,'٣٩٧٧ - حديث موته - صلى الله عليه وسلم - قال العراقي:','2493','3941','0');
INSERT INTO [t38171] VALUES (4033,'٣٩٧٨ - وجبهته ترشح رشحا وفيه يا عائشة إن نفس المؤمن تخرج بالرشح','2500','3941','0');
INSERT INTO [t38171] VALUES (4034,'٣٩٨٠ - (قالت عائشة رضي الله عنها مات رسول الله - صلى الله عليه وسلم - بين ارتفاع الضحى وانتصاف النهار يوم الاثنين).','2500','3941','0');
INSERT INTO [t38171] VALUES (4035,'٣٩٨١ - قال العراقي: هذا السياق بطوله منكر لم أجد له أصلا انتهى.','2501','3941','0');
INSERT INTO [t38171] VALUES (4036,'٣٩٨٢ - قال العراقي: رواه البخاري ومسلم من حديث عائشة','2502','3941','0');
INSERT INTO [t38171] VALUES (4037,'٣٩٨٣ - وفي رواية أن أبا بكر رضي الله عنه: (لما بلغه الخبر دخل بيت رسول الله - صلى الله عليه وسلم - وهو يصلي على النبي - صلى الله عليه وسلم - وعيناه تهملان)','2503','3941','0');
INSERT INTO [t38171] VALUES (4038,'٣٩٨٤ - وعن ابن عمر رضي الله عنهما (أنه لما دخل أبو بكر رضي الله عنه البيت)','2504','3941','0');
INSERT INTO [t38171] VALUES (4039,'٣٩٨٥ - (واستوفى القعقاع بن عمرو)','2508','3941','0');
INSERT INTO [t38171] VALUES (4040,'٣٩٨٦ - (قال أبو جعفر محمد بن علي بن الحسين بن علي بن أبي طالب فرش لحده بمفرشة وقطيفة وفرشت ثيابه عليها التي كان يلبس يقظانا على القطيفة والمفرش ثم وضع عليها في أكفانه).','2510','3941','0');
INSERT INTO [t38171] VALUES (4041,'٣٩٨٧ - (ففي وفاته) - صلى الله عليه وسلم - (عبرة تامة وللمسلمين به أسوة حسنة)','2510','3941','0');
INSERT INTO [t38171] VALUES (4042,'٣٩٨٨ - حديث موت أبي بكر -رضي الله عنه-.','2511','3941','0');
INSERT INTO [t38171] VALUES (4043,'٣٩٨٩ - روي ابن ماجة وابن عساكر من حديث أبي بكر بلفظ من وصلى الصبح فهو','2512','3941','0');
INSERT INTO [t38171] VALUES (4044,'٣٩٩٠ - (ولما ثقل أبو بكر رضي الله عنه وأراد الناس منه أن يستخلف فاستخلف عمر فقال الناس له استخلفت علينا فظا غليظا فماذا تقول لربك فقال أقول استخلفت على خلقك خير خلقك)','2513','3941','0');
INSERT INTO [t38171] VALUES (4045,'٣٩٩١ - (ثم أرسل إلى عمر رضي الله عنه فجاء فقال إني موصيك بوصية اعلم أن لله حقا في النهار لا يقبله في الليل وأن','2516','3941','0');
INSERT INTO [t38171] VALUES (4046,'٣٩٩٢ - موت عمر رضي الله عنه','2518','3941','0');
INSERT INTO [t38171] VALUES (4047,'٣٩٩٣ - (وعن النبي - صلى الله عليه وسلم - قال قال لي جبريل عليه السلام ليبك الإسلام على موت عمر).','2523','3941','0');
INSERT INTO [t38171] VALUES (4048,'٣٩٩٤ - وعن ابن عباس رضي الله عنه: (قال وضع عمر على سريره) بعدما كفن (فتكفنه الناس) أي أحاطوا حواليه','2523','3941','0');
INSERT INTO [t38171] VALUES (4049,'٣٩٩٥ - موت عثمان رضي الله عنه رواه سيف بن عمر التميمي وابن عائذ','2524','3941','0');
INSERT INTO [t38171] VALUES (4050,'٣٩٩٦ - قال عبد الله بن أحمد:','2527','3941','0');
INSERT INTO [t38171] VALUES (4051,'٣٩٩٧ - وقال ثمامة بن حزن بن عبد الله بن مسلمة بن قشير بن كعب بن ربيعة بن عامر بن صعصعة (القشيري)','2527','3941','0');
INSERT INTO [t38171] VALUES (4052,'٤٩٩٨ - وروى يحيى بن ميمون العدادي عن الحارث بن عمير عن معمر بن عقيل','2529','3941','0');
INSERT INTO [t38171] VALUES (4053,'٣٩٩٩ - (قال الأصبغ) بن نباتة التميمي (الحنظلي)','2530','3941','0');
INSERT INTO [t38171] VALUES (4054,'٤٠٠٠ - (وعن) أبي جعفر (محمد بن علي) بن الحسن بن علي رضي الله عنه (أنه)','2533','3941','0');
INSERT INTO [t38171] VALUES (4055,'٤٠٠١ - (ولما ثقل الحسن بن علي رضي الله عنهما)','2533','3941','0');
INSERT INTO [t38171] VALUES (4056,'٤٠٠٢ - قال محمود بن محمد بن الفضل في كتاب المتفجعين:','2535','3941','0');
INSERT INTO [t38171] VALUES (4057,'٤٠٠٣ - قال محمود بن محمد بن الفضل في كتاب المتفجعين:','2536','3941','0');
INSERT INTO [t38171] VALUES (4058,'٤٠٠٤ - (إذا وفى أجلي فول غسلي رجلا لبيبا فإن اللبيب من الله بمكان فلينعم الغسل وليجهر بالتكبير ثم اعمد)','2536','3941','0');
INSERT INTO [t38171] VALUES (4059,'٤٠٠٥ - قال محمود بن محمد بن الفضل:','2537','3941','0');
INSERT INTO [t38171] VALUES (4060,'٤٠٠٦ - وقال محمود بن محمد بن الفضل:','2539','3941','0');
INSERT INTO [t38171] VALUES (4061,'٤٠٠٧ - وروي أنه لما ثقل عمر بن عبد العزيز رحمه الله تعالى (دعي له طبيب فلما نظر إليه قال الرجل قد سقي السم ولا آمن عليه الموت فرفع عمر)','2542','3941','0');
INSERT INTO [t38171] VALUES (4062,'٤٠٠٨ - وقال محمود بن محمد بن الفضل:','2543','3941','0');
INSERT INTO [t38171] VALUES (4063,'٤٠٠٩ - قال محمود بن محمد:','2545','3941','0');
INSERT INTO [t38171] VALUES (4064,'٤٠١٠ - قال أبو الحسن المدائني:','2545','3941','0');
INSERT INTO [t38171] VALUES (4065,'٤٠١١ - قال محمود بن الفضل:','2547','3941','0');
INSERT INTO [t38171] VALUES (4066,'٤٠١٢ - (وأحضرت سلمان)','2547','3941','0');
INSERT INTO [t38171] VALUES (4067,'٤٠١٣ - عمار بن ياسر رضي الله عنه.','2550','3941','0');
INSERT INTO [t38171] VALUES (4068,'٤٠١٤ - وقال ابن أبي الدنيا:','2550','3941','0');
INSERT INTO [t38171] VALUES (4069,'٤٠١٥ - قال ابن أبي الدنيا:','2551','3941','0');
INSERT INTO [t38171] VALUES (4070,'٤٠١٧ - (يروى عن أبي هريرة) رضي الله عنه (أنه كان إذا رأى جنازة قال امضوا فإنا على الأثر)','2551','3941','0');
INSERT INTO [t38171] VALUES (4071,'٤٠١٨ - (قال الضحاك)','2552','3941','0');
INSERT INTO [t38171] VALUES (4072,'٤٠١٩ - (وقال عمر بن الخطاب رضي الله عنه خرجنا مع رسول الله - صلى الله عليه وسلم - إلى المقابر فجلس إلى قبر وكنت أدنى القوم منه','2552','3941','0');
INSERT INTO [t38171] VALUES (4073,'٤٠٢٠ - (وكان عثمان بن عفان رضي الله عنه إذا وقف على قبر بكى حتى يبل لحيته فسئل عن ذلك وقيل له تذكر الجنة والنار فلا تبكي وتبكي إذا وقفت على قبر فقال سمعت رسول الله - صلى الله عليه وسلم - يقول إن القبر أول منازل الآخرة فإن نجا منه صاحبه فما بعده أيسر منه وإن لم ينج منه فما بعده أشد).','2554','3941','0');
INSERT INTO [t38171] VALUES (4074,'٤٠٢١ - وقال محمود بن محمد في كتاب المتفجعين:','2555','3941','0');
INSERT INTO [t38171] VALUES (4075,'٤٠٢٢ - (قال رسول الله - صلى الله عليه وسلم - لأن أقدم سقطا أحب إلي من أن أخلف مائة فارس كلهم يقاتل في سبيل الله)','2555','3941','0');
INSERT INTO [t38171] VALUES (4076,'٤٠٢٣ - (وقد روي عن علي رضي الله عنه عن رسول الله في أنه قال كنت نهيتكم عن زيارة القبور فزوروها فإنها تذكركم الآخرة غير أن لا تقولوا هجرا).','2556','3941','0');
INSERT INTO [t38171] VALUES (4077,'٤٠٢٤ - (وزار رسول الله - صلى الله عليه وسلم - قبر أمه في ألف مقنع فلم ير باكيا أكثر من يومئذ وفي هذا اليوم قال أذن لي في الزيارة دون الاستغفار).','2557','3941','0');
INSERT INTO [t38171] VALUES (4078,'٤٠٢٥ - وقال ابن أبي مليكة:','2558','3941','0');
INSERT INTO [t38171] VALUES (4079,'٤٠٢٦ - (قال رسول الله - صلى الله عليه وسلم - زر القبور تذكر بها الآخرة واغسل الموتى فإن معالجة جسد خاو موعظة بليغة وصل على الجنائز لعل ذلك أن يحزنك فإن الحزين في ظل الله).','2558','3941','0');
INSERT INTO [t38171] VALUES (4080,'٤٠٢٧ - (وقال ابن أبي مليكة) عبد الله بن عبيد الله التميمي التابعي (قال رسول الله - صلى الله عليه وسلم - زوروا موتكم وسلموا عليهم فإن لكم فيهم عبرة).','2559','3941','0');
INSERT INTO [t38171] VALUES (4081,'٤٠٢٨ - (وقال النبي - صلى الله عليه وسلم - من زار قبر أبويه) وفي لفظ والديه (أو أحدهما في كل جمعة غفر له وكتب برا)','2559','3941','0');
INSERT INTO [t38171] VALUES (4082,'٤٠٢٩ - وعن ابن سيرين محمد رحمه الله تعالى: (قال قال رسول الله - صلى الله عليه وسلم - إن الرجل ليموت والداه وهو عاق بهما فيدعو الله لهما من بعدهما فيكتبه الله من البارين).','2560','3941','0');
INSERT INTO [t38171] VALUES (4083,'٤٠٣٠ - (وقال النبي - صلى الله عليه وسلم - من زار قبري وجبت له شفاعتي)','2561','3941','0');
INSERT INTO [t38171] VALUES (4084,'٤٠٣١ - (وقالت عائشة رضي الله عنها قال رسول الله - صلى الله عليه وسلم - ما من رجل يزور قبر أخيه ويجلس عنده إلا استأنس به ورد عليه حتى يقوم).','2561','3941','0');
INSERT INTO [t38171] VALUES (4085,'٤٠٣٢ - (وقال سليمان بن سحيم)','2562','3941','0');
INSERT INTO [t38171] VALUES (4086,'٤٠٣٣ - وقال أبو هريرة رضي الله عنه: (إذا مر الرجل بقبر الرجل يعرفه فسلم عليه رد عليه السلام وعرفه وإذا مر بقبر لا يعرفه فسلم عليه رد عليه السلام)','2562','3941','0');
INSERT INTO [t38171] VALUES (4087,'٤٠٣٤ - (وقال رسول الله - صلى الله عليه وسلم - ما الميت في قبره إلا كالغريق المتغوث)','2562','3941','0');
INSERT INTO [t38171] VALUES (4088,'٤٠٣٥ - (وقال سعيد بن عبد الله الأودي)','2563','3941','0');
INSERT INTO [t38171] VALUES (4089,'٤٠٣٦ - (قال رسول الله - صلى الله عليه وسلم - إذا مات صاحبكم)','2564','3941','0');
INSERT INTO [t38171] VALUES (4090,'٤٠٣٧ - (وقال - صلى الله عليه وسلم - لا تسبوا الأموات)','2565','3941','0');
INSERT INTO [t38171] VALUES (4091,'٤٠٣٨ - (وقال - صلى الله عليه وسلم - لا تذكروا موتاكم إلا بخير فإنهم إن يكونوا من أهل الجنة تأثموا وإن يكونوا من أهل النار فحسبهم ما هم فيه).','2565','3941','0');
INSERT INTO [t38171] VALUES (4092,'٤٠٣٩ - وقال أنس بن مالك رضي الله عنه: (مرت جنازة على رسول الله - صلى الله عليه وسلم - فأثنوا عليه شرا فقال) (وجبت ومروا بأخرى فأثنوا عليها خيرا فقال وجبت فسأله عمر) رضي الله عنه (عن ذلك فقال إن هذا أثنيتم عليه خيرا فوجبت له الجنة وهذا أثنيتم عليه شرا فوجبت له النار وأنتم شهداء الله في الأرض).','2566','3941','0');
INSERT INTO [t38171] VALUES (4093,'٤٠٤٠ - وقال أبو هريرة رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - إن العبد ليموت فيثني عليه القوم الثناء يعلم الله منه غيره فيقول الله تعالى لملائكته أشهدكم أني قد قبلت شهادة عبيدي على عبدي وتجاوزت عن علمي في عبدي منه).','2566','3941','0');
INSERT INTO [t38171] VALUES (4094,'٤٠٤١ - (فقال - صلى الله عليه وسلم - والذي نفسي بيده إنهم لأسمع لهذا الكلام منكم إلا أنهم لا يقدرون على الجواب).','2567','3941','0');
INSERT INTO [t38171] VALUES (4095,'٤٠٤٢ - (قال - صلى الله عليه وسلم - القبر إما حفرة من حفر النار أو روضة من رياض الجنة)','2568','3941','0');
INSERT INTO [t38171] VALUES (4096,'٤٠٤٣ - (وقال - صلى الله عليه وسلم - إذا مات أحدكم عرض عليه مقعده غدوة وعشية إن كان من أهل الجنة فمن أهل الجنة وإن كان من أهل النار فمن أهل النار).','2568','3941','0');
INSERT INTO [t38171] VALUES (4097,'٤٠٤٤ - وقال أبو هريرة رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - من مات مريضا شهيدا وقي فتاني القبر وغدي وريح عليه برزقه من الجنة).','2569','3941','0');
INSERT INTO [t38171] VALUES (4098,'٤٠٤٥ - قال عبد الله بن عمرو بن العاص رضي الله عنهما: (إنما مثل المؤمن حين تخرج نفسه أو) قال (روحه) شك من الراوي (مثل رجل كان في سجن فأخرج منه فهو يتفسح في الأرض ويتقلب فيها)','2570','3941','0');
INSERT INTO [t38171] VALUES (4099,'٤٠٤٦ - روي عن عائشة رضي الله عنها: (أنها قالت قال رسول الله - صلى الله عليه وسلم - لجابر)','2570','3941','0');
INSERT INTO [t38171] VALUES (4100,'٤٠٤٧ - (وقد ضرب له رسول الله - صلى الله عليه وسلم - مثلا فقال لرجل مات أصبح هذا مرتحلا عن الدنيا وتركها لأهلها فإن كان قد رضي فلا يسره أن يرجع إلى الدنيا كما لا يسر أحدكم أن يرجع إلى بطن أمه).','2571','3941','0');
INSERT INTO [t38171] VALUES (4101,'٤٠٤٨ - (وقال - صلى الله عليه وسلم - إن مثل المؤمن في الدنيا كمثل الجنين في بطن أمه إذا خرج من بطنها بكى على مخرجه حتى إذا رأى الضوء ورضع لم يحب أن يرجع إلى مكانه وكذلك المؤمن يجزع من الموت فإذا أفضى إلى ربه لم يحب أن يرجع إلى الدنيا كما لا يحب الجنين أن يرجع إلى بطن أمه).','2571','3941','0');
INSERT INTO [t38171] VALUES (4102,'٤٠٤٩ - (وقيل لرسول الله - صلى الله عليه وسلم - إن فلانا قد مات فقال مستريح أو مستراح منه).','2572','3941','0');
INSERT INTO [t38171] VALUES (4103,'٤٠٥٠ - وقال النعمان بن بشير الأنصاري رضي الله عنهما: (سمعت رسول الله - صلى الله عليه وسلم - على المنبر يقول ألا إنه لم يبق من الدنيا إلا مثل الذباب تمور) أي تضطرب (في جوها) وهو ما بين السماء والأرض (فالله الله في إخوانكم من أهل القبور فإن أعمالكم تعرض عليهم).','2573','3941','0');
INSERT INTO [t38171] VALUES (4104,'٤٠٥١ - وقال أبو هريرة رضي الله عنه: (قال النبي - صلى الله عليه وسلم - لا تفضحوا','2573','3941','0');
INSERT INTO [t38171] VALUES (4105,'٤٠٥٢ - وقال أبو سعيد الخدري رضي الله عنه: (سمعت','2574','3941','0');
INSERT INTO [t38171] VALUES (4106,'٤٠٥٣ - (وروى أبو أيوب) خالد بن يزيد بن كليب (الأنصاري) البدري رضي الله عنه (عن النبي - صلى الله عليه وسلم - أنه قال أن نفس المؤمن إذا قبضت تلقاها أهل الرحمة من عند الله) كذا في النسخ والصواب من عباد الله (كما يتلقى البشير في الدنيا فيقولون انظروا أخاكم) وفي لفظ صاحبكم والأنظار الأمهال (حتى يستريح فإنه كان في كرب شديد فيسألونه ماذا فعل فلان وماذا فعلت فلانة وهل تزوجت فلانة فإذا سألوه عن رجل مات قبله وقال مات قبلي قالوا إنا لله وإنا إليه راجعون ذهب به إلى أمه الهاوية).','2576','3941','0');
INSERT INTO [t38171] VALUES (4107,'٤٠٥٤ - (قال رسول الله - صلى الله عليه وسلم - يقول القبر للميت حين يوضع فيه ويحك يا ابن آدم ما غرك بي ألم تعلم أني بيت الفتنة وبيت الظلمة وبيت الوحدة وبيت الدود ما غرك بي إن كنت تمر بي فدادا فإن كان مصلحا أجاب عنه مجيب القبر فيقول أرأيت إن كان يأمر بالمعروف وينهى عن المنكر فيقول القبر إني إذا أتحول عليه خضرا ويعود جسده عليه نورا وتصعد روحه إلى الله تعالى)','2579','3941','0');
INSERT INTO [t38171] VALUES (4108,'٤٠٥٥ - (وقال عبد الله بن عبيد بن عمير)','2580','3941','0');
INSERT INTO [t38171] VALUES (4109,'٤٠٥٦ - روى ابن أبي شيبة عن يزيد بن شجرة قال','2581','3941','0');
INSERT INTO [t38171] VALUES (4110,'٤٠٥٧ - يفسح له في قبره مد بصره','2582','3941','0');
INSERT INTO [t38171] VALUES (4111,'٤٠٥٨ - قال أبو هريرة:','2583','3941','0');
INSERT INTO [t38171] VALUES (4112,'٤٠٥٩ - قال العراقي:','2584','3941','0');
INSERT INTO [t38171] VALUES (4113,'٤٠٦٠ - قال أبو هريرة رضي الله عنه: (قال النبي - صلى الله عليه وسلم - المؤمن في قبره في روضة خضراء ويرحب) أي يوسع (له قبره سبعين ذراعا) وفي بعض النسخ في قبره سبعون ذراعا (ويضيء حتى يكون كالقمر ليلة البدر هل تدرون فيماذا أنزلت فإن له معيشة ضنكا قالوا الله ورسوله أعلم قال في عذاب الكافر في قبره يسلط عليه تسعة وتسعون تنينا هل تدرون ما التنين تسعة وتسعون حية لكل حية سبعة رؤس يخدشونه ويلحسونه وينفخون في جسمه إلى يبوم يبعثون)','2589','3941','0');
INSERT INTO [t38171] VALUES (4114,'٤٠٦١ - (قوله - صلى الله عليه وسلم - صاحب الدرهم أخف حسابا من صاحب الدرهمين).','2590','3941','0');
INSERT INTO [t38171] VALUES (4115,'٤٠٦٢ - قال أبو هريرة رضي الله عنه: (قال النبي - صلى الله عليه وسلم - إذا مات العبد) وفي رواية إذا قبر الميت (أتاه ملكان أسودان أزرقان يقال لأحدهما منكر وللآخر نكير فيقولان له ما كنت تقول في النبي)','2590','3941','0');
INSERT INTO [t38171] VALUES (4116,'٤٠٦٣ - (عن عطاء بن يسار) الهلالي أبي محمد المدني مولى ميمونة (قال قال رسول الله - صلى الله عليه وسلم - لعمر بن الخطاب رضي الله عنه يا عمر كيف بك إذا أنت مت فانطلق بك قومك فقاسوا لك ثلاثة أذرع في ثلاثة أذرع)','2593','3941','0');
INSERT INTO [t38171] VALUES (4117,'٤٠٦٤ - وأما حديث أنس فأخرج الشيخان وغيرهما من طريق قتادة عنه قال','2595','3941','0');
INSERT INTO [t38171] VALUES (4118,'٤٠٦٥ - قال أبو هريرة رضي الله عنه:','2603','3941','0');
INSERT INTO [t38171] VALUES (4119,'٤٠٦٦ - قال ابن شاهين في السنة:','2604','3941','0');
INSERT INTO [t38171] VALUES (4120,'٤٠٦٧ - عن حذيفة بن اليمان رضي الله عنهما: (قال كنا مع','2604','3941','0');
INSERT INTO [t38171] VALUES (4121,'٤٠١٨ - (قالت عائشة رضي الله عنها قال رسول الله - صلى الله عليه وسلم - إن للقبر ضغطة ولو سلم منها أو نجا منها أحد لنجا سعد بن معاذ).','2605','3941','0');
INSERT INTO [t38171] VALUES (4122,'٤٠٦٩ - عن أنس رضي الله عنه: (قال توفيت زينب بنت رسول الله - صلى الله عليه وسلم -)','2607','3941','0');
INSERT INTO [t38171] VALUES (4123,'٤٠٧٠ - (أمر رسول الله - صلى الله عليه وسلم - بالطهارة عند النوم لينام طاهرا).','2609','3941','0');
INSERT INTO [t38171] VALUES (4124,'٤٠٧١ - (انكشف دخول مكة لرسول الله - صلى الله عليه وسلم - في النوم','2609','3941','0');
INSERT INTO [t38171] VALUES (4125,'٤٠٧٢ - (قال - صلى الله عليه وسلم - من رآني) أي في نومه (فقد رآني حقا فإن الشيطان لا يتمثل بي).','2610','3941','0');
INSERT INTO [t38171] VALUES (4126,'٤٠٧٣ - (قال عمر بن الخطاب رضي الله عنه رأيت رسول الله - صلى الله عليه وسلم - في المنام فرأيته لا ينظر إلي فقلت يا رسول الله ما شأني فالتفت إلى وقال ألست المقبل وأنت صائم قال والذي نفسي بيده لا أقبل امرأة وأنا صائم أبدا)','2612','3941','0');
INSERT INTO [t38171] VALUES (4127,'٤٠٧٤ - (قال بعض الشيوخ رأيت رسول الله - صلى الله عليه وسلم -) في النوم (فقلت يا رسول الله استغفر لي فأعرض عني فقلت يا رسول الله إن سفيان بن عيينة حدثنا عن محمد بن المنكدر)','2612','3941','0');
INSERT INTO [t38171] VALUES (4128,'٤٠٧٥ - قال أبو محمد خلف بن عمر العكبري في فوائده:','2612','3941','0');
INSERT INTO [t38171] VALUES (4129,'٤٠٧٦ - (رؤي مالك بن أنس) الإمام رحمه الله تعالى في النوم (فقيل له ما فعل الله بك قال غفر لي بكلمة كان يقولها عثمان بن عفان رضي الله عنه عند رؤية الجنازة سبحان الحي الذي لا يموت)','2614','3941','0');
INSERT INTO [t38171] VALUES (4130,'٤٠٧٧ - (رأى رجل من التابعين النبي - صلى الله عليه وسلم - في المنام فقال يا رسول الله عظني قال نعم من لم يتفقد النقصان فهو في نقصان ومن كان في نقصان فالموت خير له)','2615','3941','0');
INSERT INTO [t38171] VALUES (4131,'٤٠٧٨ - حديث إلهي: كذبني ابن آدم وشتمني.','2615','3941','0');
INSERT INTO [t38171] VALUES (4132,'٤٠٧٩ - (قال رسول الله - صلى الله عليه وسلم - كيف أنعم وصاحب الصور قد التقم القرن وحني الجبهة) أي أمالها (وأصغى بالأذن) ليستمع (متى يؤمر) بالنفخ (فينفخ).','2615','3941','0');
INSERT INTO [t38171] VALUES (4133,'٤٠٨٠ - (قال مقاتل) بن سليمان بن بشير الأزدي البلخي أبو بسطام صدوق فاضل روى له أبو داود في كتاب المسائل له (الصور هو القرن وذلك أن إسرافيل واضع فاه على القرن كهيئة البوق ودائرة رأس القرن كعرض السماوات والأرض وهو شاخص بصره نحو العرش ينتظر متى يؤمر فينفخ النفخة الأولى فإذا نفخ صعق من في السماوات ومن في الأرض أي مات كل حيوان من شدة الفزع إلا من شاء الله وهو جبريل وميكائيل وإسرافيل وملك الموت ثم يأمر ملك الموت أن يقبض روح جبريل ثم روح ميكائيل ثم روح إسرافيل ثم يآمر ملك الموت فيموت ثم يلبث الخلق بعد النفخة الأولى في البرزخ أربعين سنة ثم يحيي','2616','3941','0');
INSERT INTO [t38171] VALUES (4134,'٤٠٨١ - (قال - صلى الله عليه وسلم - حين بعث إلى بعث إلى صاحب الصور فأهوى به إلى فمه وقدم رجلا وأخر أخرى ينتظر متى يؤمر بالنفخ ألا فاتقوا النفخة).','2621','3941','0');
INSERT INTO [t38171] VALUES (4135,'٤٠٨٢ - (قال رسول الله - صلى الله عليه وسلم - يحشر الناس يوم القيامة على أرض بيضاء عفراء كقرص) وفي لفظ كقرصة (النقي ليس فيها معلم لأحد).','2621','3941','0');
INSERT INTO [t38171] VALUES (4136,'٤٠٨٣ - (قال رسول الله - صلى الله عليه وسلم - يبعث الناس حفاة عراة غرلا قد ألجمهم العرق وبلغ شحوم الأذان قالت سودة زوج النبي - صلى الله عليه وسلم - راوية الحديث)','2622','3941','0');
INSERT INTO [t38171] VALUES (4137,'٤٠٨٤ - قال أبو هريرة رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - يحشر الناس يوم القيامة ثلاثة أصناف ركبانا ومشاة وعلى وجوههم فقال رجل يا رسول الله وكيف يمشون على وجوههم قال الذي أمشاهم على أقدامهم قادر أن يمشيهم على وجوههم).','2624','3941','0');
INSERT INTO [t38171] VALUES (4138,'٤٠٨٥ - قال ابن عمر رضي الله عنهما: (قال رسول الله - صلى الله عليه وسلم - يوم يقوم الناس لرب العالمين حتى يغيب أحدهم في رشحه إلى أنصاف أذنيه).','2624','3941','0');
INSERT INTO [t38171] VALUES (4139,'٤٠٨٦ - (وفي حديث آخر قياما شاخصة أبصارهم أربعين سنة إلى السماء فيلجمهم العرق من شدة الكرب).','2625','3941','0');
INSERT INTO [t38171] VALUES (4140,'٤٠٨٧ - قال عقبة بن عامر رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - تدنو الشمس من الأرض يوم القيامة فيغرق الناس فمن الناس من يبلغ عرقه عقبه ومنهم من يبلغ نصف ساقه ومنهم من يبلغ ركبتيه ومنهم من يبلغ فخذه ومنهم من يبلغ فاه وأشار بيده فألجمها فاه)','2626','3941','0');
INSERT INTO [t38171] VALUES (4141,'٤٠٨٨ - قال عبد الله بن عمر: هكذا في النسخ وفي بعضها عبد الله بن عمرو (تلا رسول الله - صلى الله عليه وسلم - هذه الآية) يعني قوله تعالى يوم يقوم الناس لرب العالمين (ثم قال كيف بكم إذا جمعكم الله كما تجمع النبل في الكنانة خمسين ألف سنة لا ينظر إليكم).','2627','3941','0');
INSERT INTO [t38171] VALUES (4142,'٤٠٨٩ - (قال رسول الله - صلى الله عليه وسلم - لما سئل عن طول ذلك اليوم فقال والذي نفسي بيده إنه ليخفف على المؤمن حتى يكون أهون عليه من الصلاة المكتوبة يصليها في الدنيا)','2628','3941','0');
INSERT INTO [t38171] VALUES (4143,'٤٠٩٠ - (قال رسول الله - صلى الله عليه وسلم - إن لله عز وجل ملكا بين شطري عينيه) أى طرفيهما (مسيرة مائة عام).','2628','3941','0');
INSERT INTO [t38171] VALUES (4144,'٤٠٩١ - قال أبو هريرة رضي الله عنه: (قالوا يا رسول الله هل نرى ربنا يوم القيامة فقال هل تضارون)','2629','3941','0');
INSERT INTO [t38171] VALUES (4145,'٤٠٩٢ - قال أنس رضي الله عنه: (كنا مع رسول الله - صلى الله عليه وسلم - فضحك ثم قال أتدرون ممن أضحك قالوا الله ورسوله أعلم قال من مخاطبة العبد ربه يقول يا رب ألم تجرني من الظلم قال يقول بلى قال فيقول فإني لا أجيز على نفسي إلا شاهدا مني فيقول كفى بنفسك اليوم عليك شهيدا وبالكرام الكاتبين شهودا قال فيختم على فيه ويقال لأركانه انطقي قال فتنطق بأعماله ثم يخلى بينه وبين الكلام فيقول لأعضائه بعدا لكن وسحقا فعنكن كنت أناضل)','2630','3941','0');
INSERT INTO [t38171] VALUES (4146,'٤٠٩٣ - (قال رسول الله - صلى الله عليه وسلم - يدنو أحدكم من ربه حتي يضع كنفه عليه فيقول عملت كذا وكذا فيقول نعم فيقول عملت كذا وكذا فيقول نعم ثم يقول إني سترتها) عليك (في الدنيا وإني أغفرها لك اليوم).','2631','3941','0');
INSERT INTO [t38171] VALUES (4147,'٤٠٩٤ - (قال رسول الله - صلى الله عليه وسلم - ما منكم من أحد إلا ويسأله الله رب العالمين ليس بينه وبينه حجاب ولا ترجمان).','2631','3941','0');
INSERT INTO [t38171] VALUES (4148,'٤٠٩٥ - (قال رسول الله - صلى الله عليه وسلم - ليقفن أحدكم بين يدي الله','2631','3941','0');
INSERT INTO [t38171] VALUES (4149,'٤٠٩٦ - قال حنبل بن إسحاق:','2632','3941','0');
INSERT INTO [t38171] VALUES (4150,'٤٠٩٧ - روى الحسن البصري رحمه الله: (أن رسول الله - صلى الله عليه وسلم - كان رأسه في حجر عائشة رضي الله عنها فنعس فذكرت الآخرة فبكت حتى سال دمعها فنقط على خد رسول الله - صلى الله عليه وسلم - فانتبه فقال ما يبكيك يا عائشة قالت ذكرت الآخرة هل تذكرون أهليكم يوم القيامة قال والذي نفسي بيده في ثلاث مواطن فإن أحدا لا يذكر إلا نفسه إذا وضعت الموازين ووزنت الأعمال حتى ينظر ابن ادم أتخف ميزانه أم تثقل وعند الصحف حتى ينظر أبيمينه يأخذ كتابه أو بشماله وعند الصراط).','2633','3941','0');
INSERT INTO [t38171] VALUES (4151,'٤٠٩٨ - وعن أنس رضي الله عنه: (قال يؤتى بابن آدم يوم القيامة حتى يوقف بين كفتي الميزان ويوكل به ملك فإن ثقل ميزانه نادى الملك بصوت يسمع الخلائق سعد فلان سعادة لا يشقى بعدها أبدا وإن خفت ميزانه نادى بصوت يسمع الخلائق شقي فلان شقاوة لا يسعد بعدها أبدا وعند خفة كفة','2634','3941','0');
INSERT INTO [t38171] VALUES (4152,'٤٠٩٩ - (قال رسول الله - صلى الله عليه وسلم - في يوم القيامة إنه يوم ينادي الله تعالى فيه آدم عليه السلام فيقول قم يا آدم فابعث بعث النار فيقول من كل ألف تسعمائة وتسعة وتسعون فلما سمع الصحابة ذلك أبلسوا حتى ما أوضحوا بضاحكة فلما رأى رسول الله - صلى الله عليه وسلم - الذي عند أصحابه قال اعملوا وبشروا فوالذي نفس محمد بيده إن معكم لخليقتين ما كانتا مع أحد قط إلا كثرتاه مع من هلك من بني آدم وبني إبليس قالوا وما هما يا رسول الله قال يأجوج ومأجوج قال فسري عن القوم فقال اعملوا وبشروا فوالذي نفس محمد بيده ما أنتم في الناس يوم القيامة إلا كالشامة في جنب البعير أو كالرقمة في ذراع الدابة).','2636','3941','0');
INSERT INTO [t38171] VALUES (4153,'٤١٠٠ - روى أبو ذر الغفاري رضي الله عنه (أن رسول الله - صلى الله عليه وسلم - رأى شاتين ينتطحان فقال يا أبا ذر أتدري فيم ينتطحان قلت لا قال ولكن الله يدري وسيقضي بينهما يوم القيامة).','2637','3941','0');
INSERT INTO [t38171] VALUES (4154,'٤١٠١ - (قال رسول الله - صلى الله عليه وسلم - إن الشيطان قد أيس أن تعبد الأصنام بأرض العرب ولكن سيرضى منكم بما هو دون ذلك بالمحقرات وهي الموبقات فاتقوا الظلم ما استطعتم فإن العبد يجيء يوم القيامة بأمثال الجبال من الطاعات فيرى أنهن ستنجيه فما يزال عبد يجيء فيقول يا رب إن فلانا ظلمني بمظلمة فيقول امح من حسناته فما يزال كذلك حتى ما يبقى من حسناته شيء وإن مثل ذلك مثل سفر نزلوا بفلاة من الأرض','2637','3941','0');
INSERT INTO [t38171] VALUES (4155,'٤١٠٢ - (ولما نزل قوله تعالى إنك ميت وإنهم ميتون ثم إنكم يوم القيامة عند ربكم تختصمون قال الزبير) بن العوام رضي الله عنه (يا رسول الله أيكرر علينا ما كان بيننا في الدنيا مع خواص الذنوب قال نعم ليكررن عليكم) ذلك (حتى تؤدوا إلى كل ذي حق حقه قال الزبير والله إن الأمر لشديد).','2639','3941','0');
INSERT INTO [t38171] VALUES (4156,'٤١٠٣ - قال أنس رضي الله عنه: (هكذا في سائر النسخ وهو غلط صوابه عبد الله بن أنيس كما سيأتي (سمعت رسول الله - صلى الله عليه وسلم - يقول يحشر الله العباد عراة غبرا بهما قال قلنا ما بهما','2639','3941','0');
INSERT INTO [t38171] VALUES (4157,'٤١٠٥ - روي عن أنس رضي الله عنه: (عن رسول الله - صلى الله عليه وسلم - إنه قال بينما رسول الله - صلى الله عليه وسلم - جالس إذ رأيناه يضحك حتى بدت ثناياه فقال عمر)','2643','3941','0');
INSERT INTO [t38171] VALUES (4158,'٤١٠٦ - (قال رسول الله - صلى الله عليه وسلم - يضرب الصراط بين ظهري)','2644','3941','0');
INSERT INTO [t38171] VALUES (4159,'٤١٠٧ - قال أبو سعيد الخدري رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - يمر الناس على جسر جهنم وعليه حسك وكلاليب وخطاطيف تخطف الناس يمينا وشمالا وعلى جنبيه)','2645','3941','0');
INSERT INTO [t38171] VALUES (4160,'٤١٠٨ - وعن ابن مسعود رضي الله عنه:','2647','3941','0');
INSERT INTO [t38171] VALUES (4161,'٤١٠٩ - قال أنس بن مالك رضي الله عنه: (سمعت رسول الله - صلى الله عليه وسلم - يقول الصراط كحد السيف أو كحد الشعرة وإن الملائكة ينجون المؤمنين والمؤمنات وإن جبريل عليه السلام لآخذ بحجزتي وإني لأقول يا رب سلم سلم فالزالون والزالات يومئذ كثير).','2648','3941','0');
INSERT INTO [t38171] VALUES (4162,'٤١١٠ - وروى عمرو بن العاص رضي الله عنه:','2650','3941','0');
INSERT INTO [t38171] VALUES (4163,'٤١١١ - حديث أعطيت خمسا','2651','3941','0');
INSERT INTO [t38171] VALUES (4164,'٤١١٢ - (قال - صلى الله عليه وسلم - إذا كان يوم القيامة كنت إمام النبيين وخطيبهم وصاحب شفاعتهم من غير فخر).','2652','3941','0');
INSERT INTO [t38171] VALUES (4165,'٤١١٣ - (قال - صلى الله عليه وسلم - أنا سيد ولد آدم)','2653','3941','0');
INSERT INTO [t38171] VALUES (4166,'٤١١٤ - (قال - صلى الله عليه وسلم - لكل نبي دعوة مستجابة فأريد أن','2653','3941','0');
INSERT INTO [t38171] VALUES (4167,'٤١١٥ - قال ابن عباس رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - تنصب للأنبياء منابر من ذهب فيجلسون عليها ويبقى منبري)','2654','3941','0');
INSERT INTO [t38171] VALUES (4168,'٤١١٦ - (قال - صلى الله عليه وسلم - إني لأشفع يوم القيامة لأكثر مما على وجه الأرض من حجر ومدر).','2655','3941','0');
INSERT INTO [t38171] VALUES (4169,'٤١١٧ - (وفي حديث آخر هذا السياق بعينه مع ذكر خطايا إبراهيم عليه السلام وهو قوله في الكوكب هذا ربي وقوله لآلهتهم بل فعله كبيرهم وقوله إني سقيم).','2656','3941','0');
INSERT INTO [t38171] VALUES (4170,'٤١١٨ - حديث يدخل الجنة بشفاعة رجل من أمتي مثل مضر.','2659','3941','0');
INSERT INTO [t38171] VALUES (4171,'٤١١٩ - (قال - صلى الله عليه وسلم - يقال للرجل قم يا فلان فاشفع فيقوم الرجل فيشفع للقبيلة ولأهل البيت وللرجل والرجلين على قدر عمله).','2660','3941','0');
INSERT INTO [t38171] VALUES (4172,'٤١٢٠ - قال أنس رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - إن رجلا من أهل الجنة يشرف يوم القيامة على أهل النار فيناديه رجل من أهل النار ويقول يا فلان هل تعرفني فيقول لا والله ما أعرفك من أنت فيقول أنا الذي مررت بي في الدنيا فاستسقيتني شربة ماء فسقيتك قال قد عرفت قال فاشفع لي بها عند ربك فيسأل الله تعالى ذكره ويقول إني أشرفت على أهل النار فناداني رجل من أهلها فقال هل تعرفني فقلت لا من أنت فقال أنا الذي استسقيتني في الدنيا فسقيتك فاشفع لي عند ربك فشفعني فيه فشفعه الله فيه فيؤمر به فيخرج من النار).','2661','3941','0');
INSERT INTO [t38171] VALUES (4173,'٤١٢١ - عن أنس رضي الله عنه (قال قال رسول الله - صلى الله عليه وسلم - أنا أول الناس خروجا إذا بعثوا وأنا خطيبهم إذا وفدوا وأنا مبشرهم إذا يئسوا لواء الحمد يومئذ بيدي وأنا أكرم ولد آدم','2661','3941','0');
INSERT INTO [t38171] VALUES (4174,'٤١٢٣ - قال ابن عباس رضي الله عنهما: (جلس ناس من أصحاب رسول الله - صلى الله عليه وسلم - ينتظرونه فخرج حتى إذا دنا منهم سمعهم يتذاكرون فسمع حديثهم فقال بعضهم عجبا إن الله عز وجل اتخذ من خلقه خليلا اتخذ إبراهيم خليلا وقال الآخر ماذا بأعجب من كلام موسى كلمه تكليما وقال الآخر فعيسى كلمة الله وروحه وقال آخر آدم اصطفاه الله فخرج عليهم فسلم وقال قد سمعت كلامكم وعجبكم إن إبراهيم خليل الله وهو كذلك وموسى نجي الله وهو كذلك وعيسى روح الله وكلمته وهو كذلك وآدم اصطفاه الله وهو كذلك ألا وأنا حبيب الله ولا فخر وأنا حامل لواء الحمد يوم القيامة ولا فخر وأنا أول شافع وأول مشفع يوم القيامة ولا فخر وأنا أول من يحرك حلق الجنة فيفتح الله لي فأدخلها ومعي فقراء المؤمنين ولا فخر وأنا أكرم الأولين والآخرين ولا فخر).','2662','3941','0');
INSERT INTO [t38171] VALUES (4175,'٤١٢٢ - (قال رسول الله - صلى الله عليه وسلم - فأكسى حلة من حلل الجنة ثم أقوم عن يمين العرش ليس أحد من الخلائق يقوم ذلك المقام غيري).','2663','3941','0');
INSERT INTO [t38171] VALUES (4176,'٤١٢٤ - قال أنس رضي الله عنه: (أغفى رسول الله - صلى الله عليه وسلم - إغفاءة فرفع رأسه متبسما فقالوا له يا رسول الله لم ضحكت فقال آية أنزلت علي آنفا وقرأ بسم الله الرحمن الرحيم إنا أعطيناك الكوثر حتى ختمها ثم قال هل تدرون ما الكوثر قالوا الله ورسوله أعلم قال إنه نهر وعدنيه ربي عز وجل في الجنة عليه خير كثير عليه حوض ترد عليه أمتي يوم القيامة آنيته عدد نجوم السماء).','2663','3941','0');
INSERT INTO [t38171] VALUES (4177,'٤١٢٥ - وقال أنس رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - بينما أنا أسير في الجنة إذا بنهر حافتاه قباب اللؤلؤ المجوف قلت ما هذا يا جبريل قال هذا الكوثر الذي أعطاك ربك فضرب الملك بيده فإذا طينه مسك أذفر).','2664','3941','0');
INSERT INTO [t38171] VALUES (4178,'٤١٢٦ - قال أنس أيضا: (كان رسول الله - صلى الله عليه وسلم - يقول ما بين لابتي حوضي مثل ما بين المدينة وصنعاء أو مثل ما بين المدينة وعمان)','2665','3941','0');
INSERT INTO [t38171] VALUES (4179,'٤١٢٧ - (وروى ابن عمر رضي الله عنه أنه لما نزل قوله تعالى إنا أعطيناك الكوثر قال رسول الله - صلى الله عليه وسلم - هو نهر في الجنة حافتاه من ذهب شرابه أشد بياضا من اللبن وأحلى من العسل وأطيب ريحا من المسك يجري على جنادل اللؤلؤ والمرجان).','2666','3941','0');
INSERT INTO [t38171] VALUES (4180,'٤١٢٨ - وقال ثوبان بن بجدد (مولى رسول الله - صلى الله عليه وسلم - قال رسول الله - صلى الله عليه وسلم - إن حوضي ما بين عدن إلى عمان البلقاء ماؤه أشد بياضا من اللبن وأحلى من العسل وأكوابه عدد نجوم السماء من شرب منه شربة لم يظمأ بعدها أبدا أول الناس ورودا عليه','2666','3941','0');
INSERT INTO [t38171] VALUES (4181,'٤١٢٩ - (عن أبي ذر) رضي الله عنه (قال قلت يا رسول الله ما آنية الحوض قال والذي نفس محمد بيده لآنيته أكثر من عدد نجوم السماء وكواكبها في الليلة المظلمة والمصحية من شرب منه لم يظمأ آخر ما عليه يشخب فيه ميزابان من الجنة عرضه مثل طوله ما بين عمان وأيلة ماؤه أشد بياضا من اللبن وأحلى من العسل).','2668','3941','0');
INSERT INTO [t38171] VALUES (4182,'٤١٣٠ - وأما حديث عبد الله بن عمرو رضي الله عنه','2668','3941','0');
INSERT INTO [t38171] VALUES (4183,'٣١٣١ - وأما حديث جابر بن سمرة رضي الله عنه','2669','3941','0');
INSERT INTO [t38171] VALUES (4184,'٤١٣٢ - وأما حديث جابر بن عبد الله الأنصاري رضي الله عنه','2671','3941','0');
INSERT INTO [t38171] VALUES (4185,'٤١٣٣ - وأما حديث أبي هريرة فرواه أبو طاهر المخلص','2672','3941','0');
INSERT INTO [t38171] VALUES (4186,'٤١٣٤ - وأما حديث أبي سعيد الخدري','2672','3941','0');
INSERT INTO [t38171] VALUES (4187,'٤١٣٥ - وأما حديث بريدة بن الحصيب رضي الله عنه','2673','3941','0');
INSERT INTO [t38171] VALUES (4188,'٤١٣٦ - وأما حديث ابن مسعود رضي الله عنه','2674','3941','0');
INSERT INTO [t38171] VALUES (4189,'٤١٣٧ - وأما حديث جندب بن عبد الله البجلي رضي الله عنه','2674','3941','0');
INSERT INTO [t38171] VALUES (4190,'٤١٣٨ - وأما حديث جابر بن سمرة رضي الله عنه','2675','3941','0');
INSERT INTO [t38171] VALUES (4191,'٤١٣٩ - وأما حديث أبي برزة الأسلمي','2675','3941','0');
INSERT INTO [t38171] VALUES (4192,'٤١٤٠ - وأما حديث أبي أمامة الباهلي رضي الله عنه','2676','3941','0');
INSERT INTO [t38171] VALUES (4193,'٤١٤١ - وأما حديث ابن عباس رضي الله عنه','2676','3941','0');
INSERT INTO [t38171] VALUES (4194,'٤١٤٢ - وأما حديث عتبة بن عبد السلمي رضي الله عنه','2677','3941','0');
INSERT INTO [t38171] VALUES (4195,'٤١٤٣ - وأما حديث حارثة بن وهب الخزاعي رضي الله عنه','2677','3941','0');
INSERT INTO [t38171] VALUES (4196,'٤١٤٤ - وأما حديث المستورد بن شداد الفهري رضي الله عنه','2678','3941','0');
INSERT INTO [t38171] VALUES (4197,'٤١٤٥ - وأما حديث عائشة رضي الله عنها','2679','3941','0');
INSERT INTO [t38171] VALUES (4198,'٤١٤٦ - وأما حديث أبي لبابة رضي الله عنه','2679','3941','0');
INSERT INTO [t38171] VALUES (4199,'٤١٤٧ - وأما حديث البراء بن عازب رضي الله عنه','2680','3941','0');
INSERT INTO [t38171] VALUES (4200,'٤١٤٨ - وأما حديث جبير بن مطعم رضي الله عنه','2680','3941','0');
INSERT INTO [t38171] VALUES (4201,'٤١٤٩ - وأما حديث أسامة بن زيد رضي الله عنه','2680','3941','0');
INSERT INTO [t38171] VALUES (4202,'٤١٥٠ - وأما حديث حمزة بن عبد المطلب رضي الله عنه','2681','3941','0');
INSERT INTO [t38171] VALUES (4203,'٤١٥١ - وأما حديث أم محمد خولة بنت قيس الأنصارية رضي الله عنها','2681','3941','0');
INSERT INTO [t38171] VALUES (4204,'٤١٥٢ - وأما حديث حذيفة بن أسيد رضي الله عنه','2681','3941','0');
INSERT INTO [t38171] VALUES (4205,'٤١٥٣ - وأما حديث خباب بن الأرت رضي الله عنه فرواه أحمد وابن أبي عاصم والطبراني كلاهما في كتاب السنة من طريق سماك بن حرب عن عبد الله بن خباب عن أبيه رفعه سيكون عليكم أمراء فلا تعينوهم على ظلمهم ولا تصدقوهم بكذبهم فإن من أعانهم على ظلمهم وصدقهم بكذبهم فلن يرد على الحوض.','2682','3941','0');
INSERT INTO [t38171] VALUES (4206,'٤١٥٤ - وأما حديث زيد بن الأرقم رضي الله عنه فقال','2682','3941','0');
INSERT INTO [t38171] VALUES (4207,'٤١٥٥ - وأما حديث أوس بن الأرقم رضي الله عنه وهو أخو','2683','3941','0');
INSERT INTO [t38171] VALUES (4208,'٤١٥٦ - وأما حديث زيد بن أبي أوفى رضي الله عنه وهو أخو عبد الله بن أبي أوفى','2684','3941','0');
INSERT INTO [t38171] VALUES (4209,'٤١٥٧ - وأما حديث زيد بن ثابت رضي الله عنه فرواه','2685','3941','0');
INSERT INTO [t38171] VALUES (4210,'٤١٥٨ - وأما حديث سويد بن عامر رضي الله عنه','2685','3941','0');
INSERT INTO [t38171] VALUES (4211,'٤١٥٩ - وأما حديث أبي بكرة رضي الله عنه','2685','3941','0');
INSERT INTO [t38171] VALUES (4212,'٤١٦٠ - وأما حديث أبي الدرداء رضي الله عنه','2685','3941','0');
INSERT INTO [t38171] VALUES (4213,'٤١٦٢ - وأما حديث سهل بن سعد رضي الله عنه','2686','3941','0');
INSERT INTO [t38171] VALUES (4214,'٤١٦٣ - وأما حديث أسماء بنت أبي بكر رضي الله عنها','2686','3941','0');
INSERT INTO [t38171] VALUES (4215,'٤١٦٤ - وأما حديث أم سلمة رضي الله عنها','2686','3941','0');
INSERT INTO [t38171] VALUES (4216,'٤١٦٥ - وأما حديث عقبة بن عامر رضي الله عنه','2686','3941','0');
INSERT INTO [t38171] VALUES (4217,'٤١٦٦ - وأما حديث الصنابحي رضي الله عنه','2687','3941','0');
INSERT INTO [t38171] VALUES (4218,'٤١٦٧ - وأما حديث علي رضي الله عنه','2687','3941','0');
INSERT INTO [t38171] VALUES (4219,'٤١٦٨ - (عن سمرة)','2688','3941','0');
INSERT INTO [t38171] VALUES (4220,'٤١٦٩ - ونقل صاحب المواهب عن أبي سعد في شرف النبوة والغيلاني من حديث أنس رفعه لحوضي أربعة','2688','3941','0');
INSERT INTO [t38171] VALUES (4221,'٤١٧٠ - (وقال النبي - صلى الله عليه وسلم - إن في جهنم سبعين ألف واد في كل واد سبعون ألف شعب في كل شعب سبعون ألف ثعبان وسبعون ألف عقرب لا ينتهي الكافر والمنافق حتى يواقع ذلك كله).','2689','3941','0');
INSERT INTO [t38171] VALUES (4222,'٤١٧١ - (وقال علي كرم الله وجهه قال رسول الله - صلى الله عليه وسلم - نعوذ بالله من جب الحزن أو)','2689','3941','0');
INSERT INTO [t38171] VALUES (4223,'٤١٧٢ - (قال أبو هريرة) رضي الله عنه (كنا مع رسول الله - صلى الله عليه وسلم - فسمعنا وجبة)','2690','3941','0');
INSERT INTO [t38171] VALUES (4224,'٤١٧٣ - (قال رسول الله - صلى الله عليه وسلم - إن أدنى أهل النار عذابا يوم القيامة ينتعل بنعلين من نار يغلي دماغه من حرارة نعليه).','2691','3941','0');
INSERT INTO [t38171] VALUES (4225,'٤١٧٤ - (قيل إن نار الدنيا غسلت بسبعين ماء من مياه الرحمة حتى أطاقها أهل الدنيا).','2691','3941','0');
INSERT INTO [t38171] VALUES (4226,'٤١٧٥ - (فقال أمر الله تعالى أن يوقد على النار ألف عام حتى احمرت ثم أوقد عليها ألف عام حتى ابيضت ثم أوقد عليها ألف عام حتى اسودت فهي سوداء مظلمة).','2692','3941','0');
INSERT INTO [t38171] VALUES (4227,'٤١٧٦ - (قال - صلى الله عليه وسلم - اشتكت النار إلى ربها فقالت يا رب أكل بعضي بعضا فأذن لها في نفسين نفس في الشتاء ونفس في الصيف فأشد ما تجدونه في الصيف من حرها وأشد ما تجدونه في الشتاء من زمهريرها).','2693','3941','0');
INSERT INTO [t38171] VALUES (4228,'٤١٧٧ - (قال أنس بن مالك) رضي الله عنه (يؤتى بأنعم الناس في الدنيا من الكفار فيقال اغمسوه في النار غمسة ثم يقال له هل رأيت نعيما قط فيقول لا ويؤتى بأشد الناس ضرا في الدنيا فيقال اغمسوه في الجنة غمسة ثم يقال له هل رأيت ضرا قط فيقول لا).','2694','3941','0');
INSERT INTO [t38171] VALUES (4229,'٤١٧٨ - (قال أبو هريرة) رضي الله عنه (لو كان في المسجد مائة ألف أو يزيدون ثم تنفس رجل من أهل النار لماتوا)','2694','3941','0');
INSERT INTO [t38171] VALUES (4230,'٤١٧٩ - (قال أبو سعيد الخدري) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - لو أن دلوا من غساق جهنم ألقي في الدنيا لأنتن أهل الأرض).','2695','3941','0');
INSERT INTO [t38171] VALUES (4231,'٤١٨٠ - (قال ابن عباس) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - لو أن قطرة من الزقوم)','2695','3941','0');
INSERT INTO [t38171] VALUES (4232,'٤١٨٨ - (وقال أنس) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - ارغبوا فيما رغبكم الله واحذروا وخافوا ما خوفكم الله به من عذابه وعقابه ومن جهنم فإنه لو كانت قطرة من الجنة معكم في دنياكم التي أنتم فيها حلتها لكم ولو كانت قطرة من النار معكم','2695','3941','0');
INSERT INTO [t38171] VALUES (4233,'٤١٨٢ - حديث مناداة أهل النار ملائكتها قال العراقي:','2696','3941','0');
INSERT INTO [t38171] VALUES (4234,'٤١٨٤ - (قال أبو أمامة) الباهلي رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - في قوله تعالى ويسقى من ماء صديد يتجرعه ولا يكاد يسيغه قال يقرب إليه)','2696','3941','0');
INSERT INTO [t38171] VALUES (4235,'٤١٨٤ - (قال أبو هريرة) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - من آتاه الله مالا فلم يؤد زكاته مثل له يوم القيامة شجاعا أقرع له زبيبتان يطوقه يوم القيامة ثم يأخذ بلهازمه أي أشداقه فيقول أنا مالك أنا كنزك ثم تلا قوله تعالى ولا يحسبن الذين يبخلون بما آتاهم الله من فضله الآية).','2697','3941','0');
INSERT INTO [t38171] VALUES (4236,'٤١٨٥ - (قال أبو هريرة) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - ضرس الكافر في النار مثل أحد) وهو الجبل المعروف (وغلظ جلده مسيرة ثلاث).','2697','3941','0');
INSERT INTO [t38171] VALUES (4237,'٤١٨٦ - (قال رسول الله - صلى الله عليه وسلم - شفته السفلى ساقطة على صدره والعليا قالصة قد غطت وجهه).','2698','3941','0');
INSERT INTO [t38171] VALUES (4238,'٤١٨٧ - (وقال - صلى الله عليه وسلم - إن الكافر ليجر لسانه في سجين يوم القيامة يتوطؤه الناس) أي يطؤنه بأرجلهم.','2698','3941','0');
INSERT INTO [t38171] VALUES (4239,'٤١٨٨ - (قال رسول الله - صلى الله عليه وسلم - يؤتى بجهنم يومئذ لها سبعون ألف زمام مع كل زمام سبعون ألف ملك).','2699','3941','0');
INSERT INTO [t38171] VALUES (4240,'٤١٨٩ - (قال أنس) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم -','2699','3941','0');
INSERT INTO [t38171] VALUES (4241,'٤١٩٠ - (قال - صلى الله عليه وسلم - يؤتى بالموت يوم القيامة كأنه كبش أملح فيذبح بين الجنة والنار ويقال يا أهل الجنة خلود لا موت ويا أهل النار خلود لا موت).','2700','3941','0');
INSERT INTO [t38171] VALUES (4242,'٤١٩١ - (قال رسول الله - صلى الله عليه وسلم - يؤمر يوم القيامة بناس من النار إلى الجنة حتى إذا دنوا منها)','2701','3941','0');
INSERT INTO [t38171] VALUES (4243,'٤١٩٢ - (قال أبو هريرة) رضي الله عنه (قال رسول الله - صلى الله عليه وسلم - ينادي مناد يوم القيامة إن لكم) يا أهل الجنة (أن تصحوا فلا تسقموا أبدا وإن لكم أن تحيوا فلا تموتوا أبدا وإن لكم أن تشبوا لا تهرموا أبدا وإن لكم أن تنعموا فلا تبأسوا أبدا فذلك قوله عز وجل ونودوا أن تلكم الجنة أورثتموها بما كنتم تعملون).','2702','3941','0');
INSERT INTO [t38171] VALUES (4244,'٤١٩٣ - (قال رسول الله - صلى الله عليه وسلم - في قوله تعالى ولمن خاف مقام ربه جنتان قال)','2702','3941','0');
INSERT INTO [t38171] VALUES (4245,'٤١٩٤ - وقال رسول الله - صلى الله عليه وسلم - آتي يوم القيامة باب الجنة فأستفتح فيقول الخازن من أنت فأقول محمد فيقول بك أمرت أن لا أفتح لأحد قبلك.','2703','3941','0');
INSERT INTO [t38171] VALUES (4246,'٤١٩٥ - قال أبو سعيد الخدري: قال رسول الله - صلى الله عليه وسلم - إن أهل الجنة ليتراءون الكوكب الغائر في الأفق من المشرق والمغرب لتفاضل ما بينهم قالوا يا رسول الله تلك منازل الأنبياء لا يبلغها غيرهم قال بلى والذي نفسي بيده رجال آمنوا بالله وصدقوا المرسلين.','2704','3941','0');
INSERT INTO [t38171] VALUES (4247,'٤١٩٦ - (وقال) - صلى الله عليه وسلم - (أيضا إن أهل الدرجات العلى ليراهم من تحتهم) منزلة (كما ترون النجم الطالع في أفق من آفاق السماء وإن أبا بكر وعمر) رضي الله عنهما (منهم وأنعما).','2704','3941','0');
INSERT INTO [t38171] VALUES (4248,'٤١٩٧ - وقال جابر بن عبد الله الأنصاري رضي الله عنه: (قال لنا رسول الله - صلى الله عليه وسلم - ألا أحدثكم بغرف الجنة قال قلت بلى يا رسول الله صلى الله عليك بأبينا أنت وأمنا قال إن في الجنة غرفا من أصناف الجوهر كله)','2705','3941','0');
INSERT INTO [t38171] VALUES (4249,'٤١٩٨ - سئل رسول الله - صلى الله عليه وسلم - عن قوله عز وجل: (ومساكن طيبة في جنات عدن قال قصور من لؤلؤ في كل قصر سبعون دارا من ياقوتة حمراء في كل دار سبعون بيتا من زمرد أخضر في كل بيت سرير على كل سرير سبعون فراشا من كل لون على كل فراش زوجة من الحور العين في كل بيت سبعون مائدة على كل مائدة سبعون لونا من الطعام في كل بيت سبعون وصيفة ويعطى المؤمن في كل غداة يعني من القوة ما يأتي على ذلك أجمع).','2707','3941','0');
INSERT INTO [t38171] VALUES (4250,'٤١٩٩ - قال أبو هريرة رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - إن حائط الجنة لبنة من فضة ولبنة من ذهب ترابها زعفران وطينها مسك).','2707','3941','0');
INSERT INTO [t38171] VALUES (4251,'٤٢٠٠ - (وسئل - صلى الله عليه وسلم - عن تربة الجنة فقال درمكة بيضاء مسك خالص).','2710','3941','0');
INSERT INTO [t38171] VALUES (4252,'٤٢٠١ - وقال أبو هريرة رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - من سره أن يسقيه الله عز وجل الخمر في الآخرة فليتركها في الدنيا ومن سره أن يكسوه الله الحرير في الآخرة فليتركه في الدنيا).','2711','3941','0');
INSERT INTO [t38171] VALUES (4253,'٤٢٠٢ - وقال أبو هريرة رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - إن في الجنة شجرة)','2712','3941','0');
INSERT INTO [t38171] VALUES (4254,'٤٢٠٣ - أقبل أعرابي من البادية فقال: (يا رسول الله قد ذكر الله في القرآن شجرة مؤذية وما كنت أرى أن في الجنة شجرة تؤذي صاحبها فقال رسول الله - صلى الله عليه وسلم - ما هي قال السدر فإن لها شوكا فقال) - صلى الله عليه وسلم - (قد قال الله تعالى في سدر مخضود) أي (يحصد الله شوكه) أي يكسره (فيجعل مكان كل شوكة ثمرة ثم تنفتق الثمرة منها عن اثنين وسبعين لونا من طعام ما منها لون يشبه الآخر).','2714','3941','0');
INSERT INTO [t38171] VALUES (4255,'٤٢٠٤ - روى أبو هريرة رضي الله عنه: (أن النبي - صلى الله عليه وسلم - قال من يدخل الجنة ينعم ولا يبأس لا تبلى ثيابه ولا يفنى شبابه في الجنة ما لا عين رأت ولا أذن سمعت ولا خطر على قلب بشر).','2714','3941','0');
INSERT INTO [t38171] VALUES (4256,'٤٢٠٥ - وقال رجل: (يا رسول الله أخبرنا عن ثياب أهل الجنة أخلق تخلق أم نسج تنسج)','2715','3941','0');
INSERT INTO [t38171] VALUES (4257,'٤٢٠٦ - (وقال - صلى الله عليه وسلم - في قوله تعالى) جنات عدن يدخلونها (يحلون فيها من أساور من ذهب قال إن عليهم التيجان إن أدنى لؤلؤة فيها تضيء ما بين المشرق والمغرب).','2716','3941','0');
INSERT INTO [t38171] VALUES (4258,'٤٢٠٧ - (وقال - صلى الله عليه وسلم - الخيمة)','2716','3941','0');
INSERT INTO [t38171] VALUES (4259,'٤٢٠٨ - وقال أبو سعيد الخدري رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - في قوله تعالى وفرش مرفوعة قال ما بين الفراش كما بين السماء والأرض).','2716','3941','0');
INSERT INTO [t38171] VALUES (4260,'٤٢٠٩ - (وقال ثوبان مولى رسول الله - صلى الله عليه وسلم - كنت قائما عند رسول الله - صلى الله عليه وسلم - فجاء حبر من أحبار اليهود فذكر أسئلة إلى أن قال فمن أول إجازة يعني على الصراط فقال فقراء المهاجرين قال اليهود فما تحفتهم حين يدخلون الجنة فقال زيادة كبد الحوت قال فما غذاؤهم على أثرها قال ينحر لهم ثور الجنة الذي كان يأكل في أطرافها قال فما شرابهم عليه قال من عين فيها تسمى سلسبيلا فقال صدقت).','2717','3941','0');
INSERT INTO [t38171] VALUES (4261,'٤٢١٠ - وعن يزيد بن الأرقم رضي الله عنه قال: (جاء رجل من اليهود إلى رسول الله - صلى الله عليه وسلم - وقال يا أبا القاسم ألست تزعم أن أهل الجنة يأكلون فيها ويشربون وقال لأصحابه أن أقر لي بها خصمته)','2717','3941','0');
INSERT INTO [t38171] VALUES (4262,'٤٢١١ - وقال ابن مسعود رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - إنك لتنظر إلى الطير في الجنة فتشتهيه فيخر بين يديك مشويا).','2719','3941','0');
INSERT INTO [t38171] VALUES (4263,'٤٢١٢ - وقال حذيفة رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - إن في الجنة طيرا أمثال البخاتي)','2720','3941','0');
INSERT INTO [t38171] VALUES (4264,'٤٢١٣ - قال عبد الله بن عمرو رضي الله عنهما (في قوله تعالى) يطاف عليهم بصحاف من ذهب (قال يطاف) عليهم (بسبعين صحفة من ذهب كل صحفة فيها لون ليس في الأخرى) ورواه الحاكم في المستدرك وصححه وروى ابن المبارك في','2720','3941','0');
INSERT INTO [t38171] VALUES (4265,'٤٢١٤ - روى أنس رضي الله عنه: (أن رسول الله - صلى الله عليه وسلم - قال غدوة في سبيل الله أو روحة خير من الدنيا وما فيها ولقاب قوس أحدكم أو موضع قدمه من الجنة خير من الدنيا وما فيها ولو أن امرأة من نساء أهل الجنة اطلعت إلى الأرض لأضاءت ولملأت ما بينهما رائحة ولنصيفها على رأسها خير من الدنيا بما فيها يعني الخمار)','2721','3941','0');
INSERT INTO [t38171] VALUES (4266,'٤٢١٥ - وقال أبو سعيد الخدري رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - في قوله تعالى كأنهن الياقوت والمرجان قال ينظر إلى وجهها في خدرها أصفى من المرآة وإن أدنى لؤلؤة عليها لتضيء ما بين المشرق والمغرب وإنه يكون عليها سبعون ثوبا ينفذها بصره حتى يرى مخ ساقها من وراء ذلك).','2722','3941','0');
INSERT INTO [t38171] VALUES (4267,'٤٢١٦ - وقال أنس رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - لما أسري بي دخلت الجنة موضعا يسمى البيدخ) كحيدر والدال مهملة وآخره خاء معجم اسم نهر في الجنة (عليه خيام اللؤلؤ والزبرجد الأخضر والياقوت الأحمر فقلن السلام عليك يا رسول الله فقلت يا جبريل ما هذا النداء قال هؤلاء المقصورات في الخيام استأذن ربهن في السلام عليك فأذن لهن فطفق يقلن نحن الراضيات فلا نسخط أبدا ونحن الخالدات فلا نظعن أبدا وقرأ رسول الله - صلى الله عليه وسلم - قوله تعالى حور مقصورات في الخيام).','2723','3941','0');
INSERT INTO [t38171] VALUES (4268,'٤٢١٧ - (وقال رجل يا رسول الله أيباضع أهل الجنة قال يعطي الرجل منهم من القوة في اليوم الواحد أفضل من سبعين منكم).','2727','3941','0');
INSERT INTO [t38171] VALUES (4269,'٤٢١٨ - وقال عبد الله بن عمر رضي الله عنهما: (إن أدنى أهل الجنة منزلة من يسعى معه ألف خادم كل خادم على عمل ليس عليه صاحبه)','2728','3941','0');
INSERT INTO [t38171] VALUES (4270,'٤٢١٩ - (وقال رسول الله - صلى الله عليه وسلم - إن الرجل من أهل الجنة ليتزوج)','2729','3941','0');
INSERT INTO [t38171] VALUES (4271,'٤٢٢٠ - (وقال النبي - صلى الله عليه وسلم - إن في الجنة سوقا)','2730','3941','0');
INSERT INTO [t38171] VALUES (4272,'٤٢٢١ - وقال أنس رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - إن الحور في الجنة يتغنين نحن الحور الحسان خبئنا) وفي نسخة خبئن وفي أخرى خلقن وفي أخرى خلقنا (لأزواج كرام).','2731','3941','0');
INSERT INTO [t38171] VALUES (4273,'٤٢٢٢ - وقال أبو أمامة الباهلي رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - ما من عبد يدخل الجنة إلا ويجلس عند رأسه وعند رجليه ثنتان من الحور العين تغنيانه بأحسن صوت سمعه الإنس والجن وليس بمزمار الشيطان ولكن بتحميد الله)','2731','3941','0');
INSERT INTO [t38171] VALUES (4274,'٤٢٢٣ - روى أسامة بن زيد بن شرحبيل رضي الله عنهما: (أن رسول الله - صلى الله عليه وسلم - قال لأصحابه ألا هل مشمر للجنة إن الجنة لا خطر لها)','2732','3941','0');
INSERT INTO [t38171] VALUES (4275,'٤٢٢٤ - (وجاء رجل إلى رسول الله - صلى الله عليه وسلم - وقال هل في الجنة خيل فإنها تعجبني قال إن أحببت ذلك أتيت بفرس من ياقوتة حمراء فتطير بك في الجنة حيث شئت وقال له رجل إن الإبل تعجبني فهل في الجنة من إبل فقال يا عبد الله إن أدخلت الجنة ذلك فيها ما اشتهت نفسك ولذت عيناك).','2733','3941','0');
INSERT INTO [t38171] VALUES (4276,'٤٢٢٥ - وقال أبو سعيد الخدري رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - إن الرجل من أهل الجنة ليولد له الولد كما يشتهي يكون حمله وفصاله وشبابه)','2734','3941','0');
INSERT INTO [t38171] VALUES (4277,'٤٢٢٦ - (وقال - صلى الله عليه وسلم - إذا استقر أهل الجنة في الجنة اشتاق الإخوان إلى الإخوان فيسير سرير هذا إلى سرير هذا فيلتقيان ويتحدثان ما كان بينهما في دار الدنيا فيقول يا أخي تذكر يوم كذا في مجلس كذا فدعونا الله عز وجل فغفر لنا).','2734','3941','0');
INSERT INTO [t38171] VALUES (4278,'٤٢٢٧ - (وقال - صلى الله عليه وسلم - أهل الجنة جرد)','2735','3941','0');
INSERT INTO [t38171] VALUES (4279,'٤٢٢٨ - (أدق أهل الجنة منزلة).','2736','3941','0');
INSERT INTO [t38171] VALUES (4280,'٤٢٢٩ - (وقال - صلى الله عليه وسلم - نظرت إلى الجنة فإذا الرمانة من رمانها كجلف البعير المقتب)','2737','3941','0');
INSERT INTO [t38171] VALUES (4281,'٤٢٣٠ - وقال كعب الأحبار رحمه الله تعالى: (خلق الله تعالى آدم بيده وكتب التوراة بيده وغرس الجنة بيده ثم قال لها تكلمي فقالت قد أفلح المؤمنون)','2738','3941','0');
INSERT INTO [t38171] VALUES (4282,'٤٢٣٢ - (كان رسول الله - صلى الله عليه وسلم - يحب الفأل)','2739','3941','0');
INSERT INTO [t38171] VALUES (4283,'٤٢٣٣ - (قال رسول الله - صلى الله عليه وسلم - إن لله تعالى مائة رحمة أنزل منها رحمة واحدة بين الجن والإنس والطير والبهائم والهوام فيها يتعاطفون وبها يتراحمون وأخر تسعا وتسعين رحمة يرحم','2739','3941','0');
INSERT INTO [t38171] VALUES (4284,'٤٢٣٤ - (ويروى أنه إذا كان يوم القيامة أخرج الله تعالى كتابا من تحت العرش فيه إن رحمتي سبقت غضبي وأنا أرحم الراحمين فيخرج من النار مثلا أهل الجنة).','2741','3941','0');
INSERT INTO [t38171] VALUES (4285,'٤٢٣٥ - (وقال رسول الله - صلى الله عليه وسلم - يتجلى الله عز وجل لنا يوم القيامة ضاحكا فيقول أبشروا معشر المسلمين فإنه ليس منكم أحد إلا وقد جعلت مكانه في النار يهوديا أو نصرانيا).','2742','3941','0');
INSERT INTO [t38171] VALUES (4286,'٤٢٣٦ - (وقال النبي - صلى الله عليه وسلم - يشفع الله آدم يوم القيامة من جميع ذريته في مائة ألف ألف وعشرة آلاف ألف).','2743','3941','0');
INSERT INTO [t38171] VALUES (4287,'٤٢٣٧ - (وقال - صلى الله عليه وسلم - إن الله عز وجل يقول يوم القيامة للمؤمنين هل أحببتم لقائي فيقولون نعم يا ربنا فيقول لم فيقولون رجونا عفوك ومغفرتك فيقول قد أوجبت لكم مغفرتي).','2744','3941','0');
INSERT INTO [t38171] VALUES (4288,'٤٢٣٨ - (وقال - صلى الله عليه وسلم - يقول الله عز وجل يوم القيامة أخرجوا من النار من ذكرني يوما أو خافني في مقام).','2744','3941','0');
INSERT INTO [t38171] VALUES (4289,'٤٢٣٩ - (وقال رسول الله - صلى الله عليه وسلم - إذا اجتمع أهل النار في','2744','3941','0');
INSERT INTO [t38171] VALUES (4290,'٤٢٤٠ - وقال جابر بن عبد الله عنه: (من زادت حسناته على سيئاته يوم القيامة فذاك يدخل الجنة بغير حساب ومن استوت حسناته وسيآته فذاك يحاسب حسابا يسيرا ثم يدخل الجنة وإنما شفاعة رسول الله - صلى الله عليه وسلم - لمن أوبق نفسه)','2747','3941','0');
INSERT INTO [t38171] VALUES (4291,'٤٢٤١ - روى الصابوني في المائتين فقال أخبرنا أبو العباد عبد الصمد بن عبد الله المعمري','2748','3941','0');
INSERT INTO [t38171] VALUES (4292,'٤٢٤٢ - (وقال رسول الله - صلى الله عليه وسلم - ينادي مناد من تحت العرش يوم القيامة يا أمة محمد أما ما كان لي قبلكم فقد وهبته لكم وبقيت التبعات)','2749','3941','0');
INSERT INTO [t38171] VALUES (4293,'٤٢٤٣ - عن عبادة بن الصامت قال: (سمعت رسول الله - صلى الله عليه وسلم - يقول من شهد أن لا إله إلا الله وأن محمدا رسول الله حرم الله عليه النار).','2749','3941','0');
INSERT INTO [t38171] VALUES (4294,'٤٢٤٤ - وهذا الحديث يعرف بحديث البطاقة مشهور عند المحدثين مذكور في مسلسلاتهم فقوله في أول الحديث إن الله إلى قوله','2749','3941','0');
INSERT INTO [t38171] VALUES (4295,'٤٢٤٥ - (روى البخاري ومسلم في صحيحهما)','2756','3941','0');
INSERT INTO [t38171] VALUES (4296,'٤٢٤٦ - وروى البخاري أيضا عن ابن عباس رضي الله عنهما:','2758','3941','0');
INSERT INTO [t38171] VALUES (4297,'٤٢٤٧ - وعن عمرو بن حزم بن زيد بن لوذان الأنصاري رضي الله عنه:','2760','3941','0');
INSERT INTO [t38171] VALUES (4298,'٤٢٤٨ - وقال أبو ذر الغفاري رضي الله عنه: (قال رسول الله - صلى الله عليه وسلم - عرض لي جبريل في جانب الحرة)','2763','3941','0');
INSERT INTO [t38171] VALUES (4299,'٤٢٤٩ - وقال أبو الدرداء رضي الله عنه: (قرأ رسول الله - صلى الله عليه وسلم -) قوله تعالى: (ولمن خاف مقام ربه جنتان فقلت وإن سرق وإن زنى يا رسول الله فقال) الثانية (ولمن خاف مقام ربه جنتان فقلت وإن سرق وإن زنى يا رسول الله فقال)','2764','3941','0');
INSERT INTO [t38171] VALUES (4300,'٤٢٥٠ - عن أبي موسى عبد الله بن قيس الأشعري رضي الله عنه:','2765','3941','0');
