INSERT INTO [t3975] VALUES (1,'«وأما عمل المعاصرين، ومنهجهم المتعجل في بيان مراتب الأحاديث من الصحة والضعف','1','0','0');
INSERT INTO [t3975] VALUES (2,'فإن «تقريب التهذيب» للحافظ الشهاب ابن حجر العسقلاني، هو زبدة ما توصل إليه من أحكام الجرح والتعديل','2','1','0');
INSERT INTO [t3975] VALUES (3,'ونص أحكامه في مقدمة التقريب: «وباعتبار ما ذكرت انحصر لي الكلام على أحوالهم','3','1','0');
INSERT INTO [t3975] VALUES (4,'فمن الواضح اللائح: أن الحافظ اقتصر على بيان أحكام الجرح والتعديل للرواة، ولم يرد قط بيان درجة أحاديثهم','4','1','0');
INSERT INTO [t3975] VALUES (5,'إن الحكم على درجة حديث الراوي لا يتوقف على معرفة عدالته أو جرحه فقط.','4','1','0');
INSERT INTO [t3975] VALUES (6,'وأما عمل المعاصرين، ومنهجهم المتعجل المذكور آنفا، فأكثر مباينة، وأوسع خلافا من ذا','5','1','0');
INSERT INTO [t3975] VALUES (7,'وقد تكلف جماعة من العلماء المتأخرين بيان ما لم يرد الحافظ بيانه، فسدد كل سهما لكن في غير مرماه','6','1','0');
INSERT INTO [t3975] VALUES (8,'وممن أغار على أحكام التقريب من رفعاء الشيوخ وعقلائهم، فصرفها عن مقاصدها وغاياتها، وتصرف بالجور في تأويل دلالاتها','6','1','0');
INSERT INTO [t3975] VALUES (9,'قوله «وما بعد المرتبة الرابعة فمن المردود من العجائب الغرائب','7','1','0');
INSERT INTO [t3975] VALUES (10,'وهذا الاقتضاء الثالث هو الذي حملني على: التأسف مما في هذا الحكم من الجور والتعسف','8','1','0');
INSERT INTO [t3975] VALUES (11,'إن الحكم الصحيح على أحاديث رجال هاتين المرتبتين ليس بدرجة واحدة، بل يجب النظر في حديث كل راو على حدة','8','1','0');
INSERT INTO [t3975] VALUES (12,'ومن التمثيل على عجالة لهذا الصنف من رواة الصحيح من المرتبة الخامسة','9','1','0');
INSERT INTO [t3975] VALUES (13,'فهذه السباعيات الست، بعدة أوصاف رجال المرتبة الخامسة','12','1','0');
INSERT INTO [t3975] VALUES (14,'ومن أوضح البراهين على خطأ هذا الحكم، لجوره، ووعورة مسلكه، ومخالفته للاستقراء والتتبع','12','1','0');
INSERT INTO [t3975] VALUES (15,'وأما شبيه هذا التمثيل برواة الصحيح من المرتبة التالية، وهي السادسة من مراتب تقريب الحافظ الجهبذ','13','1','0');
INSERT INTO [t3975] VALUES (16,'فمن المرتبة الخامسة:','15','0','0');
INSERT INTO [t3975] VALUES (17,'[١] محمد بن طلحة بن مصرف اليامي كوفي صدوق له أوهام، وأنكروا سماعه من أبيه لصغره','15','16','0');
INSERT INTO [t3975] VALUES (18,'أخرجا له في الصحيحين سبعة أحاديث، تفرد البخاري بأربعة منها، ومسلم بالثلاثة الأخرى.','16','17','0');
INSERT INTO [t3975] VALUES (19,'[٢] حاتم بن إسماعيل المدني أبو إسماعيل الحارثي مولاهم الكوفي','18','16','0');
INSERT INTO [t3975] VALUES (20,'وقد أكثر الشيخان من تخريج أحاديثه في صحيحهما، فاتفقا على ثمانية أحاديث','19','19','0');
INSERT INTO [t3975] VALUES (21,'[٣] شريك بن عبد الله بن أبي نمر أبو عبد الله المدني صدوق يخطئ [خ م د تم س ق]','32','16','0');
INSERT INTO [t3975] VALUES (22,'وقد أكثر الشيخان من تخريج أحاديثه في صحيحهما، فاتفقا على ستة أحاديث، وتفرد البخاري بحديثين','32','21','0');
INSERT INTO [t3975] VALUES (23,'[٤] الوليد بن كثير القرشي المخزومي مولاهم أبو محمد المدني الكوفي صدوق رمي برأي الخوارج','37','16','0');
INSERT INTO [t3975] VALUES (24,'وهذا حكم مقارب سديد، وهو بمعني: صدوق ثبت لم تثبت تهمته، بل هو برئ مما رماه به بعضهم من الإباضية.','37','23','0');
INSERT INTO [t3975] VALUES (25,'ومن المرتبة السادسة:','41','0','0');
INSERT INTO [t3975] VALUES (26,'[١] طلق بن معاوية النخعي أبو غياث الكوفي تابعي كبير مخضرم مقبول من الثانية [بخ م س] .','41','25','0');
INSERT INTO [t3975] VALUES (27,'[٢] عبد الله بن عبد الرحمن بن أبى بكر التيمي المدنى مقبول من الثالثة [خ م خد س ق] .','42','25','0');
INSERT INTO [t3975] VALUES (28,'[٣] معبد بن كعب بن مالك الأنصاري السلمي المدنى مقبول من الثالثة [خ م خد س ق] .','43','25','0');
INSERT INTO [t3975] VALUES (29,'[٤] جعفر بن أبي ثور عكرمة السوائي أبو ثور الكوفي مقبول من الثالثة [م ق] .','44','25','0');
INSERT INTO [t3975] VALUES (30,'[٥] مسلم بن قرظة الأشجعي الشامي ابن أخي عوف بن مالك الأشجعي مقبول من الثالثة [م] .','45','25','0');
INSERT INTO [t3975] VALUES (31,'[٦] مصدع أبو يحيى الأعرج المعرقب مولى معاذ بن عفراء مقبول من الثالثة [م]','46','25','0');
INSERT INTO [t3975] VALUES (32,'[٧] يحيى بن قزعة القرشي المكي المؤذن مقبول من العاشرة [خ]','47','25','0');
INSERT INTO [t3975] VALUES (33,'وأما باقي التعقب والبيان، فانظره في كتابنا: «إتحاف الأريب بأحكام مراتب تقريب التهذيب»','52','0','0');
