INSERT INTO [t39] VALUES (160,'٢٣١ - قال الله عز وجل: {وإذا طلقتم النساء فبلغن أجلهن فأمسكوهن بمعروف أو سرحوهن بمعروف ولا تمسكوهن ضرارا لتعتدوا}','239','31','0');
INSERT INTO [t39] VALUES (161,'٢٣٢ - وقيل في الآية التي بعد هذه الآية: {وإذا طلقتم النساء فبلغن أجلهن فلا تعضلوهن أن ينكحن أزواجهن إذا تراضوا بينهم}','240','31','0');
INSERT INTO [t39] VALUES (162,'[سبب النزول]','240','161','0');
INSERT INTO [t39] VALUES (163,'٢٣٣ - قال الله تبارك وتعالى: {والوالدات يرضعن أولادهن حولين كاملين لمن أراد أن يتم الرضاعة وعلى المولود له رزقهن وكسوتهن بالمعروف لا تكلف نفس إلا وسعها لا تضار والدة بولدها ولا مولود له بولده وعلى الوارث مثل ذلك}','241','31','0');
INSERT INTO [t39] VALUES (164,'[الرضاع]','241','163','0');
INSERT INTO [t39] VALUES (165,'٢٣٣ - قال الله تبارك وتعالى: {فإن أرادا فصالا عن تراض منهما وتشاور فلا جناح عليهما}','244','31','0');
INSERT INTO [t39] VALUES (166,'٢٣٤ - قال الله عز وجل: {والذين يتوفون منكم ويذرون أزواجا يتربصن بأنفسهن أربعة أشهر وعشرا} ٢٤٠ - وقوله: {متاعا إلى الحول غير إخراج}','245','31','0');
INSERT INTO [t39] VALUES (167,'[عدة الحامل والمتوفى عنها زوجها]','245','166','0');
INSERT INTO [t39] VALUES (168,'٢٣٥ - قال عز من قائل: {ولا جناح عليكم فيما عرضتم به من خطبة النساء أو أكننتم في أنفسكم} الآية.','247','31','0');
INSERT INTO [t39] VALUES (169,'[التعريض بالنكاح]','247','168','0');
INSERT INTO [t39] VALUES (170,'٢٣٦ - قال الله تبارك وتعالى: {لا جناح عليكم إن طلقتم النساء ما لم تمسوهن أو تفرضوا لهن فريضة} إلى قوله سبحانه: {حقا على المحسنين} ٢٤١ - وقال عز من قائل: {وللمطلقات متاع بالمعروف حقا على المتقين}','250','31','0');
INSERT INTO [t39] VALUES (171,'[متعة المطلقة]','250','170','0');
INSERT INTO [t39] VALUES (172,'٢٣٧ - قال الله عز وجل: {وإن طلقتموهن من قبل أن تمسوهن وقد فرضتم لهن فريضة فنصف ما فرضتم إلا أن يعفون أو يعفو الذي بيده عقدة النكاح}','255','31','0');
INSERT INTO [t39] VALUES (173,'[من الذي بيده عقدة النكاح؟ ]','255','172','0');
INSERT INTO [t39] VALUES (174,'٢٣٨ - قال الله عز وجل: {حافظوا على الصلوات والصلاة الوسطى وقوموا لله قانتين}','260','31','0');
INSERT INTO [t39] VALUES (175,'[الصلاة الوسطى]','260','174','0');
INSERT INTO [t39] VALUES (176,'٢٣٨ - قال الله عز وجل: {وقوموا لله قانتين}','263','31','0');
