INSERT INTO [t39] VALUES (178,'٢٣٩ - قال الله عز وجل: {فإن خفتم فرجالا أو ركبانا}','264','31','0');
INSERT INTO [t39] VALUES (179,'[الصلاة عند اشتداد الخوف]','264','178','0');
INSERT INTO [t39] VALUES (180,'٢٦٧ - قال الله تبارك وتعالى: {أنفقوا من طيبات ما كسبتم ومما أخرجنا لكم من الأرض}','265','31','0');
INSERT INTO [t39] VALUES (181,'٢٧٢ - قال الله عز وجل: {ليس عليك هداهم} إلى قوله: {وأنتم لا تظلمون}','267','31','0');
INSERT INTO [t39] VALUES (182,'[الصدقة على غير المسلمين]','267','181','0');
INSERT INTO [t39] VALUES (183,'٢٧٥ - ٢٧٩ - قال الله تبارك وتعالى: {الذين يأكلون الربا لا يقومون إلا كما يقوم الذي يتخبطه الشيطان من المس}، وقوله: {وإن تبتم فلكم رءوس أموالكم لا تظلمون ولا تظلمون}','270','31','0');
