INSERT INTO [t39] VALUES (185,'[عقوبة الربا]','271','183','0');
INSERT INTO [t39] VALUES (186,'٢٨٠ - قال الله عز وجل: {وإن كان ذو عسرة فنظرة إلى ميسرة}','272','31','0');
INSERT INTO [t39] VALUES (187,'[إنظار المعسر]','272','186','0');
INSERT INTO [t39] VALUES (188,'٢٨٢ - قال الله تبارك وتعالى: {ياأيها الذين آمنوا إذا تداينتم بدين إلى أجل مسمى فاكتبوه}','274','31','0');
