INSERT INTO [t39] VALUES (190,'٢٨٢ - قال الله عز وجل: {فاكتبوه وليكتب بينكم كاتب بالعدل}','276','31','0');
INSERT INTO [t39] VALUES (191,'[شروط الكاتب]','276','190','0');
INSERT INTO [t39] VALUES (192,'[أحكام الكاتب والشاهد ومن عليه الحق]','276','190','0');
