INSERT INTO [t39] VALUES (194,'٢٨٢ - قال الله عز وجل: {واستشهدوا شهيدين من رجالكم}','279','31','0');
INSERT INTO [t39] VALUES (195,'[شرط الإشهاد]','279','194','0');
INSERT INTO [t39] VALUES (196,'٢٨٢ - قال الله عز وجل: {فإن لم يكونا رجلين فرجل وامرأتان ممن ترضون من الشهداء أن تضل إحداهما فتذكر إحداهما الأخرى}','282','31','0');
INSERT INTO [t39] VALUES (197,'[حكم أداء الشهادة]','282','196','0');
INSERT INTO [t39] VALUES (198,'٢٨٢ - قال الله عز وجل: {ولا تسأموا أن تكتبوه صغيرا أو كبيرا إلى أجله} إلى قوله عز من قائل: {وأشهدوا إذا تبايعتم}','283','31','0');
