INSERT INTO [t39] VALUES (200,'[الحكمة من الإشهاد والرهن]','284','198','0');
INSERT INTO [t39] VALUES (201,'[القضاء بالشاهد واليمين]','284','198','0');
INSERT INTO [t39] VALUES (202,'[القضاء بالمرأتين واليمين]','285','198','0');
