INSERT INTO [t39] VALUES (205,'[شرط صحة الرهن]','287','198','0');
INSERT INTO [t39] VALUES (206,'٢٨٤ - قال الله عز وجل: {لله ما في السماوات وما في الأرض وإن تبدوا ما في أنفسكم أو تخفوه يحاسبكم به الله} إلى قوله عز وجل: {ما اكتسبت}','289','31','0');
INSERT INTO [t39] VALUES (207,'سورة آل عمران','291','0','0');
INSERT INTO [t39] VALUES (208,'٧ - قال الله تبارك وتعالى: {هو الذي أنزل عليك الكتاب منه آيات محكمات هن أم الكتاب وأخر متشابهات} إلى قوله عز وجل: {كل من عند ربنا}','292','207','0');
INSERT INTO [t39] VALUES (209,'٩٧ - قال الله عز وجل: {فيه آيات بينات مقام إبراهيم}','295','207','0');
INSERT INTO [t39] VALUES (210,'٩٧ - قال الله عز وجل: {ومن دخله كان آمنا}','297','207','0');
INSERT INTO [t39] VALUES (211,'[إقامة الحدود في الحرم]','297','210','0');
INSERT INTO [t39] VALUES (212,'٩٧ - قال الله تبارك وتعالى: {ولله على الناس حج البيت}','298','207','0');
INSERT INTO [t39] VALUES (213,'٩٧ - قال الله تبارك وتعالى: {من استطاع إليه سبيلا}','300','207','0');
INSERT INTO [t39] VALUES (214,'٩٧ - قال الله عز وجل: {ومن كفر فإن الله غني عن العالمين}','302','207','0');
INSERT INTO [t39] VALUES (215,'٢٨ - قال الله عز وجل: {لا يتخذ المؤمنون الكافرين أولياء من دون المؤمنين}','304','207','0');
INSERT INTO [t39] VALUES (216,'١٠٢ - قال الله عز وجل: {ياأيها الذين آمنوا اتقوا الله حق تقاته}','305','207','0');
INSERT INTO [t39] VALUES (217,'سورة النساء','306','0','0');
INSERT INTO [t39] VALUES (218,'٣ - قال الله تعالى: {وإن خفتم ألا تقسطوا في اليتامى فانكحوا ما طاب لكم من النساء مثنى وثلاث ورباع}','307','217','0');
INSERT INTO [t39] VALUES (219,'[الزواج بالولية]','307','218','0');
INSERT INTO [t39] VALUES (220,'٣ - قال الله تبارك وتعالى: {فإن خفتم ألا تعدلوا فواحدة أو ما ملكت أيمانكم ذلك أدنى ألا تعولوا}','309','217','0');
INSERT INTO [t39] VALUES (221,'٤ - قال الله تبارك وتعالى: {وآتوا النساء صدقاتهن نحلة فإن طبن لكم عن شيء منه نفسا فكلوه هنيئا مريئا}','311','217','0');
INSERT INTO [t39] VALUES (222,'٥ - قال الله عز وجل: {ولا تؤتوا السفهاء أموالكم التي جعل الله لكم قياما وارزقوهم فيها واكسوهم وقولوا لهم قولا معروفا}','312','217','0');
INSERT INTO [t39] VALUES (223,'[نفي السفه عن المرأة والصبي]','312','222','0');
INSERT INTO [t39] VALUES (224,'٦ - قال الله عز وجل: {وابتلوا اليتامى حتى إذا بلغوا النكاح فإن آنستم منهم رشدا فادفعوا إليهم أموالهم}','314','217','0');
INSERT INTO [t39] VALUES (225,'٦ - قال الله تبارك وتعالى: {ومن كان غنيا فليستعفف ومن كان فقيرا فليأكل بالمعروف}','315','217','0');
INSERT INTO [t39] VALUES (226,'٧ - قال الله عز وجل: {للرجال نصيب مما ترك الوالدان والأقربون}','317','217','0');
INSERT INTO [t39] VALUES (227,'[سبب النزول]','317','226','0');
INSERT INTO [t39] VALUES (228,'٨ - قال الله عز وجل: {وإذا حضر القسمة أولو القربى}','318','217','0');
INSERT INTO [t39] VALUES (229,'١١ - قال الله تبارك وتعالى: {يوصيكم الله في أولادكم للذكر مثل حظ الأنثيين فإن كن نساء فوق اثنتين فلهن ثلثا ما ترك وإن كانت واحدة فلها النصف}','320','217','0');
INSERT INTO [t39] VALUES (230,'١١ - قال الله عز وجل: {ولأبويه لكل واحد منهما السدس} إلى قوله عز وجل: {فلأمه الثلث}','322','217','0');
INSERT INTO [t39] VALUES (231,'١١ - قال الله تبارك وتعالى: {فإن كان له إخوة فلأمه السدس}','323','217','0');
INSERT INTO [t39] VALUES (232,'١٢ - قال الله تبارك وتعالى: {وإن كان رجل يورث كلالة أو امرأة وله أخ أو أخت فلكل واحد منهما السدس} إلى قوله: {فهم شركاء في الثلث}','324','217','0');
INSERT INTO [t39] VALUES (233,'١٧٦ - قال الله عز وجل: {يستفتونك قل الله يفتيكم في الكلالة إن امرؤ هلك ليس له ولد وله أخت فلها نصف ما ترك وهو يرثها إن لم يكن لها ولد فإن كانتا اثنتين فلهما الثلثان مما ترك وإن كانوا إخوة رجالا ونساء فللذكر مثل حظ الأنثيين يبين الله لكم أن تضلوا}','325','217','0');
INSERT INTO [t39] VALUES (234,'١٢ - وقال الله عز وجل: {وإن كان رجل يورث كلالة أو امرأة وله أخ أو أخت} ١٧٦ - وقال: {يستفتونك قل الله يفتيكم في الكلالة إن امرؤ هلك ليس له ولد} الآية','327','217','0');
INSERT INTO [t39] VALUES (235,'١٢ - قال الله تبارك وتعالى: {ولكم نصف ما ترك أزواجكم} الآيتين','330','217','0');
INSERT INTO [t39] VALUES (236,'١٢ - قال الله تبارك وتعالى: {من بعد وصية يوصي بها أو دين}','332','217','0');
INSERT INTO [t39] VALUES (237,'١٥ - قال الله تبارك وتعالى: {واللاتي يأتين الفاحشة من نسائكم} إلى قوله عز من قائل: {أو يجعل الله لهن سبيلا}','334','217','0');
INSERT INTO [t39] VALUES (238,'١٦ - قال الله عز وجل: {واللذان يأتيانها منكم فآذوهما}','335','217','0');
INSERT INTO [t39] VALUES (239,'١٧ - ١٨ - قال الله عز وجل: {إنما التوبة على الله للذين يعملون السوء بجهالة} إلى قوله: {أعتدنا لهم عذابا أليما}','336','217','0');
INSERT INTO [t39] VALUES (240,'١٩ - قال الله عز وجل: {ياأيها الذين آمنوا لا يحل لكم أن ترثوا النساء كرها}','337','217','0');
INSERT INTO [t39] VALUES (241,'١٩ - قال الله عز وجل: {ولا تعضلوهن لتذهبوا ببعض ما آتيتموهن إلا أن يأتين بفاحشة مبينة}','338','217','0');
INSERT INTO [t39] VALUES (242,'١٩ - ٢٠ - قال الله تبارك وتعالى: {وعاشروهن بالمعروف} إلى قوله عز من قائل: {فلا تأخذوا منه شيئا}','339','217','0');
INSERT INTO [t39] VALUES (243,'٢٢ - قال الله عز وجل: {ولا تنكحوا ما نكح آباؤكم من النساء إلا ما قد سلف إنه كان فاحشة ومقتا وساء سبيلا}','341','217','0');
INSERT INTO [t39] VALUES (244,'٢٣ - قال الله تبارك وتعالى: {وكان الله عليما حكيما}','342','217','0');
INSERT INTO [t39] VALUES (245,'٢٣ - وقد قال الله جل وعز: {وأن تجمعوا بين الأختين إلا ما قد سلف}','343','217','0');
INSERT INTO [t39] VALUES (246,'٢٣ - قال الله تبارك وتعالى: {حرمت عليكم أمهاتكم} الآية','344','217','0');
INSERT INTO [t39] VALUES (247,'٢٣ - قال الله عز وجل: {وحلائل أبنائكم الذين من أصلابكم}','347','217','0');
INSERT INTO [t39] VALUES (248,'[سبب النزول]','347','247','0');
INSERT INTO [t39] VALUES (249,'٢٣ - قال الله عز وجل: {وأن تجمعوا بين الأختين إلا ما قد سلف}','348','217','0');
INSERT INTO [t39] VALUES (250,'٢٤ - قال الله تبارك وتعالى: {والمحصنات من النساء إلا ما ملكت أيمانكم}','350','217','0');
INSERT INTO [t39] VALUES (251,'٢٤ - قال الله عز وجل: {كتاب الله عليكم}','351','217','0');
INSERT INTO [t39] VALUES (252,'٢٤ - قال الله جل وعز: {فما استمتعتم به منهن فآتوهن أجورهن فريضة}','353','217','0');
INSERT INTO [t39] VALUES (253,'قال الله جل وعز: {ولا جناح عليكم أن تنكحوهن إذا آتيتموهن أجورهن}','355','217','0');
INSERT INTO [t39] VALUES (254,'٢٥ - قال الله عز وجل: {ومن لم يستطع منكم طولا أن ينكح المحصنات المؤمنات فمن ما ملكت أيمانكم من فتياتكم المؤمنات} إلى قوله: {والله غفور رحيم}','356','217','0');
INSERT INTO [t39] VALUES (255,'٢٥ - قال الله عز وجل: {فانكحوهن بإذن أهلهن وآتوهن أجورهن}','362','217','0');
INSERT INTO [t39] VALUES (256,'٢٥ - قال الله عز وجل: {فإن أتين بفاحشة فعليهن نصف ما على المحصنات من العذاب}','365','217','0');
INSERT INTO [t39] VALUES (257,'[زنا الإماء والعبيد]','365','256','0');
INSERT INTO [t39] VALUES (258,'٢٩ - قال الله تبارك وتعالى: {لا تأكلوا أموالكم بينكم بالباطل إلا أن تكون تجارة عن تراض منكم}','368','217','0');
INSERT INTO [t39] VALUES (259,'٢٩ - قال الله جل وعز: {ولا تقتلوا أنفسكم إن الله كان بكم رحيما}','369','217','0');
INSERT INTO [t39] VALUES (260,'٣١ - قال الله تبارك وتعالى: {إن تجتنبوا كبائر ما تنهون عنه نكفر عنكم سيئاتكم وندخلكم مدخلا كريما}','371','217','0');
INSERT INTO [t39] VALUES (261,'٣٢ - قال الله تبارك وتعالى: {ولا تتمنوا ما فضل الله به بعضكم على بعض} إلى قوله: {واسألوا الله من فضله}','374','217','0');
INSERT INTO [t39] VALUES (262,'٣٣ - قال الله عز وجل: {ولكل جعلنا موالي مما ترك الوالدان والأقربون}','375','217','0');
INSERT INTO [t39] VALUES (263,'٣٤ - قال الله عز وجل: {الرجال قوامون على النساء بما فضل الله بعضهم على بعض} إلى قوله: {فلا تبغوا عليهن سبيلا}','376','217','0');
INSERT INTO [t39] VALUES (264,'[القوامة]','376','263','0');
INSERT INTO [t39] VALUES (265,'[تأديب الرجل امرأته]','377','263','0');
INSERT INTO [t39] VALUES (266,'٣٥ - قال الله عز وجل: {وإن خفتم شقاق بينهما} إلى قوله: {إن الله كان عليما خبيرا}','380','217','0');
INSERT INTO [t39] VALUES (267,'[بعث الحكمين]','380','266','0');
INSERT INTO [t39] VALUES (268,'٤٣ - قال الله تبارك وتعالى: {لا تقربوا الصلاة وأنتم سكارى}','387','217','0');
INSERT INTO [t39] VALUES (269,'٤٣ - قال الله عز وجل: {ولا جنبا إلا عابري سبيل حتى تغتسلوا}','388','217','0');
INSERT INTO [t39] VALUES (270,'[دخول الجنب المسجد وصلاته]','388','269','0');
INSERT INTO [t39] VALUES (271,'٤٣ - قال الله عز وجل: {وإن كنتم مرضى أو على سفر} إلى قوله: {صعيدا طيبا}','390','217','0');
INSERT INTO [t39] VALUES (272,'[تيمم المريض]','390','271','0');
INSERT INTO [t39] VALUES (273,'٤٣ - قال الله تبارك وتعالى: {أو لامستم النساء}','391','217','0');
INSERT INTO [t39] VALUES (274,'[اللمس الموجب للوضوء]','391','273','0');
INSERT INTO [t39] VALUES (275,'٤٣ - قال الله تبارك وتعالى: {فلم تجدوا ماء فتيمموا صعيدا طيبا فامسحوا بوجوهكم وأيديكم}','394','217','0');
INSERT INTO [t39] VALUES (276,'[أحكام التيمم]','394','275','0');
INSERT INTO [t39] VALUES (277,'١١٦ - قال الله تبارك وتعالى: {إن الله لا يغفر أن يشرك به ويغفر ما دون ذلك لمن يشاء}','397','217','0');
INSERT INTO [t39] VALUES (278,'[عموم المغفرة للمذنبين من أمته -صلى الله عليه وسلم-]','397','277','0');
INSERT INTO [t39] VALUES (279,'٥٨ - قال الله عز وجل: {إن الله يأمركم أن تؤدوا الأمانات إلى أهلها}','398','217','0');
INSERT INTO [t39] VALUES (280,'٥٩ - قال الله تبارك وتعالى: {ياأيها الذين آمنوا أطيعوا الله وأطيعوا الرسول وأولي الأمر منكم فإن تنازعتم في شيء فردوه إلى الله والرسول إن كنتم تؤمنون بالله واليوم الآخر}','399','217','0');
