INSERT INTO [t39] VALUES (286,'٩٢ - قال الله تبارك وتعالى: {فإن كان من قوم عدو لكم وهو مؤمن فتحرير رقبة مؤمنة وإن كان من قوم بينكم وبينهم ميثاق فدية مسلمة إلى أهله وتحرير رقبة مؤمنة}','404','217','0');
