INSERT INTO [t39] VALUES (293,'١٠١ - ١٠٢ - قال الله تبارك وتعالى: {وإذا ضربتم في الأرض فليس عليكم جناح أن تقصروا من الصلاة إن خفتم أن يفتنكم الذين كفروا إن الكافرين كانوا لكم عدوا مبينا (١٠١) وإذا كنت فيهم فأقمت لهم الصلاة فلتقم طائفة منهم معك}','413','217','0');
