INSERT INTO [t39] VALUES (708,'[سبب النزول]','930','707','0');
INSERT INTO [t39] VALUES (709,'٩ - قال الله تبارك وتعالى: {وإن طائفتان من المؤمنين اقتتلوا} الآية','931','702','0');
INSERT INTO [t39] VALUES (710,'١١ - قال الله تبارك وتعالى: {ياأيها الذين آمنوا لا يسخر قوم من قوم عسى أن يكونوا خيرا منهم ولا نساء من نساء عسى أن يكن خيرا منهن}','933','702','0');
