INSERT INTO [t39] VALUES (724,'٥٦ - ٥٧ - قال الله عز وجل: {وما خلقت الجن والإنس إلا ليعبدون (٥٦) ما أريد منهم من رزق وما أريد أن يطعمون}','951','721','0');
