INSERT INTO [t39] VALUES (726,'٢١ - قال الله تبارك وتعالى: {والذين آمنوا واتبعتهم ذريتهم بإيمان ألحقنا بهم ذريتهم وما ألتناهم من عملهم من شيء}','954','725','0');
