INSERT INTO [t39] VALUES (740,'٢٧ - قال الله تبارك وتعالى: {ورهبانية ابتدعوها ما كتبناها عليهم إلا ابتغاء رضوان الله فما رعوها حق رعايتها}','972','737','0');
