INSERT INTO [t39] VALUES (751,'١١ - قال الله عز وعلا: {ياأيها الذين آمنوا إذا قيل لكم تفسحوا في المجالس فافسحوا يفسح الله لكم} إلى قوله سبحانه: {أوتوا العلم درجات}','993','746','0');
INSERT INTO [t39] VALUES (752,'١٢ - قال الله تبارك وتعالى: {ياأيها الذين آمنوا إذا ناجيتم الرسول فقدموا بين يدي نجواكم صدقة}','994','746','0');
