INSERT INTO [t39] VALUES (757,'٦ - قال الله عز وعلا: {فما أوجفتم عليه من خيل ولا ركاب ولكن الله يسلط رسله} الآية إلى قوله سبحانه: {والله على كل شيء قدير}','1001','753','0');
