INSERT INTO [t39] VALUES (766,'٤ - قال الله تبارك وتعالى: {قد كانت لكم أسوة حسنة في إبراهيم} إلى قوله: {وما أملك لك من الله من شيء}','1014','764','0');
INSERT INTO [t39] VALUES (767,'١٠ - ١١ - قال الله سبحانه: {ياأيها الذين آمنوا إذا جاءكم المؤمنات مهاجرات فامتحنوهن الله أعلم بإيمانهن فإن علمتموهن مؤمنات} إلى قوله: {واتقوا الله الذي أنتم به مؤمنون}','1015','764','0');
INSERT INTO [t39] VALUES (768,'١٢ - قال الله عز وجل: {ياأيها النبي إذا جاءك المؤمنات يبايعنك على أن لا يشركن بالله} الآية','1018','764','0');
INSERT INTO [t39] VALUES (769,'٨ - قال الله تبارك وتعالى: {لا ينهاكم الله عن الذين لم يقاتلوكم في الدين ولم يخرجوكم من دياركم} إلى: {إن الله يحب المقسطين}','1020','764','0');
INSERT INTO [t39] VALUES (770,'ومن سورة الحواريين','1021','0','0');
