INSERT INTO [t4182] VALUES (1,'مقدمة','2','0','0');
INSERT INTO [t4182] VALUES (2,'بيان ما ورد في فضل أصبهان','3','1','0');
INSERT INTO [t4182] VALUES (3,'ذكر الأشياء التي خصت بها أصبهان ذكروا أن بوادي أصبهان زرنروذ مفيض يسمى هنام ليس في الأرض مفيض أعجب منه , وذلك أن الأودية الكبار مصبها إلى البحار , ووادي زرنروذ ينصب في هذا المفيض وهو ثمانية عشر فرسخا في فرسخين لا يرتفع الماء في حافاته عن المقدار المعهود','10','1','0');
INSERT INTO [t4182] VALUES (4,'ذكر بناء مدينة أصبهان وبانيها ومساحتها وقدرها ذكر العلماء بذلك أنه بناها الإسكندر الرومي , واستتمها كسرى أنوشيروان على يدي آذرشابور ومساحتها ألفا جريب , ويحيط بالمدينة ألف قصبة تكون ستة آلاف ذراع , وهو نصف فرسخ لأن الفرسخ اثنا عشر ألف ذراع , وقطرها','31','1','0');
INSERT INTO [t4182] VALUES (5,'ذكر طول مدينتها حكي عن أبي عمرو بن حكيم قال: يقال إن طول مدينة أصبهان ألف وسبعمائة واثنتين وخمسين ذراعا في عرض ألف وخمسمائة ذراع يكون ألفي ألف وستمائة وثمانية وعشرين ألف ذراع , يكون بالدهقان ألف وثمان مائة وخمسة وعشرون جريبا , ودوران المدينة سبعون ألفا','33','1','0');
INSERT INTO [t4182] VALUES (6,'مساحة مسجد الجامع بيهودية أصبهان اثنا عشر جريبا , وله سبعة وعشرون بابا وفيه تسعة وخمسون طاقا , وفيه من الأسطوانات مائتان وست وثمانون أسطوانة , وطوله خمس مائة آجرة , وعرضه ثلاثمائة آجرة يكون مائة وخمسين ألف آجرة يسع كل أربع آجرات رجل , يكون تسعة وثلاثين','34','1','0');
INSERT INTO [t4182] VALUES (7,'ذكر فتوح أصبهان ومشاورة عمر بن الخطاب رضي الله عنه أصحاب رسول الله صلى الله عليه وسلم على رسوله فيها وأجوبتهم له , وذكر المبعوثين إليها','35','1','0');
INSERT INTO [t4182] VALUES (8,'الطبقة الأولى','41','0','0');
INSERT INTO [t4182] VALUES (9,'ذكر أسامي الصحابة رضي الله عليهم الذين قدموا أصبهان','42','8','0');
INSERT INTO [t4182] VALUES (10,'الحسن بن علي بن أبي طالب فممن دخله فيما ثنا أبو بشر عن بعض مشايخه أن الحسن بن علي بن أبي طالب , وابن الزبير قدما غازيين إلى جرجان , ويكنى الحسن بن علي أبا محمد ولد في النصف من شهر رمضان سنة ثلاث من الهجرة وتوفي سنة تسع وأربعين ودفن بالبقيع وصلى عليه سعيد','43','8','0');
INSERT INTO [t4182] VALUES (11,'عبد الله بن الزبير بن العوام وعبد الله بن الزبير يكنى أبا خبيب ويقال أبو بكر وأمه أسماء بنت أبي بكر قتل بمكة سنة ثلاث وسبعين','47','8','0');
INSERT INTO [t4182] VALUES (12,'سلمان الفارسي ومما زين الله به أصبهان وأهلها أن جعل سلمان الفارسي منها , ورزقه صحبة نبينا صلى الله عليه وسلم حتى قال فيه صلى الله عليه وسلم: " سلمان منا أهل البيت "','55','8','0');
INSERT INTO [t4182] VALUES (13,'ذكر أبي موسى الأشعري عبد الله بن قيس بن سليم بن حضار بن حرب بن عامر بن عنز بن بكر بن عامر بن عذر بن وائل بن ناجية بن الجماهر بن الأشعر حدثنا بنسبه عمر بن أحمد بن إسحاق قال: ثنا شباب , توفي سنة أربع وأربعين. قدم على النبي صلى الله عليه وسلم بخيبر مع','71','8','0');
INSERT INTO [t4182] VALUES (14,'ذكر صلاة أبي موسى بأصبهان','75','8','0');
INSERT INTO [t4182] VALUES (15,'ذكر ما حدث بأصبهان بعد افتتاحه','78','8','0');
INSERT INTO [t4182] VALUES (16,'ذكر أبناء أبي موسى الأشعري وكان لأبي موسى الأشعري خمسة من البنين إبراهيم , وأبو بردة , وأبو بكر , وموسى , ومحمد بنو أبي موسى , وأبو بردة أكثرهم رواية عن أبي موسى , وروى أبو بردة عن علي بن أبي طالب , وابن عمر , وعائشة , ومعاوية , فأما إبراهيم بن أبي موسى','82','8','0');
INSERT INTO [t4182] VALUES (17,'رافع بن خديج الأنصاري قدم أصبهان في خلافة عمر فأصاب بها عشرة أعبد','85','8','0');
INSERT INTO [t4182] VALUES (18,'عبد الله بن يزيد الخطمي الأنصاري من أصحاب رسول الله صلى الله عليه وسلم قدم أصبهان','87','8','0');
INSERT INTO [t4182] VALUES (19,'عبد الله بن عامر بن كريز بن حبيب بن عبد شمس بن عبد مناف بن قصي بن كلاب يكنى بأبي عبد الرحمن , وابنه عبد الأعلى بن عبد الله بن عامر بن كريز وكان عثمان ولى عبد الله بن عامر البصرة وسار منها إلى أصبهان غازيا وعلى مقدمته عبد الله بن بديل فأتى أصبهان وخلف على','89','8','0');
INSERT INTO [t4182] VALUES (20,'بديل بن عامر بن ورقاء بن عمرو بن ربيعة بن عبد العزى بن ربيعة بن جزي بن عامر بن مازن الخزاعي ذكر علي بن مجاهد قال: افتتحت أصبهان في خلافة عمر سنة إحدى وعشرين , لما قتل النعمان بنهاوند وولي حذيفة وفتح الله على يده بعد حذيفة الجبل فبعث بديل بن ورقاء ,','92','8','0');
INSERT INTO [t4182] VALUES (21,'مجاشع بن مسعود السلمي من أصحاب رسول الله صلى الله عليه وسلم وله أخ يقال له مجالد بن مسعود , وقتل مجاشع يوم الجمل سنة ست وثلاثين ودفن في داره في بني سدوس بالبصرة وهو من المهاجرين وله روايات','97','8','0');
INSERT INTO [t4182] VALUES (22,'وعائذ بن عمرو المزني وهو ابن عمرو بن هلال بن عبيد بن يزيد بن رواحة بن زبيد بن عدي بن عامر , وأخوه رافع بن عمرو , وهم من مزينة ذكر محمد بن عاصم عن يسار بن سمير قال: ثنا عتاب بن زهير بن ثعلبة قال: ثني مرداس عن أبيه قال: خرج أبو موسى الأشعري من أصبهان','102','8','0');
INSERT INTO [t4182] VALUES (23,'النابغة الجعدي واسمه قيس بن عبد الله بن عدس بن ربيعة بن جعدة بن كعب بن عامر بن صعصعة , وهو الشاعر يكنى أبا ليلى , وقدم أصبهان مع الحارث بن عبد الله بن عبد عوف بن أصرم وكان سيره معاوية إلى أصبهان , وكان الحارث واليا عليها من قبل علي ثم من قبل معاوية ,','104','8','0');
INSERT INTO [t4182] VALUES (24,'ومخنف بن سليم بن الحارث ابن عوف بن ثعلبة بن عامر بن ذهل بن مازن بن ذبيان بن ثعلبة بن الدول بن سعد ولاه علي بن أبي طالب رضي الله عنه أصبهان بعدما هلك يزيد بن قيس','106','8','0');
INSERT INTO [t4182] VALUES (25,'وخالد بن غلاب الطائفي القرشي من عمال عثمان على أصبهان وهو جد الغلابيين الذين هم بالبصرة','111','8','0');
INSERT INTO [t4182] VALUES (26,'وحممة الدوسي من أصحاب النبي صلى الله عليه وسلم مات بأصبهان مبطونا','113','8','0');
INSERT INTO [t4182] VALUES (27,'عبد الله بن عبد الله بن عتبان الأنصاري من عمال عمر','115','8','0');
INSERT INTO [t4182] VALUES (28,'عدد من دخل من الصحابة أصبهان عدد من دخل أصبهان من أصحاب النبي صلى الله عليه وسلم ثمانية عشر رجلا: (١) الحسن بن علي (٢) وابن الزبير (٣) وسلمان (٤) وأبو موسى (٥) وابنه موسى (٦) وعبد الله بن عامر (٧) وعبد الله بن بديل بن ورقاء (٨) وبديل بن','120','8','0');
INSERT INTO [t4182] VALUES (29,'ما ذكر من أمر أصبهان , الصلح منها','121','8','0');
INSERT INTO [t4182] VALUES (30,'الطبقة الثانية','127','0','0');
INSERT INTO [t4182] VALUES (31,'الأحنف بن قيس من بني تميم ثم من مضر روى عن عمر , وعثمان , وعلي , والعباس بن عبد المطلب , يقال صخر بن قيس أبو بحر , ويقال الضحاك بن قيس أدرك زمان رسول الله صلى الله عليه وسلم ولم يلقه','128','30','0');
INSERT INTO [t4182] VALUES (32,'السائب بن الأقرع بن عوف بن جابر بن سفيان بن سالم بن مالك بن حطيط بن جشم بن ثقيف استخلفه عبد الله بن بديل على أصبهان وهو ابن عم عثمان بن أبي العاص الثقفي وكان ولاه عمر بن الخطاب قسمة الغنائم بنهاوند , وينسب إلى السائب من أهل أصبهان الفضيل بن السائب وولده','135','30','0');
INSERT INTO [t4182] VALUES (33,'أسيد بن المتشمس بن معاوية ابن عم الأحنف بن قيس , قدم أصبهان غازيا مع أبي موسى الأشعري','138','30','0');
INSERT INTO [t4182] VALUES (34,'جبير بن حية بن مسعود ابن معتب بن مالك بن كعب بن عمرو بن سعد بن عوف بن ثقيف وكان يسكن الطائف وكان معلم كتاب , ثم قدم العراق فصار من كتبة الديوان فلما ولي زياد أكرمه وعظمه وقربه فعظم شأنه وولاه أصبهان وله بالبصرة أولاد منهم عاصم بن جبير بن حية , وزياد بن','140','30','0');
INSERT INTO [t4182] VALUES (35,'يزيد بن قيس ولاه علي بن أبي طالب أصبهان , والري , وهمذان , فلما مات يزيد فرقه على ثلاثة نفر فجعل أصبهان إلى مخنف بن سليم , وعمرو بن سلمة إلى همذان وآخر إلى الري','143','30','0');
INSERT INTO [t4182] VALUES (36,'أبو العالية رفيع الرياحي روي عن أبي موسى أنه خطبهم بأصبهان , وكان ممن شهدها وقرأ القرآن بعد وفاة النبي صلى الله عليه وسلم بعشر سنين. وروى عن أبي بكر , وعمر , وأبي بن كعب وغيرهم , وأدرك الجاهلية والإسلام , وروى عن أبي بكر حديثا لم يروه غيره','145','30','0');
INSERT INTO [t4182] VALUES (37,'أبو محمد سعيد بن جبير قدم أصبهان أيام الحجاج وروى عنه من الأصبهانيين جماعة , منهم: جعفر بن أبي المغيرة , وحجر الأصبهاني , ويزيد بن هزاري , والقاسم بن أبي أيوب , ومات سنة خمس وتسعين , قتله الحجاج صبرا , وله ثلاث بنين عبد الله , ومحمد , وعبد الملك , وكان','147','30','0');
INSERT INTO [t4182] VALUES (38,'حسان بن عبد الرحمن الضبعي وعداده في البصريين قدم أصبهان مع أبي موسى وله حديث لم يحدث به غيره','153','30','0');
INSERT INTO [t4182] VALUES (39,'قطن بن قبيصة الهلالي وهو قطن بن قبيصة بن مخارق بن عبد الله بن شداد بن أبي ربيعة بن نهيك بن هلال بن عامر بن صعصة , يكنى أبا سهلة كان ولاه معاوية بن أبي سفيان , ويقال عبد الملك بن مروان أصبهان , فأقام بها ثم خرج إلى خراسان وولى البراء بن قبيصة وأبوه قبيصة','156','30','0');
INSERT INTO [t4182] VALUES (40,'أبو إسماعيل حماد بن أبي سليمان الفقيه مولى إبراهيم بن أبي موسى الأشعري حدثنا عبدان بن أحمد قال: سمعت أبا بكر بن أبي شيبة , يقول: مات حماد بن أبي سليمان سنة عشرين ومائة , حكى محمد بن يحيى بن منده قال: حماد بن أبي سليمان من أهل برخوار حدثنا أبو بكر أحمد','158','30','0');
INSERT INTO [t4182] VALUES (41,'عبد الرحمن أبو إسماعيل السدي من أهل أصبهان روى عنه ابنه وهو ابن أبي كريمة مولى بني هاشم مولى زينب بنت قيس بن مخرمة روى أسباط عن السدي عن أبيه قال: كاتبتني زينب بنت قيس بن مخرمة على عشرة آلاف درهم , فتركت لي ألفا وكانت زينب قد صلت مع رسول الله صلى الله','165','30','0');
INSERT INTO [t4182] VALUES (42,'وابنه السدي إسماعيل بن عبد الرحمن كان عظيما من عظماء أهل أصبهان , ومات سنة سبع وعشرين ومائة في ولاية بني مروان ويكنى أبا محمد وحدثنا محمد بن يحيى , قال: ثنا موسى بن عبد الرحمن , قال: حدثني إسماعيل بن موسى , قال: حدثتني أمي , قالت: كان السدي من أهل','166','30','0');
INSERT INTO [t4182] VALUES (43,'أبو إسحاق السبيعي عمرو بن عبد الله الهمداني قدم أصبهان مجتازا إلى خراسان حدثنا أبو أيوب الفقيه , وأخرج إلينا كتب جده الحكم بن أيوب , فكتبنا منه وقرأناه عليه , ثنا الحكم عن إسرائيل عن أبي إسحاق قال: ثنا أبو الوداك ونحن بأصبهان قال: ثنا أبو سعيد الخدري','170','30','0');
INSERT INTO [t4182] VALUES (44,'عبيد الله بن أبي بكرة واسم أبي بكرة: نفيع بن الحارث بن كلدة بن عمرو بن علاج بن أبي سلمة عبد العزيز بن غيرة بن عوف بن ثقيف يقال ولاه معاوية بن أبي سفيان إطفاء النيران فقدم أصبهان وأخذ إلى سجستان فأصاب أربعين ألف ألف , فحال عليه الحول وعليه دين , ولي','172','30','0');
INSERT INTO [t4182] VALUES (45,'عبد الله بن أبي بكرة الثقفي ولي أصبهان ذكر النسابة قال: دخلت سنة عشرين ومائة فولي عبد الله بن عبيد الله بن أبي بكرة','174','30','0');
INSERT INTO [t4182] VALUES (46,'مرداس الأصبهاني قيم الجامع روى عن أنس بن مالك','176','30','0');
INSERT INTO [t4182] VALUES (47,'الصباح بن عاصم الأصبهاني روى عن أنس بن مالك','178','30','0');
INSERT INTO [t4182] VALUES (48,'عبد الله بن الأسود الأصبهاني رأى أنسا وسمع منه','180','30','0');
INSERT INTO [t4182] VALUES (49,'أبو غالب الأصبهاني يحدث عن صاحبه أبي أمامة , واسمه حزور حدثنا محمد بن يحيى بن منده , قال: ثنا إسماعيل بن موسى , قال: ثنا شريك , عن داود الحماني , قال: ثنا أبو غالب الأصبهاني , عن أبي أمامة , عن النبي صلى الله عليه وسلم قصة الخوارج','182','30','0');
INSERT INTO [t4182] VALUES (50,'إبراهيم بن هدبة يكنى أبا هدبة قدم أصبهان فحدث على المنبر عن أنس بن مالك , فرفع ذلك إلى جرير بن عبد الحميد , وكان يصدقه وكان المأمون أيضا يصدقه , حكى ذلك المسوحي عن عبيد الله بن عمر , وسمع منه بأصبهان رسته , والحجاج بن يوسف , وحامد بن مسور , وهو متروك','183','30','0');
INSERT INTO [t4182] VALUES (51,'جعفر بن أبي المغيرة القمي من التابعين روى عن عبد الرحمن بن أبزى ورأى ابن الزبير ودخل مكة أيام عبد الله بن عمر مع سعيد بن جبير ذكر ابن عامر , عن أبيه , عن يعقوب , عن جعفر قال: " دخلت مع سعيد بن جبير , فرأيت عبد الله بن الزبير مصلوبا "، وذكر أيضا عن','186','30','0');
INSERT INTO [t4182] VALUES (52,'وثاب أبو يحيى بن وثاب يقال: إن وثابا كان من أهل قاشان فوقع إلى ابن عباس فأقام معه سنتين ثم استأذنه في الرجوع إلى قاشان فأذن له فرحل من الحجاز معه ابنه يحيى بن وثاب , فلما بلغ الكوفة قال لأبيه: إني مؤثر حظ العلم على حظ المال , فأعطني الإذن في المقام ,','189','30','0');
INSERT INTO [t4182] VALUES (53,'يحيى بن وثاب بالكوفة فصار إماما في القراءة وله أحاديث كثيرة','190','30','0');
INSERT INTO [t4182] VALUES (54,'واقد مولى أبي موسى الأشعري من أهل أصبهان ذكر ذلك محمد بن إبراهيم بن عامر بن إبراهيم بن واقد , قال جدي مولى أبي موسى الأشعري خلفه أبو موسى بأصبهان على الأذان , فلم يزل يتوارثونه إلى اليوم. قال النسابة: كان الإمام في مسجد الجامع حبيب بن الزبير بن مشكان','194','30','0');
INSERT INTO [t4182] VALUES (55,'يزيد الأودي كان والي أصبهان أيام عمر بن عبد العزيز','195','30','0');
INSERT INTO [t4182] VALUES (56,'الطبقة الثالثة','198','0','0');
INSERT INTO [t4182] VALUES (57,'عبد الرحمن بن سليمان بن الأصبهاني مات في ولاية خالد روى عن أبي صالح , وأبي حازم , وعكرمة وغيره , نزل الكوفة: جهني كان يتجر إلى أصبهان وله بالكوفة أولاد , وأهل بيت. مات في وسط ولاية خالد','199','56','0');
INSERT INTO [t4182] VALUES (58,'متابعة عطاء بن السائب بن مالك الثقفي يكنى أبا زيد , توفي سنة ثلاثين ومائة وروى عن أنس بن مالك , وابن أبي أوفى , وحدث عن أبيه عن عبد الله بن عمرو حديث التسبيح وغيره','202','56','0');
INSERT INTO [t4182] VALUES (59,'حفص بن حميد أبو عبيد من أهل قم , روى عنه يعقوب القمي وغيره','204','56','0');
INSERT INTO [t4182] VALUES (60,'سليمان الأصبهاني رأى علي بن أبي طالب','206','56','0');
INSERT INTO [t4182] VALUES (61,'حبيب بن الزبير بن مشكان الهلالي الأصبهاني حدثنا عبد الله بن محمد بن يعقوب , قال: سمعت أبا حاتم , يقول: حبيب بن الزبير أصبهاني كتب عنه شعبة بأصبهان , وهو جد يونس بن حبيب يحدث عن عكرمة وعن ابن أبي الهذيل ولا أعلم أحدا حدث عنه غير شعبة وطلبت من غير حديث','208','56','0');
INSERT INTO [t4182] VALUES (62,'إبراهيم بن محمد ابن الحنفية ابن علي بن أبي طالب روى عنه يعقوب القمي قدم عليهم قم','214','56','0');
INSERT INTO [t4182] VALUES (63,'نافع بن أبي نعيم القاري وهو نافع بن عبد الرحمن بن أبي نعيم المقرئ والي أهل المدينة حدثنا أبو العباس الهروي , قال: ثنا أبو حاتم , قال: ثنا الأصمعي , قال: قال نافع بن أبي نعيم أصلي من أصبهان سمعت علي بن سراج يقول: نافع بن أبي نعيم يكنى أبا رويم حدثنا','218','56','0');
INSERT INTO [t4182] VALUES (64,'ومن الطبقة السابعة نافع بن أبي نعيم بن عبد الرحمن وحدثنا محمد بن أحمد بن أبي يحيى , قال: ثنا أبو غسان , قال: ثنا الأصمعي , قال: قال نافع بن أبي نعيم مقرئ أهل المدينة: أصلي من أصبهان , وقال ابن أبي الزناد: أصلي من همذان','219','56','0');
INSERT INTO [t4182] VALUES (65,'بشر بن الحسين الأصبهاني يحدث عن الزبير بن عدي توفي بعد المائتين من أهل المدينة وذكر أنه جاء إلى أبي داود فقال له: حدثني الزبير بن عدي فقال له: كذبت وكتب عنه يحيى بن أبي بكير وهو مار إلى الري فكتب عنه ولم يعرفه حدثنا ابن الجارود , ومحمد بن أحمد الزهري ,','221','56','0');
INSERT INTO [t4182] VALUES (66,'جعفر بن ناجية الأصبهاني كان منزله بشميكان حدث عن عطاء بن أبي رباح','224','56','0');
INSERT INTO [t4182] VALUES (67,'عامر بن ناجية','226','56','0');
INSERT INTO [t4182] VALUES (68,'محمد بن أبي يحيى الأسلمي نزيل المدينة توفي سنة ست وأربعين ومائة , واسم أبي يحيى سمعان , روى عن أبيه عن أبي سعيد , وروى محمد عن عبد الله بن الزبير , وعكرمة , وهو أبو إبراهيم بن محمد بن أبي يحيى وحكى الحسين بن حفص عن إبراهيم بن محمد بن أبي يحيى قال: نحن','228','56','0');
INSERT INTO [t4182] VALUES (69,'أنيس بن أبي يحيى أخو محمد بن أبي يحيى توفي سنة ست وأربعين ومائة روى إبراهيم بن محمد بن أبي يحيى , قال: ثني أبي يحيى قال: ثني عمي أنيس بن أبي يحيى عن أبيه , عن أبي سعيد , قال: قال: النبي صلى الله عليه وسلم في المسجد الذي أسس على التقوى','232','56','0');
INSERT INTO [t4182] VALUES (70,'إبراهيم بن محمد بن أبي يحيى أبو إسحاق حدث عنه ابن جريج يقول مرة: ذا الأسلمي ومرة ابن أبي يحيى ومرة إبراهيم حدثنا عبدان , قال: ثنا ابن أبي عاصم , عن ابن كاسب , قال: اجتمع جفينة وفلان وفلان فقالوا: تعالوا حتى نضع لإبراهيم بن أبي يحيى حديثا , فقالوا:','234','56','0');
INSERT INTO [t4182] VALUES (71,'الطبقة الرابعة','236','0','0');
INSERT INTO [t4182] VALUES (72,'مبارك بن فضالة ابن أبي أمية بن كنانة مولى عمر بن الخطاب , وكان جده مكاتبا لعمر , واسمه عبد الرحمن يكنى أبا فضالة مات سنة أربع وستين ومائة , قدم أصبهان على أيوب بن زياد , وكان واليا عليها من قبل أبي جعفر المنصور خمس سنين , وروى عنه من أهل أصبهان النعمان','237','71','0');
INSERT INTO [t4182] VALUES (73,'وابنه عبد الرحمن بن المبارك','241','71','0');
INSERT INTO [t4182] VALUES (74,'ليث بن سعد يكنى أبا الحارث من أهل أصبهان حدثني ابن صبيح , قال: ثنا إسماعيل بن يزيد , قال: سمعت بعض أصحابنا يقول: كان ليث بن سعد من أهل أصبهان من ماربين وروى عنه من الأصبهانيين قتيبة بن مهران بن عبد الرحمن الأزاداني , وكان سبي من أصبهان ونزل فسطاط مصر','246','71','0');
INSERT INTO [t4182] VALUES (75,'يونس الأصبهاني روى عنه صغدي بن سنان قال: ثنا يونس الأصبهاني','249','71','0');
INSERT INTO [t4182] VALUES (76,'جسر بن فرقد قدم أصبهان , سمعت علي بن الصباح يقول: سمعت عقيل بن يحيى يقول: سمعت عامر بن إبراهيم يقول: قدم جسر القصاب أصبهان. حدث عنه من الأصبهانيين منخل , وشعبة بن عمران','253','71','0');
INSERT INTO [t4182] VALUES (77,'عبد العزيز الماجشون حكى ابن أبي خيثمة قال: كان الماجشون من أهل أصبهان فنزل المدينة ووقع بها فكان يلقى الناس فيقول لهم: جوني جوني','256','71','0');
INSERT INTO [t4182] VALUES (78,'عبد العزيز الدراوردي ذكر الطبراني عن أحمد بن رشدين قال: سمعت أحمد بن صالح يقول: كان الدراوردي من أهل أصبهان نزل بالمدينة فكان يقول للرجل إذا أراد أن يدخل قال: أندرون، فلقبه أهل المدينة الدراوردي','257','71','0');
INSERT INTO [t4182] VALUES (79,'جرير بن عبد الحميد حدثنا محمد بن أحمد بن يزيد , قال: ثنا عبد الله بن عبد الوهاب , قال: ثنا محمد بن عمرو , عن جرير , قال: ولدت في آبة من قرية أصبهان','258','71','0');
INSERT INTO [t4182] VALUES (80,'القاسم بن أبي أيوب أصبهاني لقي سعيد بن جبير','259','71','0');
INSERT INTO [t4182] VALUES (81,'محمد بن عبد الوهاب القناد كان أصله من أصبهان كتب إلينا محمد بن إسحاق قال: ثنا هارون بن إسحاق قال: ثنا محمد بن عبد الوهاب القناد , وكان من أفضل الناس وكان أصله من أصبهان يكنى أبا يحيى , مات سنة اثنتي عشرة ومائتين','261','71','0');
INSERT INTO [t4182] VALUES (82,'محمد بن الصلت قال محمد بن إسحاق: وثنا الجوهري قال محمد بن الصلت يكنى أبا جعفر كان أصبهانيا فصار إلى الكوفة فنزلها ومات سنة ثلاث عشرة ومائتين','262','71','0');
INSERT INTO [t4182] VALUES (83,'حميد بن وهب أبو وهب روى عنه عامر بن إبراهيم , يروي عن إسماعيل بن أبي خالد , وهشام بن عروة','263','71','0');
INSERT INTO [t4182] VALUES (84,'حجر','266','71','0');
INSERT INTO [t4182] VALUES (85,'رجل من أصبهان يحدث عنه عمارة بن أبي حفصة','267','71','0');
INSERT INTO [t4182] VALUES (86,'خطاب بن جعفر بن أبي المغيرة روى عنه عامر , والحسين بن حفص ذكر أنه كان يقدم من قم إلى البلد وكان خراج قم حينئذ إلى البلد , وسمع منه عامر , والحسين بن حفص , وحكي أن أبا حاتم كتب إلى بعض إخوانه مهما وقع عندكم من حديث الخطاب بن جعفر فاجمعوه لي وخذوا إلي به','269','71','0');
INSERT INTO [t4182] VALUES (87,'عبد الله بن معاوية بن عبد الله بن جعفر بن أبي طالب قدم أصبهان وغلب عليها أيام مروان بن محمد , وأقام بها وبنى بها وله ميدان بجرواآن يقال له ميدان عبد الله بن معاوية , ويقال خرج بالكوفة في خلافة مروان فبعث إليه مروان جندا فلحق بأصبهان فغلب عليها , وما زال','279','71','0');
INSERT INTO [t4182] VALUES (88,'أبو جعفر المنصور عبد الله بن علي بن عبد الله بن العباس بن عبد المطلب قدم أصبهان مع عبد الله بن معاوية وخرج منها إلى فارس وحكى وشنة قال: كان أبو جعفر المنصور يجيء إلى عندنا إلى الحمام بيوان , قال: فخرج أياما عن معسكر عبد الله بن معاوية قال: فرجع إلى','281','71','0');
INSERT INTO [t4182] VALUES (89,'عبد الله بن أبي مريم الأموي قاضي أصبهان كان على القضاء أيام الحجاج وكانوا ثلاثة إخوة عبد الله , وعبيد الله , ويزيد , وكان قاضيها أيام الحجاج بن يوسف , ومن ولده محمد بن المغيرة بن سالم بن عبد الله صاحب النعمان , وعلي بن بشر بن عبد الملك بن عبيد الله بن','284','71','0');
INSERT INTO [t4182] VALUES (90,'مبشر بن ورقاء قاضي أصبهان حدث عنه محمد بن بكير , وأبو حجرعمرو بن رافع , وأحمد بن منيع , وحكى أحمد بن يوسف قال: رأيت مبشر بن ورقاء يقضي في مسجد يعقوب بن زياد','286','71','0');
INSERT INTO [t4182] VALUES (91,'نهشل بن سعيد الترمذي يكنى بأبي سعيد الترمذي , قدم أصبهان وحدث عنه عامر بن إبراهيم نسخة عن الضحاك في التفسير وغيره , وكتبنا من حديثه ما لم نكتب عن غيره','290','71','0');
INSERT INTO [t4182] VALUES (92,'غياث بن إبراهيم التميمي روى عنه عامر بن إبراهيم , وغالب بن فرقد الأصبهاني وروى عنه يحيى بن أبي بكير','293','71','0');
INSERT INTO [t4182] VALUES (93,'إبراهيم بن ميمون الصائغ أصبهاني انتقل إلى خراسان. سمعت أبا جعفر محمد بن عبد الرحمن قال: ثنا عبد الله بن محمود المروزي قال: سمعت إبراهيم بن يزيد البيوردي يقول: إبراهيم الصائغ أصله من أصبهان','296','71','0');
INSERT INTO [t4182] VALUES (94,'زفر بن الهذيل بن قيس بن سالم بن قيس بن مكمل بن ذهل بن ذويب بن عمر ويكنى أبا الهذيل , روى عنه النعمان , والحكم بن أيوب وغيرهم , وكان متواضعا , وكان أبوه الهذيل بن قيس مقيما بأصبهان في سنة ست وعشرين ومائة في خلافة يزيد بن الوليد بن عبد الملك , وهو الذي كان','297','71','0');
INSERT INTO [t4182] VALUES (95,'خالد بن أبي كريمة قالوا هو من أصبهان من سنبلان','300','71','0');
INSERT INTO [t4182] VALUES (96,'عمير بن سعيد النخعي قدم أصبهان قال: كنا بأصبهان نعطي الغنم بكذا وكذا جبنة , وكذا وكذا مصلة','302','71','0');
INSERT INTO [t4182] VALUES (97,'حميد بن أبي غنية أبو عبد الملك بن حميد , قال البخاري: هو أصبهاني لما فتحها أبو موسى انتسبوا إليه','304','71','0');
INSERT INTO [t4182] VALUES (98,'إسماعيل بن حماد بن أبي سليمان الفقيه جده حماد من أهل أصبهان , حكى محمد بن يحيى قال: كان جد حماد بن أبي سليمان من بزخوار. وروى عنه معتمر بن أبي سليمان , وخالد الواسطي','305','71','0');
INSERT INTO [t4182] VALUES (99,'الزحاف بن أبي الزحاف الأصبهاني يحدث عن ابن جريج , والمثنى بن الصباح , ومسلم بن خالد , وله بأصبهان عقب , روى عنه عقيل بن يحيى , وذكر محمد بن عاصم أنه حدثه من كتب عن ابنه جعفر بن الزحاف عن أبيه , عن ابن جريج أربعة آلاف حديث','308','71','0');
INSERT INTO [t4182] VALUES (100,'الطبقة الخامسة','310','0','0');
INSERT INTO [t4182] VALUES (101,'النعمان بن عبد السلام ابن حبيب بن حطيط بن عقبة بن خثيم بن وائل بن مهانة بن تيم الله بن ثعلبة بن بكر بن وائل أبو المنذر , قرأت نسبه على باب داره , وهو أرفع من روى عن سفيان الثوري من الأصبهانيين , وكان أبوه عبد السلام مع السلطان بأصبهان , وخلف ضيعة , فتركه','311','100','0');
INSERT INTO [t4182] VALUES (102,'أبو هانئ إسماعيل بن خليفة ولي قضاء أصبهان , روى عن إسماعيل بن أبي خالد , ومجالد , والثوري , ومبارك بن فضالة , وروى عنه الحسين بن حفص , وعامر المؤذن , وإبراهيم بن أيوب , وأبو سفيان , ويروي عنه ابنه سعيد بن أبي هانئ. وحكى محمد بن يحيى بن منده , عن سعيد بن','328','100','0');
INSERT INTO [t4182] VALUES (103,'محمد بن يوسف بن معدان بن سليمان أبو عبد الله المعرف بعروس الزهاد وكان يسكن جورجير , وكانوا ثلاثة إخوة , محمد , وعبد الرحمن , وعبد العزيز , بنو يوسف , مات محمد بن يوسف بالمصيصة , وقبر إلى جنب قبر مخلد بن الحسين , لم أر روى حديثا مسندا عنه أحد إلا حديثا','333','100','0');
INSERT INTO [t4182] VALUES (104,'عبد الرحمن بن يوسف أخوه','345','100','0');
INSERT INTO [t4182] VALUES (105,'محمد بن أبان بن الحكم العنبري ويكنى أبا عبد الرحمن بن يزيد بن جابر بن حيوان بن أخزم بن ذهل بن ذؤيب بن عمرو بن عنبر العنبري , كوفي قدم أصبهان , وهو عم محمد بن يحيى بن أبان , سمع منه بعد المائتين , روى عن الثوري , وأبي حنيفة , وزفر , وحدث عنه سهل بن عثمان','353','100','0');
INSERT INTO [t4182] VALUES (106,'يعقوب بن عبد الله بن سعد الأشعري القمي يكنى بأبي الحسن , روى عنه جرير بن عبد الحميد , وعبد الرحمن بن مهدي , وعامة من أدركه من أهل العراق , وكان جرير إذا مر به يعقوب القمي يقول: هذا مؤمن آل فرعون','361','100','0');
INSERT INTO [t4182] VALUES (107,'إبراهيم بن قرة القاشاني الأصم من أصحاب الثوري , صنف له الجامع , روى عنه إبراهيم بن أيوب , وأبو حجر عمرو بن رافع , وابن حميد , وكان من الثقات , وكان الثوري يحدثه في أذنه سمعت محمد بن يحيى بن منده يقول: إبراهيم بن قرة صاحب سفيان , وكان من أهل قاشان ,','366','100','0');
INSERT INTO [t4182] VALUES (108,'إسحاق بن إبراهيم بن قرة خرج إلى مصر , وقد كان سمع من أبي حفص الفلاس وغيره , وحدث بمصر','369','100','0');
INSERT INTO [t4182] VALUES (109,'شيبان بن زكريا المعالج','371','100','0');
INSERT INTO [t4182] VALUES (110,'عنبسة بن أبي حفص الأصبهاني يروي عن الحسن , وعكرمة , وابن سيرين , ومجالد , سمع منه بأصبهان شعبة بن عمران , وعامر بن إبراهيم','376','100','0');
INSERT INTO [t4182] VALUES (111,'محمد بن سليمان بن عبد الرحمن الأصبهاني يكنى أبا علي , توفي سنة إحدى وثمانين ومائة , يروي عن سهيل بن أبي صالح , ومنزله بسنبلان','378','100','0');
INSERT INTO [t4182] VALUES (112,'عمران بن الحصين الأصبهاني لم أر له ذكرا إلا في هذا الحديث','381','100','0');
INSERT INTO [t4182] VALUES (113,'أبو داود الطيالسي سليمان بن داود بن الجارود , من أهل البصرة , قدم أصبهان بعد المائتين , وولد سنة ثلاث وثلاثين ومائة , وتوفي سنة أربع ومائتين , وهو ابن إحدى وسبعين سنة','383','100','0');
INSERT INTO [t4182] VALUES (114,'بكر بن بكار أبو عمرو القيسي من أهل البصرة قدم أصبهان سنة ست ومائتين , روى عن ابن عون , وشعبة , والثوري , وفطر بن خليفة. حدث عنه أبو داود الطيالسي','392','100','0');
INSERT INTO [t4182] VALUES (115,'الحسين بن حفص بن الفضل ابن يحيى بن ذكوان بن جريج بن محمد بن عبد الله بن همدان , يكنى بأبي محمد , توفي سنة ثنتي عشرة ومائتين , من ناقلة الكوفة من همدان , وكان له بنون ماتوا قبل أبيهم: أحمد , ومحمد , وإبراهيم','398','100','0');
INSERT INTO [t4182] VALUES (116,'شعبة بن عمران وكان ممن يسمع الحديث مع النعمان , يكنى أبا رافع , وكان يرى الإرجاء , وينازع النعمان بن عبد السلام','407','100','0');
INSERT INTO [t4182] VALUES (117,'إبراهيم بن أيوب الفرساني سمع من سفيان الثوري , ومبارك بن فضالة سمعت عبد الله بن محمد بن الجعد يقول: " رأيت إبراهيم بن أيوب الفرساني مخضوب الرأس واللحية , وكان غضن الوجه من كبر سنه " وحكى أبو صالح الوراق قال: سمعت أحمد بن معاوية بن الهذيل يقول: ما أعرف','409','100','0');
INSERT INTO [t4182] VALUES (118,'أبو إسحاق إسماعيل بن عمرو بن نجيح مولى بجيلة , كوفي قدم أصبهان , توفي سنة سبع وعشرين ومائتين , روى عنه ابن عائشة , وأبو الربيع الزهراني , وأحمد بن محمد بن عمر بن يونس. رأيت كتابا في الفضائل , تأليف محمد بن عمر بن يونس فيه أحاديث كثيرة عن إسماعيل بن عمرو','415','100','0');
INSERT INTO [t4182] VALUES (119,'محمد بن إبراهيم المدني أبو عبد الله روى عنه عامر بن إبراهيم','419','100','0');
